INSERT INTO scriptures (testament, book_name, chapter, verse_number, content, visual_theme) VALUES
('Old', 'Génesis', 34, 20, 'Entonces Hamor y Sichêm su hijo vinieron á la puerta de su ciudad, y hablaron á los varones de su ciudad, diciendo:', 'nebula'),
('Old', 'Génesis', 34, 21, 'Estos varones son pacíficos con nosotros, y habitarán en el país, y traficarán en él: pues he aquí la tierra es bastante ancha para ellos: nosotros tomaremos sus hijas por mujeres, y les daremos las nuestras.', 'nebula'),
('Old', 'Génesis', 34, 22, 'Mas con esta condición nos harán estos hombres el placer de habitar con nosotros, para que seamos un pueblo: si se circuncidare en nosotros todo varón, así como ellos son circuncidados.', 'nebula'),
('Old', 'Génesis', 34, 23, 'Sus ganados, y su hacienda y todas sus bestias, serán nuestras: solamente convengamos con ellos, y habitarán con nosotros.', 'nebula'),
('Old', 'Génesis', 34, 24, 'Y obedecieron á Hamor y á Sichêm su hijo todos los que salían por la puerta de la ciudad, y circuncidaron á todo varón, á cuantos salían por la puerta de su ciudad.', 'nebula'),
('Old', 'Génesis', 34, 25, 'Y sucedió que al tercer día, cuando sentían ellos el mayor dolor, los dos hijos de Jacob, Simeón y Leví, hermanos de Dina, tomaron cada uno su espada, y vinieron contra la ciudad animosamente, y mataron á todo varón.', 'nebula'),
('Old', 'Génesis', 34, 26, 'Y á Hamor y á Sichêm su hijo los mataron á filo de espada: y tomaron á Dina de casa de Sichêm, y saliéronse.', 'nebula'),
('Old', 'Génesis', 34, 27, 'Y los hijos de Jacob vinieron á los muertos y saquearon la ciudad; por cuanto habían amancillado á su hermana.', 'nebula'),
('Old', 'Génesis', 34, 28, 'Tomaron sus ovejas y vacas y sus asnos, y lo que había en la ciudad y en el campo,', 'nebula'),
('Old', 'Génesis', 34, 29, 'Y toda su hacienda; se llevaron cautivos á todos sus niños y sus mujeres, y robaron todo lo que había en casa.', 'nebula'),
('Old', 'Génesis', 34, 30, 'Entonces dijo Jacob á Simeón y á Leví: Habéisme turbado con hacerme abominable á los moradores de aquesta tierra, el Cananeo y el Pherezeo; y teniendo yo pocos hombres, juntarse han contra mí, y me herirán, y seré destruido yo y mi casa.', 'nebula'),
('Old', 'Génesis', 34, 31, 'Y ellos respondieron ¿Había él de tratar á nuestra hermana como á una ramera?', 'nebula'),
('Old', 'Génesis', 35, 1, 'Y DIJO Dios á Jacob: Levántate, sube á Beth-el, y estáte allí; y haz allí un altar al Dios que te apareció cuando huías de tu hermano Esaú.', 'nebula'),
('Old', 'Génesis', 35, 2, 'Entonces Jacob dijo á su familia y á todos los que con él estaban: Quitad los dioses ajenos que hay entre vosotros, y limpiaos, y mudad vuestros vestidos.', 'nebula'),
('Old', 'Génesis', 35, 3, 'Y levantémonos, y subamos á Beth-el; y haré allí altar al Dios que me respondió en el día de mi angustia, y ha sido conmigo en el camino que he andado.', 'nebula'),
('Old', 'Génesis', 35, 4, 'Así dieron á Jacob todos los dioses ajenos que había en poder de ellos, y los zarzillos que estaban en sus orejas; y Jacob los escondió debajo de una encina, que estaba junto á Sichêm.', 'nebula'),
('Old', 'Génesis', 35, 5, 'Y partiéronse, y el terror de Dios fué sobre las ciudades que había en sus alrededores, y no siguieron tras los hijos de Jacob.', 'nebula'),
('Old', 'Génesis', 35, 6, 'Y llegó Jacob á Luz, que está en tierra de Canaán, (esta es Beth-el) él y todo el pueblo que con él estaba;', 'nebula'),
('Old', 'Génesis', 35, 7, 'Y edificó allí un altar, y llamó el lugar El-Beth-el, porque allí le había aparecido Dios, cuando huía de su hermano.', 'nebula'),
('Old', 'Génesis', 35, 8, 'Entonces murió Débora, ama de Rebeca, y fue sepultada á las raíces de Beth-el, debajo de una encina: y llamóse su nombre Allon-Bacuth.', 'nebula'),
('Old', 'Génesis', 35, 9, 'Y aparecióse otra vez Dios á Jacob, cuando se había vuelto de Padan-aram, y bendíjole.', 'nebula'),
('Old', 'Génesis', 35, 10, 'Y díjole Dios: Tu nombre es Jacob; no se llamará más tu nombre Jacob, sino Israel será tu nombre: y llamó su nombre Israel.', 'nebula'),
('Old', 'Génesis', 35, 11, 'Y díjole Dios: Yo soy el Dios Omnipotente: crece y multiplícate; una nación y conjunto de naciones procederá de ti, y reyes saldrán de tus lomos:', 'nebula'),
('Old', 'Génesis', 35, 12, 'Y la tierra que yo he dado á Abraham y á Isaac, la daré á ti: y á tu simiente después de ti daré la tierra.', 'nebula'),
('Old', 'Génesis', 35, 13, 'Y fuése de él Dios, del lugar donde con él había hablado.', 'nebula'),
('Old', 'Génesis', 35, 14, 'Y Jacob erigió un título en el lugar donde había hablado con él, un título de piedra, y derramó sobre él libación, y echó sobre él aceite.', 'nebula'),
('Old', 'Génesis', 35, 15, 'Y llamó Jacob el nombre de aquel lugar donde Dios había hablado con él, Beth-el.', 'nebula'),
('Old', 'Génesis', 35, 16, 'Y partieron de Beth-el, y había aún como media legua de tierra para llegar á Ephrata, cuando parió Rachêl, y hubo trabajo en su parto.', 'nebula'),
('Old', 'Génesis', 35, 17, 'Y aconteció, que como había trabajo en su parir, díjole la partera: No temas, que también tendrás este hijo.', 'nebula'),
('Old', 'Génesis', 35, 18, 'Y acaeció que al salírsele el alma, (pues murió) llamó su nombre Benoni; mas su padre lo llamó Benjamín.', 'nebula'),
('Old', 'Génesis', 35, 19, 'Así murió Rachêl, y fué sepultada en el camino del Ephrata, la cual es Beth-lehem.', 'nebula'),
('Old', 'Génesis', 35, 20, 'Y puso Jacob un título sobre su sepultura: este es el título de la sepultura de Rachêl hasta hoy.', 'nebula'),
('Old', 'Génesis', 35, 21, 'Y partió Israel, y tendió su tienda de la otra parte de Migdaleder.', 'nebula'),
('Old', 'Génesis', 35, 22, 'Y acaeció, morando Israel en aquella tierra, que fué Rubén y durmió con Bilha la concubina de su padre; lo cual llegó á entender Israel. Ahora bien, los hijos de Israel fueron doce:', 'nebula'),
('Old', 'Génesis', 35, 23, 'Los hijos de Lea: Rubén el primogénito de Jacob, y Simeón, y Leví, y Judá, é Issachâr, y Zabulón.', 'nebula'),
('Old', 'Génesis', 35, 24, 'Los hijos de Rachêl: José, y Benjamín.', 'nebula'),
('Old', 'Génesis', 35, 25, 'Y los hijos de Bilha, sierva de Rachêl: Dan, y Nephtalí.', 'nebula'),
('Old', 'Génesis', 35, 26, 'Y los hijos de Zilpa, sierva de Lea: Gad, y Aser. Estos fueron los hijos de Jacob, que le nacieron en Padan-aram.', 'nebula'),
('Old', 'Génesis', 35, 27, 'Y vino Jacob á Isaac su padre á Mamre, á la ciudad de Arba, que es Hebrón, donde habitaron Abraham é Isaac.', 'nebula'),
('Old', 'Génesis', 35, 28, 'Y fueron los días de Isaac ciento ochenta años.', 'nebula'),
('Old', 'Génesis', 35, 29, 'Y exhaló Isaac el espíritu, y murió, y fué recogido á sus pueblos, viejo y harto de días; y sepultáronlo Esaú y Jacob sus hijos.', 'nebula'),
('Old', 'Génesis', 36, 1, 'Y ESTAS son las generaciones de Esaú, el cual es Edom.', 'nebula'),
('Old', 'Génesis', 36, 2, 'Esaú tomó sus mujeres de las hijas de Canaán: á Ada, hija de Elón Hetheo, y á Aholibama, hija de Ana, hija de Zibeón el Heveo;', 'nebula'),
('Old', 'Génesis', 36, 3, 'Y á Basemath, hija de Ismael, hermana de Navaioth.', 'nebula'),
('Old', 'Génesis', 36, 4, 'Y Ada parió á Esaú á Eliphaz; y Basemath parió á Reuel.', 'nebula'),
('Old', 'Génesis', 36, 5, 'Y Aholibama parió á Jeús, y á Jaalam, y á Cora: estos son los hijos de Esaú, que le nacieron en la tierra de Canaán.', 'nebula'),
('Old', 'Génesis', 36, 6, 'Y Esaú tomó sus mujeres, y sus hijos, y sus hijas, y todas las personas de su casa, y sus ganados, y todas sus bestias, y toda su hacienda que había adquirido en la tierra de Canaán, y fuése á otra tierra de delante de Jacob su hermano.', 'nebula'),
('Old', 'Génesis', 36, 7, 'Porque la hacienda de ellos era grande, y no podían habitar juntos, ni la tierra de su peregrinación los podía sostener á causa de sus ganados.', 'nebula'),
('Old', 'Génesis', 36, 8, 'Y Esaú habitó en el monte de Seir: Esaú es Edom.', 'nebula'),
('Old', 'Génesis', 36, 9, 'Estos son los linajes de Esaú, padre de Edom, en el monte de Seir.', 'nebula'),
('Old', 'Génesis', 36, 10, 'Estos son los nombres de los hijos de Esaú: Eliphaz, hijo de Ada, mujer de Esaú; Reuel, hijo de Basemath, mujer de Esaú.', 'nebula'),
('Old', 'Génesis', 36, 11, 'Y los hijos de Eliphaz fueron Temán, Omar, Zepho, Gatam, y Cenaz.', 'nebula'),
('Old', 'Génesis', 36, 12, 'Y Timna fué concubina de Eliphaz, hijo de Esaú, la cual le parió á Amalec: estos son los hijos de Ada, mujer de Esaú.', 'nebula'),
('Old', 'Génesis', 36, 13, 'Y los hijos de Reuel fueron Nahath, Zera, Samma, y Mizza: estos son los hijos de Basemath, mujer de Esaú.', 'nebula'),
('Old', 'Génesis', 36, 14, 'Estos fueron los hijos de Aholibama, mujer de Esaú, hija de Ana, que fué hija de Zibeón: ella parió á Esaú á Jeús, Jaalam, y Cora.', 'nebula'),
('Old', 'Génesis', 36, 15, 'Estos son los duques de los hijos de Esaú. Hijos de Eliphaz, primogénito de Esaú: el duque Temán, el duque Omar, el duque Zepho, el duque Cenaz,', 'nebula'),
('Old', 'Génesis', 36, 16, 'El duque Cora, el duque Gatam, y el duque Amalec: estos son los duques de Eliphaz en la tierra de Edom; estos fueron los hijos de Ada.', 'nebula'),
('Old', 'Génesis', 36, 17, 'Y estos son los hijos de Reuel, hijo de Esaú: el duque Nahath, el duque Zera, el duque Samma, y el duque Mizza: estos son los duques de la línea de Reuel en la tierra de Edom; estos hijos vienen de Basemath, mujer de Esaú.', 'nebula'),
('Old', 'Génesis', 36, 18, 'Y estos son los hijos de Aholibama, mujer de Esaú: el duque Jeús, el duque Jaalam, y el duque Cora: estos fueron los duques que salieron de Aholibama, mujer de Esaú, hija de Ana.', 'nebula'),
('Old', 'Génesis', 36, 19, 'Estos, pues, son los hijos de Esaú, y sus duques: él es Edom.', 'nebula'),
('Old', 'Génesis', 36, 20, 'Y estos son los hijos de Seir Horeo, moradores de aquella tierra: Lotán, Sobal, Zibeón, Ana,', 'nebula'),
('Old', 'Génesis', 36, 21, 'Disón, Ezer, y Disán: estos son los duques de los Horeos, hijos de Seir en la tierra de Edom.', 'nebula'),
('Old', 'Génesis', 36, 22, 'Los hijos de Lotán fueron Hori y Hemán; y Timna fué hermana de Lotán.', 'nebula'),
('Old', 'Génesis', 36, 23, 'Y los hijos de Sobal fueron Alván, Manahath, Ebal, Sepho, y Onán.', 'nebula'),
('Old', 'Génesis', 36, 24, 'Y los hijos de Zibeón fueron Aja, y Ana. Este Ana es el que descubrió los mulos en el desierto, cuando apacentaba los asnos de Zibeón su padre.', 'nebula'),
('Old', 'Génesis', 36, 25, 'Los hijos de Ana fueron Disón, y Aholibama, hija de Ana.', 'nebula'),
('Old', 'Génesis', 36, 26, 'Y estos fueron los hijos de Disón: Hemdán, Eshbán, Ithram, y Cherán.', 'nebula'),
('Old', 'Génesis', 36, 27, 'Y estos fueron los hijos de Ezer: Bilhán, Zaaván, y Acán.', 'nebula'),
('Old', 'Génesis', 36, 28, 'Estos fueron los hijos de Disán: Huz, y Arán.', 'nebula'),
('Old', 'Génesis', 36, 29, 'Y estos fueron los duques de los Horeos: el duque Lotán, el duque Sobal, el duque Zibeón, el duque Ana.', 'nebula'),
('Old', 'Génesis', 36, 30, 'El duque Disón, el duque Ezer, el duque Disán: estos fueron los duques de los Horeos: por sus ducados en la tierra de Seir.', 'nebula'),
('Old', 'Génesis', 36, 31, 'Y los reyes que reinaron en la tierra de Edom, antes que reinase rey sobre los hijos de Israel, fueron estos:', 'nebula'),
('Old', 'Génesis', 36, 32, 'Bela, hijo de Beor, reinó en Edom: y el nombre de su ciudad fué Dinaba.', 'nebula'),
('Old', 'Génesis', 36, 33, 'Y murió Bela, y reinó en su lugar Jobab, hijo de Zera, de Bosra.', 'nebula'),
('Old', 'Génesis', 36, 34, 'Y murió Jobab, y en su lugar reinó Husam, de tierra de Temán.', 'nebula'),
('Old', 'Génesis', 36, 35, 'Y murió Husam, y reinó en su lugar Adad, hijo de Badad, el que hirió á Midián en el campo de Moab: y el nombre de su ciudad fué Avith.', 'nebula'),
('Old', 'Génesis', 36, 36, 'Y murió Adad, y en su lugar reinó Samla, de Masreca.', 'nebula'),
('Old', 'Génesis', 36, 37, 'Y murió Samla, y reinó en su lugar Saúl, de Rehoboth del Río.', 'nebula'),
('Old', 'Génesis', 36, 38, 'Y murió Saúl, y en lugar suyo reinó Baalanán, hijo de Achbor.', 'nebula'),
('Old', 'Génesis', 36, 39, 'Y murió Baalanán, hijo de Achbor, y reinó Adar en lugar suyo: y el nombre de su ciudad fué Pau; y el nombre de su mujer Meetabel, hija de Matred, hija de Mezaab.', 'nebula'),
('Old', 'Génesis', 36, 40, 'Estos, pues, son los nombres de los duques de Esaú por sus linajes, por sus lugares, y sus nombres: el duque Timna, el duque Alva, el duque Jetheth,', 'nebula'),
('Old', 'Génesis', 36, 41, 'El duque Aholibama, el duque Ela, el duque Pinón,', 'nebula'),
('Old', 'Génesis', 36, 42, 'El duque Cenaz, el duque Temán, el duque Mibzar,', 'nebula'),
('Old', 'Génesis', 36, 43, 'El duque Magdiel, y el duque Hiram. Estos fueron los duques de Edom por sus habitaciones en la tierra de su posesión. Edom es el mismo Esaú, padre de los Idumeos.', 'nebula'),
('Old', 'Génesis', 37, 1, 'Y HABITO Jacob en la tierra donde peregrinó su padre, en la tierra de Canaán.', 'nebula'),
('Old', 'Génesis', 37, 2, 'Estas fueron las generaciones de Jacob. José, siendo de edad de diez y siete años apacentaba las ovejas con sus hermanos; y el joven estaba con los hijos de Bilha, y con los hijos de Zilpa, mujeres de su padre: y noticiaba José á su padre la mala fama de ellos.', 'nebula'),
('Old', 'Génesis', 37, 3, 'Y amaba Israel á José más que á todos sus hijos, porque le había tenido en su vejez: y le hizo una ropa de diversos colores.', 'nebula'),
('Old', 'Génesis', 37, 4, 'Y viendo sus hermanos que su padre lo amaba más que á todos sus hermanos, aborrecíanle, y no le podían hablar pacíficamente.', 'nebula'),
('Old', 'Génesis', 37, 5, 'Y soñó José un sueño y contólo á sus hermanos; y ellos vinieron á aborrecerle más todavía.', 'nebula'),
('Old', 'Génesis', 37, 6, 'Y él les dijo: Oíd ahora este sueño que he soñado:', 'nebula'),
('Old', 'Génesis', 37, 7, 'He aquí que atábamos manojos en medio del campo, y he aquí que mi manojo se levantaba, y estaba derecho, y que vuestros manojos estaban alrededor, y se inclinaban al mío.', 'nebula'),
('Old', 'Génesis', 37, 8, 'Y respondiéronle sus hermanos: ¿Has de reinar tú sobre nosotros, ó te has de enseñorear sobre nosotros? Y le aborrecieron aún más á causa de sus sueños y de sus palabras.', 'nebula'),
('Old', 'Génesis', 37, 9, 'Y soñó aún otro sueño, y contólo á sus hermanos, diciendo: He aquí que he soñado otro sueño, y he aquí que el sol y la luna y once estrellas se inclinaban á mí.', 'nebula'),
('Old', 'Génesis', 37, 10, 'Y contólo á su padre y á sus hermanos: y su padre le reprendió, y díjole: ¿Qué sueño es este que soñaste? ¿Hemos de venir yo y tu madre, y tus hermanos, á inclinarnos á ti á tierra?', 'nebula'),
('Old', 'Génesis', 37, 11, 'Y sus hermanos le tenían envidia, mas su padre paraba la consideración en ello.', 'nebula'),
('Old', 'Génesis', 37, 12, 'Y fueron sus hermanos á apacentar las ovejas de su padre en Sichêm.', 'nebula'),
('Old', 'Génesis', 37, 13, 'Y dijo Israel á José: Tus hermanos apacientan las ovejas en Sichêm: ven, y te enviaré á ellos. Y él respondió: Heme aquí.', 'nebula'),
('Old', 'Génesis', 37, 14, 'Y él le dijo: Ve ahora, mira cómo están tus hermanos y cómo están las ovejas, y tráeme la respuesta. Y enviólo del valle de Hebrón, y llegó á Sichêm.', 'nebula'),
('Old', 'Génesis', 37, 15, 'Y hallólo un hombre, andando él perdido por el campo, y preguntóle aquel hombre, diciendo: ¿Qué buscas?', 'nebula'),
('Old', 'Génesis', 37, 16, 'Y él respondió: Busco á mis hermanos: ruégote que me muestres dónde pastan.', 'nebula'),
('Old', 'Génesis', 37, 17, 'Y aquel hombre respondió: Ya se han ido de aquí; yo les oí decir: Vamos á Dothán. Entonces José fué tras de sus hermanos, y hallólos en Dothán.', 'nebula'),
('Old', 'Génesis', 37, 18, 'Y como ellos lo vieron de lejos, antes que cerca de ellos llegara, proyectaron contra él para matarle.', 'nebula'),
('Old', 'Génesis', 37, 19, 'Y dijeron el uno al otro: He aquí viene el soñador;', 'nebula'),
('Old', 'Génesis', 37, 20, 'Ahora pues, venid, y matémoslo y echémosle en una cisterna, y diremos: Alguna mala bestia le devoró: y veremos qué serán sus sueños.', 'nebula'),
('Old', 'Génesis', 37, 21, 'Y como Rubén oyó esto, librólo de sus manos y dijo: No lo matemos.', 'nebula'),
('Old', 'Génesis', 37, 22, 'Y díjoles Rubén: No derraméis sangre; echadlo en esta cisterna que está en el desierto, y no pongáis mano en él; por librarlo así de sus manos, para hacerlo volver á su padre.', 'nebula'),
('Old', 'Génesis', 37, 23, 'Y sucedió que, cuando llegó José á sus hermanos, ellos hicieron desnudar á José su ropa, la ropa de colores que tenía sobre sí;', 'nebula'),
('Old', 'Génesis', 37, 24, 'Y tomáronlo, y echáronle en la cisterna; mas la cisterna estaba vacía, no había en ella agua.', 'nebula'),
('Old', 'Génesis', 37, 25, 'Y sentáronse á comer pan: y alzando los ojos miraron, y he aquí una compañía de Ismaelitas que venía de Galaad, y sus camellos traían aromas y bálsamo y mirra, é iban á llevarlo á Egipto.', 'nebula'),
('Old', 'Génesis', 37, 26, 'Entonces Judá dijo á sus hermanos: ¿Qué provecho el que matemos á nuestro hermano y encubramos su muerte?', 'nebula'),
('Old', 'Génesis', 37, 27, 'Venid, y vendámosle á los Ismaelitas, y no sea nuestra mano sobre él; que nuestro hermano es nuestra carne. Y sus hermanos acordaron con él.', 'nebula'),
('Old', 'Génesis', 37, 28, 'Y como pasaban los Midianitas mercaderes, sacaron ellos á José de la cisterna, y trajéronle arriba, y le vendieron á los Ismaelitas por veinte piezas de plata. Y llevaron á José á Egipto.', 'nebula'),
('Old', 'Génesis', 37, 29, 'Y Rubén volvió á la cisterna, y no halló á José dentro, y rasgó sus vestidos.', 'nebula'),
('Old', 'Génesis', 37, 30, 'Y tornó á sus hermanos y dijo: El mozo no parece; y yo, ¿adónde iré yo?', 'nebula'),
('Old', 'Génesis', 37, 31, 'Entonces tomaron ellos la ropa de José, y degollaron un cabrito de las cabras, y tiñeron la ropa con la sangre;', 'nebula'),
('Old', 'Génesis', 37, 32, 'Y enviaron la ropa de colores y trajéronla á su padre, y dijeron: Esta hemos hallado, reconoce ahora si es ó no la ropa de tu hijo.', 'nebula'),
('Old', 'Génesis', 37, 33, 'Y él la conoció, y dijo: La ropa de mi hijo es; alguna mala bestia le devoró; José ha sido despedazado.', 'nebula'),
('Old', 'Génesis', 37, 34, 'Entonces Jacob rasgó sus vestidos, y puso saco sobre sus lomos, y enlutóse por su hijo muchos días.', 'nebula'),
('Old', 'Génesis', 37, 35, 'Y levantáronse todos sus hijos y todas sus hijas para consolarlo; mas él no quiso tomar consolación, y dijo: Porque yo tengo de descender á mi hijo enlutado hasta la sepultura. Y llorólo su padre.', 'nebula'),
('Old', 'Génesis', 37, 36, 'Y los Midianitas lo vendieron en Egipto á Potiphar, eunuco de Faraón, capitán de los de la guardia.', 'nebula'),
('Old', 'Génesis', 38, 1, 'Y ACONTECIO en aquel tiempo, que Judá descendió de con sus hermanos, y fuése á un varón Adullamita, que se llamaba Hira.', 'nebula'),
('Old', 'Génesis', 38, 2, 'Y vió allí Judá la hija de un hombre Cananeo, el cual se llamaba Súa; y tomóla, y entró á ella:', 'nebula'),
('Old', 'Génesis', 38, 3, 'La cual concibió, y parió un hijo; y llamó su nombre Er.', 'nebula'),
('Old', 'Génesis', 38, 4, 'Y concibió otra vez, y parió un hijo, y llamó su nombre Onán.', 'nebula'),
('Old', 'Génesis', 38, 5, 'Y volvió á concebir, y parió un hijo, y llamó su nombre Sela. Y estaba en Chezib cuando lo parió.', 'nebula'),
('Old', 'Génesis', 38, 6, 'Y Judá tomó mujer para su primogénito Er, la cual se llamaba Thamar.', 'nebula'),
('Old', 'Génesis', 38, 7, 'Y Er, el primogénito de Judá, fué malo á los ojos de Jehová, y quitóle Jehová la vida.', 'nebula'),
('Old', 'Génesis', 38, 8, 'Entonces Judá dijo á Onán: Entra á la mujer de tu hermano, y despósate con ella, y suscita simiente á tu hermano.', 'nebula'),
('Old', 'Génesis', 38, 9, 'Y sabiendo Onán que la simiente no había de ser suya, sucedía que cuando entraba á la mujer de su hermano vertía en tierra, por no dar simiente á su hermano.', 'nebula'),
('Old', 'Génesis', 38, 10, 'Y desagradó en ojos de Jehová lo que hacía, y también quitó á él la vida.', 'nebula'),
('Old', 'Génesis', 38, 11, 'Y Judá dijo á Thamar su nuera: Estáte viuda en casa de tu padre, hasta que crezca Sela mi hijo; porque dijo: Que quizá no muera él también como sus hermanos. Y fuése Thamar, y estúvose en casa de su padre.', 'nebula'),
('Old', 'Génesis', 38, 12, 'Y pasaron muchos días, y murió la hija de Súa, mujer de Judá; y Judá se consoló, y subía á los trasquiladores de sus ovejas á Timnath, él y su amigo Hira el Adullamita.', 'nebula'),
('Old', 'Génesis', 38, 13, 'Y fué dado aviso á Thamar, diciendo: He aquí tu suegro sube á Timnath á trasquilar sus ovejas.', 'nebula'),
('Old', 'Génesis', 38, 14, 'Entonces quitó ella de sobre sí los vestidos de su viudez, y cubrióse con un velo, y arrebozóse, y se puso á la puerta de las aguas que están junto al camino de Timnath; porque veía que había crecido Sela, y ella no era dada á él por mujer.', 'nebula'),
('Old', 'Génesis', 38, 15, 'Y vióla Judá, y túvola por ramera, porque había ella cubierto su rostro.', 'nebula'),
('Old', 'Génesis', 38, 16, 'Y apartóse del camino hacia ella, y díjole: Ea, pues, ahora entraré á ti; porque no sabía que era su nuera; y ella dijo: ¿Qué me has de dar, si entrares á mí?', 'nebula'),
('Old', 'Génesis', 38, 17, 'El respondió: Yo te enviaré del ganado un cabrito de las cabras. Y ella dijo: Hasme de dar prenda hasta que lo envíes.', 'nebula'),
('Old', 'Génesis', 38, 18, 'Entonces él dijo: ¿Qué prenda te daré? Ella respondió: Tu anillo, y tu manto, y tu bordón que tienes en tu mano. Y él se los dió, y entró á ella, la cual concibió de él.', 'nebula'),
('Old', 'Génesis', 38, 19, 'Y levantóse, y fuése: y quitóse el velo de sobre sí, y vistióse las ropas de su viudez.', 'nebula'),
('Old', 'Génesis', 38, 20, 'Y Judá envió el cabrito de las cabras por mano de su amigo el Adullamita, para que tomase la prenda de mano de la mujer; mas no la halló.', 'nebula'),
('Old', 'Génesis', 38, 21, 'Y preguntó á los hombres de aquel lugar, diciendo: ¿Dónde está la ramera de las aguas junto al camino? Y ellos le dijeron: No ha estado aquí ramera.', 'nebula'),
('Old', 'Génesis', 38, 22, 'Entonces él se volvió á Judá, y dijo: No la he hallado; y también los hombres del lugar dijeron: Aquí no ha estado ramera.', 'nebula'),
('Old', 'Génesis', 38, 23, 'Y Judá dijo: Tómeselo para sí, porque no seamos menospreciados: he aquí yo he enviado este cabrito, y tú no la hallaste.', 'nebula'),
('Old', 'Génesis', 38, 24, 'Y acaeció que al cabo de unos tres meses fué dado aviso á Judá, diciendo: Thamar tu nuera ha fornicado, y aun cierto está preñada de las fornicaciones. Y Judá dijo: Sacadla, y sea quemada.', 'nebula'),
('Old', 'Génesis', 38, 25, 'Y ella cuando la sacaban, envió á decir á su suegro: Del varón cuyas son estas cosas, estoy preñada: y dijo más: Mira ahora cuyas son estas cosas, el anillo, y el manto, y el bordón.', 'nebula'),
('Old', 'Génesis', 38, 26, 'Entonces Judá los reconoció, y dijo: Más justa es que yo, por cuanto no la he dado á Sela mi hijo. Y nunca más la conoció.', 'nebula'),
('Old', 'Génesis', 38, 27, 'Y aconteció que al tiempo del parir, he aquí había dos en su vientre.', 'nebula'),
('Old', 'Génesis', 38, 28, 'Y sucedió, cuando paría, que sacó la mano el uno, y la partera tomó y ató á su mano un hilo de grana, diciendo: Este salió primero.', 'nebula'),
('Old', 'Génesis', 38, 29, 'Empero fué que tornando él á meter la mano, he aquí su hermano salió; y ella dijo: ¿Por qué has hecho sobre ti rotura? Y llamó su nombre Phares.', 'nebula'),
('Old', 'Génesis', 38, 30, 'Y después salió su hermano, el que tenía en su mano el hilo de grana, y llamó su nombre Zara.', 'nebula'),
('Old', 'Génesis', 39, 1, 'Y LLEVADO José á Egipto, comprólo Potiphar, eunuco de Faraón, capitán de los de la guardia, varón Egipcio, de mano de los Ismaelitas que lo habían llevado allá.', 'nebula'),
('Old', 'Génesis', 39, 2, 'Mas Jehová fué con José, y fué varón prosperado: y estaba en la casa de su señor el Egipcio.', 'nebula'),
('Old', 'Génesis', 39, 3, 'Y vió su señor que Jehová era con él, y que todo lo que él hacía, Jehová lo hacía prosperar en su mano.', 'nebula'),
('Old', 'Génesis', 39, 4, 'Así halló José gracia en sus ojos, y servíale; y él le hizo mayordomo de su casa, y entregó en su poder todo lo que tenía.', 'nebula'),
('Old', 'Génesis', 39, 5, 'Y aconteció que, desde cuando le dió el encargo de su casa, y de todo lo que tenía, Jehová bendijo la casa del Egipcio á causa de José; y la bendición de Jehová fué sobre todo lo que tenía, así en casa como en el campo.', 'nebula'),
('Old', 'Génesis', 39, 6, 'Y dejó todo lo que tenía en mano de José; ni con él sabía de nada más que del pan que comía. Y era José de hermoso semblante y bella presencia.', 'nebula'),
('Old', 'Génesis', 39, 7, 'Y aconteció después de esto, que la mujer de su señor puso sus ojos en José, y dijo: Duerme conmigo.', 'nebula'),
('Old', 'Génesis', 39, 8, 'Y él no quiso, y dijo á la mujer de su señor: He aquí que mi señor no sabe conmigo lo que hay en casa, y ha puesto en mi mano todo lo que tiene:', 'nebula'),
('Old', 'Génesis', 39, 9, 'No hay otro mayor que yo en esta casa, y ninguna cosa me ha reservado sino á ti, por cuanto tú eres su mujer; ¿cómo, pues, haría yo este grande mal y pecaría contra Dios?', 'nebula'),
('Old', 'Génesis', 39, 10, 'Y fué que hablando ella á José cada día, y no escuchándola él para acostarse al lado de ella, para estar con ella.', 'nebula'),
('Old', 'Génesis', 39, 11, 'Aconteció que entró él un día en casa para hacer su oficio, y no había nadie de los de casa allí en casa.', 'nebula'),
('Old', 'Génesis', 39, 12, 'Y asiólo ella por su ropa, diciendo: Duerme conmigo. Entonces dejóla él su ropa en las manos, y huyó, y salióse fuera.', 'nebula'),
('Old', 'Génesis', 39, 13, 'Y acaeció que cuando vió ella que le había dejado su ropa en sus manos, y había huído fuera,', 'nebula'),
('Old', 'Génesis', 39, 14, 'Llamó á los de casa, y hablóles diciendo: Mirad, nos ha traído un Hebreo, para que hiciese burla de nosotros: vino él á mí para dormir conmigo, y yo dí grandes voces;', 'nebula'),
('Old', 'Génesis', 39, 15, 'Y viendo que yo alzaba la voz y gritaba, dejó junto á mí su ropa, y huyó, y salióse fuera.', 'nebula'),
('Old', 'Génesis', 39, 16, 'Y ella puso junto á sí la ropa de él, hasta que vino su señor á su casa.', 'nebula'),
('Old', 'Génesis', 39, 17, 'Entonces le habló ella semejantes palabras, diciendo: El siervo Hebreo que nos trajiste, vino á mí para deshonrarme;', 'nebula'),
('Old', 'Génesis', 39, 18, 'Y como yo alcé mi voz y grite, él dejó su ropa junto á mí, y huyó fuera.', 'nebula'),
('Old', 'Génesis', 39, 19, 'Y sucedió que como oyó su señor las palabras que su mujer le hablara, diciendo: Así me ha tratado tu siervo; encendióse su furor.', 'nebula'),
('Old', 'Génesis', 39, 20, 'Y tomó su señor á José, y púsole en la casa de la cárcel, donde estaban los presos del rey, y estuvo allí en la casa de la cárcel.', 'nebula'),
('Old', 'Génesis', 39, 21, 'Mas Jehová fué con José, y extendió á él su misericordia, y dióle gracia en ojos del principal de la casa de la cárcel.', 'nebula'),
('Old', 'Génesis', 39, 22, 'Y el principal de la casa de la cárcel entregó en mano de José todos los presos que había en aquella prisión; todo lo que hacían allí, él lo hacía.', 'nebula'),
('Old', 'Génesis', 39, 23, 'No veía el principal de la cárcel cosa alguna que en su mano estaba; porque Jehová era con él, y lo que él hacía, Jehová lo prosperaba.', 'nebula'),
('Old', 'Génesis', 40, 1, 'Y ACONTECIO después de estas cosas, que el copero del rey de Egipto y el panadero delinquieron contra su señor el rey de Egipto.', 'nebula'),
('Old', 'Génesis', 40, 2, 'Y enojóse Faraón contra sus dos eunucos, contra el principal de los coperos, y contra el principal de los panaderos:', 'nebula'),
('Old', 'Génesis', 40, 3, 'Y púsolos en prisión en la casa del capitán de los de la guardia, en la casa de la cárcel donde José estaba preso.', 'nebula'),
('Old', 'Génesis', 40, 4, 'Y el capitán de los de la guardia dió cargo de ellos á José, y él les servía: y estuvieron días en la prisión.', 'nebula'),
('Old', 'Génesis', 40, 5, 'Y ambos á dos, el copero y el panadero del rey de Egipto, que estaban arrestados en la prisión, vieron un sueño, cada uno su sueño en una misma noche, cada uno conforme á la declaración de su sueño.', 'nebula'),
('Old', 'Génesis', 40, 6, 'Y vino á ellos José por la mañana, y mirólos, y he aquí que estaban tristes.', 'nebula'),
('Old', 'Génesis', 40, 7, 'Y él preguntó á aquellos eunucos de Faraón, que estaban con él en la prisión de la casa de su señor, diciendo: ¿Por qué parecen hoy mal vuestros semblantes?', 'nebula'),
('Old', 'Génesis', 40, 8, 'Y ellos le dijeron: Hemos tenido un sueño, y no hay quien lo declare. Entonces les dijo José: ¿No son de Dios las declaraciones? Contádmelo ahora.', 'nebula'),
('Old', 'Génesis', 40, 9, 'Entonces el principal de los coperos contó su sueño á José, y díjole: Yo soñaba que veía una vid delante de mí,', 'nebula'),
('Old', 'Génesis', 40, 10, 'Y en la vid tres sarmientos; y ella como que brotaba, y arrojaba su flor, viniendo á madurar sus racimos de uvas:', 'nebula'),
('Old', 'Génesis', 40, 11, 'Y que la copa de Faraón estaba en mi mano, y tomaba yo las uvas, y las exprimía en la copa de Faraón, y daba yo la copa en mano de Faraón.', 'nebula'),
('Old', 'Génesis', 40, 12, 'Y díjole José: Esta es su declaración: Los tres sarmientos son tres días:', 'nebula'),
('Old', 'Génesis', 40, 13, 'Al cabo de tres días Faraón te hará levantar cabeza, y te restituirá á tu puesto: y darás la copa á Faraón en su mano, como solías cuando eras su copero.', 'nebula'),
('Old', 'Génesis', 40, 14, 'Acuérdate, pues, de mí para contigo cuando tuvieres ese bien, y ruégote que uses conmigo de misericordia, y hagas mención de mí á Faraón, y me saques de esta casa:', 'nebula'),
('Old', 'Génesis', 40, 15, 'Porque hurtado he sido de la tierra de los Hebreos; y tampoco he hecho aquí porqué me hubiesen de poner en la cárcel.', 'nebula'),
('Old', 'Génesis', 40, 16, 'Y viendo el principal de los panaderos que había declarado para bien, dijo á José: También yo soñaba que veía tres canastillos blancos sobre mi cabeza;', 'nebula'),
('Old', 'Génesis', 40, 17, 'Y en el canastillo más alto había de todas las viandas de Faraón, obra de panadero; y que las aves las comían del canastillo de sobre mi cabeza.', 'nebula'),
('Old', 'Génesis', 40, 18, 'Entonces respondió José, y dijo: Esta es su declaración: Los tres canastillos tres días son;', 'nebula'),
('Old', 'Génesis', 40, 19, 'Al cabo de tres días quitará Faraón tu cabeza de sobre ti, y te hará colgar en la horca, y las aves comerán tu carne de sobre ti.', 'nebula'),
('Old', 'Génesis', 40, 20, 'Y fué el tercero día el día del nacimiento de Faraón, é hizo banquete á todos sus sirvientes: y alzó la cabeza del principal de los coperos, y la cabeza del principal de los panaderos, entre sus servidores.', 'nebula'),
('Old', 'Génesis', 40, 21, 'E hizo volver á su oficio al principal de los coperos; y dió él la copa en mano de Faraón.', 'nebula'),
('Old', 'Génesis', 40, 22, 'Mas hizo ahorcar al principal de los panaderos, como le había declarado José.', 'nebula'),
('Old', 'Génesis', 40, 23, 'Y el principal de los coperos no se acordó de José, sino que le olvidó.', 'nebula'),
('Old', 'Génesis', 41, 1, 'Y ACONTECIO que pasados dos años tuvo Faraón un sueño: Parecíale que estaba junto al río;', 'nebula'),
('Old', 'Génesis', 41, 2, 'Y que del río subían siete vacas, hermosas á la vista, y muy gordas, y pacían en el prado:', 'nebula'),
('Old', 'Génesis', 41, 3, 'Y que otras siete vacas subían tras ellas del río, de fea vista, y enjutas de carne, y se pararon cerca de las vacas hermosas á la orilla del río:', 'nebula'),
('Old', 'Génesis', 41, 4, 'Y que las vacas de fea vista y enjutas de carne devoraban á las siete vacas hermosas y muy gordas. Y despertó Faraón.', 'nebula'),
('Old', 'Génesis', 41, 5, 'Durmióse de nuevo, y soñó la segunda vez: Que siete espigas llenas y hermosas subían de una sola caña:', 'nebula'),
('Old', 'Génesis', 41, 6, 'Y que otras siete espigas menudas y abatidas del Solano, salían después de ellas:', 'nebula'),
('Old', 'Génesis', 41, 7, 'Y las siete espigas menudas devoraban á las siete espigas gruesas y llenas. Y despertó Faraón, y he aquí que era sueño.', 'nebula'),
('Old', 'Génesis', 41, 8, 'Y acaeció que á la mañana estaba agitado su espíritu; y envió é hizo llamar á todos los magos de Egipto, y á todos sus sabios: y contóles Faraón sus sueños, mas no había quien á Faraón los declarase.', 'nebula'),
('Old', 'Génesis', 41, 9, 'Entonces el principal de los coperos habló á Faraón, diciendo: Acuérdome hoy de mis faltas:', 'nebula'),
('Old', 'Génesis', 41, 10, 'Faraón se enojó contra sus siervos, y á mí me echó á la prisión de la casa del capitán de los de la guardia, á mí y al principal de los panaderos:', 'nebula'),
('Old', 'Génesis', 41, 11, 'Y yo y él vimos un sueño una misma noche: cada uno soñó conforme á la declaración de su sueño.', 'nebula'),
('Old', 'Génesis', 41, 12, 'Y estaba allí con nosotros un mozo Hebreo, sirviente del capitán de los de la guardia; y se lo contamos, y él nos declaró nuestros sueños, y declaró á cada uno conforme á su sueño.', 'nebula'),
('Old', 'Génesis', 41, 13, 'Y aconteció que como él nos declaró, así fué: á mí me hizo volver á mi puesto, é hizo colgar al otro.', 'nebula'),
('Old', 'Génesis', 41, 14, 'Entonces Faraón envió y llamó á José; é hiciéronle salir corriendo de la cárcel, y le cortaron el pelo, y mudaron sus vestidos, y vino á Faraón.', 'nebula'),
('Old', 'Génesis', 41, 15, 'Y dijo Faraón á José: Yo he tenido un sueño, y no hay quien lo declare; mas he oído decir de ti, que oyes sueños para declararlos.', 'nebula'),
('Old', 'Génesis', 41, 16, 'Y respondió José á Faraón, diciendo: No está en mí; Dios será el que responda paz á Faraón.', 'nebula'),
('Old', 'Génesis', 41, 17, 'Entonces Faraón dijo á José: En mi sueño parecíame que estaba á la orilla del río:', 'nebula'),
('Old', 'Génesis', 41, 18, 'Y que del río subían siete vacas de gruesas carnes y hermosa apariencia, que pacían en el prado:', 'nebula'),
('Old', 'Génesis', 41, 19, 'Y que otras siete vacas subían después de ellas, flacas y de muy fea traza; tan extenuadas, que no he visto otras semejantes en toda la tierra de Egipto en fealdad:', 'nebula'),
('Old', 'Génesis', 41, 20, 'Y las vacas flacas y feas devoraban á las siete primeras vacas gruesas:', 'nebula'),
('Old', 'Génesis', 41, 21, 'Y entraban en sus entrañas, mas no se conocía que hubiese entrado en ellas, porque su parecer era aún malo, como de primero. Y yo desperté.', 'nebula'),
('Old', 'Génesis', 41, 22, 'Vi también soñando, que siete espigas subían en una misma caña llenas y hermosas;', 'nebula'),
('Old', 'Génesis', 41, 23, 'Y que otras siete espigas menudas, marchitas, abatidas del Solano, subían después de ellas:', 'nebula'),
('Old', 'Génesis', 41, 24, 'Y las espigas menudas devoraban á las siete espigas hermosas: y helo dicho á los magos, mas no hay quien me lo declare.', 'nebula'),
('Old', 'Génesis', 41, 25, 'Entonces respondió José á Faraón: El sueño de Faraón es uno mismo: Dios ha mostrado á Faraón lo que va á hacer.', 'nebula'),
('Old', 'Génesis', 41, 26, 'Las siete vacas hermosas siete años son; y las espigas hermosas son siete años: el sueño es uno mismo.', 'nebula'),
('Old', 'Génesis', 41, 27, 'También las siete vacas flacas y feas que subían tras ellas, son siete años; y las siete espigas menudas y marchitas del Solano, siete años serán de hambre.', 'nebula'),
('Old', 'Génesis', 41, 28, 'Esto es lo que respondo á Faraón. Lo que Dios va á hacer, halo mostrado á Faraón.', 'nebula'),
('Old', 'Génesis', 41, 29, 'He aquí vienen siete años de grande hartura en toda la tierra de Egipto:', 'nebula'),
('Old', 'Génesis', 41, 30, 'Y levantarse han tras ellos siete años de hambre; y toda la hartura será olvidada en la tierra de Egipto; y el hambre consumirá la tierra;', 'nebula'),
('Old', 'Génesis', 41, 31, 'Y aquella abundancia no se echará de ver á causa del hambre siguiente, la cual será gravísima.', 'nebula'),
('Old', 'Génesis', 41, 32, 'Y el suceder el sueño á Faraón dos veces, significa que la cosa es firme de parte de Dios, y que Dios se apresura á hacerla.', 'nebula'),
('Old', 'Génesis', 41, 33, 'Por tanto, provéase ahora Faraón de un varón prudente y sabio, y póngalo sobre la tierra de Egipto.', 'nebula'),
('Old', 'Génesis', 41, 34, 'Haga esto Faraón, y ponga gobernadores sobre el país, y quinte la tierra de Egipto en los siete años de la hartura;', 'nebula'),
('Old', 'Génesis', 41, 35, 'Y junten toda la provisión de estos buenos años que vienen, y alleguen el trigo bajo la mano de Faraón para mantenimiento de las ciudades; y guárdenlo.', 'nebula'),
('Old', 'Génesis', 41, 36, 'Y esté aquella provisión en depósito para el país, para los siete años del hambre que serán en la tierra de Egipto; y el país no perecerá de hambre.', 'nebula'),
('Old', 'Génesis', 41, 37, 'Y el negocio pareció bien á Faraón, y á sus siervos.', 'nebula'),
('Old', 'Génesis', 41, 38, 'Y dijo Faraón á sus siervos: ¿Hemos de hallar otro hombre como éste, en quien haya espíritu de Dios?', 'nebula'),
('Old', 'Génesis', 41, 39, 'Y dijo Faraón á José: Pues que Dios te ha hecho saber todo esto, no hay entendido ni sabio como tú:', 'nebula'),
('Old', 'Génesis', 41, 40, 'Tú serás sobre mi casa, y por tu dicho se gobernará todo mi pueblo: solamente en el trono seré yo mayor que tú.', 'nebula'),
('Old', 'Génesis', 41, 41, 'Dijo más Faraón á José: He aquí yo te he puesto sobre toda la tierra de Egipto.', 'nebula'),
('Old', 'Génesis', 41, 42, 'Entonces Faraón quitó su anillo de su mano, y púsolo en la mano de José, é hízole vestir de ropas de lino finísimo, y puso un collar de oro en su cuello;', 'nebula'),
('Old', 'Génesis', 41, 43, 'E hízolo subir en su segundo carro, y pregonaron delante de él: Doblad la rodilla: y púsole sobre toda la tierra de Egipto.', 'nebula'),
('Old', 'Génesis', 41, 44, 'Y dijo Faraón á José: Yo Faraón; y sin ti ninguno alzará su mano ni su pie en toda la tierra de Egipto.', 'nebula'),
('Old', 'Génesis', 41, 45, 'Y llamó Faraón el nombre de José, Zaphnath-paaneah; y dióle por mujer á Asenath, hija de Potipherah, sacerdote de On. Y salió José por toda la tierra de Egipto.', 'nebula'),
('Old', 'Génesis', 41, 46, 'Y era José de edad de treinta años cuando fué presentado delante de Faraón, rey de Egipto: y salió José de delante de Faraón, y transitó por toda la tierra de Egipto.', 'nebula'),
('Old', 'Génesis', 41, 47, 'E hizo la tierra en aquellos siete años de hartura á montones.', 'nebula'),
('Old', 'Génesis', 41, 48, 'Y él juntó todo el mantenimiento de los siete años que fueron en la tierra de Egipto, y guardó mantenimiento en las ciudades, poniendo en cada ciudad el mantenimiento del campo de sus alrededores.', 'nebula'),
('Old', 'Génesis', 41, 49, 'Y acopió José trigo como arena de la mar, mucho en extremo, hasta no poderse contar, porque no tenía número.', 'nebula'),
('Old', 'Génesis', 41, 50, 'Y nacieron á José dos hijos antes que viniese el primer año del hambre, los cuales le parió Asenath, hija de Potipherah, sacerdote de On.', 'nebula'),
('Old', 'Génesis', 41, 51, 'Y llamó José el nombre del primogénito Manasés; porque Dios (dijo) me hizo olvidar todo mi trabajo, y toda la casa de mi padre.', 'nebula'),
('Old', 'Génesis', 41, 52, 'Y el nombre del segundo llamólo Ephraim; porque Dios (dijo) me hizo fértil en la tierra de mi aflicción.', 'nebula'),
('Old', 'Génesis', 41, 53, 'Y cumpliéronse los siete años de la hartura, que hubo en la tierra de Egipto.', 'nebula'),
('Old', 'Génesis', 41, 54, 'Y comenzaron á venir los siete años del hambre, como José había dicho: y hubo hambre en todos los países, mas en toda la tierra de Egipto había pan.', 'nebula'),
('Old', 'Génesis', 41, 55, 'Y cuando se sintió el hambre en toda la tierra de Egipto, el pueblo clamó á Faraón por pan. Y dijo Faraón á todos los Egipcios: Id á José, y haced lo que él os dijere.', 'nebula'),
('Old', 'Génesis', 41, 56, 'Y el hambre estaba por toda la extensión del país. Entonces abrió José todo granero donde había, y vendía á los Egipcios; porque había crecido el hambre en la tierra de Egipto.', 'nebula'),
('Old', 'Génesis', 41, 57, 'Y toda la tierra venía á Egipto para comprar de José, porque por toda la tierra había crecido el hambre.', 'nebula'),
('Old', 'Génesis', 42, 1, 'Y VIENDO Jacob que en Egipto había alimentos, dijo á sus hijos: ¿Por qué os estáis mirando?', 'nebula'),
('Old', 'Génesis', 42, 2, 'Y dijo: He aquí, yo he oído que hay víveres en Egipto; descended allá, y comprad de allí para nosotros, para que podamos vivir, y no nos muramos.', 'nebula'),
('Old', 'Génesis', 42, 3, 'Y descendieron los diez hermanos de José á comprar trigo á Egipto.', 'nebula'),
('Old', 'Génesis', 42, 4, 'Mas Jacob no envió á Benjamín hermano de José con sus hermanos; porque dijo: No sea acaso que le acontezca algún desastre.', 'nebula'),
('Old', 'Génesis', 42, 5, 'Y vinieron los hijos de Israel á comprar entre los que venían: porque había hambre en la tierra de Canaán.', 'nebula'),
('Old', 'Génesis', 42, 6, 'Y José era el señor de la tierra, que vendía á todo el pueblo de la tierra: y llegaron los hermanos de José, é inclináronse á él rostro por tierra.', 'nebula'),
('Old', 'Génesis', 42, 7, 'Y José como vió á sus hermanos, conociólos; mas hizo que no los conocía, y hablóles ásperamente, y les dijo: ¿De dónde habéis venido? Ellos respondieron: De la tierra de Canaán á comprar alimentos.', 'nebula'),
('Old', 'Génesis', 42, 8, 'José, pues, conoció á sus hermanos; pero ellos no le conocieron.', 'nebula'),
('Old', 'Génesis', 42, 9, 'Entonces se acordó José de los sueños que había tenido de ellos, y díjoles: Espías sois; por ver lo descubierto del país habéis venido.', 'nebula'),
('Old', 'Génesis', 42, 10, 'Y ellos le respondieron: No, señor mío: mas tus siervos han venido á comprar alimentos.', 'nebula'),
('Old', 'Génesis', 42, 11, 'Todos nosotros somos hijos de un varón: somos hombres de verdad: tus siervos nunca fueron espías.', 'nebula'),
('Old', 'Génesis', 42, 12, 'Y él les dijo: No; á ver lo descubierto del país habéis venido.', 'nebula'),
('Old', 'Génesis', 42, 13, 'Y ellos respondieron: Tus siervos somos doce hermanos, hijos de un varón en la tierra de Canaán; y he aquí el menor está hoy con nuestro padre, y otro no parece.', 'nebula'),
('Old', 'Génesis', 42, 14, 'Y José les dijo: Eso es lo que os he dicho, afirmando que sois espías:', 'nebula'),
('Old', 'Génesis', 42, 15, 'En esto seréis probados: Vive Faraón que no saldréis de aquí, sino cuando vuestro hermano menor aquí viniere.', 'nebula'),
('Old', 'Génesis', 42, 16, 'Enviad uno de vosotros, y traiga á vuestro hermano; y vosotros quedad presos, y vuestras palabras serán probadas, si hay verdad con vosotros: y si no, vive Faraón, que sois espías.', 'nebula'),
('Old', 'Génesis', 42, 17, 'Y juntólos en la cárcel por tres días.', 'nebula'),
('Old', 'Génesis', 42, 18, 'Y al tercer día díjoles José: Haced esto, y vivid: Yo temo á Dios:', 'nebula'),
('Old', 'Génesis', 42, 19, 'Si sois hombres de verdad, quede preso en la casa de vuestra cárcel uno de vuestros hermanos; y vosotros id, llevad el alimento para el hambre de vuestra casa:', 'nebula'),
('Old', 'Génesis', 42, 20, 'Pero habéis de traerme á vuestro hermano menor, y serán verificadas vuestras palabras, y no moriréis. Y ellos lo hicieron así.', 'nebula'),
('Old', 'Génesis', 42, 21, 'Y decían el uno al otro: Verdaderamente hemos pecado contra nuestro hermano, que vimos la angustia de su alma cuando nos rogaba, y no le oímos: por eso ha venido sobre nosotros esta angustia.', 'nebula'),
('Old', 'Génesis', 42, 22, 'Entonces Rubén les respondió, diciendo: ¿No os hablé yo y dije: No pequéis contra el mozo; y no escuchásteis? He aquí también su sangre es requerida.', 'nebula'),
('Old', 'Génesis', 42, 23, 'Y ellos no sabían que los entendía José, porque había intérprete entre ellos.', 'nebula'),
('Old', 'Génesis', 42, 24, 'Y apartóse él de ellos, y lloró: después volvió á ellos, y les habló, y tomó de entre ellos á Simeón, y aprisionóle á vista de ellos.', 'nebula'),
('Old', 'Génesis', 42, 25, 'Y mandó José que llenaran sus sacos de trigo, y devolviesen el dinero de cada uno de ellos, poniéndolo en su saco, y les diesen comida para el camino: é hízose así con ellos.', 'nebula'),
('Old', 'Génesis', 42, 26, 'Y ellos pusieron su trigo sobre sus asnos, y fuéronse de allí.', 'nebula'),
('Old', 'Génesis', 42, 27, 'Y abriendo uno de ellos su saco para dar de comer á su asno en el mesón, vió su dinero que estaba en la boca de su costal.', 'nebula'),
('Old', 'Génesis', 42, 28, 'Y dijo á sus hermanos: Mi dinero se me ha devuelto, y aun helo aquí en mi saco. Sobresaltóseles entonces el corazón, y espantados dijeron el uno al otro: ¿Qué es esto que nos ha hecho Dios?', 'nebula'),
('Old', 'Génesis', 42, 29, 'Y venidos á Jacob su padre en tierra de Canaán, contáronle todo lo que les había acaecido, diciendo:', 'nebula'),
('Old', 'Génesis', 42, 30, 'Aquel varón, señor de la tierra, nos habló ásperamente, y nos trató como á espías de la tierra:', 'nebula'),
('Old', 'Génesis', 42, 31, 'Y nosotros le dijimos: Somos hombres de verdad, nunca fuimos espías:', 'nebula'),
('Old', 'Génesis', 42, 32, 'Somos doce hermanos, hijos de nuestro padre; uno no parece, y el menor está hoy con nuestro padre en la tierra de Canaán.', 'nebula'),
('Old', 'Génesis', 42, 33, 'Y aquel varón, señor de la tierra, nos dijo: En esto conoceré que sois hombres de verdad; dejad conmigo uno de vuestros hermanos, y tomad para el hambre de vuestras casas, y andad,', 'nebula'),
('Old', 'Génesis', 42, 34, 'Y traedme á vuestro hermano el menor, para que yo sepa que no sois espías, sino hombres de verdad: así os daré á vuestro hermano, y negociaréis en la tierra.', 'nebula'),
('Old', 'Génesis', 42, 35, 'Y aconteció que vaciando ellos sus sacos, he aquí que en el saco de cada uno estaba el atado de su dinero: y viendo ellos y su padre los atados de su dinero, tuvieron temor.', 'nebula'),
('Old', 'Génesis', 42, 36, 'Entonces su padre Jacob les dijo: Habéisme privado de mis hijos; José no parece, ni Simeón tampoco, y á Benjamín le llevaréis: contra mí son todas estas cosas.', 'nebula'),
('Old', 'Génesis', 42, 37, 'Y Rubén habló á su padre, diciendo: Harás morir á mis dos hijos, si no te lo volviere; entrégalo en mi mano, que yo lo volveré á ti.', 'nebula'),
('Old', 'Génesis', 42, 38, 'Y él dijo: No descenderá mi hijo con vosotros; que su hermano es muerto, y él solo ha quedado: y si le aconteciere algún desastre en el camino por donde vais, haréis descender mis canas con dolor á la sepultura.', 'nebula'),
('Old', 'Génesis', 43, 1, 'Y EL hambre era grande en la tierra.', 'nebula'),
('Old', 'Génesis', 43, 2, 'Y aconteció que como acabaron de comer el trigo que trajeron de Egipto, díjoles su padre: Volved, y comprad para nosotros un poco de alimento.', 'nebula'),
('Old', 'Génesis', 43, 3, 'Y respondió Judá, diciendo: Aquel varón nos protestó con ánimo resuelto, diciendo: No veréis mi rostro sin vuestro hermano con vosotros.', 'nebula'),
('Old', 'Génesis', 43, 4, 'Si enviares á nuestro hermano con nosotros, descenderemos y te compraremos alimento:', 'nebula'),
('Old', 'Génesis', 43, 5, 'Pero si no le enviares, no descenderemos: porque aquel varón nos dijo: No veréis mi rostro sin vuestro hermano con vosotros.', 'nebula'),
('Old', 'Génesis', 43, 6, 'Y dijo Israel: ¿Por qué me hicisteis tanto mal, declarando al varón que teníais más hermano?', 'nebula'),
('Old', 'Génesis', 43, 7, 'Y ellos respondieron: Aquel varón nos preguntó expresamente por nosotros, y por nuestra parentela, diciendo: ¿Vive aún vuestro padre? ¿tenéis otro hermano? y declarámosle conforme á estas palabras. ¿Podíamos nosotros saber que había de decir: Haced venir á vuestro hermano?', 'nebula'),
('Old', 'Génesis', 43, 8, 'Entonces Judá dijo á Israel su padre: Envía al mozo conmigo, y nos levantaremos é iremos, á fin que vivamos y no muramos nosotros, y tú, y nuestros niños.', 'nebula'),
('Old', 'Génesis', 43, 9, 'Yo lo fío; á mí me pedirás cuenta de él: si yo no te lo volviere y lo pusiere delante de ti, seré para ti el culpante todos los días:', 'nebula'),
('Old', 'Génesis', 43, 10, 'Que si no nos hubiéramos detenido, cierto ahora hubiéramos ya vuelto dos veces.', 'nebula'),
('Old', 'Génesis', 43, 11, 'Entonces Israel su padre les respondió: Pues que así es, hacedlo; tomad de lo mejor de la tierra en vuestros vasos, y llevad á aquel varón un presente, un poco de bálsamo, y un poco de miel, aromas y mirra, nueces y almendras.', 'nebula'),
('Old', 'Génesis', 43, 12, 'Y tomad en vuestras manos doblado dinero, y llevad en vuestra mano el dinero vuelto en las bocas de vuestros costales; quizá fué yerro.', 'nebula'),
('Old', 'Génesis', 43, 13, 'Tomad también á vuestro hermano, y levantaos, y volved á aquel varón.', 'nebula'),
('Old', 'Génesis', 43, 14, 'Y el Dios Omnipotente os dé misericordias delante de aquel varón, y os suelte al otro vuestro hermano, y á este Benjamín. Y si he de ser privado de mis hijos, séalo.', 'nebula'),
('Old', 'Génesis', 43, 15, 'Entonces tomaron aquellos varones el presente, y tomaron en su mano doblado dinero, y á Benjamín; y se levantaron, y descendieron á Egipto, y presentáronse delante de José.', 'nebula'),
('Old', 'Génesis', 43, 16, 'Y vió José á Benjamín con ellos, y dijo al mayordomo de su casa: Mete en casa á esos hombres, y degüella víctima, y aderéza la; porque estos hombres comerán conmigo al medio día.', 'nebula'),
('Old', 'Génesis', 43, 17, 'E hizo el hombre como José dijo; y metió aquel hombre á los hombres en casa de José.', 'nebula'),
('Old', 'Génesis', 43, 18, 'Y aquellos hombres tuvieron temor, cuando fueron metidos en casa de José, y decían: Por el dinero que fué vuelto en nuestros costales la primera vez nos han metido aquí, para revolver contra nosotros, y dar sobre nosotros, y tomarnos por siervos á nosotros, y á nuestros asnos.', 'nebula'),
('Old', 'Génesis', 43, 19, 'Y llegáronse al mayordomo de la casa de José, y le hablaron á la entrada de la casa.', 'nebula'),
('Old', 'Génesis', 43, 20, 'Y dijeron: Ay, señor mío, nosotros en realidad de verdad descendimos al principio á comprar alimentos:', 'nebula'),
('Old', 'Génesis', 43, 21, 'Y aconteció que como vinimos al mesón y abrimos nuestros costales, he aquí el dinero de cada uno estaba en la boca de su costal, nuestro dinero en su justo peso; y hémoslo vuelto en nuestras manos.', 'nebula'),
('Old', 'Génesis', 43, 22, 'Hemos también traído en nuestras manos otro dinero para comprar alimentos: nosotros no sabemos quién haya puesto nuestro dinero en nuestros costales.', 'nebula'),
('Old', 'Génesis', 43, 23, 'Y él respondió: Paz á vosotros, no temáis; vuestro Dios y el Dios de vuestro padre os dió el tesoro en vuestros costales: vuestro dinero vino á mí. Y sacó á Simeón á ellos.', 'nebula'),
('Old', 'Génesis', 43, 24, 'Y metió aquel varón á aquellos hombres en casa de José: y dióles agua, y lavaron sus pies: y dió de comer á sus asnos.', 'nebula'),
('Old', 'Génesis', 43, 25, 'Y ellos prepararon el presente entretanto que venía José al medio día, porque habían oído que allí habían de comer pan.', 'nebula'),
('Old', 'Génesis', 43, 26, 'Y vino José á casa, y ellos le trajeron el presente que tenían en su mano dentro de casa, é inclináronse á él hasta tierra.', 'nebula'),
('Old', 'Génesis', 43, 27, 'Entonces les preguntó él cómo estaban, y dijo: ¿Vuestro padre, el anciano que dijisteis, lo pasa bien? ¿vive todavía?', 'nebula'),
('Old', 'Génesis', 43, 28, 'Y ellos respondieron: Bien va á tu siervo nuestro padre; aun vive. Y se inclinaron, é hicieron reverencia.', 'nebula'),
('Old', 'Génesis', 43, 29, 'Y alzando él sus ojos vió á Benjamín su hermano, hijo de su madre, y dijo: ¿Es éste vuestro hermano menor, de quien me hablasteis? Y dijo: Dios tenga misericordia de ti, hijo mío.', 'nebula'),
('Old', 'Génesis', 43, 30, 'Entonces José se apresuró, porque se conmovieron sus entrañas á causa de su hermano, y procuró donde llorar: y entróse en su cámara, y lloró allí.', 'nebula'),
('Old', 'Génesis', 43, 31, 'Y lavó su rostro, y salió fuera, y reprimióse, y dijo: Poned pan.', 'nebula'),
('Old', 'Génesis', 43, 32, 'Y pusieron para él aparte, y separadamente para ellos, y aparte para los Egipcios que con él comían: porque los Egipcios no pueden comer pan con los Hebreos, lo cual es abominación á los Egipcios.', 'nebula'),
('Old', 'Génesis', 43, 33, 'Y sentáronse delante de él, el mayor conforme á su mayoría, y el menor conforme á su menoría; y estaban aquellos hombres atónitos mirándose el uno al otro.', 'nebula'),
('Old', 'Génesis', 43, 34, 'Y él tomó viandas de delante de sí para ellos; mas la porción de Benjamín era cinco veces como cualquiera de las de ellos. Y bebieron, y alegráronse con él.', 'nebula'),
('Old', 'Génesis', 44, 1, 'Y MANDO José al mayordomo de su casa, diciendo: Hinche los costales de aquestos varones de alimentos, cuanto pudieren llevar, y pon el dinero de cada uno en la boca de su costal:', 'nebula'),
('Old', 'Génesis', 44, 2, 'Y pondrás mi copa, la copa de plata, en la boca del costal del menor, con el dinero de su trigo. Y él hizo como dijo José.', 'nebula'),
('Old', 'Génesis', 44, 3, 'Venida la mañana, los hombres fueron despedidos con sus asnos.', 'nebula'),
('Old', 'Génesis', 44, 4, 'Habiendo ellos salido de la ciudad, de la que aun no se habían alejado, dijo José á su mayordomo: Levántate, y sigue á esos hombres; y cuando los alcanzares, diles: ¿Por qué habéis vuelto mal por bien?', 'nebula'),
('Old', 'Génesis', 44, 5, '¿No es ésta en la que bebe mi señor, y por la que suele adivinar? habéis hecho mal en lo que hicisteis.', 'nebula'),
('Old', 'Génesis', 44, 6, 'Y como él los alcanzó, díjoles estas palabras.', 'nebula'),
('Old', 'Génesis', 44, 7, 'Y ellos le respondieron: ¿Por qué dice mi señor tales cosas? Nunca tal hagan tus siervos.', 'nebula'),
('Old', 'Génesis', 44, 8, 'He aquí, el dinero que hallamos en la boca de nuestros costales, te lo volvimos á traer desde la tierra de Canaán; ¿cómo, pues, habíamos de hurtar de casa de tu señor plata ni oro?', 'nebula'),
('Old', 'Génesis', 44, 9, 'Aquel de tus siervos en quien fuere hallada la copa, que muera, y aun nosotros seremos siervos de mi señor.', 'nebula'),
('Old', 'Génesis', 44, 10, 'Y él dijo: También ahora sea conforme á vuestras palabras; aquél en quien se hallare, será mi siervo, y vosotros seréis sin culpa.', 'nebula'),
('Old', 'Génesis', 44, 11, 'Ellos entonces se dieron prisa, y derribando cada uno su costal en tierra, abrió cada cual el costal suyo.', 'nebula'),
('Old', 'Génesis', 44, 12, 'Y buscó; desde el mayor comenzó, y acabó en el menor; y la copa fué hallada en el costal de Benjamín.', 'nebula'),
('Old', 'Génesis', 44, 13, 'Entonces ellos rasgaron sus vestidos, y cargó cada uno su asno, y volvieron á la ciudad.', 'nebula'),
('Old', 'Génesis', 44, 14, 'Y llegó Judá con sus hermanos á casa de José, que aun estaba allí, y postráronse delante de él en tierra.', 'nebula'),
('Old', 'Génesis', 44, 15, 'Y díjoles José: ¿Qué obra es esta que habéis hecho? ¿no sabéis que un hombre como yo sabe adivinar?', 'nebula'),
('Old', 'Génesis', 44, 16, 'Entonces dijo Judá: ¿Qué diremos á mi señor? ¿qué hablaremos? ¿ó con qué nos justificaremos? Dios ha hallado la maldad de tus siervos: he aquí, nosotros somos siervos de mi señor, nosotros, y también aquél en cuyo poder fué hallada la copa.', 'nebula'),
('Old', 'Génesis', 44, 17, 'Y él respondió: Nunca yo tal haga: el varón en cuyo poder fué hallada la copa, él será mi siervo; vosotros id en paz á vuestro padre.', 'nebula'),
('Old', 'Génesis', 44, 18, 'Entonces Judá se llegó á él, y dijo: Ay señor mío, ruégote que hable tu siervo una palabra en oídos de mi señor, y no se encienda tu enojo contra tu siervo, pues que tú eres como Faraón.', 'nebula'),
('Old', 'Génesis', 44, 19, 'Mi señor preguntó á sus siervos, diciendo: ¿Tenéis padre ó hermano?', 'nebula'),
('Old', 'Génesis', 44, 20, 'Y nosotros respondimos á mi señor: Tenemos un padre anciano, y un mozo que le nació en su vejez, pequeño aún; y un hermano suyo murió, y él quedó solo de su madre, y su padre lo ama.', 'nebula'),
('Old', 'Génesis', 44, 21, 'Y tú dijiste á tus siervos: Traédmelo, y pondré mis ojos sobre él.', 'nebula'),
('Old', 'Génesis', 44, 22, 'Y nosotros dijimos á mi señor: El mozo no puede dejar á su padre, porque si le dejare, su padre morirá.', 'nebula'),
('Old', 'Génesis', 44, 23, 'Y dijiste á tus siervos: Si vuestro hermano menor no descendiere con vosotros, no veáis más mi rostro.', 'nebula'),
('Old', 'Génesis', 44, 24, 'Aconteció pues, que como llegamos á mi padre tu siervo, contámosle las palabras de mi señor.', 'nebula'),
('Old', 'Génesis', 44, 25, 'Y dijo nuestro padre: Volved á comprarnos un poco de alimento.', 'nebula'),
('Old', 'Génesis', 44, 26, 'Y nosotros respondimos: No podemos ir: si nuestro hermano fuere con nosotros, iremos; porque no podemos ver el rostro del varón, no estando con nosotros nuestro hermano el menor.', 'nebula'),
('Old', 'Génesis', 44, 27, 'Entonces tu siervo mi padre nos dijo: Vosotros sabéis que dos me parió mi mujer;', 'nebula'),
('Old', 'Génesis', 44, 28, 'Y el uno salió de conmigo, y pienso de cierto que fué despedazado, y hasta ahora no le he visto;', 'nebula'),
('Old', 'Génesis', 44, 29, 'Y si tomareis también éste de delante de mí, y le aconteciere algún desastre, haréis descender mis canas con dolor á la sepultura.', 'nebula'),
('Old', 'Génesis', 44, 30, 'Ahora, pues, cuando llegare yo á tu siervo mi padre, y el mozo no fuere conmigo, como su alma está ligada al alma de él,', 'nebula'),
('Old', 'Génesis', 44, 31, 'Sucederá que cuando no vea al mozo, morirá: y tus siervos harán descender las canas de tu siervo nuestro padre con dolor á la sepultura.', 'nebula'),
('Old', 'Génesis', 44, 32, 'Como tu siervo salió por fiador del mozo con mi padre, diciendo: Si no te lo volviere, entonces yo seré culpable para mi padre todos los días;', 'nebula'),
('Old', 'Génesis', 44, 33, 'Ruégote por tanto que quede ahora tu siervo por el mozo por siervo de mi señor, y que el mozo vaya con sus hermanos.', 'nebula'),
('Old', 'Génesis', 44, 34, 'Porque ¿cómo iré yo á mi padre sin el mozo? No podré, por no ver el mal que sobrevendrá á mi padre.', 'nebula'),
('Old', 'Génesis', 45, 1, 'NO podía ya José contenerse delante de todos los que estaban al lado suyo, y clamó: Haced salir de conmigo á todos. Y no quedó nadie con él, al darse á conocer José á sus hermanos.', 'nebula'),
('Old', 'Génesis', 45, 2, 'Entonces se dió á llorar á voz en grito; y oyeron los Egipcios, y oyó también la casa de Faraón.', 'nebula'),
('Old', 'Génesis', 45, 3, 'Y dijo José á sus hermanos: Yo soy José: ¿vive aún mi padre? Y sus hermanos no pudieron responderle, porque estaban turbados delante de él.', 'nebula'),
('Old', 'Génesis', 45, 4, 'Entonces dijo José á sus hermanos: Llegaos ahora á mí. Y ellos se llegaron. Y él dijo: Yo soy José vuestro hermano el que vendisteis para Egipto.', 'nebula'),
('Old', 'Génesis', 45, 5, 'Ahora pues, no os entristezcáis, ni os pese de haberme vendido acá; que para preservación de vida me envió Dios delante de vosotros:', 'nebula'),
('Old', 'Génesis', 45, 6, 'Que ya ha habido dos años de hambre en medio de la tierra, y aun quedan cinco años en que ni habrá arada ni siega.', 'nebula'),
('Old', 'Génesis', 45, 7, 'Y Dios me envió delante de vosotros, para que vosotros quedaseis en la tierra, y para daros vida por medio de grande salvamento.', 'nebula'),
('Old', 'Génesis', 45, 8, 'Así pues, no me enviasteis vosotros acá, sino Dios, que me ha puesto por padre de Faraón, y por señor de toda su casa, y por gobernador en toda la tierra de Egipto.', 'nebula'),
('Old', 'Génesis', 45, 9, 'Daos priesa, id á mi padre y decidle: Así dice tu hijo José: Dios me ha puesto por señor de todo Egipto; ven á mí, no te detengas:', 'nebula'),
('Old', 'Génesis', 45, 10, 'Y habitarás en la tierra de Gosén, y estarás cerca de mí, tú y tus hijos, y los hijos de tus hijos, tus ganados y tus vacas, y todo lo que tienes.', 'nebula'),
('Old', 'Génesis', 45, 11, 'Y allí te alimentaré, pues aun quedan cinco años de hambre, porque no perezcas de pobreza tú y tu casa, y todo lo que tienes:', 'nebula'),
('Old', 'Génesis', 45, 12, 'Y he aquí, vuestros ojos ven, y los ojos de mi hermano Benjamín, que mi boca os habla.', 'nebula'),
('Old', 'Génesis', 45, 13, 'Haréis pues saber á mi padre toda mi gloria en Egipto, y todo lo que habéis visto: y daos priesa, y traed á mi padre acá.', 'nebula'),
('Old', 'Génesis', 45, 14, 'Y echóse sobre el cuello de Benjamín su hermano, y lloró; y también Benjamín lloró sobre su cuello.', 'nebula'),
('Old', 'Génesis', 45, 15, 'Y besó á todos sus hermanos, y lloró sobre ellos: y después sus hermanos hablaron con él.', 'nebula'),
('Old', 'Génesis', 45, 16, 'Y oyóse la noticia en la casa de Faraón, diciendo: Los hermanos de José han venido. Y plugo en los ojos de Faraón y de sus siervos.', 'nebula'),
('Old', 'Génesis', 45, 17, 'Y dijo Faraón á José: Di á tus hermanos: Haced esto: cargad vuestras bestias, é id, volved á la tierra de Canaán;', 'nebula'),
('Old', 'Génesis', 45, 18, 'Y tomad á vuestro padre y vuestras familias, y venid á mí, que yo os daré lo bueno de la tierra de Egipto y comeréis la grosura de la tierra.', 'nebula'),
('Old', 'Génesis', 45, 19, 'Y tú manda: Haced esto: tomaos de la tierra de Egipto carros para vuestros niños y vuestras mujeres; y tomad á vuestro padre, y venid.', 'nebula'),
('Old', 'Génesis', 45, 20, 'Y no se os dé nada de vuestras alhajas, porque el bien de la tierra de Egipto será vuestro.', 'nebula'),
('Old', 'Génesis', 45, 21, 'E hiciéronlo así los hijos de Israel: y dióles José carros conforme á la orden de Faraón, y suministróles víveres para el camino.', 'nebula'),
('Old', 'Génesis', 45, 22, 'A cada uno de todos ellos dió mudas de vestidos, y á Benjamín dió trescientas piezas de plata, y cinco mudas de vestidos.', 'nebula'),
('Old', 'Génesis', 45, 23, 'Y á su padre envió esto: diez asnos cargados de lo mejor de Egipto, y diez asnas cargadas de trigo, y pan y comida, para su padre en el camino.', 'nebula'),
('Old', 'Génesis', 45, 24, 'Y despidió á sus hermanos, y fuéronse. Y él les dijo: No riñáis por el camino.', 'nebula'),
('Old', 'Génesis', 45, 25, 'Y subieron de Egipto, y llegaron á la tierra de Canaán á Jacob su padre.', 'nebula'),
('Old', 'Génesis', 45, 26, 'Y diéronle las nuevas, diciendo: José vive aún; y él es señor en toda la tierra de Egipto. Y su corazón se desmayó; pues no los creía.', 'nebula'),
('Old', 'Génesis', 45, 27, 'Y ellos le contaron todas las palabras de José, que él les había hablado; y viendo él los carros que José enviaba para llevarlo, el espíritu de Jacob su padre revivió.', 'nebula'),
('Old', 'Génesis', 45, 28, 'Entonces dijo Israel: Basta; José mi hijo vive todavía: iré, y le veré antes que yo muera.', 'nebula'),
('Old', 'Génesis', 46, 1, 'Y PARTIOSE Israel con todo lo que tenía, y vino á Beer-seba, y ofreció sacrificios al Dios de su padre Isaac.', 'nebula'),
('Old', 'Génesis', 46, 2, 'Y habló Dios á Israel en visiones de noche, y dijo: Jacob, Jacob. Y él respondió: Heme aquí.', 'nebula'),
('Old', 'Génesis', 46, 3, 'Y dijo: Yo soy Dios, el Dios de tu padre; no temas de descender á Egipto, porque yo te pondré allí en gran gente.', 'nebula'),
('Old', 'Génesis', 46, 4, 'Yo descenderé contigo á Egipto, y yo también te haré volver: y José pondrá su mano sobre tus ojos.', 'nebula'),
('Old', 'Génesis', 46, 5, 'Y levantóse Jacob de Beer-seba; y tomaron los hijos de Israel á su padre Jacob, y á sus niños, y á sus mujeres, en los carros que Faraón había enviado para llevarlo.', 'nebula'),
('Old', 'Génesis', 46, 6, 'Y tomaron sus ganados, y su hacienda que había adquirido en la tierra de Canaán, y viniéronse á Egipto, Jacob, y toda su simiente consigo;', 'nebula'),
('Old', 'Génesis', 46, 7, 'Sus hijos, y los hijos de sus hijos consigo; sus hijas, y las hijas de sus hijos, y á toda su simiente trajo consigo á Egipto.', 'nebula'),
('Old', 'Génesis', 46, 8, 'Y estos son los nombres de los hijos de Israel, que entraron en Egipto, Jacob y sus hijos: Rubén, el primogénito de Jacob.', 'nebula'),
('Old', 'Génesis', 46, 9, 'Y los hijos de Rubén: Hanoch, y Phallu, y Hezrón, y Carmi.', 'nebula'),
('Old', 'Génesis', 46, 10, 'Y los hijos de Simeón: Jemuel, y Jamín, y Ohad, y Jachîn, y Zohar, y Saúl, hijo de la Cananea.', 'nebula'),
('Old', 'Génesis', 46, 11, 'Y los hijos de Leví: Gersón, Coath, y Merari.', 'nebula'),
('Old', 'Génesis', 46, 12, 'Y los hijos de Judá: Er, y Onán, y Sela, y Phares, y Zara: mas Er y Onán, murieron en la tierra de Canaán. Y los hijos de Phares fueron Hezrón y Hamul.', 'nebula'),
('Old', 'Génesis', 46, 13, 'Y los hijos de Issachâr: Thola, y Phua, y Job, y Simrón.', 'nebula'),
('Old', 'Génesis', 46, 14, 'Y los hijos de Zabulón: Sered y Elón, y Jahleel.', 'nebula'),
('Old', 'Génesis', 46, 15, 'Estos fueron los hijos de Lea, los que parió á Jacob en Padan-aram, y además su hija Dina: treinta y tres las almas todas de sus hijos é hijas.', 'nebula'),
('Old', 'Génesis', 46, 16, 'Y los hijos de Gad: Ziphión, y Aggi, y Ezbón, y Suni, y Heri, y Arodi, y Areli.', 'nebula'),
('Old', 'Génesis', 46, 17, 'Y los hijos de Aser: Jimna, é Ishua, é Isui y Beria, y Sera, hermana de ellos. Los hijos de Beria: Heber, y Malchîel.', 'nebula'),
('Old', 'Génesis', 46, 18, 'Estos fueron los hijos de Zilpa, la que Labán dió á su hija Lea, y parió estos á Jacob; todas diez y seis almas.', 'nebula'),
('Old', 'Génesis', 46, 19, 'Y los hijos de Rachêl, mujer de Jacob: José y Benjamín.', 'nebula'),
('Old', 'Génesis', 46, 20, 'Y nacieron á José en la tierra de Egipto Manasés y Ephraim, los que le parió Asenath, hija de Potipherah, sacerdote de On.', 'nebula'),
('Old', 'Génesis', 46, 21, 'Y los hijos de Benjamín fueron Bela, y Bechêr y Asbel, y Gera, y Naamán, y Ehi, y Ros y Muppim, y Huppim, y Ard.', 'nebula'),
('Old', 'Génesis', 46, 22, 'Estos fueron los hijos de Rachêl, que nacieron á Jacob: en todas, catorce almas.', 'nebula'),
('Old', 'Génesis', 46, 23, 'Y los hijos de Dan: Husim.', 'nebula'),
('Old', 'Génesis', 46, 24, 'Y los hijos de Nephtalí: Jahzeel, y Guni, y Jezer, y Shillem.', 'nebula'),
('Old', 'Génesis', 46, 25, 'Estos fueron los hijos de Bilha, la que dió Labán á Rachêl su hija, y parió estos á Jacob; todas siete almas.', 'nebula'),
('Old', 'Génesis', 46, 26, 'Todas las personas que vinieron con Jacob á Egipto, procedentes de sus lomos, sin las mujeres de los hijos de Jacob, todas las personas fueron sesenta y seis.', 'nebula'),
('Old', 'Génesis', 46, 27, 'Y los hijos de José, que le nacieron en Egipto, dos personas. Todas las almas de la casa de Jacob, que entraron en Egipto, fueron setenta.', 'nebula'),
('Old', 'Génesis', 46, 28, 'Y envió á Judá delante de sí á José, para que le viniese á ver á Gosén; y llegaron á la tierra de Gosén.', 'nebula'),
('Old', 'Génesis', 46, 29, 'Y José unció su carro y vino á recibir á Israel su padre á Gosén; y se manifestó á él, y echóse sobre su cuello, y lloró sobre su cuello bastante.', 'nebula'),
('Old', 'Génesis', 46, 30, 'Entonces Israel dijo á José: Muera yo ahora, ya que he visto tu rostro, pues aun vives.', 'nebula'),
('Old', 'Génesis', 46, 31, 'Y José dijo á sus hermanos, y á la casa de su padre: Subiré y haré saber á Faraón, y diréle: Mis hermanos y la casa de mi padre, que estaban en la tierra de Canaán, han venido á mí;', 'nebula'),
('Old', 'Génesis', 46, 32, 'Y los hombres son pastores de ovejas, porque son hombres ganaderos: y han traído sus ovejas y sus vacas, y todo lo que tenían.', 'nebula'),
('Old', 'Génesis', 46, 33, 'Y cuando Faraón os llamare y dijere: ¿cuál es vuestro oficio?', 'nebula'),
('Old', 'Génesis', 46, 34, 'Entonces diréis: Hombres de ganadería han sido tus siervos desde nuestra mocedad hasta ahora, nosotros y nuestros padres; á fin que moréis en la tierra de Gosén, porque los Egipcios abominan todo pastor de ovejas.', 'nebula'),
('Old', 'Génesis', 47, 1, 'Y JOSÉ vino, é hizo saber á Faraón, y dijo: Mi padre y mis hermanos, y sus ovejas y sus vacas, con todo lo que tienen, han venido de la tierra de Canaán, y he aquí, están en la tierra de Gosén.', 'nebula'),
('Old', 'Génesis', 47, 2, 'Y de los postreros de sus hermanos tomó cinco varones, y presentólos delante de Faraón.', 'nebula'),
('Old', 'Génesis', 47, 3, 'Y Faraón dijo á sus hermanos: ¿Cuál es vuestro oficio? Y ellos respondieron á Faraón: Pastores de ovejas son tus siervos, así nosotros como nuestros padres.', 'nebula'),
('Old', 'Génesis', 47, 4, 'Dijeron además á Faraón: Por morar en esta tierra hemos venido; porque no hay pasto para las ovejas de tus siervos, pues el hambre es grave en la tierra de Canaán: por tanto, te rogamos ahora que habiten tus siervos en la tierra de Gosén.', 'nebula'),
('Old', 'Génesis', 47, 5, 'Entonces Faraón habló á José, diciendo: Tu padre y tus hermanos han venido á ti;', 'nebula'),
('Old', 'Génesis', 47, 6, 'La tierra de Egipto delante de ti está; en lo mejor de la tierra haz habitar á tu padre y á tus hermanos; habiten en la tierra de Gosén; y si entiendes que hay entre ellos hombres eficaces, ponlos por mayorales del ganado mío.', 'nebula'),
('Old', 'Génesis', 47, 7, 'Y José introdujo á su padre, y presentólo delante de Faraón; y Jacob bendijo á Faraón.', 'nebula'),
('Old', 'Génesis', 47, 8, 'Y dijo Faraón á Jacob: ¿Cuántos son los días de los años de tu vida?', 'nebula'),
('Old', 'Génesis', 47, 9, 'Y Jacob respondió á Faraón: Los días de los años de mi peregrinación son ciento treinta años; pocos y malos han sido los días de los años de mi vida, y no han llegado á los días de los años de la vida de mis padres en los días de su peregrinación.', 'nebula'),
('Old', 'Génesis', 47, 10, 'Y Jacob bendijo á Faraón, y salióse de delante de Faraón.', 'nebula'),
('Old', 'Génesis', 47, 11, 'Así José hizo habitar á su padre y á sus hermanos, y dióles posesión en la tierra de Egipto, en lo mejor de la tierra, en la tierra de Rameses como mandó Faraón.', 'nebula'),
('Old', 'Génesis', 47, 12, 'Y alimentaba José á su padre y á sus hermanos, y á toda la casa de su padre, de pan, hasta la boca del niño.', 'nebula'),
('Old', 'Génesis', 47, 13, 'Y no había pan en toda la tierra, y el hambre era muy grave; por lo que desfalleció de hambre la tierra de Egipto y la tierra de Canaán.', 'nebula'),
('Old', 'Génesis', 47, 14, 'Y recogió José todo el dinero que se halló en la tierra de Egipto y en la tierra de Canaán, por los alimentos que de él compraban; y metió José el dinero en casa de Faraón.', 'nebula'),
('Old', 'Génesis', 47, 15, 'Y acabado el dinero de la tierra de Egipto y de la tierra de Canaán, vino todo Egipto á José diciendo: Danos pan: ¿por qué moriremos delante de ti, por haberse acabado el dinero?', 'nebula'),
('Old', 'Génesis', 47, 16, 'Y José dijo: Dad vuestros ganados, y yo os daré por vuestros ganados, si se ha acabado el dinero.', 'nebula'),
('Old', 'Génesis', 47, 17, 'Y ellos trajeron sus ganados á José; y José les dió alimentos por caballos, y por el ganado de las ovejas, y por el ganado de las vacas, y por asnos: y sustentólos de pan por todos sus ganados aquel año.', 'nebula'),
('Old', 'Génesis', 47, 18, 'Y acabado aquel año, vinieron á él el segundo año, y le dijeron: No encubriremos á nuestro señor que el dinero ciertamente se ha acabado; también el ganado es ya de nuestro señor; nada ha quedado delante de nuestro señor sino nuestros cuerpos y nuestra tierra.', 'nebula'),
('Old', 'Génesis', 47, 19, '¿Por qué moriremos delante de tus ojos, así nosotros como nuestra tierra? Cómpranos á nosotros y á nuestra tierra por pan, y seremos nosotros y nuestra tierra siervos de Faraón: y danos simiente para que vivamos y no muramos, y no sea asolada la tierra.', 'nebula'),
('Old', 'Génesis', 47, 20, 'Entonces compró José toda la tierra de Egipto para Faraón; pues los Egipcios vendieron cada uno sus tierras, porque se agravó el hambre sobre ellos: y la tierra vino á ser de Faraón.', 'nebula'),
('Old', 'Génesis', 47, 21, 'Y al pueblo hízolo pasar á las ciudades desde el un cabo del término de Egipto hasta el otro cabo.', 'nebula'),
('Old', 'Génesis', 47, 22, 'Solamente la tierra de los sacerdotes no compró, por cuanto los sacerdotes tenían ración de Faraón, y ellos comían su ración que Faraón les daba: por eso no vendieron su tierra.', 'nebula'),
('Old', 'Génesis', 47, 23, 'Y José dijo al pueblo: He aquí os he hoy comprado y á vuestra tierra para Faraón: ved aquí simiente, y sembraréis la tierra.', 'nebula'),
('Old', 'Génesis', 47, 24, 'Y será que de los frutos daréis el quinto á Faraón, y las cuatro partes serán vuestras para sembrar las tierras, y para vuestro mantenimiento, y de los que están en vuestras casas, y para que coman vuestros niños.', 'nebula'),
('Old', 'Génesis', 47, 25, 'Y ellos respondieron: La vida nos has dado: hallemos gracia en ojos de mi señor, y seamos siervos de Faraón.', 'nebula'),
('Old', 'Génesis', 47, 26, 'Entonces José lo puso por fuero hasta hoy sobre la tierra de Egipto, señalando para Faraón el quinto; excepto sólo la tierra de los sacerdotes, que no fué de Faraón.', 'nebula'),
('Old', 'Génesis', 47, 27, 'Así habitó Israel en la tierra de Egipto, en la tierra de Gosén; y aposesionáronse en ella, y se aumentaron, y multiplicaron en gran manera.', 'nebula'),
('Old', 'Génesis', 47, 28, 'Y vivió Jacob en la tierra de Egipto diecisiete años: y fueron los días de Jacob, los años de su vida, ciento cuarenta y siete años.', 'nebula'),
('Old', 'Génesis', 47, 29, 'Y llegáronse los días de Israel para morir, y llamó á José su hijo, y le dijo: Si he hallado ahora gracia en tus ojos, ruégote que pongas tu mano debajo de mi muslo, y harás conmigo misericordia y verdad; ruégote que no me entierres en Egipto;', 'nebula'),
('Old', 'Génesis', 47, 30, 'Mas cuando durmiere con mis padres, llevarme has de Egipto, y me sepultarás en el sepulcro de ellos. Y él respondió: Yo haré como tú dices.', 'nebula'),
('Old', 'Génesis', 47, 31, 'Y él dijo: Júramelo. Y él le juró. Entonces Israel se inclinó sobre la cabecera de la cama.', 'nebula'),
('Old', 'Génesis', 48, 1, 'Y SUCEDIO después de estas cosas el haberse dicho á José: He aquí tu padre está enfermo. Y él tomó consigo sus dos hijos Manasés y Ephraim.', 'nebula'),
('Old', 'Génesis', 48, 2, 'Y se hizo saber á Jacob, diciendo: He aquí tu hijo José viene á ti. Entonces se esforzó Israel, y sentóse sobre la cama;', 'nebula'),
('Old', 'Génesis', 48, 3, 'Y dijo á José: El Dios Omnipotente me apareció en Luz en la tierra de Canaán, y me bendijo,', 'nebula'),
('Old', 'Génesis', 48, 4, 'Y díjome: He aquí, yo te haré crecer, y te multiplicaré, y te pondré por estirpe de pueblos: y daré esta tierra á tu simiente después de ti por heredad perpetua.', 'nebula'),
('Old', 'Génesis', 48, 5, 'Y ahora tus dos hijos Ephraim y Manasés, que te nacieron en la tierra de Egipto, antes que viniese á ti á la tierra de Egipto, míos son; como Rubén y Simeón, serán míos:', 'nebula'),
('Old', 'Génesis', 48, 6, 'Y los que después de ellos has engendrado, serán tuyos; por el nombre de sus hermanos serán llamados en sus heredades.', 'nebula'),
('Old', 'Génesis', 48, 7, 'Porque cuando yo venía de Padan-aram, se me murió Rachêl en la tierra de Canaán, en el camino, como media legua de tierra viniendo á Ephrata; y sepultéla allí en el camino de Ephrata, que es Bethlehem.', 'nebula'),
('Old', 'Génesis', 48, 8, 'Y vió Israel los hijos de José, y dijo: ¿Quiénes son éstos?', 'nebula'),
('Old', 'Génesis', 48, 9, 'Y respondió José á su padre: Son mis hijos, que Dios me ha dado aquí. Y él dijo: Allégalos ahora á mí, y los bendeciré.', 'nebula'),
('Old', 'Génesis', 48, 10, 'Y los ojos de Israel estaban tan agravados de la vejez, que no podía ver. Hízoles, pues, llegar á él, y él los besó y abrazó.', 'nebula'),
('Old', 'Génesis', 48, 11, 'Y dijo Israel á José: No pensaba yo ver tu rostro, y he aquí Dios me ha hecho ver también tu simiente.', 'nebula'),
('Old', 'Génesis', 48, 12, 'Entonces José los sacó de entre sus rodillas, é inclinóse á tierra.', 'nebula'),
('Old', 'Génesis', 48, 13, 'Y tomólos José á ambos, Ephraim á su diestra, á la siniestra de Israel; y á Manasés á su izquierda, á la derecha de Israel; é hízoles llegar á él.', 'nebula'),
('Old', 'Génesis', 48, 14, 'Entonces Israel extendió su diestra, y púsola sobre la cabeza de Ephraim, que era el menor, y su siniestra sobre la cabeza de Manasés, colocando así sus manos adrede, aunque Manasés era el primogénito.', 'nebula'),
('Old', 'Génesis', 48, 15, 'Y bendijo á José, y dijo: El Dios en cuya presencia anduvieron mis padres Abraham é Isaac, el Dios que me mantiene desde que yo soy hasta este día,', 'nebula'),
('Old', 'Génesis', 48, 16, 'El Angel que me liberta de todo mal, bendiga á estos mozos: y mi nombre sea llamado en ellos, y el nombre de mis padres Abraham é Isaac: y multipliquen en gran manera en medio de la tierra.', 'nebula'),
('Old', 'Génesis', 48, 17, 'Entonces viendo José que su padre ponía la mano derecha sobre la cabeza de Eprhaim, causóle esto disgusto; y asió la mano de su padre, para mudarla de sobre la cabeza de Ephraim á la cabeza de Manasés.', 'nebula'),
('Old', 'Génesis', 48, 18, 'Y dijo José á su padre: No así, padre mío, porque éste es el primogénito; pon tu diestra sobre su cabeza.', 'nebula'),
('Old', 'Génesis', 48, 19, 'Mas su padre no quiso, y dijo: Lo sé, hijo mío, lo sé: también él vendrá á ser un pueblo, y será también acrecentado; pero su hermano menor será más grande que él, y su simiente será plenitud de gentes.', 'nebula'),
('Old', 'Génesis', 48, 20, 'Y bendíjolos aquel día, diciendo: En ti bendecirá Israel, diciendo: Póngate Dios como á Ephraim y como á Manasés. Y puso á Ephraim delante de Manasés.', 'nebula'),
('Old', 'Génesis', 48, 21, 'Y dijo Israel á José: He aquí, yo muero, mas Dios será con vosotros, y os hará volver á la tierra de vuestros padres.', 'nebula'),
('Old', 'Génesis', 48, 22, 'Y yo te he dado á ti una parte sobre tus hermanos, la cual tomé yo de mano del Amorrheo con mi espada y con mi arco.', 'nebula'),
('Old', 'Génesis', 49, 1, 'Y LLAMO Jacob á sus hijos, y dijo: Juntaos, y os declararé lo que os ha de acontecer en los postreros días.', 'nebula'),
('Old', 'Génesis', 49, 2, 'Juntaos y oid, hijos de Jacob; Y escuchad á vuestro padre Israel.', 'nebula'),
('Old', 'Génesis', 49, 3, 'Rubén, tú eres mi primogénito, mi fortaleza, y el principio de mi vigor; Principal en dignidad, principal en poder.', 'nebula'),
('Old', 'Génesis', 49, 4, 'Corriente como las aguas, no seas el principal; Por cuanto subiste al lecho de tu padre: Entonces te envileciste, subiendo á mi estrado.', 'nebula'),
('Old', 'Génesis', 49, 5, 'Simeón y Leví, hermanos: Armas de iniquidad sus armas.', 'nebula'),
('Old', 'Génesis', 49, 6, 'En su secreto no entre mi alma, Ni mi honra se junte en su compañía; Que en su furor mataron varón, Y en su voluntad arrancaron muro.', 'nebula'),
('Old', 'Génesis', 49, 7, 'Maldito su furor, que fué fiero; Y su ira, que fué dura: Yo los apartaré en Jacob, Y los esparciré en Israel.', 'nebula'),
('Old', 'Génesis', 49, 8, 'Judá, alabarte han tus hermanos: Tu mano en la cerviz de tus enemigos: Los hijos de tu padre se inclinarán á ti.', 'nebula'),
('Old', 'Génesis', 49, 9, 'Cachorro de león Judá: De la presa subiste, hijo mío: Encorvóse, echóse como león, Así como león viejo; ¿quién lo despertará?', 'nebula'),
('Old', 'Génesis', 49, 10, 'No será quitado el cetro de Judá, Y el legislador de entre sus piés, Hasta que venga Shiloh; Y á él se congregarán los pueblos.', 'nebula'),
('Old', 'Génesis', 49, 11, 'Atando á la vid su pollino, Y á la cepa el hijo de su asna, Lavó en el vino su vestido, Y en la sangre de uvas su manto:', 'nebula'),
('Old', 'Génesis', 49, 12, 'Sus ojos bermejos del vino, Y los dientes blancos de la leche.', 'nebula'),
('Old', 'Génesis', 49, 13, 'Zabulón en puertos de mar habitará, Y será para puerto de navíos; Y su término hasta Sidón.', 'nebula'),
('Old', 'Génesis', 49, 14, 'Issachâr, asno huesudo Echado entre dos tercios:', 'nebula'),
('Old', 'Génesis', 49, 15, 'Y vió que el descanso era bueno, Y que la tierra era deleitosa; Y bajó su hombro para llevar, Y sirvió en tributo.', 'nebula'),
('Old', 'Génesis', 49, 16, 'Dan juzgará á su pueblo, Como una de las tribus de Israel.', 'nebula'),
('Old', 'Génesis', 49, 17, 'Será Dan serpiente junto al camino, Cerasta junto á la senda, Que muerde los talones de los caballos, Y hace caer por detrás al cabalgador de ellos.', 'nebula'),
('Old', 'Génesis', 49, 18, 'Tu salud esperé, oh Jehová.', 'nebula'),
('Old', 'Génesis', 49, 19, 'Gad, ejército lo acometerá; Mas él acometerá al fin.', 'nebula'),
('Old', 'Génesis', 49, 20, 'El pan de Aser será grueso, Y él dará deleites al rey.', 'nebula'),
('Old', 'Génesis', 49, 21, 'Nephtalí, sierva dejada, Que dará dichos hermosos.', 'nebula'),
('Old', 'Génesis', 49, 22, 'Ramo fructífero José, Ramo fructífero junto á fuente, Cuyos vástagos se extienden sobre el muro.', 'nebula'),
('Old', 'Génesis', 49, 23, 'Y causáronle amargura, Y asaeteáronle, Y aborreciéronle los archeros:', 'nebula'),
('Old', 'Génesis', 49, 24, 'Mas su arco quedó en fortaleza, Y los brazos de sus manos se corroboraron Por las manos del Fuerte de Jacob, (De allí el pastor, y la piedra de Israel,)', 'nebula'),
('Old', 'Génesis', 49, 25, 'Del Dios de tu padre, el cual te ayudará, Y del Omnipotente, el cual te bendecirá Con bendiciones de los cielos de arriba, Con bendiciones del abismo que está abajo, Con bendiciones del seno y de la matriz.', 'nebula'),
('Old', 'Génesis', 49, 26, 'Las bendiciones de tu padre Fueron mayores que las bendiciones de mis progenitores: Hasta el término de los collados eternos Serán sobre la cabeza de José, Y sobre la mollera del Nazareo de sus hermanos.', 'nebula'),
('Old', 'Génesis', 49, 27, 'Benjamín, lobo arrebatador: A la mañana comerá la presa, Y á la tarde repartirá los despojos.', 'nebula'),
('Old', 'Génesis', 49, 28, 'Todos estos fueron las doce tribus de Israel: y esto fué lo que su padre les dijo, y bendíjolos; á cada uno por su bendición los bendijo.', 'nebula'),
('Old', 'Génesis', 49, 29, 'Mandóles luego, y díjoles: Yo voy á ser reunido con mi pueblo: sepultadme con mis padres en la cueva que está en el campo de Ephrón el Hetheo;', 'nebula'),
('Old', 'Génesis', 49, 30, 'En la cueva que está en el campo de Macpela, que está delante de Mamre en la tierra de Canaán, la cual compró Abraham con el mismo campo de Ephrón el Hetheo, para heredad de sepultura.', 'nebula'),
('Old', 'Génesis', 49, 31, 'Allí sepultaron á Abraham y á Sara su mujer; allí sepultaron á Isaac y á Rebeca su mujer; allí también sepulté yo á Lea.', 'nebula'),
('Old', 'Génesis', 49, 32, 'La compra del campo y de la cueva que está en él, fué de los hijos de Heth.', 'nebula'),
('Old', 'Génesis', 49, 33, 'Y como acabó Jacob de dar órdenes á sus hijos, encogió sus pies en la cama, y espiró: y fué reunido con sus padres.', 'nebula'),
('Old', 'Génesis', 50, 1, 'ENTONCES se echó José sobre el rostro de su padre, y lloró sobre él, y besólo.', 'nebula'),
('Old', 'Génesis', 50, 2, 'Y mandó José á sus médicos familiares que embalsamasen á su padre: y los médicos embalsamaron á Israel.', 'nebula'),
('Old', 'Génesis', 50, 3, 'Y cumpliéronle cuarenta días, porque así cumplían los días de los embalsamados, y lloráronlo los Egipcios setenta días.', 'nebula'),
('Old', 'Génesis', 50, 4, 'Y pasados los días de su luto, habló José á los de la casa de Faraón, diciendo: Si he hallado ahora gracia en vuestros ojos, os ruego que habléis en oídos de Faraón, diciendo:', 'nebula'),
('Old', 'Génesis', 50, 5, 'Mi padre me conjuró diciendo: He aquí yo muero; en mi sepulcro que yo cavé para mí en la tierra de Canaán, allí me sepultarás; ruego pues que vaya yo ahora, y sepultaré á mi padre, y volveré.', 'nebula'),
('Old', 'Génesis', 50, 6, 'Y Faraón dijo: Ve, y sepulta á tu padre, como él te conjuró.', 'nebula'),
('Old', 'Génesis', 50, 7, 'Entonces José subió á sepultar á su padre; y subieron con él todos los siervos de Faraón, los ancianos de su casa, y todos los ancianos de la tierra de Egipto.', 'nebula'),
('Old', 'Génesis', 50, 8, 'Y toda la casa de José, y sus hermanos, y la casa de su padre: solamente dejaron en la tierra de Gosén sus niños, y sus ovejas y sus vacas.', 'nebula'),
('Old', 'Génesis', 50, 9, 'Y subieron también con él carros y gente de á caballo, é hízose un escuadrón muy grande.', 'nebula'),
('Old', 'Génesis', 50, 10, 'Y llegaron hasta la era de Atad, que está á la otra parte del Jordán, y endecharon allí con grande y muy grave lamentación: y José hizo á su padre duelo por siete días.', 'nebula'),
('Old', 'Génesis', 50, 11, 'Y viendo los moradores de la tierra, los Cananeos, el llanto en la era de Atad, dijeron: Llanto grande es este de los Egipcios: por eso fué llamado su nombre Abelmizraim, que está á la otra parte del Jordán.', 'nebula'),
('Old', 'Génesis', 50, 12, 'Hicieron, pues, sus hijos con él, según les había mandado:', 'nebula'),
('Old', 'Génesis', 50, 13, 'Pues lleváronlo sus hijos á la tierra de Canaán, y le sepultaron en la cueva del campo de Macpela, la que había comprado Abraham con el mismo campo, para heredad de sepultura, de Ephrón el Hetheo, delante de Mamre.', 'nebula'),
('Old', 'Génesis', 50, 14, 'Y tornóse José á Egipto, él y sus hermanos, y todos los que subieron con él á sepultar á su padre, después que le hubo sepultado.', 'nebula'),
('Old', 'Génesis', 50, 15, 'Y viendo los hermanos de José que su padre era muerto, dijeron: Quizá nos aborrecerá José, y nos dará el pago de todo el mal que le hicimos.', 'nebula'),
('Old', 'Génesis', 50, 16, 'Y enviaron á decir á José: Tu padre mandó antes de su muerte, diciendo:', 'nebula'),
('Old', 'Génesis', 50, 17, 'Así diréis á José: Ruégote que perdones ahora la maldad de tus hermanos y su pecado, porque mal te trataron: por tanto ahora te rogamos que perdones la maldad de los siervos del Dios de tu padre. Y José lloró mientras hablaban.', 'nebula'),
('Old', 'Génesis', 50, 18, 'Y vinieron también sus hermanos, y postráronse delante de él, y dijeron: Henos aquí por tus siervos.', 'nebula'),
('Old', 'Génesis', 50, 19, 'Y respondióles José: No temáis: ¿estoy yo en lugar de Dios?', 'nebula'),
('Old', 'Génesis', 50, 20, 'Vosotros pensasteis mal sobre mí, mas Dios lo encaminó á bien, para hacer lo que vemos hoy, para mantener en vida á mucho pueblo.', 'nebula'),
('Old', 'Génesis', 50, 21, 'Ahora, pues, no tengáis miedo; yo os sustentaré á vosotros y á vuestros hijos. Así los consoló, y les habló al corazón.', 'nebula'),
('Old', 'Génesis', 50, 22, 'Y estuvo José en Egipto, él y la casa de su padre: y vivió José ciento diez años.', 'nebula'),
('Old', 'Génesis', 50, 23, 'Y vió José los hijos de Ephraim hasta la tercera generación: también los hijos de Machîr, hijo de Manasés, fueron criados sobre las rodillas de José.', 'nebula'),
('Old', 'Génesis', 50, 24, 'Y José dijo á sus hermanos: Yo me muero; mas Dios ciertamente os visitará, y os hará subir de aquesta tierra á la tierra que juró á Abraham, á Isaac, y á Jacob.', 'nebula'),
('Old', 'Génesis', 50, 25, 'Y conjuró José á los hijos de Israel, diciendo: Dios ciertamente os visitará, y haréis llevar de aquí mis huesos.', 'nebula'),
('Old', 'Génesis', 50, 26, 'Y murió José de edad de ciento diez años; y embalsamáronlo, y fué puesto en un ataúd en Egipto.', 'nebula'),
('Old', 'Éxodo', 1, 1, 'ESTOS son los nombres de los hijos de Israel, que entraron en Egipto con Jacob; cada uno entró con su familia.', 'sand'),
('Old', 'Éxodo', 1, 2, 'Rubén, Simeón, Leví y Judá;', 'sand'),
('Old', 'Éxodo', 1, 3, 'Issachâr, Zabulón y Benjamín;', 'sand'),
('Old', 'Éxodo', 1, 4, 'Dan y Nephtalí, Gad y Aser.', 'sand'),
('Old', 'Éxodo', 1, 5, 'Y todas las almas de los que salieron del muslo de Jacob, fueron setenta. Y José estaba en Egipto.', 'sand'),
('Old', 'Éxodo', 1, 6, 'Y murió José, y todos sus hermanos, y toda aquella generación.', 'sand'),
('Old', 'Éxodo', 1, 7, 'Y los hijos de Israel crecieron, y multiplicaron, y fueron aumentados y corroborados en extremo; y llenóse la tierra de ellos.', 'sand'),
('Old', 'Éxodo', 1, 8, 'Levantóse entretanto un nuevo rey sobre Egipto, que no conocía á José; el cual dijo á su pueblo:', 'sand'),
('Old', 'Éxodo', 1, 9, 'He aquí, el pueblo de los hijos de Israel es mayor y más fuerte que nosotros:', 'sand'),
('Old', 'Éxodo', 1, 10, 'Ahora, pues, seamos sabios para con él, porque no se multiplique, y acontezca que viniendo guerra, él también se junte con nuestros enemigos, y pelee contra nosotros, y se vaya de la tierra.', 'sand'),
('Old', 'Éxodo', 1, 11, 'Entonces pusieron sobre él comisarios de tributos que los molestasen con sus cargas; y edificaron á Faraón las ciudades de los bastimentos, Phithom y Raamses.', 'sand'),
('Old', 'Éxodo', 1, 12, 'Empero cuanto más los oprimían, tanto más se multiplicaban y crecían: así que estaban ellos fastidiados de los hijos de Israel.', 'sand'),
('Old', 'Éxodo', 1, 13, 'Y los Egipcios hicieron servir á los hijos de Israel con dureza:', 'sand'),
('Old', 'Éxodo', 1, 14, 'Y amargaron su vida con dura servidumbre, en hacer barro y ladrillo, y en toda labor del campo, y en todo su servicio, al cual los obligaban con rigorismo.', 'sand'),
('Old', 'Éxodo', 1, 15, 'Y habló el rey de Egipto á las parteras de las Hebreas, una de las cuales se llamaba Siphra, y otra Phúa, y díjoles:', 'sand'),
('Old', 'Éxodo', 1, 16, 'Cuando parteareis á las Hebreas, y mirareis los asientos, si fuere hijo, matadlo; y si fuere hija, entonces viva.', 'sand'),
('Old', 'Éxodo', 1, 17, 'Mas las parteras temieron á Dios, y no hicieron como les mandó el rey de Egipto, sino que reservaban la vida á los niños.', 'sand'),
('Old', 'Éxodo', 1, 18, 'Y el rey de Egipto hizo llamar á las parteras y díjoles: ¿Por qué habéis hecho esto, que habéis reservado la vida á los niños?', 'sand'),
('Old', 'Éxodo', 1, 19, 'Y las parteras respondieron á Faraón: Porque las mujeres Hebreas no son como las Egipcias: porque son robustas, y paren antes que la partera venga á ellas.', 'sand'),
('Old', 'Éxodo', 1, 20, 'Y Dios hizo bien á las parteras: y el pueblo se multiplicó, y se corroboraron en gran manera.', 'sand'),
('Old', 'Éxodo', 1, 21, 'Y por haber las parteras temido á Dios, él les hizo casas.', 'sand'),
('Old', 'Éxodo', 1, 22, 'Entonces Faraón mandó á todo su pueblo, diciendo: Echad en el río todo hijo que naciere, y á toda hija reservad la vida.', 'sand'),
('Old', 'Éxodo', 2, 1, 'UN varón de la familia de Leví fué, y tomó por mujer una hija de Leví:', 'sand'),
('Old', 'Éxodo', 2, 2, 'La cual concibió, y parió un hijo: y viéndolo que era hermoso, túvole escondido tres meses.', 'sand'),
('Old', 'Éxodo', 2, 3, 'Pero no pudiendo ocultarle más tiempo, tomó una arquilla de juncos, y calafateóla con pez y betún, y colocó en ella al niño, y púsolo en un carrizal á la orilla del río:', 'sand'),
('Old', 'Éxodo', 2, 4, 'Y paróse una hermana suya á lo lejos, para ver lo que le acontecería.', 'sand'),
('Old', 'Éxodo', 2, 5, 'Y la hija de Faraón descendió á lavarse al río, y paseándose sus doncellas por la ribera del río, vió ella la arquilla en el carrizal, y envió una criada suya á que la tomase.', 'sand'),
('Old', 'Éxodo', 2, 6, 'Y como la abrió, vió al niño; y he aquí que el niño lloraba. Y teniendo compasión de él, dijo: De los niños de los Hebreos es éste.', 'sand'),
('Old', 'Éxodo', 2, 7, 'Entonces su hermana dijo á la hija de Faraón: ¿Iré á llamarte un ama de las Hebreas, para que te críe este niño?', 'sand'),
('Old', 'Éxodo', 2, 8, 'Y la hija de Faraón respondió: Ve. Entonces fué la doncella, y llamó á la madre del niño;', 'sand'),
('Old', 'Éxodo', 2, 9, 'A la cual dijo la hija de Faraón: Lleva este niño, y críamelo, y yo te lo pagaré. Y la mujer tomó al niño, y criólo.', 'sand'),
('Old', 'Éxodo', 2, 10, 'Y como creció el niño, ella lo trajo á la hija de Faraón, la cual lo prohijó, y púsole por nombre Moisés, diciendo: Porque de las aguas lo saqué.', 'sand'),
('Old', 'Éxodo', 2, 11, 'Y en aquellos días acaeció que, crecido ya Moisés, salió á sus hermanos, y vió sus cargas: y observó á un Egipcio que hería á uno de los Hebreos, sus hermanos.', 'sand'),
('Old', 'Éxodo', 2, 12, 'Y miró á todas partes, y viendo que no parecía nadie, mató al Egipcio, y escondiólo en la arena.', 'sand'),
('Old', 'Éxodo', 2, 13, 'Y salió al día siguiente, y viendo á dos Hebreos que reñían, dijo al que hacía la injuria: ¿Por qué hieres á tu prójimo?', 'sand'),
('Old', 'Éxodo', 2, 14, 'Y él respondió: ¿Quién te ha puesto á ti por príncipe y juez sobre nosotros? ¿piensas matarme como mataste al Egipcio? Entonces Moisés tuvo miedo, y dijo: Ciertamente esta cosa es descubierta.', 'sand'),
('Old', 'Éxodo', 2, 15, 'Y oyendo Faraón este negocio, procuró matar á Moisés: mas Moisés huyó de delante de Faraón, y habitó en la tierra de Madián; y sentóse junto á un pozo.', 'sand'),
('Old', 'Éxodo', 2, 16, 'Tenía el sacerdote de Madián siete hijas, las cuales vinieron á sacar agua, para llenar las pilas y dar de beber á las ovejas de su padre.', 'sand'),
('Old', 'Éxodo', 2, 17, 'Mas los pastores vinieron, y echáronlas: Entonces Moisés se levantó y defendiólas, y abrevó sus ovejas.', 'sand'),
('Old', 'Éxodo', 2, 18, 'Y volviendo ellas á Ragüel su padre, díjoles él: ¿Por qué habéis hoy venido tan presto?', 'sand'),
('Old', 'Éxodo', 2, 19, 'Y ellas respondieron: Un varón Egipcio nos defendió de mano de los pastores, y también nos sacó el agua, y abrevó las ovejas.', 'sand'),
('Old', 'Éxodo', 2, 20, 'Y dijo á sus hijas: ¿Y dónde está? ¿por qué habéis dejado ese hombre? llamadle para que coma pan.', 'sand'),
('Old', 'Éxodo', 2, 21, 'Y Moisés acordó en morar con aquel varón; y él dió á Moisés á su hija Séphora:', 'sand'),
('Old', 'Éxodo', 2, 22, 'La cual le parió un hijo, y él le puso por nombre Gersom: porque dijo: Peregrino soy en tierra ajena.', 'sand'),
('Old', 'Éxodo', 2, 23, 'Y aconteció que después de muchos días murió el rey de Egipto, y los hijos de Israel suspiraron á causa de la servidumbre, y clamaron: y subió á Dios el clamor de ellos con motivo de su servidumbre.', 'sand'),
('Old', 'Éxodo', 2, 24, 'Y oyó Dios el gemido de ellos, y acordóse de su pacto con Abraham, Isaac y Jacob.', 'sand'),
('Old', 'Éxodo', 2, 25, 'Y miró Dios á los hijos de Israel, y reconociólos Dios.', 'sand'),
('Old', 'Éxodo', 3, 1, 'Y APACENTANDO Moisés las ovejas de Jethro su suegro, sacerdote de Madián, llevó las ovejas detrás del desierto, y vino á Horeb, monte de Dios.', 'sand'),
('Old', 'Éxodo', 3, 2, 'Y apareciósele el Angel de Jehová en una llama de fuego en medio de una zarza: y él miró, y vió que la zarza ardía en fuego, y la zarza no se consumía.', 'sand'),
('Old', 'Éxodo', 3, 3, 'Entonces Moisés dijo: Iré yo ahora, y veré esta grande visión, por qué causa la zarza no se quema.', 'sand'),
('Old', 'Éxodo', 3, 4, 'Y viendo Jehová que iba á ver, llamólo Dios de en medio de la zarza, y dijo: ¡Moisés, Moisés! Y él respondió: Heme aquí.', 'sand'),
('Old', 'Éxodo', 3, 5, 'Y dijo: No te llegues acá: quita tus zapatos de tus pies, porque el lugar en que tú estás, tierra santa es.', 'sand'),
('Old', 'Éxodo', 3, 6, 'Y dijo: Yo soy el Dios de tu padre, Dios de Abraham, Dios de Isaac, Dios de Jacob. Entonces Moisés cubrió su rostro, porque tuvo miedo de mirar á Dios.', 'sand'),
('Old', 'Éxodo', 3, 7, 'Y dijo Jehová: Bien he visto la aflicción de mi pueblo que está en Egipto, y he oído su clamor á causa de sus exactores; pues tengo conocidas sus angustias:', 'sand'),
('Old', 'Éxodo', 3, 8, 'Y he descendido para librarlos de mano de los Egipcios, y sacarlos de aquella tierra á una tierra buena y ancha, á tierra que fluye leche y miel, á los lugares del Cananeo, del Hetheo, del Amorrheo, del Pherezeo, del Heveo, y del Jebuseo.', 'sand'),
('Old', 'Éxodo', 3, 9, 'El clamor, pues, de los hijos de Israel ha venido delante de mí, y también he visto la opresión con que los Egipcios los oprimen.', 'sand'),
('Old', 'Éxodo', 3, 10, 'Ven por tanto ahora, y enviarte he á Faraón, para que saques á mi pueblo, los hijos de Israel, de Egipto.', 'sand'),
('Old', 'Éxodo', 3, 11, 'Entonces Moisés respondió á Dios: ¿Quién soy yo, para que vaya á Faraón, y saque de Egipto á los hijos de Israel?', 'sand'),
('Old', 'Éxodo', 3, 12, 'Y él le respondió: Ve, porque yo seré contigo; y esto te será por señal de que yo te he enviado: luego que hubieres sacado este pueblo de Egipto, serviréis á Dios sobre este monte.', 'sand'),
('Old', 'Éxodo', 3, 13, 'Y dijo Moisés á Dios: He aquí que llego yo á los hijos de Israel, y les digo, El Dios de vuestros padres me ha enviado á vosotros; si ellos me preguntaren: ¿Cuál es su nombre? ¿qué les responderé?', 'sand'),
('Old', 'Éxodo', 3, 14, 'Y respondió Dios á Moisés: YO SOY EL QUE SOY. Y dijo: Así dirás á los hijos de Israel: YO SOY me ha enviado á vosotros.', 'sand'),
('Old', 'Éxodo', 3, 15, 'Y dijo más Dios á Moisés: Así dirás á los hijos de Israel: Jehová, el Dios de vuestros padres, el Dios de Abraham, Dios de Isaac y Dios de Jacob, me ha enviado á vosotros. Este es mi nombre para siempre, este es mi memorial por todos los siglos.', 'sand'),
('Old', 'Éxodo', 3, 16, 'Ve, y junta los ancianos de Israel, y diles: Jehová, el Dios de vuestros padres, el Dios de Abraham, de Isaac, y de Jacob, me apareció, diciendo: De cierto os he visitado, y visto lo que se os hace en Egipto;', 'sand'),
('Old', 'Éxodo', 3, 17, 'Y he dicho: Yo os sacaré de la aflicción de Egipto á la tierra del Cananeo, y del Hetheo, y del Amorrheo, y del Pherezeo, y del Heveo, y del Jebuseo, á una tierra que fluye leche y miel.', 'sand'),
('Old', 'Éxodo', 3, 18, 'Y oirán tu voz; é irás tú, y los ancianos de Israel, al rey de Egipto, y le diréis: Jehová, el Dios de los Hebreos, nos ha encontrado; por tanto nosotros iremos ahora camino de tres días por el desierto, para que sacrifiquemos á Jehová nuestro Dios.', 'sand'),
('Old', 'Éxodo', 3, 19, 'Mas yo sé que el rey de Egipto no os dejará ir sino por mano fuerte.', 'sand'),
('Old', 'Éxodo', 3, 20, 'Empero yo extenderé mi mano, y heriré á Egipto con todas mis maravillas que haré en él, y entonces os dejará ir.', 'sand'),
('Old', 'Éxodo', 3, 21, 'Y yo daré á este pueblo gracia en los ojos de los Egipcios, para que cuando os partiereis, no salgáis vacíos:', 'sand'),
('Old', 'Éxodo', 3, 22, 'Sino que demandará cada mujer á su vecina y á su huéspeda vasos de plata, vasos de oro, y vestidos: los cuales pondréis sobre vuestros hijos y vuestras hijas, y despojaréis á Egipto.', 'sand'),
('Old', 'Éxodo', 4, 1, 'ENTONCES Moisés respondió, y dijo: He aquí que ellos no me creerán, ni oirán mi voz; porque dirán: No te ha aparecido Jehová.', 'sand'),
('Old', 'Éxodo', 4, 2, 'Y Jehová dijo: ¿Qué es eso que tienes en tu mano? Y él respondió: Una vara.', 'sand'),
('Old', 'Éxodo', 4, 3, 'Y él le dijo: Échala en tierra. Y él la echó en tierra, y tornóse una culebra: y Moisés huía de ella.', 'sand'),
('Old', 'Éxodo', 4, 4, 'Entonces dijo Jehová á Moisés: Extiende tu mano, y tómala por la cola. Y él extendió su mano, y tomóla, y tornóse vara en su mano.', 'sand'),
('Old', 'Éxodo', 4, 5, 'Por esto creerán que se te ha aparecido Jehová, el Dios de tus padres, el Dios de Abraham, Dios de Isaac, y Dios de Jacob.', 'sand'),
('Old', 'Éxodo', 4, 6, 'Y díjole más Jehová: Mete ahora tu mano en tu seno. Y él metió la mano en su seno; y como la sacó, he aquí que su mano estaba leprosa como la nieve.', 'sand'),
('Old', 'Éxodo', 4, 7, 'Y dijo: Vuelve á meter tu mano en tu seno: y él volvió á meter su mano en su seno; y volviéndola á sacar del seno, he aquí que se había vuelto como la otra carne.', 'sand'),
('Old', 'Éxodo', 4, 8, 'Si aconteciere, que no te creyeren, ni obedecieren á la voz de la primera señal, creerán á la voz de la postrera.', 'sand'),
('Old', 'Éxodo', 4, 9, 'Y si aún no creyeren á estas dos señales, ni oyeren tu voz, tomarás de las aguas del río, y derrámalas en tierra; y volverse han aquellas aguas que tomarás del río, se volverán sangre en la tierra.', 'sand'),
('Old', 'Éxodo', 4, 10, 'Entonces dijo Moisés á Jehová: ¡Ay Señor! yo no soy hombre de palabras de ayer ni de anteayer, ni aun desde que tú hablas á tu siervo; porque soy tardo en el habla y torpe de lengua.', 'sand'),
('Old', 'Éxodo', 4, 11, 'Y Jehová le respondió: ¿Quién dió la boca al hombre? ¿ó quién hizo al mudo y al sordo, al que ve y al ciego? ¿no soy yo Jehová?', 'sand'),
('Old', 'Éxodo', 4, 12, 'Ahora pues, ve, que yo seré en tu boca, y te enseñaré lo que hayas de hablar.', 'sand'),
('Old', 'Éxodo', 4, 13, 'Y él dijo: ¡Ay Señor! envía por mano del que has de enviar.', 'sand'),
('Old', 'Éxodo', 4, 14, 'Entonces Jehová se enojó contra Moisés, y dijo: ¿No conozco yo á tu hermano Aarón, Levita, y que él hablará? Y aun he aquí que él te saldrá á recibir, y en viéndote, se alegrará en su corazón.', 'sand'),
('Old', 'Éxodo', 4, 15, 'Tú hablarás á él, y pondrás en su boca las palabras, y yo seré en tu boca y en la suya, y os enseñaré lo que hayáis de hacer.', 'sand'),
('Old', 'Éxodo', 4, 16, 'Y él hablará por ti al pueblo; y él te será á ti en lugar de boca, y tú serás para él en lugar de Dios.', 'sand'),
('Old', 'Éxodo', 4, 17, 'Y tomarás esta vara en tu mano, con la cual harás las señales.', 'sand'),
('Old', 'Éxodo', 4, 18, 'Así se fué Moisés, y volviendo á su suegro Jethro, díjole: Iré ahora, y volveré á mis hermanos que están en Egipto, para ver si aún viven. Y Jethro dijo á Moisés: Ve en paz.', 'sand'),
('Old', 'Éxodo', 4, 19, 'Dijo también Jehová á Moisés en Madián: Ve, y vuélvete á Egipto, porque han muerto todos los que procuraban tu muerte.', 'sand'),
('Old', 'Éxodo', 4, 20, 'Entonces Moisés tomó su mujer y sus hijos, y púsolos sobre un asno, y volvióse á tierra de Egipto: tomó también Moisés la vara de Dios en su mano.', 'sand'),
('Old', 'Éxodo', 4, 21, 'Y dijo Jehová á Moisés: Cuando hubiereis vuelto á Egipto, mira que hagas delante de Faraón todas las maravillas que he puesto en tu mano: yo empero endureceré su corazón, de modo que no dejará ir al pueblo.', 'sand'),
('Old', 'Éxodo', 4, 22, 'Y dirás á Faraón: Jehová ha dicho así: Israel es mi hijo, mi primogénito.', 'sand'),
('Old', 'Éxodo', 4, 23, 'Ya te he dicho que dejes ir á mi hijo, para que me sirva, mas no has querido dejarlo ir: he aquí yo voy á matar á tu hijo, tu primogénito.', 'sand'),
('Old', 'Éxodo', 4, 24, 'Y aconteció en el camino, que en una posada le salió al encuentro Jehová, y quiso matarlo.', 'sand'),
('Old', 'Éxodo', 4, 25, 'Entonces Séphora cogió un afilado pedernal, y cortó el prepucio de su hijo, y echólo á sus pies, diciendo: A la verdad tú me eres un esposo de sangre.', 'sand'),
('Old', 'Éxodo', 4, 26, 'Así le dejó luego ir. Y ella dijo: Esposo de sangre, á causa de la circuncisión.', 'sand'),
('Old', 'Éxodo', 4, 27, 'Y Jehová dijo á Aarón: Ve á recibir á Moisés al desierto. Y él fue, y encontrólo en el monte de Dios, y besóle.', 'sand'),
('Old', 'Éxodo', 4, 28, 'Entonces contó Moisés á Aarón todas las palabras de Jehová que le enviaba, y todas las señales que le había dado.', 'sand'),
('Old', 'Éxodo', 4, 29, 'Y fueron Moisés y Aarón, y juntaron todos los ancianos de los hijos de Israel:', 'sand'),
('Old', 'Éxodo', 4, 30, 'Y habló Aarón todas las palabras que Jehová había dicho á Moisés, é hizo las señales delante de los ojos del pueblo.', 'sand'),
('Old', 'Éxodo', 4, 31, 'Y el pueblo creyó: y oyendo que Jehová había visitado los hijos de Israel, y que había visto su aflicción, inclináronse y adoraron.', 'sand'),
('Old', 'Éxodo', 5, 1, 'DESPUÉS entraron Moisés y Aarón á Faraón, y le dijeron: Jehová, el Dios de Israel, dice así: Deja ir á mi pueblo á celebrarme fiesta en el desierto.', 'sand'),
('Old', 'Éxodo', 5, 2, 'Y Faraón respondió: ¿Quién es Jehová, para que yo oiga su voz y deje ir á Israel? Yo no conozco á Jehová, ni tampoco dejaré ir á Israel.', 'sand'),
('Old', 'Éxodo', 5, 3, 'Y ellos dijeron: El Dios de los Hebreos nos ha encontrado: iremos, pues, ahora camino de tres días por el desierto, y sacrificaremos á Jehová nuestro Dios; porque no venga sobre nosotros con pestilencia ó con espada.', 'sand'),
('Old', 'Éxodo', 5, 4, 'Entonces el rey de Egipto les dijo: Moisés y Aarón, ¿por qué hacéis cesar al pueblo de su obra? idos á vuestros cargos.', 'sand'),
('Old', 'Éxodo', 5, 5, 'Dijo también Faraón: He aquí el pueblo de la tierra es ahora mucho, y vosotros les hacéis cesar de sus cargos.', 'sand'),
('Old', 'Éxodo', 5, 6, 'Y mandó Faraón aquel mismo día á los cuadrilleros del pueblo que le tenían á su cargo, y á sus gobernadores, diciendo:', 'sand'),
('Old', 'Éxodo', 5, 7, 'De aquí adelante no daréis paja al pueblo para hacer ladrillo, como ayer y antes de ayer; vayan ellos y recojan por sí mismos la paja:', 'sand'),
('Old', 'Éxodo', 5, 8, 'Y habéis de ponerles la tarea del ladrillo que hacían antes, y no les disminuiréis nada; porque están ociosos, y por eso levantan la voz diciendo: Vamos y sacrificaremos á nuestro Dios.', 'sand'),
('Old', 'Éxodo', 5, 9, 'Agrávese la servidumbre sobre ellos, para que se ocupen en ella, y no atiendan á palabras de mentira.', 'sand'),
('Old', 'Éxodo', 5, 10, 'Y saliendo los cuadrilleros del pueblo y sus gobernadores, hablaron al pueblo, diciendo: Así ha dicho Faraón: Yo no os doy paja.', 'sand'),
('Old', 'Éxodo', 5, 11, 'Id vosotros, y recoged paja donde la hallareis; que nada se disminuirá de vuestra tarea.', 'sand'),
('Old', 'Éxodo', 5, 12, 'Entonces el pueblo se derramó por toda la tierra de Egipto á coger rastrojo en lugar de paja.', 'sand'),
('Old', 'Éxodo', 5, 13, 'Y los cuadrilleros los apremiaban, diciendo: Acabad vuestra obra, la tarea del día en su día, como cuando se os daba paja.', 'sand'),
('Old', 'Éxodo', 5, 14, 'Y azotaban á los capataces de los hijos de Israel, que los cuadrilleros de Faraón habían puesto sobre ellos, diciendo: ¿Por qué no habéis cumplido vuestra tarea de ladrillo ni ayer ni hoy, como antes?', 'sand'),
('Old', 'Éxodo', 5, 15, 'Y los capataces de los hijos de Israel vinieron á Faraón, y se quejaron á él, diciendo: ¿Por qué lo haces así con tus siervos?', 'sand'),
('Old', 'Éxodo', 5, 16, 'No se da paja á tus siervos, y con todo nos dicen: Haced el ladrillo. Y he aquí tus siervos son azotados, y tu pueblo cae en falta.', 'sand'),
('Old', 'Éxodo', 5, 17, 'Y él respondió: Estáis ociosos, sí, ociosos, y por eso decís: Vamos y sacrifiquemos á Jehová.', 'sand'),
('Old', 'Éxodo', 5, 18, 'Id pues ahora, y trabajad. No se os dará paja, y habéis de dar la tarea del ladrillo.', 'sand'),
('Old', 'Éxodo', 5, 19, 'Entonces los capataces de los hijos de Israel se vieron en aflicción, habiéndoseles dicho: No se disminuirá nada de vuestro ladrillo, de la tarea de cada día.', 'sand'),
('Old', 'Éxodo', 5, 20, 'Y encontrando á Moisés y á Aarón, que estaban á la vista de ellos cuando salían de Faraón,', 'sand'),
('Old', 'Éxodo', 5, 21, 'Dijéronles: Mire Jehová sobre vosotros, y juzgue; pues habéis hecho heder nuestro olor delante de Faraón y de sus siervos, dándoles el cuchillo en las manos para que nos maten.', 'sand'),
('Old', 'Éxodo', 5, 22, 'Entonces Moisés se volvió á Jehová, y dijo: Señor, ¿por qué afliges á este pueblo? ¿para qué me enviaste?', 'sand'),
('Old', 'Éxodo', 5, 23, 'Porque desde que yo vine á Faraón para hablarle en tu nombre, ha afligido á este pueblo; y tú tampoco has librado á tu pueblo.', 'sand'),
('Old', 'Éxodo', 6, 1, 'JEHOVA respondió á Moisés: Ahora verás lo que yo haré á Faraón; porque con mano fuerte los ha de dejar ir; y con mano fuerte los ha de echar de su tierra.', 'sand'),
('Old', 'Éxodo', 6, 2, 'Habló todavía Dios á Moisés, y díjole: Yo soy JEHOVA;', 'sand'),
('Old', 'Éxodo', 6, 3, 'Y aparecí á Abraham, á Isaac y á Jacob bajo el nombre de Dios Omnipotente, mas en mi nombre JEHOVA no me notifiqué á ellos.', 'sand'),
('Old', 'Éxodo', 6, 4, 'Y también establecí mi pacto con ellos, de darles la tierra de Canaán, la tierra en que fueron extranjeros, y en la cual peregrinaron.', 'sand'),
('Old', 'Éxodo', 6, 5, 'Y asimismo yo he oído el gemido de los hijos de Israel, á quienes hacen servir los Egipcios, y heme acordado de mi pacto.', 'sand'),
('Old', 'Éxodo', 6, 6, 'Por tanto dirás á los hijos de Israel: YO JEHOVA; y yo os sacaré de debajo de las cargas de Egipto, y os libraré de su servidumbre, y os redimiré con brazo extendido, y con juicios grandes:', 'sand'),
('Old', 'Éxodo', 6, 7, 'Y os tomaré por mi pueblo y seré vuestro Dios: y vosotros sabréis que yo soy Jehová vuestro Dios, que os saco de debajo de las cargas de Egipto:', 'sand'),
('Old', 'Éxodo', 6, 8, 'Y os meteré en la tierra, por la cual alcé mi mano que la daría á Abraham, á Isaac y á Jacob: y yo os la daré por heredad. YO JEHOVA.', 'sand'),
('Old', 'Éxodo', 6, 9, 'De esta manera habló Moisés á los hijos de Israel: mas ellos no escuchaban á Moisés á causa de la congoja de espíritu, y de la dura servidumbre.', 'sand'),
('Old', 'Éxodo', 6, 10, 'Y habló Jehová á Moisés, diciendo:', 'sand'),
('Old', 'Éxodo', 6, 11, 'Entra, y habla á Faraón rey de Egipto, que deje ir de su tierra á los hijos de Israel.', 'sand'),
('Old', 'Éxodo', 6, 12, 'Y respondió Moisés delante de Jehová, diciendo: He aquí, los hijos de Israel no me escuchan: ¿cómo pues me escuchará Faraón, mayormente siendo yo incircunciso de labios?', 'sand'),
('Old', 'Éxodo', 6, 13, 'Entonces Jehová habló á Moisés y á Aarón, y dióles mandamiento para los hijos de Israel, y para Faraón rey de Egipto, para que sacasen á los hijos de Israel de la tierra de Egipto.', 'sand'),
('Old', 'Éxodo', 6, 14, 'Estas son las cabezas de las familias de sus padres. Los hijos de Rubén, el primogénito de Israel: Hanoch y Phallú, Hezrón y Carmi: estas son las familias de Rubén.', 'sand'),
('Old', 'Éxodo', 6, 15, 'Los hijos de Simeón: Jemuel, y Jamín, y Ohad, y Jachîn, y Zoar, y Saúl, hijo de una Cananea: estas son las familias de Simeón.', 'sand'),
('Old', 'Éxodo', 6, 16, 'Y estos son los nombres de los hijos de Leví por sus linajes: Gersón, y Coath, y Merari: Y los años de la vida de Leví fueron ciento treinta y siete años.', 'sand'),
('Old', 'Éxodo', 6, 17, 'Y los hijos de Gersón: Libni, y Shimi, por sus familias.', 'sand'),
('Old', 'Éxodo', 6, 18, 'Y los hijos de Coath: Amram, é Izhar, y Hebrón, y Uzziel. Y los años de la vida de Coath fueron ciento treinta y tres años.', 'sand'),
('Old', 'Éxodo', 6, 19, 'Y los hijos de Merari: Mahali, y Musi: estas son las familias de Leví por sus linajes.', 'sand'),
('Old', 'Éxodo', 6, 20, 'Y Amram tomó por mujer á Jochêbed su tía, la cual le parió á Aarón y á Moisés. Y los años de la vida de Amram fueron ciento treinta y siete años.', 'sand'),
('Old', 'Éxodo', 6, 21, 'Y los hijos de Izhar: Cora, y Nepheg y Zithri.', 'sand'),
('Old', 'Éxodo', 6, 22, 'Y los hijos de Uzziel: Misael, y Elzaphán y Zithri.', 'sand'),
('Old', 'Éxodo', 6, 23, 'Y tomóse Aarón por mujer á Elisabeth, hija de Aminadab, hermana de Naasón; la cual le parió á Nadab, y á Abiú, y á Eleazar, y á Ithamar.', 'sand'),
('Old', 'Éxodo', 6, 24, 'Y los hijos de Cora: Assir, y Elcana, y Abiasaph: estas son las familias de los Coritas.', 'sand'),
('Old', 'Éxodo', 6, 25, 'Y Eleazar, hijo de Aarón, tomó para sí mujer de las hijas de Phutiel, la cual le parió á Phinees: Y estas son las cabezas de los padres de los Levitas por sus familias.', 'sand'),
('Old', 'Éxodo', 6, 26, 'Este es aquel Aarón y aquel Moisés, á los cuales Jehová dijo: Sacad á los hijos de Israel de la tierra de Egipto por sus escuadrones.', 'sand'),
('Old', 'Éxodo', 6, 27, 'Estos son los que hablaron á Faraón rey de Egipto, para sacar de Egipto á los hijos de Israel. Moisés y Aarón fueron éstos.', 'sand'),
('Old', 'Éxodo', 6, 28, 'Cuando Jehová habló á Moisés en la tierra de Egipto,', 'sand'),
('Old', 'Éxodo', 6, 29, 'Entonces Jehová habló á Moisés, diciendo: Yo soy JEHOVA; di á Faraón rey de Egipto todas las cosas que yo te digo á ti.', 'sand'),
('Old', 'Éxodo', 6, 30, 'Y Moisés respondió delante de Jehová: He aquí, yo soy incircunciso de labios, ¿cómo pues me ha de oír Faraón?', 'sand'),
('Old', 'Éxodo', 7, 1, 'JEHOVA dijo á Moisés: Mira, yo te he constituído dios para Faraón, y tu hermano Aarón será tu profeta.', 'sand'),
('Old', 'Éxodo', 7, 2, 'Tú dirás todas las cosas que yo te mandaré, y Aarón tu hermano hablará á Faraón, para que deje ir de su tierra á los hijos de Israel.', 'sand'),
('Old', 'Éxodo', 7, 3, 'Y yo endureceré el corazón de Faraón, y multiplicaré en la tierra de Egipto mis señales y mis maravillas.', 'sand'),
('Old', 'Éxodo', 7, 4, 'Y Faraón no os oirá; mas yo pondré mi mano sobre Egipto, y sacaré á mis ejércitos, mi pueblo, los hijos de Israel, de la tierra de Egipto, con grandes juicios.', 'sand'),
('Old', 'Éxodo', 7, 5, 'Y sabrán los Egipcios que yo soy Jehová, cuando extenderé mi mano sobre Egipto, y sacaré los hijos de Israel de en medio de ellos.', 'sand'),
('Old', 'Éxodo', 7, 6, 'E hizo Moisés y Aarón como Jehová les mandó: hiciéronlo así.', 'sand'),
('Old', 'Éxodo', 7, 7, 'Y era Moisés de edad de ochenta años, y Aarón de edad de ochenta y tres, cuando hablaron á Faraón.', 'sand'),
('Old', 'Éxodo', 7, 8, 'Y habló Jehová á Moisés y á Aarón, diciendo:', 'sand'),
('Old', 'Éxodo', 7, 9, 'Si Faraón os respondiere diciendo, Mostrad milagro; dirás á Aarón: Toma tu vara, y échala delante de Faraón, para que se torne culebra.', 'sand'),
('Old', 'Éxodo', 7, 10, 'Vinieron, pues, Moisés y Aarón á Faraón, é hicieron como Jehová lo había mandado: y echó Aarón su vara delante de Faraón y de sus siervos, y tornóse culebra.', 'sand'),
('Old', 'Éxodo', 7, 11, 'Entonces llamó también Faraón sabios y encantadores; é hicieron también lo mismo los encantadores de Egipto con sus encantamientos;', 'sand'),
('Old', 'Éxodo', 7, 12, 'Pues echó cada uno su vara, las cuales se volvieron culebras: mas la vara de Aarón devoró las varas de ellos.', 'sand'),
('Old', 'Éxodo', 7, 13, 'Y el corazón de Faraón se endureció, y no los escuchó; como Jehová lo había dicho.', 'sand'),
('Old', 'Éxodo', 7, 14, 'Entonces Jehová dijo á Moisés: El corazón de Faraón está agravado, que no quiere dejar ir al pueblo.', 'sand'),
('Old', 'Éxodo', 7, 15, 'Ve por la mañana á Faraón, he aquí que él sale á las aguas; y tú ponte á la orilla del río delante de él, y toma en tu mano la vara que se volvió culebra,', 'sand'),
('Old', 'Éxodo', 7, 16, 'Y dile: Jehová el Dios de los Hebreos me ha enviado á ti, diciendo: Deja ir á mi pueblo, para que me sirvan en el desierto; y he aquí que hasta ahora no has querido oir.', 'sand'),
('Old', 'Éxodo', 7, 17, 'Así ha dicho Jehová: En esto conocerás que yo soy Jehová: he aquí, yo heriré con la vara que tengo en mi mano el agua que está en el río, y se convertirá en sangre:', 'sand'),
('Old', 'Éxodo', 7, 18, 'Y los peces que hay en el río morirán, y hederá el río, y tendrán asco los Egipcios de beber el agua del río.', 'sand'),
('Old', 'Éxodo', 7, 19, 'Y Jehová dijo á Moisés: Di á Aarón: Toma tu vara, y extiende tu mano sobre las aguas de Egipto, sobre sus ríos, sobre sus arroyos y sobre sus estanques, y sobre todos sus depósitos de aguas, para que se conviertan en sangre, y haya sangre por toda la región de Egipto, así en los vasos de madera como en los de piedra.', 'sand'),
('Old', 'Éxodo', 7, 20, 'Y Moisés y Aarón hicieron como Jehová lo mandó; y alzando la vara hirió las aguas que había en el río, en presencia de Faraón y de sus siervos; y todas las aguas que había en el río se convirtieron en sangre.', 'sand'),
('Old', 'Éxodo', 7, 21, 'Asimismo los peces que había en el río murieron; y el río se corrompió, que los Egipcios no podían beber de él: y hubo sangre por toda la tierra de Egipto.', 'sand'),
('Old', 'Éxodo', 7, 22, 'Y los encantadores de Egipto hicieron lo mismo con sus encantamientos: y el corazón de Faraón se endureció, y no los escuchó; como Jehová lo había dicho.', 'sand'),
('Old', 'Éxodo', 7, 23, 'Y tornando Faraón volvióse á su casa, y no puso su corazón aun en esto.', 'sand'),
('Old', 'Éxodo', 7, 24, 'Y en todo Egipto hicieron pozos alrededor del río para beber, porque no podían beber de las aguas del río.', 'sand'),
('Old', 'Éxodo', 7, 25, 'Y cumpliéronse siete días después que Jehová hirió el río.', 'sand'),
('Old', 'Éxodo', 8, 1, 'ENTONCES Jehová dijo á Moisés: Entra á Faraón, y dile: Jehová ha dicho así: Deja ir á mi pueblo, para que me sirvan.', 'sand'),
('Old', 'Éxodo', 8, 2, 'Y si no lo quisieres dejar ir, he aquí yo heriré con ranas todos tus términos:', 'sand'),
('Old', 'Éxodo', 8, 3, 'Y el río criará ranas, las cuales subirán, y entrarán en tu casa, y en la cámara de tu cama, y sobre tu cama, y en las casas de tus siervos, y en tu pueblo, y en tus hornos, y en tus artesas:', 'sand'),
('Old', 'Éxodo', 8, 4, 'Y las ranas subirán sobre ti, y sobre tu pueblo, y sobre todos tus siervos.', 'sand'),
('Old', 'Éxodo', 8, 5, 'Y Jehová dijo á Moisés: Di á Aarón: Extiende tu mano con tu vara sobre los ríos, arroyos, y estanques, para que haga venir ranas sobre la tierra de Egipto.', 'sand'),
('Old', 'Éxodo', 8, 6, 'Entonces Aarón extendió su mano sobre las aguas de Egipto, y subieron ranas que cubrieron la tierra de Egipto.', 'sand'),
('Old', 'Éxodo', 8, 7, 'Y los encantadores hicieron lo mismo con sus encantamientos, é hicieron venir ranas sobre la tierra de Egipto.', 'sand'),
('Old', 'Éxodo', 8, 8, 'Entonces Faraón llamó á Moisés y á Aarón, y díjoles: Orad á Jehová que quite las ranas de mí y de mi pueblo; y dejaré ir al pueblo, para que sacrifique á Jehová.', 'sand'),
('Old', 'Éxodo', 8, 9, 'Y dijo Moisés á Faraón: Gloríate sobre mí: ¿cuándo oraré por ti, y por tus siervos, y por tu pueblo, para que las ranas sean quitadas de ti, y de tus casas, y que solamente se queden en el río?', 'sand'),
('Old', 'Éxodo', 8, 10, 'Y él dijo: Mañana. Y Moisés respondió: Se hará conforme á tu palabra, para que conozcas que no hay como Jehová nuestro Dios:', 'sand'),
('Old', 'Éxodo', 8, 11, 'Y las ranas se irán de ti, y de tus casas, y de tus siervos, y de tu pueblo, y solamente se quedarán en el río.', 'sand'),
('Old', 'Éxodo', 8, 12, 'Entonces salieron Moisés y Aarón de con Faraón, y clamó Moisés á Jehová sobre el negocio de las ranas que había puesto á Faraón.', 'sand'),
('Old', 'Éxodo', 8, 13, 'E hizo Jehová conforme á la palabra de Moisés, y murieron las ranas de las casas, de los cortijos, y de los campos.', 'sand'),
('Old', 'Éxodo', 8, 14, 'Y las juntaron en montones, y apestaban la tierra.', 'sand'),
('Old', 'Éxodo', 8, 15, 'Y viendo Faraón que le habían dado reposo, agravó su corazón, y no los escuchó; como Jehová lo había dicho.', 'sand'),
('Old', 'Éxodo', 8, 16, 'Entonces Jehová dijo á Moisés: Di á Aarón: Extiende tu vara, y hiere el polvo de la tierra, para que se vuelva piojos por todo el país de Egipto.', 'sand'),
('Old', 'Éxodo', 8, 17, 'Y ellos lo hicieron así; y Aarón extendió su mano con su vara, é hirió el polvo de la tierra, el cual se volvió piojos, así en los hombres como en las bestias: todo el polvo de la tierra se volvió piojos en todo el país de Egipto.', 'sand'),
('Old', 'Éxodo', 8, 18, 'Y los encantadores hicieron así también, para sacar piojos con sus encantamientos; mas no pudieron. Y había piojos así en los hombres como en las bestias.', 'sand'),
('Old', 'Éxodo', 8, 19, 'Entonces los magos dijeron á Faraón: Dedo de Dios es este. Mas el corazón de Faraón se endureció, y no los escuchó; como Jehová lo había dicho.', 'sand'),
('Old', 'Éxodo', 8, 20, 'Y Jehová dijo á Moisés: Levántate de mañana y ponte delante de Faraón, he aquí él sale á las aguas; y dile: Jehová ha dicho así: Deja ir á mi pueblo, para que me sirva.', 'sand'),
('Old', 'Éxodo', 8, 21, 'Porque si no dejares ir á mi pueblo, he aquí yo enviaré sobre ti, y sobre tus siervos, y sobre tu pueblo, y sobre tus casas toda suerte de moscas; y las casas de los Egipcios se henchirán de toda suerte de moscas, y asimismo la tierra donde ellos estuvieren.', 'sand'),
('Old', 'Éxodo', 8, 22, 'Y aquel día yo apartaré la tierra de Gosén, en la cual mi pueblo habita, para que ninguna suerte de moscas haya en ella; á fin de que sepas que yo soy Jehová en medio de la tierra.', 'sand'),
('Old', 'Éxodo', 8, 23, 'Y yo pondré redención entre mi pueblo y el tuyo. Mañana será esta señal.', 'sand'),
('Old', 'Éxodo', 8, 24, 'Y Jehová lo hizo así: que vino toda suerte de moscas molestísimas sobre la casa de Faraón, y sobre las casas de sus siervos, y sobre todo el país de Egipto; y la tierra fué corrompida á causa de ellas.', 'sand'),
('Old', 'Éxodo', 8, 25, 'Entonces Faraón llamó á Moisés y á Aarón, y díjoles: Andad, sacrificad á vuestro Dios en la tierra.', 'sand'),
('Old', 'Éxodo', 8, 26, 'Y Moisés respondió: No conviene que hagamos así, porque sacrificaríamos á Jehová nuestro Dios la abominación de los Egipcios. He aquí, si sacrificáramos la abominación de los Egipcios delante de ellos, ¿no nos apedrearían?', 'sand'),
('Old', 'Éxodo', 8, 27, 'Camino de tres días iremos por el desierto, y sacrificaremos á Jehová nuestro Dios, como él nos dirá.', 'sand'),
('Old', 'Éxodo', 8, 28, 'Y dijo Faraón: Yo os dejaré ir para que sacrifiquéis á Jehová vuestro Dios en el desierto, con tal que no vayáis más lejos: orad por mí.', 'sand'),
('Old', 'Éxodo', 8, 29, 'Y respondió Moisés: He aquí, en saliendo yo de contigo, rogaré á Jehová que las diversas suertes de moscas se vayan de Faraón, y de sus siervos, y de su pueblo mañana; con tal que Faraón no falte más, no dejando ir al pueblo á sacrificar á Jehová.', 'sand'),
('Old', 'Éxodo', 8, 30, 'Entonces Moisés salió de con Faraón, y oró á Jehová.', 'sand'),
('Old', 'Éxodo', 8, 31, 'Y Jehová hizo conforme á la palabra de Moisés; y quitó todas aquellas moscas de Faraón, y de sus siervos, y de su pueblo, sin que quedara una.', 'sand'),
('Old', 'Éxodo', 8, 32, 'Mas Faraón agravó aún esta vez su corazón, y no dejó ir al pueblo.', 'sand'),
('Old', 'Éxodo', 9, 1, 'ENTONCES Jehová dijo á Moisés: Entra á Faraón, y dile: Jehová, el Dios de los Hebreos, dice así: Deja ir á mi pueblo, para que me sirvan;', 'sand'),
('Old', 'Éxodo', 9, 2, 'Porque si no lo quieres dejar ir, y los detuvieres aún,', 'sand'),
('Old', 'Éxodo', 9, 3, 'He aquí la mano de Jehová será sobre tus ganados que están en el campo, caballos, asnos, camellos, vacas y ovejas, con pestilencia gravísima:', 'sand'),
('Old', 'Éxodo', 9, 4, 'Y Jehová hará separación entre los ganados de Israel y los de Egipto, de modo que nada muera de todo lo de los hijos de Israel.', 'sand'),
('Old', 'Éxodo', 9, 5, 'Y Jehová señaló tiempo, diciendo: Mañana hará Jehová esta cosa en la tierra.', 'sand'),
('Old', 'Éxodo', 9, 6, 'Y el día siguiente Jehová hizo aquello, y murió todo el ganado de Egipto; mas del ganado de los hijos de Israel no murió uno.', 'sand'),
('Old', 'Éxodo', 9, 7, 'Entonces Faraón envió, y he aquí que del ganado de los hijos de Israel no había muerto uno. Mas el corazón de Faraón se agravó, y no dejó ir al pueblo.', 'sand'),
('Old', 'Éxodo', 9, 8, 'Y Jehová dijo á Moisés y á Aarón: Tomad puñados de ceniza de un horno, y espárzala Moisés hacia el cielo delante de Faraón:', 'sand'),
('Old', 'Éxodo', 9, 9, 'Y vendrá á ser polvo sobre toda la tierra de Egipto, el cual originará sarpullido que cause tumores apostemados en los hombres y en las bestias, por todo el país de Egipto.', 'sand'),
('Old', 'Éxodo', 9, 10, 'Y tomaron la ceniza del horno, y pusiéronse delante de Faraón, y esparcióla Moisés hacia el cielo; y vino un sarpullido que causaba tumores apostemados así en los hombres como en las bestias.', 'sand'),
('Old', 'Éxodo', 9, 11, 'Y los magos no podían estar delante de Moisés á causa de los tumores, porque hubo sarpullido en los magos y en todos los Egipcios.', 'sand'),
('Old', 'Éxodo', 9, 12, 'Y Jehová endureció el corazón de Faraón, y no los oyó; como Jehová lo había dicho á Moisés.', 'sand'),
('Old', 'Éxodo', 9, 13, 'Entonces Jehová dijo á Moisés: Levántate de mañana, y ponte delante de Faraón, y dile: Jehová, el Dios de los Hebreos, dice así: Deja ir á mi pueblo, para que me sirva.', 'sand'),
('Old', 'Éxodo', 9, 14, 'Porque yo enviaré esta vez todas mis plagas á tu corazón, sobre tus siervos, y sobre tu pueblo, para que entiendas que no hay otro como yo en toda la tierra.', 'sand'),
('Old', 'Éxodo', 9, 15, 'Porque ahora yo extenderé mi mano para herirte á ti y á tu pueblo de pestilencia, y serás quitado de la tierra.', 'sand'),
('Old', 'Éxodo', 9, 16, 'Y á la verdad yo te he puesto para declarar en ti mi potencia, y que mi Nombre sea contado en toda la tierra.', 'sand'),
('Old', 'Éxodo', 9, 17, '¿Todavía te ensalzas tú contra mi pueblo, para no dejarlos ir?', 'sand'),
('Old', 'Éxodo', 9, 18, 'He aquí que mañana á estas horas yo haré llover granizo muy grave, cual nunca fué en Egipto, desde el día que se fundó hasta ahora.', 'sand'),
('Old', 'Éxodo', 9, 19, 'Envía, pues, á recoger tu ganado, y todo lo que tienes en el campo; porque todo hombre ó animal que se hallare en el campo, y no fuere recogido á casa, el granizo descenderá sobre él, y morirá.', 'sand'),
('Old', 'Éxodo', 9, 20, 'De los siervos de Faraón el que temió la palabra de Jehová, hizo huir sus criados y su ganado á casa:', 'sand'),
('Old', 'Éxodo', 9, 21, 'Mas el que no puso en su corazón la palabra de Jehová, dejó sus criados y sus ganados en el campo.', 'sand'),
('Old', 'Éxodo', 9, 22, 'Y Jehová dijo á Moisés: Extiende tu mano hacia el cielo, para que venga granizo en toda la tierra de Egipto sobre los hombres, y sobre las bestias, y sobre toda la hierba del campo en el país de Egipto.', 'sand'),
('Old', 'Éxodo', 9, 23, 'Y Moisés extendió su vara hacia el cielo, y Jehová hizo tronar y granizar, y el fuego discurría por la tierra; y llovió Jehová granizo sobre la tierra de Egipto.', 'sand'),
('Old', 'Éxodo', 9, 24, 'Hubo pues granizo, y fuego mezclado con el granizo, tan grande, cual nunca hubo en toda la tierra de Egipto desde que fué habitada.', 'sand'),
('Old', 'Éxodo', 9, 25, 'Y aquel granizo hirió en toda la tierra de Egipto todo lo que estaba en el campo, así hombres como bestias; asimismo hirió el granizo toda la hierba del campo, y desgajó todos los árboles del país.', 'sand'),
('Old', 'Éxodo', 9, 26, 'Solamente en la tierra de Gosén, donde los hijos de Israel estaban, no hubo granizo.', 'sand'),
('Old', 'Éxodo', 9, 27, 'Entonces Faraón envió á llamar á Moisés y á Aarón, y les dijo: He pecado esta vez: Jehová es justo, y yo y mi pueblo impíos.', 'sand'),
('Old', 'Éxodo', 9, 28, 'Orad á Jehová: y cesen los truenos de Dios y el granizo; y yo os dejaré ir, y no os detendréis más.', 'sand'),
('Old', 'Éxodo', 9, 29, 'Y respondióle Moisés: En saliendo yo de la ciudad extenderé mis manos á Jehová, y los truenos cesarán, y no habrá más granizo; para que sepas que de Jehová es la tierra.', 'sand'),
('Old', 'Éxodo', 9, 30, 'Mas yo sé que ni tú ni tus siervos temeréis todavía la presencia del Dios Jehová.', 'sand'),
('Old', 'Éxodo', 9, 31, 'El lino, pues, y la cebada fueron heridos; porque la cebada estaba ya espigada, y el lino en caña.', 'sand'),
('Old', 'Éxodo', 9, 32, 'Mas el trigo y el centeno no fueron heridos; porque eran tardíos.', 'sand'),
('Old', 'Éxodo', 9, 33, 'Y salido Moisés de con Faraón de la ciudad, extendió sus manos á Jehová, y cesaron los truenos y el granizo; y la lluvia no cayó más sobre la tierra.', 'sand'),
('Old', 'Éxodo', 9, 34, 'Y viendo Faraón que la lluvia había cesado y el granizo y los truenos, perseveró en pecar, y agravó su corazón, él y sus siervos.', 'sand'),
('Old', 'Éxodo', 9, 35, 'Y el corazón de Faraón se endureció, y no dejó ir á los hijos de Israel; como Jehová lo había dicho por medio de Moisés.', 'sand'),
('Old', 'Éxodo', 10, 1, 'Y JEHOVA dijo á Moisés: Entra á Faraón; porque yo he agravado su corazón, y el corazón de sus siervos, para dar entre ellos estas mis señales;', 'sand'),
('Old', 'Éxodo', 10, 2, 'Y para que cuentes á tus hijos y á tus nietos las cosas que yo hice en Egipto, y mis señales que dí entre ellos; y para que sepáis que yo soy Jehová.', 'sand'),
('Old', 'Éxodo', 10, 3, 'Entonces vinieron Moisés y Aarón á Faraón, y le dijeron: Jehová, el Dios de los Hebreos ha dicho así: ¿Hasta cuándo no querrás humillarte delante de mí? Deja ir á mi pueblo para que me sirvan.', 'sand'),
('Old', 'Éxodo', 10, 4, 'Y si aún rehusas dejarlo ir, he aquí que yo traeré mañana langosta en tus términos,', 'sand'),
('Old', 'Éxodo', 10, 5, 'La cual cubrirá la faz de la tierra, de modo que no pueda verse la tierra; y ella comerá lo que quedó salvo, lo que os ha quedado del granizo; comerá asimismo todo árbol que os produce fruto en el campo:', 'sand'),
('Old', 'Éxodo', 10, 6, 'Y llenarse han tus casas, y las casas de todos tus siervos, y las casas de todos los Egipcios, cual nunca vieron tus padres ni tus abuelos, desde que ellos fueron sobre la tierra hasta hoy. Y volvióse, y salió de con Faraón.', 'sand'),
('Old', 'Éxodo', 10, 7, 'Entonces los siervos de Faraón le dijeron: ¿Hasta cuándo nos ha de ser éste por lazo? Deja ir á estos hombres, para que sirvan á Jehová su Dios; ¿aun no sabes que Egipto está destruido?', 'sand'),
('Old', 'Éxodo', 10, 8, 'Y Moisés y Aarón volvieron á ser llamados á Faraón, el cual les dijo: Andad, servid á Jehová vuestro Dios. ¿Quién y quién son los que han de ir?', 'sand'),
('Old', 'Éxodo', 10, 9, 'Y Moisés respondió: Hemos de ir con nuestros niños y con nuestros viejos, con nuestros hijos y con nuestras hijas: con nuestras ovejas y con nuestras vacas hemos de ir; porque tenemos solemnidad de Jehová.', 'sand'),
('Old', 'Éxodo', 10, 10, 'Y él les dijo: Así sea Jehová con vosotros como yo os dejaré ir á vosotros y á vuestros niños: mirad como el mal está delante de vuestro rostro.', 'sand'),
('Old', 'Éxodo', 10, 11, 'No será así: id ahora vosotros los varones, y servid á Jehová: pues esto es lo que vosotros demandasteis. Y echáronlos de delante de Faraón.', 'sand'),
('Old', 'Éxodo', 10, 12, 'Entonces Jehová dijo á Moisés: Extiende tu mano sobre la tierra de Egipto para langosta, á fin de que suba sobre el país de Egipto, y consuma todo lo que el granizo dejó.', 'sand'),
('Old', 'Éxodo', 10, 13, 'Y extendió Moisés su vara sobre la tierra de Egipto, y Jehová trajo un viento oriental sobre el país todo aquel día y toda aquella noche; y á la mañana el viento oriental trajo la langosta:', 'sand'),
('Old', 'Éxodo', 10, 14, 'Y subió la langosta sobre toda la tierra de Egipto, y asentóse en todos los términos de Egipto, en gran manera grave: antes de ella no hubo langosta semejante, ni después de ella vendrá otra tal;', 'sand'),
('Old', 'Éxodo', 10, 15, 'Y cubrió la faz de todo el país, y oscurecióse la tierra; y consumió toda la hierba de la tierra, y todo el fruto de los árboles que había dejado el granizo; que no quedó cosa verde en árboles ni en hierba del campo, por toda la tierra de Egipto.', 'sand'),
('Old', 'Éxodo', 10, 16, 'Entonces Faraón hizo llamar apriesa á Moisés y á Aarón, y dijo: He pecado contra Jehová vuestro Dios, y contra vosotros.', 'sand'),
('Old', 'Éxodo', 10, 17, 'Mas ruego ahora que perdones mi pecado solamente esta vez, y que oréis á Jehová vuestro Dios que quite de mí solamente esta muerte.', 'sand'),
('Old', 'Éxodo', 10, 18, 'Y salió de con Faraón, y oró á Jehová.', 'sand'),
('Old', 'Éxodo', 10, 19, 'Y Jehová volvió un viento occidental fortísimo, y quitó la langosta, y arrojóla en el mar Bermejo: ni una langosta quedó en todo el término de Egipto.', 'sand'),
('Old', 'Éxodo', 10, 20, 'Mas Jehová endureció el corazón de Faraón; y no envió los hijos de Israel.', 'sand'),
('Old', 'Éxodo', 10, 21, 'Y Jehová dijo á Moisés: Extiende tu mano hacia el cielo, para que haya tinieblas sobre la tierra de Egipto, tales que cualquiera las palpe.', 'sand'),
('Old', 'Éxodo', 10, 22, 'Y extendió Moisés su mano hacia el cielo, y hubo densas tinieblas tres días por toda la tierra de Egipto.', 'sand'),
('Old', 'Éxodo', 10, 23, 'Ninguno vió á su prójimo, ni nadie se levantó de su lugar en tres días; mas todos los hijos de Israel tenían luz en sus habitaciones.', 'sand'),
('Old', 'Éxodo', 10, 24, 'Entonces Faraón hizo llamar á Moisés, y dijo: Id, servid á Jehová; solamente queden vuestras ovejas y vuestras vacas: vayan también vuestros niños con vosotros.', 'sand'),
('Old', 'Éxodo', 10, 25, 'Y Moisés respondió: Tú también nos entregarás sacrificios y holocaustos que sacrifiquemos á Jehová nuestro Dios.', 'sand'),
('Old', 'Éxodo', 10, 26, 'Nuestros ganados irán también con nosotros; no quedará ni una uña; porque de ellos hemos de tomar para servir á Jehová nuestro Dios; y no sabemos con qué hemos de servir á Jehová, hasta que lleguemos allá.', 'sand'),
('Old', 'Éxodo', 10, 27, 'Mas Jehová endureció el corazón de Faraón, y no quiso dejarlos ir.', 'sand'),
('Old', 'Éxodo', 10, 28, 'Y díjole Faraón: Retírate de mí: guárdate que no veas más mi rostro, porque en cualquier día que vieres mi rostro, morirás.', 'sand'),
('Old', 'Éxodo', 10, 29, 'Y Moisés respondió: Bien has dicho; no veré más tu rostro.', 'sand'),
('Old', 'Éxodo', 11, 1, 'Y JEHOVA dijo á Moisés: Una plaga traeré aún sobre Faraón, y sobre Egipto; después de la cual él os dejará ir de aquí; y seguramente os echará de aquí del todo.', 'sand'),
('Old', 'Éxodo', 11, 2, 'Habla ahora al pueblo, y que cada uno demande á su vecino, y cada una á su vecina, vasos de plata y de oro.', 'sand'),
('Old', 'Éxodo', 11, 3, 'Y Jehová dió gracia al pueblo en los ojos de los Egipcios. También Moisés era muy gran varón en la tierra de Egipto, á los ojos de los siervos de Faraón, y á los ojos del pueblo.', 'sand'),
('Old', 'Éxodo', 11, 4, 'Y dijo Moisés: Jehová ha dicho así: A la media noche yo saldré por medio de Egipto,', 'sand'),
('Old', 'Éxodo', 11, 5, 'Y morirá todo primogénito en tierra de Egipto, desde el primogénito de Faraón que se sienta en su trono, hasta el primogénito de la sierva que está tras la muela; y todo primogénito de las bestias.', 'sand'),
('Old', 'Éxodo', 11, 6, 'Y habrá gran clamor por toda la tierra de Egipto, cual nunca fué, ni jamás será.', 'sand'),
('Old', 'Éxodo', 11, 7, 'Mas entre todos los hijos de Israel, desde el hombre hasta la bestia, ni un perro moverá su lengua: para que sepáis que hará diferencia Jehová entre los Egipcios y los Israelitas.', 'sand'),
('Old', 'Éxodo', 11, 8, 'Y descenderán á mí todos estos tus siervos, é inclinados delante de mí dirán: Sal tú, y todo el pueblo que está bajo de ti; y después de esto yo saldré. Y salióse muy enojado de con Faraón.', 'sand'),
('Old', 'Éxodo', 11, 9, 'Y Jehová dijo á Moisés: Faraón no os oirá, para que mis maravillas se multipliquen en la tierra de Egipto.', 'sand'),
('Old', 'Éxodo', 11, 10, 'Y Moisés y Aarón hicieron todos estos prodigios delante de Faraón: mas Jehová había endurecido el corazón de Faraón, y no envió á los hijos de Israel fuera de su país.', 'sand'),
('Old', 'Éxodo', 12, 1, 'Y HABLO Jehová á Moisés y á Aarón en la tierra de Egipto, diciendo:', 'sand'),
('Old', 'Éxodo', 12, 2, 'Este mes os será principio de los meses; será este para vosotros el primero en los meses del año.', 'sand'),
('Old', 'Éxodo', 12, 3, 'Hablad á toda la congregación de Israel, diciendo: En el diez de aqueste mes tómese cada uno un cordero por las familias de los padres, un cordero por familia:', 'sand'),
('Old', 'Éxodo', 12, 4, 'Mas si la familia fuere pequeña que no baste á comer el cordero, entonces tomará á su vecino inmediato á su casa, y según el número de las personas, cada uno conforme á su comer, echaréis la cuenta sobre el cordero.', 'sand'),
('Old', 'Éxodo', 12, 5, 'El cordero será sin defecto, macho de un año: tomaréislo de las ovejas ó de las cabras:', 'sand'),
('Old', 'Éxodo', 12, 6, 'Y habéis de guardarlo hasta el día catorce de este mes; y lo inmolará toda la congregación del pueblo de Israel entre las dos tardes.', 'sand'),
('Old', 'Éxodo', 12, 7, 'Y tomarán de la sangre, y pondrán en los dos postes y en el dintel de las casas en que lo han de comer.', 'sand'),
('Old', 'Éxodo', 12, 8, 'Y aquella noche comerán la carne asada al fuego, y panes sin levadura: con hierbas amargas lo comerán.', 'sand'),
('Old', 'Éxodo', 12, 9, 'Ninguna cosa comeréis de él cruda, ni cocida en agua, sino asada al fuego; su cabeza con sus pies y sus intestinos.', 'sand'),
('Old', 'Éxodo', 12, 10, 'Ninguna cosa dejaréis de él hasta la mañana; y lo que habrá quedado hasta la mañana, habéis de quemarlo en el fuego.', 'sand'),
('Old', 'Éxodo', 12, 11, 'Y así habéis de comerlo: ceñidos vuestros lomos, vuestros zapatos en vuestros pies, y vuestro bordón en vuestra mano; y lo comeréis apresuradamente: es la Pascua de Jehová.', 'sand'),
('Old', 'Éxodo', 12, 12, 'Pues yo pasaré aquella noche por la tierra de Egipto, y heriré á todo primogénito en la tierra de Egipto, así en los hombres como en las bestias: y haré juicios en todos los dioses de Egipto. YO JEHOVA.', 'sand'),
('Old', 'Éxodo', 12, 13, 'Y la sangre os será por señal en las casas donde vosotros estéis; y veré la sangre, y pasaré de vosotros, y no habrá en vosotros plaga de mortandad, cuando heriré la tierra de Egipto.', 'sand'),
('Old', 'Éxodo', 12, 14, 'Y este día os ha de ser en memoria, y habéis de celebrarlo como solemne á Jehová durante vuestras generaciones: por estatuto perpetuo lo celebraréis.', 'sand'),
('Old', 'Éxodo', 12, 15, 'Siete días comeréis panes sin levadura; y así el primer día haréis que no haya levadura en vuestras casas: porque cualquiera que comiere leudado desde el primer día hasta el séptimo, aquella alma será cortada de Israel.', 'sand'),
('Old', 'Éxodo', 12, 16, 'El primer día habrá santa convocación, y asimismo en el séptimo día tendréis una santa convocación: ninguna obra se hará en ellos, excepto solamente que aderecéis lo que cada cual hubiere de comer.', 'sand'),
('Old', 'Éxodo', 12, 17, 'Y guardaréis la fiesta de los ázimos, porque en aqueste mismo día saqué vuestros ejércitos de la tierra de Egipto: por tanto guardaréis este día en vuestras generaciones por costumbre perpetua.', 'sand'),
('Old', 'Éxodo', 12, 18, 'En el mes primero, el día catorce del mes por la tarde, comeréis los panes sin levadura, hasta el veintiuno del mes por la tarde.', 'sand'),
('Old', 'Éxodo', 12, 19, 'Por siete días no se hallará levadura en vuestras casas, porque cualquiera que comiere leudado, así extranjero como natural del país, aquella alma será cortada de la congregación de Israel.', 'sand'),
('Old', 'Éxodo', 12, 20, 'Ninguna cosa leudada comeréis; en todas vuestras habitaciones comeréis panes sin levadura.', 'sand'),
('Old', 'Éxodo', 12, 21, 'Y Moisés convocó á todos los ancianos de Israel, y díjoles: Sacad, y tomaos corderos por vuestras familias, y sacrificad la pascua.', 'sand'),
('Old', 'Éxodo', 12, 22, 'Y tomad un manojo de hisopo, y mojadle en la sangre que estará en una jofaina, y untad el dintel y los dos postes con la sangre que estará en la jofaina; y ninguno de vosotros salga de las puertas de su casa hasta la mañana.', 'sand'),
('Old', 'Éxodo', 12, 23, 'Porque Jehová pasará hiriendo á los Egipcios; y como verá la sangre en el dintel y en los dos postes, pasará Jehová aquella puerta, y no dejará entrar al heridor en vuestras casas para herir.', 'sand'),
('Old', 'Éxodo', 12, 24, 'Y guardaréis esto por estatuto para vosotros y para vuestros hijos para siempre.', 'sand'),
('Old', 'Éxodo', 12, 25, 'Y será, cuando habréis entrado en la tierra que Jehová os dará, como tiene hablado, que guardaréis este rito.', 'sand'),
('Old', 'Éxodo', 12, 26, 'Y cuando os dijeren vuestros hijos: ¿Qué rito es este vuestro?', 'sand'),
('Old', 'Éxodo', 12, 27, 'Vosotros responderéis: Es la víctima de la Pascua de Jehová, el cual pasó las casas de los hijos de Israel en Egipto, cuando hirió á los Egipcios, y libró nuestras casas. Entonces el pueblo se inclinó y adoró.', 'sand'),
('Old', 'Éxodo', 12, 28, 'Y los hijos de Israel se fueron, é hicieron puntualmente así; como Jehová había mandado á Moisés y á Aarón.', 'sand'),
('Old', 'Éxodo', 12, 29, 'Y aconteció que á la medianoche Jehová hirió á todo primogénito en la tierra de Egipto, desde el primogénito de Faraón que se sentaba sobre su trono, hasta el primogénito del cautivo que estaba en la cárcel, y todo primogénito de los animales.', 'sand'),
('Old', 'Éxodo', 12, 30, 'Y levantóse aquella noche Faraón, él y todos sus siervos, y todos los Egipcios; y había un gran clamor en Egipto, porque no había casa donde no hubiese muerto.', 'sand'),
('Old', 'Éxodo', 12, 31, 'E hizo llamar á Moisés y á Aarón de noche, y díjoles: Salid de en medio de mi pueblo vosotros, y los hijos de Israel; é id, servid á Jehová, como habéis dicho.', 'sand'),
('Old', 'Éxodo', 12, 32, 'Tomad también vuestras ovejas y vuestras vacas, como habéis dicho, é idos; y bendecidme también á mí.', 'sand'),
('Old', 'Éxodo', 12, 33, 'Y los Egipcios apremiaban al pueblo, dándose priesa á echarlos de la tierra; porque decían: Todos somos muertos.', 'sand'),
('Old', 'Éxodo', 12, 34, 'Y llevó el pueblo su masa antes que se leudase, sus masas envueltas en sus sábanas sobre sus hombros.', 'sand'),
('Old', 'Éxodo', 12, 35, 'E hicieron los hijos de Israel conforme al mandamiento de Moisés, demandando á los Egipcios vasos de plata, y vasos de oro, y vestidos.', 'sand'),
('Old', 'Éxodo', 12, 36, 'Y Jehová dió gracia al pueblo delante de los Egipcios, y prestáronles; y ellos despojaron á los Egipcios.', 'sand'),
('Old', 'Éxodo', 12, 37, 'Y partieron los hijos de Israel de Rameses á Succoth, como seiscientos mil hombres de á pie, sin contar los niños.', 'sand'),
('Old', 'Éxodo', 12, 38, 'Y también subió con ellos grande multitud de diversa suerte de gentes; y ovejas, y ganados muy muchos.', 'sand'),
('Old', 'Éxodo', 12, 39, 'Y cocieron tortas sin levadura de la masa que habían sacado de Egipto; porque no había leudado, por cuanto echándolos los Egipcios, no habían podido detenerse, ni aun prepararse comida.', 'sand'),
('Old', 'Éxodo', 12, 40, 'El tiempo que los hijos de Israel habitaron en Egipto, fué cuatrocientos treinta años.', 'sand'),
('Old', 'Éxodo', 12, 41, 'Y pasados cuatrocientos treinta años, en el mismo día salieron todos los ejércitos de Jehová de la tierra de Egipto.', 'sand'),
('Old', 'Éxodo', 12, 42, 'Es noche de guardar á Jehová, por haberlos sacado en ella de la tierra de Egipto. Esta noche deben guardar á Jehová todos los hijos de Israel en sus generaciones.', 'sand'),
('Old', 'Éxodo', 12, 43, 'Y Jehová dijo á Moisés y á Aarón: Esta es la ordenanza de la Pascua: Ningún extraño comerá de ella:', 'sand'),
('Old', 'Éxodo', 12, 44, 'Mas todo siervo humano comprado por dinero, comerá de ella después que lo hubieres circuncidado.', 'sand'),
('Old', 'Éxodo', 12, 45, 'El extranjero y el asalariado no comerán de ella.', 'sand'),
('Old', 'Éxodo', 12, 46, 'En una casa se comerá, y no llevarás de aquella carne fuera de casa, ni quebraréis hueso suyo.', 'sand'),
('Old', 'Éxodo', 12, 47, 'Toda la congregación de Israel le sacrificará.', 'sand'),
('Old', 'Éxodo', 12, 48, 'Mas si algún extranjero peregrinare contigo, y quisiere hacer la pascua á Jehová, séale circuncidado todo varón, y entonces se llegará á hacerla, y será como el natural de la tierra; pero ningún incircunciso comerá de ella.', 'sand'),
('Old', 'Éxodo', 12, 49, 'La misma ley será para el natural y para el extranjero que peregrinare entre vosotros.', 'sand'),
('Old', 'Éxodo', 12, 50, 'Así lo hicieron todos los hijos de Israel; como mandó Jehová á Moisés y á Aarón, así lo hicieron.', 'sand'),
('Old', 'Éxodo', 12, 51, 'Y en aquel mismo día sacó Jehová á los hijos de Israel de la tierra de Egipto por sus escuadrones.', 'sand'),
('Old', 'Éxodo', 13, 1, 'Y JEHOVA habló á Moisés, diciendo:', 'sand'),
('Old', 'Éxodo', 13, 2, 'Santifícame todo primogénito, cualquiera que abre matriz entre los hijos de Israel, así de los hombres como de los animales: mío es.', 'sand'),
('Old', 'Éxodo', 13, 3, 'Y Moisés dijo al pueblo: Tened memoria de aqueste día, en el cual habéis salido de Egipto, de la casa de servidumbre; pues Jehová os ha sacado de aquí con mano fuerte; por tanto, no comeréis leudado.', 'sand'),
('Old', 'Éxodo', 13, 4, 'Vosotros salís hoy en el mes de Abib.', 'sand'),
('Old', 'Éxodo', 13, 5, 'Y cuando Jehová te hubiere metido en la tierra del Cananeo, y del Hetheo, y del Amorrheo, y del Hebeo, y del Jebuseo, la cual juró á tus padres que te daría, tierra que destila leche y miel, harás esta servicio en aqueste mes.', 'sand'),
('Old', 'Éxodo', 13, 6, 'Siete días comerás por leudar, y el séptimo día será fiesta á Jehová.', 'sand'),
('Old', 'Éxodo', 13, 7, 'Por los siete días se comerán los panes sin levadura; y no se verá contigo leudado, ni levadura en todo tu término.', 'sand'),
('Old', 'Éxodo', 13, 8, 'Y contarás en aquel día á tu hijo, diciendo: Hácese esto con motivo de lo que Jehová hizo conmigo cuando me sacó de Egipto.', 'sand'),
('Old', 'Éxodo', 13, 9, 'Y serte ha como una señal sobre tu mano, y como una memoria delante de tus ojos, para que la ley de Jehová esté en tu boca; por cuanto con mano fuerte te sacó Jehová de Egipto.', 'sand'),
('Old', 'Éxodo', 13, 10, 'Por tanto, tú guardarás este rito en su tiempo de año en año.', 'sand'),
('Old', 'Éxodo', 13, 11, 'Y cuando Jehová te hubiere metido en la tierra del Cananeo, como te ha jurado á ti y á tus padres, y cuando te la hubiere dado,', 'sand'),
('Old', 'Éxodo', 13, 12, 'Harás pasar á Jehová todo lo que abriere la matriz, asimismo todo primerizo que abriere la matriz de tus animales: los machos serán de Jehová.', 'sand'),
('Old', 'Éxodo', 13, 13, 'Mas todo primogénito de asno redimirás con un cordero; y si no lo redimieres, le degollarás: asimismo redimirás todo humano primogénito de tus hijos.', 'sand'),
('Old', 'Éxodo', 13, 14, 'Y cuando mañana te preguntare tu hijo, diciendo: ¿Qué es esto? decirle has: Jehová nos sacó con mano fuerte de Egipto, de casa de servidumbre;', 'sand'),
('Old', 'Éxodo', 13, 15, 'Y endureciéndose Faraón en no dejarnos ir, Jehová mató en la tierra de Egipto á todo primogénito, desde el primogénito humano hasta el primogénito de la bestia: y por esta causa yo sacrifico á Jehová todo primogénito macho, y redimo todo primogénito de mis hijos.', 'sand'),
('Old', 'Éxodo', 13, 16, 'Serte ha, pues, como una señal sobre tu mano, y por una memoria delante de tus ojos; ya que Jehová nos sacó de Egipto con mano fuerte.', 'sand'),
('Old', 'Éxodo', 13, 17, 'Y luego que Faraón dejó ir al pueblo, Dios no los llevó por el camino de la tierra de los Filisteos, que estaba cerca; porque dijo Dios: Que quizá no se arrepienta el pueblo cuando vieren la guerra, y se vuelvan á Egipto:', 'sand'),
('Old', 'Éxodo', 13, 18, 'Mas hizo Dios al pueblo que rodease por el camino del desierto del mar Bermejo. Y subieron los hijos de Israel de Egipto armados.', 'sand'),
('Old', 'Éxodo', 13, 19, 'Tomó también consigo Moisés los huesos de José, el cual había juramentado á los hijos de Israel, diciendo: Dios ciertamente os visitará, y haréis subir mis huesos de aquí con vosotros.', 'sand'),
('Old', 'Éxodo', 13, 20, 'Y partidos de Succoth, asentaron campo en Etham, á la entrada del desierto.', 'sand'),
('Old', 'Éxodo', 13, 21, 'Y Jehová iba delante de ellos de día en una columna de nube, para guiarlos por el camino; y de noche en una columna de fuego para alumbrarles; á fin de que anduviesen de día y de noche.', 'sand'),
('Old', 'Éxodo', 13, 22, 'Nunca se partió de delante del pueblo la columna de nube de día, ni de noche la columna de fuego.', 'sand'),
('Old', 'Éxodo', 14, 1, 'Y HABLO Jehová á Moisés, diciendo:', 'sand'),
('Old', 'Éxodo', 14, 2, 'Habla á los hijos de Israel que den la vuelta, y asienten su campo delante de Pihahiroth, entre Migdol y la mar hacia Baalzephón: delante de él asentaréis el campo, junto á la mar.', 'sand'),
('Old', 'Éxodo', 14, 3, 'Porque Faraón dirá de los hijos de Israel: Encerrados están en la tierra, el desierto los ha encerrado.', 'sand'),
('Old', 'Éxodo', 14, 4, 'Y yo endureceré el corazón de Faraón para que los siga; y seré glorificado en Faraón y en todo su ejército; y sabrán los Egipcios que yo soy Jehová. Y ellos lo hicieron así.', 'sand'),
('Old', 'Éxodo', 14, 5, 'Y fué dado aviso al rey de Egipto cómo el pueblo se huía: y el corazón de Faraón y de sus siervos se volvió contra el pueblo, y dijeron: ¿Cómo hemos hecho esto de haber dejado ir á Israel, para que no nos sirva?', 'sand'),
('Old', 'Éxodo', 14, 6, 'Y unció su carro, y tomó consigo su pueblo;', 'sand'),
('Old', 'Éxodo', 14, 7, 'y tomó seiscientos carros escogidos, y todos los carros de Egipto, y los capitanes sobre ellos.', 'sand'),
('Old', 'Éxodo', 14, 8, 'Y endureció Jehová el corazón de Faraón rey de Egipto, y siguió á los hijos de Israel; pero los hijos de Israel habían salido con mano poderosa.', 'sand'),
('Old', 'Éxodo', 14, 9, 'Siguiéndolos, pues, los Egipcios, con toda la caballería y carros de Faraón, su gente de á caballo, y todo su ejército, alcanzáronlos asentando el campo junto á la mar, al lado de Pihahiroth, delante de Baalzephón.', 'sand'),
('Old', 'Éxodo', 14, 10, 'Y cuando Faraón se hubo acercado, los hijos de Israel alzaron sus ojos, y he aquí los Egipcios que venían tras ellos; por lo que temieron en gran manera, y clamaron los hijos de Israel á Jehová.', 'sand'),
('Old', 'Éxodo', 14, 11, 'Y dijeron á Moisés: ¿No había sepulcros en Egipto, que nos has sacado para que muramos en el desierto? ¿Por qué lo has hecho así con nosotros, que nos has sacado de Egipto?', 'sand'),
('Old', 'Éxodo', 14, 12, '¿No es esto lo que te hablamos en Egipto, diciendo: Déjanos servir á los Egipcios? Que mejor nos fuera servir á los Egipcios, que morir nosotros en el desierto.', 'sand'),
('Old', 'Éxodo', 14, 13, 'Y Moisés dijo al pueblo: No temáis; estaos quedos, y ved la salud de Jehová, que él hará hoy con vosotros; porque los Egipcios que hoy habéis visto, nunca más para siempre los veréis.', 'sand'),
('Old', 'Éxodo', 14, 14, 'Jehová peleará por vosotros, y vosotros estaréis quedos.', 'sand'),
('Old', 'Éxodo', 14, 15, 'Entonces Jehová dijo á Moisés: ¿Por qué clamas á mí? Di á los hijos de Israel que marchen.', 'sand'),
('Old', 'Éxodo', 14, 16, 'Y tú alza tu vara, y extiende tu mano sobre la mar, y divídela; y entren los hijos de Israel por medio de la mar en seco.', 'sand'),
('Old', 'Éxodo', 14, 17, 'Y yo, he aquí yo endureceré el corazón de los Egipcios, para que los sigan: y yo me glorificaré en Faraón, y en todo su ejército, y en sus carros, y en su caballería;', 'sand'),
('Old', 'Éxodo', 14, 18, 'Y sabrán los Egipcios que yo soy Jehová, cuando me glorificaré en Faraón, en sus carros, y en su gente de á caballo.', 'sand'),
('Old', 'Éxodo', 14, 19, 'Y el ángel de Dios que iba delante del campo de Israel, se apartó, é iba en pos de ellos; y asimismo la columna de nube que iba delante de ellos, se apartó, y púsose á sus espaldas:', 'sand'),
('Old', 'Éxodo', 14, 20, 'E iba entre el campo de los Egipcios y el campo de Israel; y era nube y tinieblas para aquéllos, y alumbraba á Israel de noche: y en toda aquella noche nunca llegaron los unos á los otros.', 'sand'),
('Old', 'Éxodo', 14, 21, 'Y extendió Moisés su mano sobre la mar, é hizo Jehová que la mar se retirase por recio viento oriental toda aquella noche; y tornó la mar en seco, y las aguas quedaron divididas.', 'sand'),
('Old', 'Éxodo', 14, 22, 'Entonces los hijos de Israel entraron por medio de la mar en seco, teniendo las aguas como muro á su diestra y á su siniestra:', 'sand'),
('Old', 'Éxodo', 14, 23, 'Y siguiéndolos los Egipcios, entraron tras ellos hasta el medio de la mar, toda la caballería de Faraón, sus carros, y su gente de á caballo.', 'sand'),
('Old', 'Éxodo', 14, 24, 'Y aconteció á la vela de la mañana, que Jehová miró al campo de los Egipcios desde la columna de fuego y nube, y perturbó el campo de los Egipcios.', 'sand'),
('Old', 'Éxodo', 14, 25, 'Y quitóles las ruedas de sus carros, y trastornólos gravemente. Entonces los Egipcios dijeron: Huyamos de delante de Israel, porque Jehová pelea por ellos contra los Egipcios.', 'sand'),
('Old', 'Éxodo', 14, 26, 'Y Jehová dijo á Moisés: Extiende tu mano sobre la mar, para que las aguas vuelvan sobre los Egipcios, sobre sus carros, y sobre su caballería.', 'sand'),
('Old', 'Éxodo', 14, 27, 'Y Moisés extendió su mano sobre la mar, y la mar se volvió en su fuerza cuando amanecía; y los Egipcios iban hacia ella: y Jehová derribó á los Egipcios en medio de la mar.', 'sand'),
('Old', 'Éxodo', 14, 28, 'Y volvieron las aguas, y cubrieron los carros y la caballería, y todo el ejército de Faraón que había entrado tras ellos en la mar; no quedó de ellos ni uno.', 'sand'),
('Old', 'Éxodo', 14, 29, 'Y los hijos de Israel fueron por medio de la mar en seco, teniendo las aguas por muro á su diestra y á su siniestra.', 'sand'),
('Old', 'Éxodo', 14, 30, 'Así salvó Jehová aquel día á Israel de mano de los Egipcios; é Israel vió á los Egipcios muertos á la orilla de la mar.', 'sand'),
('Old', 'Éxodo', 14, 31, 'Y vió Israel aquel grande hecho que Jehová ejecutó contra los Egipcios: y el pueblo temió á Jehová, y creyeron á Jehová y á Moisés su siervo.', 'sand'),
('Old', 'Éxodo', 15, 1, 'ENTONCES cantó Moisés y los hijos de Israel este cántico á Jehová, y dijeron: Cantaré yo á Jehová, porque se ha magnificado grandemente, Echando en la mar al caballo y al que en él subía.', 'sand'),
('Old', 'Éxodo', 15, 2, 'Jehová es mi fortaleza, y mi canción, Y hame sido por salud: Este es mi Dios, y á éste engrandeceré; Dios de mi padre, y á éste ensalzaré.', 'sand'),
('Old', 'Éxodo', 15, 3, 'Jehová, varón de guerra; Jehová es su nombre.', 'sand'),
('Old', 'Éxodo', 15, 4, 'Los carros de Faraón y á su ejército echó en la mar; Y sus escogidos príncipes fueron hundidos en el mar Bermejo.', 'sand'),
('Old', 'Éxodo', 15, 5, 'Los abismos los cubrieron; Como piedra descendieron á los profundos.', 'sand'),
('Old', 'Éxodo', 15, 6, 'Tu diestra, oh Jehová, ha sido magnificada en fortaleza; Tu diestra, oh Jehová, ha quebrantado al enemigo.', 'sand'),
('Old', 'Éxodo', 15, 7, 'Y con la grandeza de tu poder has trastornado á los que se levantaron contra ti: Enviaste tu furor; los tragó como á hojarasca.', 'sand'),
('Old', 'Éxodo', 15, 8, 'Con el soplo de tus narices se amontonaron las aguas; Paráronse las corrientes como en un montón; Los abismos se cuajaron en medio de la mar.', 'sand'),
('Old', 'Éxodo', 15, 9, 'El enemigo dijo: Perseguiré, prenderé, repartiré despojos; Mi alma se henchirá de ellos; Sacaré mi espada, destruirlos ha mi mano.', 'sand'),
('Old', 'Éxodo', 15, 10, 'Soplaste con tu viento, cubriólos la mar: Hundiéronse como plomo en las impetuosas aguas.', 'sand'),
('Old', 'Éxodo', 15, 11, '¿Quién como tú, Jehová, entre los dioses? ¿Quién como tú, magnífico en santidad, Terrible en loores, hacedor de maravillas?', 'sand'),
('Old', 'Éxodo', 15, 12, 'Extendiste tu diestra; La tierra los tragó.', 'sand'),
('Old', 'Éxodo', 15, 13, 'Condujiste en tu misericordia á este pueblo, al cual salvaste; Llevástelo con tu fortaleza á la habitación de tu santuario.', 'sand'),
('Old', 'Éxodo', 15, 14, 'Oiránlo los pueblos, y temblarán; Apoderarse ha dolor de los moradores de Palestina.', 'sand'),
('Old', 'Éxodo', 15, 15, 'Entonces los príncipes de Edom se turbarán; A los robustos de Moab los ocupará temblor; Abatirse han todos los moradores de Canaán.', 'sand'),
('Old', 'Éxodo', 15, 16, 'Caiga sobre ellos temblor y espanto; A la grandeza de tu brazo enmudezcan como una piedra; Hasta que haya pasado tu pueblo, oh Jehová, Hasta que haya pasado este pueblo que tú rescataste.', 'sand'),
('Old', 'Éxodo', 15, 17, 'Tú los introducirás y los plantarás en el monte de tu heredad, En el lugar de tu morada, que tú has aparejado, oh Jehová; En el santuario del Señor, que han afirmado tus manos.', 'sand'),
('Old', 'Éxodo', 15, 18, 'Jehová reinará por los siglos de los siglos.', 'sand'),
('Old', 'Éxodo', 15, 19, 'Porque Faraón entró cabalgando con sus carros y su gente de á caballo en la mar, y Jehová volvió á traer las aguas de la mar sobre ellos; mas los hijos de Israel fueron en seco por medio de la mar.', 'sand'),
('Old', 'Éxodo', 15, 20, 'Y María la profetisa, hermana de Aarón, tomó un pandero en su mano, y todas las mujeres salieron en pos de ella con panderos y danzas.', 'sand'),
('Old', 'Éxodo', 15, 21, 'Y María les respondía: Cantad á Jehová; porque en extremo se ha engrandecido, Echando en la mar al caballo, y al que en él subía.', 'sand'),
('Old', 'Éxodo', 15, 22, 'E hizo Moisés que partiese Israel del mar Bermejo, y salieron al desierto de Shur; y anduvieron tres días por el desierto sin hallar agua.', 'sand'),
('Old', 'Éxodo', 15, 23, 'Y llegaron á Mara, y no pudieron beber las aguas de Mara, porque eran amargas; por eso le pusieron el nombre de Mara.', 'sand'),
('Old', 'Éxodo', 15, 24, 'Entonces el pueblo murmuró contra Moisés, y dijo: ¿Qué hemos de beber?', 'sand'),
('Old', 'Éxodo', 15, 25, 'Y Moisés clamó á Jehová; y Jehová le mostró un árbol, el cual metídolo que hubo dentro de las aguas, las aguas se endulzaron. Allí les dió estatutos y ordenanzas, y allí los probó;', 'sand'),
('Old', 'Éxodo', 15, 26, 'Y dijo: Si oyeres atentamente la voz de Jehová tu Dios, é hicieres lo recto delante de sus ojos, y dieres oído á sus mandamientos, y guardares todos sus estatutos, ninguna enfermedad de las que envié á los Egipcios te enviaré á ti; porque yo soy Jehová tu Sanador.', 'sand'),
('Old', 'Éxodo', 15, 27, 'Y llegaron á Elim, donde había doce fuentes de aguas, y setenta palmas; y asentaron allí junto á las aguas.', 'sand'),
('Old', 'Éxodo', 16, 1, 'Y PARTIENDO de Elim toda la congregación de los hijos de Israel, vino al desierto de Sin, que está entre Elim y Sinaí, á los quince días del segundo mes después que salieron de la tierra de Egipto.', 'sand'),
('Old', 'Éxodo', 16, 2, 'Y toda la congregación de los hijos de Israel murmuró contra Moisés y Aarón en el desierto;', 'sand'),
('Old', 'Éxodo', 16, 3, 'Y decíanles los hijos de Israel: Ojalá hubiéramos muerto por mano de Jehová en la tierra de Egipto, cuando nos sentábamos á las ollas de las carnes, cuando comíamos pan en hartura; pues nos habéis sacado á este desierto, para matar de hambre á toda esta multitud.', 'sand'),
('Old', 'Éxodo', 16, 4, 'Y Jehová dijo á Moisés: He aquí yo os haré llover pan del cielo; y el pueblo saldrá, y cogerá para cada un día, para que yo le pruebe si anda en mi ley, ó no.', 'sand'),
('Old', 'Éxodo', 16, 5, 'Mas al sexto día aparejarán lo que han de encerrar, que será el doble de lo que solían coger cada día.', 'sand'),
('Old', 'Éxodo', 16, 6, 'Entonces dijo Moisés y Aarón á todos los hijos de Israel: A la tarde sabréis que Jehová os ha sacado de la tierra de Egipto:', 'sand'),
('Old', 'Éxodo', 16, 7, 'Y á la mañana veréis la gloria de Jehová; porque él ha oído vuestras murmuraciones contra Jehová; que nosotros, ¿qué somos, para que vosotros murmuréis contra nosotros?', 'sand'),
('Old', 'Éxodo', 16, 8, 'Y dijo Moisés: Jehová os dará á la tarde carne para comer, y á la mañana pan en hartura; por cuanto Jehová ha oído vuestras murmuraciones con que habéis murmurado contra él: que nosotros, ¿qué somos? vuestras murmuraciones no son contra nosotros, sino contra Jehová.', 'sand'),
('Old', 'Éxodo', 16, 9, 'Y dijo Moisés á Aarón: Di á toda la congregación de los hijos de Israel: Acercaos á la presencia de Jehová; que él ha oído vuestras murmuraciones.', 'sand'),
('Old', 'Éxodo', 16, 10, 'Y hablando Aarón á toda la congregación de los hijos de Israel, miraron hacia el desierto, y he aquí la gloria de Jehová, que apareció en la nube.', 'sand'),
('Old', 'Éxodo', 16, 11, 'Y Jehová habló á Moisés, diciendo:', 'sand'),
('Old', 'Éxodo', 16, 12, 'Yo he oído las murmuraciones de los hijos de Israel; háblales, diciendo: Entre las dos tardes comeréis carne, y por la mañana os hartaréis de pan, y sabréis que yo soy Jehová vuestro Dios.', 'sand'),
('Old', 'Éxodo', 16, 13, 'Y venida la tarde subieron codornices que cubrieron el real; y á la mañana descendió rocío en derredor del real.', 'sand'),
('Old', 'Éxodo', 16, 14, 'Y como el rocío cesó de descender, he aquí sobre la haz del desierto una cosa menuda, redonda, menuda como una helada sobre la tierra.', 'sand'),
('Old', 'Éxodo', 16, 15, 'Y viéndolo los hijos de Israel, se dijeron unos á otros: ¿Qué es esto? porque no sabían qué era. Entonces Moisés les dijo: Es el pan que Jehová os da para comer.', 'sand'),
('Old', 'Éxodo', 16, 16, 'Esto es lo que Jehová ha mandado: Cogereis de él cada uno según pudiere comer; un gomer por cabeza, conforme al número de vuestras personas, tomaréis cada uno para los que están en su tienda.', 'sand'),
('Old', 'Éxodo', 16, 17, 'Y los hijos de Israel lo hicieron así: y recogieron unos más, otros menos:', 'sand'),
('Old', 'Éxodo', 16, 18, 'Y medíanlo por gomer, y no sobraba al que había recogido mucho, ni faltaba al que había recogido poco: cada uno recogió conforme á lo que había de comer.', 'sand'),
('Old', 'Éxodo', 16, 19, 'Y díjoles Moisés: Ninguno deje nada de ello para mañana.', 'sand'),
('Old', 'Éxodo', 16, 20, 'Mas ellos no obedecieron á Moisés, sino que algunos dejaron de ello para otro día, y crió gusanos, y pudrióse; y enojóse contra ellos Moisés.', 'sand'),
('Old', 'Éxodo', 16, 21, 'Y recogíanlo cada mañana, cada uno según lo que había de comer: y luego que el sol calentaba, derretíase.', 'sand'),
('Old', 'Éxodo', 16, 22, 'En el sexto día recogieron doblada comida, dos gomeres para cada uno: y todos los príncipes de la congregación vinieron á Moisés, y se lo hicieron saber.', 'sand'),
('Old', 'Éxodo', 16, 23, 'Y él les dijo: Esto es lo que ha dicho Jehová: Mañana es el santo sábado, el reposo de Jehová: lo que hubiereis de cocer, cocedlo hoy, y lo que hubiereis de cocinar, cocinadlo; y todo lo que os sobrare, guardadlo para mañana.', 'sand'),
('Old', 'Éxodo', 16, 24, 'Y ellos lo guardaron hasta la mañana, según que Moisés había mandado, y no se pudrió, ni hubo en él gusano.', 'sand'),
('Old', 'Éxodo', 16, 25, 'Y dijo Moisés: Comedlo hoy, porque hoy es sábado de Jehová: hoy no hallaréis en el campo.', 'sand'),
('Old', 'Éxodo', 16, 26, 'En los seis días lo recogeréis; mas el séptimo día es sábado, en el cual no se hallará.', 'sand'),
('Old', 'Éxodo', 16, 27, 'Y aconteció que algunos del pueblo salieron en el séptimo día á recoger, y no hallaron.', 'sand'),
('Old', 'Éxodo', 16, 28, 'Y Jehová dijo á Moisés: ¿Hasta cuándo no querréis guardar mis mandamientos y mis leyes?', 'sand'),
('Old', 'Éxodo', 16, 29, 'Mirad que Jehová os dió el sábado, y por eso os da en el sexto día pan para dos días. Estése, pues, cada uno en su estancia, y nadie salga de su lugar en el séptimo día.', 'sand'),
('Old', 'Éxodo', 16, 30, 'Así el pueblo reposó el séptimo día.', 'sand'),
('Old', 'Éxodo', 16, 31, 'Y la casa de Israel lo llamó Maná; y era como simiente de culantro, blanco, y su sabor como de hojuelas con miel.', 'sand'),
('Old', 'Éxodo', 16, 32, 'Y dijo Moisés: Esto es lo que Jehová ha mandado: Henchirás un gomer de él para que se guarde para vuestros descendientes, á fin de que vean el pan que yo os dí á comer en el desierto, cuando yo os saqué de la tierra de Egipto.', 'sand'),
('Old', 'Éxodo', 16, 33, 'Y dijo Moisés á Aarón: Toma un vaso y pon en él un gomer lleno de maná, y ponlo delante de Jehová, para que sea guardado para vuestros descendientes.', 'sand'),
('Old', 'Éxodo', 16, 34, 'Y Aarón lo puso delante del Testimonio para guardarlo, como Jehová lo mandó á Moisés.', 'sand'),
('Old', 'Éxodo', 16, 35, 'Así comieron los hijos de Israel maná cuarenta años, hasta que entraron en la tierra habitada: maná comieron hasta que llegaron al término de la tierra de Canaán.', 'sand'),
('Old', 'Éxodo', 16, 36, 'Y un gomer es la décima parte del epha.', 'sand'),
('Old', 'Éxodo', 17, 1, 'Y TODA la congregación de los hijos de Israel partió del desierto de Sin, por sus jornadas, al mandamiento de Jehová, y asentaron el campo en Rephidim: y no había agua para que el pueblo bebiese.', 'sand'),
('Old', 'Éxodo', 17, 2, 'Y altercó el pueblo con Moisés, y dijeron: Danos agua que bebamos. Y Moisés les dijo: ¿Por qué altercáis conmigo? ¿por qué tentáis á Jehová?', 'sand'),
('Old', 'Éxodo', 17, 3, 'Así que el pueblo tuvo allí sed de agua, y murmuró contra Moisés, y dijo: ¿Por qué nos hiciste subir de Egipto para matarnos de sed á nosotros, y á nuestros hijos y á nuestros ganados?', 'sand'),
('Old', 'Éxodo', 17, 4, 'Entonces clamó Moisés á Jehová, diciendo: ¿Qué haré con este pueblo? de aquí á un poco me apedrearán.', 'sand'),
('Old', 'Éxodo', 17, 5, 'Y Jehová dijo á Moisés: Pasa delante del pueblo, y toma contigo de los ancianos de Israel; y toma también en tu mano tu vara, con que heriste el río, y ve:', 'sand'),
('Old', 'Éxodo', 17, 6, 'He aquí que yo estoy delante de ti allí sobre la peña en Horeb; y herirás la peña, y saldrán de ella aguas, y beberá el pueblo. Y Moisés lo hizo así en presencia de los ancianos de Israel.', 'sand'),
('Old', 'Éxodo', 17, 7, 'Y llamó el nombre de aquel lugar Massah y Meribah, por la rencilla de los hijos de Israel, y porque tentaron á Jehová, diciendo: ¿Está, pues, Jehová entre nosotros, ó no?', 'sand'),
('Old', 'Éxodo', 17, 8, 'Y vino Amalec y peleó con Israel en Rephidim.', 'sand'),
('Old', 'Éxodo', 17, 9, 'Y dijo Moisés á Josué: Escógenos varones, y sal, pelea con Amalec: mañana yo estaré sobre la cumbre del collado, y la vara de Dios en mi mano.', 'sand'),
('Old', 'Éxodo', 17, 10, 'E hizo Josué como le dijo Moisés, peleando con Amalec; y Moisés y Aarón y Hur subieron á la cumbre del collado.', 'sand'),
('Old', 'Éxodo', 17, 11, 'Y sucedía que cuando alzaba Moisés su mano, Israel prevalecía; mas cuando él bajaba su mano, prevalecía Amalec.', 'sand'),
('Old', 'Éxodo', 17, 12, 'Y las manos de Moisés estaban pesadas; por lo que tomaron una piedra, y pusiéronla debajo de él, y se sentó sobre ella; y Aarón y Hur sustentaban sus manos, el uno de una parte y el otro de otra; así hubo en sus manos firmeza hasta que se puso el sol.', 'sand'),
('Old', 'Éxodo', 17, 13, 'Y Josué deshizo á Amalec y á su pueblo á filo de espada.', 'sand'),
('Old', 'Éxodo', 17, 14, 'Y Jehová dijo á Moisés: Escribe esto para memoria en un libro, y di á Josué que del todo tengo de raer la memoria de Amalec de debajo del cielo.', 'sand'),
('Old', 'Éxodo', 17, 15, 'Y Moisés edificó un altar, y llamó su nombre Jehová-nissi;', 'sand'),
('Old', 'Éxodo', 17, 16, 'Y dijo: Por cuanto la mano sobre el trono de Jehová, Jehová tendrá guerra con Amalec de generación en generación.', 'sand');