INSERT INTO scriptures (testament, book_name, chapter, verse_number, content, visual_theme) VALUES
('Old', 'Levítico', 11, 3, 'De entre los animales, todo el de pezuña, y que tiene las pezuñas hendidas, y que rumia, éste comeréis.', 'cosmic'),
('Old', 'Levítico', 11, 4, 'Estos empero no comeréis de los que rumian y de los que tienen pezuña: el camello, porque rumia mas no tiene pezuña hendida, habéis de tenerlo por inmundo;', 'cosmic'),
('Old', 'Levítico', 11, 5, 'También el conejo, porque rumia, mas no tiene pezuña, tendréislo por inmundo;', 'cosmic'),
('Old', 'Levítico', 11, 6, 'Asimismo la liebre, porque rumia, mas no tiene pezuña, tendréisla por inmunda;', 'cosmic'),
('Old', 'Levítico', 11, 7, 'También el puerco, porque tiene pezuñas, y es de pezuñas hendidas, mas no rumia, tendréislo por inmundo.', 'cosmic'),
('Old', 'Levítico', 11, 8, 'De la carne de ellos no comeréis, ni tocaréis su cuerpo muerto: tendréislos por inmundos.', 'cosmic'),
('Old', 'Levítico', 11, 9, 'Esto comeréis de todas las cosas que están en las aguas: todas las cosas que tienen aletas y escamas en las aguas de la mar, y en los ríos, aquellas comeréis;', 'cosmic'),
('Old', 'Levítico', 11, 10, 'Mas todas las cosas que no tienen aletas ni escamas en la mar y en los ríos, así de todo reptil de agua como de toda cosa viviente que está en las aguas, las tendréis en abominación.', 'cosmic'),
('Old', 'Levítico', 11, 11, 'Os serán, pues, en abominación: de su carne no comeréis, y abominaréis sus cuerpos muertos.', 'cosmic'),
('Old', 'Levítico', 11, 12, 'Todo lo que no tuviere aletas y escamas en las aguas, tendréislo en abominación.', 'cosmic'),
('Old', 'Levítico', 11, 13, 'Y de las aves, éstas tendréis en abominación; no se comerán, serán abominación: el águila, el quebrantahuesos, el esmerejón,', 'cosmic'),
('Old', 'Levítico', 11, 14, 'El milano, y el buitre según su especie;', 'cosmic'),
('Old', 'Levítico', 11, 15, 'Todo cuervo según su especie;', 'cosmic'),
('Old', 'Levítico', 11, 16, 'El avestruz, y la lechuza, y el laro, y el gavilán según su especie;', 'cosmic'),
('Old', 'Levítico', 11, 17, 'Y el buho, y el somormujo, y el ibis,', 'cosmic'),
('Old', 'Levítico', 11, 18, 'Y el calamón, y el cisne, y el onocrótalo,', 'cosmic'),
('Old', 'Levítico', 11, 19, 'Y el herodión, y el caradrión, según su especie, y la abubilla, y el murciélago.', 'cosmic'),
('Old', 'Levítico', 11, 20, 'Todo reptil alado que anduviere sobre cuatro pies, tendréis en abominación.', 'cosmic'),
('Old', 'Levítico', 11, 21, 'Empero esto comeréis de todo reptil alado que anda sobre cuatro pies, que tuviere piernas además de sus pies para saltar con ellas sobre la tierra;', 'cosmic'),
('Old', 'Levítico', 11, 22, 'Estos comeréis de ellos: la langosta según su especie, y el langostín según su especie, y el aregol según su especie, y el haghab según su especie.', 'cosmic'),
('Old', 'Levítico', 11, 23, 'Todo reptil alado que tenga cuatro pies, tendréis en abominación.', 'cosmic'),
('Old', 'Levítico', 11, 24, 'Y por estas cosas seréis inmundos: cualquiera que tocare á sus cuerpos muertos, será inmundo hasta la tarde:', 'cosmic'),
('Old', 'Levítico', 11, 25, 'Y cualquiera que llevare de sus cuerpos muertos, lavará sus vestidos, y será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 11, 26, 'Todo animal de pezuña, pero que no tiene pezuña hendida, ni rumia, tendréis por inmundo: cualquiera que los tocare será inmundo.', 'cosmic'),
('Old', 'Levítico', 11, 27, 'Y de todos los animales que andan á cuatro pies, tendréis por inmundo cualquiera que ande sobre sus garras: cualquiera que tocare sus cuerpos muertos, será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 11, 28, 'Y el que llevare sus cuerpos muertos, lavará sus vestidos, y será inmundo hasta la tarde: habéis de tenerlos por inmundos.', 'cosmic'),
('Old', 'Levítico', 11, 29, 'Y estos tendréis por inmundos de los reptiles que van arrastrando sobre la tierra: la comadreja, y el ratón, y la rana según su especie,', 'cosmic'),
('Old', 'Levítico', 11, 30, 'Y el erizo, y el lagarto, y el caracol, y la babosa, y el topo.', 'cosmic'),
('Old', 'Levítico', 11, 31, 'Estos tendréis por inmundos de todos los reptiles: cualquiera que los tocare, cuando estuvieren muertos, será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 11, 32, 'Y todo aquello sobre que cayere alguno de ellos después de muertos, será inmundo; así vaso de madera, como vestido, ó piel, ó saco, cualquier instrumento con que se hace obra, será metido en agua, y será inmundo hasta la tarde, y así será limpio.', 'cosmic'),
('Old', 'Levítico', 11, 33, 'Y toda vasija de barro dentro de la cual cayere alguno de ellos, todo lo que estuviere en ella será inmundo, y quebraréis la vasija:', 'cosmic'),
('Old', 'Levítico', 11, 34, 'Toda vianda que se come, sobre la cual viniere el agua de tales vasijas, será inmunda: y toda bebida que se bebiere, será en todas esas vasijas inmunda:', 'cosmic'),
('Old', 'Levítico', 11, 35, 'Y todo aquello sobre que cayere algo del cuerpo muerto de ellos, será inmundo: el horno ú hornillos se derribarán; son inmundos, y por inmundos los tendréis.', 'cosmic'),
('Old', 'Levítico', 11, 36, 'Con todo, la fuente y la cisterna donde se recogen aguas, serán limpias: mas lo que hubiere tocado en sus cuerpos muertos será inmundo.', 'cosmic'),
('Old', 'Levítico', 11, 37, 'Y si cayere de sus cuerpos muertos sobre alguna simiente que se haya de sembrar, será limpia.', 'cosmic'),
('Old', 'Levítico', 11, 38, 'Mas si se hubiere puesto agua en la simiente, y cayere de sus cuerpos muertos sobre ella, tendréisla por inmunda.', 'cosmic'),
('Old', 'Levítico', 11, 39, 'Y si algún animal que tuviereis para comer se muriere, el que tocare su cuerpo muerto será inmundo hasta la tarde:', 'cosmic'),
('Old', 'Levítico', 11, 40, 'Y el que comiere de su cuerpo muerto, lavará sus vestidos, y será inmundo hasta la tarde: asimismo el que sacare su cuerpo muerto, lavará sus vestidos, y será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 11, 41, 'Y todo reptil que va arrastrando sobre la tierra, es abominación; no se comerá.', 'cosmic'),
('Old', 'Levítico', 11, 42, 'Todo lo que anda sobre el pecho, y todo lo que anda sobre cuatro ó más pies, de todo reptil que anda arrastrando sobre la tierra, no lo comeréis, porque es abominación.', 'cosmic'),
('Old', 'Levítico', 11, 43, 'No ensuciéis vuestras personas con ningún reptil que anda arrastrando, ni os contaminéis con ellos, ni seáis inmundos por ellos.', 'cosmic'),
('Old', 'Levítico', 11, 44, 'Pues que yo soy Jehová vuestro Dios, vosotros por tanto os santificaréis, y seréis santos, porque yo soy santo: así que no ensuciéis vuestras personas con ningún reptil que anduviere arrastrando sobre la tierra.', 'cosmic'),
('Old', 'Levítico', 11, 45, 'Porque yo soy Jehová, que os hago subir de la tierra de Egipto para seros por Dios: seréis pues santos, porque yo soy santo.', 'cosmic'),
('Old', 'Levítico', 11, 46, 'Esta es la ley de los animales y de las aves, y de todo ser viviente que se mueve en las aguas, y de todo animal que anda arrastrando sobre la tierra;', 'cosmic'),
('Old', 'Levítico', 11, 47, 'Para hacer diferencia entre inmundo y limpio, y entre los animales que se pueden comer y los animales que no se pueden comer.', 'cosmic'),
('Old', 'Levítico', 12, 1, 'Y HABLO Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 12, 2, 'Habla á los hijos de Israel, diciendo: La mujer cuando concibiere y pariere varón, será inmunda siete días; conforme á los días que está separada por su menstruo, será inmunda.', 'cosmic'),
('Old', 'Levítico', 12, 3, 'Y al octavo día circuncidará la carne de su prepucio.', 'cosmic'),
('Old', 'Levítico', 12, 4, 'Mas ella permanecerá treinta y tres días en la sangre de su purgación: ninguna cosa santa tocará, ni vendrá al santuario, hasta que sean cumplidos los días de su purgación.', 'cosmic'),
('Old', 'Levítico', 12, 5, 'Y si pariere hembra será inmunda dos semanas, conforme á su separación, y sesenta y seis días estará purificándose de su sangre.', 'cosmic'),
('Old', 'Levítico', 12, 6, 'Y cuando los días de su purgación fueren cumplidos, por hijo ó por hija, traerá un cordero de un año para holocausto, y un palomino ó una tórtola para expiación, á la puerta del tabernáculo del testimonio, al sacerdote:', 'cosmic'),
('Old', 'Levítico', 12, 7, 'Y él ofrecerá delante de Jehová, y hará expiación por ella, y será limpia del flujo de su sangre. Esta es la ley de la que pariere varón ó hembra.', 'cosmic'),
('Old', 'Levítico', 12, 8, 'Y si no alcanzare su mano lo suficiente para un cordero, tomará entonces dos tórtolas ó dos palominos, uno para holocausto, y otro para expiación: y el sacerdote hará expiación por ella, y será limpia.', 'cosmic'),
('Old', 'Levítico', 13, 1, 'Y HABLO Jehová á Moisés y á Aarón, diciendo:', 'cosmic'),
('Old', 'Levítico', 13, 2, 'Cuando el hombre tuviere en la piel de su carne hinchazón, ó postilla, ó mancha blanca, y hubiere en la piel de su carne como llaga de lepra, será traído á Aarón el sacerdote, ó á uno de los sacerdotes sus hijos:', 'cosmic'),
('Old', 'Levítico', 13, 3, 'Y el sacerdote mirará la llaga en la piel de la carne: si el pelo en la llaga se ha vuelto blanco, y pareciere la llaga más hundida que la tez de la carne, llaga de lepra es; y el sacerdote le reconocerá, y le dará por inmundo.', 'cosmic'),
('Old', 'Levítico', 13, 4, 'Y si en la piel de su carne hubiere mancha blanca, pero no pareciere más hundida que la tez, ni su pelo se hubiere vuelto blanco, entonces el sacerdote encerrará al llagado por siete días;', 'cosmic'),
('Old', 'Levítico', 13, 5, 'Y al séptimo día el sacerdote lo mirará; y si la llaga a su parecer se hubiere estancado, no habiéndose extendido en la piel, entonces el sacerdote le volverá á encerrar por otros siete días.', 'cosmic'),
('Old', 'Levítico', 13, 6, 'Y al séptimo día el sacerdote le reconocerá de nuevo; y si parece haberse oscurecido la llaga, y que no ha cundido en la piel, entonces el sacerdote lo dará por limpio: era postilla; y lavará sus vestidos, y será limpio.', 'cosmic'),
('Old', 'Levítico', 13, 7, 'Mas si hubiere ido creciendo la postilla en la piel, después que fué mostrado al sacerdote para ser limpio, será visto otra vez del sacerdote:', 'cosmic'),
('Old', 'Levítico', 13, 8, 'Y si reconociéndolo el sacerdote, ve que la postilla ha crecido en la piel, el sacerdote lo dará por inmundo: es lepra.', 'cosmic'),
('Old', 'Levítico', 13, 9, 'Cuando hubiere llaga de lepra en el hombre, será traído al sacerdote;', 'cosmic'),
('Old', 'Levítico', 13, 10, 'Y el sacerdote mirará, y si pareciere tumor blanco en la piel, el cual haya mudado el color del pelo, y se descubre asimismo la carne viva,', 'cosmic'),
('Old', 'Levítico', 13, 11, 'Lepra es envejecida en la piel de su carne; y le dará por inmundo el sacerdote, y no le encerrará, porque es inmundo.', 'cosmic'),
('Old', 'Levítico', 13, 12, 'Mas si brotare la lepra cundiendo por el cutis, y ella cubriere toda la piel del llagado desde su cabeza hasta sus pies, á toda vista de ojos del sacerdote;', 'cosmic'),
('Old', 'Levítico', 13, 13, 'Entonces el sacerdote le reconocerá; y si la lepra hubiere cubierto toda su carne, dará por limpio al llagado: hase vuelto toda ella blanca; y él es limpio.', 'cosmic'),
('Old', 'Levítico', 13, 14, 'Mas el día que apareciere en él la carne viva, será inmundo.', 'cosmic'),
('Old', 'Levítico', 13, 15, 'Y el sacerdote mirará la carne viva, y lo dará por inmundo. Es inmunda la carne viva: es lepra.', 'cosmic'),
('Old', 'Levítico', 13, 16, 'Mas cuando la carne viva se mudare y volviere blanca, entonces vendrá al sacerdote;', 'cosmic'),
('Old', 'Levítico', 13, 17, 'Y el sacerdote mirará, y si la llaga se hubiere vuelto blanca, el sacerdote dará por limpio al que tenía la llaga, y será limpio.', 'cosmic'),
('Old', 'Levítico', 13, 18, 'Y cuando en la carne, en su piel, hubiere apostema, y se sanare,', 'cosmic'),
('Old', 'Levítico', 13, 19, 'Y sucediere en el lugar de la apostema tumor blanco, ó mancha blanca embermejecida, será mostrado al sacerdote:', 'cosmic'),
('Old', 'Levítico', 13, 20, 'Y el sacerdote mirará; y si pareciere estar más baja que su piel, y su pelo se hubiere vuelto blanco, darálo el sacerdote por inmundo: es llaga de lepra que se originó en la apostema.', 'cosmic'),
('Old', 'Levítico', 13, 21, 'Y si el sacerdote la considerare, y no pareciere en ella pelo blanco, ni estuviere más baja que la piel, sino oscura, entonces el sacerdote lo encerrará por siete días:', 'cosmic'),
('Old', 'Levítico', 13, 22, 'Y si se fuere extendiendo por la piel, entonces el sacerdote lo dará por inmundo: es llaga.', 'cosmic'),
('Old', 'Levítico', 13, 23, 'Empero si la mancha blanca se estuviere en su lugar, que no haya cundido, es la costra de la apostema; y el sacerdote lo dará por limpio.', 'cosmic'),
('Old', 'Levítico', 13, 24, 'Asimismo cuando la carne tuviere en su piel quemadura de fuego, y hubiere en lo sanado del fuego mancha blanquecina, bermejiza ó blanca,', 'cosmic'),
('Old', 'Levítico', 13, 25, 'El sacerdote la mirará; y si el pelo se hubiere vuelto blanco en la mancha, y pareciere estar más hundida que la piel, es lepra que salió en la quemadura; y el sacerdote declarará al sujeto inmundo, por ser llaga de lepra.', 'cosmic'),
('Old', 'Levítico', 13, 26, 'Mas si el sacerdote la mirare, y no pareciere en la mancha pelo blanco, ni estuviere más baja que la tez, sino que está oscura, le encerrará el sacerdote por siete días;', 'cosmic'),
('Old', 'Levítico', 13, 27, 'Y al séptimo día el sacerdote la reconocerá: si se hubiere ido extendiendo por la piel, el sacerdote lo dará por inmundo: es llaga de lepra.', 'cosmic'),
('Old', 'Levítico', 13, 28, 'Empero si la mancha se estuviere en su lugar, y no se hubiere extendido en la piel, sino que está oscura, hinchazón es de la quemadura: darálo el sacerdote por limpio; que señal de la quemadura es.', 'cosmic'),
('Old', 'Levítico', 13, 29, 'Y al hombre ó mujer que le saliere llaga en la cabeza, ó en la barba,', 'cosmic'),
('Old', 'Levítico', 13, 30, 'El sacerdote mirará la llaga; y si pareciere estar más profunda que la tez, y el pelo en ella fuera rubio y adelgazado, entonces el sacerdote lo dará por inmundo: es tiña, es lepra de la cabeza ó de la barba.', 'cosmic'),
('Old', 'Levítico', 13, 31, 'Mas cuando el sacerdote hubiere mirado la llaga de la tiña, y no pareciere estar más profunda que la tez, ni fuere en ella pelo negro, el sacerdote encerrará al llagado de la tiña por siete días:', 'cosmic'),
('Old', 'Levítico', 13, 32, 'Y al séptimo día el sacerdote mirará la llaga: y si la tiña no pareciere haberse extendido, ni hubiere en ella pelo rubio, ni pareciere la tiña más profunda que la tez,', 'cosmic'),
('Old', 'Levítico', 13, 33, 'Entonces lo trasquilarán, mas no trasquilarán el lugar de la tiña: y encerrará el sacerdote al que tiene la tiña por otros siete días.', 'cosmic'),
('Old', 'Levítico', 13, 34, 'Y al séptimo día mirará el sacerdote la tiña; y si la tiña no hubiere cundido en la piel, ni pareciere estar más profunda que la tez, el sacerdote lo dará por limpio; y lavará sus vestidos, y será limpio.', 'cosmic'),
('Old', 'Levítico', 13, 35, 'Empero si la tiña se hubiere ido extendiendo en la piel después de su purificación,', 'cosmic'),
('Old', 'Levítico', 13, 36, 'Entonces el sacerdote la mirará; y si la tiña hubiere cundido en la piel, no busque el sacerdote el pelo rubio, es inmundo.', 'cosmic'),
('Old', 'Levítico', 13, 37, 'Mas si le pareciere que la tiña está detenida, y que ha salido en ella el pelo negro, la tiña está sanada; él está limpio, y por limpio lo dará el sacerdote.', 'cosmic'),
('Old', 'Levítico', 13, 38, 'Asimismo el hombre ó mujer, cuando en la piel de su carne tuviere manchas, manchas blancas,', 'cosmic'),
('Old', 'Levítico', 13, 39, 'El sacerdote mirará: y si en la piel de su carne parecieren manchas blancas algo oscurecidas, es empeine que brotó en la piel, está limpia la persona.', 'cosmic'),
('Old', 'Levítico', 13, 40, 'Y el hombre, cuando se le pelare la cabeza, es calvo, mas limpio.', 'cosmic'),
('Old', 'Levítico', 13, 41, 'Y si á la parte de su rostro se le pelare la cabeza, es calvo por delante, pero limpio.', 'cosmic'),
('Old', 'Levítico', 13, 42, 'Mas cuando en la calva ó en la antecalva hubiere llaga blanca rojiza, lepra es que brota en su calva ó en su antecalva.', 'cosmic'),
('Old', 'Levítico', 13, 43, 'Entonces el sacerdote lo mirará, y si pareciere la hinchazón de la llaga blanca rojiza en su calva ó en su antecalva, como el parecer de la lepra de la tez de la carne,', 'cosmic'),
('Old', 'Levítico', 13, 44, 'Leproso es, es inmundo; el sacerdote lo dará luego por inmundo; en su cabeza tiene su llaga.', 'cosmic'),
('Old', 'Levítico', 13, 45, 'Y el leproso en quien hubiere llaga, sus vestidos serán deshechos y su cabeza descubierta, y embozado pregonará: ¡Inmundo! ¡inmundo!', 'cosmic'),
('Old', 'Levítico', 13, 46, 'Todo el tiempo que la llaga estuviere en él, será inmundo; estará impuro: habitará solo; fuera del real será su morada.', 'cosmic'),
('Old', 'Levítico', 13, 47, 'Y cuando en el vestido hubiere plaga de lepra, en vestido de lana, ó en vestido de lino;', 'cosmic'),
('Old', 'Levítico', 13, 48, 'O en estambre ó en trama, de lino ó de lana, ó en piel, ó en cualquiera obra de piel;', 'cosmic'),
('Old', 'Levítico', 13, 49, 'Y que la plaga sea verde, ó bermeja, en vestido ó en piel, ó en estambre, ó en trama, ó en cualquiera obra de piel; plaga es de lepra, y se ha de mostrar al sacerdote.', 'cosmic'),
('Old', 'Levítico', 13, 50, 'Y el sacerdote mirará la plaga, y encerrará la cosa plagada por siete días.', 'cosmic'),
('Old', 'Levítico', 13, 51, 'Y al séptimo día mirará la plaga: y si hubiere cundido la plaga en el vestido, ó estambre, ó en la trama, ó en piel, ó en cualquiera obra que se hace de pieles, lepra roedora es la plaga; inmunda será.', 'cosmic'),
('Old', 'Levítico', 13, 52, 'Será quemado el vestido, ó estambre ó trama, de lana ó de lino, ó cualquiera obra de pieles en que hubiere tal plaga; porque lepra roedora es; al fuego será quemada.', 'cosmic'),
('Old', 'Levítico', 13, 53, 'Y si el sacerdote mirare, y no pareciere que la plaga se haya extendido en el vestido, ó estambre, ó en la trama, ó en cualquiera obra de pieles;', 'cosmic'),
('Old', 'Levítico', 13, 54, 'Entonces el sacerdote mandará que laven donde está la plaga, y lo encerrará otra vez por siete días.', 'cosmic'),
('Old', 'Levítico', 13, 55, 'Y el sacerdote mirará después que la plaga fuere lavada; y si pareciere que la plaga no ha mudado su aspecto, bien que no haya cundido la plaga, inmunda es; la quemarás al fuego; corrosión es penetrante, esté lo raído en la haz ó en el revés de aquella cosa.', 'cosmic'),
('Old', 'Levítico', 13, 56, 'Mas si el sacerdote la viere, y pareciere que la plaga se ha oscurecido después que fué lavada, la cortará del vestido, ó de la piel, ó del estambre, ó de la trama.', 'cosmic'),
('Old', 'Levítico', 13, 57, 'Y si apareciere más en el vestido, ó estambre, ó trama, ó en cualquiera cosa de pieles, reverdeciendo en ella, quemarás al fuego aquello donde estuviere la plaga.', 'cosmic'),
('Old', 'Levítico', 13, 58, 'Empero el vestido, ó estambre, ó trama, ó cualquiera cosa de piel que lavares, y que se le quitare la plaga, lavarse ha segunda vez, y entonces será limpia.', 'cosmic'),
('Old', 'Levítico', 13, 59, 'Esta es la ley de la plaga de la lepra del vestido de lana ó de lino, ó del estambre, ó de la trama, ó de cualquiera cosa de piel, para que sea dada por limpia ó por inmunda.', 'cosmic'),
('Old', 'Levítico', 14, 1, 'Y HABLO Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 14, 2, 'Esta será la ley del leproso cuando se limpiare: Será traído al sacerdote:', 'cosmic'),
('Old', 'Levítico', 14, 3, 'Y el sacerdote saldrá fuera del real; y mirará el sacerdote, y viendo que está sana la plaga de la lepra del leproso,', 'cosmic'),
('Old', 'Levítico', 14, 4, 'El sacerdote mandará luego que se tomen para el que se purifica dos avecillas vivas, limpias, y palo de cedro, y grana, é hisopo;', 'cosmic'),
('Old', 'Levítico', 14, 5, 'Y mandará el sacerdote matar la una avecilla en un vaso de barro sobre aguas vivas;', 'cosmic'),
('Old', 'Levítico', 14, 6, 'Después tomará la avecilla viva, y el palo de cedro, y la grana, y el hisopo, y lo mojará con la avecilla viva en la sangre de la avecilla muerta sobre las aguas vivas:', 'cosmic'),
('Old', 'Levítico', 14, 7, 'Y rociará siete veces sobre el que se purifica de la lepra, y le dará por limpio; y soltará la avecilla viva sobre la haz del campo.', 'cosmic'),
('Old', 'Levítico', 14, 8, 'Y el que se purifica lavará sus vestidos, y raerá todos sus pelos, y se ha de lavar con agua, y será limpio: y después entrará en el real, y morará fuera de su tienda siete días.', 'cosmic'),
('Old', 'Levítico', 14, 9, 'Y será, que al séptimo día raerá todos sus pelos, su cabeza, y su barba, y las cejas de sus ojos; finalmente, raerá todo su pelo, y lavará sus vestidos, y lavará su carne en aguas, y será limpio.', 'cosmic'),
('Old', 'Levítico', 14, 10, 'Y el día octavo tomará dos corderos sin defecto, y una cordera de un año sin tacha; y tres décimas de flor de harina para presente amasada con aceite, y un log de aceite.', 'cosmic'),
('Old', 'Levítico', 14, 11, 'Y el sacerdote que le purifica presentará con aquellas cosas al que se ha de limpiar delante de Jehová, á la puerta del tabernáculo del testimonio:', 'cosmic'),
('Old', 'Levítico', 14, 12, 'Y tomará el sacerdote el un cordero, y ofrecerálo por la culpa, con el log de aceite, y lo mecerá como ofrenda agitada delante de Jehová:', 'cosmic'),
('Old', 'Levítico', 14, 13, 'Y degollará el cordero en el lugar donde degüellan la víctima por el pecado y el holocausto, en el lugar del santuario: porque como la víctima por el pecado, así también la víctima por la culpa es del sacerdote: es cosa muy sagrada.', 'cosmic'),
('Old', 'Levítico', 14, 14, 'Y tomará el sacerdote de la sangre de la víctima por la culpa, y pondrá el sacerdote sobre la ternilla de la oreja derecha del que se purifica, y sobre el pulgar de su mano derecha, y sobre el pulgar de su pie derecho.', 'cosmic'),
('Old', 'Levítico', 14, 15, 'Asimismo tomará el sacerdote del log de aceite, y echará sobre la palma de su mano izquierda:', 'cosmic'),
('Old', 'Levítico', 14, 16, 'Y mojará su dedo derecho en el aceite que tiene en su mano izquierda, y esparcirá del aceite con su dedo siete veces delante de Jehová:', 'cosmic'),
('Old', 'Levítico', 14, 17, 'Y de lo que quedare del aceite que tiene en su mano, pondrá el sacerdote sobre la ternilla de la oreja derecha del que se purifica, y sobre el pulgar de su mano derecha, y sobre el pulgar de su pie derecho, sobre la sangre de la expiación por la culpa:', 'cosmic'),
('Old', 'Levítico', 14, 18, 'Y lo que quedare del aceite que tiene en su mano, pondrá sobre la cabeza del que se purifica: y hará el sacerdote expiación por él delante de Jehová.', 'cosmic'),
('Old', 'Levítico', 14, 19, 'Ofrecerá luego el sacerdote el sacrificio por el pecado, y hará expiación por el que se ha de purificar de su inmundicia, y después degollará el holocausto:', 'cosmic'),
('Old', 'Levítico', 14, 20, 'Y hará subir el sacerdote el holocausto y el presente sobre el altar. Así hará el sacerdote expiación por él, y será limpio.', 'cosmic'),
('Old', 'Levítico', 14, 21, 'Mas si fuere pobre, que no alcanzare su mano á tanto, entonces tomará un cordero para ser ofrecido como ofrenda agitada por la culpa, para reconciliarse, y una décima de flor de harina amasada con aceite para presente, y un log de aceite;', 'cosmic'),
('Old', 'Levítico', 14, 22, 'Y dos tórtolas, ó dos palominos, lo que alcanzare su mano: y el uno será para expiación por el pecado, y el otro para holocausto;', 'cosmic'),
('Old', 'Levítico', 14, 23, 'Las cuales cosas traerá al octavo día de su purificación al sacerdote, á la puerta del tabernáculo del testimonio delante de Jehová.', 'cosmic'),
('Old', 'Levítico', 14, 24, 'Y el sacerdote tomará el cordero de la expiación por la culpa, y el log de aceite, y mecerálo el sacerdote como ofrenda agitada delante de Jehová;', 'cosmic'),
('Old', 'Levítico', 14, 25, 'Luego degollará el cordero de la culpa, y tomará el sacerdote de la sangre de la culpa, y pondrá sobre la ternilla de la oreja derecha del que se purifica, y sobre el pulgar de su mano derecha, y sobre el pulgar de su pie derecho.', 'cosmic'),
('Old', 'Levítico', 14, 26, 'Y el sacerdote echará del aceite sobre la palma de su mano izquierda;', 'cosmic'),
('Old', 'Levítico', 14, 27, 'Y con su dedo derecho rociará el sacerdote del aceite que tiene en su mano izquierda, siete veces delante de Jehová.', 'cosmic'),
('Old', 'Levítico', 14, 28, 'También pondrá el sacerdote del aceite que tiene en su mano sobre la ternilla de la oreja derecha del que se purifica, y sobre el pulgar de su mano derecha, y sobre el pulgar de su pie derecho, en el lugar de la sangre de la culpa.', 'cosmic'),
('Old', 'Levítico', 14, 29, 'Y lo que sobrare del aceite que el sacerdote tiene en su mano, pondrálo sobre la cabeza del que se purifica, para reconciliarlo delante de Jehová.', 'cosmic'),
('Old', 'Levítico', 14, 30, 'Asimismo ofrecerá la una de las tórtolas, ó de los palominos, lo que alcanzare su mano:', 'cosmic'),
('Old', 'Levítico', 14, 31, 'El uno de lo que alcanzare su mano, en expiación por el pecado, y el otro en holocausto, además del presente: y hará el sacerdote expiación por el que se ha de purificar, delante de Jehová.', 'cosmic'),
('Old', 'Levítico', 14, 32, 'Esta es la ley del que hubiere tenido plaga de lepra, cuya mano no alcanzare lo prescrito para purificarse.', 'cosmic'),
('Old', 'Levítico', 14, 33, 'Y habló Jehová á Moisés y á Aarón, diciendo:', 'cosmic'),
('Old', 'Levítico', 14, 34, 'Cuando hubieres entrado en la tierra de Canaán, la cual yo os doy en posesión, y pusiere yo plaga de lepra en alguna casa de la tierra de vuestra posesión,', 'cosmic'),
('Old', 'Levítico', 14, 35, 'Vendrá aquél cuya fuere la casa, y dará aviso al sacerdote, diciendo: Como plaga ha aparecido en mi casa.', 'cosmic'),
('Old', 'Levítico', 14, 36, 'Entonces mandará el sacerdote, y despejarán la casa antes que el sacerdote entre á mirar la plaga, por que no sea contaminado todo lo que estuviere en la casa: y después el sacerdote entrará á reconocer la casa:', 'cosmic'),
('Old', 'Levítico', 14, 37, 'Y mirará la plaga: y si se vieren manchas en las paredes de la casa, cavernillas verdosas ó rojas, las cuales parecieren más hundidas que la pared,', 'cosmic'),
('Old', 'Levítico', 14, 38, 'El sacerdote saldrá de la casa á la puerta de ella, y cerrará la casa por siete días.', 'cosmic'),
('Old', 'Levítico', 14, 39, 'Y al séptimo día volverá el sacerdote, y mirará: y si la plaga hubiere crecido en las paredes de la casa,', 'cosmic'),
('Old', 'Levítico', 14, 40, 'Entonces mandará el sacerdote, y arrancarán las piedras en que estuviere la plaga, y las echarán fuera de la ciudad, en lugar inmundo:', 'cosmic'),
('Old', 'Levítico', 14, 41, 'Y hará descostrar la casa por dentro alrededor, y derramarán el polvo que descostraren fuera de la ciudad en lugar inmundo:', 'cosmic'),
('Old', 'Levítico', 14, 42, 'Y tomarán otras piedras, y las pondrán en lugar de las piedras quitadas; y tomarán otro barro, y encostrarán la casa.', 'cosmic'),
('Old', 'Levítico', 14, 43, 'Y si la plaga volviere á reverdecer en aquella casa, después que hizo arrancar las piedras, y descostrar la casa, y después que fue encostrada,', 'cosmic'),
('Old', 'Levítico', 14, 44, 'Entonces el sacerdote entrará y mirará; y si pareciere haberse extendido la plaga en la casa, lepra roedora está en la casa: inmunda es.', 'cosmic'),
('Old', 'Levítico', 14, 45, 'Derribará, por tanto, la tal casa, sus piedras, y sus maderos, y toda la mezcla de la casa; y lo sacará fuera de la ciudad á lugar inmundo.', 'cosmic'),
('Old', 'Levítico', 14, 46, 'Y cualquiera que entrare en aquella casa todos los días que la mandó cerrar, será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 14, 47, 'Y el que durmiere en aquella casa, lavará sus vestidos; también el que comiere en la casa, lavará sus vestidos.', 'cosmic'),
('Old', 'Levítico', 14, 48, 'Mas si entrare el sacerdote y mirare, y viere que la plaga no se ha extendido en la casa después que fue encostrada, el sacerdote dará la casa por limpia, porque la plaga ha sanado.', 'cosmic'),
('Old', 'Levítico', 14, 49, 'Entonces tomará para limpiar la casa dos avecillas, y palo de cedro, y grana, é hisopo:', 'cosmic'),
('Old', 'Levítico', 14, 50, 'Y degollará la una avecilla en una vasija de barro sobre aguas vivas:', 'cosmic'),
('Old', 'Levítico', 14, 51, 'Y tomará el palo de cedro, y el hisopo, y la grana, y la avecilla viva, y mojarálo en la sangre de la avecilla muerta y en las aguas vivas, y rociará la casa siete veces:', 'cosmic'),
('Old', 'Levítico', 14, 52, 'Y purificará la casa con la sangre de la avecilla, y con las aguas vivas, y con la avecilla viva, y el palo de cedro, y el hisopo, y la grana:', 'cosmic'),
('Old', 'Levítico', 14, 53, 'Luego soltará la avecilla viva fuera de la ciudad sobre la haz del campo: Así hará expiación por la casa, y será limpia.', 'cosmic'),
('Old', 'Levítico', 14, 54, 'Esta es la ley acerca de toda plaga de lepra, y de tiña;', 'cosmic'),
('Old', 'Levítico', 14, 55, 'Y de la lepra del vestido, y de la casa;', 'cosmic'),
('Old', 'Levítico', 14, 56, 'Y acerca de la hinchazón, y de la postilla, y de la mancha blanca:', 'cosmic'),
('Old', 'Levítico', 14, 57, 'Para enseñar cuándo es inmundo, y cuándo limpio. Aquesta es la ley tocante á la lepra.', 'cosmic'),
('Old', 'Levítico', 15, 1, 'Y HABLO Jehová á Moisés y á Aarón, diciendo:', 'cosmic'),
('Old', 'Levítico', 15, 2, 'Hablad á los hijos de Israel, y decidles: Cualquier varón, cuando su simiente manare de su carne, será inmundo.', 'cosmic'),
('Old', 'Levítico', 15, 3, 'Y esta será su inmundicia en su flujo; sea que su carne destiló por causa de su flujo, ó que su carne se obstruyó á causa de su flujo, él será inmundo.', 'cosmic'),
('Old', 'Levítico', 15, 4, 'Toda cama en que se acostare el que tuviere flujo, será inmunda; y toda cosa sobre que se sentare, inmunda será.', 'cosmic'),
('Old', 'Levítico', 15, 5, 'Y cualquiera que tocare á su cama, lavará sus vestidos; lavaráse también á sí mismo con agua, y será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 6, 'Y el que se sentare sobre aquello en que se hubiere sentado el que tiene flujo, lavará sus vestidos, se lavará también á sí mismo con agua, y será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 7, 'Asimismo el que tocare la carne del que tiene flujo, lavará sus vestidos, y á sí mismo se lavará con agua, y será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 8, 'Y si el que tiene flujo escupiere sobre el limpio, éste lavará sus vestidos, y después de haberse lavado con agua, será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 9, 'Y toda aparejo sobre que cabalgare el que tuviere flujo, será inmundo.', 'cosmic'),
('Old', 'Levítico', 15, 10, 'Y cualquiera que tocare cualquiera cosa que haya estado debajo de él, será inmundo hasta la tarde; y el que la llevare, lavará sus vestidos, y después de lavarse con agua, será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 11, 'Y todo aquel á quien tocare el que tiene flujo, y no lavare con agua sus manos, lavará sus vestidos, y á sí mismo se lavará con agua, y será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 12, 'Y la vasija de barro en que tocare el que tiene flujo, será quebrada; y toda vasija de madera será lavada con agua.', 'cosmic'),
('Old', 'Levítico', 15, 13, 'Y cuando se hubiere limpiado de su flujo el que tiene flujo, se ha de contar siete días desde su purificación, y lavará sus vestidos, y lavará su carne en aguas vivas, y será limpio.', 'cosmic'),
('Old', 'Levítico', 15, 14, 'Y el octavo día tomará dos tórtolas, ó dos palominos, y vendrá delante de Jehová á la puerta del tabernáculo del testimonio, y los dará al sacerdote:', 'cosmic'),
('Old', 'Levítico', 15, 15, 'Y harálos el sacerdote, el uno ofrenda por el pecado, y el otro holocausto: y le purificará el sacerdote de su flujo delante de Jehová.', 'cosmic'),
('Old', 'Levítico', 15, 16, 'Y el hombre, cuando de él saliere derramamiento de semen, lavará en aguas toda su carne, y será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 17, 'Y toda vestimenta, ó toda piel sobre la cual hubiere el derramamiento del semen, lavaráse con agua, y será inmunda hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 18, 'Y la mujer con quien el varón tuviera ayuntamiento de semen, ambos se lavarán con agua, y serán inmundos hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 19, 'Y cuando la mujer tuviere flujo de sangre, y su flujo fuere en su carne, siete días estará apartada; y cualquiera que tocare en ella, será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 20, 'Y todo aquello sobre que ella se acostare mientras su separación, será inmundo: también todo aquello sobre que se sentare, será inmundo.', 'cosmic'),
('Old', 'Levítico', 15, 21, 'Y cualquiera que tocare á su cama, lavará sus vestidos, y después de lavarse con agua, será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 22, 'También cualquiera que tocare cualquier mueble sobre que ella se hubiere sentado, lavará sus vestidos; lavaráse luego á sí mismo con agua, y será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 23, 'Y si estuviere sobre la cama, ó sobre la silla en que ella se hubiere sentado, el que tocare en ella será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 24, 'Y si alguno durmiere con ella, y su menstruo fuere sobre él, será inmundo por siete días; y toda cama sobre que durmiere, será inmunda.', 'cosmic'),
('Old', 'Levítico', 15, 25, 'Y la mujer, cuando siguiere el flujo de su sangre por muchos días fuera del tiempo de su costumbre, ó cuando tuviere flujo de sangre más de su costumbre; todo el tiempo del flujo de su inmundicia, será inmunda como en los días de su costumbre.', 'cosmic'),
('Old', 'Levítico', 15, 26, 'Toda cama en que durmiere todo el tiempo de su flujo, le será como la cama de su costumbre; y todo mueble sobre que se sentare, será inmundo, como la inmundicia de su costumbre.', 'cosmic'),
('Old', 'Levítico', 15, 27, 'Cualquiera que tocare en esas cosas será inmundo; y lavará sus vestidos, y á sí mismo se lavará con agua, y será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Levítico', 15, 28, 'Y cuando fuere libre de su flujo, se ha de contar siete días, y después será limpia.', 'cosmic'),
('Old', 'Levítico', 15, 29, 'Y el octavo día tomará consigo dos tórtolas, ó dos palominos, y los traerá al sacerdote, á la puerta del tabernáculo del testimonio:', 'cosmic'),
('Old', 'Levítico', 15, 30, 'Y el sacerdote hará el uno ofrenda por el pecado, y el otro holocausto; y la purificará el sacerdote delante de Jehová del flujo de su inmundicia.', 'cosmic'),
('Old', 'Levítico', 15, 31, 'Así apartaréis los hijos de Israel de sus inmundicias, á fin de que no mueran por sus inmundicias, ensuciando mi tabernáculo que está entre ellos.', 'cosmic'),
('Old', 'Levítico', 15, 32, 'Esta es la ley del que tiene flujo, y del que sale derramamiento de semen, viniendo á ser inmundo á causa de ello;', 'cosmic'),
('Old', 'Levítico', 15, 33, 'Y de la que padece su costumbre, y acerca del que tuviere flujo, sea varón ó hembra, y del hombre que durmiere con mujer inmunda.', 'cosmic'),
('Old', 'Levítico', 16, 1, 'Y HABLO Jehová á Moisés, después que murieron los dos hijos de Aarón, cuando se llegaron delante de Jehová, y murieron;', 'cosmic'),
('Old', 'Levítico', 16, 2, 'Y Jehová dijo á Moisés: Di á Aarón tu hermano, que no en todo tiempo entre en el santuario del velo adentro, delante de la cubierta que está sobre el arca, para que no muera: porque yo apareceré en la nube sobre la cubierta.', 'cosmic'),
('Old', 'Levítico', 16, 3, 'Con esto entrará Aarón en el santuario: con un becerro por expiación, y un carnero en holocausto.', 'cosmic'),
('Old', 'Levítico', 16, 4, 'La túnica santa de lino se vestirá, y sobre su carne tendrá pañetes de lino, y ceñiráse el cinto de lino; y con la mitra de lino se cubrirá: son las santas vestiduras: con ellas, después de lavar su carne con agua, se ha de vestir.', 'cosmic'),
('Old', 'Levítico', 16, 5, 'Y de la congregación de los hijos de Israel tomará dos machos de cabrío para expiación, y un carnero para holocausto.', 'cosmic'),
('Old', 'Levítico', 16, 6, 'Y hará allegar Aarón el becerro de la expiación, que es suyo, y hará la reconciliación por sí y por su casa.', 'cosmic'),
('Old', 'Levítico', 16, 7, 'Después tomará los dos machos de cabrío, y los presentará delante de Jehová á la puerta del tabernáculo del testimonio.', 'cosmic'),
('Old', 'Levítico', 16, 8, 'Y echará suertes Aarón sobre los dos machos de cabrío; la una suerte por Jehová, y la otra suerte por Azazel.', 'cosmic'),
('Old', 'Levítico', 16, 9, 'Y hará allegar Aarón el macho cabrío sobre el cual cayere la suerte por Jehová, y ofrecerálo en expiación.', 'cosmic'),
('Old', 'Levítico', 16, 10, 'Mas el macho cabrío, sobre el cual cayere la suerte por Azazel, lo presentará vivo delante de Jehová, para hacer la reconciliación sobre él, para enviarlo á Azazel al desierto.', 'cosmic'),
('Old', 'Levítico', 16, 11, 'Y hará llegar Aarón el becerro que era suyo para expiación, y hará la reconciliación por sí y por su casa, y degollará en expiación el becerro que es suyo.', 'cosmic'),
('Old', 'Levítico', 16, 12, 'Después tomará el incensario lleno de brasas de fuego, del altar de delante de Jehová, y sus puños llenos del perfume aromático molido, y meterálo del velo adentro:', 'cosmic'),
('Old', 'Levítico', 16, 13, 'Y pondrá el perfume sobre el fuego delante de Jehová, y la nube del perfume cubrirá la cubierta que está sobre el testimonio, y no morirá.', 'cosmic'),
('Old', 'Levítico', 16, 14, 'Tomará luego de la sangre del becerro, y rociará con su dedo hacia la cubierta al lado oriental: hacia la cubierta esparcirá siete veces de aquella sangre con su dedo.', 'cosmic'),
('Old', 'Levítico', 16, 15, 'Después degollará en expiación el macho cabrío, que era del pueblo, y meterá la sangre de él del velo adentro; y hará de su sangre como hizo de la sangre del becerro, y esparcirá sobre la cubierta y delante de la cubierta:', 'cosmic'),
('Old', 'Levítico', 16, 16, 'Y limpiará el santuario, de las inmundicias de los hijos de Israel, y de sus rebeliones, y de todos sus pecados: de la misma manera hará también al tabernáculo del testimonio, el cual reside entre ellos en medio de sus inmundicias.', 'cosmic'),
('Old', 'Levítico', 16, 17, 'Y ningún hombre estará en el tabernáculo del testimonio cuando él entrare á hacer la reconciliación en el santuario, hasta que él salga, y haya hecho la reconciliación por sí, y por su casa, y por toda la congregación de Israel.', 'cosmic'),
('Old', 'Levítico', 16, 18, 'Y saldrá al altar que está delante de Jehová, y lo expiará; y tomará de la sangre del becerro, y de la sangre del macho cabrío, y pondrá sobre los cuernos del altar alrededor.', 'cosmic'),
('Old', 'Levítico', 16, 19, 'Y esparcirá sobre él de la sangre con su dedo siete veces, y lo limpiará, y lo santificará de las inmundicias de los hijos de Israel.', 'cosmic'),
('Old', 'Levítico', 16, 20, 'Y cuando hubiere acabado de expiar el santuario, y el tabernáculo del testimonio, y el altar, hará llegar el macho cabrío vivo:', 'cosmic'),
('Old', 'Levítico', 16, 21, 'Y pondrá Aarón ambas manos suyas sobre la cabeza del macho cabrío vivo, y confesará sobre él todas las iniquidades de los hijos de Israel, y todas sus rebeliones, y todos sus pecados, poniéndolos así sobre la cabeza del macho cabrío, y lo enviará al desierto por mano de un hombre destinado para esto.', 'cosmic'),
('Old', 'Levítico', 16, 22, 'Y aquel macho cabrío llevará sobre sí todas las iniquidades de ellos á tierra inhabitada: y dejará ir el macho cabrío por el desierto.', 'cosmic'),
('Old', 'Levítico', 16, 23, 'Después vendrá Aarón al tabernáculo del testimonio, y se desnudará las vestimentas de lino, que había vestido para entrar en el santuario, y pondrálas allí.', 'cosmic'),
('Old', 'Levítico', 16, 24, 'Lavará luego su carne con agua en el lugar del santuario, y después de ponerse sus vestidos saldrá, y hará su holocausto, y el holocausto del pueblo, y hará la reconciliación por sí y por el pueblo.', 'cosmic'),
('Old', 'Levítico', 16, 25, 'Y quemará el sebo de la expiación sobre el altar.', 'cosmic'),
('Old', 'Levítico', 16, 26, 'Y el que hubiere llevado el macho cabrío á Azazel, lavará sus vestidos, lavará también con agua su carne, y después entrará en el real.', 'cosmic'),
('Old', 'Levítico', 16, 27, 'Y sacará fuera del real el becerro del pecado, y el macho cabrío de la culpa, la sangre de los cuales fué metida para hacer la expiación en el santuario; y quemarán en el fuego sus pellejos, y sus carnes, y su estiércol.', 'cosmic'),
('Old', 'Levítico', 16, 28, 'Y el que los quemare, lavará sus vestidos, lavará también su carne con agua, y después entrará en el real.', 'cosmic'),
('Old', 'Levítico', 16, 29, 'Y esto tendréis por estatuto perpetuo: En el mes séptimo, á los diez del mes, afligiréis vuestras almas, y ninguna obra haréis, ni el natural ni el extranjero que peregrina entre vosotros:', 'cosmic'),
('Old', 'Levítico', 16, 30, 'Porque en este día se os reconciliará para limpiaros; y seréis limpios de todos vuestros pecados delante de Jehová.', 'cosmic'),
('Old', 'Levítico', 16, 31, 'Sábado de reposo es para vosotros, y afligiréis vuestras almas, por estatuto perpetuo.', 'cosmic'),
('Old', 'Levítico', 16, 32, 'Y hará la reconciliación el sacerdote que fuere ungido, y cuya mano hubiere sido llena para ser sacerdote en lugar de su padre; y se vestirá las vestimentas de lino, las vestiduras sagradas:', 'cosmic'),
('Old', 'Levítico', 16, 33, 'Y expiará el santuario santo, y el tabernáculo del testimonio; expiará también el altar, y á los sacerdotes, y á todo el pueblo de la congregación.', 'cosmic'),
('Old', 'Levítico', 16, 34, 'Y esto tendréis por estatuto perpetuo, para expiar á los hijos de Israel de todos sus pecados una vez en el año. Y Moisés lo hizo como Jehová le mandó.', 'cosmic'),
('Old', 'Levítico', 17, 1, 'Y HABLO Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 17, 2, 'Habla á Aarón y á sus hijos, y á todos los hijos de Israel, y diles: Esto es lo que ha mandado Jehová, diciendo:', 'cosmic'),
('Old', 'Levítico', 17, 3, 'Cualquier varón de la casa de Israel que degollare buey, ó cordero, ó cabra, en el real, ó fuera del real,', 'cosmic'),
('Old', 'Levítico', 17, 4, 'Y no lo trajere á la puerta del tabernáculo del testimonio, para ofrecer ofrenda á Jehová delante del tabernáculo de Jehová, sangre será imputada al tal varón: sangre derramó; cortado será el tal varón de entre su pueblo:', 'cosmic'),
('Old', 'Levítico', 17, 5, 'A fin de que traigan los hijos de Israel sus sacrificios, los que sacrifican sobre la haz del campo, para que los traigan á Jehová á la puerta del tabernáculo del testimonio al sacerdote, y sacrifiquen ellos sacrificios de paces á Jehová.', 'cosmic'),
('Old', 'Levítico', 17, 6, 'Y el sacerdote esparcirá la sangre sobre el altar de Jehová, á la puerta del tabernáculo del testimonio, y quemará el sebo en olor de suavidad á Jehová.', 'cosmic'),
('Old', 'Levítico', 17, 7, 'Y nunca más sacrificarán sus sacrificios á los demonios, tras de los cuales han fornicado: tendrán esto por estatuto perpetuo por sus edades.', 'cosmic'),
('Old', 'Levítico', 17, 8, 'Les dirás también: Cualquier varón de la casa de Israel, ó de los extranjeros que peregrinan entre vosotros, que ofreciere holocausto ó sacrificio,', 'cosmic'),
('Old', 'Levítico', 17, 9, 'Y no lo trajere á la puerta del tabernáculo del testimonio, para hacerlo á Jehová, el tal varón será igualmente cortado de sus pueblos.', 'cosmic'),
('Old', 'Levítico', 17, 10, 'Y cualquier varón de la casa de Israel, ó de los extranjeros que peregrinan entre ellos, que comiere alguna sangre, yo pondré mi rostro contra la persona que comiere sangre, y le cortaré de entre su pueblo.', 'cosmic'),
('Old', 'Levítico', 17, 11, 'Porque la vida de la carne en la sangre está: y yo os la he dado para expiar vuestras personas sobre el altar: por lo cual la misma sangre expiará la persona.', 'cosmic'),
('Old', 'Levítico', 17, 12, 'Por tanto, he dicho á los hijos de Israel: Ninguna persona de vosotros comerá sangre, ni el extranjero que peregrina entre vosotros comerá sangre.', 'cosmic'),
('Old', 'Levítico', 17, 13, 'Y cualquier varón de los hijos de Israel, ó de los extranjeros que peregrinan entre ellos, que cogiere caza de animal ó de ave que sea de comer, derramará su sangre y cubrirála con tierra:', 'cosmic'),
('Old', 'Levítico', 17, 14, 'Porque el alma de toda carne, su vida, está en su sangre: por tanto he dicho á los hijos de Israel: No comeréis la sangre de ninguna carne, porque la vida de toda carne es su sangre; cualquiera que la comiere será cortado.', 'cosmic'),
('Old', 'Levítico', 17, 15, 'Y cualquiera persona que comiere cosa mortecina ó despedazada por fiera, así de los naturales como de los extranjeros, lavará sus vestidos y á sí mismo se lavará con agua, y será inmundo hasta la tarde; y se limpiará.', 'cosmic'),
('Old', 'Levítico', 17, 16, 'Y si no los lavare, ni lavare su carne, llevará su iniquidad.', 'cosmic'),
('Old', 'Levítico', 18, 1, 'Y HABLO Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 18, 2, 'Habla á los hijos de Israel, y diles: Yo soy Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 18, 3, 'No haréis como hacen en la tierra de Egipto, en la cual morasteis; ni haréis como hacen en la tierra de Canaán, á la cual yo os conduzco; ni andaréis en sus estatutos.', 'cosmic'),
('Old', 'Levítico', 18, 4, 'Mis derechos pondréis por obra, y mis estatutos guardaréis, andando en ellos: Yo Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 18, 5, 'Por tanto mis estatutos y mis derechos guardaréis, los cuales haciendo el hombre, vivirá en ellos: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 18, 6, 'Ningún varón se allegue á ninguna cercana de su carne, para descubrir su desnudez: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 18, 7, 'La desnudez de tu padre, ó la desnudez de tu madre, no descubrirás: tu madre es, no descubrirás su desnudez.', 'cosmic'),
('Old', 'Levítico', 18, 8, 'La desnudez de la mujer de tu padre no descubrirás; es la desnudez de tu padre.', 'cosmic'),
('Old', 'Levítico', 18, 9, 'La desnudez de tu hermana, hija de tu padre, ó hija de tu madre, nacida en casa ó nacida fuera, su desnudez no descubrirás.', 'cosmic'),
('Old', 'Levítico', 18, 10, 'La desnudez de la hija de tu hijo, ó de la hija de tu hija, su desnudez no descubirás, porque es la desnudez tuya.', 'cosmic'),
('Old', 'Levítico', 18, 11, 'La desnudez de la hija de la mujer de tu padre, engendrada de tu padre, tu hermana es, su desnudez no descubrirás.', 'cosmic'),
('Old', 'Levítico', 18, 12, 'La desnudez de la hermana de tu padre no descubrirás: es parienta de tu padre.', 'cosmic'),
('Old', 'Levítico', 18, 13, 'La desnudez de la hermana de tu madre no descubrirás: porque parienta de tu madre es.', 'cosmic'),
('Old', 'Levítico', 18, 14, 'La desnudez del hermano de tu padre no descubrirás: no llegarás á su mujer: es mujer del hermano de tu padre.', 'cosmic'),
('Old', 'Levítico', 18, 15, 'La desnudez de tu nuera no descubrirás: mujer es de tu hijo, no descubrirás su desnudez.', 'cosmic'),
('Old', 'Levítico', 18, 16, 'La desnudez de la mujer de tu hermano no descubrirás: es la desnudez de tu hermano.', 'cosmic'),
('Old', 'Levítico', 18, 17, 'La desnudez de la mujer y de su hija no descubrirás: no tomarás la hija de su hijo, ni la hija de su hija, para descubrir su desnudez: son parientas, es maldad.', 'cosmic'),
('Old', 'Levítico', 18, 18, 'No tomarás mujer juntamente con su hermana, para hacerla su rival, descubriendo su desnudez delante de ella en su vida.', 'cosmic'),
('Old', 'Levítico', 18, 19, 'Y no llegarás á la mujer en el apartamiento de su inmundicia, para descubrir su desnudez.', 'cosmic'),
('Old', 'Levítico', 18, 20, 'Además, no tendrás acto carnal con la mujer de tu prójimo, contaminándote en ella.', 'cosmic'),
('Old', 'Levítico', 18, 21, 'Y no des de tu simiente para hacerla pasar por el fuego á Moloch; no contamines el nombre de tu Dios: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 18, 22, 'No te echarás con varón como con mujer: es abominación.', 'cosmic'),
('Old', 'Levítico', 18, 23, 'Ni con ningún animal tendrás ayuntamiento amancillándote con él; ni mujer alguna se pondrá delante de animal para ayuntarse con él: es confusión.', 'cosmic'),
('Old', 'Levítico', 18, 24, 'En ninguna de estas cosas os amancillaréis; porque en todas estas cosas se han ensuciado las gentes que yo echo de delante de vosotros:', 'cosmic'),
('Old', 'Levítico', 18, 25, 'Y la tierra fue contaminada; y yo visité su maldad sobre ella, y la tierra vomitó sus moradores.', 'cosmic'),
('Old', 'Levítico', 18, 26, 'Guardad, pues, vosotros mis estatutos y mis derechos, y no hagáis ninguna de todas estas abominaciones: ni el natural ni el extranjero que peregrina entre vosotros.', 'cosmic'),
('Old', 'Levítico', 18, 27, '(Porque todas estas abominaciones hicieron los hombres de la tierra, que fueron antes de vosotros, y la tierra fue contaminada:)', 'cosmic'),
('Old', 'Levítico', 18, 28, 'Y la tierra no os vomitará, por haberla contaminado, como vomitó á la gente que fué antes de vosotros.', 'cosmic'),
('Old', 'Levítico', 18, 29, 'Porque cualquiera que hiciere alguna de todas estas abominaciones, las personas que las hicieren, serán cortadas de entre su pueblo.', 'cosmic'),
('Old', 'Levítico', 18, 30, 'Guardad, pues, mi ordenanza, no haciendo de las prácticas abominables que tuvieron lugar antes de vosotros, y no os ensuciéis en ellas: Yo Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 19, 1, 'Y HABLO Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 19, 2, 'Habla á toda la congregación de los hijos de Israel, y diles: Santos seréis, porque santo soy yo Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 19, 3, 'Cada uno temerá á su madre y á su padre, y mis sábados guardaréis: Yo Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 19, 4, 'No os volveréis á los ídolos, ni haréis para vosotros dioses de fundición: Yo Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 19, 5, 'Y cuando sacrificareis sacrificio de paces á Jehová, de vuestra voluntad lo sacrificaréis.', 'cosmic'),
('Old', 'Levítico', 19, 6, 'Será comido el día que lo sacrificareis, y el siguiente día: y lo que quedare para el tercer día, será quemado en el fuego.', 'cosmic'),
('Old', 'Levítico', 19, 7, 'Y si se comiere el día tercero, será abominación; no será acepto:', 'cosmic'),
('Old', 'Levítico', 19, 8, 'Y el que lo comiere, llevará su delito, por cuanto profanó lo santo de Jehová; y la tal persona será cortada de sus pueblos.', 'cosmic'),
('Old', 'Levítico', 19, 9, 'Cuando segareis la mies de vuestra tierra, no acabarás de segar el rincón de tu haza, ni espigarás tu tierra segada.', 'cosmic'),
('Old', 'Levítico', 19, 10, 'Y no rebuscarás tu viña, ni recogerás los granos caídos de tu viña; para el pobre y para el extranjero los dejarás: Yo Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 19, 11, 'No hurtaréis, y no engañaréis, ni mentiréis ninguno á su prójimo.', 'cosmic'),
('Old', 'Levítico', 19, 12, 'Y no juraréis en mi nombre con mentira, ni profanarás el nombre de tu Dios: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 19, 13, 'No oprimirás á tu prójimo, ni le robarás. No se detendrá el trabajo del jornalero en tu casa hasta la mañana.', 'cosmic'),
('Old', 'Levítico', 19, 14, 'No maldigas al sordo, y delante del ciego no pongas tropiezo, mas tendrás temor de tu Dios: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 19, 15, 'No harás agravio en el juicio: no tendrás respeto al pobre, ni honrarás la cara del grande: con justicia juzgarás á tu prójimo.', 'cosmic'),
('Old', 'Levítico', 19, 16, 'No andarás chismeando en tus pueblos. No te pondrás contra la sangre de tu prójimo: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 19, 17, 'No aborrecerás á tu hermano en tu corazón: ingenuamente reprenderás á tu prójimo, y no consentirás sobre él pecado.', 'cosmic'),
('Old', 'Levítico', 19, 18, 'No te vengarás, ni guardarás rencor á los hijos de tu pueblo: mas amarás á tu prójimo como á ti mismo: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 19, 19, 'Mis estatutos guardaréis. A tu animal no harás ayuntar para misturas; tu haza no sembrarás con mistura de semillas, y no te pondrás vestidos con mezcla de diversas cosas.', 'cosmic'),
('Old', 'Levítico', 19, 20, 'Y cuando un hombre tuviere cópula con mujer, y ella fuere sierva desposada con alguno, y no estuviere rescatada, ni le hubiere sido dada libertad, ambos serán azotados: no morirán, por cuanto ella no es libre.', 'cosmic'),
('Old', 'Levítico', 19, 21, 'Y él traerá á Jehová, á la puerta del tabernáculo del testimonio, un carnero en expiación por su culpa.', 'cosmic'),
('Old', 'Levítico', 19, 22, 'Y con el carnero de la expiación lo reconciliará el sacerdote delante de Jehová, por su pecado que cometió: y se le perdonará su pecado que ha cometido.', 'cosmic'),
('Old', 'Levítico', 19, 23, 'Y cuando hubiereis entrado en la tierra, y plantareis todo árbol de comer, quitaréis su prepucio, lo primero de su fruto: tres años os será incircunciso: su fruto no se comerá.', 'cosmic'),
('Old', 'Levítico', 19, 24, 'Y el cuarto año todo su fruto será santidad de loores á Jehová.', 'cosmic'),
('Old', 'Levítico', 19, 25, 'Mas al quinto año comeréis el fruto de él, para que os haga crecer su fruto: Yo Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 19, 26, 'No comeréis cosa alguna con sangre. No seréis agoreros, ni adivinaréis.', 'cosmic'),
('Old', 'Levítico', 19, 27, 'No cortaréis en redondo las extremidades de vuestras cabezas, ni dañarás la punta de tu barba.', 'cosmic'),
('Old', 'Levítico', 19, 28, 'Y no haréis rasguños en vuestra carne por un muerto, ni imprimiréis en vosotros señal alguna: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 19, 29, 'No contaminarás tu hija haciéndola fornicar: porque no se prostituya la tierra, y se hincha de maldad.', 'cosmic'),
('Old', 'Levítico', 19, 30, 'Mis sábados guardaréis, y mi santuario tendréis en reverencia: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 19, 31, 'No os volváis á los encantadores y á los adivinos: no los consultéis ensuciándoos con ellos: Yo Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 19, 32, 'Delante de las canas te levantarás, y honrarás el rostro del anciano, y de tu Dios tendrás temor: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 19, 33, 'Y cuando el extranjero morare contigo en vuestra tierra, no le oprimiréis.', 'cosmic'),
('Old', 'Levítico', 19, 34, 'Como á un natural de vosotros tendréis al extranjero que peregrinare entre vosotros; y ámalo como á ti mismo; porque peregrinos fuisteis en la tierra de Egipto: Yo Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 19, 35, 'No hagáis agravio en juicio, en medida de tierra, ni en peso, ni en otra medida.', 'cosmic'),
('Old', 'Levítico', 19, 36, 'Balanzas justas, pesas justas, epha justo, é hin justo tendréis: Yo Jehová vuestro Dios, que os saqué de la tierra de Egipto.', 'cosmic'),
('Old', 'Levítico', 19, 37, 'Guardad pues todos mis estatutos, y todos mis derechos, y ponedlos por obra: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 20, 1, 'Y HABLO Jehová á Moisés diciendo:', 'cosmic'),
('Old', 'Levítico', 20, 2, 'Dirás asimismo á los hijos de Israel: Cualquier varón de los hijos de Israel, ó de los extranjeros que peregrinan en Israel, que diere de su simiente á Moloch, de seguro morirá: el pueblo de la tierra lo apedreará con piedras.', 'cosmic'),
('Old', 'Levítico', 20, 3, 'Y yo pondré mi rostro contra el tal varón, y lo cortaré de entre su pueblo; por cuanto dió de su simiente á Moloch, contaminando mi santuario, y amancillando mi santo nombre.', 'cosmic'),
('Old', 'Levítico', 20, 4, 'Que si escondiere el pueblo de la tierra sus ojos de aquel varón que hubiere dado de su simiente á Moloch, para no matarle,', 'cosmic'),
('Old', 'Levítico', 20, 5, 'Entonces yo pondré mi rostro contra aquel varón, y contra su familia, y le cortaré de entre su pueblo, con todos los que fornicaron en pos de él, prostituyéndose con Moloch.', 'cosmic'),
('Old', 'Levítico', 20, 6, 'Y la persona que atendiere á encantadores ó adivinos, para prostituirse tras de ellos, yo pondré mi rostro contra la tal persona, y cortaréla de entre su pueblo.', 'cosmic'),
('Old', 'Levítico', 20, 7, 'Santificaos, pues, y sed santos, porque yo Jehová soy vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 20, 8, 'Y guardad mis estatutos, y ponedlos por obra: Yo Jehová que os santifico.', 'cosmic'),
('Old', 'Levítico', 20, 9, 'Porque varón que maldijere á su padre ó á su madre, de cierto morirá: á su padre ó á su madre maldijo; su sangre será sobre él.', 'cosmic'),
('Old', 'Levítico', 20, 10, 'Y el hombre que adulterare con la mujer de otro, el que cometiere adulterio con la mujer de su prójimo, indefectiblemente se hará morir al adúltero y á la adúltera.', 'cosmic'),
('Old', 'Levítico', 20, 11, 'Y cualquiera que se echare con la mujer de su padre, la desnudez de su padre descubrió; ambos han de ser muertos; su sangre será sobre ellos.', 'cosmic'),
('Old', 'Levítico', 20, 12, 'Y cualquiera que durmiere con su nuera, ambos han de morir: hicieron confusión; su sangre será sobre ellos.', 'cosmic'),
('Old', 'Levítico', 20, 13, 'Y cualquiera que tuviere ayuntamiento con varón como con mujer, abominación hicieron: entrambos han de ser muertos; sobre ellos será su sangre.', 'cosmic'),
('Old', 'Levítico', 20, 14, 'Y el que tomare mujer y á la madre de ella, comete vileza: quemarán en fuego á él y á ellas, porque no haya vileza entre vosotros.', 'cosmic'),
('Old', 'Levítico', 20, 15, 'Y cualquiera que tuviere cópula con bestia, ha de ser muerto; y mataréis á la bestia.', 'cosmic'),
('Old', 'Levítico', 20, 16, 'Y la mujer que se allegare á algún animal, para tener ayuntamiento con él, á la mujer y al animal matarás: morirán infaliblemente; será su sangre sobre ellos.', 'cosmic'),
('Old', 'Levítico', 20, 17, 'Y cualquiera que tomare á su hermana, hija de su padre ó hija de su madre, y viere su desnudez, y ella viere la suya, cosa es execrable; por tanto serán muertos á ojos de los hijos de su pueblo: descubrió la desnudez de su hermana; su pecado llevará.', 'cosmic'),
('Old', 'Levítico', 20, 18, 'Y cualquiera que durmiere con mujer menstruosa, y descubriere su desnudez, su fuente descubrió, y ella descubrió la fuente de su sangre: ambos serán cortados de entre su pueblo.', 'cosmic'),
('Old', 'Levítico', 20, 19, 'La desnudez de la hermana de tu madre, ó de la hermana de tu padre, no descubrirás: por cuanto descubrió su parienta, su iniquidad llevarán.', 'cosmic'),
('Old', 'Levítico', 20, 20, 'Y cualquiera que durmiere con la mujer del hermano de su padre, la desnudez del hermano de su padre descubrió; su pecado llevarán; morirán sin hijos.', 'cosmic'),
('Old', 'Levítico', 20, 21, 'Y el que tomare la mujer de su hermano, es suciedad; la desnudez de su hermano descubrió; sin hijos serán.', 'cosmic'),
('Old', 'Levítico', 20, 22, 'Guardad, pues, todos mis estatutos y todos mis derechos, y ponedlos por obra: y no os vomitará la tierra, en la cual yo os introduzco para que habitéis en ella.', 'cosmic'),
('Old', 'Levítico', 20, 23, 'Y no andéis en las prácticas de la gente que yo echaré de delante de vosotros: porque ellos hicieron todas estas cosas, y los tuve en abominación.', 'cosmic'),
('Old', 'Levítico', 20, 24, 'Empero á vosotros os he dicho: Vosotros poseeréis la tierra de ellos, y yo os la daré para que la poseáis por heredad, tierra que fluye leche y miel: Yo Jehová vuestro Dios, que os he apartado de los pueblos.', 'cosmic'),
('Old', 'Levítico', 20, 25, 'Por tanto, vosotros haréis diferencia entre animal limpio é inmundo, y entre ave inmunda y limpia: y no ensuciéis vuestras personas en los animales, ni en las aves, ni en ninguna cosa que va arrastrando por la tierra, las cuales os he apartado por inmundas.', 'cosmic'),
('Old', 'Levítico', 20, 26, 'Habéis, pues, de serme santos, porque yo Jehová soy santo, y os he apartado de los pueblos, para que seáis míos.', 'cosmic'),
('Old', 'Levítico', 20, 27, 'Y el hombre ó la mujer en quienes hubiere espíritu phitónico ó de adivinación, han de ser muertos: los apedrearán con piedras; su sangre sobre ellos.', 'cosmic'),
('Old', 'Levítico', 21, 1, 'Y JEHOVA dijo á Moisés: Habla á los sacerdotes hijos de Aarón, y diles que no se contaminen por un muerto en sus pueblos.', 'cosmic'),
('Old', 'Levítico', 21, 2, 'Mas por su pariente cercano á sí, por su madre, ó por su padre, ó por su hijo, ó por su hermano,', 'cosmic'),
('Old', 'Levítico', 21, 3, 'O por su hermana virgen, á él cercana, la cual no haya tenido marido, por ella se contaminará.', 'cosmic'),
('Old', 'Levítico', 21, 4, 'No se contaminará, porque es príncipe en sus pueblos, haciéndose inmundo.', 'cosmic'),
('Old', 'Levítico', 21, 5, 'No harán calva en su cabeza, ni raerán la punta de su barba, ni en su carne harán rasguños.', 'cosmic'),
('Old', 'Levítico', 21, 6, 'Santos serán á su Dios, y no profanarán el nombre de su Dios; porque los fuegos de Jehová y el pan de su Dios ofrecen: por tanto serán santos.', 'cosmic'),
('Old', 'Levítico', 21, 7, 'Mujer ramera ó infame no tomarán: ni tomarán mujer repudiada de su marido: porque es santo á su Dios.', 'cosmic'),
('Old', 'Levítico', 21, 8, 'Lo santificarás por tanto, pues el pan de tu Dios ofrece: santo será para ti, porque santo soy yo Jehová vuestro santificador.', 'cosmic'),
('Old', 'Levítico', 21, 9, 'Y la hija del varón sacerdote, si comenzare á fornicar, á su padre amancilla: quemada será al fuego.', 'cosmic'),
('Old', 'Levítico', 21, 10, 'Y el sumo sacerdote entre sus hermanos, sobre cuya cabeza fué derramado el aceite de la unción, y que hinchió su mano para vestir las vestimentas, no descubrirá su cabeza, ni romperá sus vestidos:', 'cosmic'),
('Old', 'Levítico', 21, 11, 'Ni entrará donde haya alguna persona muerta, ni por su padre, ó por su madre se contaminará.', 'cosmic'),
('Old', 'Levítico', 21, 12, 'Ni saldrá del santuario, ni contaminará el santuario de su Dios; porque la corona del aceite de la unción de su Dios está sobre él: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 21, 13, 'Y tomará él mujer con su virginidad.', 'cosmic'),
('Old', 'Levítico', 21, 14, 'Viuda, ó repudiada, ó infame, ó ramera, éstas no tomará: mas tomará virgen de sus pueblos por mujer.', 'cosmic'),
('Old', 'Levítico', 21, 15, 'Y no amancillará su simiente en sus pueblos; porque yo Jehová soy el que los santifico.', 'cosmic'),
('Old', 'Levítico', 21, 16, 'Y Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 21, 17, 'Habla á Aarón, y dile: El varón de tu simiente en sus generaciones, en el cual hubiere falta, no se allegará para ofrecer el pan de su Dios.', 'cosmic'),
('Old', 'Levítico', 21, 18, 'Porque ningún varón en el cual hubiere falta, se allegará: varón ciego, ó cojo, ó falto, ó sobrado,', 'cosmic'),
('Old', 'Levítico', 21, 19, 'O varón en el cual hubiere quebradura de pie ó rotura de mano,', 'cosmic'),
('Old', 'Levítico', 21, 20, 'O corcobado, ó lagañoso, ó que tuviere nube en el ojo, ó que tenga sarna, ó empeine, ó compañón relajado;', 'cosmic'),
('Old', 'Levítico', 21, 21, 'Ningún varón de la simiente de Aarón sacerdote, en el cual hubiere falta, se allegará para ofrecer las ofrendas encendidas de Jehová. Hay falta en él; no se allegará á ofrecer el pan de su Dios.', 'cosmic'),
('Old', 'Levítico', 21, 22, 'El pan de su Dios, de lo muy santo y las cosas santificadas, comerá.', 'cosmic'),
('Old', 'Levítico', 21, 23, 'Empero no entrará del velo adentro, ni se allegará al altar, por cuanto hay falta en él: y no profanará mi santuario, porque yo Jehová soy el que los santifico.', 'cosmic'),
('Old', 'Levítico', 21, 24, 'Y Moisés habló esto á Aarón, y á sus hijos, y á todos los hijos de Israel.', 'cosmic'),
('Old', 'Levítico', 22, 1, 'Y HABLO Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 22, 2, 'Di á Aarón y á sus hijos, que se abstengan de las santificaciones de los hijos de Israel, y que no profanen mi santo nombre en lo que ellos me santifican: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 22, 3, 'Diles: Todo varón de toda vuestra simiente en vuestras generaciones que llegare á las cosas sagradas, que los hijos de Israel consagran á Jehová, teniendo inmundicia sobre sí, de delante de mí será cortada su alma: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 22, 4, 'Cualquier varón de la simiente de Aarón que fuere leproso, ó padeciere flujo, no comerá de las cosas sagradas hasta que esté limpio: y el que tocare cualquiera cosa inmunda de mortecino, ó el varón del cual hubiere salido derramamiento de semen;', 'cosmic'),
('Old', 'Levítico', 22, 5, 'O el varón que hubiere tocado cualquier reptil, por el cual será inmundo, ú hombre por el cual venga á ser inmundo, conforme á cualquiera inmundicia suya;', 'cosmic'),
('Old', 'Levítico', 22, 6, 'La persona que lo tocare, será inmunda hasta la tarde, y no comerá de las cosas sagradas antes que haya lavado su carne con agua.', 'cosmic'),
('Old', 'Levítico', 22, 7, 'Y cuando el sol se pusiere, será limpio; y después comerá las cosas sagradas, porque su pan es.', 'cosmic'),
('Old', 'Levítico', 22, 8, 'Mortecino ni despedazado por fiera no comerá, para contaminarse en ello: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 22, 9, 'Guarden, pues, mi ordenanza, y no lleven pecado por ello, no sea que así mueran cuando la profanaren: Yo Jehová que los santifico.', 'cosmic'),
('Old', 'Levítico', 22, 10, 'Ningún extraño comerá cosa sagrada; el huésped del sacerdote, ni el jornalero, no comerá cosa sagrada.', 'cosmic'),
('Old', 'Levítico', 22, 11, 'Mas el sacerdote, cuando comprare persona de su dinero, ésta comerá de ella, y el nacido en su casa: estos comerán de su pan.', 'cosmic'),
('Old', 'Levítico', 22, 12, 'Empero la hija del sacerdote, cuando se casare con varón extraño, ella no comerá de la ofrenda de las cosas sagradas.', 'cosmic'),
('Old', 'Levítico', 22, 13, 'Pero si la hija del sacerdote fuere viuda, ó repudiada, y no tuviere prole, y se hubiere vuelto á la casa de su padre, como en su mocedad, comerá del pan de su padre; mas ningún extraño coma de él.', 'cosmic'),
('Old', 'Levítico', 22, 14, 'Y el que por yerro comiere cosa sagrada, añadirá á ella su quinto, y darálo al sacerdote con la cosa sagrada.', 'cosmic'),
('Old', 'Levítico', 22, 15, 'No profanarán, pues, las cosas santas de los hijos de Israel, las cuales apartan para Jehová:', 'cosmic'),
('Old', 'Levítico', 22, 16, 'Y no les harán llevar la iniquidad del pecado, comiendo las cosas santas de ellos: porque yo Jehová soy el que los santifico.', 'cosmic'),
('Old', 'Levítico', 22, 17, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 22, 18, 'Habla á Aarón y á sus hijos, y á todos los hijos de Israel, y diles: Cualquier varón de la casa de Israel, ó de los extranjeros en Israel, que ofreciere su ofrenda por todos sus votos, y por todas sus voluntarias oblaciones que ofrecieren á Jehová en holocausto;', 'cosmic'),
('Old', 'Levítico', 22, 19, 'De vuestra voluntad ofreceréis macho sin defecto de entre las vacas, de entre los corderos, ó de entre las cabras.', 'cosmic'),
('Old', 'Levítico', 22, 20, 'Ninguna cosa en que haya falta ofreceréis, porque no será acepto por vosotros.', 'cosmic'),
('Old', 'Levítico', 22, 21, 'Asimismo, cuando alguno ofreciere sacrificio de paces á Jehová para presentar voto, ú ofreciendo voluntariamente, sea de vacas ó de ovejas, sin tacha será acepto; no ha de haber en él falta.', 'cosmic'),
('Old', 'Levítico', 22, 22, 'Ciego, ó perniquebrado, ó mutilado, ó verrugoso, ó sarnoso ó roñoso, no ofreceréis éstos á Jehová, ni de ellos pondréis ofrenda encendida sobre el altar de Jehová.', 'cosmic'),
('Old', 'Levítico', 22, 23, 'Buey ó carnero que tenga de más ó de menos, podrás ofrecer por ofrenda voluntaria; mas por voto no será acepto.', 'cosmic'),
('Old', 'Levítico', 22, 24, 'Herido ó magullado, rompido ó cortado, no ofreceréis á Jehová, ni en vuestra tierra lo haréis.', 'cosmic'),
('Old', 'Levítico', 22, 25, 'Y de mano de hijo de extranjero no ofreceréis el pan de vuestro Dios de todas estas cosas; porque su corrupción está en ellas: hay en ellas falta, no se os aceptarán.', 'cosmic'),
('Old', 'Levítico', 22, 26, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 22, 27, 'El buey, ó el cordero, ó la cabra, cuando naciere, siete días estará mamando de su madre: mas desde el octavo día en adelante será acepto para ofrenda de sacrificio encendido a Jehová.', 'cosmic'),
('Old', 'Levítico', 22, 28, 'Y sea buey ó carnero, no degollaréis en un día á el y á su hijo.', 'cosmic'),
('Old', 'Levítico', 22, 29, 'Y cuando sacrificareis sacrificio de hacimiento de gracias á Jehová, de vuestra voluntad lo sacrificaréis.', 'cosmic'),
('Old', 'Levítico', 22, 30, 'En el mismo día se comerá; no dejaréis de él para otro día: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 22, 31, 'Guardad pues mis mandamientos, y ejecutadlos: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 22, 32, 'Y no amancilléis mi santo nombre, y yo me santificaré en medio de los hijos de Israel: Yo Jehová que os santifico;', 'cosmic'),
('Old', 'Levítico', 22, 33, 'Que os saqué de la tierra de Egipto, para ser vuestro Dios: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 23, 1, 'Y HABLO Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 23, 2, 'Habla á los hijos de Israel, y diles: Las solemnidades de Jehová, las cuales proclamaréis santas convocaciones, aquestas serán mis solemnidades.', 'cosmic'),
('Old', 'Levítico', 23, 3, 'Seis días se trabajará, y el séptimo día sábado de reposo será, convocación santa: ninguna obra haréis; sábado es de Jehová en todas vuestras habitaciones.', 'cosmic'),
('Old', 'Levítico', 23, 4, 'Estas son las solemnidades de Jehová, las convocaciones santas, á las cuales convocaréis en sus tiempos.', 'cosmic'),
('Old', 'Levítico', 23, 5, 'En el mes primero, á los catorce del mes, entre las dos tardes, pascua es de Jehová.', 'cosmic'),
('Old', 'Levítico', 23, 6, 'Y á los quince días de este mes es la solemnidad de los ázimos á Jehová: siete días comeréis ázimos.', 'cosmic'),
('Old', 'Levítico', 23, 7, 'El primer día tendréis santa convocación: ningúna obra servil haréis.', 'cosmic'),
('Old', 'Levítico', 23, 8, 'Y ofreceréis á Jehová siete días ofrenda encendida: el séptimo día será santa convocación; ninguna obra servil haréis.', 'cosmic'),
('Old', 'Levítico', 23, 9, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 23, 10, 'Habla á los hijos de Israel, y diles: Cuando hubiereis entrado en la tierra que yo os doy, y segareis su mies, traeréis al sacerdote un omer por primicia de los primeros frutos de vuestra siega;', 'cosmic'),
('Old', 'Levítico', 23, 11, 'El cual mecerá el omer delante de Jehová, para que seáis aceptos: el siguiente día del sábado lo mecerá el sacerdote.', 'cosmic'),
('Old', 'Levítico', 23, 12, 'Y el día que ofrezcáis el omer, ofreceréis un cordero de un año, sin defecto, en holocausto á Jehová.', 'cosmic'),
('Old', 'Levítico', 23, 13, 'Y su presente será dos décimas de flor de harina amasada con aceite, ofrenda encendida á Jehová en olor suavísimo; y su libación de vino, la cuarta parte de un hin.', 'cosmic'),
('Old', 'Levítico', 23, 14, 'Y no comeréis pan, ni grano tostado, ni espiga fresca, hasta este mismo día, hasta que hayáis ofrecido la ofrenda de vuestro Dios; estatuto perpetuo es por vuestras edades en todas vuestras habitaciones.', 'cosmic'),
('Old', 'Levítico', 23, 15, 'Y os habéis de contar desde el siguiente día del sábado, desde el día en que ofrecisteis el omer de la ofrenda mecida; siete semanas cumplidas serán:', 'cosmic'),
('Old', 'Levítico', 23, 16, 'Hasta el siguiente día del sábado séptimo contaréis cincuenta días; entonces ofreceréis nuevo presente a Jehová.', 'cosmic'),
('Old', 'Levítico', 23, 17, 'De vuestras habitaciones traeréis dos panes para ofrenda mecida, que serán de dos décimas de flor de harina, cocidos con levadura, por primicias á Jehová.', 'cosmic'),
('Old', 'Levítico', 23, 18, 'Y ofreceréis con el pan siete corderos de un año sin defecto, y un becerro de la vacada y dos carneros: serán holocausto á Jehová, con su presente y sus libaciones; ofrenda encendida de suave olor á Jehová.', 'cosmic'),
('Old', 'Levítico', 23, 19, 'Ofreceréis además un macho de cabrío por expiación; y dos corderos de un año en sacrificio de paces.', 'cosmic'),
('Old', 'Levítico', 23, 20, 'Y el sacerdote los mecerá en ofrenda agitada delante de Jehová, con el pan de las primicias, y los dos corderos: serán cosa sagrada de Jehová para el sacerdote.', 'cosmic'),
('Old', 'Levítico', 23, 21, 'Y convocaréis en este mismo día; os será santa convocación: ninguna obra servil haréis: estatuto perpetuo en todas vuestras habitaciones por vuestras edades.', 'cosmic'),
('Old', 'Levítico', 23, 22, 'Y cuando segareis la mies de vuestra tierra, no acabarás de segar el rincón de tu haza, ni espigarás tu siega; para el pobre, y para el extranjero la dejarás: Yo Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 23, 23, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 23, 24, 'Habla á los hijos de Israel, y diles: En el mes séptimo, al primero del mes tendréis sábado, una conmemoración al son de trompetas, y una santa convocación.', 'cosmic'),
('Old', 'Levítico', 23, 25, 'Ninguna obra servil haréis; y ofreceréis ofrenda encendida á Jehová.', 'cosmic'),
('Old', 'Levítico', 23, 26, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 23, 27, 'Empero á los diez de este mes séptimo será el día de las expiaciones: tendréis santa convocación, y afligiréis vuestras almas, y ofreceréis ofrenda encendida á Jehová.', 'cosmic'),
('Old', 'Levítico', 23, 28, 'Ninguna obra haréis en este mismo día; porque es día de expiaciones, para reconciliaros delante de Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 23, 29, 'Porque toda persona que no se afligiere en este mismo día, será cortada de sus pueblos.', 'cosmic'),
('Old', 'Levítico', 23, 30, 'Y cualquiera persona que hiciere obra alguna en este mismo día, yo destruiré la tal persona de entre su pueblo.', 'cosmic'),
('Old', 'Levítico', 23, 31, 'Ninguna obra haréis: estatuto perpetuo es por vuestras edades en todas vuestras habitaciones.', 'cosmic'),
('Old', 'Levítico', 23, 32, 'Sábado de reposo será á vosotros, y afligiréis vuestras almas, comenzando á los nueve del mes en la tarde: de tarde á tarde holgaréis vuestro sábado.', 'cosmic'),
('Old', 'Levítico', 23, 33, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 23, 34, 'Habla á los hijos de Israel, y diles: A los quince días de este mes séptimo será la solemnidad de las cabañas á Jehová por siete días.', 'cosmic'),
('Old', 'Levítico', 23, 35, 'El primer día habrá santa convocación: ninguna obra servil haréis.', 'cosmic'),
('Old', 'Levítico', 23, 36, 'Siete días ofreceréis ofrenda encendida á Jehová: el octavo día tendréis santa convocación, y ofreceréis ofrenda encendida á Jehová: es fiesta: ninguna obra servil haréis.', 'cosmic'),
('Old', 'Levítico', 23, 37, 'Estas son las solemnidades de Jehová, á las que convocaréis santas reuniones, para ofrecer ofrenda encendida a Jehová, holocausto y presente, sacrificio y libaciones, cada cosa en su tiempo:', 'cosmic'),
('Old', 'Levítico', 23, 38, 'Además de los sábados de Jehová y además de vuestros dones, y á más de todos vuestros votos, y además de todas vuestras ofrendas voluntarias, que daréis á Jehová.', 'cosmic'),
('Old', 'Levítico', 23, 39, 'Empero á los quince del mes séptimo, cuando hubiereis allegado el fruto de la tierra, haréis fiesta a Jehová por siete días: el primer día será sábado; sábado será también el octavo día.', 'cosmic'),
('Old', 'Levítico', 23, 40, 'Y tomaréis el primer día gajos con fruto de árbol hermoso, ramos de palmas, y ramas de árboles espesos, y sauces de los arroyos; y os regocijaréis delante de Jehová vuestro Dios por siete días.', 'cosmic'),
('Old', 'Levítico', 23, 41, 'Y le haréis fiesta á Jehová por siete días cada un año; será estatuto perpetuo por vuestras edades; en el mes séptimo la haréis.', 'cosmic'),
('Old', 'Levítico', 23, 42, 'En cabañas habitaréis siete días: todo natural de Israel habitará en cabañas;', 'cosmic'),
('Old', 'Levítico', 23, 43, 'Para que sepan vuestros descendientes que en cabañas hice yo habitar á los hijos de Israel, cuando los saqué de la tierra de Egipto: Yo Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 23, 44, 'Así habló Moisés á los hijos de Israel sobre las solemnidades de Jehová.', 'cosmic'),
('Old', 'Levítico', 24, 1, 'Y HABLO Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 24, 2, 'Manda á los hijos de Israel que te traigan aceite de olivas claro, molido, para la luminaria, para hacer arder las lámparas de continuo.', 'cosmic'),
('Old', 'Levítico', 24, 3, 'Fuera del velo del testimonio, en el tabernáculo del testimonio, las aderezará Aarón desde la tarde hasta la mañana delante de Jehová, de continuo: estatuto perpetuo por vuestras edades.', 'cosmic'),
('Old', 'Levítico', 24, 4, 'Sobre el candelero limpio pondrá siempre en orden las lámparas delante de Jehová.', 'cosmic'),
('Old', 'Levítico', 24, 5, 'Y tomarás flor de harina, y cocerás de ella doce tortas: cada torta será de dos décimas.', 'cosmic'),
('Old', 'Levítico', 24, 6, 'Y has de ponerlas en dos órdenes, seis en cada orden, sobre la mesa limpia delante de Jehová.', 'cosmic'),
('Old', 'Levítico', 24, 7, 'Pondrás también sobre cada orden incienso limpio, y será para el pan por perfume, ofrenda encendida á Jehová.', 'cosmic'),
('Old', 'Levítico', 24, 8, 'Cada día de sábado lo pondrá de continuo en orden delante de Jehová, de los hijos de Israel por pacto sempiterno.', 'cosmic'),
('Old', 'Levítico', 24, 9, 'Y será de Aarón y de sus hijos, los cuales lo comerán en el lugar santo; porque es cosa muy santa para él, de las ofrendas encendidas á Jehová, por fuero perpetuo.', 'cosmic'),
('Old', 'Levítico', 24, 10, 'En aquella sazón el hijo de una mujer Israelita, el cual era hijo de un Egipcio, salió entre los hijos de Israel; y el hijo de la Israelita y un hombre de Israel riñeron en el real:', 'cosmic'),
('Old', 'Levítico', 24, 11, 'Y el hijo de la mujer Israelita pronunció el Nombre, y maldijo: entonces le llevaron á Moisés. Y su madre se llamaba Selomith, hija de Dribi, de la tribu de Dan.', 'cosmic'),
('Old', 'Levítico', 24, 12, 'Y pusiéronlo en la cárcel, hasta que les fuese declarado por palabra de Jehová.', 'cosmic'),
('Old', 'Levítico', 24, 13, 'Y Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 24, 14, 'Saca al blasfemo fuera del real, y todos los que le oyeron pongan sus manos sobre la cabeza de él, y apedréelo toda la congregación.', 'cosmic'),
('Old', 'Levítico', 24, 15, 'Y á los hijos de Israel hablarás, diciendo: Cualquiera que maldijere á su Dios, llevará su iniquidad.', 'cosmic'),
('Old', 'Levítico', 24, 16, 'Y el que blasfemare el nombre de Jehová, ha de ser muerto; toda la congregación lo apedreará: así el extranjero como el natural, si blasfemare el Nombre, que muera.', 'cosmic'),
('Old', 'Levítico', 24, 17, 'Asimismo el hombre que hiere de muerte á cualquiera persona, que sufra la muerte.', 'cosmic'),
('Old', 'Levítico', 24, 18, 'Y el que hiere á algún animal ha de restituirlo: animal por animal.', 'cosmic'),
('Old', 'Levítico', 24, 19, 'Y el que causare lesión en su prójimo, según hizo, así le sea hecho:', 'cosmic'),
('Old', 'Levítico', 24, 20, 'Rotura por rotura, ojo por ojo, diente por diente: según la lesión que habrá hecho á otro, tal se hará á él.', 'cosmic'),
('Old', 'Levítico', 24, 21, 'El que hiere algún animal, ha de restituirlo; mas el que hiere de muerte á un hombre, que muera.', 'cosmic'),
('Old', 'Levítico', 24, 22, 'Un mismo derecho tendréis: como el extranjero, así será el natural: porque yo soy Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 24, 23, 'Y habló Moisés á los hijos de Israel, y ellos sacaron al blasfemo fuera del real, y apedreáronlo con piedras. Y los hijos de Israel hicieron según que Jehová había mandado á Moisés.', 'cosmic'),
('Old', 'Levítico', 25, 1, 'Y JEHOVA habló á Moisés en el monte de Sinaí, diciendo:', 'cosmic'),
('Old', 'Levítico', 25, 2, 'Habla á los hijos de Israel, y diles: Cuando hubiereis entrado en la tierra que yo os doy, la tierra hará sábado á Jehová.', 'cosmic'),
('Old', 'Levítico', 25, 3, 'Seis años sembrarás tu tierra, y seis años podarás tu viña, y cogerás sus frutos;', 'cosmic'),
('Old', 'Levítico', 25, 4, 'Y el séptimo año la tierra tendrá sábado de holganza, sábado á Jehová: no sembrarás tu tierra, ni podarás tu viña.', 'cosmic'),
('Old', 'Levítico', 25, 5, 'Lo que de suyo se naciere en tu tierra segada, no lo segarás; y las uvas de tu viñedo no vendimiarás: año de holganza será á la tierra.', 'cosmic'),
('Old', 'Levítico', 25, 6, 'Mas el sábado de la tierra os será para comer á ti, y á tu siervo, y á tu sierva, y á tu criado, y á tu extranjero que morare contigo:', 'cosmic'),
('Old', 'Levítico', 25, 7, 'Y á tu animal, y á la bestia que hubiere en tu tierra, será todo el fruto de ella para comer.', 'cosmic'),
('Old', 'Levítico', 25, 8, 'Y te has de contar siete semanas de años, siete veces siete años; de modo que los días de las siete semanas de años vendrán á serte cuarenta y nueve años.', 'cosmic'),
('Old', 'Levítico', 25, 9, 'Entonces harás pasar la trompeta de jubilación en el mes séptimo á los diez del mes; el día de la expiación haréis pasar la trompeta por toda vuestra tierra.', 'cosmic'),
('Old', 'Levítico', 25, 10, 'Y santificaréis el año cincuenta, y pregonaréis libertad en la tierra á todos sus moradores: este os será jubileo; y volveréis cada uno á su posesión, y cada cual volverá á su familia.', 'cosmic'),
('Old', 'Levítico', 25, 11, 'El año de los cincuenta años os será jubileo: no sembraréis, ni segaréis lo que naciere de suyo en la tierra, ni vendimiaréis sus viñedos:', 'cosmic'),
('Old', 'Levítico', 25, 12, 'Porque es jubileo: santo será á vosotros; el producto de la tierra comeréis.', 'cosmic'),
('Old', 'Levítico', 25, 13, 'En este año de jubileo volveréis cada uno á su posesión.', 'cosmic'),
('Old', 'Levítico', 25, 14, 'Y cuando vendiereis algo á vuestro prójimo, ó comprareis de mano de vuestro prójimo, no engañe ninguno a su hermano:', 'cosmic'),
('Old', 'Levítico', 25, 15, 'Conforme al número de los años después del jubileo comprarás de tu prójimo; conforme al número de los años de los frutos te venderá él á ti.', 'cosmic'),
('Old', 'Levítico', 25, 16, 'Conforme á la multitud de los años aumentarás el precio, y conforme á la disminución de los años disminuirás el precio; porque según el número de los rendimientos te ha de vender él.', 'cosmic'),
('Old', 'Levítico', 25, 17, 'Y no engañe ninguno á su prójimo; mas tendrás temor de tu Dios: porque yo soy Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 25, 18, 'Ejecutad, pues, mis estatutos, y guardad mis derechos, y ponedlos por obra, y habitaréis en la tierra seguros;', 'cosmic'),
('Old', 'Levítico', 25, 19, 'Y la tierra dará su fruto, y comeréis hasta hartura, y habitaréis en ella con seguridad.', 'cosmic'),
('Old', 'Levítico', 25, 20, 'Y si dijereis: ¿Qué comeremos el séptimo año? he aquí no hemos de sembrar, ni hemos de coger nuestros frutos:', 'cosmic'),
('Old', 'Levítico', 25, 21, 'Entonces yo os enviaré mi bendición el sexto año, y hará fruto por tres años.', 'cosmic'),
('Old', 'Levítico', 25, 22, 'Y sembraréis el año octavo, y comeréis del fruto añejo; hasta el año noveno, hasta que venga su fruto comeréis del añejo.', 'cosmic'),
('Old', 'Levítico', 25, 23, 'Y la tierra no se venderá rematadamente, porque la tierra mía es; que vosotros peregrinos y extranjeros sois para conmigo.', 'cosmic'),
('Old', 'Levítico', 25, 24, 'Por tanto, en toda la tierra de vuestra posesión, otorgaréis redención á la tierra.', 'cosmic'),
('Old', 'Levítico', 25, 25, 'Cuando tu hermano empobreciere, y vendiere algo de su posesión, vendrá el rescatador, su cercano, y rescatará lo que su hermano hubiere vendido.', 'cosmic'),
('Old', 'Levítico', 25, 26, 'Y cuando el hombre no tuviere rescatador, si alcanzare su mano, y hallare lo que basta para su rescate;', 'cosmic'),
('Old', 'Levítico', 25, 27, 'Entonces contará los años de su venta, y pagará lo que quedare al varón á quien vendió, y volverá á su posesión.', 'cosmic'),
('Old', 'Levítico', 25, 28, 'Mas si no alcanzare su mano lo que basta para que vuelva á él, lo que vendió estará en poder del que lo compró hasta el año del jubileo; y al jubileo saldrá, y él volverá á su posesión.', 'cosmic'),
('Old', 'Levítico', 25, 29, 'Y el varón que vendiere casa de morada en ciudad cercada, tendrá facultad de redimirla hasta acabarse el año de su venta: un año será el término de poderse redimir.', 'cosmic'),
('Old', 'Levítico', 25, 30, 'Y si no fuere redimida dentro de un año entero, la casa que estuviere en la ciudad murada quedará para siempre por de aquel que la compró, y para sus descendientes: no saldrá en el jubileo.', 'cosmic'),
('Old', 'Levítico', 25, 31, 'Mas las casas de las aldeas que no tienen muro alrededor, serán estimadas como una haza de tierra: tendrán redención, y saldrán en el jubileo.', 'cosmic'),
('Old', 'Levítico', 25, 32, 'Pero en cuanto á las ciudades de los Levitas, siempre podrán redimir los Levitas las casas de las ciudades que poseyeren.', 'cosmic'),
('Old', 'Levítico', 25, 33, 'Y el que comprare de los Levitas, saldrá de la casa vendida, ó de la ciudad de su posesión, en el jubileo: por cuanto las casas de las ciudades de los Levitas es la posesión de ellos entre los hijos de Israel.', 'cosmic'),
('Old', 'Levítico', 25, 34, 'Mas la tierra del ejido de sus ciudades no se venderá, porque es perpetua posesión de ellos.', 'cosmic'),
('Old', 'Levítico', 25, 35, 'Y cuando tu hermano empobreciere, y se acogiere á ti, tú lo ampararás: como peregrino y extranjero vivirá contigo.', 'cosmic'),
('Old', 'Levítico', 25, 36, 'No tomarás usura de él, ni aumento; mas tendrás temor de tu Dios, y tu hermano vivirá contigo.', 'cosmic'),
('Old', 'Levítico', 25, 37, 'No le darás tu dinero á usura, ni tu vitualla á ganancia:', 'cosmic'),
('Old', 'Levítico', 25, 38, 'Yo Jehová vuestro Dios, que os saqué de la tierra de Egipto, para daros la tierra de Canaán, para ser vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 25, 39, 'Y cuando tu hermano empobreciere, estando contigo, y se vendiere á ti, no le harás servir como siervo:', 'cosmic'),
('Old', 'Levítico', 25, 40, 'Como criado, como extranjero estará contigo; hasta el año del jubileo te servirá.', 'cosmic'),
('Old', 'Levítico', 25, 41, 'Entonces saldrá de contigo, él y sus hijos consigo, y volverá á su familia, y á la posesión de sus padres se restituirá.', 'cosmic'),
('Old', 'Levítico', 25, 42, 'Porque son mis siervos, los cuales saqué yo de la tierra de Egipto: no serán vendidos á manera de siervos.', 'cosmic'),
('Old', 'Levítico', 25, 43, 'No te enseñorearás de él con dureza, mas tendrás temor de tu Dios.', 'cosmic'),
('Old', 'Levítico', 25, 44, 'Así tu siervo como tu sierva que tuvieres, serán de las gentes que están en vuestro alrededor: de ellos compraréis siervos y siervas.', 'cosmic'),
('Old', 'Levítico', 25, 45, 'También compraréis de los hijos de los forasteros que viven entre vosotros, y de los que del linaje de ellos son nacidos en vuestra tierra, que están con vosotros; los cuales tendréis por posesión:', 'cosmic'),
('Old', 'Levítico', 25, 46, 'Y los poseeréis por juro de heredad para vuestros hijos después de vosotros, como posesión hereditaria; para siempre os serviréis de ellos; empero en vuestros hermanos los hijos de Israel, no os enseñorearéis cada uno sobre su hermano con dureza.', 'cosmic'),
('Old', 'Levítico', 25, 47, 'Y si el peregrino ó extranjero que está contigo, adquiriese medios, y tu hermano que está con él empobreciere, y se vendiere al peregrino ó extranjero que está contigo, ó á la raza de la familia del extranjero;', 'cosmic'),
('Old', 'Levítico', 25, 48, 'Después que se hubiere vendido, podrá ser rescatado: uno de sus hermanos lo rescatará;', 'cosmic'),
('Old', 'Levítico', 25, 49, 'O su tío, ó el hijo de su tío lo rescatará, ó el cercano de su carne, de su linaje, lo rescatará; ó si sus medios alcanzaren, él mismo se redimirá.', 'cosmic'),
('Old', 'Levítico', 25, 50, 'Y contará con el que lo compró, desde el año que se vendió á él hasta el año del jubileo: y ha de apreciarse el dinero de su venta conforme al número de los años, y se hará con él conforme al tiempo de un criado asalariado.', 'cosmic'),
('Old', 'Levítico', 25, 51, 'Si aún fueren muchos años, conforme á ellos volverá para su rescate del dinero por el cual se vendió.', 'cosmic'),
('Old', 'Levítico', 25, 52, 'Y si quedare poco tiempo hasta el año del jubileo, entonces contará con él, y devolverá su rescate conforme á sus años.', 'cosmic'),
('Old', 'Levítico', 25, 53, 'Como con tomado á salario anualmente hará con él: no se enseñoreará en él con aspereza delante de tus ojos.', 'cosmic'),
('Old', 'Levítico', 25, 54, 'Mas si no se redimiere en esos años, en el año del jubileo saldrá, él, y sus hijos con él.', 'cosmic'),
('Old', 'Levítico', 25, 55, 'Porque mis siervos son los hijos de Israel; son siervos míos, á los cuales saqué de la tierra de Egipto: Yo Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 26, 1, 'NO haréis para vosotros ídolos, ni escultura, ni os levantaréis estatua, ni pondréis en vuestra tierra piedra pintada para inclinaros á ella: porque yo soy Jehová vuestro Dios.', 'cosmic'),
('Old', 'Levítico', 26, 2, 'Guardad mis sábados, y tened en reverencia mi santuario: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 26, 3, 'Si anduviereis en mis decretos, y guardareis mis mandamientos, y los pusiereis por obra;', 'cosmic'),
('Old', 'Levítico', 26, 4, 'Yo daré vuestra lluvia en su tiempo, cy la tierra rendirá sus producciones, y el árbol del campo dará su fruto;', 'cosmic'),
('Old', 'Levítico', 26, 5, 'Y la trilla os alcanzará á la vendimia, y la vendimia alcanzará á la sementera, y comeréis vuestro pan en hartura y habitaréis seguros en vuestra tierra:', 'cosmic'),
('Old', 'Levítico', 26, 6, 'Y yo daré paz en la tierra, y dormiréis, y no habrá quien os espante: y haré quitar las malas bestias de vuestra tierra, y no pasará por vuestro país la espada:', 'cosmic'),
('Old', 'Levítico', 26, 7, 'Y perseguiréis á vuestros enemigos, y caerán á cuchillo delante de vosotros:', 'cosmic'),
('Old', 'Levítico', 26, 8, 'Y cinco de vosotros perseguirán á ciento, y ciento de vosotros perseguirán á diez mil, y vuestros enemigos caerán á cuchillo delante de vosotros.', 'cosmic'),
('Old', 'Levítico', 26, 9, 'Porque yo me volveré á vosotros, y os haré crecer, y os multiplicaré, y afirmaré mi pacto con vosotros:', 'cosmic'),
('Old', 'Levítico', 26, 10, 'Y comeréis lo añejo de mucho tiempo, y sacareis fuera lo añejo á causa de lo nuevo:', 'cosmic'),
('Old', 'Levítico', 26, 11, 'Y pondré mi morada en medio de vosotros, y mi alma no os abominará:', 'cosmic'),
('Old', 'Levítico', 26, 12, 'Y andaré entre vosotros, y yo seré vuestro Dios, y vosotros seréis mi pueblo.', 'cosmic'),
('Old', 'Levítico', 26, 13, 'Yo Jehová vuestro Dios, que os saqué de la tierra de Egipto, para que no fueseis sus siervos; y rompí las coyundas de vuestro yugo, y os he hecho andar el rostro alto.', 'cosmic'),
('Old', 'Levítico', 26, 14, 'Empero si no me oyereis, ni hiciereis todos estos mis mandamientos,', 'cosmic'),
('Old', 'Levítico', 26, 15, 'Y si abominareis mis decretos, y vuestra alma menospreciare mis derechos, no ejecutando todos mis mandamientos, é invalidando mi pacto;', 'cosmic'),
('Old', 'Levítico', 26, 16, 'Yo también haré con vosotros esto: enviaré sobre vosotros terror, extenuación y calentura, que consuman los ojos y atormenten el alma: y sembraréis en balde vuestra simiente, porque vuestros enemigos la comerán:', 'cosmic'),
('Old', 'Levítico', 26, 17, 'Y pondré mi ira sobre vosotros, y seréis heridos delante de vuestros enemigos; y los que os aborrecen se enseñorearán de vosotros, y huiréis sin que haya quien os persiga.', 'cosmic'),
('Old', 'Levítico', 26, 18, 'Y si aun con estas cosas no me oyereis, yo tornaré á castigaros siete veces más por vuestros pecados.', 'cosmic'),
('Old', 'Levítico', 26, 19, 'Y quebrantaré la soberbia de vuestra fortaleza, y tornaré vuestro cielo como hierro, y vuestra tierra como metal:', 'cosmic'),
('Old', 'Levítico', 26, 20, 'Y vuestra fuerza se consumirá en vano; que vuestra tierra no dará su esquilmo, y los árboles de la tierra no darán su fruto.', 'cosmic'),
('Old', 'Levítico', 26, 21, 'Y si anduviereis conmigo en oposición, y no me quisiereis oír, yo añadiré sobre vosotros siete veces más plagas según vuestros pecados.', 'cosmic'),
('Old', 'Levítico', 26, 22, 'Enviaré también contra vosotros bestias fieras que os arrebaten los hijos, y destruyan vuestros animales, y os apoquen, y vuestros caminos sean desiertos.', 'cosmic'),
('Old', 'Levítico', 26, 23, 'Y si con estas cosas no fuereis corregidos, sino que anduviereis conmigo en oposición,', 'cosmic'),
('Old', 'Levítico', 26, 24, 'Yo también procederé con vosotros, en oposición y os heriré aún siete veces por vuestros pecados:', 'cosmic'),
('Old', 'Levítico', 26, 25, 'Y traeré sobre vosotros espada vengadora, en vindicación del pacto; y os recogeréis á vuestras ciudades; mas yo enviaré pestilencia entre vosotros, y seréis entregados en mano del enemigo.', 'cosmic'),
('Old', 'Levítico', 26, 26, 'Cuando yo os quebrantare el arrimo del pan, cocerán diez mujeres vuestro pan en un horno, y os devolverán vuestro pan por peso; y comeréis, y no os hartaréis.', 'cosmic'),
('Old', 'Levítico', 26, 27, 'Y si con esto no me oyereis, mas procediereis conmigo en oposición,', 'cosmic'),
('Old', 'Levítico', 26, 28, 'Yo procederé con vosotros en contra y con ira, y os catigaré aún siete veces por vuestros pecados.', 'cosmic'),
('Old', 'Levítico', 26, 29, 'Y comeréis las carnes de vuestros hijos, y comeréis las carnes de vuestras hijas:', 'cosmic'),
('Old', 'Levítico', 26, 30, 'Y destruiré vuestros altos, y talaré vuestras imágenes, y pondré vuestros cuerpos muertos sobre los cuerpos muertos de vuestros ídolos, y mi alma os abominará:', 'cosmic'),
('Old', 'Levítico', 26, 31, 'Y pondré vuestras ciudades en desierto, y asolaré vuestros santuarios, y no oleré la fragancia de vuestro suave perfume.', 'cosmic'),
('Old', 'Levítico', 26, 32, 'Yo asolaré también la tierra, y se pasmarán de ella vuestros enemigos que en ella moran:', 'cosmic'),
('Old', 'Levítico', 26, 33, 'Y á vosotros os esparciré por las gentes, y desenvainaré espada en pos de vosotros: y vuestra tierra estará asolada, y yermas vuestras ciudades.', 'cosmic'),
('Old', 'Levítico', 26, 34, 'Entonces la tierra holgará sus sábados todos los días que estuviere asolada, y vosotros en la tierra de vuestros enemigos: la tierra descansará entonces y gozará sus sábados.', 'cosmic'),
('Old', 'Levítico', 26, 35, 'Todo el tiempo que estará asolada, holgará lo que no holgó en vuestros sábados mientras habitabais en ella.', 'cosmic'),
('Old', 'Levítico', 26, 36, 'Y á los que quedaren de vosotros infundiré en sus corazones tal cobardía, en la tierra de sus enemigos, que el sonido de una hoja movida los perseguirá, y huirán como de cuchillo, y caerán sin que nadie los persiga:', 'cosmic'),
('Old', 'Levítico', 26, 37, 'Y tropezarán los unos en los otros, como si huyeran delante de cuchillo, aunque nadie los persiga; y no podréis resistir delante de vuestros enemigos.', 'cosmic'),
('Old', 'Levítico', 26, 38, 'Y pereceréis entre las gentes, y la tierra de vuestros enemigos os consumirá.', 'cosmic'),
('Old', 'Levítico', 26, 39, 'Y los que quedaren de vosotros decaerán en las tierras de vuestros enemigos por su iniquidad; y por la iniquidad de sus padres decaerán con ellos:', 'cosmic'),
('Old', 'Levítico', 26, 40, 'Y confesarán su iniquidad, y la iniquidad de sus padres, por su prevaricación con que prevaricaron contra mí: y también porque anduvieron conmigo en oposición,', 'cosmic'),
('Old', 'Levítico', 26, 41, 'Yo también habré andado con ellos en contra, y los habré metido en la tierra de sus enemigos: y entonces se humillará su corazón incircunciso, y reconocerán su pecado;', 'cosmic'),
('Old', 'Levítico', 26, 42, 'Y yo me acordaré de mi pacto con Jacob, y asimismo de mi pacto con Isaac, y también de mi pacto con Abraham me acordaré; y haré memoria de la tierra.', 'cosmic'),
('Old', 'Levítico', 26, 43, 'Que la tierra estará desamparada de ellos, y holgará sus sábados, estando yerma á causa de ellos; mas entretanto se someterán al castigo de sus iniquidades: por cuanto menospreciaron mis derechos, y tuvo el alma de ellos fastidio de mis estatutos.', 'cosmic'),
('Old', 'Levítico', 26, 44, 'Y aun con todo esto, estando ellos en tierra de sus enemigos, yo no los desecharé, ni los abominaré para consumirlos, invalidando mi pacto con ellos: porque yo Jehová soy su Dios:', 'cosmic'),
('Old', 'Levítico', 26, 45, 'Antes me acordaré de ellos por el pacto antiguo, cuando los saqué de la tierra de Egipto á los ojos de las gentes, para ser su Dios: Yo Jehová.', 'cosmic'),
('Old', 'Levítico', 26, 46, 'Estos son los decretos, derechos y leyes que estableció Jehová entre sí y los hijos de Israel en el monte de Sinaí por mano de Moisés.', 'cosmic'),
('Old', 'Levítico', 27, 1, 'Y HABLO Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Levítico', 27, 2, 'Habla á los hijos de Israel, y diles: Cuando alguno hiciere especial voto á Jehová, según la estimación de las personas que se hayan de redimir, así será tu estimación:', 'cosmic'),
('Old', 'Levítico', 27, 3, 'En cuanto al varón de veinte años hasta sesenta, tu estimación será cincuenta siclos de plata, según el siclo del santuario.', 'cosmic'),
('Old', 'Levítico', 27, 4, 'Y si fuere hembra, la estimación será treinta siclos.', 'cosmic'),
('Old', 'Levítico', 27, 5, 'Y si fuere de cinco años hasta veinte, tu estimación será respecto al varón veinte siclos, y á la hembra diez siclos.', 'cosmic'),
('Old', 'Levítico', 27, 6, 'Y si fuere de un mes hasta cinco años, tu estimación será en orden al varón, cinco siclos de plata; y por la hembra será tu estimación tres siclos de plata.', 'cosmic'),
('Old', 'Levítico', 27, 7, 'Mas si fuere de sesenta años arriba, por el varón tu estimación será quince siclos, y por la hembra diez siclos.', 'cosmic'),
('Old', 'Levítico', 27, 8, 'Pero si fuere más pobre que tu estimación, entonces comparecerá ante el sacerdote, y el sacerdote le pondrá tasa: conforme á la facultad del votante le impondrá tasa el sacerdote.', 'cosmic'),
('Old', 'Levítico', 27, 9, 'Y si fuere animal de que se ofrece ofrenda á Jehová, todo lo que se diere de el á Jehová será santo.', 'cosmic'),
('Old', 'Levítico', 27, 10, 'No será mudado ni trocado, bueno por malo, ni malo por bueno; y si se permutare un animal por otro, él y el dado por él en cambio serán sagrados.', 'cosmic'),
('Old', 'Levítico', 27, 11, 'Y si fuere algún animal inmundo, de que no se ofrece ofrenda á Jehová, entonces el animal será puesto delante del sacerdote:', 'cosmic'),
('Old', 'Levítico', 27, 12, 'Y el sacerdote lo apreciará, sea bueno ó sea malo; conforme á la estimación del sacerdote, así será.', 'cosmic'),
('Old', 'Levítico', 27, 13, 'Y si lo hubieren de redimir, añadirán su quinto sobre tu valuación.', 'cosmic'),
('Old', 'Levítico', 27, 14, 'Y cuando alguno santificare su casa consagrándola á Jehová, la apreciará el sacerdote, sea buena ó sea mala: según la apreciare el sacerdote, así quedará.', 'cosmic'),
('Old', 'Levítico', 27, 15, 'Mas si el santificante redimiere su casa, añadirá á tu valuación el quinto del dinero de ella, y será suya.', 'cosmic'),
('Old', 'Levítico', 27, 16, 'Y si alguno santificare de la tierra de su posesión á Jehová, tu estimación será conforme á su sembradura: un omer de sembradura de cebada se apreciará en cincuenta siclos de plata.', 'cosmic'),
('Old', 'Levítico', 27, 17, 'Y si santificare su tierra desde el año del jubileo, conforme á tu estimación quedará.', 'cosmic'),
('Old', 'Levítico', 27, 18, 'Mas si después del jubileo santificare su tierra, entonces el sacerdote hará la cuenta del dinero conforme á los años que quedaren hasta el año del jubileo, y se rebajará de tu estimación.', 'cosmic'),
('Old', 'Levítico', 27, 19, 'Y si el que santificó la tierra quisiere redimirla, añadirá á tu estimación el quinto del dinero de ella, y quedaráse para él.', 'cosmic'),
('Old', 'Levítico', 27, 20, 'Mas si él no redimiere la tierra, y la tierra se vendiere á otro, no la redimirá más;', 'cosmic'),
('Old', 'Levítico', 27, 21, 'Sino que cuando saliere en el jubileo, la tierra será santa á Jehová, como tierra consagrada: la posesión de ella será del sacerdote.', 'cosmic'),
('Old', 'Levítico', 27, 22, 'Y si santificare alguno á Jehová la tierra que él compró, que no era de la tierra de su herencia,', 'cosmic'),
('Old', 'Levítico', 27, 23, 'Entonces el sacerdote calculará con él la suma de tu estimación hasta el año del jubileo, y aquel día dará tu señalado precio, cosa consagrada á Jehová.', 'cosmic'),
('Old', 'Levítico', 27, 24, 'En el año del jubileo, volverá la tierra á aquél de quien él la compró, cuya es la herencia de la tierra.', 'cosmic'),
('Old', 'Levítico', 27, 25, 'Y todo lo que apreciares será conforme al siclo del santuario: el siclo tiene veinte óbolos.', 'cosmic'),
('Old', 'Levítico', 27, 26, 'Empero el primogénito de los animales, que por la primogenitura es de Jehová, nadie lo santificará; sea buey ú oveja, de Jehová es.', 'cosmic'),
('Old', 'Levítico', 27, 27, 'Mas si fuere de los animales inmundos, lo redimirán conforme á tu estimación, y añadirán sobre ella su quinto: y si no lo redimieren, se venderá conforme á tu estimación.', 'cosmic'),
('Old', 'Levítico', 27, 28, 'Pero ninguna cosa consagrada, que alguno hubiere santificado á Jehová de todo lo que tuviere, de hombres y animales, y de las tierras de su posesión, no se venderá, ni se redimirá: todo lo consagrado será cosa santísima á Jehová.', 'cosmic'),
('Old', 'Levítico', 27, 29, 'Cualquier anatema (cosa consagrada) de hombres que se consagrare no será redimido: indefectiblemente ha de ser muerto.', 'cosmic'),
('Old', 'Levítico', 27, 30, 'Y todas las décimas de la tierra, así de la simiente de la tierra como del fruto de los árboles, de Jehová son: es cosa consagrada á Jehová.', 'cosmic'),
('Old', 'Levítico', 27, 31, 'Y si alguno quisiere redimir algo de sus décimas, añadirá su quinto á ello.', 'cosmic'),
('Old', 'Levítico', 27, 32, 'Y toda décima de vacas ó de ovejas, de todo lo que pasa bajo la vara, la décima será consagrada á Jehová.', 'cosmic'),
('Old', 'Levítico', 27, 33, 'No mirará si es bueno ó malo, ni lo trocará: y si lo trocare, ello y su trueque serán cosas sagradas; no se redimirá.', 'cosmic'),
('Old', 'Levítico', 27, 34, 'Estos son los mandamientos que ordenó Jehová á Moisés, para los hijos de Israel, en el monte de Sinaí.', 'cosmic'),
('Old', 'Números', 1, 1, 'Y HABLO Jehová á Moisés en el desierto de Sinaí, en el tabernáculo del testimonio, en el primero del mes segundo, en el segundo año de su salida de la tierra de Egipto, diciendo:', 'cosmic'),
('Old', 'Números', 1, 2, 'Tomad el encabezamiento de toda la congregación de los hijos de Israel por sus familias, por las casas de sus padres, con la cuenta de los nombres, todos los varones por sus cabezas:', 'cosmic'),
('Old', 'Números', 1, 3, 'De veinte años arriba, todos los que pueden salir á la guerra en Israel, los contaréis tú y Aarón por sus cuadrillas.', 'cosmic'),
('Old', 'Números', 1, 4, 'Y estará con vosotros un varón de cada tribu, cada uno cabeza de la casa de sus padres.', 'cosmic'),
('Old', 'Números', 1, 5, 'Y estos son los nombres de los varones que estarán con vosotros: De la tribu de Rubén, Elisur hijo de Sedeur.', 'cosmic'),
('Old', 'Números', 1, 6, 'De Simeón, Selumiel hijo de Zurisaddai.', 'cosmic'),
('Old', 'Números', 1, 7, 'De Judá, Naasón hijo de Aminadab.', 'cosmic'),
('Old', 'Números', 1, 8, 'De Issachâr, Nathanael hijo de Suar.', 'cosmic'),
('Old', 'Números', 1, 9, 'De Zabulón, Eliab hijo de Helón.', 'cosmic'),
('Old', 'Números', 1, 10, 'De los hijos de José: de Ephraim, Elisama hijo de Ammiud; de Manasés, Gamaliel hijo de Pedasur.', 'cosmic'),
('Old', 'Números', 1, 11, 'De Benjamín, Abidán hijo de Gedeón.', 'cosmic'),
('Old', 'Números', 1, 12, 'De Dan, Ahiezer hijo de Ammisaddai.', 'cosmic'),
('Old', 'Números', 1, 13, 'De Aser, Phegiel hijo de Ocrán.', 'cosmic'),
('Old', 'Números', 1, 14, 'De Gad, Eliasaph hijo de Dehuel.', 'cosmic'),
('Old', 'Números', 1, 15, 'De Nephtalí, Ahira hijo de Enán.', 'cosmic'),
('Old', 'Números', 1, 16, 'Estos eran los nombrados de la congregación, príncipes de las tribus de sus padres, capitanes de los millares de Israel.', 'cosmic'),
('Old', 'Números', 1, 17, 'Tomó pues Moisés y Aarón á estos varones que fueron declarados por sus nombres:', 'cosmic'),
('Old', 'Números', 1, 18, 'Y juntaron toda la congregación en el primero del mes segundo, y fueron reunidos sus linajes, por las casas de sus padres, según la cuenta de los nombres, de veinte años arriba, por sus cabezas,', 'cosmic'),
('Old', 'Números', 1, 19, 'Como Jehová lo había mandado á Moisés; y contólos en el desierto de Sinaí.', 'cosmic'),
('Old', 'Números', 1, 20, 'Y los hijos de Rubén, primogénito de Israel, por sus generaciones, por sus familias, por las casas de sus padres, conforme á la cuenta de los nombres por sus cabezas, todos los varones de veinte años arriba, todos los que podían salir á la guerra;', 'cosmic'),
('Old', 'Números', 1, 21, 'Los contados de ellos, de la tribu de Rubén, fueron cuarenta y seis mil y quinientos.', 'cosmic'),
('Old', 'Números', 1, 22, 'De los hijos de Simeón, por sus generaciones, por sus familias, por las casas de sus padres, los contados de ellos conforme á la cuenta de los nombres por sus cabezas, todos los varones de veinte años arriba, todos los que podían salir á la guerra;', 'cosmic'),
('Old', 'Números', 1, 23, 'Los contados de ellos, de la tribu de Simeón, cincuenta y nueve mil y trescientos.', 'cosmic'),
('Old', 'Números', 1, 24, 'De los hijos de Gad, por sus generaciones, por sus familias, por las casas de sus padres, conforme á la cuenta de los nombres, de veinte años arriba, todos los que podían salir á la guerra;', 'cosmic'),
('Old', 'Números', 1, 25, 'Los contados de ellos, de la tribu de Gad, cuarenta y cinco mil seiscientos y cincuenta.', 'cosmic'),
('Old', 'Números', 1, 26, 'De los hijos de Judá, por sus generaciones, por sus familias, por las casas de sus padres, conforme á la cuenta de los nombres, de veinte años arriba, todos los que podían salir á la guerra;', 'cosmic'),
('Old', 'Números', 1, 27, 'Los contados de ellos, de la tribu de Judá, setenta y cuatro mil y seiscientos.', 'cosmic'),
('Old', 'Números', 1, 28, 'De los hijos de Issachâr, por sus generaciones, por sus familias, por las casas de sus padres, conforme á la cuenta de los nombres, de veinte años arriba, todos los que podían salir á la guerra;', 'cosmic'),
('Old', 'Números', 1, 29, 'Los contados de ellos, de la tribu de Issachâr, cincuenta y cuatro mil y cuatrocientos.', 'cosmic'),
('Old', 'Números', 1, 30, 'De los hijos de Zabulón, por sus generaciones, por sus familias, por las casas de sus padres, conforme á la cuenta de sus nombres, de veinte años arriba, todos los que podían salir á la guerra;', 'cosmic'),
('Old', 'Números', 1, 31, 'Los contados de ellos, de la tribu de Zabulón, cincuenta y siete mil y cuatrocientos.', 'cosmic'),
('Old', 'Números', 1, 32, 'De los hijos de José: de los hijos de Ephraim, por sus generaciones, por sus familias, por las casas de sus padres, conforme á la cuenta de los nombres, de veinte años arriba, todos los que podían salir á la guerra;', 'cosmic'),
('Old', 'Números', 1, 33, 'Los contados de ellos, de la tribu de Ephraim, cuarenta mil y quinientos.', 'cosmic'),
('Old', 'Números', 1, 34, 'De los hijos de Manasés, por sus generaciones, por sus familias, por las casas de sus padres, conforme á la cuenta de los nombres, de veinte años arriba, todos los que podían salir á la guerra;', 'cosmic'),
('Old', 'Números', 1, 35, 'Los contados de ellos, de la tribu de Manasés, treinta y dos mil y doscientos.', 'cosmic'),
('Old', 'Números', 1, 36, 'De los hijos de Benjamín, por sus generaciones, por sus familias, por las casas de sus padres, conforme á la cuenta de los nombres, de veinte años arriba, todos los que podían salir á la guerra;', 'cosmic'),
('Old', 'Números', 1, 37, 'Los contados de ellos, de la tribu de Benjamín, treinta y cinco mil y cuatrocientos.', 'cosmic'),
('Old', 'Números', 1, 38, 'De los hijos de Dan, por sus generaciones, por sus familias, por las casas de sus padres, conforme á la cuenta de los nombres, de veinte años arriba, todos los que podían salir á la guerra;', 'cosmic'),
('Old', 'Números', 1, 39, 'Los contados de ellos, de la tribu de Dan, sesenta y dos mil y setecientos.', 'cosmic'),
('Old', 'Números', 1, 40, 'De los hijos de Aser, por sus generaciones, por sus familias, por las casas de sus padres, conforme á la cuenta de los nombres, de veinte años arriba, todos los que podían salir á la guerra.', 'cosmic'),
('Old', 'Números', 1, 41, 'Los contados de ellos, de la tribu de Aser, cuarenta y un mil y quinientos.', 'cosmic'),
('Old', 'Números', 1, 42, 'De los hijos de Nephtalí, por sus generaciones, por sus familias, por las casas de sus padres, conforme á la cuenta de los nombres, de veinte años arriba, todos los que podían salir á la guerra;', 'cosmic'),
('Old', 'Números', 1, 43, 'Los contados de ellos, de la tribu de Nephtalí, cincuenta y tres mil y cuatrocientos.', 'cosmic'),
('Old', 'Números', 1, 44, 'Estos fueron los contados, los cuales contaron Moisés y Aarón, con los príncipes de Israel, que eran doce, uno por cada casa de sus padres.', 'cosmic'),
('Old', 'Números', 1, 45, 'Y fueron todos los contados de los hijos de Israel por las casas de sus padres, de veinte años arriba, todos los que podían salir á la guerra en Israel;', 'cosmic'),
('Old', 'Números', 1, 46, 'Fueron todos los contados seiscientos tres mil quinientos y cincuenta.', 'cosmic'),
('Old', 'Números', 1, 47, 'Pero los Levitas no fueron contados entre ellos según la tribu de sus padres.', 'cosmic'),
('Old', 'Números', 1, 48, 'Porque habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 1, 49, 'Solamente no contarás la tribu de Leví, ni tomarás la cuenta de ellos entre los hijos de Israel:', 'cosmic'),
('Old', 'Números', 1, 50, 'Mas tú pondrás á los Levitas en el tabernáculo del testimonio, y sobre todos sus vasos, y sobre todas las cosas que le pertenecen: ellos llevarán el tabernáculo y todos sus vasos, y ellos servirán en él, y asentarán sus tiendas alrededor del tabernáculo.', 'cosmic'),
('Old', 'Números', 1, 51, 'Y cuando el tabernáculo partiere, los Levitas lo desarmarán; y cuando el tabernáculo parare, los Levitas lo armarán: y el extraño que se llegare, morirá.', 'cosmic'),
('Old', 'Números', 1, 52, 'Y los hijos de Israel asentarán sus tiendas cada uno en su escuadrón, y cada uno junto á su bandera, por sus cuadrillas;', 'cosmic'),
('Old', 'Números', 1, 53, 'Mas los Levitas asentarán las suyas alrededor del tabernáculo del testimonio, y no habrá ira sobre la congregacíon de los hijos de Israel: y los Levitas tendrán la guarda del tabernáculo del testimonio.', 'cosmic'),
('Old', 'Números', 1, 54, 'E hicieron los hijos de Israel conforme á todas las cosas que mandó Jehová á Moisés; así lo hicieron.', 'cosmic'),
('Old', 'Números', 2, 1, 'Y HABLO Jehová á Moisés y á Aarón, diciendo:', 'cosmic'),
('Old', 'Números', 2, 2, 'Los hijos de Israel acamparán cada uno junto á su bandera, según las enseñas de las casas de sus padres; alrededor del tabernáculo del testimonio acamparán.', 'cosmic'),
('Old', 'Números', 2, 3, 'Estos acamparán al levante, al oriente: la bandera del ejército de Judá, por sus escuadrones; y el jefe de los hijos de Judá, Naasón hijo de Aminadab:', 'cosmic'),
('Old', 'Números', 2, 4, 'Su hueste, con los contados de ellos, setenta y cuatro mil y seiscientos.', 'cosmic'),
('Old', 'Números', 2, 5, 'Junto á él acamparán los de la tribu de Issachâr: y el jefe de los hijos de Issachâr, Nathanael hijo de Suar;', 'cosmic'),
('Old', 'Números', 2, 6, 'Y su hueste, con sus contados, cincuenta y cuatro mil y cuatrocientos:', 'cosmic'),
('Old', 'Números', 2, 7, 'Y la tribu de Zabulón: y el jefe de los hijos de Zabulón, Eliab hijo de Helón;', 'cosmic'),
('Old', 'Números', 2, 8, 'Y su hueste, con sus contados, cincuenta y siete mil y cuatrocientos.', 'cosmic'),
('Old', 'Números', 2, 9, 'Todos los contados en el ejército de Judá, ciento ochenta y seis mil y cuatrocientos, por sus escuadrones, irán delante.', 'cosmic'),
('Old', 'Números', 2, 10, 'La bandera del ejército de Rubén al mediodía, por sus escuadrones: y el jefe de los hijos de Rubén, Elisur hijo de Sedeur;', 'cosmic'),
('Old', 'Números', 2, 11, 'Y su hueste, sus contados, cuarenta y seis mil y quinientos.', 'cosmic'),
('Old', 'Números', 2, 12, 'Y acamparán junto á él los de la tribu de Simeón: y el jefe de los hijos de Simeón, Selumiel hijo de Zurisaddai;', 'cosmic'),
('Old', 'Números', 2, 13, 'Y su hueste, con los contados de ellos, cincuenta y nueve mil y trescientos:', 'cosmic'),
('Old', 'Números', 2, 14, 'Y la tribu de Gad: y el jefe de los hijos de Gad, Eliasaph hijo de Rehuel;', 'cosmic'),
('Old', 'Números', 2, 15, 'Y su hueste, con los contados de ellos, cuarenta y cinco mil seiscientos y cincuenta.', 'cosmic'),
('Old', 'Números', 2, 16, 'Todos los contados en el ejército de Rubén, ciento cincuenta y un mil cuatrocientos y cincuenta, por sus escuadrones, irán los segundos.', 'cosmic'),
('Old', 'Números', 2, 17, 'Luego irá el tabernáculo del testimonio, el campo de los Levitas en medio de los ejércitos: de la manera que asientan el campo, así caminarán, cada uno en su lugar, junto á sus banderas.', 'cosmic'),
('Old', 'Números', 2, 18, 'La bandera del ejército de Ephraim por sus escuadrones, al occidente: y el jefe de los hijos de Ephraim, Elisama hijo de Ammiud;', 'cosmic'),
('Old', 'Números', 2, 19, 'Y su hueste, con los contados de ellos, cuarenta mil y quinientos.', 'cosmic'),
('Old', 'Números', 2, 20, 'Junto á él estará la tribu de Manasés; y el jefe de los hijos de Manasés, Gamaliel hijo de Pedasur;', 'cosmic'),
('Old', 'Números', 2, 21, 'Y su hueste, con los contados de ellos, treinta y dos mil y doscientos:', 'cosmic'),
('Old', 'Números', 2, 22, 'Y la tribu de Benjamín: y el jefe de los hijos de Benjamín, Abidán hijo de Gedeón;', 'cosmic'),
('Old', 'Números', 2, 23, 'Y su hueste, con los contados de ellos, treinta y cinco mil y cuatrocientos.', 'cosmic'),
('Old', 'Números', 2, 24, 'Todos los contados en el ejército de Ephraim, ciento ocho mil y ciento, por sus escuadrones, irán los terceros.', 'cosmic'),
('Old', 'Números', 2, 25, 'La bandera del ejército de Dan estará al aquilón, por sus escuadrones: y el jefe de los hijos de Dan, Ahiezer hijo de Amisaddai;', 'cosmic'),
('Old', 'Números', 2, 26, 'Y su hueste, con los contados de ellos, sesenta y dos mil y setecientos.', 'cosmic'),
('Old', 'Números', 2, 27, 'Junto á él acamparán los de la tribu de Aser: y el jefe de los hijos de Aser, Phegiel hijo de Ocrán;', 'cosmic'),
('Old', 'Números', 2, 28, 'Y su hueste, con los contados de ellos, cuarenta y un mil y quinientos:', 'cosmic'),
('Old', 'Números', 2, 29, 'Y la tribu de Nephtalí: y el jefe de los hijos de Nephtalí, Ahira hijo de Enán;', 'cosmic'),
('Old', 'Números', 2, 30, 'Y su hueste, con los contados de ellos, cincuenta y tres mil y cuatrocientos.', 'cosmic'),
('Old', 'Números', 2, 31, 'Todos los contados en el ejército de Dan, ciento cincuenta y siete mil y seiscientos: irán los postreros tras sus banderas.', 'cosmic'),
('Old', 'Números', 2, 32, 'Estos son los contados de los hijos de Israel, por las casas de sus padres: todos los contados por ejércitos, por sus escuadrones, seiscientos tres mil quinientos y cincuenta.', 'cosmic'),
('Old', 'Números', 2, 33, 'Mas los Levitas no fueron contados entre los hijos de Israel; como Jehová lo mandó á Moisés.', 'cosmic'),
('Old', 'Números', 2, 34, 'E hicieron los hijos de Israel conforme á todas las cosas que Jehová mandó á Moisés; así asentaron el campo por sus banderas, y así marcharon cada uno por sus familias, según las casas de sus padres.', 'cosmic'),
('Old', 'Números', 3, 1, 'Y ESTAS son las generaciones de Aarón y de Moisés, desde que Jehová habló á Moisés en el monte de Sinaí.', 'cosmic'),
('Old', 'Números', 3, 2, 'Y estos son los nombres de los hijos de Aarón: Nadab el primogénito, y Abiú, Eleazar, é Ithamar.', 'cosmic'),
('Old', 'Números', 3, 3, 'Estos son los nombres de los hijos de Aarón, sacerdotes ungidos; cuyas manos él hinchió para administrar el sacerdocio.', 'cosmic'),
('Old', 'Números', 3, 4, 'Mas Nadab y Abiú murieron delante de Jehová, cuando ofrecieron fuego extraño delante de Jehová, en el desierto de Sinaí: y no tuvieron hijos: y Eleazar é Ithamar ejercieron el sacerdocio delante de Aarón su padre.', 'cosmic'),
('Old', 'Números', 3, 5, 'Y Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 3, 6, 'Haz llegar á la tribu de Leví, y hazla estar delante del sacerdote Aarón, para que le ministren;', 'cosmic'),
('Old', 'Números', 3, 7, 'Y desempeñen su cargo, y el cargo de toda la congregación delante del tabernáculo del testimonio, para servir en el ministerio del tabernáculo;', 'cosmic'),
('Old', 'Números', 3, 8, 'Y guarden todas las alhajas del tabernáculo del testimonio, y lo encargado á ellos de los hijos de Israel, y ministren en el servicio del tabernáculo.', 'cosmic'),
('Old', 'Números', 3, 9, 'Y darás los Levitas á Aarón y á sus hijos: le son enteramente dados de entre los hijos de Israel.', 'cosmic'),
('Old', 'Números', 3, 10, 'Y constituirás á Aarón y á sus hijos, para que ejerzan su sacerdocio: y el extraño que se llegare, morirá.', 'cosmic'),
('Old', 'Números', 3, 11, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 3, 12, 'Y he aquí yo he tomado los Levitas de entre los hijos de Israel en lugar de todos los primogénitos que abren la matriz entre los hijos de Israel; serán pues míos los Levitas:', 'cosmic'),
('Old', 'Números', 3, 13, 'Porque mío es todo primogénito; desde el día que yo maté todos los primogénitos en la tierra de Egipto, yo santifiqué á mí todos los primogénitos en Israel, así de hombres como de animales: míos serán: Yo Jehová.', 'cosmic'),
('Old', 'Números', 3, 14, 'Y Jehová habló á Moisés en el desierto de Sinaí, diciendo:', 'cosmic'),
('Old', 'Números', 3, 15, 'Cuenta los hijos de Leví por las casas de sus padres, por sus familias: contarás todos los varones de un mes arriba.', 'cosmic'),
('Old', 'Números', 3, 16, 'Y Moisés los contó conforme á la palabra de Jehová, como le fué mandado.', 'cosmic'),
('Old', 'Números', 3, 17, 'Y los hijos de Leví fueron estos por sus nombres: Gersón, y Coath, y Merari.', 'cosmic'),
('Old', 'Números', 3, 18, 'Y los nombres de los hijos de Gersón, por sus familias, estos: Libni, y Simei.', 'cosmic'),
('Old', 'Números', 3, 19, 'Y los hijos de Coath, por sus familias: Amram, é Izhar, y Hebrón, y Uzziel.', 'cosmic'),
('Old', 'Números', 3, 20, 'Y los hijos de Merari, por sus familias: Mahali, y Musi. Estas, las familias de Leví, por las casas de sus padres.', 'cosmic'),
('Old', 'Números', 3, 21, 'De Gersón, la familia de Libni y la de Simei: estas son las familias de Gersón.', 'cosmic'),
('Old', 'Números', 3, 22, 'Los contados de ellos conforme á la cuenta de todos los varones de un mes arriba, los contados de ellos, siete mil y quinientos.', 'cosmic'),
('Old', 'Números', 3, 23, 'Las familias de Gersón asentarán sus tiendas á espaldas del tabernáculo, al occidente;', 'cosmic'),
('Old', 'Números', 3, 24, 'Y el jefe de la casa del padre de los Gersonitas, Eliasaph hijo de Lael.', 'cosmic'),
('Old', 'Números', 3, 25, 'A cargo de los hijos de Gersón, en el tabernáculo del testimonio, estará el tabernáculo, y la tienda, y su cubierta, y el pabellón de la puerta del tabernáculo del testimonio,', 'cosmic'),
('Old', 'Números', 3, 26, 'Y las cortinas del atrio, y el pabellón de la puerta del atrio, que está junto al tabernáculo y junto al altar alrededor; asimismo sus cuerdas para todo su servicio.', 'cosmic'),
('Old', 'Números', 3, 27, 'Y de Coath, la familia Amramítica, y la familia Izeharítica, y la familia Hebronítica, y la familia Ozielítica: estas son las familias Coathitas.', 'cosmic'),
('Old', 'Números', 3, 28, 'Por la cuenta de todos los varones de un mes arriba, eran ocho mil y seiscientos, que tenían la guarda del santuario.', 'cosmic'),
('Old', 'Números', 3, 29, 'Las familias de los hijos de Coath acamparán al lado del tabernáculo, al mediodía;', 'cosmic'),
('Old', 'Números', 3, 30, 'Y el jefe de la casa del padre de las familias de Coath, Elisaphán hijo de Uzziel.', 'cosmic'),
('Old', 'Números', 3, 31, 'Y á cargo de ellos estará el arca, y la mesa, y el candelero, y los altares, y los vasos del santuario con que ministran, y el velo, con todo su servicio.', 'cosmic'),
('Old', 'Números', 3, 32, 'Y el principal de los jefes de los Levitas será Eleazar, hijo de Aarón el sacerdote, prepósito de los que tienen la guarda del santuario.', 'cosmic'),
('Old', 'Números', 3, 33, 'De Merari, la familia Mahalítica y la familia Musítica: estas son las familias de Merari.', 'cosmic'),
('Old', 'Números', 3, 34, 'Y los contados de ellos conforme á la cuenta de todos los varones de un mes arriba, fueron seis mil y doscientos.', 'cosmic'),
('Old', 'Números', 3, 35, 'Y el jefe de la casa del padre de las familias de Merari, Suriel hijo de Abihail: acamparán al lado del tabernáculo, al aquilón.', 'cosmic'),
('Old', 'Números', 3, 36, 'Y á cargo de los hijos de Merari estará la custodia de las tablas del tabernáculo, y sus barras, y sus columnas, y sus basas, y todos sus enseres, con todo su servicio:', 'cosmic'),
('Old', 'Números', 3, 37, 'Y las columnas en derredor del atrio, y sus basas, y sus estacas, y sus cuerdas.', 'cosmic'),
('Old', 'Números', 3, 38, 'Y los que acamparán delante del tabernáculo al oriente, delante del tabernáculo del testimonio al levante, serán Moisés, y Aarón y sus hijos, teniendo la guarda del santuario en lugar de los hijos de Israel: y el extraño que se acercare, morirá.', 'cosmic'),
('Old', 'Números', 3, 39, 'Todos los contados de los Levitas, que Moisés y Aarón conforme á la palabra de Jehová contaron por sus familias, todos los varones de un mes arriba, fueron veinte y dos mil.', 'cosmic'),
('Old', 'Números', 3, 40, 'Y Jehová dijo á Moisés: Cuenta todos los primogénitos varones de los hijos de Israel de un mes arriba, y toma la cuenta de los nombres de ellos.', 'cosmic'),
('Old', 'Números', 3, 41, 'Y tomarás los Levitas para mí, yo Jehová, en lugar de todos los primogénitos de los hijos de Israel: y los animales de los Levitas en lugar de todos los primogénitos de los animales de los hijos de Israel.', 'cosmic'),
('Old', 'Números', 3, 42, 'Y contó Moisés, como Jehová le mandó, todos los primogénitos de los hijos de Israel.', 'cosmic'),
('Old', 'Números', 3, 43, 'Y todos los primogénitos varones, conforme á la cuenta de los nombres, de un mes arriba, los contados de ellos fueron veinte y dos mil doscientos setenta y tres.', 'cosmic'),
('Old', 'Números', 3, 44, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 3, 45, 'Toma los Levitas en lugar de todos los primogénitos de los hijos de Israel, y los animales de los Levitas en lugar de sus animales; y los Levitas serán míos: Yo Jehová.', 'cosmic'),
('Old', 'Números', 3, 46, 'Y por los rescates de los doscientos y setenta y tres, que sobrepujan á los Levitas los primogénitos de los hijos de Israel;', 'cosmic'),
('Old', 'Números', 3, 47, 'Tomarás cinco siclos por cabeza; conforme al siclo del santuario tomarás: el siclo tiene veinte óbolos:', 'cosmic'),
('Old', 'Números', 3, 48, 'Y darás á Aarón y á sus hijos el dinero por los rescates de los que de ellos sobran.', 'cosmic'),
('Old', 'Números', 3, 49, 'Tomó, pues, Moisés el dinero del rescate de los que resultaron de más de los redimidos por los Levitas:', 'cosmic'),
('Old', 'Números', 3, 50, 'Y recibió de los primogénitos de los hijos de Israel en dinero, mil trescientos sesenta y cinco siclos, conforme al siclo del santuario.', 'cosmic'),
('Old', 'Números', 3, 51, 'Y Moisés dió el dinero de los rescates á Aarón y á sus hijos, conforme al dicho de Jehová, según que Jehová había mandado á Moisés.', 'cosmic'),
('Old', 'Números', 4, 1, 'Y HABLO Jehová á Moisés y á Aarón, diciendo:', 'cosmic'),
('Old', 'Números', 4, 2, 'Toma la cuenta de los hijos de Coath de entre los hijos de Leví, por sus familias, por las casas de sus padres,', 'cosmic'),
('Old', 'Números', 4, 3, 'De edad de treinta años arriba hasta cincuenta años, todos los que entran en compañía, para hacer servicio en el tabernáculo del testimonio.', 'cosmic'),
('Old', 'Números', 4, 4, 'Este será el oficio de los hijos de Coath en el tabernáculo del testimonio, en el lugar santísimo:', 'cosmic'),
('Old', 'Números', 4, 5, 'Cuando se hubiere de mudar el campo, vendrán Aarón y sus hijos, y desarmarán el velo de la tienda, y cubrirán con él el arca del testimonio:', 'cosmic'),
('Old', 'Números', 4, 6, 'Y pondrán sobre ella la cubierta de pieles de tejones, y extenderán encima el paño todo de cárdeno, y le pondrán sus varas.', 'cosmic'),
('Old', 'Números', 4, 7, 'Y sobre la mesa de la proposición extenderán el paño cárdeno, y pondrán sobre ella las escudillas, y las cucharas, y las copas, y los tazones para libar: y el pan continuo estará sobre ella.', 'cosmic'),
('Old', 'Números', 4, 8, 'Y extenderán sobre ella el paño de carmesí colorado, y lo cubrirán con la cubierta de pieles de tejones; y le pondrán sus varas.', 'cosmic'),
('Old', 'Números', 4, 9, 'Y tomarán un paño cárdeno, y cubrirán el candelero de la luminaria; y sus candilejas, y sus despabiladeras, y sus platillos, y todos sus vasos del aceite con que se sirve;', 'cosmic'),
('Old', 'Números', 4, 10, 'Y lo pondrán con todos sus vasos en una cubierta de pieles de tejones, y lo colocarán sobre unas parihuelas.', 'cosmic'),
('Old', 'Números', 4, 11, 'Y sobre el altar de oro extenderán el paño cárdeno, y le cubrirán con la cubierta de pieles de tejones, y le pondrán sus varales.', 'cosmic'),
('Old', 'Números', 4, 12, 'Y tomarán todos los vasos del servicio, de que hacen uso en el santuario, y los pondrán en un paño cárdeno, y los cubrirán con una cubierta de pieles de tejones, y los colocarán sobre unas parihuelas.', 'cosmic'),
('Old', 'Números', 4, 13, 'Y quitarán la ceniza del altar, y extenderán sobre él un paño de púrpura:', 'cosmic'),
('Old', 'Números', 4, 14, 'Y pondrán sobre él todos sus instrumentos de que se sirve: las paletas, los garfios, los braseros, y los tazones, todos los vasos del altar; y extenderán sobre él la cubierta de pieles de tejones, y le pondrán además las varas.', 'cosmic'),
('Old', 'Números', 4, 15, 'Y en acabando Aarón y sus hijos de cubrir el santuario y todos los vasos del santuario, cuando el campo se hubiere de mudar, vendrán después de ello los hijos de Coath para conducir: mas no tocarán cosa santa, que morirán. Estas serán las cargas de los hijos de Coath en el tabernáculo del testimonio.', 'cosmic'),
('Old', 'Números', 4, 16, 'Empero al cargo de Eleazar, hijo de Aarón el sacerdote, estará el aceite de la luminaria, y el perfume aromático, y el presente continuo, y el aceite de la unción; el cargo de todo el tabernáculo, y de todo lo que está en él, en el santuario, y en sus vasos.', 'cosmic'),
('Old', 'Números', 4, 17, 'Y habló Jehová á Moisés y á Aarón, diciendo:', 'cosmic'),
('Old', 'Números', 4, 18, 'No cortaréis la tribu de las familias de Coath de entre los Levitas;', 'cosmic'),
('Old', 'Números', 4, 19, 'Mas esto haréis con ellos, para que vivan, y no mueran cuando llegaren al lugar santísimo: Aarón y sus hijos vendrán y los pondrán á cada uno en su oficio, y en su cargo.', 'cosmic'),
('Old', 'Números', 4, 20, 'No entrarán para ver, cuando cubrieren las cosas santas; que morirán.', 'cosmic'),
('Old', 'Números', 4, 21, 'Y habló Jehová á Moisés diciendo:', 'cosmic'),
('Old', 'Números', 4, 22, 'Toma también la cuenta de los hijos de Gersón por las casas de sus padres, por sus familias.', 'cosmic'),
('Old', 'Números', 4, 23, 'De edad de treinta años arriba hasta cincuenta años los contarás; todos los que entran en compañía, para hacer servicio en el tabrenáculo del testimonio.', 'cosmic'),
('Old', 'Números', 4, 24, 'Este será el oficio de las familias de Gersón, para ministrar y para llevar:', 'cosmic'),
('Old', 'Números', 4, 25, 'Llevarán las cortinas del tabernáculo, y el tabernáculo del testimonio, su cubierta, y la cubierta de pieles de tejones que está sobre él encima, y el pabellón de la puerta del tabernáculo del testimonio,', 'cosmic'),
('Old', 'Números', 4, 26, 'Y las cortinas del atrio, y el pabellón de la puerta del atrio, que está cerca del tabernáculo y cerca del altar alrededor, y sus cuerdas, y todos los instrumentos de su servicio, y todo lo que será hecho para ellos: así servirán.', 'cosmic'),
('Old', 'Números', 4, 27, 'Según la orden de Aarón y de sus hijos será todo el ministerio de los hijos de Gersón en todos sus cargos, y en todo su servicio: y les encomendaréis en guarda todos sus cargos.', 'cosmic'),
('Old', 'Números', 4, 28, 'Este es el servicio de las familias de los hijos de Gersón en el tabernáculo del testimonio: y el cargo de ellos estará bajo la mano de Ithamar, hijo de Aarón el sacerdote.', 'cosmic'),
('Old', 'Números', 4, 29, 'Contarás los hijos de Merari por sus familias, por las casas de sus padres.', 'cosmic'),
('Old', 'Números', 4, 30, 'Desde el de edad de treinta años arriba hasta el de cincuenta años, los contarás; todos los que entran en compañía, para hacer servicio en el tabernáculo del testimonio.', 'cosmic'),
('Old', 'Números', 4, 31, 'Y este será el deber de su cargo para todo su servicio en el tabernáculo del testimonio: las tablas del tabernáculo, y sus barras, y sus columnas, y sus basas,', 'cosmic'),
('Old', 'Números', 4, 32, 'Y las columnas del atrio alrededor, y sus basas, y sus estacas, y sus cuerdas con todos sus instrumentos, y todo su servicio; y contaréis por sus nombres todos los vasos de la guarda de su cargo.', 'cosmic'),
('Old', 'Números', 4, 33, 'Este será el servicio de las familias de los hijos de Merari para todo su ministerio en el tabernáculo del testimonio, bajo la mano de Ithamar, hijo de Aarón el sacerdote.', 'cosmic'),
('Old', 'Números', 4, 34, 'Moisés, pues, y Aarón, y los jefes de la congregación, contaron los hijos de Coath por sus familias, y por las casas de sus padres,', 'cosmic'),
('Old', 'Números', 4, 35, 'Desde el de edad de treinta años arriba hasta el de edad de cincuenta años; todos los que entran en compañía, para ministrar en el tabernáculo del testimonio.', 'cosmic'),
('Old', 'Números', 4, 36, 'Y fueron los contados de ellos por sus familias, dos mil setecientos y cincuenta.', 'cosmic'),
('Old', 'Números', 4, 37, 'Estos fueron los contados de las familias de Coath, todos los que ministran en el tabernáculo del testimonio, los cuales contaron Moisés y Aarón, como lo mandó Jehová por mano de Moisés.', 'cosmic'),
('Old', 'Números', 4, 38, 'Y los contados de los hijos de Gersón, por sus familias, y por las casas de sus padres,', 'cosmic'),
('Old', 'Números', 4, 39, 'Desde el de edad de treinta años arriba hasta el de edad de cincuenta años, todos los que entran en compañía, para ministrar en el tabernáculo del testimonio;', 'cosmic'),
('Old', 'Números', 4, 40, 'Los contados de ellos por sus familias, por las casas de sus padres, fueron dos mil seiscientos y treinta.', 'cosmic'),
('Old', 'Números', 4, 41, 'Estos son los contados de las familias de los hijos de Gersón, todos los que ministran en el tabernáculo del testimonio, los cuales contaron Moisés y Aarón por mandato de Jehová.', 'cosmic'),
('Old', 'Números', 4, 42, 'Y los contados de las familias de los hijos de Merari, por sus familias, por las casas de sus padres,', 'cosmic'),
('Old', 'Números', 4, 43, 'Desde el de edad de treinta años arriba hasta el de edad de cincuenta años, todos los que entran en compañía, para ministrar en el tabernáculo del testimonio;', 'cosmic'),
('Old', 'Números', 4, 44, 'Los contados de ellos, por sus familias, fueron tres mil y doscientos.', 'cosmic'),
('Old', 'Números', 4, 45, 'Estos fueron los contados de las familias de los hijos de Merari, los cuales contaron Moisés y Aarón, según lo mandó Jehová por mano de Moisés.', 'cosmic'),
('Old', 'Números', 4, 46, 'Todos los contados de los Levitas, que Moisés y Aarón y los jefes de Israel contaron por sus familias, y por las casas de sus padres,', 'cosmic'),
('Old', 'Números', 4, 47, 'Desde el de edad de treinta años arriba hasta el de edad de cincuenta años, todos los que entraban para ministrar en el servicio, y tener cargo de obra en el tabernáculo del testimonio;', 'cosmic'),
('Old', 'Números', 4, 48, 'Los contados de ellos fueron ocho mil quinientos y ochenta,', 'cosmic'),
('Old', 'Números', 4, 49, 'Como lo mandó Jehová por mano de Moisés fueron contados, cada uno según su oficio, y según su cargo; los cuales contó él, como le fué mandado.', 'cosmic'),
('Old', 'Números', 5, 1, 'Y JEHOVA habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 5, 2, 'Manda á los hijos de Israel que echen del campo á todo leproso, y á todos los que padecen flujo de semen, y á todo contaminado sobre muerto:', 'cosmic'),
('Old', 'Números', 5, 3, 'Así hombres como mujeres echaréis, fuera del campo los echaréis; porque no contaminen el campo de aquellos entre los cuales yo habito.', 'cosmic'),
('Old', 'Números', 5, 4, 'E hiciéronlo así los hijos de Israel, que los echaron fuera del campo: como Jehová dijo á Moisés, así lo hicieron los hijos de Israel.', 'cosmic'),
('Old', 'Números', 5, 5, 'Además habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 5, 6, 'Habla á los hijos de Israel: El hombre ó la mujer que cometiere alguno de todos los pecados de los hombres, haciendo prevaricación contra Jehová, y delinquiere aquella persona;', 'cosmic'),
('Old', 'Números', 5, 7, 'Confesarán su pecado que cometieron, y compensarán su ofensa enteramente, y añadirán su quinto sobre ello, y lo darán á aquel contra quien pecaron.', 'cosmic'),
('Old', 'Números', 5, 8, 'Y si aquel hombre no tuviere pariente al cual sea resarcida la ofensa, daráse la indemnización del agravio á Jehová, al sacerdote, á más del carnero de las expiaciones, con el cual hará expiación por él.', 'cosmic'),
('Old', 'Números', 5, 9, 'Y toda ofrenda de todas las cosas santas que los hijos de Israel presentaren al sacerdote, suya será.', 'cosmic'),
('Old', 'Números', 5, 10, 'Y lo santificado de cualquiera será suyo: asimismo lo que cualquiera diere al sacerdote, suyo será.', 'cosmic'),
('Old', 'Números', 5, 11, 'Y Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 5, 12, 'Habla á los hijos de Israel, y diles: Cuando la mujer de alguno se desmandare, é hiciere traición contra él,', 'cosmic'),
('Old', 'Números', 5, 13, 'Que alguno se hubiere echado con ella en carnal ayuntamiento, y su marido no lo hubiese visto por haberse ella contaminado ocultamente, ni hubiere testigo contra ella, ni ella hubiere sido cogida en el acto;', 'cosmic'),
('Old', 'Números', 5, 14, 'Si viniere sobre él espíritu de celo, y tuviere celos de su mujer, habiéndose ella contaminado; ó viniere sobre él espíritu de celo, y tuviere celos de su mujer, no habiéndose ella contaminado;', 'cosmic'),
('Old', 'Números', 5, 15, 'Entonces el marido traerá su mujer al sacerdote, y traerá su ofrenda con ella, la décima de un epha de harina de cebada; no echará sobre ella aceite, ni pondrá sobre ella incienso: porque es presente de celos, presente de recordación, que trae en memoria pecado.', 'cosmic'),
('Old', 'Números', 5, 16, 'Y el sacerdote la hará acercar, y la hará poner delante de Jehová.', 'cosmic'),
('Old', 'Números', 5, 17, 'Luego tomará el sacerdote del agua santa en un vaso de barro: tomará también el sacerdote del polvo que hubiere en el suelo del tabernáculo, y echarálo en el agua.', 'cosmic'),
('Old', 'Números', 5, 18, 'Y hará el sacerdote estar en pie á la mujer delante de Jehová, y descubrirá la cabeza de la mujer, y pondrá sobre sus manos el presente de la recordación, que es el presente de celos: y el sacerdote tendrá en la mano las aguas amargas que acarrean maldición.', 'cosmic'),
('Old', 'Números', 5, 19, 'Y el sacerdote la conjurará, y le dirá: Si ninguno hubiere dormido contigo, y si no te has apartado de tu marido á inmundicia, libre seas de estas aguas amargas que traen maldición:', 'cosmic'),
('Old', 'Números', 5, 20, 'Mas si te has descarriado de tu marido, y te has amancillado, y alguno hubiere tenido coito contigo, fuera de tu marido:', 'cosmic'),
('Old', 'Números', 5, 21, '(El sacerdote conjurará á la mujer con juramento de maldición, y dirá á la mujer): Jehová te dé en maldición y en conjuración en medio de tu pueblo, haciendo Jehová á tu muslo que caiga, y á tu vientre que se te hinche;', 'cosmic'),
('Old', 'Números', 5, 22, 'Y estas aguas que dan maldición entren en tus entrañas, y hagan hinchar tu vientre, y caer tu muslo. Y la mujer dirá: Amén, amén.', 'cosmic'),
('Old', 'Números', 5, 23, 'Y el sacerdote escribirá estas maldiciones en un libro, y las borrará con las aguas amargas:', 'cosmic'),
('Old', 'Números', 5, 24, 'Y dará á beber á la mujer las aguas amargas que traen maldición; y las aguas que obran maldición entrarán en ella por amargas.', 'cosmic'),
('Old', 'Números', 5, 25, 'Después tomará el sacerdote de la mano de la mujer el presente de los celos, y mecerálo delante de Jehová, y lo ofrecerá delante del altar:', 'cosmic'),
('Old', 'Números', 5, 26, 'Y tomará el sacerdote un puñado del presente, en memoria de ella, y lo quemará sobre el altar, y después dará á beber las aguas á la mujer.', 'cosmic'),
('Old', 'Números', 5, 27, 'Darále pues á beber las aguas; y será, que si fuere inmunda y hubiere hecho traición contra su marido, las aguas que obran maldición entrarán en ella en amargura, y su vientre se hinchará, y caerá su muslo; y la mujer será por maldición en medio de su pueblo.', 'cosmic'),
('Old', 'Números', 5, 28, 'Mas si la mujer no fuere inmunda, sino que estuviere limpia, ella será libre, y será fecunda.', 'cosmic'),
('Old', 'Números', 5, 29, 'Esta es la ley de los celos, cuando la mujer hiciere traición á su marido, y se amancillare;', 'cosmic'),
('Old', 'Números', 5, 30, 'O del marido, sobre el cual pasare espíritu de celo, y tuviere celos de su mujer: presentarála entonces delante de Jehová, y el sacerdote ejecutará en ella toda esta ley.', 'cosmic'),
('Old', 'Números', 5, 31, 'Y aquel varón será libre de iniquidad, y la mujer llevará su pecado.', 'cosmic'),
('Old', 'Números', 6, 1, 'Y HABLO Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 6, 2, 'Habla á los hijos de Israel, y diles: El hombre, ó la mujer, cuando se apartare haciendo voto de Nazareo, para dedicarse á Jehová,', 'cosmic'),
('Old', 'Números', 6, 3, 'Se abstendrá de vino y de sidra; vinagre de vino, ni vinagre de sidra no beberá, ni beberá algún licor de uvas, ni tampoco comerá uvas frescas ni secas.', 'cosmic'),
('Old', 'Números', 6, 4, 'Todo el tiempo de su nazareato, de todo lo que se hace de vid de vino, desde los granillos hasta el hollejo, no comerá.', 'cosmic'),
('Old', 'Números', 6, 5, 'Todo el tiempo del voto de su nazareato no pasará navaja sobre su cabeza, hasta que sean cumplidos los días de su apartamiento á Jehová: santo será; dejará crecer las guedejas del cabello de su cabeza.', 'cosmic'),
('Old', 'Números', 6, 6, 'Todo el tiempo que se apartaré á Jehová, no entrará á persona muerta.', 'cosmic'),
('Old', 'Números', 6, 7, 'Por su padre, ni por su madre, por su hermano, ni por su hermana, no se contaminará con ellos cuando murieren; porque consagración de su Dios tiene sobre su cabeza.', 'cosmic'),
('Old', 'Números', 6, 8, 'Todo el tiempo de su nazareato, será santo á Jehová.', 'cosmic'),
('Old', 'Números', 6, 9, 'Y si alguno muriere muy de repente junto á el, contaminará la cabeza de su nazareato; por tanto el día de su purificacíon raerá su cabeza; al séptimo día la raerá.', 'cosmic'),
('Old', 'Números', 6, 10, 'Y el día octavo traerá dos tórtolas ó dos palominos al sacerdote, á la puerta del tabernáculo del testimonio;', 'cosmic'),
('Old', 'Números', 6, 11, 'Y el sacerdote hará el uno en expiación, y el otro en holocausto: y expiarálo de lo que pecó sobre el muerto, y santificará su cabeza en aquel día.', 'cosmic'),
('Old', 'Números', 6, 12, 'Y consagrará á Jehová los días de su nazareato, y traerá un cordero de un año en expiación por la culpa; y los días primeros serán anulados, por cuanto fué contaminado su nazareato.', 'cosmic'),
('Old', 'Números', 6, 13, 'Esta es, pues, la ley del Nazareo el día que se cumpliere el tiempo de su nazareato: Vendrá á la puerta del tabernáculo del testimonio;', 'cosmic'),
('Old', 'Números', 6, 14, 'Y ofrecerá su ofrenda á Jehová, un cordero de un año sin tacha en holocausto, y una cordera de un año sin defecto en expiación, y un carnero sin defecto por sacrificio de paces:', 'cosmic'),
('Old', 'Números', 6, 15, 'Además un canastillo de cenceñas, tortas de flor de harina amasadas con aceite, y hojaldres cenceñas untadas con aceite, y su presente, y sus libaciones.', 'cosmic'),
('Old', 'Números', 6, 16, 'Y el sacerdote lo ofrecerá delante de Jehová, y hará su expiación y su holocausto:', 'cosmic'),
('Old', 'Números', 6, 17, 'Y ofrecerá el carnero en sacrificio de paces á Jehová, con el canastillo de las cenceñas; ofrecerá asimismo el sacerdote su presente, y sus libaciones.', 'cosmic'),
('Old', 'Números', 6, 18, 'Entonces el Nazareo raerá á la puerta del tabernáculo del testimonio la cabeza de su nazareato, y tomará los cabellos de la cabeza de su nazareato, y los pondrá sobre el fuego que está debajo del sacrificio de las paces.', 'cosmic'),
('Old', 'Números', 6, 19, 'Después tomará el sacerdote la espaldilla cocida del carnero, y una torta sin levadura del canastillo, y una hojaldre sin levadura, y pondrálas sobre las manos del Nazareo, después que fuere raído su nazareato:', 'cosmic'),
('Old', 'Números', 6, 20, 'Y el sacerdote mecerá aquello, ofrenda agitada delante de Jehová; lo cual será cosa santa del sacerdote, á más del pecho mecido y de la espaldilla separada: y después podrá beber vino el Nazareo.', 'cosmic'),
('Old', 'Números', 6, 21, 'Esta es la ley del Nazareo que hiciere voto de su ofrenda á Jehová por su nazareato, á más de lo que su mano alcanzare: según el voto que hiciere, así hará, conforme á la ley de su nazareato.', 'cosmic'),
('Old', 'Números', 6, 22, 'Y Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 6, 23, 'Habla á Aarón y á sus hijos, y diles: Asi bendeciréis á los hijos de Israel, diciéndoles:', 'cosmic'),
('Old', 'Números', 6, 24, 'Jehová te bendiga, y te guarde:', 'cosmic'),
('Old', 'Números', 6, 25, 'Haga resplandecer Jehová su rostro sobre ti, y haya de ti misericordia:', 'cosmic'),
('Old', 'Números', 6, 26, 'Jehová alce á ti su rostro, y ponga en ti paz.', 'cosmic'),
('Old', 'Números', 6, 27, 'Y pondrán mi nombre sobre los hijos de Israel, y yo los bendeciré.', 'cosmic'),
('Old', 'Números', 7, 1, 'Y ACONTECIO, que cuando Moisés hubo acabado de levantar el tabernáculo, y ungídolo, y santificádolo, con todos sus vasos; y asimismo ungido y santificado el altar, con todos sus vasos;', 'cosmic'),
('Old', 'Números', 7, 2, 'Entonces los príncipes de Israel, las cabezas de las casas de sus padres, los cuales eran los príncipes de las tribus, que estaban sobre los contados, ofrecieron;', 'cosmic'),
('Old', 'Números', 7, 3, 'Y trajeron sus ofrendas delante de Jehová, seis carros cubiertos, y doce bueyes; por cada dos príncipes un carro, y cada uno un buey; lo cual ofrecieron delante del tabernáculo.', 'cosmic'),
('Old', 'Números', 7, 4, 'Y Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 7, 5, 'Tómalo de ellos, y será para el servicio del tabernáculo del testimonio: y lo darás á los Levitas, á cada uno conforme á su ministerio.', 'cosmic'),
('Old', 'Números', 7, 6, 'Entonces Moisés recibió los carros y los bueyes, y diólos á los Levitas.', 'cosmic'),
('Old', 'Números', 7, 7, 'Dos carros y cuatro bueyes, dió á los hijos de Gersón, conforme á su ministerio;', 'cosmic'),
('Old', 'Números', 7, 8, 'Y á los hijos de Merari dió los cuatro carros y ocho bueyes, conforme á su ministerio, bajo la mano de Ithamar, hijo de Aarón el sacerdote.', 'cosmic'),
('Old', 'Números', 7, 9, 'Y á los hijos de Coath no dió; porque llevaban sobre sí en los hombros el servicio del santuario.', 'cosmic'),
('Old', 'Números', 7, 10, 'Y ofrecieron los príncipes á la dedicación del altar el día que fué ungido, ofrecieron los príncipes su ofrenda delante del altar.', 'cosmic'),
('Old', 'Números', 7, 11, 'Y Jehová dijo á Moisés: Ofrecerán su ofrenda, un príncipe un día, y otro príncipe otro día, á la dedicación del altar.', 'cosmic'),
('Old', 'Números', 7, 12, 'Y el que ofreció su ofrenda el primer día fué Naasón hijo de Aminadab, de la tribu de Judá.', 'cosmic'),
('Old', 'Números', 7, 13, 'Y fué su ofrenda un plato de plata de peso de ciento y treinta siclos, y un jarro de plata de setenta siclos, al siclo del santuario; ambos llenos de flor de harina amasada con aceite para presente;', 'cosmic'),
('Old', 'Números', 7, 14, 'Una cuchara de oro de diez siclos, llena de perfume;', 'cosmic'),
('Old', 'Números', 7, 15, 'Un becerro, un carnero, un cordero de un año para holocausto;', 'cosmic'),
('Old', 'Números', 7, 16, 'Un macho cabrío para expiación;', 'cosmic'),
('Old', 'Números', 7, 17, 'Y para sacrificio de paces, dos bueyes, cinco carneros, cinco machos de cabrío, cinco corderos de un año. Esta fué la ofrenda de Naasón, hijo de Aminadab.', 'cosmic'),
('Old', 'Números', 7, 18, 'El segundo día ofreció Nathanael hijo de Suar, príncipe de Issachâr.', 'cosmic'),
('Old', 'Números', 7, 19, 'Ofreció por su ofrenda un plato de plata de ciento y treinta siclos de peso, un jarro de plata de setenta siclos, al siclo del santuario; ambos llenos de flor de harina amasada con aceite para presente;', 'cosmic'),
