INSERT INTO scriptures (testament, book_name, chapter, verse_number, content, visual_theme) VALUES
('Old', 'Números', 10, 12, 'Y movieron los hijos de Israel por sus partidas del desierto de Sinaí; y paró la nube en el desierto de Parán.', 'cosmic'),
('Old', 'Números', 10, 13, 'Y movieron la primera vez al dicho de Jehová por mano de Moisés.', 'cosmic'),
('Old', 'Números', 10, 14, 'Y la bandera del campo de los hijos de Judá comenzó á marchar primero, por sus escuadrones: y Naasón, hijo de Aminadab, era sobre su ejército.', 'cosmic'),
('Old', 'Números', 10, 15, 'Y sobre el ejército de la tribu de los hijos de Issachâr, Nathanael hijo de Suar.', 'cosmic'),
('Old', 'Números', 10, 16, 'Y sobre el ejército de la tribu de los hijos de Zabulón, Eliab hijo de Helón.', 'cosmic'),
('Old', 'Números', 10, 17, 'Y después que estaba ya desarmado el tabernáculo, movieron los hijos de Gersón y los hijos de Merari, que lo llevaban.', 'cosmic'),
('Old', 'Números', 10, 18, 'Luego comenzó á marchar la bandera del campo de Rubén por sus escuadrones: y Elisur, hijo de Sedeur, era sobre su ejército.', 'cosmic'),
('Old', 'Números', 10, 19, 'Y sobre el ejército de la tribu de los hijos de Simeón, Selumiel hijo de Zurisaddai.', 'cosmic'),
('Old', 'Números', 10, 20, 'Y sobre el ejército de la tribu de los hijos de Gad, Eliasaph hijo de Dehuel.', 'cosmic'),
('Old', 'Números', 10, 21, 'Luego comenzaron á marchar los Coathitas llevando el santuario; y entre tanto que ellos llegaban, los otros acondicionaron el tabernáculo.', 'cosmic'),
('Old', 'Números', 10, 22, 'Después comenzó á marchar la bandera del campo de los hijos de Ephraim por sus escuadrones: y Elisama, hijo de Ammiud, era sobre su ejército.', 'cosmic'),
('Old', 'Números', 10, 23, 'Y sobre el ejército de la tribu de los hijos de Manasés, Gamaliel hijo de Pedasur.', 'cosmic'),
('Old', 'Números', 10, 24, 'Y sobre el ejército de la tribu de los hijos de Benjamín, Abidán hijo de Gedeón.', 'cosmic'),
('Old', 'Números', 10, 25, 'Luego comenzó á marchar la bandera del campo de los hijos de Dan por sus escuadrones, recogiendo todos los campos: y Ahiezer, hijo de Ammisaddai, era sobre su ejército.', 'cosmic'),
('Old', 'Números', 10, 26, 'Y sobre el ejército de la tribu de los hijos de Aser, Pagiel hijo de Ocrán.', 'cosmic'),
('Old', 'Números', 10, 27, 'Y sobre el ejército de la tribu de los hijos de Nephtalí, Ahira hijo de Enán.', 'cosmic'),
('Old', 'Números', 10, 28, 'Estas son las partidas de los hijos de Israel por sus ejércitos, cuando se movían.', 'cosmic'),
('Old', 'Números', 10, 29, 'Entonces dijo Moisés á Hobab, hijo de Ragüel Madianita, su suegro: Nosotros nos partimos para el lugar del cual Jehová ha dicho: Yo os lo daré. Ven con nosotros, y te haremos bien: porque Jehová ha hablado bien respecto á Israel.', 'cosmic'),
('Old', 'Números', 10, 30, 'Y él le respondió: Yo no iré, sino que me marcharé á mi tierra y á mi parentela.', 'cosmic'),
('Old', 'Números', 10, 31, 'Y él le dijo: Ruégote que no nos dejes; porque tú sabes nuestros alojamientos en el desierto, y nos serás en lugar de ojos.', 'cosmic'),
('Old', 'Números', 10, 32, 'Y será, que si vinieres con nosotros, cuando tuviéremos el bien que Jehová nos ha de hacer, nosotros te haremos bien.', 'cosmic'),
('Old', 'Números', 10, 33, 'Así partieron del monte de Jehová, camino de tres días; y el arca de la alianza de Jehová fué delante de ellos camino de tres días, buscándoles lugar de descanso.', 'cosmic'),
('Old', 'Números', 10, 34, 'Y la nube de Jehová iba sobre ellos de día, desde que partieron del campo.', 'cosmic'),
('Old', 'Números', 10, 35, 'Y fué, que en moviendo el arca, Moisés decía: Levántate, Jehová, y sean disipados tus enemigos, y huyan de tu presencia los que te aborrecen.', 'cosmic'),
('Old', 'Números', 10, 36, 'Y cuando ella asentaba, decía: Vuelve, Jehová, á los millares de millares de Israel.', 'cosmic'),
('Old', 'Números', 11, 1, 'Y ACONTECIO que el pueblo se quejó á oídos de Jehová: y oyólo Jehová, y enardecióse su furor, y encendióse en ellos fuego de Jehová y consumió el un cabo del campo.', 'cosmic'),
('Old', 'Números', 11, 2, 'Entonces el pueblo dió voces á Moisés, y Moisés oró á Jehová, y soterróse el fuego.', 'cosmic'),
('Old', 'Números', 11, 3, 'Y llamó á aquel lugar Taberah; porque el fuego de Jehová se encendió en ellos.', 'cosmic'),
('Old', 'Números', 11, 4, 'Y el vulgo que había en medio tuvo un vivo deseo, y volvieron, y aun lloraron los hijos de Israel, y dijeron: ¡Quién nos diera á comer carne!', 'cosmic'),
('Old', 'Números', 11, 5, 'Nos acordamos del pescado que comíamos en Egipto de balde, de los cohombros, y de los melones, y de los puerros, y de las cebollas, y de los ajos:', 'cosmic'),
('Old', 'Números', 11, 6, 'Y ahora nuestra alma se seca; que nada sino maná ven nuestros ojos.', 'cosmic'),
('Old', 'Números', 11, 7, 'Y era el maná como semilla de culantro, y su color como color de bdelio.', 'cosmic'),
('Old', 'Números', 11, 8, 'Derrámabase el pueblo, y recogían, y molían en molinos, ó majaban en morteros, y lo cocían en caldera, ó hacían de él tortas: y su sabor era como sabor de aceite nuevo.', 'cosmic'),
('Old', 'Números', 11, 9, 'Y cuando descendía el rocío sobre el real de noche, el maná descendía de sobre él.', 'cosmic'),
('Old', 'Números', 11, 10, 'Y oyó Moisés al pueblo, que lloraba por sus familias, cada uno á la puerta de su tienda: y el furor de Jehová se encendió en gran manera; también pareció mal á Moisés.', 'cosmic'),
('Old', 'Números', 11, 11, 'Y dijo Moisés á Jehová: ¿Por qué has hecho mal á tu siervo? ¿y por qué no he hallado gracia en tus ojos, que has puesto la carga de todo este pueblo sobre mi?', 'cosmic'),
('Old', 'Números', 11, 12, '¿Concebí yo á todo este pueblo? ¿engendrélo yo, para que me digas: Llévalo en tu seno, como lleva la que cría al que mama, á la tierra de la cual juraste á sus padres?', 'cosmic'),
('Old', 'Números', 11, 13, '¿De donde tengo yo carne para dar á todo este pueblo? porque lloran á mí, diciendo: Danos carne que comamos.', 'cosmic'),
('Old', 'Números', 11, 14, 'No puedo yo solo soportar á todo este pueblo, que me es pesado en demasía.', 'cosmic'),
('Old', 'Números', 11, 15, 'Y si así lo haces tú conmigo, yo te ruego que me des muerte, si he hallado gracia en tus ojos; y que yo no vea mi mal.', 'cosmic'),
('Old', 'Números', 11, 16, 'Entonces Jehová dijo á Moisés: Júntame setenta varones de los ancianos de Israel, que tu sabes que son ancianos del pueblo y sus principales; y tráelos á la puerta del tabernáculo del testimonio, y esperen allí contigo.', 'cosmic'),
('Old', 'Números', 11, 17, 'Y yo descenderé y hablaré allí contigo; y tomaré del espíritu que está en ti, y pondré en ellos; y llevarán contigo la carga del pueblo, y no la llevarás tú solo.', 'cosmic'),
('Old', 'Números', 11, 18, 'Empero dirás al pueblo: Santificaos para mañana, y comeréis carne: pues que habéis llorado en oídos de Jehová, diciendo: ¡Quién nos diera á comer carne! ¡cierto mejor nos iba en Egipto! Jehová, pues, os dará carne, y comeréis.', 'cosmic'),
('Old', 'Números', 11, 19, 'No comeréis un día, ni dos días, ni cinco días, ni diez días, ni veinte días;', 'cosmic'),
('Old', 'Números', 11, 20, 'Sino hasta un mes de tiempo, hasta que os salga por las narices, y os sea en aborrecimiento: por cuanto menospreciasteis á Jehová que está en medio de vosotros, y llorasteis delante de él, diciendo: ¿Para qué salimos acá de Egipto?', 'cosmic'),
('Old', 'Números', 11, 21, 'Entonces dijo Moisés: Seiscientos mil de á pie es el pueblo en medio del cual yo estoy; y tú dices: Les daré carne, y comerán el tiempo de un mes.', 'cosmic'),
('Old', 'Números', 11, 22, '¿Se han de degollar para ellos ovejas y bueyes que les basten? ¿ó se juntarán para ellos todos los peces de la mar para que tengan abasto?', 'cosmic'),
('Old', 'Números', 11, 23, 'Entonces Jehová respondió á Moisés: ¿Hase acortado la mano de Jehová? ahora verás si te sucede mi dicho, ó no.', 'cosmic'),
('Old', 'Números', 11, 24, 'Y salió Moisés, y dijo al pueblo las palabras de Jehová: y juntó los setenta varones de los ancianos del pueblo, é hízolos estar alrededor del tabernáculo.', 'cosmic'),
('Old', 'Números', 11, 25, 'Entonces Jehová descendió en la nube, y hablóle; y tomó del espíritu que estaba en él, y púsolo en los setenta varones ancianos; y fué que, cuando posó sobre ellos el espíritu, profetizaron, y no cesaron.', 'cosmic'),
('Old', 'Números', 11, 26, 'Y habían quedado en el campo dos varones, llamado el uno Eldad y el otro Medad, sobre los cuales también reposó el espíritu: estaban estos entre los escritos, mas no habían salido al tabernáculo; y profetizaron en el campo.', 'cosmic'),
('Old', 'Números', 11, 27, 'Y corrió un mozo, y dió aviso á Moisés, y dijo: Eldad y Medad profetizan en el campo.', 'cosmic'),
('Old', 'Números', 11, 28, 'Entonces respondió Josué hijo de Nun, ministro de Moisés, uno de sus mancebos, y dijo: Señor mío Moisés, impídelos.', 'cosmic'),
('Old', 'Números', 11, 29, 'Y Moisés le respondió: ¿Tienes tú celos por mí? mas ojalá que todo el pueblo de Jehová fuesen profetas, que Jehová pusiera su espíritu sobre ellos.', 'cosmic'),
('Old', 'Números', 11, 30, 'Y recogióse Moisés al campo, él y los ancianos de Israel.', 'cosmic'),
('Old', 'Números', 11, 31, 'Y salió un viento de Jehová, y trajo codornices de la mar, y dejólas sobre el real, un día de camino de la una parte, y un día de camino de la otra, en derredor del campo, y casi dos codos sobre la haz de la tierra.', 'cosmic'),
('Old', 'Números', 11, 32, 'Entonces el pueblo estuvo levantado todo aquel día, y toda la noche, y todo el día siguiente, y recogiéronse codornices: el que menos, recogió diez montones; y las tendieron para sí á lo largo en derredor del campo.', 'cosmic'),
('Old', 'Números', 11, 33, 'Aun estaba la carne entre los dientes de ellos, antes que fuese mascada, cuando el furor de Jehová se encendió en el pueblo, é hirío Jehová al pueblo con una muy grande plaga.', 'cosmic'),
('Old', 'Números', 11, 34, 'Y llamó el nombre de aquel lugar Kibroth-hattaavah, por cuanto allí sepultaron al pueblo codicioso.', 'cosmic'),
('Old', 'Números', 11, 35, 'De Kibroth-hattaavah movió el pueblo á Haseroth, y pararon en Haseroth.', 'cosmic'),
('Old', 'Números', 12, 1, 'Y HABLARON María y Aarón contra Moisés á causa de la mujer Ethiope que había tomado: porque él había tomado mujer Ethiope.', 'cosmic'),
('Old', 'Números', 12, 2, 'Y dijeron: ¿Solamente por Moisés ha hablado Jehová? ¿no ha hablado también por nosotros? Y oyólo Jehová.', 'cosmic'),
('Old', 'Números', 12, 3, 'Y aquel varón Moisés era muy manso, más que todos los hombres que había sobre la tierra,', 'cosmic'),
('Old', 'Números', 12, 4, 'Y luego dijo Jehová á Moisés, y á Aarón, y á María: Salid vosotros tres al tabernáculo del testimonio. Y salieron ellos tres.', 'cosmic'),
('Old', 'Números', 12, 5, 'Entonces Jehová descendió en la columna de la nube, y púsose á la la puerta del tabernáculo, y llamó á Aarón y á María; y salieron ellos ambos.', 'cosmic'),
('Old', 'Números', 12, 6, 'Y él les dijo: Oid ahora mis palabras: si tuviereis profeta de Jehová, le apareceré en visión, en sueños hablaré con él.', 'cosmic'),
('Old', 'Números', 12, 7, 'No así á mi siervo Moisés, que es fiel en toda mi casa:', 'cosmic'),
('Old', 'Números', 12, 8, 'Boca á boca hablaré con él, y á las claras, y no por figuras; y verá la apariencia de Jehová: ¿por qué pues no tuvisteis temor de hablar contra mi siervo Moisés?', 'cosmic'),
('Old', 'Números', 12, 9, 'Entonces el furor de Jehová se encendió en ellos; y fuése.', 'cosmic'),
('Old', 'Números', 12, 10, 'Y la nube se apartó del tabernáculo: y he aquí que María era leprosa como la nieve; y miró Aarón á María, y he aquí que estaba leprosa.', 'cosmic'),
('Old', 'Números', 12, 11, 'Y dijo Aarón á Moisés: ¡Ah! señor mío, no pongas ahora sobre nosotros pecado; porque locamente lo hemos hecho, y hemos pecado.', 'cosmic'),
('Old', 'Números', 12, 12, 'No sea ella ahora como el que sale muerto del vientre de su madre, consumida la mitad de su carne.', 'cosmic'),
('Old', 'Números', 12, 13, 'Entonces Moisés clamó á Jehová, diciendo: Ruégote, oh Dios, que la sanes ahora.', 'cosmic'),
('Old', 'Números', 12, 14, 'Respondió Jehová á Moisés: Pues si su padre hubiera escupido en su cara, ¿no se avergonzaría por siete días?: sea echada fuera del real por siete días, y después se reunirá.', 'cosmic'),
('Old', 'Números', 12, 15, 'Así María fué echada del real siete días; y el pueblo no pasó adelante hasta que se le reunió María.', 'cosmic'),
('Old', 'Números', 12, 16, '(13-1) Y DESPUÉS movió el pueblo de Haseroth, y asentaron el campo en el desierto de Parán.', 'cosmic'),
('Old', 'Números', 13, 1, '(13-2) Y Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 13, 2, '(13-3) Envía tú hombres que reconozcan la tierra de Canaán, la cual yo doy á los hijos de Israel: de cada tribu de sus padres enviaréis un varón, cada uno príncipe entre ellos.', 'cosmic'),
('Old', 'Números', 13, 3, '(13-4) Y Moisés los envío desde el desierto de Parán, conforme á la palabra de Jehová: y todos aquellos varones eran príncipes de los hijos de Israel.', 'cosmic'),
('Old', 'Números', 13, 4, '(13-5) Los nombres de los cuales son estos: De la tribu de Rubén, Sammua hijo de Zaccur.', 'cosmic'),
('Old', 'Números', 13, 5, '(13-6) De la tribu de Simeón, Saphat hijo de Huri.', 'cosmic'),
('Old', 'Números', 13, 6, '(13-7) De la tribu de Judá, Caleb hijo de Jephone.', 'cosmic'),
('Old', 'Números', 13, 7, '(13-8) De la tribu de Issachâr, Igal hijo de Joseph.', 'cosmic'),
('Old', 'Números', 13, 8, '(13-9) De la tribu de Ephraim, Oseas hijo de Nun.', 'cosmic'),
('Old', 'Números', 13, 9, '(13-10) De la tribu de Benjamín, Palti hijo de Raphu.', 'cosmic'),
('Old', 'Números', 13, 10, '(13-11) De la tribu de Zabulón, Gaddiel hijo de Sodi.', 'cosmic'),
('Old', 'Números', 13, 11, '(13-12) De la tribu de José, de la tribu de Manasés, Gaddi hijo de Susi.', 'cosmic'),
('Old', 'Números', 13, 12, '(13-13) De la tribu de Dan, Ammiel hijo de Gemalli.', 'cosmic'),
('Old', 'Números', 13, 13, '(13-14) De la tribu de Aser, Sethur hijo de Michâel.', 'cosmic'),
('Old', 'Números', 13, 14, '(13-15) De la tribu de Nephtalí, Nahabí hijo de Vapsi.', 'cosmic'),
('Old', 'Números', 13, 15, '(13-16) De la tribu de Gad, Gehuel hijo de Machî.', 'cosmic'),
('Old', 'Números', 13, 16, '(13-17) Estos son los nombres de los varones que Moisés envió á reconocer la tierra: y á Oseas hijo de Nun, le puso Moisés el nombre de Josué.', 'cosmic'),
('Old', 'Números', 13, 17, '(13-18) Enviólos, pues, Moisés á reconocer la tierra de Canaán, diciéndoles: Subid por aquí, por el mediodía, y subid al monte:', 'cosmic'),
('Old', 'Números', 13, 18, '(13-19) Y observad la tierra qué tal es; y el pueblo que la habita, si es fuerte ó débil, si poco ó numeroso;', 'cosmic'),
('Old', 'Números', 13, 19, '(13-20) Qué tal la tierra habitada, si es buena ó mala; y qué tales son las ciudades habitadas, si de tiendas ó de fortalezas;', 'cosmic'),
('Old', 'Números', 13, 20, '(13-21) Y cuál sea el terreno, si es pingüe ó flaco, si en él hay ó no árboles: y esforzaos, y coged del fruto del país. Y el tiempo era el tiempo de las primeras uvas.', 'cosmic'),
('Old', 'Números', 13, 21, '(13-22) Y ellos subieron, y reconocieron la tierra desde el desierto de Zin hasta Rehob, entrando en Emath.', 'cosmic'),
('Old', 'Números', 13, 22, '(13-23) Y subieron por el mediodía, y vinieron hasta Hebrón: y allí estaban Aimán, y Sesai, y Talmai, hijos de Anac. Hebrón fué edificada siete años antes de Zoán, la de Egipto.', 'cosmic'),
('Old', 'Números', 13, 23, '(13-24) Y llegaron hasta el arroyo de Escol, y de allí cortaron un sarmiento con un racimo de uvas, el cual trejeron dos en un palo, y de las granadas y de los higos.', 'cosmic'),
('Old', 'Números', 13, 24, '(13-25) Y llamóse aquel lugar Nahal-escol por el racimo que cortaron de allí los hijos de Israel.', 'cosmic'),
('Old', 'Números', 13, 25, '(13-26) Y volvieron de reconocer la tierra al cabo de cuarenta días.', 'cosmic'),
('Old', 'Números', 13, 26, '(13-27) Y anduvieron y vinieron á Moisés y á Aarón, y á toda la congregación de los hijos de Israel, en el desierto de Parán, en Cades, y diéronles la respuesta, y á toda la congregación, y les mostraron el fruto de la tierra.', 'cosmic'),
('Old', 'Números', 13, 27, '(13-28) Y le contaron, y dijeron: Nosotros llegamos á la tierra á la cual nos enviaste, la que ciertamente fluye leche y miel; y este es el fruto de ella.', 'cosmic'),
('Old', 'Números', 13, 28, '(13-29) Mas el pueblo que habita aquella tierra es fuerte, y las ciudades muy grandes y fuertes; y también vimos allí los hijos de Anac.', 'cosmic'),
('Old', 'Números', 13, 29, '(13-30) Amalec habita la tierra del mediodía; y el Hetheo, y el Jebuseo, y el Amorrheo, habitan en el monte; y el Cananeo habita junto á la mar, y á la ribera del Jordán.', 'cosmic'),
('Old', 'Números', 13, 30, '(13-31) Entonces Caleb hizo callar el pueblo delante de Moisés, y dijo: Subamos luego, y poseámosla; que más podremos que ella.', 'cosmic'),
('Old', 'Números', 13, 31, '(13-32) Mas los varones que subieron con él, dijeron: No podremos subir contra aquel pueblo; porque es más fuerte que nosotros.', 'cosmic'),
('Old', 'Números', 13, 32, '(13-33) y vituperaron entre los hijos de Israel la tierra que habían reconocido, diciendo: La tierra por donde pasamos para reconocerla, es tierra que traga á sus moradores; y todo el pueblo que vimos en medio de ella, son hombres de grande estatura.', 'cosmic'),
('Old', 'Números', 13, 33, '(13-34) También vimos allí gigantes, hijos de Anac, raza de los gigantes: y éramos nosotros, á nuestro parecer, como langostas; y así les parecíamos á ellos.', 'cosmic'),
('Old', 'Números', 14, 1, 'ENTONCES toda la congregación alzaron grita, y dieron voces: y el pueblo lloró aquella noche.', 'cosmic'),
('Old', 'Números', 14, 2, 'Y quejáronse contra Moisés y contra Aarón todos los hijos de Israel; y díjoles toda la multitud: ¡Ojalá muriéramos en la tierra de Egipto; ó en este desierto ojalá muriéramos!', 'cosmic'),
('Old', 'Números', 14, 3, '¿Y por qué nos trae Jehová á esta tierra para caer á cuchillo y que nuestras mujeres y nuestros chiquitos sean por presa? ¿no nos sería mejor volvernos á Egipto?', 'cosmic'),
('Old', 'Números', 14, 4, 'Y decían el uno al otro: Hagamos un capitán, y volvámonos á Egipto.', 'cosmic'),
('Old', 'Números', 14, 5, 'Entonces Moisés y Aarón cayeron sobre sus rostros delante de toda la multitud de la congregación de los hijos de Israel.', 'cosmic'),
('Old', 'Números', 14, 6, 'Y Josué hijo de Nun, y Caleb hijo de Jephone, que eran de los que habían reconocido la tierra, rompieron sus vestidos;', 'cosmic'),
('Old', 'Números', 14, 7, 'Y hablaron á toda la congregación de los hijos de Israel, diciendo: La tierra por donde pasamos para reconocerla, es tierra en gran manera buena.', 'cosmic'),
('Old', 'Números', 14, 8, 'Si Jehová se agradare de nosotros, él nos meterá en esta tierra, y nos la entregará; tierra que fluye leche y miel.', 'cosmic'),
('Old', 'Números', 14, 9, 'Por tanto, no seáis rebeldes contra Jehová, ni temáis al pueblo de aquesta tierra, porque nuestro pan son: su amparo se ha apartado de ellos, y con nosotros está Jehová: no los temáis.', 'cosmic'),
('Old', 'Números', 14, 10, 'Entonces toda la multitud habló de apedrearlos con piedras. Mas la gloria de Jehová se mostró en el tabernáculo del testimonio á todos los hijos de Israel.', 'cosmic'),
('Old', 'Números', 14, 11, 'Y Jehová dijo á Moisés: ¿Hasta cuándo me ha de irritar este pueblo? ¿hasta cuándo no me ha de creer con todas las señales que he hecho en medio de ellos?.', 'cosmic'),
('Old', 'Números', 14, 12, 'Yo le heriré de mortandad, y lo destruiré, y á ti te pondré sobre gente grande y más fuerte que ellos.', 'cosmic'),
('Old', 'Números', 14, 13, 'Y Moisés respondió á Jehová: Oiránlo luego los Egipcios, porque de en medio de ellos sacaste á este pueblo con tu fortaleza:', 'cosmic'),
('Old', 'Números', 14, 14, 'Y lo dirán á los habitadores de esta tierra; los cuales han oído que tú, oh Jehová, estabas en medio de este pueblo, que ojo á ojo aparecías tú, oh Jehová, y que tu nube estaba sobre ellos, y que de día ibas delante de ellos en columna de nube, y de noche en columna de fuego:', 'cosmic'),
('Old', 'Números', 14, 15, 'Y que has hecho morir á este pueblo como á un hombre: y las gentes que hubieren oído tu fama hablarán, diciendo:', 'cosmic'),
('Old', 'Números', 14, 16, 'Porque no pudo Jehová meter este pueblo en la tierra de la cual les había jurado, los mató en el desierto.', 'cosmic'),
('Old', 'Números', 14, 17, 'Ahora, pues, yo te ruego que sea magnificada la fortaleza del Señor, como lo hablaste, diciendo:', 'cosmic'),
('Old', 'Números', 14, 18, 'Jehová, tardo de ira y grande en misericordia, que perdona la iniquidad y la rebelión, y absolviendo no absolverá al culpado; que visita la maldad de los padres sobre los hijos hasta los terceros y hasta los cuartos.', 'cosmic'),
('Old', 'Números', 14, 19, 'Perdona ahora la iniquidad de este pueblo según la grandeza de tu misericordia, y como has perdonado á este pueblo desde Egipto hasta aquí.', 'cosmic'),
('Old', 'Números', 14, 20, 'Entonces Jehová dijo: Yo lo he perdonado conforme á tu dicho:', 'cosmic'),
('Old', 'Números', 14, 21, 'Mas, ciertamente vivo yo y mi gloria hinche toda la tierra,', 'cosmic'),
('Old', 'Números', 14, 22, 'Que todos los que vieron mi gloria y mis señales que he hecho en Egipto y en el desierto, y me han tentado ya diez veces, y no han oído mi voz,', 'cosmic'),
('Old', 'Números', 14, 23, 'No verán la tierra de la cual juré á sus padres: no, ninguno de los que me han irritado la verá.', 'cosmic'),
('Old', 'Números', 14, 24, 'Empero mi siervo Caleb, por cuanto hubo en él otro espíritu, y cumplió de ir en pos de mi, yo le meteré en la tierra donde entró y su simiente la recibirá en heredad.', 'cosmic'),
('Old', 'Números', 14, 25, 'Ahora bien, el Amalecita y el Cananeo habitan en el valle; volveos mañana, y partíos al desierto, camino del mar Bermejo.', 'cosmic'),
('Old', 'Números', 14, 26, 'Y Jehová habló á Moisés y á Aarón, diciendo:', 'cosmic'),
('Old', 'Números', 14, 27, '¿Hasta cuándo oiré esta depravada multitud que murmura contra mí, las querellas de los hijos de Israel, que de mí se quejan?', 'cosmic'),
('Old', 'Números', 14, 28, 'Diles: Vivo yo, dice Jehová, que según habéis hablado á mis oídos, así haré yo con vosotros:', 'cosmic'),
('Old', 'Números', 14, 29, 'En este desierto caerán vuestros cuerpos; todos vuestros contados según toda vuestra cuenta, de veinte años arriba, los cuales habéis murmurado contra mí;', 'cosmic'),
('Old', 'Números', 14, 30, 'Vosotros á la verdad no entraréis en la tierra, por la cual alcé mi mano de haceros habitar en ella; exceptuando á Caleb hijo de Jephone, y á Josué hijo de Nun.', 'cosmic'),
('Old', 'Números', 14, 31, 'Mas vuestros chiquitos, de los cuales dijisteis que serían por presa, yo los introduciré, y ellos conocerán la tierra que vosotros despreciasteis.', 'cosmic'),
('Old', 'Números', 14, 32, 'Y en cuanto á vosotros, vuestros cuerpos caerán en este desierto.', 'cosmic'),
('Old', 'Números', 14, 33, 'Y vuestros hijos andarán pastoreando en el desierto cuarenta años, y ellos llevarán vuestras fornicaciones, hasta que vuestros cuerpos sean consumidos en el desierto.', 'cosmic'),
('Old', 'Números', 14, 34, 'Conforme al número de los días, de los cuarenta días en que reconocisteis la tierra, llevaréis vuestras iniquidades cuarenta años, un año por cada día; y conoceréis mi castigo.', 'cosmic'),
('Old', 'Números', 14, 35, 'Yo Jehová he hablado; así haré á toda esta multitud perversa que se ha juntado contra mí; en este desierto serán consumidos, y ahí morirán.', 'cosmic'),
('Old', 'Números', 14, 36, 'Y los varones que Moisés envió á reconocer la tierra, y vueltos habían hecho murmurar contra él á toda la congregación, desacreditando aquel país,', 'cosmic'),
('Old', 'Números', 14, 37, 'Aquellos varones que habían hablado mal de la tierra, murieron de plaga delante de Jehová.', 'cosmic'),
('Old', 'Números', 14, 38, 'Mas Josué hijo de Nun, y Caleb hijo de Jephone, quedaron con vida de entre aquellos hombres que habían ido á reconocer la tierra.', 'cosmic'),
('Old', 'Números', 14, 39, 'Y Moisés dijo estas cosas á todos los hijos de Israel, y el pueblo se enlutó mucho.', 'cosmic'),
('Old', 'Números', 14, 40, 'Y levantáronse por la mañana, y subieron á la cumbre del monte, diciendo: Henos aquí para subir al lugar del cual ha hablado Jehová; porque hemos pecado.', 'cosmic'),
('Old', 'Números', 14, 41, 'Y dijo Moisés: ¿Por qué quebrantáis el dicho de Jehová? Esto tampoco os sucederá bien.', 'cosmic'),
('Old', 'Números', 14, 42, 'No subáis, porque Jehová no está en medio de vosotros, no seáis heridos delante de vuestros enemigos.', 'cosmic'),
('Old', 'Números', 14, 43, 'Porque el Amalecita y el Cananeo están allí delante de vosotros, y caeréis á cuchillo: pues por cuanto os habéis retraído de seguir á Jehová, por eso no será Jehová con vosotros.', 'cosmic'),
('Old', 'Números', 14, 44, 'Sin embargo, se obstinaron en subir á la cima del monte: mas el arca de la alianza de Jehová, y Moisés, no se apartaron de en medio del campo.', 'cosmic'),
('Old', 'Números', 14, 45, 'Y descendieron el Amalecita y el Cananeo, que habitaban en aquel monte, é hiriéronlos y derrotáronlos, persiguiéndolos hasta Horma.', 'cosmic'),
('Old', 'Números', 15, 1, 'Y JEHOVA habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 15, 2, 'Habla á los hijos de Israel, y diles: Cuando hubiereis entrado en la tierra de vuestras habitaciones, que yo os doy,', 'cosmic'),
('Old', 'Números', 15, 3, 'E hiciereis ofrenda encendida á Jehová, holocausto, ó sacrificio, por especial voto, ó de vuestra voluntad, ó para hacer en vuestras solemnidades olor suave á Jehová, de vacas ó de ovejas;', 'cosmic'),
('Old', 'Números', 15, 4, 'Entonces el que ofreciere su ofrenda á Jehová, traerá por presente una décima de un epha de flor de harina, amasada con la cuarta parte de un hin de aceite;', 'cosmic'),
('Old', 'Números', 15, 5, 'Y de vino para la libación ofrecerás la cuarta parte de un hin, además del holocausto ó del sacrificio, por cada un cordero.', 'cosmic'),
('Old', 'Números', 15, 6, 'Y por cada carnero harás presente de dos décimas de flor de harina, amasada con el tercio de un hin de aceite:', 'cosmic'),
('Old', 'Números', 15, 7, 'Y de vino para la libación ofrecerás el tercio de un hin, en olor suave á Jehová.', 'cosmic'),
('Old', 'Números', 15, 8, 'Y cuando ofreciereis novillo en holocausto ó sacrificio, por especial voto, ó de paces á Jehová,', 'cosmic'),
('Old', 'Números', 15, 9, 'Ofrecerás con el novillo un presente de tres décimas de flor de harina, amasada con la mitad de un hin de aceite:', 'cosmic'),
('Old', 'Números', 15, 10, 'Y de vino para la libación ofrecerás la mitad de un hin, en ofrenda encendida de olor suave á Jehová.', 'cosmic'),
('Old', 'Números', 15, 11, 'Así se hará con cada un buey, ó carnero, ó cordero, lo mismo de ovejas que de cabras.', 'cosmic'),
('Old', 'Números', 15, 12, 'Conforme al número así haréis con cada uno según el número de ellos.', 'cosmic'),
('Old', 'Números', 15, 13, 'Todo natural hará estas cosas así, para ofrecer ofrenda encendida de olor suave á Jehová.', 'cosmic'),
('Old', 'Números', 15, 14, 'Y cuando habitare con vosotros extranjero, ó cualquiera que estuviere entre vosotros por vuestras edades, si hiciere ofrenda encendida de olor suave á Jehová, como vosotros hiciereis, así hará él.', 'cosmic'),
('Old', 'Números', 15, 15, 'Un mismo estatuto tendréis, vosotros de la congregación y el extranjero que con vosotros mora; estatuto que será perpetuo por vuestras edades: como vosotros, así será el peregrino delante de Jehová.', 'cosmic'),
('Old', 'Números', 15, 16, 'Una misma ley y un mismo derecho tendréis, vosotros y el peregrino que con vosotros mora.', 'cosmic'),
('Old', 'Números', 15, 17, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 15, 18, 'Habla á los hijos de Israel, y diles: Cuando hubiereis entrado en la tierra á la cual yo os llevo,', 'cosmic'),
('Old', 'Números', 15, 19, 'Será que cuando comenzareis á comer el pan de la tierra, ofreceréis ofrenda á Jehová.', 'cosmic'),
('Old', 'Números', 15, 20, 'De lo primero que amasareis, ofreceréis una torta en ofrenda; como la ofrenda de la era, así la ofreceréis.', 'cosmic'),
('Old', 'Números', 15, 21, 'De las primicias de vuestras masas daréis á Jehová ofrenda por vuestras generaciones.', 'cosmic'),
('Old', 'Números', 15, 22, 'Y cuando errareis, y no hiciereis todos estos mandamientos que Jehová ha dicho á Moisés,', 'cosmic'),
('Old', 'Números', 15, 23, 'Todas las cosas que Jehová os ha mandado por la mano de Moisés, desde el día que Jehová lo mandó, y en adelante por vuestras edades,', 'cosmic'),
('Old', 'Números', 15, 24, 'Será que, si el pecado fué hecho por yerro con ignorancia de la congregación, toda la congregación ofrecerá un novillo por holocausto, en olor suave á Jehová, con su presente y su libación, conforme á la ley; y un macho cabrío en expiación.', 'cosmic'),
('Old', 'Números', 15, 25, 'Y el sacerdote hará expiación por toda la congregación de los hijos de Israel; y les será perdonado, porque yerro es: y ellos traerán sus ofrendas, ofrenda encendida á Jehová, y sus expiaciones delante de Jehová, por sus yerros:', 'cosmic'),
('Old', 'Números', 15, 26, 'Y será perdonado á toda la congregación de los hijos de Israel, y al extranjero que peregrina entre ellos, por cuanto es yerro de todo el pueblo.', 'cosmic'),
('Old', 'Números', 15, 27, 'Y si una persona pecare por yerro, ofrecerá una cabra de un año por expiación.', 'cosmic'),
('Old', 'Números', 15, 28, 'Y el sacerdote hará expiación por la persona que habrá pecado por yerro, cuando pecare por yerro delante de Jehová, la reconciliará, y le será perdonado.', 'cosmic'),
('Old', 'Números', 15, 29, 'El natural entre los hijos de Israel, y el peregrino que habitare entre ellos, una misma ley tendréis para el que hiciere algo por yerro.', 'cosmic'),
('Old', 'Números', 15, 30, 'Mas la persona que hiciere algo con altiva mano, así el natural como el extranjero, á Jehová injurió; y la tal persona será cortada de en medio de su pueblo.', 'cosmic'),
('Old', 'Números', 15, 31, 'Por cuanto tuvo en poco la palabra de Jehová, y dió por nulo su mandamiento, enteramente será cortada la tal persona: su iniquidad será sobre ella.', 'cosmic'),
('Old', 'Números', 15, 32, 'Y estando los hijos de Israel en el desierto, hallaron un hombre que recogía leña en día de sábado.', 'cosmic'),
('Old', 'Números', 15, 33, 'Y los que le hallaron recogiendo leña trajéronle á Moisés y á Aarón, y á toda la congregación:', 'cosmic'),
('Old', 'Números', 15, 34, 'Y pusiéronlo en la cárcel, por que no estaba declarado qué le habían de hacer.', 'cosmic'),
('Old', 'Números', 15, 35, 'Y Jehová dijo á Moisés: Irremisiblemente muera aquel hombre; apedréelo con piedras toda la congregación fuera del campo.', 'cosmic'),
('Old', 'Números', 15, 36, 'Entonces lo sacó la congregación fuera del campo, y apedreáronlo con piedras, y murió; como Jehová mandó á Moisés.', 'cosmic'),
('Old', 'Números', 15, 37, 'Y Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 15, 38, 'Habla á los hijos de Israel, y diles que se hagan pezuelos (franjas) en los remates de sus vestidos, por sus generaciones; y pongan en cada pezuelo de los remates un cordón de cárdeno:', 'cosmic'),
('Old', 'Números', 15, 39, 'Y serviros ha de pezuelo, para que cuando lo viereis, os acordéis de todos los mandamientos de Jehová, para ponerlos por obra; y no miréis en pos de vuestro corazón y de vuestros ojos, en pos de los cuales fornicáis:', 'cosmic'),
('Old', 'Números', 15, 40, 'Para que os acordéis, y hagáis todos mis mandamientos, y seáis santos á vuestro Dios.', 'cosmic'),
('Old', 'Números', 15, 41, 'Yo Jehová vuestro Dios, que os saqué de la tierra de Egipto, para ser vuestro Dios: Yo Jehová vuestro Dios.', 'cosmic'),
('Old', 'Números', 16, 1, 'Y CORÉ, hijo de Ishar, hijo de Coath, hijo de Leví; y Dathán y Abiram, hijos de Eliab; y Hon, hijo de Peleth, de los hijos de Rubén, tomaron gente,', 'cosmic'),
('Old', 'Números', 16, 2, 'Y levantáronse contra Moisés con doscientos y cincuenta varones de los hijos de Israel, príncipes de la congregación, de los del consejo, varones de nombre;', 'cosmic'),
('Old', 'Números', 16, 3, 'Y se juntaron contra Moisés y Aarón, y les dijeron: Básteos, porque toda la congregación, todos ellos son santos, y en medio de ellos está Jehová: ¿por qué, pues, os levantáis vosotros sobre la congregación de Jehová?', 'cosmic'),
('Old', 'Números', 16, 4, 'Y como lo oyó Moisés, echóse sobre su rostro;', 'cosmic'),
('Old', 'Números', 16, 5, 'Y habló á Coré y á todo su séquito, diciendo: Mañana mostrará Jehová quién es suyo, y al santo harálo llegar á sí; y al que él escogiere, él lo allegará á sí.', 'cosmic'),
('Old', 'Números', 16, 6, 'Haced esto: tomad incensarios, Coré y todo su séquito:', 'cosmic'),
('Old', 'Números', 16, 7, 'Y poned fuego en ellos, y poned en ellos sahumerio delante de Jehová mañana; y será que el varón á quien Jehová escogiere, aquel será el santo: básteos esto, hijos de Leví.', 'cosmic'),
('Old', 'Números', 16, 8, 'Dijo más Moisés á Coré: Oid ahora, hijos de Leví:', 'cosmic'),
('Old', 'Números', 16, 9, '¿Os es poco que el Dios de Israel os haya apartado de la congregación de Israel, haciéndoos allegar á sí para que ministraseis en el servicio del tabernáculo de Jehová, y estuvieseis delante de la congregación para ministrarles?', 'cosmic'),
('Old', 'Números', 16, 10, '¿Y que te hizo acercar á ti, y á todos tus hermanos los hijos de Leví contigo; para que procuréis también el sacerdocio?', 'cosmic'),
('Old', 'Números', 16, 11, 'Por tanto, tú y todo tu séquito sois los que os juntáis contra Jehová: pues Aarón, ¿qué es para que contra él murmuréis?', 'cosmic'),
('Old', 'Números', 16, 12, 'Y envió Moisés á llamar á Dathán y Abiram, hijos de Eliab; mas ellos respondieron: No iremos allá:', 'cosmic'),
('Old', 'Números', 16, 13, '¿Es poco que nos hayas hecho venir de una tierra que destila leche y miel, para hacernos morir en el desierto, sino que también te enseñorees de nosotros imperiosamente?', 'cosmic'),
('Old', 'Números', 16, 14, 'Ni tampoco nos has metido tú en tierra que fluya leche y miel, ni nos has dado heredades de tierras y viñas: ¿has de arrancar los ojos de estos hombres? No subiremos.', 'cosmic'),
('Old', 'Números', 16, 15, 'Entonces Moisés se enojó en gran manera, y dijo á Jehová: No mires á su presente: ni aun un asno he tomado de ellos, ni á ninguno de ellos he hecho mal.', 'cosmic'),
('Old', 'Números', 16, 16, 'Después dijo Moisés á Coré: Tú y todo tu séquito, poneos mañana delante de Jehová; tú, y ellos, y Aarón:', 'cosmic'),
('Old', 'Números', 16, 17, 'Y tomad cada uno su incensario, y poned sahumerio en ellos, y allegad delante de Jehová cada uno su incensario: doscientos y cincuenta incensarios: tú también, y Aarón, cada uno con su incensario.', 'cosmic'),
('Old', 'Números', 16, 18, 'Y tomaron cada uno su incensario, y pusieron en ellos fuego, y echaron en ellos sahumerio, y pusiéronse á la puerta del tabernáculo del testimonio con Moisés y Aarón.', 'cosmic'),
('Old', 'Números', 16, 19, 'Ya Coré había hecho juntar contra ellos toda la congregación á la puerta del tabernáculo del testimonio: entonces la gloria de Jehová apareció á toda la congregación.', 'cosmic'),
('Old', 'Números', 16, 20, 'Y Jehová habló á Moisés y á Aarón, diciendo:', 'cosmic'),
('Old', 'Números', 16, 21, 'Apartaos de entre esta congregación, y consumirlos he en un momento.', 'cosmic'),
('Old', 'Números', 16, 22, 'Y ellos se echaron sobre sus rostros, y dijeron: Dios, Dios de los espíritus de toda carne, ¿no es un hombre el que pecó? ¿y airarte has tu contra toda la congregación?', 'cosmic'),
('Old', 'Números', 16, 23, 'Entonces Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 16, 24, 'Habla á la congregación, diciendo: Apartaos de en derredor de la tienda de Coré, Dathán, y Abiram.', 'cosmic'),
('Old', 'Números', 16, 25, 'Y Moisés se levantó, y fué á Dathán y Abiram; y los ancianos de Israel fueron en pos de él.', 'cosmic'),
('Old', 'Números', 16, 26, 'Y él habló á la congregación, diciendo: Apartaos ahora de las tiendas de estos impíos hombres, y no toquéis ninguna cosa suya, por que no perezcáis en todos sus pecados.', 'cosmic'),
('Old', 'Números', 16, 27, 'Y apartáronse de las tiendas de Coré, de Dathán, y de Abiram en derredor: y Dathán y Abiram salieron y pusiéronse á las puertas de sus tiendas, con sus mujeres, y sus hijos, y sus chiquitos.', 'cosmic'),
('Old', 'Números', 16, 28, 'Y dijo Moisés: En esto conoceréis que Jehová me ha enviado para que hiciese todas estas cosas: que no de mi corazón las hice.', 'cosmic'),
('Old', 'Números', 16, 29, 'Si como mueren todos los hombres murieren éstos, ó si fueren ellos visitados á la manera de todos los hombres, Jehová no me envió.', 'cosmic'),
('Old', 'Números', 16, 30, 'Mas si Jehová hiciere una nueva cosa, y la tierra abriere su boca, y los tragare con todas sus cosas, y descendieren vivos al abismo, entonces conoceréis que estos hombres irritaron á Jehová.', 'cosmic'),
('Old', 'Números', 16, 31, 'Y aconteció, que en acabando él de hablar todas estas palabras, rompióse la tierra que estaba debajo de ellos:', 'cosmic'),
('Old', 'Números', 16, 32, 'Y abrió la tierra su boca, y tragólos á ellos, y á sus casas, y á todos los hombres de Coré, y á toda su hacienda.', 'cosmic'),
('Old', 'Números', 16, 33, 'Y ellos, con todo lo que tenían, descendieron vivos al abismo, y cubriólos la tierra, y perecieron de en medio de la congregación.', 'cosmic'),
('Old', 'Números', 16, 34, 'Y todo Israel, los que estaban en derredor de ellos, huyeron al grito de ellos; porque decían: No nos trague también la tierra.', 'cosmic'),
('Old', 'Números', 16, 35, 'Y salió fuego de Jehová, y consumió los doscientos y cincuenta hombres que ofrecían el sahumerio.', 'cosmic'),
('Old', 'Números', 16, 36, 'Entonces Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 16, 37, 'Di á Eleazar, hijo de Aarón sacerdote, que tome los incensarios de en medio del incendio, y derrame más allá el fuego; porque son santificados:', 'cosmic'),
('Old', 'Números', 16, 38, 'Los incensarios de estos pecadores contra sus almas: y harán de ellos planchas extendidas para cubrir el altar: por cuanto ofrecieron con ellos delante de Jehová, son santificados; y serán por señal á los hijos de Israel.', 'cosmic'),
('Old', 'Números', 16, 39, 'Y el sacerdote Eleazar tomó los incensarios de metal con que los quemados habían ofrecido; y extendiéronlos para cubrir el altar,', 'cosmic'),
('Old', 'Números', 16, 40, 'En recuerdo á los hijos de Israel que ningún extraño que no sea de la simiente de Aarón, llegue á ofrecer sahumerio delante de Jehová, porque no sea como Coré, y como su séquito; según se lo dijo Jehová por mano de Moisés.', 'cosmic'),
('Old', 'Números', 16, 41, 'El día siguiente toda la congregación de los hijos de Israel murmuró contra Moisés y Aarón, diciendo: Vosotros habéis muerto al pueblo de Jehová.', 'cosmic'),
('Old', 'Números', 16, 42, 'Y aconteció que, como se juntó la congregación contra Moisés y Aarón, miraron hacia el tabernáculo del testimonio, y he aquí la nube lo había cubierto, y apareció la gloria de Jehová.', 'cosmic'),
('Old', 'Números', 16, 43, 'Y vinieron Moisés y Aarón delante del tabernáculo del testimonio.', 'cosmic'),
('Old', 'Números', 16, 44, 'Y Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 16, 45, 'Apartaos de en medio de esta congregación, y consumirélos en un momento. Y ellos se echaron sobre sus rostros.', 'cosmic'),
('Old', 'Números', 16, 46, 'Y dijo Moisés A Aarón: Toma el incensario, y pon en él fuego del altar, y sobre él pon perfume, y ve presto á la congregación, y haz expiación por ellos; porque el furor ha salido de delante de la faz de Jehová: la mortandad ha comenzado.', 'cosmic'),
('Old', 'Números', 16, 47, 'Entonces tomó Aarón el incensario, como Moisés dijo, y corrió en medio de la congregación: y he aquí que la mortandad había comenzado en el pueblo: y él puso perfume, é hizo expiación por el pueblo.', 'cosmic'),
('Old', 'Números', 16, 48, 'Y púsose entre los muertos y los vivos, y cesó la mortandad.', 'cosmic'),
('Old', 'Números', 16, 49, 'Y los que murieron en aquella mortandad fueron catorce mil y setecientos, sin los muertos por el negocio de Coré.', 'cosmic'),
('Old', 'Números', 16, 50, 'Después se volvió Aarón á Moisés á la puerta del tabernáculo del testimonio, cuando la mortandad había cesado.', 'cosmic'),
('Old', 'Números', 17, 1, 'Y HABLO Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 17, 2, 'Habla á los hijos de Israel, y toma de ellos una vara por cada casa de los padres, de todos los príncipes de ellos, doce varas conforme á las casas de sus padres; y escribirás el nombre de cada uno sobre su vara.', 'cosmic'),
('Old', 'Números', 17, 3, 'Y escribirás el nombre de Aarón sobre la vara de Leví; porque cada cabeza de familia de sus padres tendrá una vara.', 'cosmic'),
('Old', 'Números', 17, 4, 'Y las pondrás en el tabernáculo del testimonio delante del testimonio, donde yo me declararé á vosotros.', 'cosmic'),
('Old', 'Números', 17, 5, 'Y será, que el varón que yo escogiere, su vara florecerá: y haré cesar de sobre mí las quejas de los hijos de Israel, con que murmuran contra vosotros.', 'cosmic'),
('Old', 'Números', 17, 6, 'Y Moisés habló á los hijos de Israel, y todos los príncipes de ellos le dieron varas; cada príncipe por las casas de sus padres una vara, en todas doce varas; y la vara de Aarón estaba entre las varas de ellos.', 'cosmic'),
('Old', 'Números', 17, 7, 'Y Moisés puso las varas delante de Jehová en el tabernáculo del testimonio.', 'cosmic'),
('Old', 'Números', 17, 8, 'Y aconteció que el día siguiente vino Moisés al tabernáculo del testimonio; y he aquí que la vara de Aarón de la casa de Leví había brotado, y echado flores, y arrojado renuevos, y producido almendras.', 'cosmic'),
('Old', 'Números', 17, 9, 'Entonces sacó Moisés todas las varas de delante de Jehová á todos los hijos de Israel; y ellos lo vieron, y tomaron cada uno su vara.', 'cosmic'),
('Old', 'Números', 17, 10, 'Y Jehová dijo á Moisés: Vuelve la vara de Aarón delante del testimonio, para que se guarde por señal á los hijos rebeldes; y harás cesar sus quejas de sobre mí, porque no mueran.', 'cosmic'),
('Old', 'Números', 17, 11, 'E hízolo Moisés: como le mandó Jehová, así hizo.', 'cosmic'),
('Old', 'Números', 17, 12, 'Entonces los hijos de Israel hablaron á Moisés, diciendo: He aquí nosotros somos muertos, perdidos somos, todos nosotros somos perdidos.', 'cosmic'),
('Old', 'Números', 17, 13, 'Cualquiera que se llegare, el que se acercare al tabernáculo de Jehová morirá: ¿acabaremos de perecer todos?', 'cosmic'),
('Old', 'Números', 18, 1, 'Y JEHOVA dijo á Aarón: Tú y tus hijos, y la casa de tu padre contigo, llevaréis el pecado del santuario: y tú y tus hijos contigo llevaréis el pecado de vuestro sacerdocio.', 'cosmic'),
('Old', 'Números', 18, 2, 'Y á tus hermanos también, la tribu de Leví, la tribu de tu padre, hazlos llegar á ti, y júntense contigo, y servirte han; y tú y tus hijos contigo serviréis delante del tabernáculo del testimonio.', 'cosmic'),
('Old', 'Números', 18, 3, 'Y guardarán lo que tú ordenares, y el cargo de todo el tabernáculo: mas no llegarán á los vasos santos ni al altar, porque no mueran ellos y vosotros.', 'cosmic'),
('Old', 'Números', 18, 4, 'Se juntarán, pues, contigo, y tendrán el cargo del tabernáculo del testimonio en todo el servicio del tabernáculo; ningún extraño se ha de llegar á vosotros.', 'cosmic'),
('Old', 'Números', 18, 5, 'Y tendréis la guarda del santuario, y la guarda del altar, para que no sea más la ira sobre los hijos de Israel.', 'cosmic'),
('Old', 'Números', 18, 6, 'Porque he aquí yo he tomado á vuestros hermanos los Levitas de entre los hijos de Israel, dados á vosotros en don de Jehová, para que sirvan en el ministerio del tabernáculo del testimonio.', 'cosmic'),
('Old', 'Números', 18, 7, 'Mas tú y tus hijos contigo guardaréis vuestro sacerdocio en todo negocio del altar, y del velo adentro, y ministraréis. Yo os he dado en don el servicio de vuestro sacerdocio; y el extraño que se llegare, morirá.', 'cosmic'),
('Old', 'Números', 18, 8, 'Dijo más Jehová á Aarón: He aquí yo te he dado también la guarda de mis ofrendas: todas las cosas consagradas de los hijos de Israel te he dado por razón de la unción, y á tus hijos, por estatuto perpetuo.', 'cosmic'),
('Old', 'Números', 18, 9, 'Esto será tuyo de la ofrenda de las cosas santas reservadas del fuego: toda ofrenda de ellos, todo presente suyo, y toda expiación por el pecado de ellos, que me han de presentar, será cosa muy santa para ti y para tus hijos.', 'cosmic'),
('Old', 'Números', 18, 10, 'En el santuario la comerás; todo varón comerá de ella: cosa santa será para ti.', 'cosmic'),
('Old', 'Números', 18, 11, 'Esto también será tuyo: la ofrenda elevada de sus dones, y todas las ofrendas agitadas de los hijos de Israel, he dado á ti, y á tus hijos, y á tus hijas contigo, por estatuto perpetuo: todo limpio en tu casa comerá de ellas.', 'cosmic'),
('Old', 'Números', 18, 12, 'De aceite, y de mosto, y de trigo, todo lo más escogido, las primicias de ello, que presentarán á Jehová, á ti las he dado.', 'cosmic'),
('Old', 'Números', 18, 13, 'Las primicias de todas las cosas de la tierra de ellos, las cuales traerán á Jehová, serán tuyas: todo limpio en tu casa comerá de ellas.', 'cosmic'),
('Old', 'Números', 18, 14, 'Todo lo consagrado por voto en Israel será tuyo.', 'cosmic'),
('Old', 'Números', 18, 15, 'Todo lo que abriere matriz en toda carne que ofrecerán á Jehová, así de hombres como de animales, será tuyo: mas has de hacer redimir el primogénito del hombre: también harás redimir el primogénito de animal inmundo.', 'cosmic'),
('Old', 'Números', 18, 16, 'Y de un mes harás efectuar el rescate de ellos, conforme á tu estimación, por precio de cinco siclos, al siclo del santuario, que es de veinte óbolos.', 'cosmic'),
('Old', 'Números', 18, 17, 'Mas el primogénito de vaca, y el primogénito de oveja, y el primogénito de cabra, no redimirás; santificados son: la sangre de ellos rociarás sobre el altar, y quemarás la grosura de ellos, ofrenda encendida en olor suave á Jehová.', 'cosmic'),
('Old', 'Números', 18, 18, 'Y la carne de ellos será tuya: como el pecho de la mecedura y como la espaldilla derecha, será tuya.', 'cosmic'),
('Old', 'Números', 18, 19, 'Todas las ofrendas elevadas de las cosas santas, que los hijos de Israel ofrecieren á Jehová, helas dado para ti, y para tus hijos y para tus hijas contigo, por estatuto perpetuo: pacto de sal perpetuo es delante de Jehová para ti y para tu simiente contigo.', 'cosmic'),
('Old', 'Números', 18, 20, 'Y Jehová dijo á Aarón: De la tierra de ellos no tendrás heredad, ni entre ellos tendrás parte: Yo soy tu parte y tu heredad en medio de los hijos de Israel.', 'cosmic'),
('Old', 'Números', 18, 21, 'Y he aquí yo he dado á los hijos de Leví todos los diezmos en Israel por heredad, por su ministerio, por cuanto ellos sirven en el ministerio del tabernáculo del testimonio.', 'cosmic'),
('Old', 'Números', 18, 22, 'Y no llegarán más los hijos de Israel al tabernáculo del testimonio, porque no lleven pecado, por el cual mueran.', 'cosmic'),
('Old', 'Números', 18, 23, 'Mas los Levitas harán el servicio del tabernáculo del testimonio, y ellos llevarán su iniquidad: estatuto perpetuo por vuestras edades; y no poseerán heredad entre los hijos de Israel.', 'cosmic'),
('Old', 'Números', 18, 24, 'Porque á los Levitas he dado por heredad los diezmos de los hijos de Israel, que ofrecerán á Jehová en ofrenda: por lo cual les he dicho: Entre los hijos de Israel no poseerán heredad.', 'cosmic'),
('Old', 'Números', 18, 25, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 18, 26, 'Así hablarás á los Levitas, y les dirás: Cuando tomareis de los hijos de Israel los diezmos que os he dado de ellos por vuestra heredad, vosotros presentaréis de ellos en ofrenda mecida á Jehová el diezmo de los diezmos.', 'cosmic'),
('Old', 'Números', 18, 27, 'Y se os contará vuestra ofrenda como grano de la era, y como acopio del lagar.', 'cosmic'),
('Old', 'Números', 18, 28, 'Así ofreceréis también vosotros ofrenda á Jehová de todos vuestros diezmos que hubiereis recibido de los hijos de Israel; y daréis de ellos la ofrenda de Jehová á Aarón el sacerdote.', 'cosmic'),
('Old', 'Números', 18, 29, 'De todos vuestros dones ofreceréis toda ofrenda á Jehová; de todo lo mejor de ellos ofreceréis la porción que ha de ser consagrada.', 'cosmic'),
('Old', 'Números', 18, 30, 'Y les dirás: Cuando ofreciereis lo mejor de ellos, será contado á los Levitas por fruto de la era, y commo fruto del lagar.', 'cosmic'),
('Old', 'Números', 18, 31, 'Y lo comeréis en cualquier lugar, vosotros y vuestra familia: pues es vuestra remuneración por vuestro ministerio en el tabernáculo del testimonio.', 'cosmic'),
('Old', 'Números', 18, 32, 'Y cuando vosotros hubiereis ofrecido de ello lo mejor suyo, no llevaréis por ello pecado: y no habéis de contaminar las cosas santas de los hijos de Israel, y no moriréis.', 'cosmic'),
('Old', 'Números', 19, 1, 'Y Jehová habló á Moisés y á Aarón, diciendo:', 'cosmic'),
('Old', 'Números', 19, 2, 'Esta es la ordenanza de la ley que Jehová ha prescrito, diciendo: Di á los hijos de Israel que te traigan una vaca bermeja, perfecta, en la cual no haya falta, sobre la cual no se haya puesto yugo:', 'cosmic'),
('Old', 'Números', 19, 3, 'Y la daréis á Eleazar el sacerdote, y él la sacará fuera del campo, y harála degollar en su presencia.', 'cosmic'),
('Old', 'Números', 19, 4, 'Y tomará Eleazar el sacerdote de su sangre con su dedo, y rociará hacia la delantera del tabernáculo del testimonio con la sangre de ella siete veces;', 'cosmic'),
('Old', 'Números', 19, 5, 'Y hará quemar la vaca ante sus ojos: su cuero y su carne y su sangre, con su estiercol, hará quemar.', 'cosmic'),
('Old', 'Números', 19, 6, 'Luego tomará el sacerdote palo de cedro, é hisopo, y escarlata, y lo echará en medio del fuego en que arde la vaca.', 'cosmic'),
('Old', 'Números', 19, 7, 'El sacerdote lavará luego sus vestidos, lavará también su carne con agua, y después entrará en el real; y será inmundo el sacerdote hasta la tarde.', 'cosmic'),
('Old', 'Números', 19, 8, 'Asimismo el que la quemó, lavará sus vestidos en agua, también lavará en agua su carne, y será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Números', 19, 9, 'Y un hombre limpio recogerá las cenizas de la vaca, y las pondrá fuera del campo en lugar limpio, y las guardará la congregación de los hijos de Israel para el agua de separación: es una expiación.', 'cosmic'),
('Old', 'Números', 19, 10, 'Y el que recogió las cenizas de la vaca, lavará sus vestidos, y será inmundo hasta la tarde: y será á los hijos de Israel, y al extranjero que peregrina entre ellos, por estatuto perpetuo.', 'cosmic'),
('Old', 'Números', 19, 11, 'El que tocare muerto de cualquiera persona humana, siete días será inmundo:', 'cosmic'),
('Old', 'Números', 19, 12, 'Este se purificará al tercer día con aquesta agua, y al séptimo día será limpio; y si al tercer día no se purificare, no será limpio al séptimo día.', 'cosmic'),
('Old', 'Números', 19, 13, 'Cualquiera que tocare en muerto, en persona de hombre que estuviere muerto, y no se purificare, el tabernáculo de Jehová contaminó; y aquella persona será cortada de Israel: por cuanto el agua de la separación no fué rociada sobre él, inmundo será; y su inmundicia será sobre él.', 'cosmic'),
('Old', 'Números', 19, 14, 'Esta es la ley para cuando alguno muriere en la tienda: cualquiera que entrare en la tienda y todo lo que estuviere en ella, será inmundo siete días.', 'cosmic'),
('Old', 'Números', 19, 15, 'Y todo vaso abierto, sobre el cual no hubiere tapadera bien ajustada, sera inmundo.', 'cosmic'),
('Old', 'Números', 19, 16, 'Y cualquiera que tocare en muerto á cuchillo sobre la haz del campo, ó en muerto, ó en hueso humano, ó en sepulcro, siete días será inmundo.', 'cosmic'),
('Old', 'Números', 19, 17, 'Y para el inmundo tomarán de la ceniza de la quemada vaca de la expiación, y echarán sobre ella agua viva en un vaso:', 'cosmic'),
('Old', 'Números', 19, 18, 'Y un hombre limpio tomará hisopo. y mojarálo en el agua, y rociará sobre la tienda, y sobre todos los muebles, y sobre las personas que allí estuvieren, y sobre aquel que hubiere tocado el hueso, ó el matado, ó el muerto, ó el sepulcro:', 'cosmic'),
('Old', 'Números', 19, 19, 'Y el limpio rociará sobre el inmundo al tercero y al séptimo día: y cuando lo habrá purificado al día séptimo, él lavará luego sus vestidos, y á sí mismo se lavará con agua, y será limpio á la tarde.', 'cosmic'),
('Old', 'Números', 19, 20, 'Y el que fuere inmundo, y no se purificare, la tal persona será cortada de entre la congregación, por cuanto contaminó el tabernáculo de Jehová: no fué rociada sobre él el agua de separación, es inmundo.', 'cosmic'),
('Old', 'Números', 19, 21, 'Y les será por estatuto perpetuo: también el que rociare el agua de la separación lavará sus vestidos; y el que tocare el agua de la separación, será inmundo hasta la tarde.', 'cosmic'),
('Old', 'Números', 19, 22, 'Y todo lo que el inmundo tocare, será inmundo: y la persona que lo tocare, será inmunda hasta la tarde.', 'cosmic'),
('Old', 'Números', 20, 1, 'Y LLEGARON los hijos de Israel, toda la congregación, al desierto de Zin, en el mes primero, y asentó el pueblo en Cades; y allí murió María, y fué allí sepultada.', 'cosmic'),
('Old', 'Números', 20, 2, 'Y como no hubiese agua para la congregación, juntáronse contra Moisés y Aarón.', 'cosmic'),
('Old', 'Números', 20, 3, 'Y regañó el pueblo con Moisés, y hablaron diciendo: ¡Ojalá que nosotros hubiéramos muerto cuando perecieron nuestros hermanos delante de Jehová!', 'cosmic'),
('Old', 'Números', 20, 4, 'Y ¿por qué hiciste venir la congregación de Jehová á este desierto, para que muramos aquí nosotros y nuestras bestias?', 'cosmic'),
('Old', 'Números', 20, 5, '¿Y por qué nos has hecho subir de Egipto, para traernos á este mal lugar? No es lugar de sementera, de higueras, de viñas, ni granadas: ni aun de agua para beber.', 'cosmic'),
('Old', 'Números', 20, 6, 'Y fuéronse Moisés y Aarón de delante de la congregación á la puerta del tabernáculo del testimonio, y echáronse sobre sus rostros; y la gloria de Jehová apareció sobre ellos.', 'cosmic'),
('Old', 'Números', 20, 7, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 20, 8, 'Toma la vara y reune la congregación, tú y Aarón tu hermano, y hablad á la peña en ojos de ellos; y ella dará su agua, y les sacarás aguas de la peña, y darás de beber á la congregación, y á sus bestias.', 'cosmic'),
('Old', 'Números', 20, 9, 'Entonces Moisés tomó la vara de delante de Jehová, como él le mandó.', 'cosmic'),
('Old', 'Números', 20, 10, 'Y juntaron Moisés y Aarón la congregación delante de la peña, y díjoles: Oid ahora, rebeldes: ¿os hemos de hacer salir aguas de esta peña?', 'cosmic'),
('Old', 'Números', 20, 11, 'Entonces alzó Moisés su mano, é hirió la peña con su vara dos veces: y salieron muchas aguas, y bebió la congregación, y sus bestias.', 'cosmic'),
('Old', 'Números', 20, 12, 'Y Jehová dijo á Moisés y á Aarón: Por cuanto no creísteis en mí, para santificarme en ojos de los hijos de Israel, por tanto, no meteréis esta congregación en la tierra que les he dado.', 'cosmic'),
('Old', 'Números', 20, 13, 'Estas son las aguas de la rencilla, por las cuales contendieron los hijos de Israel con Jehová, y él se santificó en ellos.', 'cosmic'),
('Old', 'Números', 20, 14, 'Y envió Moisés embajadores al rey de Edom desde Cades: Así dice Israel tu hermano: Tú has sabido todo el trabajo que nos ha venido:', 'cosmic'),
('Old', 'Números', 20, 15, 'Cómo nuestros padres descendieron á Egipto, y estuvimos en Egipto largo tiempo, y los Egipcios nos maltrataron, y á nuestros padres;', 'cosmic'),
('Old', 'Números', 20, 16, 'Y clamamos á Jehová, el cual oyó nuestra voz, y envió ángel, y sacónos de Egipto; y he aquí estamos en Cades, ciudad al extremo de tus confines:', 'cosmic'),
('Old', 'Números', 20, 17, 'Rogámoste que pasemos por tu tierra; no pasaremos por labranza, ni por viña, ni beberemos agua de pozos: por el camino real iremos, sin apartarnos á la diestra ni á la siniestra, hasta que hayamos pasado tu término.', 'cosmic'),
('Old', 'Números', 20, 18, 'Y Edom le respondió: No pasarás por mi país, de otra manera saldré contra ti armado.', 'cosmic'),
('Old', 'Números', 20, 19, 'Y los hijos de Israel dijeron: Por el camino seguido iremos; y si bebiéremos tus aguas yo y mis ganados, daré el precio de ellas: ciertamente sin hacer otra cosa, pasaré de seguida.', 'cosmic'),
('Old', 'Números', 20, 20, 'Y él respondió: No pasarás. Y salió Edom contra él con mucho pueblo, y mano fuerte.', 'cosmic'),
('Old', 'Números', 20, 21, 'No quiso, pues, Edom dejar pasar á Israel por su término, y apartóse Israel de él.', 'cosmic'),
('Old', 'Números', 20, 22, 'Y partidos de Cades los hijos de Israel, toda aquella congregación, vinieron al monte de Hor.', 'cosmic'),
('Old', 'Números', 20, 23, 'Y Jehová habló á Moisés y Aarón en el monte de Hor, en los confines de la tierra de Edom, diciendo:', 'cosmic'),
('Old', 'Números', 20, 24, 'Aarón será reunido á sus pueblos; pues no entrará en la tierra que yo di á los hijos de Israel, por cuanto fuisteis rebeldes á mi mandamiento en las aguas de la rencilla.', 'cosmic'),
('Old', 'Números', 20, 25, 'Toma á Aarón y á Eleazar su hijo, y hazlos subir al monte de Hor;', 'cosmic'),
('Old', 'Números', 20, 26, 'Y haz desnudar á Aarón sus vestidos, y viste de ellos á Eleazar su hijo; porque Aarón será reunido á sus pueblos, y allí morirá.', 'cosmic'),
('Old', 'Números', 20, 27, 'Y Moisés hizo como Jehová le mandó: y subieron al monte de Hor á ojos de toda la congregación.', 'cosmic'),
('Old', 'Números', 20, 28, 'Y Moisés hizo desnudar á Aarón de sus vestidos y vistiólos á Eleazar su hijo: y Aarón murió allí en la cumbre del monte: y Moisés y Eleazar descendieron del monte.', 'cosmic'),
('Old', 'Números', 20, 29, 'Y viendo toda la congregación que Aarón era muerto, hiciéronle duelo por treinta días todas las familias de Israel.', 'cosmic'),
('Old', 'Números', 21, 1, 'Y OYENDO el Cananeo, el rey de Arad, el cual habitaba al mediodía, que venía Israel por el camino de los centinelas, peleó con Israel, y tomó de él presa.', 'cosmic'),
('Old', 'Números', 21, 2, 'Entonces Israel hizo voto á Jehová, y dijo: Si en efecto entregares á este pueblo en mi mano, yo destruiré sus ciudades.', 'cosmic'),
('Old', 'Números', 21, 3, 'Y Jehová escuchó la voz de Israel, y entregó al Cananeo, y destruyólos á ellos y á sus ciudades; y llamó el nombre de aquel lugar Horma.', 'cosmic'),
('Old', 'Números', 21, 4, 'Y partieron del monte de Hor, camino del mar Bermejo, para rodear la tierra de Edom; y abatióse el ánimo del pueblo por el camino.', 'cosmic'),
('Old', 'Números', 21, 5, 'Y habló el pueblo contra Dios y Moisés: ¿Por qué nos hiciste subir de Egipto para que muramos en este desierto? que ni hay pan, ni agua, y nuestra alma tiene fastidio de este pan tan liviano.', 'cosmic'),
('Old', 'Números', 21, 6, 'Y Jehová envió entre el pueblo serpientes ardientes, que mordían al pueblo: y murió mucho pueblo de Israel.', 'cosmic'),
('Old', 'Números', 21, 7, 'Entonces el pueblo vino á Moisés, y dijeron: Pecado hemos por haber hablado contra Jehová, y contra ti: ruega á Jehová que quite de nosotros estas serpientes. Y Moisés oró por el pueblo.', 'cosmic'),
('Old', 'Números', 21, 8, 'Y Jehová dijo á Moisés: Hazte una serpiente ardiente, y ponla sobre la bandera: y será que cualquiera que fuere mordido y mirare á ella, vivirá.', 'cosmic'),
('Old', 'Números', 21, 9, 'Y Moisés hizo una serpiente de metal, y púsola sobre la bandera, y fué, que cuando alguna serpiente mordía á alguno, miraba á la serpiente de metal, y vivía.', 'cosmic'),
('Old', 'Números', 21, 10, 'Y partieron los hijos de Israel, y asentaron campo en Oboth.', 'cosmic'),
('Old', 'Números', 21, 11, 'Y partidos de Oboth, asentaron en Ije-abarim, en el desierto que está delante de Moab, al nacimiento del sol.', 'cosmic'),
('Old', 'Números', 21, 12, 'Partidos de allí, asentaron en la arroyada de Zared.', 'cosmic'),
('Old', 'Números', 21, 13, 'De allí movieron, y asentaron de la otra parte de Arnón, que está en el desierto, y que sale del término del Amorrheo; porque Arnón es término de Moab, entre Moab y el Amorrheo.', 'cosmic'),
('Old', 'Números', 21, 14, 'Por tanto se dice en el libro de las batallas de Jehová: Lo que hizo en el mar Bermejo, Y en los arroyos de Arnón:', 'cosmic'),
('Old', 'Números', 21, 15, 'Y á la corriente de los arroyos Que va á parar en Ar, Y descansa en el término de Moab.', 'cosmic'),
('Old', 'Números', 21, 16, 'Y de allí vinieron á Beer: este es el pozo del cual Jehová dijo á Moisés: Junta al pueblo, y les daré agua.', 'cosmic'),
('Old', 'Números', 21, 17, 'Entonces cantó Israel esta canción: Sube, oh pozo; á él cantad:', 'cosmic'),
('Old', 'Números', 21, 18, 'Pozo, el cual cavaron los señores; Caváronlo los príncipes del pueblo, Y el legislador, con sus bordones.', 'cosmic'),
('Old', 'Números', 21, 19, 'Y de Mathana á Nahaliel: y de Nahaliel á Bamoth:', 'cosmic'),
('Old', 'Números', 21, 20, 'Y de Bamoth al valle que está en los campos de Moab, y á la cumbre de Pisga, que mira á Jesimón.', 'cosmic'),
('Old', 'Números', 21, 21, 'Y envió Israel embajadores á Sehón, rey de los Amorrheos, diciendo:', 'cosmic'),
('Old', 'Números', 21, 22, 'Pasaré por tu tierra: no nos apartaremos por los labrados, ni por las viñas; no beberemos las aguas de los pozos: por el camino real iremos, hasta que pasemos tu término.', 'cosmic'),
('Old', 'Números', 21, 23, 'Mas Sehón no dejó pasar á Israel por su término: antes juntó Sehón todo su pueblo, y salió contra Israel en el desierto: y vino á Jahaz, y peleó contra Israel.', 'cosmic'),
('Old', 'Números', 21, 24, 'E hirióle Israel á filo de espada, y tomó su tierra desde Arnón hasta Jaboc, hasta los hijos de Ammón: porque el término de los hijos de Ammón era fuerte.', 'cosmic'),
('Old', 'Números', 21, 25, 'Y tomó Israel todas estas ciudades: y habitó Israel en todas las ciudades del Amorrheo, en Hesbón y en todas sus aldeas.', 'cosmic'),
('Old', 'Números', 21, 26, 'Porque Hesbón era la ciudad de Sehón, rey de los Amorrheos; el cual había tenido guerra antes con el rey de Moab, y tomado de su poder toda su tierra hasta Arnón.', 'cosmic'),
('Old', 'Números', 21, 27, 'Por tanto, dicen los proverbistas: Venid á Hesbón, Edifíquese y repárese la ciudad de Sehón:', 'cosmic'),
('Old', 'Números', 21, 28, 'Que fuego salió de Hesbón, Y llama de la ciudad de Sehón, Y consumió á Ar de Moab, A los señores de los altos de Arnón.', 'cosmic'),
('Old', 'Números', 21, 29, '¡¡Ay de ti, Moab¡ Perecido has, pueblo de Chêmos: Puso sus hijos en huída, Y sus hijas en cautividad, Por Sehón rey de los Amorrheos.', 'cosmic'),
('Old', 'Números', 21, 30, 'Mas devastamos el reino de ellos; pereció Hesbón hasta Dibón, Y destruimos hasta Nopha y Medeba.', 'cosmic'),
('Old', 'Números', 21, 31, 'Así habitó Israel en la tierra del Amorrheo.', 'cosmic'),
('Old', 'Números', 21, 32, 'Y envió Moisés á reconocer á Jazer; y tomaron sus aldeas, y echaron al Amorrheo que estaba allí.', 'cosmic'),
('Old', 'Números', 21, 33, 'Y volvieron, y subieron camino de Basán, y salió contra ellos Og rey de Basán, él y todo su pueblo, para pelear en Edrei.', 'cosmic'),
('Old', 'Números', 21, 34, 'Entonces Jehová dijo á Moisés: No le tengas miedo, que en tu mano lo he dado, á el y á todo su pueblo, y á su tierra; y harás de él como hiciste de Sehón, rey de los Amorrheos, que habitaba en Hesbón.', 'cosmic'),
('Old', 'Números', 21, 35, 'E hirieron á él, y á sus hijos, y á toda su gente, sin que le quedara uno, y poseyeron su tierra.', 'cosmic'),
('Old', 'Números', 22, 1, 'Y MOVIERON los hijos de Israel, y asentaron en los campos de Moab, de esta parte del Jordán de Jericó.', 'cosmic'),
('Old', 'Números', 22, 2, 'Y vió Balac, hijo de Zippor, todo lo que Israel había hecho al Amorrheo.', 'cosmic'),
('Old', 'Números', 22, 3, 'Y Moab temió mucho á causa del pueblo que era mucho; y angustióse Moab á causa de los hijos de Israel.', 'cosmic'),
('Old', 'Números', 22, 4, 'Y dijo Moab á los ancianos de Madián: Ahora lamerá esta gente todos nuestros contornos, como lame el buey la grama del campo. Y Balac, hijo de Zippor, era entonces rey de Moab.', 'cosmic'),
('Old', 'Números', 22, 5, 'Por tanto envió mensajeros á Balaam hijo de Beor, á Pethor, que está junto al río en la tierra de los hijos de su pueblo, para que lo llamasen, diciendo: Un pueblo ha salido de Egipto, y he aquí cubre la haz de la tierra, y habita delante de mí:', 'cosmic'),
('Old', 'Números', 22, 6, 'Ven pues ahora, te ruego, maldíceme este pueblo, porque es más fuerte que yo: quizá podré yo herirlo, y echarlo de la tierra: que yo sé que el que tú bendijeres, será bendito, y el que tú maldijeres, será maldito.', 'cosmic'),
('Old', 'Números', 22, 7, 'Y fueron los ancianos de Moab, y los ancianos de Madián, con las dádivas de adivinación en su mano, y llegaron á Balaam, y le dijeron las palabras de Balac.', 'cosmic'),
('Old', 'Números', 22, 8, 'Y él les dijo: Reposad aquí esta noche, y yo os referiré las palabras, como Jehová me hablare. Así los príncipes de Moab se quedaron con Balaam.', 'cosmic'),
('Old', 'Números', 22, 9, 'Y vino Dios á Balaam, y díjole: ¿Qué varones son estos que están contigo?', 'cosmic'),
('Old', 'Números', 22, 10, 'Y Balaam respondió á Dios: Balac hijo de Zippor, rey de Moab, ha enviado á mí diciendo:', 'cosmic'),
('Old', 'Números', 22, 11, 'He aquí este pueblo que ha salido de Egipto, cubre la haz de la tierra: ven pues ahora, y maldícemelo; quizá podré pelear con él, y echarlo.', 'cosmic'),
('Old', 'Números', 22, 12, 'Entonces dijo Dios á Balaam: No vayas con ellos, ni maldigas al pueblo; porque es bendito.', 'cosmic'),
('Old', 'Números', 22, 13, 'Así Balaam se levantó por la mañana, y dijo á los príncipes de Balac: Volveos á vuestra tierra, porque Jehová no me quiere dejar ir con vosotros.', 'cosmic'),
('Old', 'Números', 22, 14, 'Y los príncipes de Moab se levantaron, y vinieron á Balac, y dijeron: Balaam no quiso venir con nosotros.', 'cosmic'),
('Old', 'Números', 22, 15, 'Y tornó Balac á enviar otra vez más príncipes, y más honorables que los otros.', 'cosmic'),
('Old', 'Números', 22, 16, 'Los cuales vinieron á Balaam, y dijéronle: Así dice Balac, hijo de Zippor: Ruégote que no dejes de venir á mí:', 'cosmic'),
('Old', 'Números', 22, 17, 'Porque sin duda te honraré mucho, y haré todo lo que me dijeres: ven pues ahora, maldíceme á este pueblo.', 'cosmic'),
('Old', 'Números', 22, 18, 'Y Balaam respondió, y dijo á los siervos de Balac: Aunque Balac me diese su casa llena de plata y oro, no puedo traspasar la palabra de Jehová mi Dios, para hacer cosa chica ni grande.', 'cosmic'),
('Old', 'Números', 22, 19, 'Ruégoos por tanto ahora, que reposeis aquí esta noche, para que yo sepa que me vuelve á decir Jehová.', 'cosmic'),
('Old', 'Números', 22, 20, 'Y vino Dios á Balaam de noche, y díjole: Si vinieren á llamarte hombres, levántate y ve con ellos: empero harás lo que yo te dijere.', 'cosmic'),
('Old', 'Números', 22, 21, 'Así Balaam se levantó por la mañana, y cinchó su asna, y fué con los príncipes de Moab.', 'cosmic'),
('Old', 'Números', 22, 22, 'Y el furor de Dios se encendió porque él iba; y el ángel de Jehová se puso en el camino por adversario suyo. Iba, pues, él montado sobre su asna, y con él dos mozos suyos.', 'cosmic'),
('Old', 'Números', 22, 23, 'Y el asna vió al ángel de Jehová, que estaba en el camino con su espada desnuda en su mano; y apartóse el asna del camino, é iba por el campo. Entonces hirió Balaam al asna para hacerla volver al camino.', 'cosmic'),
('Old', 'Números', 22, 24, 'Mas el ángel de Jehová se puso en una senda de viñas que tenía pared de una parte y pared de otra.', 'cosmic'),
('Old', 'Números', 22, 25, 'Y viendo el asna al ángel de Jehová, pegóse á la pared, y apretó contra la pared el pie de Balaam: y él volvió á herirla.', 'cosmic'),
('Old', 'Números', 22, 26, 'Y el ángel de Jehová pasó más allá, y púsose en una angostura, donde no había camino para apartarse ni á diestra ni á siniestra.', 'cosmic'),
('Old', 'Números', 22, 27, 'Y viendo el asna al ángel de Jehová, echóse debajo de Balaam: y enojóse Balaam, é hirió al asna con el palo.', 'cosmic'),
('Old', 'Números', 22, 28, 'Entonces Jehová abrió la boca al asna, la cual dijo á Balaam: ¿Qué te he hecho, que me has herido estas tres veces?', 'cosmic'),
('Old', 'Números', 22, 29, 'Y Balaam respondió al asna: Porque te has burlado de mí: ¡ojalá tuviera espada en mi mano, que ahora te mataría!', 'cosmic'),
('Old', 'Números', 22, 30, 'Y el asna dijo á Balaam: ¿No soy yo tu asna? sobre mí has cabalgado desde que tú me tienes hasta este día; ¿he acostumbrado á hacerlo así contigo? Y él respondió: No.', 'cosmic'),
('Old', 'Números', 22, 31, 'Entonces Jehová abrió los ojos á Balaam, y vió al ángel de Jehová que estaba en el camino, y tenía su espada desnuda en su mano. Y Balaam hizo reverencia, é inclinóse sobre su rostro.', 'cosmic'),
('Old', 'Números', 22, 32, 'Y el ángel de Jehová le dijo: ¿Por qué has herido tu asna estas tres veces? he aquí yo he salido para contrarrestarte, porque tu camino es perverso delante de mí:', 'cosmic'),
('Old', 'Números', 22, 33, 'El asna me ha visto, y hase apartado luego de delante de mí estas tres veces: y si de mí no se hubiera apartado, yo también ahora te mataría á ti, y á ella dejaría viva.', 'cosmic'),
('Old', 'Números', 22, 34, 'Entonces Balaam dijo al ángel de Jehová: He pecado, que no sabía que tú te ponías delante de mí en el camino: mas ahora, si te parece mal, yo me volveré.', 'cosmic'),
('Old', 'Números', 22, 35, 'Y el ángel de Jehová dijo á Balaam: Ve con esos hombres: empero la palabra que yo te dijere, esa hablarás. Así Balaam fué con los príncipes de Balac.', 'cosmic'),
('Old', 'Números', 22, 36, 'Y oyendo Balac que Balaam venía, salió á recibirlo á la ciudad de Moab, que está junto al término de Arnón, que es el cabo de los confines.', 'cosmic'),
('Old', 'Números', 22, 37, 'Y Balac dijo á Balaam: ¿No envié yo á ti á llamarte? ¿por qué no has venido á mí? ¿no puedo yo honrarte?', 'cosmic'),
('Old', 'Números', 22, 38, 'Y Balaam respondió á Balac: He aquí yo he venido á ti: mas ¿podré ahora hablar alguna cosa? La palabra que Dios pusiere en mi boca, esa hablaré.', 'cosmic'),
('Old', 'Números', 22, 39, 'Y fué Balaam con Balac, y vinieron á la ciudad de Husoth.', 'cosmic'),
('Old', 'Números', 22, 40, 'Y Balac hizo matar bueyes y ovejas, y envió á Balaam, y á los príncipes que estaban con él.', 'cosmic'),
('Old', 'Números', 22, 41, 'Y el día siguiente Balac tomó á Balaam, é hízolo subir á los altos de Baal, y desde allí vió la extremidad del pueblo.', 'cosmic'),
('Old', 'Números', 23, 1, 'Y BALAAM dijo á Balac: Edifícame aquí siete altares, y prepárame aquí siete becerros y siete carneros.', 'cosmic'),
('Old', 'Números', 23, 2, 'Y Balac hizo como le dijo Balaam: y ofrecieron Balac y Balaam un becerro y un carnero en cada altar.', 'cosmic'),
('Old', 'Números', 23, 3, 'Y Balaam dijo á Balac: Ponte junto á tu holocausto, y yo iré: quizá Jehová me vendrá al encuentro, y cualquiera cosa que me mostrare, te la noticiaré. Y así se fué solo.', 'cosmic'),
('Old', 'Números', 23, 4, 'Y vino Dios al encuentro de Balaam, y éste le dijo: Siete altares he ordenado, y en cada altar he ofrecido un becerro y un carnero.', 'cosmic'),
('Old', 'Números', 23, 5, 'Y Jehová puso palabra en la boca de Balaam, y díjole: Vuelve á Balac, y has de hablar así.', 'cosmic'),
('Old', 'Números', 23, 6, 'Y volvió á él, y he aquí estaba él junto á su holocausto, él y todos los príncipes de Moab.', 'cosmic'),
('Old', 'Números', 23, 7, 'Y él tomó su parábola, y dijo: De Aram me trajo Balac, Rey de Moab, de los montes del oriente: Ven, maldíceme á Jacob; Y ven, execra á Israel.', 'cosmic'),
('Old', 'Números', 23, 8, '¿Por qué maldeciré yo al que Dios no maldijo? ¿Y por qué he de execrar al que Jehová no ha execrado?', 'cosmic'),
('Old', 'Números', 23, 9, 'Porque de la cumbre de las peñas lo veré, Y desde los collados lo miraré: He aquí un pueblo que habitará confiado, Y no será contado entre las gentes.', 'cosmic'),
('Old', 'Números', 23, 10, '¿Quién contará el polvo de Jacob, O el número de la cuarta parte de Israel? Muera mi persona de la muerte de los rectos, Y mi postrimería sea como la suya.', 'cosmic'),
('Old', 'Números', 23, 11, 'Entonces Balac dijo á Balaam: ¿Qué me has hecho? hete tomado para que maldigas á mis enemigos, y he aquí has proferido bendiciones.', 'cosmic'),
('Old', 'Números', 23, 12, 'Y él respondió, y dijo: ¿No observaré yo lo que Jehová pusiere en mi boca para decirlo?', 'cosmic'),
('Old', 'Números', 23, 13, 'Y dijo Balac: Ruégote que vengas conmigo á otro lugar desde el cual lo veas; su extremidad solamente verás, que no lo verás todo; y desde allí me lo maldecirás.', 'cosmic'),
('Old', 'Números', 23, 14, 'Y llevólo al campo de Sophim, á la cumbre de Pisga, y edificó siete altares, y ofreció un becerro y un carnero en cada altar.', 'cosmic'),
('Old', 'Números', 23, 15, 'Entonces él dijo á Balac: Ponte aquí junto á tu holocausto, y yo iré á encontrar á Dios allí.', 'cosmic'),
('Old', 'Números', 23, 16, 'Y Jehová salió al encuentro de Balaam, y puso palabra en su boca, y díjole: Vuelve á Balac, y así has de decir.', 'cosmic'),
('Old', 'Números', 23, 17, 'Y vino á él, y he aquí que él estaba junto á su holocausto, y con él los príncipes de Moab: y díjole Balac: ¿Qué ha dicho Jehová?', 'cosmic'),
('Old', 'Números', 23, 18, 'Entonces él tomó su parábola, y dijo: Balac, levántate y oye; Escucha mis palabras, hijo de Zippor:', 'cosmic'),
('Old', 'Números', 23, 19, 'Dios no es hombre, para que mienta; Ni hijo de hombre para que se arrepienta: El dijo, ¿y no hará?; Habló, ¿y no lo ejecutará?', 'cosmic'),
('Old', 'Números', 23, 20, 'He aquí, yo he tomado bendición: Y él bendijo, y no podré revocarla.', 'cosmic'),
('Old', 'Números', 23, 21, 'No ha notado iniquidad en Jacob, Ni ha visto perversidad en Israel: Jehová su Dios es con él, Y júbilo de rey en él.', 'cosmic'),
('Old', 'Números', 23, 22, 'Dios los ha sacado de Egipto; Tiene fuerzas como de unicornio.', 'cosmic'),
('Old', 'Números', 23, 23, 'Porque en Jacob no hay agüero, Ni adivinación en Israel: Como ahora, será dicho de Jacob y de Israel: ¡Lo que ha hecho Dios!', 'cosmic'),
('Old', 'Números', 23, 24, 'He aquí el pueblo, que como león se levantará, Y como león se erguirá: No se echará hasta que coma la presa, Y beba la sangre de los muertos.', 'cosmic'),
('Old', 'Números', 23, 25, 'Entonces Balac dijo á Balaam: Ya que no lo maldices, ni tampoco lo bendigas.', 'cosmic'),
('Old', 'Números', 23, 26, 'Y Balaam respondió, y dijo á Balac: ¿No te he dicho que todo lo que Jehová me dijere, aquello tengo de hacer?', 'cosmic'),
('Old', 'Números', 23, 27, 'Y dijo Balac á Balaam: Ruégote que vengas, te llevaré á otro lugar; por ventura parecerá bien á Dios que desde allí me lo maldigas.', 'cosmic'),
('Old', 'Números', 23, 28, 'Y Balac llevó á Balaam á la cumbre de Peor, que mira hacia Jesimón.', 'cosmic'),
('Old', 'Números', 23, 29, 'Entonces Balaam dijo á Balac: Edifícame aquí siete altares, y prepárame aquí siete becerros y siete carneros.', 'cosmic'),
('Old', 'Números', 23, 30, 'Y Balac hizo como Balaam le dijo; y ofreció un becerro y un carnero en cada altar.', 'cosmic'),
('Old', 'Números', 24, 1, 'Y COMO vió Balaam que parecía bien á Jehová que el bendijese á Israel, no fué, como la primera y segunda vez, á encuentro de agüeros, sino que puso su rostro hacia el desierto;', 'cosmic'),
('Old', 'Números', 24, 2, 'Y alzando sus ojos, vió á Israel alojado por sus tribus; y el espíritu de Dios vino sobre él.', 'cosmic'),
('Old', 'Números', 24, 3, 'Entonces tomó su parábola, y dijo: Dijo Balaam hijo de Beor, Y dijo el varón de ojos abiertos:', 'cosmic'),
('Old', 'Números', 24, 4, 'Dijo el que oyó los dichos de Dios, El que vió la visión del Omnipotente; Caído, mas abiertos los ojos:', 'cosmic'),
('Old', 'Números', 24, 5, '¡Cuán hermosas son tus tiendas, oh Jacob, Tus habitaciones, oh Israel!', 'cosmic'),
('Old', 'Números', 24, 6, 'Como arroyos están extendidas, Como huertos junto al río, Como lináloes plantados por Jehová, Como cedros junto á las aguas.', 'cosmic'),
('Old', 'Números', 24, 7, 'De sus manos destilarán aguas, Y su simiente será en muchas aguas: Y ensalzarse ha su rey más que Agag, Y su reino será ensalzado.', 'cosmic'),
('Old', 'Números', 24, 8, 'Dios lo sacó de Egipto; Tiene fuerzas como de unicornio: Comerá á las gentes sus enemigas, Y desmenuzará sus huesos, Y asaeteará con sus saetas.', 'cosmic'),
('Old', 'Números', 24, 9, 'Se encorvará para echarse como león, Y como leona; ¿quién lo despertará? Benditos los que te bendijeren, Y malditos los que te maldijeren.', 'cosmic'),
('Old', 'Números', 24, 10, 'Entonces se encendió la ira de Balac contra Balaam, y batiendo sus palmas le dijo: Para maldecir á mis enemigos te he llamado, y he aquí los has resueltamente bendecido ya tres veces.', 'cosmic'),
('Old', 'Números', 24, 11, 'Húyete, por tanto, ahora á tu lugar: yo dije que te honraría, mas he aquí que Jehová te ha privado de honra.', 'cosmic'),
('Old', 'Números', 24, 12, 'Y Balaam le respondió: ¿No lo declaré yo también á tus mensajeros que me enviaste, diciendo:', 'cosmic'),
('Old', 'Números', 24, 13, 'Si Balac me diése su casa llena de plata y oro, yo no podré traspasar el dicho de Jehová para hacer cosa buena ni mala de mi arbitrio; mas lo que Jehová hablare, eso diré yo?', 'cosmic'),
('Old', 'Números', 24, 14, 'He aquí yo me voy ahora á mi pueblo: por tanto, ven, te indicaré lo que este pueblo ha de hacer á tu pueblo en los postrimeros días.', 'cosmic'),
('Old', 'Números', 24, 15, 'Y tomó su parábola, y dijo: Dijo Balaam hijo de Beor, Dijo el varón de ojos abiertos:', 'cosmic'),
('Old', 'Números', 24, 16, 'Dijo el que oyó los dichos de Jehová, Y el que sabe la ciencia del Altísimo, El que vió la visión del Omnipotente; Caído, mas abiertos los ojos:', 'cosmic'),
('Old', 'Números', 24, 17, 'Verélo, mas no ahora: Lo miraré, mas no de cerca: Saldrá ESTRELLA de Jacob, Y levantaráse cetro de Israel, Y herirá los cantones de Moab, Y destruirá á todos los hijos de Seth.', 'cosmic'),
('Old', 'Números', 24, 18, 'Y será tomada Edom, Será también tomada Seir por sus enemigos, E Israel se portará varonilmente.', 'cosmic'),
('Old', 'Números', 24, 19, 'Y el de Jacob se enseñoreará, Y destruirá de la ciudad lo que quedare.', 'cosmic'),
('Old', 'Números', 24, 20, 'Y viendo á Amalec, tomó su parábola, y dijo: Amalec, cabeza de gentes; Mas su postrimería perecerá para siempre.', 'cosmic'),
('Old', 'Números', 24, 21, 'Y viendo al Cineo, tomó su parábola, y dijo: Fuerte es tu habitación, Pon en la peña tu nido:', 'cosmic'),
('Old', 'Números', 24, 22, 'Que el Cineo será echado, Cuando Assur te llevará cautivo.', 'cosmic'),
('Old', 'Números', 24, 23, 'Todavía tomó su parábola, y dijo: ¡Ay! ¿quién vivirá cuando hiciere Dios estas cosas?', 'cosmic'),
('Old', 'Números', 24, 24, 'Y vendrán navíos de la costa de Cittim, Y afligirán á Assur, afligirán también á Eber: Mas él también perecerá para siempre.', 'cosmic'),
('Old', 'Números', 24, 25, 'Entonces se levantó Balaam, y se fué, y volvióse á su lugar: y también Balac se fué por su camino.', 'cosmic'),
('Old', 'Números', 25, 1, 'Y REPOSO Israel en Sittim, y el pueblo empezó á fornicar con las hijas de Moab:', 'cosmic'),
('Old', 'Números', 25, 2, 'Las cuales llamaron al pueblo á los sacrificios de sus dioses: y el pueblo comió, é inclinóse á sus dioses.', 'cosmic'),
('Old', 'Números', 25, 3, 'Y allegóse el pueblo á Baal-peor; y el furor de Jehová se encendió contra Israel.', 'cosmic'),
('Old', 'Números', 25, 4, 'Y Jehová dijo á Moisés: Toma todos los príncipes del pueblo, y ahórcalos á Jehová delante del sol; y la ira del furor de Jehová se apartará de Israel.', 'cosmic'),
('Old', 'Números', 25, 5, 'Entonces Moisés dijo á los jueces de Israel: Matad cada uno á aquellos de los suyos que se han allegado á Baal-peor.', 'cosmic'),
('Old', 'Números', 25, 6, 'Y he aquí un varón de los hijos de Israel vino y trajo una Madianita á sus hermanos, á ojos de Moisés y de toda la congregación de los hijos de Israel, llorando ellos á la puerta del tabernáculo del testimonio.', 'cosmic'),
('Old', 'Números', 25, 7, 'Y viólo Phinees, hijo de Eleazar, hijo de Aarón el sacerdote, y levantóse de en medio de la congregación, y tomó una lanza en su mano:', 'cosmic'),
('Old', 'Números', 25, 8, 'Y fué tras el varón de Israel á la tienda, y alanceólos á ambos, al varón de Israel, y á la mujer por su vientre. Y cesó la mortandad de los hijos de Israel.', 'cosmic'),
('Old', 'Números', 25, 9, 'Y murieron de aquella mortandad veinte y cuatro mil.', 'cosmic'),
('Old', 'Números', 25, 10, 'Entonces Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 25, 11, 'Phinees, hijo de Eleazar, hijo de Aarón el sacerdote, ha hecho tornar mi furor de los hijos de Israel, llevado de celo entre ellos: por lo cual yo no he consumido en mi celo á los hijos de Israel.', 'cosmic'),
('Old', 'Números', 25, 12, 'Por tanto di les: He aquí yo establezco mi pacto de paz con él;', 'cosmic'),
('Old', 'Números', 25, 13, 'Y tendrá él, y su simiente después de él, el pacto del sacerdocio perpetuo; por cuanto tuvo celo por su Dios, é hizo expiación por los hijos de Israel.', 'cosmic'),
('Old', 'Números', 25, 14, 'Y el nombre del varón muerto, que fué muerto con la Madianita, era Zimri hijo de Salu, jefe de una familia de la tribu de Simeón.', 'cosmic'),
('Old', 'Números', 25, 15, 'Y el nombre de la mujer Madianita muerta, era Cozbi, hija de Zur, príncipe de pueblos, padre de familia en Madián.', 'cosmic'),
('Old', 'Números', 25, 16, 'Y Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 25, 17, 'Hostilizaréis á los Madianitas, y los heriréis:', 'cosmic'),
('Old', 'Números', 25, 18, 'Por cuanto ellos os afligieron á vosotros con sus ardides, con que os han engañado en el negocio de Peor, y en el negocio de Cozbi, hija del príncipe de Madián, su hermana, la cual fué muerta el día de la mortandad por causa de Peor.', 'cosmic'),
('Old', 'Números', 26, 1, 'Y ACONTECIO después de la mortandad, que Jehová habló á Moisés, y á Eleazar hijo del sacerdote Aarón, diciendo:', 'cosmic'),
('Old', 'Números', 26, 2, 'Tomad la suma de toda la congregación de los hijos de Israel, de veinte años arriba, por las casas de sus padres, todos los que puedan salir á la guerra en Israel.', 'cosmic'),
('Old', 'Números', 26, 3, 'Y Moisés y Eleazar el sacerdote hablaron con ellos en los campos de Moab, junto al Jordán de Jericó, diciendo:', 'cosmic'),
('Old', 'Números', 26, 4, 'Contaréis el pueblo de veinte años arriba, como mandó Jehová á Moisés y á los hijos de Israel, que habían salido de tierra de Egipto.', 'cosmic'),
('Old', 'Números', 26, 5, 'Rubén primogénito de Israel: los hijos de Rubén: Hanoc, del cual era la familia de los Hanochîtas; de Phallú, la familia de los Phalluitas;', 'cosmic'),
('Old', 'Números', 26, 6, 'De Hesrón, la familia de los Hesronitas; de Carmi, la familia de los Carmitas.', 'cosmic'),
('Old', 'Números', 26, 7, 'Estas son las familias de los Rubenitas: y sus contados fueron cuarenta y tres mil setecientos y treinta.', 'cosmic'),
('Old', 'Números', 26, 8, 'Y los hijos de Phallú: Eliab.', 'cosmic'),
('Old', 'Números', 26, 9, 'Y los hijos de Eliab: Nemuel, y Dathán, y Abiram. Estos Dathán y Abiram fueron los del consejo de la congregación, que hicieron el motín contra Moisés y Aarón con la compañía de Coré, cuando se amotinaron contra Jehová.', 'cosmic'),
('Old', 'Números', 26, 10, 'Que la tierra abrió su boca y tragó á ellos y á Coré, cuando aquella compañía murió, cuando consumió el fuego doscientos y cincuenta varones, los cuales fueron por señal.', 'cosmic'),
('Old', 'Números', 26, 11, 'Mas los hijos de Coré no murieron.', 'cosmic'),
('Old', 'Números', 26, 12, 'Los hijos de Simeón por sus familias: de Nemuel, la familia de los Nemuelitas; de Jamín, la familia de los Jaminitas; de Jachîn, la familia de los Jachînitas;', 'cosmic'),
('Old', 'Números', 26, 13, 'De Zera, la familia de los Zeraitas; de Saul, la familia de los Saulitas.', 'cosmic'),
('Old', 'Números', 26, 14, 'Estas son las familias de los Simeonitas, veinte y dos mil y doscientos.', 'cosmic'),
('Old', 'Números', 26, 15, 'Los hijos de Gad por sus familias: de Zephón, la familia de los Zephonitas; de Aggi, la familia de los Aggitas; de Suni, la familia de los Sunitas;', 'cosmic'),
('Old', 'Números', 26, 16, 'De Ozni, la familia de los Oznitas; de Eri, la familia de los Eritas;', 'cosmic'),
('Old', 'Números', 26, 17, 'De Aroz, la familia de los Aroditas; de Areli, la familia de los Arelitas.', 'cosmic'),
('Old', 'Números', 26, 18, 'Estas son las familias de Gad, por sus contados, cuarenta mil y quinientos.', 'cosmic'),
('Old', 'Números', 26, 19, 'Los hijos de Judá: Er y Onán; y Er y Onán murieron en la tierra de Canaán.', 'cosmic'),
('Old', 'Números', 26, 20, 'Y fueron los hijos de Judá por sus familias: de Sela, la familia de los Selaitas; de Phares, la familia de los Pharesitas; de Zera, la familia de los Zeraitas.', 'cosmic'),
('Old', 'Números', 26, 21, 'Y fueron los hijos de Phares: de Hesrón, la familia de los Hesronitas; de Hamul, la familia de los Hamulitas.', 'cosmic'),
('Old', 'Números', 26, 22, 'Estas son las familias de Judá, por sus contados, setenta y seis mil y quinientos.', 'cosmic'),
('Old', 'Números', 26, 23, 'Los hijos de Issachâr por sus familias: de Thola, la familia de los Tholaitas; de Puá la familia de los Puanitas;', 'cosmic'),
('Old', 'Números', 26, 24, 'De Jasub, la familia de los Jasubitas; de Simron, la familia de los Simronitas.', 'cosmic'),
('Old', 'Números', 26, 25, 'Estas son las familias de Issachâr, por sus contados, sesenta y cuatro mil y trescientos.', 'cosmic'),
('Old', 'Números', 26, 26, 'Los hijos de Zabulón por sus familias: de Sered, la familia de los Sereditas; de Elón, la familia de los Elonitas; de Jalel, la familia de los Jalelitas.', 'cosmic'),
('Old', 'Números', 26, 27, 'Estas son las familias de los Zabulonitas, por sus contados, sesenta mil y quinientos.', 'cosmic'),
('Old', 'Números', 26, 28, 'Los hijos de José por sus familias: Manasés y Ephraim.', 'cosmic'),
('Old', 'Números', 26, 29, 'Los hijos de Manasés: de Machîr, la familia de los Machîritas; y Machîr engendró á Galaad; de Galaad, la familia de los Galaaditas.', 'cosmic'),
('Old', 'Números', 26, 30, 'Estos son los hijos de Galaad: de Jezer, la familia de los Jezeritas; de Helec, la familia de los Helecitas;', 'cosmic'),
('Old', 'Números', 26, 31, 'De Asriel, la familia de los Asrielitas: de Sechêm, la familia de los Sechêmitas;', 'cosmic'),
('Old', 'Números', 26, 32, 'De Semida, la familia de los Semidaitas; de Hepher, la familia de los Hepheritas.', 'cosmic'),
('Old', 'Números', 26, 33, 'Y Salphaad, hijo de Hepher, no tuvo hijos sino hijas: y los nombres de las hijas de Salphaad fueron Maala, y Noa, y Hogla, y Milca, y Tirsa.', 'cosmic'),
('Old', 'Números', 26, 34, 'Estas son las familias de Manasés; y sus contados, cincuenta y dos mil y setecientos.', 'cosmic'),
('Old', 'Números', 26, 35, 'Estos son los hijos de Ephraim por sus familias: de Suthala, la familia de los Suthalaitas; de Bechêr, la familia de los Bechêritas; de Tahan, la familia de los Tahanitas.', 'cosmic'),
('Old', 'Números', 26, 36, 'Y estos son los hijos de Suthala: de Herán, la familia de los Heranitas.', 'cosmic'),
('Old', 'Números', 26, 37, 'Estas son las familias de los hijos de Ephraim, por sus contados, treinta y dos mil y quinientos. Estos son los hijos de José por sus familias.', 'cosmic'),
('Old', 'Números', 26, 38, 'Los hijos de Benjamín por sus familias: de Bela, la familia de los Belaitas; de Asbel, la familia de los Asbelitas; de Achîram, la familia de los Achîramitas;', 'cosmic'),
('Old', 'Números', 26, 39, 'De Supham, la familia de los Suphamitas; de Hupham, la familia de los Huphamitas.', 'cosmic'),
('Old', 'Números', 26, 40, 'Y los hijos de Bela fueron Ard y Naamán: de Ard, la familia de los Arditas; de Naamán, la familia de los Naamanitas.', 'cosmic'),
('Old', 'Números', 26, 41, 'Estos son los hijos de Benjamín por sus familias; y sus contados, cuarenta y cinco mil y seiscientos.', 'cosmic'),
('Old', 'Números', 26, 42, 'Estos son los hijos de Dan por sus familias: de Suham, la familia de los Suhamitas. Estas son las familias de Dan por sus familias.', 'cosmic'),
('Old', 'Números', 26, 43, 'Todas las familias de los Suhamitas, por sus contados, sesenta y cuatro mil y cuatrocientos.', 'cosmic'),
('Old', 'Números', 26, 44, 'Los hijos de Aser por sus familias: de Imna, la familia de los Imnaitas; de Issui, la familia de los Issuitas; de Beria, la familia de los Beriaitas.', 'cosmic'),
('Old', 'Números', 26, 45, 'Los hijos de Beria: de Heber, la familia de los Heberitas; de Malchîel, la familia de los Malchîelitas.', 'cosmic'),
('Old', 'Números', 26, 46, 'Y el nombre de la hija de Aser fué Sera.', 'cosmic'),
('Old', 'Números', 26, 47, 'Estas son las familias de los hijos de Aser, por sus contados, cincuenta y tres mil y cuatrocientos.', 'cosmic'),
('Old', 'Números', 26, 48, 'Los hijos de Nephtalí por sus familias: de Jahzeel, la familia de los Jahzeelitas; de Guni, la familia de los Gunitas;', 'cosmic'),
('Old', 'Números', 26, 49, 'De Jeser, la familia de los Jeseritas; de Sillem, la familia de los Sillemitas.', 'cosmic'),
('Old', 'Números', 26, 50, 'Estas son las familias de Nephtalí por sus familias; y sus contados, cuarenta y cinco mil y cuatrocientos.', 'cosmic'),
('Old', 'Números', 26, 51, 'Estos son los contados de los hijos de Israel, seiscientos y un mil setecientos y treinta.', 'cosmic'),
('Old', 'Números', 26, 52, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 26, 53, 'A estos se repartirá la tierra en heredad, por la cuenta de los nombres.', 'cosmic'),
('Old', 'Números', 26, 54, 'A los más darás mayor heredad, y á los menos menor; y á cada uno se le dará su heredad conforme á sus contados.', 'cosmic'),
('Old', 'Números', 26, 55, 'Empero la tierra será repartida por suerte; y por los nombres de las tribus de sus padres heredarán.', 'cosmic'),
('Old', 'Números', 26, 56, 'Conforme á la suerte será repartida su heredad entre el grande y el pequeño.', 'cosmic'),
('Old', 'Números', 26, 57, 'Y los contados de los Levitas por sus familias son estos: de Gersón, la familia de los Gersonitas; de Coath, la familia de los Coathitas; de Merari, la familia de los Meraritas.', 'cosmic'),
('Old', 'Números', 26, 58, 'Estas son las familias de los Levitas: la familia de los Libnitas, la familia de los Hebronitas, la familia de los Mahalitas, la familia de los Musitas, la familia de los Coritas. Y Coath engendró á Amram.', 'cosmic'),
('Old', 'Números', 26, 59, 'Y la mujer de Amram se llamó Jochâbed, hija de Leví, la cual nació á Leví en Egipto: ésta parió de Amram á Aarón y á Moisés, y á María su hermana.', 'cosmic'),
('Old', 'Números', 26, 60, 'Y á Aarón nacieron Nadab y Abiú, Eleazar é Ithamar.', 'cosmic'),
('Old', 'Números', 26, 61, 'Mas Nadab y Abiú murieron, cuando ofrecieron fuego extraño delante de Jehová.', 'cosmic'),
('Old', 'Números', 26, 62, 'Y los contados de los Levitas fueron veinte y tres mil, todos varones de un mes arriba: porque no fueron contados entre los hijos de Israel, por cuanto no les había de ser dada heredad entre los hijos de Israel.', 'cosmic'),
('Old', 'Números', 26, 63, 'Estos son los contados por Moisés y Eleazar el sacerdote, los cuales contaron los hijos de Israel en los campos de Moab, junto al Jordán de Jericó.', 'cosmic'),
('Old', 'Números', 26, 64, 'Y entre estos ninguno hubo de los contados por Moisés y Aarón el sacerdote, los cuales contaron á los hijos de Israel en el desierto de Sinaí.', 'cosmic'),
('Old', 'Números', 26, 65, 'Porque Jehová les dijo: Han de morir en el desierto: y no quedó varón de ellos, sino Caleb hijo de Jephone, y Josué hijo de Nun.', 'cosmic'),
('Old', 'Números', 27, 1, 'Y LAS hijas de Salphaad, hijo de Hepher, hijo de Galaad, hijo de Machîr, hijo de Manasés, de las familias de Manasés, hijo de José, los nombres de las cuales eran Maala, y Noa, y Hogla, y Milca, y Tirsa, llegaron;', 'cosmic'),
('Old', 'Números', 27, 2, 'Y presentáronse delante de Moisés, y delante del sacerdote Eleazar, y delante de los príncipes, y de toda la congregación, á la puerta del tabernáculo del testimonio, y dijeron:', 'cosmic'),
('Old', 'Números', 27, 3, 'Nuestro padre murió en el desierto, el cual no estuvo en la junta que se reunió contra Jehová en la compañía de Coré: sino que en su pecado murió, y no tuvo hijos.', 'cosmic'),
('Old', 'Números', 27, 4, '¿Por qué será quitado el nombre de nuestro padre de entre su familia, por no haber tenido hijo? Danos heredad entre los hermanos de nuestro padre.', 'cosmic'),
('Old', 'Números', 27, 5, 'Y Moisés llevó su causa delante de Jehová.', 'cosmic'),
('Old', 'Números', 27, 6, 'Y Jehová respondió á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 27, 7, 'Bien dicen las hijas de Salphaad: has de darles posesión de heredad entre los hermanos de su padre; y traspasarás la heredad de su padre á ellas.', 'cosmic'),
('Old', 'Números', 27, 8, 'Y á los hijos de Israel hablarás, diciendo: Cuando alguno muriere sin hijos, traspasaréis su herencia á su hija:', 'cosmic'),
('Old', 'Números', 27, 9, 'Y si no tuviere hija, daréis su herencia á sus hermanos:', 'cosmic'),
('Old', 'Números', 27, 10, 'Y si no tuviere hermanos, daréis su herencia á los hermanos de su padre.', 'cosmic'),
('Old', 'Números', 27, 11, 'Y si su padre no tuviere hermanos, daréis su herencia á su pariente más cercano de su linaje, el cual la poseerá: y será á los hijos de Israel por estatuto de derecho, como Jehová mandó á Moisés.', 'cosmic'),
('Old', 'Números', 27, 12, 'Y Jehová dijo á Moisés: Sube á este monte Abarim, y verás la tierra que he dado á los hijos de Israel.', 'cosmic'),
('Old', 'Números', 27, 13, 'Y después que la habrás visto, tú también serás reunido á tus pueblos, como fué reunido tu hermano Aarón:', 'cosmic'),
('Old', 'Números', 27, 14, 'Pues fuisteis rebeldes á mi dicho en el desierto de Zin, en la rencilla de la congregación, para santificarme en las aguas á ojos de ellos. Estas son las aguas de la rencilla de Cades en el desierto de Zin.', 'cosmic'),
('Old', 'Números', 27, 15, 'Entonces respondió Moisés á Jehová, diciendo:', 'cosmic'),
('Old', 'Números', 27, 16, 'Ponga Jehová, Dios de los espíritus de toda carne, varón sobre la congregación,', 'cosmic'),
('Old', 'Números', 27, 17, 'Que salga delante de ellos, y que entre delante de ellos, que los saque y los introduzca; porque la congregación de Jehová no sea como ovejas sin pastor.', 'cosmic'),
('Old', 'Números', 27, 18, 'Y Jehová dijo á Moisés: Toma á Josué hijo de Nun, varón en el cual hay espíritu, y pondrás tu mano sobre él;', 'cosmic'),
('Old', 'Números', 27, 19, 'Y ponerlo has delante de Eleazar el sacerdote, y delante de toda la congregación; y le darás órdenes en presencia de ellos.', 'cosmic'),
('Old', 'Números', 27, 20, 'Y pondrás de tu dignidad sobre él, para que toda la congregación de los hijos de Israel le obedezcan.', 'cosmic'),
('Old', 'Números', 27, 21, 'Y él estará delante de Eleazar el sacerdote, y á él preguntará por el juicio del Urim delante de Jehová: por el dicho de él saldrán, y por el dicho de él entrarán, él, y todos los hijos de Israel con él, y toda la congregación.', 'cosmic'),
('Old', 'Números', 27, 22, 'Y Moisés hizo como Jehová le había mandado; que tomó á Josué, y le puso delante de Eleazar el sacerdote, y de toda la congregación:', 'cosmic'),
('Old', 'Números', 27, 23, 'Y puso sobre él sus manos, y dióle órdenes, como Jehová había mandado por mano de Moisés.', 'cosmic'),
('Old', 'Números', 28, 1, 'Y HABLO Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 28, 2, 'Manda á los hijos de Israel, y diles: Mi ofrenda, mi pan con mis ofrendas encendidas en olor á mí agradable, guardaréis, ofreciéndomelo á su tiempo.', 'cosmic'),
('Old', 'Números', 28, 3, 'Y les dirás: Esta es la ofrenda encendida que ofreceréis á Jehová: dos corderos sin tacha de un año, cada un día, será el holocausto continuo.', 'cosmic'),
('Old', 'Números', 28, 4, 'El un cordero ofrecerás por la mañana, y el otro cordero ofrecerás entre las dos tardes:', 'cosmic'),
('Old', 'Números', 28, 5, 'Y la décima de un epha de flor de harina, amasada con una cuarta de un hin de aceite molido, en presente.', 'cosmic'),
('Old', 'Números', 28, 6, 'Es holocausto continuo, que fué hecho en el monte de Sinaí en olor de suavidad, ofrenda encendida á Jehová.', 'cosmic'),
('Old', 'Números', 28, 7, 'Y su libación, la cuarta de un hin con cada cordero: derramarás libación de superior vino á Jehová en el santuario.', 'cosmic'),
('Old', 'Números', 28, 8, 'Y ofrecerás el segundo cordero entre las dos tardes: conforme á la ofrenda de la mañana, y conforme á su libación ofrecerás, ofrenda encendida en olor de suavidad á Jehová.', 'cosmic'),
('Old', 'Números', 28, 9, 'Mas el día del sábado dos corderos de un año sin defecto, y dos décimas de flor de harina amasada con aceite, por presente, con su libación:', 'cosmic'),
('Old', 'Números', 28, 10, 'Es el holocausto del sábado en cada sábado, además del holocausto continuo y su libación.', 'cosmic'),
('Old', 'Números', 28, 11, 'Y en los principios de vuestros meses ofreceréis en holocausto á Jehová dos becerros de la vacada, y un carnero, y siete corderos de un año sin defecto;', 'cosmic'),
('Old', 'Números', 28, 12, 'Y tres décimas de flor de harina amasada con aceite, por presente con cada becerro; y dos décimas de flor de harina amasada con aceite, por presente con cada carnero;', 'cosmic'),
('Old', 'Números', 28, 13, 'Y una décima de flor de harina amasada con aceite, en ofrenda por presente con cada cordero: holocausto de olor suave, ofrenda encendida á Jehová.', 'cosmic'),
('Old', 'Números', 28, 14, 'Y sus libaciones de vino, medio hin con cada becerro, y el tercio de un hin con cada carnero, y la cuarta de un hin con cada cordero. Este es el holocausto de cada mes por todos los meses del año.', 'cosmic'),
('Old', 'Números', 28, 15, 'Y un macho cabrío en expiación se ofrecerá á Jehová, además del holocausto continuo con su libación.', 'cosmic'),
('Old', 'Números', 28, 16, 'Mas en el mes primero, á los catorce del mes será la pascua de Jehová.', 'cosmic'),
('Old', 'Números', 28, 17, 'Y á los quince días de aqueste mes, la solemnidad: por siete días se comerán ázimos.', 'cosmic'),
('Old', 'Números', 28, 18, 'El primer día, santa convocación; ninguna obra servil haréis:', 'cosmic'),
('Old', 'Números', 28, 19, 'Y ofreceréis por ofrenda encendida en holocausto á Jehová dos becerros de la vacada, y un carnero, y siete corderos de un año: sin defecto los tomaréis:', 'cosmic'),
('Old', 'Números', 28, 20, 'Y su presente de harina amasada con aceite: tres décimas con cada becerro, y dos décimas con cada carnero ofreceréis;', 'cosmic'),
('Old', 'Números', 28, 21, 'Con cada uno de los siete corderos ofreceréis una décima;', 'cosmic'),
('Old', 'Números', 28, 22, 'Y un macho cabrío por expiación, para reconciliaros.', 'cosmic'),
('Old', 'Números', 28, 23, 'Esto ofreceréis además del holocausto de la mañana, que es el holocausto continuo.', 'cosmic'),
('Old', 'Números', 28, 24, 'Conforme á esto ofreceréis cada uno de los siete días, vianda y ofrenda encendida en olor de suavidad á Jehová; ofrecerse ha, además del holocausto continuo, con su libación.', 'cosmic'),
('Old', 'Números', 28, 25, 'Y el séptimo día tendréis santa convocación: ninguna obra servil haréis.', 'cosmic'),
('Old', 'Números', 28, 26, 'Además el día de las primicias, cuando ofreciereis presente nuevo á Jehová en vuestras semanas, tendréis santa convocación: ninguna obra servil haréis:', 'cosmic'),
('Old', 'Números', 28, 27, 'Y ofreceréis en holocausto, en olor de suavidad á Jehová, dos becerros de la vacada, un carnero, siete corderos de un año:', 'cosmic'),
('Old', 'Números', 28, 28, 'Y el presente de ellos, flor de harina amasada con aceite, tres décimas con cada becerro, dos décimas con cada carnero,', 'cosmic'),
('Old', 'Números', 28, 29, 'Con cada uno de los siete corderos una décima;', 'cosmic'),
('Old', 'Números', 28, 30, 'Un macho cabrío, para hacer expiación por vosotros.', 'cosmic'),
('Old', 'Números', 28, 31, 'Los ofreceréis, además del holocausto continuo con sus presentes, y sus libaciones: sin defecto los tomaréis.', 'cosmic'),
('Old', 'Números', 29, 1, 'Y EL séptimo mes, al primero del mes tendréis santa convocación: ninguna obra servil haréis; os será día de sonar las trompetas.', 'cosmic'),
('Old', 'Números', 29, 2, 'Y ofreceréis holocausto por olor de suavidad á Jehová, un becerro de la vacada, un carnero, siete corderos de un año sin defecto;', 'cosmic'),
('Old', 'Números', 29, 3, 'Y el presente de ellos, de flor de harina amasada con aceite, tres décimas con cada becerro, dos décimas con cada carnero,', 'cosmic'),
('Old', 'Números', 29, 4, 'Y con cada uno de los siete corderos, una décima;', 'cosmic'),
('Old', 'Números', 29, 5, 'Y un macho cabrío por expiación, para reconciliaros:', 'cosmic'),
('Old', 'Números', 29, 6, 'Además del holocausto del mes, y su presente, y el holocausto continuo y su presente, y sus libaciones, conforme á su ley, por ofrenda encendida á Jehová en olor de suavidad.', 'cosmic'),
('Old', 'Números', 29, 7, 'Y en el diez de este mes séptimo tendréis santa convocación, y afligiréis vuestras almas: ninguna obra haréis:', 'cosmic'),
('Old', 'Números', 29, 8, 'Y ofreceréis en holocausto á Jehová por olor de suavidad, un becerro de la vacada, un carnero, siete corderos de un año; sin defecto los tomaréis:', 'cosmic'),
('Old', 'Números', 29, 9, 'Y sus presentes, flor de harina amasada con aceite, tres décimas con cada becerro, dos décimas con cada carnero,', 'cosmic'),
('Old', 'Números', 29, 10, 'Y con cada uno de los siete corderos, una décima;', 'cosmic'),
('Old', 'Números', 29, 11, 'Un macho cabrío por expiación: además de la ofrenda de las expiaciones por el pecado, y del holocausto continuo, y de sus presentes, y de sus libaciones.', 'cosmic'),
('Old', 'Números', 29, 12, 'También á los quince días del mes séptimo tendréis santa convocación; ninguna obra servil haréis, y celebraréis solemnidad á Jehová por siete días;', 'cosmic'),
('Old', 'Números', 29, 13, 'Y ofreceréis en holocausto, en ofrenda encendida á Jehová en olor de suavidad, trece becerros de la vacada, dos carneros, catorce corderos de un año: han de ser sin defecto;', 'cosmic'),
('Old', 'Números', 29, 14, 'Y los presentes de ellos, de flor de harina amasada con aceite, tres décimas con cada uno de los trece becerros, dos décimas con cada uno de los dos carneros,', 'cosmic'),
('Old', 'Números', 29, 15, 'Y con cada uno de los catorce corderos, una décima;', 'cosmic'),
('Old', 'Números', 29, 16, 'Y un macho cabrío por expiación: además del holocausto continuo, su presente y su libación.', 'cosmic'),
('Old', 'Números', 29, 17, 'Y el segundo día, doce becerros de la vacada, dos carneros, catorce corderos de un año sin defecto;', 'cosmic'),
('Old', 'Números', 29, 18, 'Y sus presentes y sus libaciones con los becerros, con los carneros, y con los corderos, según el número de ellos, conforme á la ley;', 'cosmic'),
('Old', 'Números', 29, 19, 'Y un macho cabrío por expiación: además del holocausto continuo, y su presente y su libación.', 'cosmic'),
('Old', 'Números', 29, 20, 'Y el día tercero, once becerros, dos carneros, catorce corderos de un año sin defecto;', 'cosmic'),
('Old', 'Números', 29, 21, 'Y sus presentes y sus libaciones con los becerros, con los carneros, y con los corderos, según el número de ellos, conforme á la ley;', 'cosmic'),
('Old', 'Números', 29, 22, 'Y un macho cabrío por expiación: además del holocausto continuo, y su presente y su libación.', 'cosmic'),
('Old', 'Números', 29, 23, 'Y el cuarto día, diez becerros, dos carneros, catorce corderos de un año sin defecto;', 'cosmic'),
('Old', 'Números', 29, 24, 'Sus presentes y sus libaciones con los becerros, con los carneros, y con los corderos, según el número de ellos, conforme á la ley;', 'cosmic'),
('Old', 'Números', 29, 25, 'Y un macho cabrío por expiación: además del holocausto continuo, su presente y su libación.', 'cosmic'),
('Old', 'Números', 29, 26, 'Y el quinto día, nueve becerros, dos carneros, catorce corderos de un año sin defecto;', 'cosmic'),
('Old', 'Números', 29, 27, 'Y sus presentes y sus libaciones con los becerros, con los carneros, y con los corderos, según el número de ellos, conforme á la ley;', 'cosmic'),
('Old', 'Números', 29, 28, 'Y un macho cabrío por expiación: además del holocausto continuo, su presente y su libación.', 'cosmic'),
('Old', 'Números', 29, 29, 'Y el sexto día, ocho becerros, dos carneros, catorce corderos de un año sin defecto;', 'cosmic'),
('Old', 'Números', 29, 30, 'Y sus presentes y sus libaciones con los becerros, con los carneros, y con los corderos, según el número de ellos, conforme á la ley;', 'cosmic'),
('Old', 'Números', 29, 31, 'Y un macho cabrío por expiación: además del holocausto continuo, su presente y sus libaciones.', 'cosmic'),
('Old', 'Números', 29, 32, 'Y el séptimo día, siete becerros, dos carneros, catorce corderos de un año sin defecto;', 'cosmic'),
('Old', 'Números', 29, 33, 'Y sus presentes y sus libaciones con los becerros, con los carneros, y con los corderos, según el número de ellos, conforme á la ley;', 'cosmic'),
('Old', 'Números', 29, 34, 'Y un macho cabrío por expiación: además del holocausto continuo, con su presente y su libación.', 'cosmic'),
('Old', 'Números', 29, 35, 'El octavo día tendréis solemnidad: ninguna obra servil haréis:', 'cosmic'),
('Old', 'Números', 29, 36, 'Y ofreceréis en holocausto, en ofrenda encendida de olor suave á Jehová, un novillo, un carnero, siete corderos de un año sin defecto;', 'cosmic'),
('Old', 'Números', 29, 37, 'Sus presentes y sus libaciones con el novillo, con el carnero, y con los corderos, según el número de ellos, conforme á la ley;', 'cosmic'),
('Old', 'Números', 29, 38, 'Y un macho cabrío por expiación: además del holocausto continuo, con su presente y su libación.', 'cosmic'),
('Old', 'Números', 29, 39, 'Estas cosas ofreceréis á Jehová en vuestras solemnidades, además de vuestros votos, y de vuestras ofrendas libres, para vuestros holocaustos, y para vuestros presentes, y para vuestras libaciones y para vuestras paces.', 'cosmic'),
('Old', 'Números', 29, 40, '(30-1) Y MOISÉS dijo á los hijos de Israel, conforme á todo lo que Jehová le había mandado.', 'cosmic'),
('Old', 'Números', 30, 1, '(30-2) Y habló Moisés á los príncipes de las tribus de los hijos de Israel, diciendo: Esto es lo que Jehová ha mandado.', 'cosmic'),
('Old', 'Números', 30, 2, '(30-3) Cuando alguno hiciere voto á Jehová, ó hiciere juramento ligando su alma con obligación, no violará su palabra: hará conforme á todo lo que salió de su boca.', 'cosmic'),
('Old', 'Números', 30, 3, '(30-4) Mas la mujer, cuando hiciere voto á Jehová, y se ligare con obligación en casa de su padre, en su mocedad;', 'cosmic'),
('Old', 'Números', 30, 4, '(30-5) Si su padre oyere su voto, y la obligación con que ligó su alma, y su padre callare á ello, todos los votos de ella serán firmes, y toda obligación con que hubiere ligado su alma, firme será.', 'cosmic'),
('Old', 'Números', 30, 5, '(30-6) Mas si su padre le vedare el día que oyere todos sus votos y sus obligaciones, con que ella hubiere ligado su alma, no serán firmes; y Jehová la perdonará, por cuanto su padre le vedó.', 'cosmic'),
('Old', 'Números', 30, 6, '(30-7) Empero si fuére casada, é hiciere votos, o pronunciare de sus labios cosa con que obligue su alma;', 'cosmic'),
('Old', 'Números', 30, 7, '(30-8) Si su marido lo oyere, y cuando lo oyere callare á ello, los votos de ella serán firmes, y la obligación con que ligó su alma, firme será.', 'cosmic'),
('Old', 'Números', 30, 8, '(30-9) Pero si cuando su marido lo oyó, le vedó, entonces el voto que ella hizo, y lo que pronunció de sus labios con que ligó su alma, será nulo; y Jehová lo perdonará.', 'cosmic'),
('Old', 'Números', 30, 9, '(30-10) Mas todo voto de viuda, ó repudiada, con que ligare su alma, será firme.', 'cosmic'),
('Old', 'Números', 30, 10, '(30-11) Y si hubiere hecho voto en casa de su marido, y hubiere ligado su alma con obligación de juramento,', 'cosmic'),
('Old', 'Números', 30, 11, '(30-12) Si su marido oyó, y calló á ello, y no le vedó; entonces todos sus votos serán firmes, y toda obligación con que hubiere ligado su alma, firme será.', 'cosmic'),
('Old', 'Números', 30, 12, '(30-13) Mas si su marido los anuló el día que los oyó; todo lo que salió de sus labios cuanto á sus votos, y cuanto á la obligación de su alma, será nulo; su marido los anuló, y Jehová la perdonará.', 'cosmic'),
('Old', 'Números', 30, 13, '(30-14) Todo voto, ó todo juramento obligándose á afligir el alma, su marido lo confirmará, ó su marido lo anulará.', 'cosmic'),
('Old', 'Números', 30, 14, '(30-15) Empero si su marido callare á ello de día en día, entonces confirmó todos sus votos, y todas las obligaciones que están sobre ella: confirmólas, por cuanto calló á ello el día que lo oyó.', 'cosmic'),
('Old', 'Números', 30, 15, '(30-16) Mas si las anulare después de haberlas oido, entonces él llevará el pecado de ella.', 'cosmic'),
('Old', 'Números', 30, 16, '(30-17) Estas son las ordenanzas que Jehová mandó á Moisés entre el varón y su mujer, entre el padre y su hija, durante su mocedad en casa de su padre.', 'cosmic'),
('Old', 'Números', 31, 1, 'Y JEHOVA habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 31, 2, 'Haz la venganza de los hijos de Israel sobre los Madianitas; después serás recogido á tus pueblos.', 'cosmic'),
('Old', 'Números', 31, 3, 'Entonces Moisés habló al pueblo, diciendo: Armaos algunos de vosotros para la guerra, é irán contra Madián, y harán la venganza de Jehová en Madián.', 'cosmic'),
('Old', 'Números', 31, 4, 'Mil de cada tribu de todas las tribus de los hijos de Israel, enviaréis á la guerra.', 'cosmic'),
('Old', 'Números', 31, 5, 'Así fueron dados de los millares de Israel, mil por cada tribu, doce mil á punto de guerra.', 'cosmic'),
('Old', 'Números', 31, 6, 'Y Moisés los envió á la guerra: mil por cada tribu envió: y Phinees, hijo de Eleazar sacerdote, fué á la guerra con los santos instrumentos, con las trompetas en su mano para tocar.', 'cosmic'),
('Old', 'Números', 31, 7, 'Y pelearon contra Madián, como Jehová lo mandó á Moisés, y mataron á todo varón.', 'cosmic'),
('Old', 'Números', 31, 8, 'Mataron también, entre los muertos de ellos, á los reyes de Madián: Evi, y Recem, y Zur, y Hur, y Reba, cinco reyes de Madián; á Balaam también, hijo de Beor, mataron á cuchillo.', 'cosmic'),
('Old', 'Números', 31, 9, 'Y llevaron cautivas los hijos de Israel las mujeres de los Madianitas, y sus chiquitos y todas sus bestias, y todos sus ganados; y arrebataron toda su hacienda.', 'cosmic'),
('Old', 'Números', 31, 10, 'Y abrasaron con fuego todas sus ciudades, aldeas y castillos.', 'cosmic'),
('Old', 'Números', 31, 11, 'Y tomaron todo el despojo, y toda la presa, así de hombres como de bestias.', 'cosmic'),
('Old', 'Números', 31, 12, 'Y trajeron á Moisés, y á Eleazar el sacerdote, y á la congregación de los hijos de Israel, los cautivos y la presa y los despojos, al campo en los llanos de Moab, que están junto al Jordán de Jericó.', 'cosmic'),
('Old', 'Números', 31, 13, 'Y salieron Moisés y Eleazar el sacerdote, y todos los príncipes de la congregación, á recibirlos fuera del campo.', 'cosmic'),
('Old', 'Números', 31, 14, 'Y enojóse Moisés contra los capitanes del ejército, contra los tribunos y centuriones que volvían de la guerra;', 'cosmic'),
('Old', 'Números', 31, 15, 'Y díjoles Moisés: ¿Todas las mujeres habéis reservado?', 'cosmic'),
('Old', 'Números', 31, 16, 'He aquí ellas fueron á los hijos de Israel, por consejo de Balaam, para causar prevaricación contra Jehová en el negocio de Peor; por lo que hubo mortandad en la congregación de Jehová.', 'cosmic'),
('Old', 'Números', 31, 17, 'Matad pues ahora todos los varones entre los niños: matad también toda mujer que haya conocido varón carnalmente.', 'cosmic'),
('Old', 'Números', 31, 18, 'Y todas las niñas entre las mujeres, que no hayan conocido ayuntamiento de varón, os reservaréis vivas.', 'cosmic'),
('Old', 'Números', 31, 19, 'Y vosotros quedaos fuera del campo siete días: y todos los que hubieren matado persona, y cualquiera que hubiere tocado muerto, os purificaréis al tercero y al séptimo día, vosotros y vuestros cautivos.', 'cosmic'),
('Old', 'Números', 31, 20, 'Asimismo purificaréis todo vestido, y toda prenda de pieles, y toda obra de pelos de cabra, y todo vaso de madera.', 'cosmic'),
('Old', 'Números', 31, 21, 'Y Eleazar el sacerdote dijo á los hombres de guerra que venían de la guerra: Esta es la ordenanza de la ley que Jehová ha mandado á Moisés:', 'cosmic'),
('Old', 'Números', 31, 22, 'Ciertamente el oro, y la plata, metal, hierro, estaño, y plomo,', 'cosmic'),
('Old', 'Números', 31, 23, 'Todo lo que resiste el fuego, por fuego lo haréis pasar, y será limpio, bien que en las aguas de purificación habrá de purificarse: mas haréis pasar por agua todo lo que no aguanta el fuego.', 'cosmic'),
('Old', 'Números', 31, 24, 'Además lavaréis vuestros vestidos el séptimo día, y así seréis limpios; y después entraréis en el campo.', 'cosmic'),
('Old', 'Números', 31, 25, 'Y Jehová habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 31, 26, 'Toma la cuenta de la presa que se ha hecho, así de las personas como de las bestias, tú y el sacerdote Eleazar, y las cabezas de los padres de la congregación:', 'cosmic'),
('Old', 'Números', 31, 27, 'Y partirás por mitad la presa entre los que pelearon, los que salieron á la guerra, y toda la congregación.', 'cosmic'),
('Old', 'Números', 31, 28, 'Y apartarás para Jehová el tributo de los hombres de guerra, que salieron á la guerra: de quinientos uno, así de las personas como de los bueyes, de los asnos, y de las ovejas:', 'cosmic'),
('Old', 'Números', 31, 29, 'De la mitad de ellos lo tomarás; y darás á Eleazar el sacerdote la ofrenda de Jehová.', 'cosmic'),
('Old', 'Números', 31, 30, 'Y de la mitad perteneciente á los hijos de Israel tomarás uno de cincuenta, de las personas, de los bueyes, de los asnos, y de las ovejas, de todo animal; y los darás á los Levitas, que tienen la guarda del tabernáculo de Jehová.', 'cosmic'),
('Old', 'Números', 31, 31, 'E hicieron Moisés y Eleazar el sacerdote como Jehová mandó á Moisés.', 'cosmic'),
('Old', 'Números', 31, 32, 'Y fué la presa, el resto de la presa que tomaron los hombres de guerra, seiscientas y setenta y cinco mil ovejas,', 'cosmic'),
('Old', 'Números', 31, 33, 'Y setenta y dos mil bueyes,', 'cosmic'),
('Old', 'Números', 31, 34, 'Y setenta y un mil asnos;', 'cosmic'),
('Old', 'Números', 31, 35, 'Y en cuanto á personas, de mujeres que no habían conocido ayuntamiento de varón, en todas trenita y dos mil.', 'cosmic'),
('Old', 'Números', 31, 36, 'Y la mitad, la parte de los que habían salido á la guerra, fué el número de trescientas treinta y siete mil y quinientas ovejas.', 'cosmic'),
('Old', 'Números', 31, 37, 'Y el tributo para Jehová de la ovejas, fué seiscientas setenta y cinco.', 'cosmic'),
('Old', 'Números', 31, 38, 'Y de los bueyes, treinta y seis mil: y de ellos el tributo para Jehová, setenta y dos.', 'cosmic'),
('Old', 'Números', 31, 39, 'Y de los asnos, treinta mil y quinientos: y de ellos el tributo para Jehová, setenta y uno.', 'cosmic'),
('Old', 'Números', 31, 40, 'Y de las personas, diez y seis mil: y de ellas el tributo para Jehová, trteinta y dos personas.', 'cosmic'),
('Old', 'Números', 31, 41, 'Y dió Moisés el tributo, por elevada ofrenda á Jehová, á Eleazar el sacerdote, como Jehová lo mandó á Moisés.', 'cosmic'),
('Old', 'Números', 31, 42, 'Y de la mitad para los hijos de Israel, que apartó Moisés de los hombres que habían ido á la guerra;', 'cosmic'),
('Old', 'Números', 31, 43, '(La mitad para la congregación fué: de las ovejas, trescientas treinta y siete mil y quinientas;', 'cosmic'),
('Old', 'Números', 31, 44, 'Y de los bueyes, treinta y seis mil;', 'cosmic'),
('Old', 'Números', 31, 45, 'Y de los asnos, treinta mil y quinientos;', 'cosmic'),
('Old', 'Números', 31, 46, 'Y de las personas, diez y seis mil:)', 'cosmic'),
('Old', 'Números', 31, 47, 'De la mitad, pues, para los hijos de Israel tomó Moisés uno de cada cincuenta, así de las personas como de los animales, y diólos á los Levitas, que tenían la guarda del tabernáculo de Jehová; como Jehová lo había mandado á Moisés.', 'cosmic'),
('Old', 'Números', 31, 48, 'Y llegaron á Moisés los jefes de los millares de aquel ejército, los tribunos y centuriones;', 'cosmic'),
('Old', 'Números', 31, 49, 'Y dijeron á Moisés: Tus siervos han tomado razón de los hombres de guerra que están en nuestro poder, y ninguno ha faltado de nosotros.', 'cosmic'),
('Old', 'Números', 31, 50, 'Por lo cual hemos ofrecido á Jehová ofrenda, cada uno de lo que ha hallado, vasos de oro, brazaletes, manillas, anillos, zarcillos, y cadenas, para hacer expiación por nuestras almas delante de Jehová.', 'cosmic'),
('Old', 'Números', 31, 51, 'Y Moisés y el sacerdote Eleazar recibieron el oro de ellos, alhajas, todas elaboradas.', 'cosmic'),
('Old', 'Números', 31, 52, 'Y todo el oro de la ofrenda que ofrecieron á Jehová de los tribunos y centuriones, fué diez y seis mil setecientos y cincuenta siclos.', 'cosmic'),
('Old', 'Números', 31, 53, 'Los hombres del ejército habían pillado cada uno para sí.', 'cosmic'),
('Old', 'Números', 31, 54, 'Recibieron, pues, Moisés y el sacerdote Eleazar, el oro de los tribunos y centuriones, y trajéronlo al tabernáculo del testimonio, por memoria de los hijos de Israel delante de Jehová.', 'cosmic'),
('Old', 'Números', 32, 1, 'Y LOS hijos de Rubén y los hijos de Gad tenían una muy grande muchedumbre de ganado; los cuales viendo la tierra de Jazer y de Galaad, parecióles el país lugar de ganado.', 'cosmic'),
('Old', 'Números', 32, 2, 'Y vinieron los hijos de Gad y los hijos de Rubén, y hablaron á Moisés, y á Eleazar el sacerdote, y á los príncipes de la congregación, diciendo:', 'cosmic'),
('Old', 'Números', 32, 3, 'Ataroth, y Dibón, y Jazer, y Nimra, y Hesbón, y Eleale, y Sabán, y Nebo, y Beón,', 'cosmic'),
('Old', 'Números', 32, 4, 'La tierra que Jehová hirió delante de la congregación de Israel, es tierra de ganado, y tus siervos tienen ganado.', 'cosmic'),
('Old', 'Números', 32, 5, 'Por tanto, dijeron, si hallamos gracia en tus ojos, dése esta tierra á tus siervos en heredad, y no nos hagas pasar el Jordán.', 'cosmic'),
('Old', 'Números', 32, 6, 'Y respondió Moisés á los hijos de Gad y á los hijos de Rubén: ¿Vendrán vuestros hermanos á la guerra, y vosotros os quedaréis aquí?', 'cosmic'),
('Old', 'Números', 32, 7, '¿Y por qué prevenís el ánimo de los hijos de Israel, para que no pasen á la tierra que les ha dado Jehová?', 'cosmic'),
('Old', 'Números', 32, 8, 'Así hicieron vuestros padres, cuando los envié desde Cades-barnea para que viesen la tierra.', 'cosmic'),
('Old', 'Números', 32, 9, 'Que subieron hasta la arroyada de Escol, y después que vieron la tierra, preocuparon el ánimo de los hijos de Israel, para que no viniesen á la tierra que Jehová les había dado.', 'cosmic'),
('Old', 'Números', 32, 10, 'Y el furor de Jehová se encendió entonces, y juró diciendo:', 'cosmic'),
('Old', 'Números', 32, 11, 'Que no verán los varones que subieron de Egipto de veinte años arriba, la tierra por la cual juré á Abraham, Isaac, y Jacob, por cuanto no fueron perfectos en pos de mí;', 'cosmic'),
('Old', 'Números', 32, 12, 'Excepto Caleb, hijo de Jephone Cenezeo, y Josué hijo de Nun, que fueron perfectos en pos de Jehová.', 'cosmic'),
('Old', 'Números', 32, 13, 'Y el furor de Jehová se encendió en Israel, é hízolos andar errantes cuarenta años por el desierto, hasta que fué acabada toda aquella generación, que había hecho mal delante de Jehová.', 'cosmic'),
('Old', 'Números', 32, 14, 'Y he aquí vosotros habéis sucedido en lugar de vuestros padres, prole de hombres pecadores, para añadir aún á la ira de Jehová contra Israel.', 'cosmic'),
('Old', 'Números', 32, 15, 'Si os volviereis de en pos de él, él volverá otra vez á dejaros en el desierto, y destruiréis á todo este pueblo.', 'cosmic'),
('Old', 'Números', 32, 16, 'Entonces ellos se allegaron á él y dijeron: Edificaremos aquí majadas para nuestro ganado, y ciudades para nuestros niños;', 'cosmic'),
('Old', 'Números', 32, 17, 'Y nosotros nos armaremos, é iremos con diligencia delante de los hijos de Israel, hasta que los metamos en su lugar: y nuestros niños quedarán en ciudades fuertes á causa de los moradores del país.', 'cosmic'),
('Old', 'Números', 32, 18, 'No volveremos á nuestras casas hasta que los hijos de Israel posean cada uno su heredad.', 'cosmic'),
('Old', 'Números', 32, 19, 'Porque no tomaremos heredad con ellos al otro lado del Jordán ni adelante, por cuanto tendremos ya nuestra heredad de estotra parte del Jordán al oriente.', 'cosmic'),
('Old', 'Números', 32, 20, 'Entonces les respondió Moisés: Si lo hiciereis así, si os apercibiereis para ir delante de Jehová á la guerra,', 'cosmic'),
('Old', 'Números', 32, 21, 'Y pasareis todos vosotros armados el Jordán delante de Jehová, hasta que haya echado á sus enemigos de delante de sí,', 'cosmic'),
('Old', 'Números', 32, 22, 'Y sea el país sojuzgado delante de Jehová; luego volveréis, y seréis libres de culpa para con Jehová, y para con Israel; y esta tierra será vuestra en heredad delante de Jehová.', 'cosmic'),
('Old', 'Números', 32, 23, 'Mas si así no lo hiciereis, he aquí habréis pecado á Jehová; y sabed que os alcanzará vuestro pecado.', 'cosmic'),
('Old', 'Números', 32, 24, 'Edificaos ciudades para vuestros niños, y majadas para vuestras ovejas, y haced lo que ha salido de vuestra boca.', 'cosmic'),
('Old', 'Números', 32, 25, 'Y hablaron los hijos de Gad y los hijos de Rubén á Moisés, diciendo: Tus siervos harán como mi señor ha mandado.', 'cosmic'),
('Old', 'Números', 32, 26, 'Nuestros niños, nuestras mujeres, nuestros ganados, y todas nuestras bestias, estarán ahí en las ciudades de Galaad;', 'cosmic'),
('Old', 'Números', 32, 27, 'Y tus siervos, armados todos de guerra, pasarán delante de Jehová á la guerra, de la manera que mi señor dice.', 'cosmic'),
('Old', 'Números', 32, 28, 'Entonces los encomendó Moisés á Eleazar el sacerdote, y á Josué hijo de Nun, y á los príncipes de los padres de las tribus de los hijos de Israel.', 'cosmic'),
('Old', 'Números', 32, 29, 'Y díjoles Moisés: Si los hijos de Gad y los hijos de Rubén, pasaren con vosotros el Jordán, armados todos de guerra delante de Jehová, luego que el país fuere sojuzgado delante de vosotros, les daréis la tierra de Galaad en posesión:', 'cosmic'),
('Old', 'Números', 32, 30, 'Mas si no pasaren armados con vosotros, entonces tendrán posesión entre vosotros en la tierra de Canaán.', 'cosmic'),
('Old', 'Números', 32, 31, 'Y los hijos de Gad y los hijos de Rubén respondieron, diciendo: Haremos lo que Jehová ha dicho á tus siervos.', 'cosmic'),
('Old', 'Números', 32, 32, 'Nosotros pasaremos armados delante de Jehová á la tierra de Canaán, y la posesión de nuestra heredad será de esta parte del Jordán.', 'cosmic'),
('Old', 'Números', 32, 33, 'Así les dió Moisés á los hijos de Gad y á los hijos de Rubén, y á la media tribu de Manasés hijo de José, el reino de Sehón rey Amorrheo, y el reino de Og rey de Basán, la tierra con sus ciudades y términos, las ciudades del país alrededor.', 'cosmic'),
('Old', 'Números', 32, 34, 'Y los hijos de Gad edificaron á Dibón, y á Ataroth, y á Aroer,', 'cosmic'),
('Old', 'Números', 32, 35, 'Y á Atroth-sophan, y á Jazer, y á Jogbaa,', 'cosmic'),
('Old', 'Números', 32, 36, 'Y á Beth-nimra, y á Betharán: ciudades fuertes, y también majadas para ovejas.', 'cosmic'),
('Old', 'Números', 32, 37, 'Y los hijos de Rubén edificaron á Hesbón, y á Eleale, y á Kiriathaim,', 'cosmic'),
('Old', 'Números', 32, 38, 'Y á Nebo, y á Baal-meón, (mudados los nombres), y á Sibma: y pusieron nombres á las ciudades que edificaron.', 'cosmic'),
('Old', 'Números', 32, 39, 'Y los hijos de Machîr hijo de Manasés fueron á Galaad, y tomáronla, y echaron al Amorrheo que estaba en ella.', 'cosmic'),
('Old', 'Números', 32, 40, 'Y Moisés dió Galaad á Machîr hijo de Manasés, el cual habitó en ella.', 'cosmic'),
('Old', 'Números', 32, 41, 'También Jair hijo de Manasés fué y tomó sus aldeas, y púsoles por nombre Havoth-jair.', 'cosmic'),
('Old', 'Números', 32, 42, 'Asimismo Noba fué y tomó á Kenath y sus aldeas, y llamóle Noba, conforme á su nombre.', 'cosmic'),
('Old', 'Números', 33, 1, 'Estas son las estancias de los hijos de Israel, los cuales salieron de la tierra de Egipto por sus escuadrones, bajo la conducta de Moisés y Aarón.', 'cosmic'),
('Old', 'Números', 33, 2, 'Y Moisés escribió sus salidas conforme á sus jornadas por mandato de Jehová. Estas, pues, son sus estancias con arreglo á sus partidas.', 'cosmic'),
('Old', 'Números', 33, 3, 'De Rameses partieron en el mes primero, á los quince diás del mes primero: el segundo día de la pascua salieron los hijos de Israel con mano alta, á ojos de todos los Egipcios.', 'cosmic'),
('Old', 'Números', 33, 4, 'Estaban enterrando los Egipcios los que Jehová había muerto de ellos, á todo primogénito; habiendo Jehová hecho también juicios en sus dioses.', 'cosmic'),
('Old', 'Números', 33, 5, 'Partieron, pues, los hijos de Israel de Rameses, y asentaron campo en Succoth.', 'cosmic'),
('Old', 'Números', 33, 6, 'Y partiendo de Succoth, asentaron en Etham, que está al cabo del desierto.', 'cosmic'),
('Old', 'Números', 33, 7, 'Y partiendo de Etham, volvieron sobre Pi-hahiroth, que está delante de Baalsephon, y asentaron delante de Migdol.', 'cosmic'),
('Old', 'Números', 33, 8, 'Y partiendo de Pi-hahiroth, pasaron por medio de la mar al desierto, y anduvieron camino de tres días por el desierto de Etham, y asentaron en Mara.', 'cosmic'),
('Old', 'Números', 33, 9, 'Y partiendo de Mara, vinieron á Elim, donde había doce fuentes de aguas, y setenta palmeras; y asentaron allí.', 'cosmic'),
('Old', 'Números', 33, 10, 'Y partidos de Elim, asentaron junto al mar Bermejo.', 'cosmic'),
('Old', 'Números', 33, 11, 'Y partidos del mar Bermejo, asentaron en el desierto de Sin.', 'cosmic'),
('Old', 'Números', 33, 12, 'Y partidos del desierto de Sin, asentaron en Dophca.', 'cosmic'),
('Old', 'Números', 33, 13, 'Y partidos de Dophca, asentaron en Alús.', 'cosmic'),
('Old', 'Números', 33, 14, 'Y partidos de Alús, asentaron en Rephidim, donde el pueblo no tuvo aguas para beber.', 'cosmic'),
('Old', 'Números', 33, 15, 'Y partidos de Rephidim, asentaron en el desierto de Sinaí.', 'cosmic'),
('Old', 'Números', 33, 16, 'Y partidos del desierto de Sinaí, asentaron en fmfm Kibroth-hataava.', 'cosmic'),
('Old', 'Números', 33, 17, 'Y partidos de Kibroth-hataava, asentaron en Haseroth.', 'cosmic'),
('Old', 'Números', 33, 18, 'Y partidos de Haseroth, asentaron en Ritma.', 'cosmic'),
('Old', 'Números', 33, 19, 'Y partidos de Ritma, asentaron en Rimmón-peres.', 'cosmic'),
('Old', 'Números', 33, 20, 'Y partidos de Rimmón-peres, asentaron en Libna.', 'cosmic'),
('Old', 'Números', 33, 21, 'Y partidos de Libna, asentaron en Rissa.', 'cosmic'),
('Old', 'Números', 33, 22, 'Y partidos de Rissa, asentaron en Ceelatha,', 'cosmic'),
('Old', 'Números', 33, 23, 'Y partidos de Ceelatha, asentaron en el monte de Sepher.', 'cosmic'),
('Old', 'Números', 33, 24, 'Y partidos del monte de Sepher, asentaron en Harada.', 'cosmic'),
('Old', 'Números', 33, 25, 'Y partidos de Harada, asentaron en Maceloth.', 'cosmic'),
('Old', 'Números', 33, 26, 'Y partidos de Maceloth, asentaron en Tahath.', 'cosmic'),
('Old', 'Números', 33, 27, 'Y partidos de Tahath, asentaron en Tara.', 'cosmic'),
('Old', 'Números', 33, 28, 'Y partidos de Tara, asentaron en Mithca.', 'cosmic'),
('Old', 'Números', 33, 29, 'Y partidos de Mithca, asentaron en Hasmona.', 'cosmic'),
('Old', 'Números', 33, 30, 'Y partidos de Hasmona, asentaron en Moseroth.', 'cosmic'),
('Old', 'Números', 33, 31, 'Y partidos de Moseroth, asentaron en Bene-jaacán.', 'cosmic'),
('Old', 'Números', 33, 32, 'Y partidos de Bene-jaacán, asentaron en el monte de Gidgad.', 'cosmic'),
('Old', 'Números', 33, 33, 'Y partidos del monte de Gidgad, asentaron en Jotbatha.', 'cosmic'),
('Old', 'Números', 33, 34, 'Y partidos de Jotbatha, asentaron en Abrona.', 'cosmic'),
('Old', 'Números', 33, 35, 'Y partidos de Abrona, asentaron en Esion-geber.', 'cosmic'),
('Old', 'Números', 33, 36, 'Y partidos de Esion-geber, asentaron en el desierto de Zin, que es Cades.', 'cosmic'),
('Old', 'Números', 33, 37, 'Y partidos de Cades, asentaron en el monte de Hor, en la extremidad del país de Edom.', 'cosmic'),
('Old', 'Números', 33, 38, 'Y subió Aarón el sacerdote al monte de Hor, conforme al dicho de Jehová, y allí murió á los cuarenta años de la salida de los hijos de Israel de la tierra de Egipto, en el mes quinto, en el primero del mes.', 'cosmic'),
('Old', 'Números', 33, 39, 'Y era Aarón de edad de ciento y veinte y tres años, cuando murió en el monte de Hor.', 'cosmic'),
('Old', 'Números', 33, 40, 'Y el Cananeo, rey de Arad, que habitaba al mediodía en la tierra de Canaán, oyó como habían venido los hijos de Israel.', 'cosmic'),
('Old', 'Números', 33, 41, 'Y partidos del monte de Hor, asentaron en Salmona.', 'cosmic'),
('Old', 'Números', 33, 42, 'Y partidos de Salmona, asentaron en Phunón.', 'cosmic'),
('Old', 'Números', 33, 43, 'Y partidos de Phunón, asentaron en Oboth.', 'cosmic'),
('Old', 'Números', 33, 44, 'Y partidos de Oboth, asentaron en Ije-abarim; en el término de Moab.', 'cosmic'),
('Old', 'Números', 33, 45, 'Y partidos de Ije-abarim, asentaron en Dibón-gad.', 'cosmic'),
('Old', 'Números', 33, 46, 'Y partidos de Dibón-gad, asentaron en Almon-diblathaim.', 'cosmic'),
('Old', 'Números', 33, 47, 'Y partidos de Almon-diblathaim, asentaron en los montes de Abarim, delante de Nebo.', 'cosmic'),
('Old', 'Números', 33, 48, 'Y partidos de los montes de Abarim, asentaron en los campos de Moab, junto al Jordán de Jericó.', 'cosmic'),
('Old', 'Números', 33, 49, 'Finalmente asentaron junto al Jordán, desde Beth-jesimoth hasta Abel-sitim, en los campos de Moab.', 'cosmic'),
('Old', 'Números', 33, 50, 'Y habló Jehová á Moisés en los campos de Moab junto al Jordán de Jericó, diciendo:', 'cosmic'),
('Old', 'Números', 33, 51, 'Habla á los hijos de Israel, y diles: Cuando hubiereis pasado el Jordán á la tierra de Canaán,', 'cosmic'),
('Old', 'Números', 33, 52, 'Echaréis á todos los moradores del país de delante de vosotros, y destruiréis todas sus pinturas, y todas sus imágenes de fundición, y arruinaréis todos sus altos;', 'cosmic'),
('Old', 'Números', 33, 53, 'Y echaréis los moradores de la tierra, y habitaréis en ella; porque yo os la he dado para que la poseáis.', 'cosmic'),
('Old', 'Números', 33, 54, 'Y heredaréis la tierra por suertes por vuestras familias: á los muchos daréis mucho por su heredad, y á los pocos daréis menos por heredad suya: donde le saliere la suerte, allí la tendrá cada uno: por las tribus de vuestros padres heredaréis.', 'cosmic'),
('Old', 'Números', 33, 55, 'Y si no echareis los moradores del país de delante de vosotros, sucederá que los que dejareis de ellos serán por aguijones en vuestros ojos, y por espinas en vuestros costados, y afligiros han sobre la tierra en que vosotros habitareis.', 'cosmic'),
('Old', 'Números', 33, 56, 'Será además, que haré á vosotros como yo pensé hacerles á ellos.', 'cosmic'),
('Old', 'Números', 34, 1, 'Y JEHOVA habló á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 34, 2, 'Manda á los hijos de Israel, y diles: Cuando hubiereis entrado en la tierra de Canaán, es á saber, la tierra que os ha de caer en heredad, la tierra de Canaán según sus términos;', 'cosmic'),
('Old', 'Números', 34, 3, 'Tendréis el lado del mediodía desde el desierto de Zin hasta los términos de Edom; y os será el término del mediodía al extremo del mar salado hacia el oriente:', 'cosmic'),
('Old', 'Números', 34, 4, 'Y este término os irá rodeando desde el mediodía hasta la subida de Acrabbim, y pasará hasta Zin; y sus salidas serán del mediodía á Cades-barnea; y saldrá á Hasar-addar, y pasará hasta Asmón;', 'cosmic'),
('Old', 'Números', 34, 5, 'Y rodeará este término, desde Asmón hasta el torrente de Egipto, y sus remates serán al occidente.', 'cosmic'),
('Old', 'Números', 34, 6, 'Y el término occidental os será la gran mar: este término os será el término occidental.', 'cosmic'),
('Old', 'Números', 34, 7, 'Y el término del norte será este: desde la gran mar os señalaréis el monte de Hor;', 'cosmic'),
('Old', 'Números', 34, 8, 'Del monte de Hor señalaréis á la entrada de Hamath, y serán las salidas de aquel término á Sedad;', 'cosmic'),
('Old', 'Números', 34, 9, 'Y saldrá este término á Ziphón, y serán sus remates en Hasar-enán: este os será el término del norte.', 'cosmic'),
('Old', 'Números', 34, 10, 'Y por término al oriente os señalaréis desde Hasar-enán hasta Sepham;', 'cosmic'),
('Old', 'Números', 34, 11, 'Y bajará este término desde Sepham á Ribla, al oriente de Ain: y descenderá el término, y llegará á la costa de la mar de Cinnereth al oriente;', 'cosmic'),
('Old', 'Números', 34, 12, 'Después descenderá este término al Jordán, y serán sus salidas al mar Salado: esta será vuestra tierra: por sus términos alrededor.', 'cosmic'),
('Old', 'Números', 34, 13, 'Y mandó Moisés á los hijos de Israel, diciendo: Esta es la tierra que heredaréis por suerte, la cual mandó Jehová que diese á las nueve tribus, y á la media tribu:', 'cosmic'),
('Old', 'Números', 34, 14, 'Porque la tribu de los hijos de Rubén según las casas de sus padres, y la tribu de los hijos de Gad según las casas de sus padres, y la media tribu de Manasés, han tomado su herencia:', 'cosmic'),
('Old', 'Números', 34, 15, 'Dos tribus y media tomaron su heredad de esta parte del Jordán de Jericó al oriente, al nacimiento del sol.', 'cosmic'),
('Old', 'Números', 34, 16, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 34, 17, 'Estos son los nombres de los varones que os aposesionarán la tierra: Eleazar el sacerdote, y Josué hijo de Nun.', 'cosmic'),
('Old', 'Números', 34, 18, 'Tomaréis también de cada tribu un príncipe, para dar la posesión de la tierra.', 'cosmic'),
('Old', 'Números', 34, 19, 'Y estos son los nombres de los varones: De la tribu de Judá, Caleb hijo de Jephone.', 'cosmic'),
('Old', 'Números', 34, 20, 'Y de la tribu de los hijos de Simeón, Samuel hijo de Ammiud.', 'cosmic'),
('Old', 'Números', 34, 21, 'De la tribu de Benjamín; Elidad hijo de Chislón.', 'cosmic'),
('Old', 'Números', 34, 22, 'Y de la tribu de los hijos de Dan, el príncipe Bucci hijo de Jogli.', 'cosmic'),
('Old', 'Números', 34, 23, 'De los hijos de José: de la tribu de los hijos de Manasés, el príncipe Haniel hijo de Ephod.', 'cosmic'),
('Old', 'Números', 34, 24, 'Y de la tribu de los hijos de Ephraim, el príncipe Chêmuel hijo de Siphtán.', 'cosmic'),
('Old', 'Números', 34, 25, 'Y de la tribu de los hijos de Zabulón, el príncipe Elisaphán hijo de Pharnach.', 'cosmic'),
('Old', 'Números', 34, 26, 'Y de la tribu de los hijos de Issachâr, el príncipe Paltiel hijo de Azan.', 'cosmic'),
('Old', 'Números', 34, 27, 'Y de la tribu de los hijos de Aser, el príncipe Ahiud hijo de Selomi.', 'cosmic'),
('Old', 'Números', 34, 28, 'Y de la tribu de los hijos de Nephtalí, el príncipe Pedael hijo de Ammiud.', 'cosmic'),
('Old', 'Números', 34, 29, 'Estos son á los que mandó Jehová que hiciesen la partición de la herencia á los hijos de Israel en la tierra de Canaán.', 'cosmic'),
('Old', 'Números', 35, 1, 'Y HABLO Jehová á Moisés en los campos de Moab, junto al Jordán de Jericó, diciendo:', 'cosmic'),
('Old', 'Números', 35, 2, 'Manda á los hijos de Israel, que den á los Levitas de la posesión de su heredad ciudades en que habiten: También daréis á los Levitas ejidos de esas ciudades alrededor de ellas.', 'cosmic'),
('Old', 'Números', 35, 3, 'Y tendrán ellos las ciudades para habitar, y los ejidos de ellas serán para sus animales, y para sus ganados, y para todas sus bestias.', 'cosmic'),
('Old', 'Números', 35, 4, 'Y los ejidos de las ciudades que daréis á los Levitas, serán mil codos alrededor, desde el muro de la ciudad para afuera.', 'cosmic'),
('Old', 'Números', 35, 5, 'Luego mediréis fuera de la ciudad á la parte del oriente dos mil codos, y á la parte del mediodía dos mil codos, y á la parte del occidente dos mil codos, y á la parte del norte dos mil codos, y la ciudad en medio: esto tendrán por los ejidos de las ciudades.', 'cosmic'),
('Old', 'Números', 35, 6, 'Y de las ciudades que daréis á los Levitas, seis ciudades serán de acogimiento, las cuales daréis para que el homicida se acoja allá: y además de éstas daréis cuarenta y dos ciudades.', 'cosmic'),
('Old', 'Números', 35, 7, 'Todas las ciudades que daréis á los Levitas serán cuarenta y ocho ciudades; ellas con sus ejidos.', 'cosmic'),
('Old', 'Números', 35, 8, 'Y las ciudades que diereis de la heredad de los hijos de Israel, del que mucho tomaréis mucho, y del que poco tomaréis poco: cada uno dará de sus ciudades á los Levitas según la posesión que heredará.', 'cosmic'),
('Old', 'Números', 35, 9, 'Y habló Jehová á Moisés, diciendo:', 'cosmic'),
('Old', 'Números', 35, 10, 'Habla á los hijos de Israel, y diles: Cuando hubiereis pasado el Jordán á la tierra de Canaán,', 'cosmic'),
('Old', 'Números', 35, 11, 'Os señalaréis ciudades, ciudades de acogimiento tendréis, donde huya el homicida que hiriere á alguno de muerte por yerro.', 'cosmic'),
('Old', 'Números', 35, 12, 'Y os serán aquellas ciudades por acogimiento del pariente, y no morirá el homicida hasta que esté á juicio delante de la congregación.', 'cosmic'),
('Old', 'Números', 35, 13, 'De las ciudades, pues, que daréis, tendréis seis ciudades de acogimiento.', 'cosmic'),
('Old', 'Números', 35, 14, 'Tres ciudades daréis de esta parte del Jordán, y tres ciudades daréis en la tierra de Canaán; las cuales serán ciudades de acogimiento.', 'cosmic'),
('Old', 'Números', 35, 15, 'Estas seis ciudades serán para acogimiento á los hijos de Israel, y al peregrino, y al que morare entre ellos, para que huya allá cualquiera que hiriere de muerte á otro por yerro.', 'cosmic'),
('Old', 'Números', 35, 16, 'Y si con instrumento de hierro lo hiriere y muriere, homicida es; el homicida morirá:', 'cosmic'),
('Old', 'Números', 35, 17, 'Y si con piedra de mano, de que pueda morir, lo hiriere, y muriere, homicida es; el homicida morirá.', 'cosmic'),
('Old', 'Números', 35, 18, 'Y si con instrumento de palo de mano, de que pueda morir, lo hiriere, y muriere, homicida es; el homicida morirá.', 'cosmic'),
('Old', 'Números', 35, 19, 'El pariente del muerto, él matará al homicida: cuando lo encontrare, él le matará.', 'cosmic'),
('Old', 'Números', 35, 20, 'Y si por odio lo empujó, ó echó sobre él alguna cosa por asechanzas, y muere;', 'cosmic'),
('Old', 'Números', 35, 21, 'O por enemistad lo hirió con su mano, y murió: el heridor morirá; es homicida; el pariente del muerto matará al homicida, cuando lo encontrare.', 'cosmic'),
('Old', 'Números', 35, 22, 'Mas si casualmente lo empujó sin enemistades, ó echó sobre él cualquier instrumento sin asechanzas,', 'cosmic'),
('Old', 'Números', 35, 23, 'O bien, sin verlo, hizo caer sobre él alguna piedra, de que pudo morir, y muriere, y él no era su enemigo, ni procuraba su mal;', 'cosmic'),
('Old', 'Números', 35, 24, 'Entonces la congregación juzgará entre el heridor y el pariente del muerto conforme á estas leyes:', 'cosmic'),
('Old', 'Números', 35, 25, 'Y la congregación librará al homicida de mano del pariente del muerto, y la congregación lo hará volver á su ciudad de acogimiento, á la cual se había acogido; y morará en ella hasta que muera el gran sacerdote, el cual fué ungido con el aceite santo.', 'cosmic'),
('Old', 'Números', 35, 26, 'Y si el homicida saliere fuera del término de su ciudad de refugio, á la cual se acogió,', 'cosmic'),
('Old', 'Números', 35, 27, 'Y el pariente del muerto le hayare fuera del término de la ciudad de su acogida, y el pariente del muerto al homicida matare, no se le culpará por ello:', 'cosmic'),
('Old', 'Números', 35, 28, 'Pues en su ciudad de refugio deberá aquél habitar hasta que muera el gran sacerdote: y después que muriere el gran sacerdote, el homicida volverá á la tierra de su posesión.', 'cosmic'),
('Old', 'Números', 35, 29, 'Y estas cosas os serán por ordenanza de derecho por vuestras edades, en todas vuestras habitaciones.', 'cosmic'),
('Old', 'Números', 35, 30, 'Cualquiera que hiriere á alguno, por dicho de testigos, morirá el homicida: mas un solo testigo no hará fé contra alguna persona para que muera.', 'cosmic'),
('Old', 'Números', 35, 31, 'Y no tomaréis precio por la vida del homicida; porque está condenado á muerte: mas indefectiblemente morirá.', 'cosmic'),
('Old', 'Números', 35, 32, 'Ni tampoco tomaréis precio del que huyó á su ciudad de refugio, para que vuelva á vivir en su tierra, hasta que muera el sacerdote.', 'cosmic'),
('Old', 'Números', 35, 33, 'Y no contaminaréis la tierra donde estuviereis: porque esta sangre amancillará la tierra: y la tierra no será expiada de la sangre que fué derramada en ella, sino por la sangre del que la derramó.', 'cosmic'),
('Old', 'Números', 35, 34, 'No contaminéis, pues, la tierra donde habitáis, en medio de la cual yo habito; porque yo Jehová habito en medio de los hijos de Israel.', 'cosmic'),
('Old', 'Números', 36, 1, 'Y LLEGARON los príncipes de los padres de la familia de Galaad, hijo de Machîr, hijo de Manasés, de las familias de los hijos de José; y hablaron delante de Moisés, y de los príncipes, cabezas de padres de los hijos de Israel,', 'cosmic'),
('Old', 'Números', 36, 2, 'Y dijeron: Jehová mandó á mi señor que por suerte diese la tierra á los hijos de Israel en posesión: también ha mandado Jehová á mi señor, que dé la posesión de Salphaad nuestro hermano á sus hijas;', 'cosmic'),
('Old', 'Números', 36, 3, 'Las cuales, si se casaren con algunos de los hijos de las otras tribus de los hijos de Israel, la herencia de ellas será así desfalcada de la herencia de nuestros padres, y será añadida á la herencia de la tribu á que serán unidas: y será quitada de la suerte de nuestra heredad.', 'cosmic'),
('Old', 'Números', 36, 4, 'Y cuando viniere el jubileo de los hijos de Israel, la heredad de ellas será añadida á la heredad de la tribu de sus maridos; y así la heredad de ellas será quitada de la heredad de la tribu de nuestros padres.', 'cosmic'),
('Old', 'Números', 36, 5, 'Entonces Moisés mandó á los hijos de Israel por dicho de Jehová, diciendo: La tribu de los hijos de José habla rectamente.', 'cosmic'),
('Old', 'Números', 36, 6, 'Esto es lo que ha mandado Jehová acerca de las hijas de Salphaad, diciendo: Cásense como á ellas les pluguiere, empero en la familia de la tribu de su padre se casarán;', 'cosmic'),
('Old', 'Números', 36, 7, 'Para que la heredad de los hijos de Israel no sea traspasada de tribu en tribu; porque cada uno de los hijos de Israel se allegará á la heredad de la tribu de sus padres.', 'cosmic'),
('Old', 'Números', 36, 8, 'Y cualquiera hija que poseyere heredad de las tribus de los hijos de Israel, con alguno de la familia de la tribu de su padre se casará, para que los hijos de Israel posean cada uno la heredad de sus padres.', 'cosmic'),
('Old', 'Números', 36, 9, 'Y no ande la heredad rodando de una tribu á otra: mas cada una de las tribus de los hijos de Israel se llegue á su heredad.', 'cosmic'),
('Old', 'Números', 36, 10, 'Como Jehová mandó á Moisés, así hicieron las hijas de Salphaad.', 'cosmic'),
('Old', 'Números', 36, 11, 'Y así Maala, y Tirsa, y Hogla, y Milchâ, y Noa, hijas de Salphaad, se casaron con hijos de sus tíos:', 'cosmic'),
('Old', 'Números', 36, 12, 'De la familia de los hijos de Manasés, hijo de José, fueron mujeres; y la heredad de ellas quedó en la tribu de la familia de su padre.', 'cosmic'),
('Old', 'Números', 36, 13, 'Estos son los mandamientos y los estatutos que mandó Jehová por mano de Moisés á los hijos de Israel en los campos de Moab, junto al Jordán de Jericó.', 'cosmic'),
('Old', 'Deuteronomio', 1, 1, 'ESTAS son las palabras que habló Moisés á todo Israel de esta parte del Jordán en el desierto, en el llano delante del mar Bermejo, entre Parán, y Thopel, y Labán, y Haseroth, y Dizahab.', 'cosmic'),
('Old', 'Deuteronomio', 1, 2, 'Once jornadas hay desde Horeb, camino del monte de Seir, hasta Cades-barnea.', 'cosmic'),
('Old', 'Deuteronomio', 1, 3, 'Y fué, que á los cuarenta años, en el mes undécimo, al primero del mes, Moisés habló á los hijos de Israel conforme á todas las cosas que Jehová le había mandado acerca de ellos;', 'cosmic'),
('Old', 'Deuteronomio', 1, 4, 'Después que hirió á Sehón rey de los Amorrheos, que habitaba en Hesbón, y á Og rey de Basán, que habitaba en Astarot en Edrei:', 'cosmic'),
('Old', 'Deuteronomio', 1, 5, 'De esta parte del Jordán, en tierra de Moab, resolvió Moisés declarar esta ley, diciendo:', 'cosmic'),
('Old', 'Deuteronomio', 1, 6, 'Jehová nuestro Dios nos habló en Horeb, diciendo: Harto habéis estado en este monte;', 'cosmic'),
('Old', 'Deuteronomio', 1, 7, 'Volveos, partíos é id al monte del Amorrheo, y á todas sus comarcas, en el llano, en el monte, y en los valles, y al mediodía, y á la costa de la mar, á la tierra del Cananeo, y el Líbano, hasta el gran río, el río Eufrates.', 'cosmic'),
('Old', 'Deuteronomio', 1, 8, 'Mirad, yo he dado la tierra en vuestra presencia; entrad y poseed la tierra que Jehová juró á vuestros padres Abraham, Isaac, y Jacob, que les daría á ellos y á su simiente después de ellos.', 'cosmic'),
('Old', 'Deuteronomio', 1, 9, 'Y yo os hablé entonces, diciendo: Yo no puedo llevaros solo:', 'cosmic'),
('Old', 'Deuteronomio', 1, 10, 'Jehová vuestro Dios os ha multiplicado, y he aquí sois hoy vosotros como las estrellas del cielo en multitud.', 'cosmic'),
('Old', 'Deuteronomio', 1, 11, '¡Jehová Dios de vuestros padres añada sobre vosotros como sois mil veces, y os bendiga, como os ha prometido!', 'cosmic'),
('Old', 'Deuteronomio', 1, 12, '¿Cómo llevaré yo solo vuestras molestias, vuestras cargas, y vuestros pleitos?', 'cosmic'),
('Old', 'Deuteronomio', 1, 13, 'Dad me de entre vosotros, de vuestras tribus, varones sabios y entendidos y expertos, para que yo los ponga por vuestros jefes.', 'cosmic'),
('Old', 'Deuteronomio', 1, 14, 'Y me respondisteis, y dijisteis: Bueno es hacer lo que has dicho.', 'cosmic'),
('Old', 'Deuteronomio', 1, 15, 'Y tomé los principales de vuestras tribus, varones sabios y expertos, y púselos por jefes sobre vosotros, jefes de millares, y jefes de cientos, y jefes de cincuenta, y cabos de diez, y gobernadores á vuestras tribus.', 'cosmic'),
('Old', 'Deuteronomio', 1, 16, 'Y entonces mandé á vuestros jueces, diciendo: Oid entre vuestros hermanos, y juzgad justamente entre el hombre y su hermano, y el que le es extranjero.', 'cosmic'),
('Old', 'Deuteronomio', 1, 17, 'No tengáis respeto de personas en el juicio: así al pequeño como al grande oiréis: no tendréis temor de ninguno, porque el juicio es de Dios: y la causa que os fuere difícil, la traeréis á mí, y yo la oiré.', 'cosmic'),
('Old', 'Deuteronomio', 1, 18, 'Os mandé, pues, en aquel tiempo todo lo que habíais de hacer.', 'cosmic'),
('Old', 'Deuteronomio', 1, 19, 'Y partidos de Horeb, anduvimos todo aquel grande y terrible desierto que habéis visto, por el camino del monte del Amorrheo, como Jehová nuestro Dios nos lo mandó; y llegamos hasta Cades-barnea.', 'cosmic'),
('Old', 'Deuteronomio', 1, 20, 'Entonces os dije: Llegado habéis al monte del Amorrheo, el cual Jehová nuestro Dios nos da.', 'cosmic'),
('Old', 'Deuteronomio', 1, 21, 'Mira, Jehová tu Dios ha dado delante de ti la tierra: sube y posée la, como Jehová el Dios de tus padres te ha dicho; no temas ni desmayes.', 'cosmic'),
('Old', 'Deuteronomio', 1, 22, 'Y llegasteis á mí todos vosotros, y dijisteis: Enviemos varones delante de nosotros, que nos reconozcan la tierra y nos traigan de vuelta razón del camino por donde hemos de subir, y de las ciudades adonde hemos de llegar.', 'cosmic'),
('Old', 'Deuteronomio', 1, 23, 'Y el dicho me pareció bien: y tomé doce varones de vosotros, un varón por tribu:', 'cosmic'),
('Old', 'Deuteronomio', 1, 24, 'Y se encaminaron, y subieron al monte, y llegaron hasta la arroyada de Escol, y reconocieron la tierra.', 'cosmic'),
('Old', 'Deuteronomio', 1, 25, 'Y tomaron en sus manos del fruto del país, y nos lo trajeron, y diéronnos cuenta, y dijeron: Es buena la tierra que Jehová nuestro Dios nos da.', 'cosmic'),
('Old', 'Deuteronomio', 1, 26, 'Empero no quisisteis subir, antes fuisteis rebeldes al dicho de Jehová vuestro Dios;', 'cosmic'),
('Old', 'Deuteronomio', 1, 27, 'Y murmurasteis en vuestras tiendas, diciendo: Porque Jehová nos aborrecía, nos ha sacado de tierra de Egipto, para entregarnos en mano del Amorrheo para destruirnos.', 'cosmic'),
('Old', 'Deuteronomio', 1, 28, '¿A dónde subimos? Nuestros hermanos han hecho desfallecer nuestro corazón, diciendo: Este pueblo es mayor y más alto que nosotros, las ciudades grandes y muradas hasta el cielo; y también vimos allí hijos de gigantes.', 'cosmic'),
('Old', 'Deuteronomio', 1, 29, 'Entonces os dije: No temáis, ni tengáis miedo de ellos.', 'cosmic'),
('Old', 'Deuteronomio', 1, 30, 'Jehová vuestro Dios, el cual va delante de vosotros, él peleará por vosotros, conforme á todas las cosas que hizo por vosotros en Egipto delante de vuestros ojos;', 'cosmic'),
('Old', 'Deuteronomio', 1, 31, 'Y en el desierto has visto que Jehová tu Dios te ha traído, como trae el hombre á su hijo, por todo el camino que habéis andado, hasta que habéis venido á este lugar.', 'cosmic'),
('Old', 'Deuteronomio', 1, 32, 'Y aun con esto no creisteis en Jehová vuestro Dios,', 'cosmic'),
('Old', 'Deuteronomio', 1, 33, 'El cual iba delante de vosotros por el camino, para reconoceros el lugar donde habíais de asentar el campo, con fuego de noche para mostraros el camino por donde aduvieseis, y con nube de día.', 'cosmic'),
('Old', 'Deuteronomio', 1, 34, 'Y oyó Jehová la voz de vuestras palabras, y enojóse, y juró diciendo:', 'cosmic'),
('Old', 'Deuteronomio', 1, 35, 'No verá hombre alguno de estos de esta mala generación, la buena tierra que juré había de dar á vuestros padres,', 'cosmic'),
('Old', 'Deuteronomio', 1, 36, 'Excepto Caleb hijo de Jephone: él la verá, y á él le daré la tierra que pisó, y á sus hijos; porque cumplió en pos de Jehová.', 'cosmic'),
('Old', 'Deuteronomio', 1, 37, 'Y también contra mí se airó Jehová por vosotros, diciendo: Tampoco tú entrarás allá:', 'cosmic'),
('Old', 'Deuteronomio', 1, 38, 'Josué hijo de Nun, que está delante de ti, él entrará allá: anímale; porque él la hará heredar á Israel.', 'cosmic'),
('Old', 'Deuteronomio', 1, 39, 'Y vuestros chiquitos, de los cuales dijisteis serán por presa, y vuestros hijos que no saben hoy bueno ni malo, ellos entrarán allá, y á ellos la daré, y ellos la heredarán.', 'cosmic'),
('Old', 'Deuteronomio', 1, 40, 'Y vosotros volveos, y partíos al desierto camino del mar Bermejo.', 'cosmic'),
('Old', 'Deuteronomio', 1, 41, 'Entonces respondisteis y me dijisteis: Pecado hemos contra Jehová; nosotros subiremos y pelearemos, conforme á todo lo que Jehová nuestro Dios nos ha mandado. Y os armasteis cada uno de sus armas de guerra, y os apercibisteis para subir al monte.', 'cosmic'),
('Old', 'Deuteronomio', 1, 42, 'Y Jehová me dijo: Diles: No subáis, ni peleéis, pues no estoy entre vosotros; porque no seáis heridos delante de vuestros enemigos.', 'cosmic'),
('Old', 'Deuteronomio', 1, 43, 'Y os hablé, y no disteis oído; antes fuisteis rebeldes al dicho de Jehová, y persistiendo con altivez, subisteis al monte.', 'cosmic'),
('Old', 'Deuteronomio', 1, 44, 'Y salió el Amorrheo, que habitaba en aquel monte, á vuestro encuentro, y os persiguieron, como hacen las avispas, y os derrotaron en Seir, persiguiéndoos hasta Horma.', 'cosmic'),
('Old', 'Deuteronomio', 1, 45, 'Y volvisteis, y llorasteis delante de Jehová; pero Jehová no escuchó vuestra voz, ni os prestó oído.', 'cosmic'),
('Old', 'Deuteronomio', 1, 46, 'Y estuvisteis en Cades por muchos días, como en los días que habéis estado.', 'cosmic'),
('Old', 'Deuteronomio', 2, 1, 'Y VOLVIMOS, y partímonos al desierto camino del mar Bermejo, como Jehová me había dicho; y rodeamos el monte de Seir por muchos días.', 'cosmic'),
('Old', 'Deuteronomio', 2, 2, 'Y Jehová me habló, diciendo:', 'cosmic'),
('Old', 'Deuteronomio', 2, 3, 'Harto habéis rodeado este monte; volveos al aquilón.', 'cosmic'),
('Old', 'Deuteronomio', 2, 4, 'Y manda al pueblo, diciendo: Pasando vosotros por el término de vuestros hermanos los hijos de Esaú, que habitan en Seir, ellos tendrán miedo de vosotros; mas vosotros guardaos mucho:', 'cosmic'),
('Old', 'Deuteronomio', 2, 5, 'No os metáis con ellos; que no os daré de su tierra ni aun la holladura de la planta de un pie; porque yo he dado por heredad á Esaú el monte de Seir.', 'cosmic'),
('Old', 'Deuteronomio', 2, 6, 'Compraréis de ellos por dinero las viandas, y comeréis; y también compraréis de ellos el agua, y beberéis:', 'cosmic'),
('Old', 'Deuteronomio', 2, 7, 'Pues Jehová tu Dios te ha bendecido en toda obra de tus manos: él sabe que andas por este gran desierto: estos cuarenta años Jehová tu Dios fué contigo; y ninguna cosa te ha faltado.', 'cosmic'),
('Old', 'Deuteronomio', 2, 8, 'Y pasamos de nuestros hermanos los hijos de Esaú, que habitaban en Seir, por el camino de la llanura de Elath y de Esiongeber. Y volvimos, y pasamos camino del desierto de Moab.', 'cosmic'),
('Old', 'Deuteronomio', 2, 9, 'Y Jehová me dijo: No molestes á Moab, ni te empeñes con ellos en guerra, que no te daré posesión de su tierra; porque yo he dado á Ar por heredad á los hijos de Lot.', 'cosmic'),
('Old', 'Deuteronomio', 2, 10, '(Los Emimeos habitaron en ella antes, pueblo grande, y numeroso, y alto como fhnumeroso, y alto como fh gigantes:', 'cosmic'),
('Old', 'Deuteronomio', 2, 11, 'Por gigantes eran ellos también contados, como los Anaceos; y los Moabitas los llaman Emimeos.', 'cosmic'),
('Old', 'Deuteronomio', 2, 12, 'Y en Seir habitaron antes los Horeos, á los cuales echaron los hijos de Esaú; y los destruyeron de delante de sí, y moraron en lugar de ellos; como hizo Israel en la tierra de su posesión que les dió Jehová.)', 'cosmic'),
('Old', 'Deuteronomio', 2, 13, 'Levantaos ahora, y pasad el arroyo de Zered. Y pasamos el arroyo de Zered.', 'cosmic'),
('Old', 'Deuteronomio', 2, 14, 'Y los días que anduvimos de Cades-barnea hasta que pasamos el arroyo de Zered, fueron treinta y ocho años; hasta que se acabó toda la generación de los hombres de guerra de en medio del campo, como Jehová les había jurado.', 'cosmic'),
('Old', 'Deuteronomio', 2, 15, 'Y también la mano de Jehová fué sobre ellos para destruirlos de en medio del campo, hasta acabarlos.', 'cosmic'),
('Old', 'Deuteronomio', 2, 16, 'Y aconteció que cuando se hubieron acabado de morir todos los hombres de guerra de entre el pueblo,', 'cosmic'),
('Old', 'Deuteronomio', 2, 17, 'Jehová me habló, diciendo:', 'cosmic'),
('Old', 'Deuteronomio', 2, 18, 'Tú pasarás hoy el término de Moab, á Ar,', 'cosmic'),
('Old', 'Deuteronomio', 2, 19, 'Y te acercarás delante de los hijos de Ammón: no los molestes, ni te metas con ellos; porque no te tengo de dar posesión de la tierra de los hijos de Ammón; que á los hijos de Lot la he dado por heredad.', 'cosmic'),
('Old', 'Deuteronomio', 2, 20, '(Por tierra de gigantes fué también ella tenida: habitaron en ella gigantes en otro tiempo, á los cuales los Ammonitas llamaban Zomzommeos;', 'cosmic'),
('Old', 'Deuteronomio', 2, 21, 'Pueblo grande, y numeroso, y alto, como los Anaceos; á los cuales Jehová destruyó de delante de los Ammonitas, quienes les sucedieron, y habitaron en su lugar:', 'cosmic'),
('Old', 'Deuteronomio', 2, 22, 'Como hizo con los hijos de Esaú, que habitaban en Seir, de delante de los cuales destruyó á los Horeos; y ellos les sucedieron, y habitaron en su lugar hasta hoy.', 'cosmic'),
('Old', 'Deuteronomio', 2, 23, 'Y á los Heveos que habitaban en Haserin hasta Gaza, los Caftoreos que salieron de Caftor los destruyeron, y habitaron en su lugar.)', 'cosmic'),
('Old', 'Deuteronomio', 2, 24, 'Levantaos, partid, y pasad el arroyo de Arnón: he aquí he dado en tu mano á Sehón rey de Hesbón, Amorrheo, y á su tierra: comienza á tomar posesión, y empéñate con él en guerra.', 'cosmic'),
('Old', 'Deuteronomio', 2, 25, 'Hoy comenzaré á poner tu miedo y tu espanto sobre los pueblos debajo de todo el cielo, los cuales oirán tu fama, y temblarán, y angustiarse han delante de ti.', 'cosmic'),
('Old', 'Deuteronomio', 2, 26, 'Y envié mensajeros desde el desierto de Cademoth á Sehón rey de Hesbón, con palabras de paz, diciendo:', 'cosmic'),
('Old', 'Deuteronomio', 2, 27, 'Pasaré por tu tierra por el camino: por el camino iré, sin apartarme á diestra ni á siniestra:', 'cosmic'),
('Old', 'Deuteronomio', 2, 28, 'La comida me venderás por dinero y comeré: el agua también me darás por dinero, y beberé: solamente pasaré á pie;', 'cosmic'),
('Old', 'Deuteronomio', 2, 29, 'Como lo hicieron conmigo los hijos de Esaú que habitaban en Seir, y los Moabitas que habitaban en Ar; hasta que pase el Jordán á la tierra que nos da Jehová nuestro Dios.', 'cosmic'),
('Old', 'Deuteronomio', 2, 30, 'Mas Sehón rey de Hesbón no quiso que pasásemos por el territorio suyo; porque Jehová tu Dios había endurecido su espíritu, y obstinado su corazón para entregarlo en tu mano, como hoy.', 'cosmic'),
('Old', 'Deuteronomio', 2, 31, 'Y díjome Jehová: He aquí yo he comenzado á dar delante de ti á Sehón y á su tierra; comienza á tomar posesión, para que heredes su tierra.', 'cosmic'),
('Old', 'Deuteronomio', 2, 32, 'Y saliónos Sehón al encuentro, él y todo su pueblo, para pelear en Jaas.', 'cosmic'),
('Old', 'Deuteronomio', 2, 33, 'Mas Jehová nuestro Dios lo entregó delante de nosotros; y herimos á él y á sus hijos, y á todo su pueblo.', 'cosmic'),
('Old', 'Deuteronomio', 2, 34, 'Y tomamos entonces todas sus ciudades, y destruimos todas las ciudades, hombres, y mujeres, y niños; no dejamos ninguno:', 'cosmic'),
('Old', 'Deuteronomio', 2, 35, 'Solamente tomamos para nosotros las bestias, y los despojos de las ciudades que habíamos tomado.', 'cosmic'),
('Old', 'Deuteronomio', 2, 36, 'Desde Aroer, que está junto á la ribera del arroyo de Arnón, y la ciudad que está en el arroyo, hasta Galaad, no hubo ciudad que escapase de nosotros: todas las entregó Jehová nuestro Dios en nuestro poder.', 'cosmic'),
('Old', 'Deuteronomio', 2, 37, 'Solamente á la tierra de los hijos de Ammón no llegaste, ni á todo lo que está á la orilla del arroyo de Jaboc ni á las ciudades del monte, ni á lugar alguno que Jehová nuestro Dios había prohibido.', 'cosmic'),
('Old', 'Deuteronomio', 3, 1, 'Y VOLVIMOS, y subimos camino de Basán, y saliónos al encuentro Og rey de Basán para pelear, él y todo su pueblo, en Edrei.', 'cosmic'),
('Old', 'Deuteronomio', 3, 2, 'Y díjome Jehová: No tengas temor de él, porque en tu mano he entregado á él y á todo su pueblo, y su tierra: y harás con él como hiciste con Sehón rey Amorrheo, que habitaba en Hesbón.', 'cosmic'),
('Old', 'Deuteronomio', 3, 3, 'Y Jehová nuestro Dios entregó también en nuestra mano á Og rey de Basán, y á todo su pueblo, al cual herimos hasta no quedar de él ninguno.', 'cosmic'),
('Old', 'Deuteronomio', 3, 4, 'Y tomamos entonces todas sus ciudades; no quedó ciudad que no les tomásemos: sesenta ciudades, toda la tierra de Argob, del reino de Og en Basán.', 'cosmic'),
('Old', 'Deuteronomio', 3, 5, 'Todas éstas eran ciudades fortalecidas con alto muro, con puertas y barras; sin otras muy muchas ciudades sin muro.', 'cosmic'),
('Old', 'Deuteronomio', 3, 6, 'Y destruímoslas, como hicimos á Sehón rey de Hesbón, destruyendo en toda ciudad hombres, mujeres, y niños.', 'cosmic'),
('Old', 'Deuteronomio', 3, 7, 'Y tomamos para nosotros todas las bestias, y los despojos de las ciudades.', 'cosmic'),
('Old', 'Deuteronomio', 3, 8, 'También tomamos en aquel tiempo de mano de dos reyes Amorrheos que estaban de esta parte del Jordán, la tierra desde el arroyo de Arnón hasta el monte de Hermón:', 'cosmic'),
('Old', 'Deuteronomio', 3, 9, '(Los Sidonios llaman á Hermón Sirión; y los Amorrheos, Senir:)', 'cosmic'),
('Old', 'Deuteronomio', 3, 10, 'Todas las ciudades de la llanura, y todo Galaad, y todo Basán hasta Salchâ y Edrei, ciudades del reino de Og en Basán.', 'cosmic'),
('Old', 'Deuteronomio', 3, 11, 'Porque sólo Og rey de Basán había quedado de los gigantes que quedaron. He aquí su cama, una cama de hierro, ¿no está en Rabbath de los hijos de Ammón?; la longitud de ella de nueve codos, y su anchura de cuatro codos, al codo de un hombre.', 'cosmic'),
('Old', 'Deuteronomio', 3, 12, 'Y esta tierra que heredamos entonces desde Aroer, que está al arroyo de Arnón, y la mitad del monte de Galaad con sus ciudades, dí á los Rubenitas y á los Gaditas:', 'cosmic'),
('Old', 'Deuteronomio', 3, 13, 'Y el resto de Galaad, y todo Basán, del reino de Og, dí lo á la media tribu de Manasés; toda la tierra de Argob, todo Basán, que se llamaba la tierra de los gigantes.', 'cosmic'),
('Old', 'Deuteronomio', 3, 14, 'Jair hijo de Manasés tomó toda la tierra de Argob hasta el término de Gessuri y Machâti; y llamóla de su nombre Basán-havoth-jair, hasta hoy.', 'cosmic'),
('Old', 'Deuteronomio', 3, 15, 'Y á Machîr dí á Galaad.', 'cosmic'),
('Old', 'Deuteronomio', 3, 16, 'Y á los Rubenitas y Gaditas dí de Galaad hasta el arroyo de Arnón, el medio del arroyo por término; hasta el arroyo de Jaboc, término de los hijos de Ammón:', 'cosmic'),
('Old', 'Deuteronomio', 3, 17, 'Asimismo la campiña, y el Jordán, y el término, desde Cinereth hasta la mar del llano, el mar Salado, las vertientes abajo del Pisga al oriente.', 'cosmic'),
('Old', 'Deuteronomio', 3, 18, 'Y os mandé entonces, diciendo: Jehová vuestro Dios os ha dado esta tierra para que la poseáis: pasaréis armados delante de vuestros hermanos los hijos de Israel todos los valientes.', 'cosmic'),
('Old', 'Deuteronomio', 3, 19, 'Solamente vuestras mujeres, vuestros niños, y vuestros ganados, (yo sé que tenéis mucho ganado,) quedarán en vuestras ciudades que os he dado,', 'cosmic'),
('Old', 'Deuteronomio', 3, 20, 'Hasta que Jehová dé reposo á vuestros hermanos, así como á vosotros, y hereden también ellos la tierra que Jehová vuestro Dios les da á la otra parte del Jordán: entonces os volveréis cada uno a su heredad que yo os he dado.', 'cosmic'),
('Old', 'Deuteronomio', 3, 21, 'Mandé también á Josué entonces, diciendo: Tus ojos vieron todo lo que Jehová vuestro Dios ha hecho á aquellos dos reyes: así hará Jehová á todos los reinos á los cuales pasarás tú.', 'cosmic'),
('Old', 'Deuteronomio', 3, 22, 'No los temáis; que Jehová vuestro Dios, él es el que pelea por vosotros.', 'cosmic'),
('Old', 'Deuteronomio', 3, 23, 'Y oré á Jehová en aquel tiempo, diciendo:', 'cosmic'),
('Old', 'Deuteronomio', 3, 24, 'Señor Jehová, tú has comenzado á mostrar á tu siervo tu grandeza, y tu mano fuerte: porque ¿qué dios hay en el cielo ni en la tierra que haga según tus obras, y según tus valentías?', 'cosmic');