INSERT INTO scriptures (testament, book_name, chapter, verse_number, content, visual_theme) VALUES
('Old', 'Job', 6, 22, '¿Os he dicho yo: Traedme, Y pagad por mí de vuestra hacienda;', 'cosmic'),
('Old', 'Job', 6, 23, 'Y libradme de la mano del opresor, Y redimidme del poder de los violentos?', 'cosmic'),
('Old', 'Job', 6, 24, 'Enseñadme, y yo callaré: Y hacedme entender en qué he errado.', 'cosmic'),
('Old', 'Job', 6, 25, '¡Cuán fuertes son las palabras de rectitud! Mas ¿qué reprende el que reprende de vosotros?', 'cosmic'),
('Old', 'Job', 6, 26, '¿Pensáis censurar palabras, Y los discursos de un desesperado, que son como el viento?', 'cosmic'),
('Old', 'Job', 6, 27, 'También os arrojáis sobre el huérfano, Y hacéis hoyo delante de vuestro amigo.', 'cosmic'),
('Old', 'Job', 6, 28, 'Ahora pues, si queréis, mirad en mí, Y ved si miento delante de vosotros.', 'cosmic'),
('Old', 'Job', 6, 29, 'Tornad ahora, y no haya iniquidad; Volved aún á considerar mi justicia en esto.', 'cosmic'),
('Old', 'Job', 6, 30, '¿Hay iniquidad en mi lengua? ¿No puede mi paladar discernir las cosas depravadas?', 'cosmic'),
('Old', 'Job', 7, 1, 'CIERTAMENTE tiempo limitado tiene el hombre sobre la tierra, Y sus días son como los días del jornalero.', 'cosmic'),
('Old', 'Job', 7, 2, 'Como el siervo anhela la sombra, Y como el jornalero espera el reposo de su trabajo:', 'cosmic'),
('Old', 'Job', 7, 3, 'Así poseo yo meses de vanidad, Y noches de trabajo me dieron por cuenta.', 'cosmic'),
('Old', 'Job', 7, 4, 'Cuando estoy acostado, digo: ¿Cuándo me levantaré? Y mide mi corazón la noche, Y estoy harto de devaneos hasta el alba.', 'cosmic'),
('Old', 'Job', 7, 5, 'Mi carne está vestida de gusanos, y de costras de polvo; Mi piel hendida y abominable.', 'cosmic'),
('Old', 'Job', 7, 6, 'Y mis días fueron más ligeros que la lanzadera del tejedor, Y fenecieron sin esperanza.', 'cosmic'),
('Old', 'Job', 7, 7, 'Acuérdate que mi vida es viento, Y que mis ojos no volverán á ver el bien.', 'cosmic'),
('Old', 'Job', 7, 8, 'Los ojos de los que me ven, no me verán más: Tus ojos sobre mí, y dejaré de ser.', 'cosmic'),
('Old', 'Job', 7, 9, 'La nube se consume, y se va: Así el que desciende al sepulcro no subirá;', 'cosmic'),
('Old', 'Job', 7, 10, 'No tornará más á su casa, Ni su lugar le conocerá más.', 'cosmic'),
('Old', 'Job', 7, 11, 'Por tanto yo no reprimiré mi boca; Hablaré en la angustia de mi espíritu, Y quejaréme con la amargura de mi alma.', 'cosmic'),
('Old', 'Job', 7, 12, '¿Soy yo la mar, ó ballena, Que me pongas guarda?', 'cosmic'),
('Old', 'Job', 7, 13, 'Cuando digo: Mi cama me consolará, Mi cama atenuará mis quejas;', 'cosmic'),
('Old', 'Job', 7, 14, 'Entonces me quebrantarás con sueños, Y me turbarás con visiones.', 'cosmic'),
('Old', 'Job', 7, 15, 'Y así mi alma tuvo por mejor el ahogamiento, Y quiso la muerte más que mis huesos.', 'cosmic'),
('Old', 'Job', 7, 16, 'Aburríme: no he de vivir yo para siempre; Déjáme, pues que mis días son vanidad.', 'cosmic'),
('Old', 'Job', 7, 17, '¿Qué es el hombre, para que lo engrandezcas, Y que pongas sobre él tu corazón,', 'cosmic'),
('Old', 'Job', 7, 18, 'Y lo visites todas las mañanas, Y todos los momentos lo pruebes?', 'cosmic'),
('Old', 'Job', 7, 19, '¿Hasta cuándo no me dejarás, Ni me soltarás hasta que trague mi saliva?', 'cosmic'),
('Old', 'Job', 7, 20, 'Pequé, ¿qué te haré, oh Guarda de los hombres? ¿Por qué me has puesto contrario á ti, Y que á mí mismo sea pesado?', 'cosmic'),
('Old', 'Job', 7, 21, '¿Y por qué no quitas mi rebelión, y perdonas mi iniquidad? Porque ahora dormiré en el polvo, Y si me buscares de mañana, ya no seré.', 'cosmic'),
('Old', 'Job', 8, 1, 'Y RESPONDIO Bildad Suhita, y dijo:', 'cosmic'),
('Old', 'Job', 8, 2, '¿Hasta cuándo hablarás tales cosas, Y las palabras de tu boca serán como un viento fuerte?', 'cosmic'),
('Old', 'Job', 8, 3, '¿Acaso pervertirá Dios el derecho, O el Todopoderoso pervertirá la justicia?', 'cosmic'),
('Old', 'Job', 8, 4, 'Si tus hijos pecaron contra él, El los echó en el lugar de su pecado.', 'cosmic'),
('Old', 'Job', 8, 5, 'Si tú de mañana buscares á Dios, Y rogares al Todopoderoso;', 'cosmic'),
('Old', 'Job', 8, 6, 'Si fueres limpio y derecho, Cierto luego se despertará sobre ti, Y hará próspera la morada de tu justicia.', 'cosmic'),
('Old', 'Job', 8, 7, 'Y tu principio habrá sido pequeño, Y tu postrimería acrecerá en gran manera.', 'cosmic'),
('Old', 'Job', 8, 8, 'Porque pregunta ahora á la edad pasada, Y disponte para inquirir de sus padres de ellos;', 'cosmic'),
('Old', 'Job', 8, 9, 'Pues nosotros somos de ayer, y no sabemos, Siendo nuestros días sobre la tierra como sombra.', 'cosmic'),
('Old', 'Job', 8, 10, '¿No te enseñarán ellos, te dirán, Y de su corazón sacarán palabras?', 'cosmic'),
('Old', 'Job', 8, 11, '¿Crece el junco sin lodo? ¿Crece el prado sin agua?', 'cosmic'),
('Old', 'Job', 8, 12, 'Aun él en su verdor no será cortado, Y antes de toda hierba se secará.', 'cosmic'),
('Old', 'Job', 8, 13, 'Tales son los caminos de todos los que olvidan á Dios: Y la esperanza del impío perecerá:', 'cosmic'),
('Old', 'Job', 8, 14, 'Porque su esperanza será cortada, Y su confianza es casa de araña.', 'cosmic'),
('Old', 'Job', 8, 15, 'Apoyaráse él sobre su casa, mas no permanecerá en pie; Atendráse á ella, mas no se afirmará.', 'cosmic'),
('Old', 'Job', 8, 16, 'A manera de un árbol, está verde delante del sol, Y sus renuevos salen sobre su huerto;', 'cosmic'),
('Old', 'Job', 8, 17, 'Vanse entretejiendo sus raíces junto á una fuente, Y enlazándose hasta un lugar pedregoso.', 'cosmic'),
('Old', 'Job', 8, 18, 'Si le arrancaren de su lugar, Este negarále entonces, diciendo: Nunca te vi.', 'cosmic'),
('Old', 'Job', 8, 19, 'Ciertamente éste será el gozo de su camino; Y de la tierra de donde se traspusiere, nacerán otros.', 'cosmic'),
('Old', 'Job', 8, 20, 'He aquí, Dios no aborrece al perfecto, Ni toma la mano de los malignos.', 'cosmic'),
('Old', 'Job', 8, 21, 'Aun henchirá tu boca de risa, Y tus labios de júbilo.', 'cosmic'),
('Old', 'Job', 8, 22, 'Los que te aborrecen, serán vestidos de confusión; Y la habitación de los impíos perecerá.', 'cosmic'),
('Old', 'Job', 9, 1, 'Y RESPONDIO Job, y dijo:', 'cosmic'),
('Old', 'Job', 9, 2, 'Ciertamente yo conozco que es así: ¿Y cómo se justificará el hombre con Dios?', 'cosmic'),
('Old', 'Job', 9, 3, 'Si quisiere contender con él, No le podrá responder á una cosa de mil.', 'cosmic'),
('Old', 'Job', 9, 4, 'El es sabio de corazón, y poderoso en fortaleza, ¿Quién se endureció contra él, y quedó en paz?', 'cosmic'),
('Old', 'Job', 9, 5, 'Que arranca los montes con su furor, Y no conocen quién los trastornó:', 'cosmic'),
('Old', 'Job', 9, 6, 'Que remueve la tierra de su lugar, Y hace temblar sus columnas:', 'cosmic'),
('Old', 'Job', 9, 7, 'Que manda al sol, y no sale; Y sella las estrellas:', 'cosmic'),
('Old', 'Job', 9, 8, 'El que extiende solo los cielos, Y anda sobre las alturas de la mar:', 'cosmic'),
('Old', 'Job', 9, 9, 'El que hizo el Arcturo, y el Orión, y las Pléyadas, Y los lugares secretos del mediodía:', 'cosmic'),
('Old', 'Job', 9, 10, 'El que hace cosas grandes é incomprensibles, Y maravillosas, sin número.', 'cosmic'),
('Old', 'Job', 9, 11, 'He aquí que él pasará delante de mí, y yo no lo veré; Y pasará, y no lo entenderé.', 'cosmic'),
('Old', 'Job', 9, 12, 'He aquí, arrebatará; ¿quién le hará restituir? ¿Quién le dirá, Qué haces?', 'cosmic'),
('Old', 'Job', 9, 13, 'Dios no tornará atrás su ira, Y debajo de él se encorvan los que ayudan á los soberbios.', 'cosmic'),
('Old', 'Job', 9, 14, '¿Cuánto menos le responderé yo, Y hablaré con él palabras estudiadas?', 'cosmic'),
('Old', 'Job', 9, 15, 'Que aunque fuese yo justo, no responderé; Antes habré de rogar á mi juez.', 'cosmic'),
('Old', 'Job', 9, 16, 'Que si yo le invocase, y él me respondiese, Aun no creeré que haya escuchado mi voz.', 'cosmic'),
('Old', 'Job', 9, 17, 'Porque me ha quebrado con tempestad, Y ha aumentado mis heridas sin causa.', 'cosmic'),
('Old', 'Job', 9, 18, 'No me ha concedido que tome mi aliento; Mas hame hartado de amarguras.', 'cosmic'),
('Old', 'Job', 9, 19, 'Si habláremos de su potencia, fuerte por cierto es; Si de juicio, ¿quién me emplazará?', 'cosmic'),
('Old', 'Job', 9, 20, 'Si yo me justificare, me condenará mi boca; Si me dijere perfecto, esto me hará inicuo.', 'cosmic'),
('Old', 'Job', 9, 21, 'Bien que yo fuese íntegro, no conozco mi alma: Reprocharé mi vida.', 'cosmic'),
('Old', 'Job', 9, 22, 'Una cosa resta que yo diga: Al perfecto y al impío él los consume.', 'cosmic'),
('Old', 'Job', 9, 23, 'Si azote mata de presto, Ríese de la prueba de los inocentes.', 'cosmic'),
('Old', 'Job', 9, 24, 'La tierra es entregada en manos de los impíos, Y él cubre el rostro de sus jueces. Si no es él, ¿quién es? ¿dónde está?', 'cosmic'),
('Old', 'Job', 9, 25, 'Mis días han sido más ligeros que un correo; Huyeron, y no vieron el bien.', 'cosmic'),
('Old', 'Job', 9, 26, 'Pasaron cual navíos veloces: Como el águila que se arroja á la comida.', 'cosmic'),
('Old', 'Job', 9, 27, 'Si digo: Olvidaré mi queja, Dejaré mi aburrimiento, y esforzaréme:', 'cosmic'),
('Old', 'Job', 9, 28, 'Contúrbanme todos mis trabajos; Sé que no me darás por libre.', 'cosmic'),
('Old', 'Job', 9, 29, 'Yo soy impío, ¿Para qué trabajaré en vano?', 'cosmic'),
('Old', 'Job', 9, 30, 'Aunque me lave con aguas de nieve, Y limpie mis manos con la misma limpieza,', 'cosmic'),
('Old', 'Job', 9, 31, 'Aun me hundirás en el hoyo, Y mis propios vestidos me abominarán.', 'cosmic'),
('Old', 'Job', 9, 32, 'Porque no es hombre como yo, para que yo le responda, Y vengamos juntamente á juicio.', 'cosmic'),
('Old', 'Job', 9, 33, 'No hay entre nosotros árbitro Que ponga su mano sobre nosotros ambos.', 'cosmic'),
('Old', 'Job', 9, 34, 'Quite de sobre mí su vara, Y su terror no me espante.', 'cosmic'),
('Old', 'Job', 9, 35, 'Entonces hablaré, y no le temeré: Porque así no estoy en mí mismo.', 'cosmic'),
('Old', 'Job', 10, 1, 'ESTA mi alma aburrida de mi vida: Daré yo suelta á mi queja sobre mí, Hablaré con amargura de mi alma.', 'cosmic'),
('Old', 'Job', 10, 2, 'Diré á Dios: no me condenes; Hazme entender por qué pleiteas conmigo.', 'cosmic'),
('Old', 'Job', 10, 3, '¿Parécete bien que oprimas, Que deseches la obra de tus manos, Y que resplandezcas sobre el consejo de los impíos?', 'cosmic'),
('Old', 'Job', 10, 4, '¿Tienes tú ojos de carne? ¿Ves tú como ve el hombre?', 'cosmic'),
('Old', 'Job', 10, 5, '¿Son tus días como los días del hombre, O tus años como los tiempos humanos,', 'cosmic'),
('Old', 'Job', 10, 6, 'Para que inquieras mi iniquidad, Y busques mi pecado,', 'cosmic'),
('Old', 'Job', 10, 7, 'Sobre saber tú que no soy impío, Y que no hay quien de tu mano libre?', 'cosmic'),
('Old', 'Job', 10, 8, 'Tus manos me formaron y me compusieron Todo en contorno: ¿y así me deshaces?', 'cosmic'),
('Old', 'Job', 10, 9, 'Acuérdate ahora que como á lodo me diste forma: ¿Y en polvo me has de tornar?', 'cosmic'),
('Old', 'Job', 10, 10, '¿No me fundiste como leche, Y como un queso me cuajaste?', 'cosmic'),
('Old', 'Job', 10, 11, 'Vestísteme de piel y carne, Y cubrísteme de huesos y nervios.', 'cosmic'),
('Old', 'Job', 10, 12, 'Vida y misericordia me concediste, Y tu visitación guardó mi espíritu.', 'cosmic'),
('Old', 'Job', 10, 13, 'Y estas cosas tienes guardadas en tu corazón; Yo sé que esto está cerca de ti.', 'cosmic'),
('Old', 'Job', 10, 14, 'Si pequé, tú me has observado, Y no me limpias de mi iniquidad.', 'cosmic'),
('Old', 'Job', 10, 15, 'Si fuere malo, ¡ay de mí! Y si fuere justo, no levantaré mi cabeza, Estando harto de deshonra, Y de verme afligido.', 'cosmic'),
('Old', 'Job', 10, 16, 'Y subirá de punto, pues me cazas como á león, Y tornas á hacer en mí maravillas.', 'cosmic'),
('Old', 'Job', 10, 17, 'Renuevas contra mí tus plagas, Y aumentas conmigo tu furor, Remudándose sobre mí ejércitos.', 'cosmic'),
('Old', 'Job', 10, 18, '¿Por qué me sacaste de la matriz? Habría yo espirado, y no me vieran ojos.', 'cosmic'),
('Old', 'Job', 10, 19, 'Fuera, como si nunca hubiera sido, Llevado desde el vientre á la sepultura.', 'cosmic'),
('Old', 'Job', 10, 20, '¿No son mis días poca cosa? Cesa pues, y déjame, para que me conforte un poco.', 'cosmic'),
('Old', 'Job', 10, 21, 'Antes que vaya para no volver, A la tierra de tinieblas y de sombra de muerte;', 'cosmic'),
('Old', 'Job', 10, 22, 'Tierra de oscuridad, lóbrega Como sombra de muerte, sin orden, Y que aparece como la oscuridad misma.', 'cosmic'),
('Old', 'Job', 11, 1, 'Y RESPONDIO Sophar Naamathita, y dijo:', 'cosmic'),
('Old', 'Job', 11, 2, '¿Las muchas palabras no han de tener respuesta? ¿Y el hombre parlero será justificado?', 'cosmic'),
('Old', 'Job', 11, 3, '¿Harán tus falacias callar á los hombres? ¿Y harás escarnio, y no habrá quien te avergüence?', 'cosmic'),
('Old', 'Job', 11, 4, 'Tú dices: Mi conversar es puro, Y yo soy limpio delante de tus ojos.', 'cosmic'),
('Old', 'Job', 11, 5, 'Mas ¡oh quién diera que Dios hablara, Y abriera sus labios contigo,', 'cosmic'),
('Old', 'Job', 11, 6, 'Y que te declarara los arcanos de la sabiduría, Que son de doble valor que la hacienda! Conocerías entonces que Dios te ha castigado menos que tu iniquidad merece.', 'cosmic'),
('Old', 'Job', 11, 7, '¿Alcanzarás tú el rastro de Dios? ¿Llegarás tú á la perfección del Todopoderoso?', 'cosmic'),
('Old', 'Job', 11, 8, 'Es más alto que los cielos: ¿qué harás? Es más profundo que el infierno: ¿cómo lo conocerás?', 'cosmic'),
('Old', 'Job', 11, 9, 'Su dimensión es más larga que la tierra, Y más ancha que la mar.', 'cosmic'),
('Old', 'Job', 11, 10, 'Si cortare, ó encerrare, O juntare, ¿quién podrá contrarrestarle?', 'cosmic'),
('Old', 'Job', 11, 11, 'Porque él conoce á los hombres vanos: Ve asimismo la iniquidad, ¿y no hará caso?', 'cosmic'),
('Old', 'Job', 11, 12, 'El hombre vano se hará entendido, Aunque nazca como el pollino del asno montés.', 'cosmic'),
('Old', 'Job', 11, 13, 'Si tú apercibieres tu corazón, Y extendieres á él tus manos;', 'cosmic'),
('Old', 'Job', 11, 14, 'Si alguna iniquidad hubiere en tu mano, y la echares de ti, Y no consintieres que more maldad en tus habitaciones;', 'cosmic'),
('Old', 'Job', 11, 15, 'Entonces levantarás tu rostro limpio de mancha, Y serás fuerte y no temerás:', 'cosmic'),
('Old', 'Job', 11, 16, 'Y olvidarás tu trabajo, O te acordarás de él como de aguas que pasaron:', 'cosmic'),
('Old', 'Job', 11, 17, 'Y en mitad de la siesta se levantará bonanza; Resplandecerás, y serás como la mañana:', 'cosmic'),
('Old', 'Job', 11, 18, 'Y confiarás, que habrá esperanza; Y cavarás, y dormirás seguro:', 'cosmic'),
('Old', 'Job', 11, 19, 'Y te acostarás, y no habrá quien te espante: Y muchos te rogarán.', 'cosmic'),
('Old', 'Job', 11, 20, 'Mas los ojos de los malos se consumirán, Y no tendrán refugio; Y su esperanza será agonía del alma.', 'cosmic'),
('Old', 'Job', 12, 1, 'Y RESPONDIO Job, y dijo:', 'cosmic'),
('Old', 'Job', 12, 2, 'Ciertamente que vosotros sois el pueblo; Y con vosotros morirá la sabiduría.', 'cosmic'),
('Old', 'Job', 12, 3, 'También tengo yo seso como vosotros; No soy yo menos que vosotros: ¿Y quién habrá que no pueda decir otro tanto?', 'cosmic'),
('Old', 'Job', 12, 4, 'Yo soy uno de quien su amigo se mofa, Que invoca á Dios, y él le responde: Con todo, el justo y perfecto es escarnecido.', 'cosmic'),
('Old', 'Job', 12, 5, 'Aquel cuyos pies van á resbalar, Es como una lámpara despreciada de aquel que está á sus anchuras.', 'cosmic'),
('Old', 'Job', 12, 6, 'Prosperan las tiendas de los ladrones, Y los que provocan á Dios viven seguros; En cuyas manos él ha puesto cuanto tienen.', 'cosmic'),
('Old', 'Job', 12, 7, 'Y en efecto, pregunta ahora á las bestias, que ellas te enseñarán; Y á las aves de los cielos, que ellas te lo mostrarán;', 'cosmic'),
('Old', 'Job', 12, 8, 'O habla á la tierra, que ella te enseñará; Los peces de la mar te lo declararán también.', 'cosmic'),
('Old', 'Job', 12, 9, '¿Qué cosa de todas estas no entiende Que la mano de Jehová la hizo?', 'cosmic'),
('Old', 'Job', 12, 10, 'En su mano está el alma de todo viviente, Y el espíritu de toda carne humana.', 'cosmic'),
('Old', 'Job', 12, 11, 'Ciertamente el oído distingue las palabras, Y el paladar gusta las viandas.', 'cosmic'),
('Old', 'Job', 12, 12, 'En los viejos está la ciencia, Y en la larga edad la inteligencia.', 'cosmic'),
('Old', 'Job', 12, 13, 'Con Dios está la sabiduría y la fortaleza; Suyo es el consejo y la inteligencia.', 'cosmic'),
('Old', 'Job', 12, 14, 'He aquí, él derribará, y no será edificado: Encerrará al hombre, y no habrá quien le abra.', 'cosmic'),
('Old', 'Job', 12, 15, 'He aquí, el detendrá las aguas, y se secarán; El las enviará, y destruirán la tierra.', 'cosmic'),
('Old', 'Job', 12, 16, 'Con él está la fortaleza y la existencia; Suyo es el que yerra, y el que hace errar.', 'cosmic'),
('Old', 'Job', 12, 17, 'El hace andar á los consejeros desnudos de consejo, Y hace enloquecer á los jueces.', 'cosmic'),
('Old', 'Job', 12, 18, 'El suelta la atadura de los tiranos, Y ata el cinto á sus lomos.', 'cosmic'),
('Old', 'Job', 12, 19, 'El lleva despojados á los príncipes, Y trastorna á los poderosos.', 'cosmic'),
('Old', 'Job', 12, 20, 'El impide el labio á los que dicen verdad, Y quita á los ancianos el consejo.', 'cosmic'),
('Old', 'Job', 12, 21, 'El derrama menosprecio sobre los príncipes, Y enflaquece la fuerza de los esforzados.', 'cosmic'),
('Old', 'Job', 12, 22, 'El descubre las profundidades de las tinieblas, Y saca á luz la sombra de muerte.', 'cosmic'),
('Old', 'Job', 12, 23, 'El multiplica las gentes, y él las destruye: El esparce las gentes, y las torna á recoger.', 'cosmic'),
('Old', 'Job', 12, 24, 'El quita el seso de las cabezas del pueblo de la tierra, Y háceles que se pierdan vagueando sin camino:', 'cosmic'),
('Old', 'Job', 12, 25, 'Van á tientas como en tinieblas y sin luz, Y los hace errar como borrachos.', 'cosmic'),
('Old', 'Job', 13, 1, 'HE AQUI que todas estas cosas han visto mis ojos, Y oído y entendido de por sí mis oídos.', 'cosmic'),
('Old', 'Job', 13, 2, 'Como vosotros lo sabéis, lo sé yo; No soy menos que vosotros.', 'cosmic'),
('Old', 'Job', 13, 3, 'Mas yo hablaría con el Todopoderoso, Y querría razonar con Dios.', 'cosmic'),
('Old', 'Job', 13, 4, 'Que ciertamente vosotros sois fraguadores de mentira; Sois todos vosotros médicos nulos.', 'cosmic'),
('Old', 'Job', 13, 5, 'Ojalá callarais del todo, Porque os fuera sabiduría.', 'cosmic'),
('Old', 'Job', 13, 6, 'Oid ahora mi razonamiento, Y estad atentos á los argumentos de mis labios.', 'cosmic'),
('Old', 'Job', 13, 7, '¿Habéis de hablar iniquidad por Dios? ¿Habéis de hablar por él engaño?', 'cosmic'),
('Old', 'Job', 13, 8, '¿Habéis de hacer acepción de su persona? ¿Habéis de pleitear vosotros por Dios?', 'cosmic'),
('Old', 'Job', 13, 9, '¿Sería bueno que él os escudriñase? ¿Os burlaréis de él como quien se burla de algún hombre?', 'cosmic'),
('Old', 'Job', 13, 10, 'El os reprochará de seguro, Si solapadamente hacéis acepción de personas.', 'cosmic'),
('Old', 'Job', 13, 11, 'De cierto su alteza os había de espantar, Y su pavor había de caer sobre vosotros.', 'cosmic'),
('Old', 'Job', 13, 12, 'Vuestras memorias serán comparadas á la ceniza, Y vuestros cuerpos como cuerpos de lodo.', 'cosmic'),
('Old', 'Job', 13, 13, 'Escuchadme, y hablaré yo, Y véngame después lo que viniere.', 'cosmic'),
('Old', 'Job', 13, 14, '¿Por qué quitaré yo mi carne con mis dientes, Y pondré mi alma en mi mano?', 'cosmic'),
('Old', 'Job', 13, 15, 'He aquí, aunque me matare, en él esperaré; Empero defenderé delante de él mis caminos.', 'cosmic'),
('Old', 'Job', 13, 16, 'Y él mismo me será salud, Porque no entrará en su presencia el hipócrita.', 'cosmic'),
('Old', 'Job', 13, 17, 'Oid con atención mi razonamiento, Y mi denunciación con vuestros oídos.', 'cosmic'),
('Old', 'Job', 13, 18, 'He aquí ahora, si yo me apercibiere á juicio, Sé que seré justificado.', 'cosmic'),
('Old', 'Job', 13, 19, '¿Quién es el que pleiteará conmigo? Porque si ahora yo callara, fenecería.', 'cosmic'),
('Old', 'Job', 13, 20, 'A lo menos dos cosas no hagas conmigo; Entonces no me esconderé de tu rostro:', 'cosmic'),
('Old', 'Job', 13, 21, 'Aparta de mí tu mano, Y no me asombre tu terror.', 'cosmic'),
('Old', 'Job', 13, 22, 'Llama luego, y yo responderé; O yo hablaré, y respóndeme tú.', 'cosmic'),
('Old', 'Job', 13, 23, '¿Cuántas iniquidades y pecados tengo yo? Hazme entender mi prevaricación y mi pecado.', 'cosmic'),
('Old', 'Job', 13, 24, '¿Por qué escondes tu rostro, Y me cuentas por tu enemigo?', 'cosmic'),
('Old', 'Job', 13, 25, '¿A la hoja arrebatada has de quebrantar? ¿Y á una arista seca has de perseguir?', 'cosmic'),
('Old', 'Job', 13, 26, '¿Por qué escribes contra mí amarguras, Y me haces cargo de los pecados de mi mocedad?', 'cosmic'),
('Old', 'Job', 13, 27, 'Pones además mis pies en el cepo, y guardas todos mis caminos, Imprimiéndolo á las raíces de mis pies.', 'cosmic'),
('Old', 'Job', 13, 28, 'Y el cuerpo mío se va gastando como de carcoma, Como vestido que se come de polilla.', 'cosmic'),
('Old', 'Job', 14, 1, 'EL HOMBRE nacido de mujer, Corto de días, y harto de sinsabores:', 'cosmic'),
('Old', 'Job', 14, 2, 'Que sale como una flor y es cortado; Y huye como la sombra, y no permanece.', 'cosmic'),
('Old', 'Job', 14, 3, '¿Y sobre éste abres tus ojos, Y me traes á juicio contigo?', 'cosmic'),
('Old', 'Job', 14, 4, '¿Quién hará limpio de inmundo? Nadie.', 'cosmic'),
('Old', 'Job', 14, 5, 'Ciertamente sus días están determinados, y el número de sus meses está cerca de ti: Tú le pusiste términos, de los cuales no pasará.', 'cosmic'),
('Old', 'Job', 14, 6, 'Si tú lo dejares, él dejará de ser: Entre tanto deseará, como el jornalero, su día.', 'cosmic'),
('Old', 'Job', 14, 7, 'Porque si el árbol fuere cortado, aún queda de él esperanza; retoñecerá aún, Y sus renuevos no faltarán.', 'cosmic'),
('Old', 'Job', 14, 8, 'Si se envejeciere en la tierra su raíz, Y su tronco fuere muerto en el polvo,', 'cosmic'),
('Old', 'Job', 14, 9, 'Al percibir el agua reverdecerá, Y hará copa como planta.', 'cosmic'),
('Old', 'Job', 14, 10, 'Mas el hombre morirá, y será cortado; Y perecerá el hombre, ¿y dónde estará él?', 'cosmic'),
('Old', 'Job', 14, 11, 'Las aguas de la mar se fueron, Y agotóse el río, secóse.', 'cosmic'),
('Old', 'Job', 14, 12, 'Así el hombre yace, y no se tornará á levantar: Hasta que no haya cielo no despertarán, Ni se levantarán de su sueño.', 'cosmic'),
('Old', 'Job', 14, 13, '¡Oh quién me diera que me escondieses en el sepulcro, Que me encubrieras hasta apaciguarse tu ira, Que me pusieses plazo, y de mí te acordaras!', 'cosmic'),
('Old', 'Job', 14, 14, 'Si el hombre muriere, ¿volverá á vivir? Todos los días de mi edad esperaré, Hasta que venga mi mutación.', 'cosmic'),
('Old', 'Job', 14, 15, 'Aficionado á la obra de tus manos, Llamarás, y yo te responderé.', 'cosmic'),
('Old', 'Job', 14, 16, 'Pues ahora me cuentas los pasos, Y no das tregua á mi pecado.', 'cosmic'),
('Old', 'Job', 14, 17, 'Tienes sellada en saco mi prevaricación, Y coacervas mi iniquidad.', 'cosmic'),
('Old', 'Job', 14, 18, 'Y ciertamente el monte que cae se deshace, Y las peñas son traspasadas de su lugar;', 'cosmic'),
('Old', 'Job', 14, 19, 'Las piedras son desgastadas con el agua impetuosa, Que se lleva el polvo de la tierra: de tal manera haces tú perecer la esperanza del hombre.', 'cosmic'),
('Old', 'Job', 14, 20, 'Para siempre serás más fuerte que él, y él se va; Demudarás su rostro, y enviaráslo.', 'cosmic'),
('Old', 'Job', 14, 21, 'Sus hijos serán honrados, y él no lo sabrá; O serán humillados, y no entenderá de ellos.', 'cosmic'),
('Old', 'Job', 14, 22, 'Mas su carne sobre él se dolerá, Y entristecerse ha en él su alma.', 'cosmic'),
('Old', 'Job', 15, 1, 'Y RESPONDIO Eliphaz Temanita, y dijo:', 'cosmic'),
('Old', 'Job', 15, 2, '¿Si proferirá el sabio vana sabiduría, Y henchirá su vientre de viento solano?', 'cosmic'),
('Old', 'Job', 15, 3, '¿Disputará con palabras inútiles, Y con razones sin provecho?', 'cosmic'),
('Old', 'Job', 15, 4, 'Tú también disipas el temor, Y menoscabas la oración delante de Dios.', 'cosmic'),
('Old', 'Job', 15, 5, 'Porque tu boca declaró tu iniquidad, Pues has escogido el hablar de los astutos.', 'cosmic'),
('Old', 'Job', 15, 6, 'Tu boca te condenará, y no yo; Y tus labios testificarán contra ti.', 'cosmic'),
('Old', 'Job', 15, 7, '¿Naciste tú primero que Adam? ¿O fuiste formado antes que los collados?', 'cosmic'),
('Old', 'Job', 15, 8, '¿Oíste tú el secreto de Dios, Que detienes en ti solo la sabiduría?', 'cosmic'),
('Old', 'Job', 15, 9, '¿Qué sabes tú que no sepamos? ¿Qué entiendes que no se halle en nosotros?', 'cosmic'),
('Old', 'Job', 15, 10, 'Entre nosotros también hay cano, también hay viejo Mucho mayor en días que tu padre.', 'cosmic'),
('Old', 'Job', 15, 11, '¿En tan poco tienes las consolaciones de Dios? ¿Tienes acaso alguna cosa oculta cerca de ti?', 'cosmic'),
('Old', 'Job', 15, 12, '¿Por qué te enajena tu corazón, Y por qué guiñan tus ojos,', 'cosmic'),
('Old', 'Job', 15, 13, 'Pues haces frente á Dios con tu espíritu, Y sacas tales palabras de tu boca?', 'cosmic'),
('Old', 'Job', 15, 14, '¿Qué cosa es el hombre para que sea limpio, Y que se justifique el nacido de mujer?', 'cosmic'),
('Old', 'Job', 15, 15, 'He aquí que en sus santos no confía, Y ni los cielos son limpios delante de sus ojos:', 'cosmic'),
('Old', 'Job', 15, 16, '¿Cuánto menos el hombre abominable y vil, Que bebe la iniquidad como agua?', 'cosmic'),
('Old', 'Job', 15, 17, 'Escúchame; yo te mostraré Y te contaré lo que he visto:', 'cosmic'),
('Old', 'Job', 15, 18, '(Lo que los sabios nos contaron De sus padres, y no lo encubrieron;', 'cosmic'),
('Old', 'Job', 15, 19, 'A los cuales solos fué dada la tierra, Y no pasó extraño por medio de ellos:)', 'cosmic'),
('Old', 'Job', 15, 20, 'Todos los días del impío, él es atormentado de dolor, Y el número de años es escondido al violento.', 'cosmic'),
('Old', 'Job', 15, 21, 'Estruendos espantosos hay en sus oídos; En la paz le vendrá quien lo asuele.', 'cosmic'),
('Old', 'Job', 15, 22, 'El no creerá que ha de volver de las tinieblas, Y está mirando al cuchillo.', 'cosmic'),
('Old', 'Job', 15, 23, 'Desasosegado á comer siempre, Sabe que le está aparejado día de tinieblas.', 'cosmic'),
('Old', 'Job', 15, 24, 'Tribulación y angustia le asombrarán, Y esforzaránse contra él como un rey apercibido para la batalla.', 'cosmic'),
('Old', 'Job', 15, 25, 'Por cuanto él extendió su mano contra Dios, Y se esforzó contra el Todopoderoso,', 'cosmic'),
('Old', 'Job', 15, 26, 'El le acometerá en la cerviz, En lo grueso de las hombreras de sus escudos:', 'cosmic'),
('Old', 'Job', 15, 27, 'Porque cubrió su rostro con su gordura, E hizo pliegues sobre los ijares;', 'cosmic'),
('Old', 'Job', 15, 28, 'Y habitó las ciudades asoladas, Las casas inhabitadas, Que estaban puestas en montones.', 'cosmic'),
('Old', 'Job', 15, 29, 'No enriquecerá, ni será firme su potencia, Ni extenderá por la tierra su hermosura.', 'cosmic'),
('Old', 'Job', 15, 30, 'No se escapará de las tinieblas: La llama secará sus ramos, Y con el aliento de su boca perecerá.', 'cosmic'),
('Old', 'Job', 15, 31, 'No confíe el iluso en la vanidad; Porque ella será su recompensa.', 'cosmic'),
('Old', 'Job', 15, 32, 'El será cortado antes de su tiempo, Y sus renuevos no reverdecerán.', 'cosmic'),
('Old', 'Job', 15, 33, 'El perderá su agraz como la vid, Y derramará su flor como la oliva.', 'cosmic'),
('Old', 'Job', 15, 34, 'Porque la sociedad de los hipócritas será asolada, Y fuego consumirá las tiendas de soborno.', 'cosmic'),
('Old', 'Job', 15, 35, 'Concibieron dolor, y parieron iniquidad; Y las entradas de ellos meditan engaño.', 'cosmic'),
('Old', 'Job', 16, 1, 'Y RESPONDIO Job, y dijo:', 'cosmic'),
('Old', 'Job', 16, 2, 'Muchas veces he oído cosas como estas: Consoladores molestos sois todos vosotros.', 'cosmic'),
('Old', 'Job', 16, 3, '¿Tendrán fin las palabras ventosas? O ¿qué te animará á responder?', 'cosmic'),
('Old', 'Job', 16, 4, 'También yo hablaría como vosotros. Ojalá vuestra alma estuviera en lugar de la mía, Que yo os tendría compañía en las palabras, Y sobre vosotros movería mi cabeza.', 'cosmic'),
('Old', 'Job', 16, 5, 'Mas yo os alentaría con mis palabras, Y la consolación de mis labios apaciguaría el dolor vuestro.', 'cosmic'),
('Old', 'Job', 16, 6, 'Si hablo, mi dolor no cesa; Y si dejo de hablar, no se aparta de mí.', 'cosmic'),
('Old', 'Job', 16, 7, 'Empero ahora me ha fatigado: Has tú asolado toda mi compañía.', 'cosmic'),
('Old', 'Job', 16, 8, 'Tú me has arrugado; testigo es mi flacura, Que se levanta contra mí para testificar en mi rostro.', 'cosmic'),
('Old', 'Job', 16, 9, 'Su furor me destrizó, y me ha sido contrario: Crujió sus dientes contra mí; Contra mí aguzó sus ojos mi enemigo.', 'cosmic'),
('Old', 'Job', 16, 10, 'Abrieron contra mí su boca; Hirieron mis mejillas con afrenta; Contra mí se juntaron todos.', 'cosmic'),
('Old', 'Job', 16, 11, 'Hame entregado Dios al mentiroso, Y en las manos de los impíos me hizo estremecer.', 'cosmic'),
('Old', 'Job', 16, 12, 'Próspero estaba, y desmenuzóme: Y arrebatóme por la cerviz, y despedazóme, Y púsome por blanco suyo.', 'cosmic'),
('Old', 'Job', 16, 13, 'Cercáronme sus flecheros, Partió mis riñones, y no perdonó: Mi hiel derramó por tierra.', 'cosmic'),
('Old', 'Job', 16, 14, 'Quebrantóme de quebranto sobre quebranto; Corrió contra mí como un gigante.', 'cosmic'),
('Old', 'Job', 16, 15, 'Yo cosí saco sobre mi piel, Y cargué mi cabeza de polvo.', 'cosmic'),
('Old', 'Job', 16, 16, 'Mi rostro está enlodado con lloro, Y mis párpados entenebrecidos:', 'cosmic'),
('Old', 'Job', 16, 17, 'A pesar de no haber iniquidad en mis manos, Y de haber sido mi oración pura.', 'cosmic'),
('Old', 'Job', 16, 18, '¡Oh tierra! no cubras mi sangre, Y no haya lugar á mi clamor.', 'cosmic'),
('Old', 'Job', 16, 19, 'Mas he aquí que en los cielos está mi testigo, Y mi testimonio en las alturas.', 'cosmic'),
('Old', 'Job', 16, 20, 'Disputadores son mis amigos: Mas á Dios destilarán mis ojos.', 'cosmic'),
('Old', 'Job', 16, 21, '¡Ojalá pudiese disputar el hombre con Dios, Como con su prójimo!', 'cosmic'),
('Old', 'Job', 16, 22, 'Mas los años contados vendrán, Y yo iré el camino por donde no volveré.', 'cosmic'),
('Old', 'Job', 17, 1, 'MI ALIENTO está corrompido, acórtanse mis días, Y me está aparejado el sepulcro.', 'cosmic'),
('Old', 'Job', 17, 2, 'No hay conmigo sino escarnecedores, En cuya acrimonia se detienen mis ojos.', 'cosmic'),
('Old', 'Job', 17, 3, 'Pon ahora, dame fianza para litigar contigo: ¿Quién tocará ahora mi mano?', 'cosmic'),
('Old', 'Job', 17, 4, 'Porque á éstos has tú escondido su corazón de inteligencia: Por tanto, no los ensalzarás.', 'cosmic'),
('Old', 'Job', 17, 5, 'El que denuncia lisonjas á sus prójimos, Los ojos de sus hijos desfallezcan.', 'cosmic'),
('Old', 'Job', 17, 6, 'El me ha puesto por parábola de pueblos, Y delante de ellos he sido como tamboril.', 'cosmic'),
('Old', 'Job', 17, 7, 'Y mis ojos se oscurecieron de desabrimiento, Y mis pensamientos todos son como sombra.', 'cosmic'),
('Old', 'Job', 17, 8, 'Los rectos se maravillarán de esto, Y el inocente se levantará contra el hipócrita.', 'cosmic'),
('Old', 'Job', 17, 9, 'No obstante, proseguirá el justo su camino, Y el limpio de manos aumentará la fuerza.', 'cosmic'),
('Old', 'Job', 17, 10, 'Mas volved todos vosotros, y venid ahora, Que no hallaré entre vosotros sabio.', 'cosmic'),
('Old', 'Job', 17, 11, 'Pasáronse mis días, fueron arrancados mis pensamientos, Los designios de mi corazón.', 'cosmic'),
('Old', 'Job', 17, 12, 'Pusieron la noche por día, Y la luz se acorta delante de las tinieblas.', 'cosmic'),
('Old', 'Job', 17, 13, 'Si yo espero, el sepulcro es mi casa: Haré mi cama en las tinieblas.', 'cosmic'),
('Old', 'Job', 17, 14, 'A la huesa tengo dicho: Mi padre eres tú; A los gusanos: Mi madre y mi hermana.', 'cosmic'),
('Old', 'Job', 17, 15, '¿Dónde pues estará ahora mi esperanza? Y mi esperanza ¿quién la verá?', 'cosmic'),
('Old', 'Job', 17, 16, 'A los rincones de la huesa descenderán, Y juntamente descansarán en el polvo.', 'cosmic'),
('Old', 'Job', 18, 1, 'Y RESPONDIO Bildad Suhita, y dijo:', 'cosmic'),
('Old', 'Job', 18, 2, '¿Cuándo pondréis fin á las palabras? Entended, y después hablemos.', 'cosmic'),
('Old', 'Job', 18, 3, '¿Por qué somos tenidos por bestias, Y en vuestros ojos somos viles?', 'cosmic'),
('Old', 'Job', 18, 4, 'Oh tú, que despedazas tu alma con tu furor, ¿Será dejada la tierra por tu causa, Y serán traspasadas de su lugar las peñas?', 'cosmic'),
('Old', 'Job', 18, 5, 'Ciertamente la luz de los impíos será apagada, Y no resplandecerá la centella de su fuego.', 'cosmic'),
('Old', 'Job', 18, 6, 'La luz se oscurecerá en su tienda, Y apagaráse sobre él su lámpara.', 'cosmic'),
('Old', 'Job', 18, 7, 'Los pasos de su pujanza serán acortados, Y precipitarálo su mismo consejo.', 'cosmic'),
('Old', 'Job', 18, 8, 'Porque red será echada en sus pies, Y sobre red andará.', 'cosmic'),
('Old', 'Job', 18, 9, 'Lazo prenderá su calcañar: Afirmaráse la trampa contra él.', 'cosmic'),
('Old', 'Job', 18, 10, 'Su cuerda está escondida en la tierra, Y su torzuelo sobre la senda.', 'cosmic'),
('Old', 'Job', 18, 11, 'De todas partes lo asombrarán temores, Y haránle huir desconcertado.', 'cosmic'),
('Old', 'Job', 18, 12, 'Su fuerza será hambrienta, Y á su lado estará aparejado quebrantamiento.', 'cosmic'),
('Old', 'Job', 18, 13, 'El primogénito de la muerte comerá los ramos de su piel, Y devorará sus miembros.', 'cosmic'),
('Old', 'Job', 18, 14, 'Su confianza será arrancada de su tienda, Y harále esto llevar al rey de los espantos.', 'cosmic'),
('Old', 'Job', 18, 15, 'En su tienda morará como si no fuese suya: Piedra azufre será esparcida sobre su morada.', 'cosmic'),
('Old', 'Job', 18, 16, 'Abajo se secarán sus raíces, Y arriba serán cortadas sus ramas.', 'cosmic'),
('Old', 'Job', 18, 17, 'Su memoria perecerá de la tierra, Y no tendrá nombre por las calles.', 'cosmic'),
('Old', 'Job', 18, 18, 'De la luz será lanzado á las tinieblas, Y echado fuera del mundo.', 'cosmic'),
('Old', 'Job', 18, 19, 'No tendrá hijo ni nieto en su pueblo, Ni quien le suceda en sus moradas.', 'cosmic'),
('Old', 'Job', 18, 20, 'Sobre su día se espantarán los por venir, Como ocupó el pavor á los que fueron antes.', 'cosmic'),
('Old', 'Job', 18, 21, 'Ciertamente tales son las moradas del impío, Y este será el lugar del que no conoció á Dios.', 'cosmic'),
('Old', 'Job', 19, 1, 'Y RESPONDIO Job, y dijo:', 'cosmic'),
('Old', 'Job', 19, 2, '¿Hasta cuándo angustiaréis mi alma, Y me moleréis con palabras?', 'cosmic'),
('Old', 'Job', 19, 3, 'Ya me habéis vituperado diez veces: ¿No os avergonzáis de descomediros delante de mí?', 'cosmic'),
('Old', 'Job', 19, 4, 'Sea así que realmente haya yo errado, Conmigo se quedará mi yerro.', 'cosmic'),
('Old', 'Job', 19, 5, 'Mas si vosotros os engrandeciereis contra mí, Y adujereis contra mí mi oprobio,', 'cosmic'),
('Old', 'Job', 19, 6, 'Sabed ahora que Dios me ha trastornado, Y traído en derredor su red sobre mí.', 'cosmic'),
('Old', 'Job', 19, 7, 'He aquí yo clamaré agravio, y no seré oído: Daré voces, y no habrá juicio.', 'cosmic'),
('Old', 'Job', 19, 8, 'Cercó de vallado mi camino, y no pasaré; Y sobre mis veredas puso tinieblas.', 'cosmic'),
('Old', 'Job', 19, 9, 'Hame despojado de mi gloria, Y quitado la corona de mi cabeza.', 'cosmic'),
('Old', 'Job', 19, 10, 'Arruinóme por todos lados, y perezco; Y ha hecho pasar mi esperanza como árbol arrancado.', 'cosmic'),
('Old', 'Job', 19, 11, 'E hizo inflamar contra mí su furor, Y contóme para sí entre sus enemigos.', 'cosmic'),
('Old', 'Job', 19, 12, 'Vinieron sus ejércitos á una, y trillaron sobre mí su camino, Y asentaron campo en derredor de mi tienda.', 'cosmic'),
('Old', 'Job', 19, 13, 'Hizo alejar de mí mis hermanos, Y positivamente se extrañaron de mí mis conocidos.', 'cosmic'),
('Old', 'Job', 19, 14, 'Mis parientes se detuvieron, Y mis conocidos se olvidaron de mí.', 'cosmic'),
('Old', 'Job', 19, 15, 'Los moradores de mi casa y mis criadas me tuvieron por extraño; Forastero fuí yo en sus ojos.', 'cosmic'),
('Old', 'Job', 19, 16, 'Llamé á mi siervo, y no respondió; De mi propia boca le suplicaba.', 'cosmic'),
('Old', 'Job', 19, 17, 'Mi aliento vino á ser extraño á mi mujer, Aunque por los hijos de mis entrañas le rogaba.', 'cosmic'),
('Old', 'Job', 19, 18, 'Aun los muchachos me menospreciaron: En levantándome, hablaban contra mí.', 'cosmic'),
('Old', 'Job', 19, 19, 'Todos mis confidentes me aborrecieron; Y los que yo amaba, se tornaron contra mí.', 'cosmic'),
('Old', 'Job', 19, 20, 'Mi cuero y mi carne se pegaron á mis huesos; Y he escapado con la piel de mis dientes.', 'cosmic'),
('Old', 'Job', 19, 21, 'Oh vosotros mis amigos, tened compasión de mí, tened compasión de mí; Porque la mano de Dios me ha tocado.', 'cosmic'),
('Old', 'Job', 19, 22, '¿Por qué me perseguís como Dios, Y no os hartáis de mis carnes?', 'cosmic'),
('Old', 'Job', 19, 23, '¡Quién diese ahora que mis palabras fuesen escritas! ¡Quién diese que se escribieran en un libro!', 'cosmic'),
('Old', 'Job', 19, 24, '¡Que con cincel de hierro y con plomo Fuesen en piedra esculpidas para siempre!', 'cosmic'),
('Old', 'Job', 19, 25, 'Yo sé que mi Redentor vive, Y al fin se levantará sobre el polvo:', 'cosmic'),
('Old', 'Job', 19, 26, 'Y después de deshecha esta mi piel, Aun he de ver en mi carne á Dios;', 'cosmic'),
('Old', 'Job', 19, 27, 'Al cual yo tengo de ver por mí, Y mis ojos lo verán, y no otro, Aunque mis riñones se consuman dentro de mí.', 'cosmic'),
('Old', 'Job', 19, 28, 'Mas debierais decir: ¿Por qué lo perseguimos? Ya que la raíz del negocio en mí se halla.', 'cosmic'),
('Old', 'Job', 19, 29, 'Temed vosotros delante de la espada; Porque sobreviene el furor de la espada á causa de las injusticias, Para que sepáis que hay un juicio.', 'cosmic'),
('Old', 'Job', 20, 1, 'Y RESPONDIO Sophar Naamathita, y dijo:', 'cosmic'),
('Old', 'Job', 20, 2, 'Por cierto mis pensamientos me hacen responder, Y por tanto me apresuro.', 'cosmic'),
('Old', 'Job', 20, 3, 'La reprensión de mi censura he oído, Y háceme responder el espíritu de mi inteligencia.', 'cosmic'),
('Old', 'Job', 20, 4, '¿No sabes esto que fué siempre, Desde el tiempo que fué puesto el hombre sobre la tierra,', 'cosmic'),
('Old', 'Job', 20, 5, 'Que la alegría de los impíos es breve, Y el gozo del hipócrita por un momento?', 'cosmic'),
('Old', 'Job', 20, 6, 'Si subiere su altivez hasta el cielo, Y su cabeza tocare en las nubes,', 'cosmic'),
('Old', 'Job', 20, 7, 'Con su estiércol perecerá para siempre: Los que le hubieren visto, dirán: ¿Qué es de él?', 'cosmic'),
('Old', 'Job', 20, 8, 'Como sueño volará, y no será hallado: Y disiparáse como visión nocturna.', 'cosmic'),
('Old', 'Job', 20, 9, 'El ojo que le habrá visto, nunca más le verá; Ni su lugar le echará más de ver.', 'cosmic'),
('Old', 'Job', 20, 10, 'Sus hijos pobres andarán rogando; Y sus manos tornarán lo que él robó.', 'cosmic'),
('Old', 'Job', 20, 11, 'Sus huesos están llenos de sus mocedades, Y con él serán sepultados en el polvo.', 'cosmic'),
('Old', 'Job', 20, 12, 'Si el mal se endulzó en su boca, Si lo ocultaba debajo de su lengua;', 'cosmic'),
('Old', 'Job', 20, 13, 'Si le parecía bien, y no lo dejaba, Mas antes lo detenía entre su paladar;', 'cosmic'),
('Old', 'Job', 20, 14, 'Su comida se mudará en sus entrañas, Hiel de áspides será dentro de él.', 'cosmic'),
('Old', 'Job', 20, 15, 'Devoró riquezas, mas vomitarálas; De su vientre las sacará Dios.', 'cosmic'),
('Old', 'Job', 20, 16, 'Veneno de áspides chupará; Matarálo lengua de víbora.', 'cosmic'),
('Old', 'Job', 20, 17, 'No verá los arroyos, los ríos, Los torrentes de miel y de manteca.', 'cosmic'),
('Old', 'Job', 20, 18, 'Restituirá el trabajo conforme á la hacienda que tomó; Y no tragará, ni gozará.', 'cosmic'),
('Old', 'Job', 20, 19, 'Por cuanto quebrantó y desamparó á los pobres, Robó casas, y no las edificó;', 'cosmic'),
('Old', 'Job', 20, 20, 'Por tanto, no sentirá él sosiego en su vientre, Ni salvará nada de lo que codiciaba.', 'cosmic'),
('Old', 'Job', 20, 21, 'No quedó nada que no comiese: Por tanto su bien no será durable.', 'cosmic'),
('Old', 'Job', 20, 22, 'Cuando fuere lleno su bastimento, tendrá angustia: Las manos todas de los malvados vendrán sobre él.', 'cosmic'),
('Old', 'Job', 20, 23, 'Cuando se pusiere á henchir su vientre, Dios enviará sobre él el furor de su ira, Y harála llover sobre él y sobre su comida.', 'cosmic'),
('Old', 'Job', 20, 24, 'Huirá de las armas de hierro, Y el arco de acero le atravesará.', 'cosmic'),
('Old', 'Job', 20, 25, 'Desenvainará y sacará saeta de su aljaba, Y relumbrante pasará por su hiel: Sobre él vendrán terrores.', 'cosmic'),
('Old', 'Job', 20, 26, 'Todas tinieblas están guardadas para sus secretos: Fuego no soplado lo devorará; Su sucesor será quebrantado en su tienda.', 'cosmic'),
('Old', 'Job', 20, 27, 'Los cielos descubrirán su iniquidad, Y la tierra se levantará contra él.', 'cosmic'),
('Old', 'Job', 20, 28, 'Los renuevos de su casa serán trasportados; Serán derramados en el día de su furor.', 'cosmic'),
('Old', 'Job', 20, 29, 'Esta es la parte que Dios apareja al hombre impío, Y la heredad que Dios le señala por su palabra.', 'cosmic'),
('Old', 'Job', 21, 1, 'Y RESPONDIO Job, y dijo:', 'cosmic'),
('Old', 'Job', 21, 2, 'Oid atentamente mi palabra, Y sea esto vuestros consuelos.', 'cosmic'),
('Old', 'Job', 21, 3, 'Soportadme, y yo hablaré; Y después que hubiere hablado, escarneced.', 'cosmic'),
('Old', 'Job', 21, 4, '¿Hablo yo á algún hombre? Y ¿por qué no se ha de angustiar mi espíritu?', 'cosmic'),
('Old', 'Job', 21, 5, 'Miradme, y espantaos, Y poned la mano sobre la boca.', 'cosmic'),
('Old', 'Job', 21, 6, 'Aun yo mismo, cuando me acuerdo, me asombro, Y toma temblor mi carne.', 'cosmic'),
('Old', 'Job', 21, 7, '¿Por qué viven los impíos, Y se envejecen, y aun crecen en riquezas?', 'cosmic'),
('Old', 'Job', 21, 8, 'Su simiente con ellos, compuesta delante de ellos; Y sus renuevos delante de sus ojos.', 'cosmic'),
('Old', 'Job', 21, 9, 'Sus casas seguras de temor, Ni hay azote de Dios sobre ellos.', 'cosmic'),
('Old', 'Job', 21, 10, 'Sus vacas conciben, no abortan; Paren sus vacas, y no malogran su cría.', 'cosmic'),
('Old', 'Job', 21, 11, 'Salen sus chiquitos como manada, Y sus hijos andan saltando.', 'cosmic'),
('Old', 'Job', 21, 12, 'Al son de tamboril y cítara saltan, Y se huelgan al son del órgano.', 'cosmic'),
('Old', 'Job', 21, 13, 'Gastan sus días en bien, Y en un momento descienden á la sepultura.', 'cosmic'),
('Old', 'Job', 21, 14, 'Dicen pues á Dios: Apártate de nosotros, Que no queremos el conocimiento de tus caminos.', 'cosmic'),
('Old', 'Job', 21, 15, '¿Quién es el Todopoderoso, para que le sirvamos? ¿Y de qué nos aprovechará que oremos á él?', 'cosmic'),
('Old', 'Job', 21, 16, 'He aquí que su bien no está en manos de ellos: El consejo de los impíos lejos esté de mí.', 'cosmic'),
('Old', 'Job', 21, 17, '¡Oh cuántas veces la lámpara de los impíos es apagada, Y viene sobre ellos su quebranto, Y Dios en su ira les reparte dolores!', 'cosmic'),
('Old', 'Job', 21, 18, 'Serán como la paja delante del viento, Y como el tamo que arrebata el torbellino.', 'cosmic'),
('Old', 'Job', 21, 19, 'Dios guardará para sus hijos su violencia; Y le dará su pago, para que conozca.', 'cosmic'),
('Old', 'Job', 21, 20, 'Verán sus ojos su quebranto, Y beberá de la ira del Todopoderoso.', 'cosmic'),
('Old', 'Job', 21, 21, 'Porque ¿qué deleite tendrá él de su casa después de sí, Siendo cortado el número de sus meses?', 'cosmic'),
('Old', 'Job', 21, 22, '¿Enseñará alguien á Dios sabiduría, Juzgando él á los que están elevados?', 'cosmic'),
('Old', 'Job', 21, 23, 'Este morirá en el vigor de su hermosura, todo quieto y pacífico.', 'cosmic'),
('Old', 'Job', 21, 24, 'Sus colodras están llenas de leche, Y sus huesos serán regados de tuétano.', 'cosmic'),
('Old', 'Job', 21, 25, 'Y estotro morirá en amargura de ánimo, Y no habiendo comido jamás con gusto.', 'cosmic'),
('Old', 'Job', 21, 26, 'Igualmente yacerán ellos en el polvo, Y gusanos los cubrirán.', 'cosmic'),
('Old', 'Job', 21, 27, 'He aquí, yo conozco vuestros pensamientos, Y las imaginaciones que contra mí forjáis.', 'cosmic'),
('Old', 'Job', 21, 28, 'Porque decís: ¿Qué es de la casa del príncipe, Y qué de la tienda de las moradas de los impíos?', 'cosmic'),
('Old', 'Job', 21, 29, '¿No habéis preguntado á los que pasan por los caminos, Por cuyas señas no negaréis,', 'cosmic'),
('Old', 'Job', 21, 30, 'Que el malo es reservado para el día de la destrucción? Presentados serán en el día de las iras.', 'cosmic'),
('Old', 'Job', 21, 31, '¿Quién le denunciará en su cara su camino? Y de lo que él hizo, ¿quién le dará el pago?', 'cosmic'),
('Old', 'Job', 21, 32, 'Porque llevado será él á los sepulcros, Y en el montón permanecerá.', 'cosmic'),
('Old', 'Job', 21, 33, 'Los terrones del valle le serán dulces; Y tras de él será llevado todo hombre, Y antes de él han ido innumerables.', 'cosmic'),
('Old', 'Job', 21, 34, '¿Cómo pues me consoláis en vano, Viniendo á parar vuestras respuestas en falacia?', 'cosmic'),
('Old', 'Job', 22, 1, 'Y RESPONDIO Eliphaz Temanita, y dijo:', 'cosmic'),
('Old', 'Job', 22, 2, '¿Traerá el hombre provecho á Dios, Porque el sabio sea provechoso á sí mismo?', 'cosmic'),
('Old', 'Job', 22, 3, '¿Tiene su contentamiento el Omnipotente en que tú seas justificado, O provecho de que tú hagas perfectos tus caminos?', 'cosmic'),
('Old', 'Job', 22, 4, '¿Castigaráte acaso, O vendrá contigo á juicio porque te teme?', 'cosmic'),
('Old', 'Job', 22, 5, 'Por cierto tu malicia es grande, Y tus maldades no tienen fin.', 'cosmic'),
('Old', 'Job', 22, 6, 'Porque sacaste prenda á tus hermanos sin causa, E hiciste desnudar las ropas de los desnudos.', 'cosmic'),
('Old', 'Job', 22, 7, 'No diste de beber agua al cansado, Y detuviste el pan al hambriento.', 'cosmic'),
('Old', 'Job', 22, 8, 'Empero el hombre pudiente tuvo la tierra; Y habitó en ella el distinguido.', 'cosmic'),
('Old', 'Job', 22, 9, 'Las viudas enviaste vacías, Y los brazos de los huérfanos fueron quebrados.', 'cosmic'),
('Old', 'Job', 22, 10, 'Por tanto hay lazos alrededor de ti, Y te turba espanto repentino;', 'cosmic'),
('Old', 'Job', 22, 11, 'O tinieblas, porque no veas; Y abundancia de agua te cubre.', 'cosmic'),
('Old', 'Job', 22, 12, '¿No está Dios en la altura de los cielos? Mira lo encumbrado de las estrellas, cuán elevadas están.', 'cosmic'),
('Old', 'Job', 22, 13, '¿Y dirás tú: Qué sabe Dios? ¿Cómo juzgará por medio de la oscuridad?', 'cosmic'),
('Old', 'Job', 22, 14, 'Las nubes son su escondedero, y no ve; Y por el circuito del cielo se pasea.', 'cosmic'),
('Old', 'Job', 22, 15, '¿Quieres tú guardar la senda antigua, Que pisaron los hombres perversos?', 'cosmic'),
('Old', 'Job', 22, 16, 'Los cuales fueron cortados antes de tiempo, Cuyo fundamento fué como un río derramado:', 'cosmic'),
('Old', 'Job', 22, 17, 'Que decían á Dios: Apártate de nosotros. ¿Y qué les había hecho el Omnipotente?', 'cosmic'),
('Old', 'Job', 22, 18, 'Habíales él henchido sus casas de bienes. Sea empero el consejo de ellos lejos de mí.', 'cosmic'),
('Old', 'Job', 22, 19, 'Verán los justos y se gozarán; Y el inocente los escarnecerá, diciendo:', 'cosmic'),
('Old', 'Job', 22, 20, 'Fué cortada nuestra sustancia, Habiendo consumido el fuego el resto de ellos.', 'cosmic'),
('Old', 'Job', 22, 21, 'Amístate ahora con él, y tendrás paz; Y por ello te vendrá bien.', 'cosmic'),
('Old', 'Job', 22, 22, 'Toma ahora la ley de su boca, Y pon sus palabras en tu corazón.', 'cosmic'),
('Old', 'Job', 22, 23, 'Si te tornares al Omnipotente, serás edificado; Alejarás de tu tienda la aflicción;', 'cosmic'),
('Old', 'Job', 22, 24, 'Y tendrás más oro que tierra, Y como piedras de arroyos oro de Ophir;', 'cosmic'),
('Old', 'Job', 22, 25, 'Y el Todopoderoso será tu defensa, Y tendrás plata á montones.', 'cosmic'),
('Old', 'Job', 22, 26, 'Porque entonces te deleitarás en el Omnipotente, Y alzarás á Dios tu rostro.', 'cosmic'),
('Old', 'Job', 22, 27, 'Orarás á él, y él te oirá; Y tú pagarás tus votos.', 'cosmic'),
('Old', 'Job', 22, 28, 'Determinarás asimismo una cosa, y serte ha firme; Y sobre tus caminos resplandecerá luz.', 'cosmic'),
('Old', 'Job', 22, 29, 'Cuando fueren abatidos, dirás tú: Ensalzamiento habrá: Y Dios salvará al humilde de ojos.', 'cosmic'),
('Old', 'Job', 22, 30, 'El libertará la isla del inocente; Y por la limpieza de tus manos será librada.', 'cosmic'),
('Old', 'Job', 23, 1, 'Y RESPONDIO Job, y dijo:', 'cosmic'),
('Old', 'Job', 23, 2, 'Hoy también hablaré con amargura; Que es más grave mi llaga que mi gemido.', 'cosmic'),
('Old', 'Job', 23, 3, '¡Quién me diera el saber dónde hallar á Dios! Yo iría hasta su silla.', 'cosmic'),
('Old', 'Job', 23, 4, 'Ordenaría juicio delante de él, Y henchiría mi boca de argumentos.', 'cosmic'),
('Old', 'Job', 23, 5, 'Yo sabría lo que él me respondería, Y entendería lo que me dijese.', 'cosmic'),
('Old', 'Job', 23, 6, '¿Pleitearía conmigo con grandeza de fuerza? No: antes él la pondría en mí.', 'cosmic'),
('Old', 'Job', 23, 7, 'Allí el justo razonaría con él: Y escaparía para siempre de mi juez.', 'cosmic'),
('Old', 'Job', 23, 8, 'He aquí yo iré al oriente, y no lo hallaré; Y al occidente, y no lo percibiré:', 'cosmic'),
('Old', 'Job', 23, 9, 'Si al norte él obrare, yo no lo veré; Al mediodía se esconderá, y no lo veré.', 'cosmic'),
('Old', 'Job', 23, 10, 'Mas él conoció mi camino: Probaráme, y saldré como oro.', 'cosmic'),
('Old', 'Job', 23, 11, 'Mis pies tomaron su rastro; Guardé su camino, y no me aparté.', 'cosmic'),
('Old', 'Job', 23, 12, 'Del mandamiento de sus labios nunca me separé; Guardé las palabras de su boca más que mi comida.', 'cosmic'),
('Old', 'Job', 23, 13, 'Empero si él se determina en una cosa, ¿quién lo apartará? Su alma deseó, é hizo.', 'cosmic'),
('Old', 'Job', 23, 14, 'El pues acabará lo que ha determinado de mí: Y muchas cosas como estas hay en él.', 'cosmic'),
('Old', 'Job', 23, 15, 'Por lo cual yo me espanto en su presencia: Consideraré, y temerélo.', 'cosmic'),
('Old', 'Job', 23, 16, 'Dios ha enervado mi corazón, Y hame turbado el Omnipotente.', 'cosmic'),
('Old', 'Job', 23, 17, '¿Por qué no fuí yo cortado delante de las tinieblas, Y cubrió con oscuridad mi rostro?', 'cosmic'),
('Old', 'Job', 24, 1, 'PUESTO que no son ocultos los tiempos al Todopoderoso, ¿Por qué los que le conocen no ven sus días?', 'cosmic'),
('Old', 'Job', 24, 2, 'Traspasan los términos, Roban los ganados, y apaciéntanlos.', 'cosmic'),
('Old', 'Job', 24, 3, 'Llévanse el asno de los huérfanos; Prenden el buey de la viuda.', 'cosmic'),
('Old', 'Job', 24, 4, 'Hacen apartar del camino á los menesterosos: Y todos los pobres de la tierra se esconden.', 'cosmic'),
('Old', 'Job', 24, 5, 'He aquí, como asnos monteses en el desierto, Salen á su obra madrugando para robar; El desierto es mantenimiento de sus hijos.', 'cosmic'),
('Old', 'Job', 24, 6, 'En el campo siegan su pasto, Y los impíos vendimian la viña ajena.', 'cosmic'),
('Old', 'Job', 24, 7, 'Al desnudo hacen dormir sin ropa, Y que en el frío no tenga cobertura.', 'cosmic'),
('Old', 'Job', 24, 8, 'Con las avenidas de los montes se mojan, Y abrazan las peñas sin tener abrigo.', 'cosmic'),
('Old', 'Job', 24, 9, 'Quitan el pecho á los huérfanos, Y de sobre el pobre toman la prenda.', 'cosmic'),
('Old', 'Job', 24, 10, 'Al desnudo hacen andar sin vestido, Y á los hambrientos quitan los hacecillos.', 'cosmic'),
('Old', 'Job', 24, 11, 'De dentro de sus paredes exprimen el aceite, Pisan los lagares, y mueren de sed.', 'cosmic'),
('Old', 'Job', 24, 12, 'De la ciudad gimen los hombres, Y claman las almas de los heridos de muerte: Mas Dios no puso estorbo.', 'cosmic'),
('Old', 'Job', 24, 13, 'Ellos son los que, rebeldes á la luz, Nunca conocieron sus caminos, Ni estuvieron en sus veredas.', 'cosmic'),
('Old', 'Job', 24, 14, 'A la luz se levanta el matador, mata al pobre y al necesitado, Y de noche es como ladrón.', 'cosmic'),
('Old', 'Job', 24, 15, 'El ojo del adúltero está aguardando la noche, Diciendo: No me verá nadie: Y esconde su rostro.', 'cosmic'),
('Old', 'Job', 24, 16, 'En las tinieblas minan las casas, Que de día para sí señalaron; No conocen la luz.', 'cosmic'),
('Old', 'Job', 24, 17, 'Porque la mañana es á todos ellos como sombra de muerte; Si son conocidos, terrores de sombra de muerte los toman.', 'cosmic'),
('Old', 'Job', 24, 18, 'Son instables más que la superficie de las aguas; Su porción es maldita en la tierra; No andarán por el camino de las viñas.', 'cosmic'),
('Old', 'Job', 24, 19, 'La sequía y el calor arrebatan las aguas de la nieve; Y el sepulcro á los pecadores.', 'cosmic'),
('Old', 'Job', 24, 20, 'Olvidaráse de ellos el seno materno; de ellos sentirán los gusanos dulzura; Nunca más habrá de ellos memoria, Y como un árbol serán los impíos quebrantados.', 'cosmic'),
('Old', 'Job', 24, 21, 'A la mujer estéril que no paría, afligió; Y á la viuda nunca hizo bien.', 'cosmic'),
('Old', 'Job', 24, 22, 'Mas á los fuertes adelantó con su poder: Levantóse, y no se da por segura la vida.', 'cosmic'),
('Old', 'Job', 24, 23, 'Le dieron á crédito, y se afirmó: Sus ojos están sobre los caminos de ellos.', 'cosmic'),
('Old', 'Job', 24, 24, 'Fueron ensalzados por un poco, mas desaparecen, Y son abatidos como cada cual: serán encerrados, Y cortados como cabezas de espigas.', 'cosmic'),
('Old', 'Job', 24, 25, 'Y si no, ¿quién me desmentirá ahora, O reducirá á nada mis palabras?', 'cosmic'),
('Old', 'Job', 25, 1, 'Y RESPONDIO Bildad Suhita, y dijo:', 'cosmic'),
('Old', 'Job', 25, 2, 'El señorío y el temor están con él: El hace paz en sus alturas.', 'cosmic'),
('Old', 'Job', 25, 3, '¿Tienen sus ejércitos número? ¿Y sobre quién no está su luz?', 'cosmic'),
('Old', 'Job', 25, 4, '¿Cómo pues se justificará el hombre con Dios? ¿Y cómo será limpio el que nace de mujer?', 'cosmic'),
('Old', 'Job', 25, 5, 'He aquí que ni aun la misma luna será resplandeciente, Ni las estrellas son limpias delante de sus ojos.', 'cosmic'),
('Old', 'Job', 25, 6, '¿Cuánto menos el hombre que es un gusano, Y el hijo de hombre, también gusano?', 'cosmic'),
('Old', 'Job', 26, 1, 'Y RESPONDIO Job, y dijo:', 'cosmic'),
('Old', 'Job', 26, 2, '¿En qué ayudaste al que no tiene fuerza? ¿Has amparado al brazo sin fortaleza?', 'cosmic'),
('Old', 'Job', 26, 3, '¿En qué aconsejaste al que no tiene ciencia, Y mostraste bien sabiduría?', 'cosmic'),
('Old', 'Job', 26, 4, '¿A quién has anunciado palabras, Y cuyo es el espíritu que de ti sale?', 'cosmic'),
('Old', 'Job', 26, 5, 'Cosas inanimadas son formadas Debajo de las aguas, y los habitantes de ellas.', 'cosmic'),
('Old', 'Job', 26, 6, 'El sepulcro es descubierto delante de él, Y el infierno no tiene cobertura.', 'cosmic'),
('Old', 'Job', 26, 7, 'Extiende el alquilón sobre vacío, Cuelga la tierra sobre nada.', 'cosmic'),
('Old', 'Job', 26, 8, 'Ata las aguas en sus nubes, Y las nubes no se rompen debajo de ellas.', 'cosmic'),
('Old', 'Job', 26, 9, 'El restriñe la faz de su trono, Y sobre él extiende su nube.', 'cosmic'),
('Old', 'Job', 26, 10, 'El cercó con término la superficie de las aguas, Hasta el fin de la luz y las tinieblas.', 'cosmic'),
('Old', 'Job', 26, 11, 'Las columnas del cielo tiemblan, Y se espantan de su reprensión.', 'cosmic'),
('Old', 'Job', 26, 12, 'El rompe la mar con su poder, Y con su entendimiento hiere la hinchazón suya.', 'cosmic'),
('Old', 'Job', 26, 13, 'Su espíritu adornó los cielos; Su mano crió la serpiente tortuosa.', 'cosmic'),
('Old', 'Job', 26, 14, 'He aquí, estas son partes de sus caminos: ¡Mas cuán poco hemos oído de él! Porque el estruendo de sus fortalezas, ¿quién lo detendrá?', 'cosmic'),
('Old', 'Job', 27, 1, 'Y REASUMIO Job su discurso, y dijo:', 'cosmic'),
('Old', 'Job', 27, 2, 'Vive Dios, el cual ha apartado mi causa, Y el Omnipotente, que amargó el alma mía,', 'cosmic'),
('Old', 'Job', 27, 3, 'Que todo el tiempo que mi alma estuviere en mí, Y hubiere hálito de Dios en mis narices,', 'cosmic'),
('Old', 'Job', 27, 4, 'Mis labios no hablarán iniquidad, Ni mi lengua pronunciará engaño.', 'cosmic'),
('Old', 'Job', 27, 5, 'Nunca tal acontezca que yo os justifique: Hasta morir no quitaré de mí mi integridad.', 'cosmic'),
('Old', 'Job', 27, 6, 'Mi justicia tengo asida, y no la cederé: No me reprochará mi corazón en el tiempo de mi vida.', 'cosmic'),
('Old', 'Job', 27, 7, 'Sea como el impío mi enemigo, Y como el inicuo mi adversario.', 'cosmic'),
('Old', 'Job', 27, 8, 'Porque ¿cuál es la esperanza del hipócrita, por mucho que hubiere robado, Cuando Dios arrebatare su alma?', 'cosmic'),
('Old', 'Job', 27, 9, '¿Oirá Dios su clamor Cuando la tribulación sobre él viniere?', 'cosmic'),
('Old', 'Job', 27, 10, '¿Deleitaráse en el Omnipotente? ¿Invocará á Dios en todo tiempo?', 'cosmic'),
('Old', 'Job', 27, 11, 'Yo os enseñaré en orden á la mano de Dios: No esconderé lo que hay para con el Omnipotente.', 'cosmic'),
('Old', 'Job', 27, 12, 'He aquí que todos vosotros lo habéis visto: ¿Por qué pues os desvanecéis con fantasía?', 'cosmic'),
('Old', 'Job', 27, 13, 'Esta es para con Dios la suerte del hombre impío, Y la herencia que los violentos han de recibir del Omnipotente.', 'cosmic'),
('Old', 'Job', 27, 14, 'Si sus hijos fueren multiplicados, serán para el cuchillo; Y sus pequeños no se hartarán de pan;', 'cosmic'),
('Old', 'Job', 27, 15, 'Los que le quedaren, en muerte serán sepultados; Y no llorarán sus viudas.', 'cosmic'),
('Old', 'Job', 27, 16, 'Si amontonare plata como polvo, Y si preparare ropa como lodo;', 'cosmic'),
('Old', 'Job', 27, 17, 'Habrála él preparado, mas el justo se vestirá, Y el inocente repartirá la plata.', 'cosmic'),
('Old', 'Job', 27, 18, 'Edificó su casa como la polilla, Y cual cabaña que el guarda hizo.', 'cosmic'),
('Old', 'Job', 27, 19, 'El rico dormirá, mas no será recogido: Abrirá sus ojos, mas él no será.', 'cosmic'),
('Old', 'Job', 27, 20, 'Asirán de él terrores como aguas: Torbellino lo arrebatará de noche.', 'cosmic'),
('Old', 'Job', 27, 21, 'Lo antecogerá el solano, y partirá; Y tempestad lo arrebatará del lugar suyo.', 'cosmic'),
('Old', 'Job', 27, 22, 'Dios pues descargará sobre él, y no perdonará: Hará él por huir de su mano.', 'cosmic'),
('Old', 'Job', 27, 23, 'Batirán sus manos sobre él, Y desde su lugar le silbarán.', 'cosmic'),
('Old', 'Job', 28, 1, 'CIERTAMENTE la plata tiene sus veneros, Y el oro lugar donde se forma.', 'cosmic'),
('Old', 'Job', 28, 2, 'El hierro se saca del polvo, Y de la piedra es fundido el metal.', 'cosmic'),
('Old', 'Job', 28, 3, 'A las tinieblas puso término, Y examina todo á la perfección, Las piedras que hay en la oscuridad y en la sombra de muerte.', 'cosmic'),
('Old', 'Job', 28, 4, 'Brota el torrente de junto al morador, Aguas que el pie había olvidado: Sécanse luego, vanse del hombre.', 'cosmic'),
('Old', 'Job', 28, 5, 'De la tierra nace el pan, Y debajo de ella estará como convertida en fuego.', 'cosmic'),
('Old', 'Job', 28, 6, 'Lugar hay cuyas piedras son zafiro, Y sus polvos de oro.', 'cosmic'),
('Old', 'Job', 28, 7, 'Senda que nunca la conoció ave, Ni ojo de buitre la vió:', 'cosmic'),
('Old', 'Job', 28, 8, 'Nunca la pisaron animales fieros, Ni león pasó por ella.', 'cosmic'),
('Old', 'Job', 28, 9, 'En el pedernal puso su mano, Y trastornó los montes de raíz.', 'cosmic'),
('Old', 'Job', 28, 10, 'De los peñascos cortó ríos, Y sus ojos vieron todo lo preciado.', 'cosmic'),
('Old', 'Job', 28, 11, 'Detuvo los ríos en su nacimiento, E hizo salir á luz lo escondido.', 'cosmic'),
('Old', 'Job', 28, 12, 'Empero ¿dónde se hallará la sabiduría? ¿Y dónde está el lugar de la prudencia?', 'cosmic'),
('Old', 'Job', 28, 13, 'No conoce su valor el hombre, Ni se halla en la tierra de los vivientes.', 'cosmic'),
('Old', 'Job', 28, 14, 'El abismo dice: No está en mí: Y la mar dijo: Ni conmigo.', 'cosmic'),
('Old', 'Job', 28, 15, 'No se dará por oro, Ni su precio será á peso de plata.', 'cosmic'),
('Old', 'Job', 28, 16, 'No puede ser apreciada con oro de Ophir, Ni con onique precioso, ni con zafiro.', 'cosmic'),
('Old', 'Job', 28, 17, 'El oro no se le igualará, ni el diamante; Ni se trocará por vaso de oro fino.', 'cosmic'),
('Old', 'Job', 28, 18, 'De coral ni de perlas no se hará mención: La sabiduría es mejor que piedras preciosas.', 'cosmic'),
('Old', 'Job', 28, 19, 'No se igualará con ella esmeralda de Ethiopía; No se podrá apreciar con oro fino.', 'cosmic'),
('Old', 'Job', 28, 20, '¿De dónde pues vendrá la sabiduría? ¿Y dónde está el lugar de la inteligencia?', 'cosmic'),
('Old', 'Job', 28, 21, 'Porque encubierta está á los ojos de todo viviente, y á toda ave del cielo es oculta.', 'cosmic'),
('Old', 'Job', 28, 22, 'El infierno y la muerte dijeron: Su fama hemos oído con nuestros oídos.', 'cosmic'),
('Old', 'Job', 28, 23, 'Dios entiende el camino de ella, Y él conoce su lugar.', 'cosmic'),
('Old', 'Job', 28, 24, 'Porque él mira hasta los fines de la tierra, Y ve debajo de todo el cielo.', 'cosmic'),
('Old', 'Job', 28, 25, 'Al dar peso al viento, Y poner las aguas por medida;', 'cosmic'),
('Old', 'Job', 28, 26, 'Cuando él hizo ley á la lluvia, Y camino al relámpago de los truenos:', 'cosmic'),
('Old', 'Job', 28, 27, 'Entonces la veía él, y la manifestaba: Preparóla y descubrióla también.', 'cosmic'),
('Old', 'Job', 28, 28, 'Y dijo al hombre: He aquí que el temor del Señor es la sabiduría, Y el apartarse del mal la inteligencia.', 'cosmic'),
('Old', 'Job', 29, 1, 'Y VOLVIO Job á tomar su propósito, y dijo:', 'cosmic'),
('Old', 'Job', 29, 2, '¡Quién me tornase como en los meses pasados, Como en los días que Dios me guardaba,', 'cosmic'),
('Old', 'Job', 29, 3, 'Cuando hacía resplandecer su candela sobre mi cabeza, A la luz de la cual yo caminaba en la oscuridad;', 'cosmic'),
('Old', 'Job', 29, 4, 'Como fué en los días de mi mocedad, Cuando el secreto de Dios estaba en mi tienda;', 'cosmic'),
('Old', 'Job', 29, 5, 'Cuando aún el Omnipotente estaba conmigo, Y mis hijos alrededor de mi;', 'cosmic'),
('Old', 'Job', 29, 6, 'Cuando lavaba yo mis caminos con manteca, Y la piedra me derramaba ríos de aceite!', 'cosmic'),
('Old', 'Job', 29, 7, 'Cuando salía á la puerta á juicio, Y en la plaza hacía preparar mi asiento,', 'cosmic'),
('Old', 'Job', 29, 8, 'Los mozos me veían, y se escondían; Y los viejos se levantaban, y estaban en pie;', 'cosmic'),
('Old', 'Job', 29, 9, 'Los príncipes detenían sus palabras, Ponían la mano sobre su boca;', 'cosmic'),
('Old', 'Job', 29, 10, 'La voz de los principales se ocultaba, Y su lengua se pegaba á su paladar:', 'cosmic'),
('Old', 'Job', 29, 11, 'Cuando los oídos que me oían, me llamaban bienaventurado, Y los ojos que me veían, me daban testimonio:', 'cosmic'),
('Old', 'Job', 29, 12, 'Porque libraba al pobre que gritaba, Y al huérfano que carecía de ayudador.', 'cosmic'),
('Old', 'Job', 29, 13, 'La bendición del que se iba á perder venía sobre mí; Y al corazón de la viuda daba alegría.', 'cosmic'),
('Old', 'Job', 29, 14, 'Vestíame de justicia, y ella me vestía como un manto; Y mi toca era juicio.', 'cosmic'),
('Old', 'Job', 29, 15, 'Yo era ojos al ciego, Y pies al cojo.', 'cosmic'),
('Old', 'Job', 29, 16, 'A los menesterosos era padre; Y de la causa que no entendía, me informaba con diligencia:', 'cosmic'),
('Old', 'Job', 29, 17, 'Y quebraba los colmillos del inicuo, Y de sus dientes hacía soltar la presa.', 'cosmic'),
('Old', 'Job', 29, 18, 'Y decía yo: En mi nido moriré, Y como arena multiplicaré días.', 'cosmic'),
('Old', 'Job', 29, 19, 'Mi raíz estaba abierta junto á las aguas, Y en mis ramas permanecía el rocío.', 'cosmic'),
('Old', 'Job', 29, 20, 'Mi honra se renovaba en mí, Y mi arco se corroboraba en mi mano.', 'cosmic'),
('Old', 'Job', 29, 21, 'Oíanme, y esperaban; Y callaban á mi consejo.', 'cosmic'),
('Old', 'Job', 29, 22, 'Tras mi palabra no replicaban, Y mi razón destilaba sobre ellos.', 'cosmic'),
('Old', 'Job', 29, 23, 'Y esperábanme como á la lluvia, Y abrían su boca como á la lluvia tardía.', 'cosmic'),
('Old', 'Job', 29, 24, 'Si me reía con ellos, no lo creían: Y no abatían la luz de mi rostro.', 'cosmic'),
('Old', 'Job', 29, 25, 'Calificaba yo el camino de ellos, y sentábame en cabecera; Y moraba como rey en el ejército, Como el que consuela llorosos.', 'cosmic'),
('Old', 'Job', 30, 1, 'MAS ahora los más mozos de días que yo, se ríen de mí; Cuyos padres yo desdeñara ponerlos con los perros de mi ganado.', 'cosmic'),
('Old', 'Job', 30, 2, 'Porque ¿para qué yo habría menester la fuerza de sus manos, En los cuales había perecido con el tiempo?', 'cosmic'),
('Old', 'Job', 30, 3, 'Por causa de la pobreza y del hambre andaban solos; Huían á la soledad, á lugar tenebroso, asolado y desierto.', 'cosmic'),
('Old', 'Job', 30, 4, 'Que cogían malvas entre los arbustos, Y raíces de enebro para calentarse.', 'cosmic'),
('Old', 'Job', 30, 5, 'Eran echados de entre las gentes, Y todos les daban grita como al ladrón.', 'cosmic'),
('Old', 'Job', 30, 6, 'Habitaban en las barrancas de los arroyos, En las cavernas de la tierra, y en las rocas.', 'cosmic'),
('Old', 'Job', 30, 7, 'Bramaban entre las matas, Y se reunían debajo de las espinas.', 'cosmic'),
('Old', 'Job', 30, 8, 'Hijos de viles, y hombres sin nombre, Más bajos que la misma tierra.', 'cosmic'),
('Old', 'Job', 30, 9, 'Y ahora yo soy su canción, Y he sido hecho su refrán.', 'cosmic'),
('Old', 'Job', 30, 10, 'Abomínanme, aléjanse de mí, Y aun de mi rostro no detuvieron su saliva.', 'cosmic'),
('Old', 'Job', 30, 11, 'Porque Dios desató mi cuerda, y me afligió, Por eso se desenfrenaron delante de mi rostro.', 'cosmic'),
('Old', 'Job', 30, 12, 'A la mano derecha se levantaron los jóvenes; Empujaron mis pies, Y sentaron contra mí las vías de su ruina.', 'cosmic'),
('Old', 'Job', 30, 13, 'Mi senda desbarataron, Aprovecháronse de mi quebrantamiento, Contra los cuales no hubo ayudador.', 'cosmic'),
('Old', 'Job', 30, 14, 'Vinieron como por portillo ancho, Revolviéronse á mi calamidad.', 'cosmic'),
('Old', 'Job', 30, 15, 'Hanse revuelto turbaciones sobre mí; Combatieron como viento mi alma, Y mi salud pasó como nube', 'cosmic'),
('Old', 'Job', 30, 16, 'Y ahora mi alma está derramada en mí; Días de aflicción me han aprehendido.', 'cosmic'),
('Old', 'Job', 30, 17, 'De noche taladra sobre mí mis huesos, Y mis pulsos no reposan.', 'cosmic'),
('Old', 'Job', 30, 18, 'Con la grande copia de materia mi vestidura está demudada; Cíñeme como el cuello de mi túnica.', 'cosmic'),
('Old', 'Job', 30, 19, 'Derribóme en el lodo, Y soy semejante al polvo y á la ceniza.', 'cosmic'),
('Old', 'Job', 30, 20, 'Clamo á ti, y no me oyes; Preséntome, y no me atiendes.', 'cosmic'),
('Old', 'Job', 30, 21, 'Haste tornado cruel para mí: Con la fortaleza de tu mano me amenazas.', 'cosmic'),
('Old', 'Job', 30, 22, 'Levantásteme, é hicísteme cabalgar sobre el viento, Y disolviste mi sustancia.', 'cosmic'),
('Old', 'Job', 30, 23, 'Porque yo conozco que me reduces á la muerte; Y á la casa determinada á todo viviente.', 'cosmic'),
('Old', 'Job', 30, 24, 'Mas él no extenderá la mano contra el sepulcro; ¿Clamarán los sepultados cuando él los quebrantare?', 'cosmic'),
('Old', 'Job', 30, 25, '¿No lloré yo al afligido? Y mi alma ¿no se entristeció sobre el menesteroso?', 'cosmic'),
('Old', 'Job', 30, 26, 'Cuando esperaba yo el bien, entonces vino el mal; Y cuando esperaba luz, la oscuridad vino.', 'cosmic'),
('Old', 'Job', 30, 27, 'Mis entrañas hierven, y no reposan; Días de aflicción me han sobrecogido.', 'cosmic'),
('Old', 'Job', 30, 28, 'Denegrido ando, y no por el sol: Levantádome he en la congregación, y clamado.', 'cosmic'),
('Old', 'Job', 30, 29, 'He venido á ser hermano de los dragones, Y compañero de los buhos.', 'cosmic'),
('Old', 'Job', 30, 30, 'Mi piel está denegrida sobre mí, Y mis huesos se secaron con ardentía.', 'cosmic'),
('Old', 'Job', 30, 31, 'Y hase tornado mi arpa en luto, Y mi órgano en voz de lamentadores.', 'cosmic'),
('Old', 'Job', 31, 1, 'HICE pacto con mis ojos: ¿Cómo pues había yo de pensar en virgen?', 'cosmic'),
('Old', 'Job', 31, 2, 'Porque ¿qué galardón me daría de arriba Dios, Y qué heredad el Omnipotente de las alturas?', 'cosmic'),
('Old', 'Job', 31, 3, '¿No hay quebrantamiento para el impío, Y extrañamiento para los que obran iniquidad?', 'cosmic'),
('Old', 'Job', 31, 4, '¿No ve él mis caminos, Y cuenta todos mis pasos?', 'cosmic'),
('Old', 'Job', 31, 5, 'Si anduve con mentira, Y si mi pie se apresuró á engaño,', 'cosmic'),
('Old', 'Job', 31, 6, 'Péseme Dios en balanzas de justicia, Y conocerá mi integridad.', 'cosmic'),
('Old', 'Job', 31, 7, 'Si mis pasos se apartaron del camino, Y si mi corazón se fué tras mis ojos, Y si algo se apegó á mis manos,', 'cosmic'),
('Old', 'Job', 31, 8, 'Siembre yo, y otro coma, Y mis verduras sean arrancadas.', 'cosmic'),
('Old', 'Job', 31, 9, 'Si fué mi corazón engañado acerca de mujer, Y si estuve acechando á la puerta de mi prójimo:', 'cosmic'),
('Old', 'Job', 31, 10, 'Muela para otro mi mujer, Y sobre ella otros se encorven.', 'cosmic'),
('Old', 'Job', 31, 11, 'Porque es maldad é iniquidad, Que han de castigar los jueces.', 'cosmic'),
('Old', 'Job', 31, 12, 'Porque es fuego que devoraría hasta el sepulcro, Y desarraigaría toda mi hacienda.', 'cosmic'),
('Old', 'Job', 31, 13, 'Si hubiera tenido en poco el derecho de mi siervo y de mi sierva, Cuando ellos pleitearan conmigo,', 'cosmic'),
('Old', 'Job', 31, 14, '¿Qué haría yo cuando Dios se levantase? Y cuando él visitara, ¿qué le respondería yo?', 'cosmic'),
('Old', 'Job', 31, 15, 'El que en el vientre me hizo á mí, ¿no lo hizo á él? ¿Y no nos dispuso uno mismo en la matriz?', 'cosmic'),
('Old', 'Job', 31, 16, 'Si estorbé el contento de los pobres, E hice desfallecer los ojos de la viuda;', 'cosmic'),
('Old', 'Job', 31, 17, 'Y si comí mi bocado solo, Y no comió de él el huerfano;', 'cosmic'),
('Old', 'Job', 31, 18, '(Porque desde mi mocedad creció conmigo como con padre, Y desde el vientre de mi madre fuí guía de la viuda;)', 'cosmic'),
('Old', 'Job', 31, 19, 'Si he visto que pereciera alguno sin vestido, Y al menesteroso sin cobertura;', 'cosmic'),
('Old', 'Job', 31, 20, 'Si no me bendijeron sus lomos, Y del vellón de mis ovejas se calentaron;', 'cosmic'),
('Old', 'Job', 31, 21, 'Si alcé contra el huérfano mi mano, Aunque viese que me ayudarían en la puerta;', 'cosmic'),
('Old', 'Job', 31, 22, 'Mi espalda se caiga de mi hombro, Y mi brazo sea quebrado de mi canilla.', 'cosmic'),
('Old', 'Job', 31, 23, 'Porque temí el castigo de Dios, Contra cuya alteza yo no tendría poder.', 'cosmic'),
('Old', 'Job', 31, 24, 'Si puse en oro mi esperanza, Y dije al oro: Mi confianza eres tú;', 'cosmic'),
('Old', 'Job', 31, 25, 'Si me alegré de que mi hacienda se multiplicase, Y de que mi mano hallase mucho;', 'cosmic'),
('Old', 'Job', 31, 26, 'Si he mirado al sol cuando resplandecía, Y á la luna cuando iba hermosa,', 'cosmic'),
('Old', 'Job', 31, 27, 'Y mi corazón se engañó en secreto, Y mi boca besó mi mano:', 'cosmic'),
('Old', 'Job', 31, 28, 'Esto también fuera maldad juzgada; Porque habría negado al Dios soberano.', 'cosmic'),
('Old', 'Job', 31, 29, 'Si me alegré en el quebrantamiento del que me aborrecía, Y me regocijé cuando le halló el mal;', 'cosmic'),
('Old', 'Job', 31, 30, '(Que ni aun entregué al pecado mi paladar, Pidiendo maldición para su alma;)', 'cosmic'),
('Old', 'Job', 31, 31, 'Cuando mis domésticos decían: ¡Quién nos diese de su carne! nunca nos hartaríamos.', 'cosmic'),
('Old', 'Job', 31, 32, 'El extranjero no tenía fuera la noche; Mis puertas abría al caminante.', 'cosmic'),
('Old', 'Job', 31, 33, 'Si encubrí, como los hombres mis prevaricaciones, Escondiendo en mi seno mi iniquidad;', 'cosmic'),
('Old', 'Job', 31, 34, 'Porque quebrantaba á la gran multitud, Y el menosprecio de las familias me atemorizó, Y callé, y no salí de mi puerta:', 'cosmic'),
('Old', 'Job', 31, 35, '¡Quién me diera quien me oyese! He aquí mi impresión es que el Omnipotente testificaría por mí, Aunque mi adversario me hiciera el proceso.', 'cosmic'),
('Old', 'Job', 31, 36, 'Ciertamente yo lo llevaría sobre mi hombro, Y me lo ataría en lugar de corona.', 'cosmic'),
('Old', 'Job', 31, 37, 'Yo le contaría el número de mis pasos, Y como príncipe me llegaría á él.', 'cosmic'),
('Old', 'Job', 31, 38, 'Si mi tierra clama contra mí, Y lloran todos sus surcos;', 'cosmic'),
('Old', 'Job', 31, 39, 'Si comí su sustancia sin dinero, O afligí el alma de sus dueños;', 'cosmic'),
('Old', 'Job', 31, 40, 'En lugar de trigo me nazcan abrojos, Y espinas en lugar de cebada.', 'cosmic'),
('Old', 'Job', 32, 1, 'Y CESARON estos tres varones de responder á Job, por cuanto él era justo en sus ojos.', 'cosmic'),
('Old', 'Job', 32, 2, 'Entonces Eliú hijo de Barachêl, Bucita, de la familia de Ram, se enojó con furor contra Job: enojóse con furor, por cuanto justificaba su vida más que á Dios.', 'cosmic'),
('Old', 'Job', 32, 3, 'Enojóse asimismo con furor contra sus tres amigos, porque no hallaban qué responder, aunque habían condenado á Job.', 'cosmic'),
('Old', 'Job', 32, 4, 'Y Eliú había esperado á Job en la disputa, porque eran más viejos de días que él.', 'cosmic'),
('Old', 'Job', 32, 5, 'Empero viendo Eliú que no había respuesta en la boca de aquelllos tres varones, su furor se encendió.', 'cosmic'),
('Old', 'Job', 32, 6, 'Y respondió Eliú hijo de Barachêl, Buzita, y dijo: Yo soy menor de días y vosotros viejos; He tenido por tanto miedo, y temido declararos mi opinión.', 'cosmic'),
('Old', 'Job', 32, 7, 'Yo decía: Los días hablarán, Y la muchedumbre de años declarará sabiduría.', 'cosmic'),
('Old', 'Job', 32, 8, 'Ciertamente espíritu hay en el hombre, E inspiración del Omnipotente los hace que entiendan.', 'cosmic'),
('Old', 'Job', 32, 9, 'No los grandes son los sabios, Ni los viejos entienden el derecho.', 'cosmic'),
('Old', 'Job', 32, 10, 'Por tanto yo dije: Escuchadme; Declararé yo también mi sabiduría.', 'cosmic'),
('Old', 'Job', 32, 11, 'He aquí yo he esperado á vuestras razones, He escuchado vuestros argumentos, En tanto que buscabais palabras.', 'cosmic'),
('Old', 'Job', 32, 12, 'Os he pues prestado atención, Y he aquí que no hay de vosotros quien redarguya á Job, Y responda á sus razones.', 'cosmic'),
('Old', 'Job', 32, 13, 'Porque no digáis: Nosotros hemos hallado sabiduría: Lanzólo Dios, no el hombre.', 'cosmic'),
('Old', 'Job', 32, 14, 'Ahora bien, Job no enderezó á mí sus palabras, Ni yo le responderé con vuestras razones.', 'cosmic'),
('Old', 'Job', 32, 15, 'Espantáronse, no respondieron más; Fuéronseles los razonamientos.', 'cosmic'),
('Old', 'Job', 32, 16, 'Yo pues he esperado, porque no hablaban, Antes pararon, y no respondieron más.', 'cosmic'),
('Old', 'Job', 32, 17, 'Por eso yo también responderé mi parte, También yo declararé mi juicio.', 'cosmic'),
('Old', 'Job', 32, 18, 'Porque lleno estoy de palabras, Y el espíritu de mi vientre me constriñe.', 'cosmic'),
('Old', 'Job', 32, 19, 'De cierto mi vientre está como el vino que no tiene respiradero, Y se rompe como odres nuevos.', 'cosmic'),
('Old', 'Job', 32, 20, 'Hablaré pues y respiraré; Abriré mis labios, y responderé.', 'cosmic'),
('Old', 'Job', 32, 21, 'No haré ahora acepción de personas, Ni usaré con hombre de lisonjeros títulos.', 'cosmic'),
('Old', 'Job', 32, 22, 'Porque no sé hablar lisonjas: De otra manera en breve mi Hacedor me consuma.', 'cosmic'),
('Old', 'Job', 33, 1, 'POR tanto, Job, oye ahora mis razones, Y escucha todas mis palabras.', 'cosmic'),
('Old', 'Job', 33, 2, 'He aquí yo abriré ahora mi boca, Y mi lengua hablará en mi garganta.', 'cosmic'),
('Old', 'Job', 33, 3, 'Mis razones declararán la rectitud de mi corazón, Y mis labios proferirán pura sabiduría.', 'cosmic'),
('Old', 'Job', 33, 4, 'El espíritu de Dios me hizo, Y la inspiración del Omnipotente me dió vida.', 'cosmic'),
('Old', 'Job', 33, 5, 'Si pudieres, respóndeme: Dispón tus palabras, está delante de mí.', 'cosmic'),
('Old', 'Job', 33, 6, 'Heme aquí á mí en lugar de Dios, conforme á tu dicho: De lodo soy yo también formado.', 'cosmic'),
('Old', 'Job', 33, 7, 'He aquí que mi terror no te espantará, Ni mi mano se agravará sobre ti.', 'cosmic'),
('Old', 'Job', 33, 8, 'De cierto tú dijiste á oídos míos, Y yo oí la voz de tus palabras que decían:', 'cosmic'),
('Old', 'Job', 33, 9, 'Yo soy limpio y sin defecto; Y soy inocente, y no hay maldad en mí.', 'cosmic'),
('Old', 'Job', 33, 10, 'He aquí que él buscó achaques contra mí, Y me tiene por su enemigo;', 'cosmic'),
('Old', 'Job', 33, 11, 'Puso mis pies en el cepo, Y guardó todas mis sendas.', 'cosmic'),
('Old', 'Job', 33, 12, 'He aquí en esto no has hablado justamente: Yo te responderé que mayor es Dios que el hombre.', 'cosmic'),
('Old', 'Job', 33, 13, '¿Por qué tomaste pleito contra él? Porque él no da cuenta de ninguna de sus razones.', 'cosmic'),
('Old', 'Job', 33, 14, 'Sin embargo, en una ó en dos maneras habla Dios; Mas el hombre no entiende.', 'cosmic'),
('Old', 'Job', 33, 15, 'Por sueño de visión nocturna, Cuando el sueño cae sobre los hombres, Cuando se adormecen sobre el lecho;', 'cosmic'),
('Old', 'Job', 33, 16, 'Entonces revela al oído de los hombres, Y les señala su consejo;', 'cosmic'),
('Old', 'Job', 33, 17, 'Para quitar al hombre de su obra, Y apartar del varón la soberbia.', 'cosmic'),
('Old', 'Job', 33, 18, 'Detendrá su alma de corrupción, Y su vida de que pase á cuchillo.', 'cosmic'),
('Old', 'Job', 33, 19, 'También sobre su cama es castigado Con dolor fuerte en todos sus huesos,', 'cosmic'),
('Old', 'Job', 33, 20, 'Que le hace que su vida aborrezca el pan, Y su alma la comida suave.', 'cosmic'),
('Old', 'Job', 33, 21, 'Su carne desfallece sin verse, Y sus huesos, que antes no se veían, aparecen.', 'cosmic'),
('Old', 'Job', 33, 22, 'Y su alma se acerca al sepulcro, Y su vida á los que causan la muerte.', 'cosmic'),
('Old', 'Job', 33, 23, 'Si tuviera cerca de él Algún elocuente anunciador muy escogido, Que anuncie al hombre su deber;', 'cosmic'),
('Old', 'Job', 33, 24, 'Que le diga que Dios tuvo de él misericordia, Que lo libró de descender al sepulcro, Que halló redención:', 'cosmic'),
('Old', 'Job', 33, 25, 'Enterneceráse su carne más que de niño, Volverá á los días de su mocedad.', 'cosmic'),
('Old', 'Job', 33, 26, 'Orará á Dios, y le amará, Y verá su faz con júbilo: Y él restituirá al hombre su justicia.', 'cosmic'),
('Old', 'Job', 33, 27, 'El mira sobre los hombres; y el que dijere: Pequé, y pervertí lo recto, Y no me ha aprovechado;', 'cosmic'),
('Old', 'Job', 33, 28, 'Dios redimirá su alma, que no pase al sepulcro, Y su vida se verá en luz.', 'cosmic'),
('Old', 'Job', 33, 29, 'He aquí, todas estas cosas hace Dios Dos y tres veces con el hombre;', 'cosmic'),
('Old', 'Job', 33, 30, 'Para apartar su alma del sepulcro, Y para iluminarlo con la luz de los vivientes.', 'cosmic'),
('Old', 'Job', 33, 31, 'Escucha, Job, y óyeme; Calla, y yo hablaré.', 'cosmic'),
('Old', 'Job', 33, 32, 'Que si tuvieres razones, respóndeme; Habla, porque yo te quiero justificar.', 'cosmic'),
('Old', 'Job', 33, 33, 'Y si no, óyeme tú á mí; Calla, y enseñarte he sabiduría.', 'cosmic'),
('Old', 'Job', 34, 1, 'ADEMAS respondió Eliú, y dijo:', 'cosmic'),
('Old', 'Job', 34, 2, 'Oid, sabios, mis palabras; Y vosotros, doctos, estadme atentos.', 'cosmic'),
('Old', 'Job', 34, 3, 'Porque el oído prueba las palabras, Como el paladar gusta para comer.', 'cosmic'),
('Old', 'Job', 34, 4, 'Escojamos para nosotros el juicio, Conozcamos entre nosotros cuál sea lo bueno;', 'cosmic'),
('Old', 'Job', 34, 5, 'Porque Job ha dicho: Yo soy justo, Y Dios me ha quitado mi derecho.', 'cosmic'),
('Old', 'Job', 34, 6, '¿He de mentir yo contra mi razón? Mi saeta es gravosa sin haber yo prevaricado.', 'cosmic'),
('Old', 'Job', 34, 7, '¿Qué hombre hay como Job, Que bebe el escarnio como agua?', 'cosmic'),
('Old', 'Job', 34, 8, 'Y va en compañía con los que obran iniquidad, Y anda con los hombres maliciosos.', 'cosmic'),
('Old', 'Job', 34, 9, 'Porque ha dicho: De nada servirá al hombre El conformar su voluntad con Dios.', 'cosmic'),
('Old', 'Job', 34, 10, 'Por tanto, varones de seso, oidme; Lejos esté de Dios la impiedad, Y del Omnipotente la iniquidad.', 'cosmic'),
('Old', 'Job', 34, 11, 'Porque él pagará al hombre según su obra, Y él le hará hallar conforme á su camino.', 'cosmic'),
('Old', 'Job', 34, 12, 'Sí, por cierto, Dios no hará injusticia, Y el Omnipotente no pervertirá el derecho.', 'cosmic'),
('Old', 'Job', 34, 13, '¿Quién visitó por él la tierra? ¿Y quién puso en orden todo el mundo?', 'cosmic'),
('Old', 'Job', 34, 14, 'Si él pusiese sobre el hombre su corazón, Y recogiese así su espíritu y su aliento,', 'cosmic'),
('Old', 'Job', 34, 15, 'Toda carne perecería juntamente, Y el hombre se tornaría en polvo.', 'cosmic'),
('Old', 'Job', 34, 16, 'Si pues hay en ti entendimiento, oye esto: Escucha la voz de mis palabras.', 'cosmic'),
('Old', 'Job', 34, 17, '¿Enseñorearáse el que aborrece juicio? ¿Y condenarás tú al que es tan justo?', 'cosmic'),
('Old', 'Job', 34, 18, '¿Hase de decir al rey: Perverso; Y á los príncipes: Impíos?', 'cosmic'),
('Old', 'Job', 34, 19, '¿Cuánto menos á aquel que no hace acepción de personas de príncipes, Ni el rico es de él más respetado que el pobre? Porque todos son obras de sus manos.', 'cosmic'),
('Old', 'Job', 34, 20, 'En un momento morirán, y á media noche Se alborotarán los pueblos, y pasarán, Y sin mano será quitado el poderoso.', 'cosmic'),
('Old', 'Job', 34, 21, 'Porque sus ojos están sobre los caminos del hombre, Y ve todos sus pasos.', 'cosmic'),
('Old', 'Job', 34, 22, 'No hay tinieblas ni sombra de muerte Donde se encubran los que obran maldad.', 'cosmic'),
('Old', 'Job', 34, 23, 'No carga pues él al hombre más de lo justo, Para que vaya con Dios á juicio.', 'cosmic'),
('Old', 'Job', 34, 24, 'El quebrantará á los fuertes sin pesquisa, Y hará estar otros en su lugar.', 'cosmic'),
('Old', 'Job', 34, 25, 'Por tanto él hará notorias las obras de ellos, Cuando los trastornará en la noche, y serán quebrantados.', 'cosmic'),
('Old', 'Job', 34, 26, 'Como á malos los herirá En lugar donde sean vistos:', 'cosmic'),
('Old', 'Job', 34, 27, 'Por cuanto así se apartaron de él, Y no consideraron todos sus caminos;', 'cosmic'),
('Old', 'Job', 34, 28, 'Haciendo venir delante de él el clamor del pobre, Y que oiga el clamor de los necesitados.', 'cosmic'),
('Old', 'Job', 34, 29, 'Y si él diere reposo, ¿quién inquietará? Si escondiere el rostro, ¿quién lo mirará? Esto sobre una nación, y lo mismo sobre un hombre;', 'cosmic'),
('Old', 'Job', 34, 30, 'Haciendo que no reine el hombre hipócrita Para vejaciones del pueblo.', 'cosmic'),
('Old', 'Job', 34, 31, 'De seguro conviene se diga á Dios: Llevado he ya castigo, no más ofenderé:', 'cosmic'),
('Old', 'Job', 34, 32, 'Enséñame tú lo que yo no veo: Que si hice mal, no lo haré más.', 'cosmic'),
('Old', 'Job', 34, 33, '¿Ha de ser eso según tu mente? El te retribuirá, ora rehuses, Ora aceptes, y no yo: Di si no, lo que tú sabes.', 'cosmic'),
('Old', 'Job', 34, 34, 'Los hombres de seso dirán conmigo, Y el hombre sabio me oirá:', 'cosmic'),
('Old', 'Job', 34, 35, 'Que Job no habla con sabiduría, Y que sus palabras no son con entendimiento.', 'cosmic'),
('Old', 'Job', 34, 36, 'Deseo yo que Job sea probado ampliamente, A causa de sus respuestas por los hombres inicuos.', 'cosmic'),
('Old', 'Job', 34, 37, 'Porque á su pecado añadió impiedad: Bate las manos entre nosotros, Y contra Dios multiplica sus palabras.', 'cosmic'),
('Old', 'Job', 35, 1, 'Y PROCEDIENDO Eliú en su razonamiento, dijo:', 'cosmic'),
('Old', 'Job', 35, 2, '¿Piensas ser conforme á derecho Esto que dijiste: Más justo soy yo que Dios?', 'cosmic'),
('Old', 'Job', 35, 3, 'Porque dijiste: ¿Qué ventaja sacarás tú de ello? ¿O qué provecho tendré de mi pecado?', 'cosmic'),
('Old', 'Job', 35, 4, 'Yo te responderé razones, Y á tus compañeros contigo.', 'cosmic'),
('Old', 'Job', 35, 5, 'Mira á los cielos, y ve, Y considera que las nubes son más altas que tú.', 'cosmic'),
('Old', 'Job', 35, 6, 'Si pecares, ¿qué habrás hecho contra él? Y si tus rebeliones se multiplicaren, ¿qué le harás tú?', 'cosmic'),
('Old', 'Job', 35, 7, 'Si fueres justo, ¿qué le darás á el? ¿O qué recibirá de tu mano?', 'cosmic'),
('Old', 'Job', 35, 8, 'Al hombre como tú dañará tu impiedad, Y al hijo del hombre aprovechará tu justicia.', 'cosmic'),
('Old', 'Job', 35, 9, 'A causa de la multitud de las violencias clamarán, Y se lamentarán por el poderío de los grandes.', 'cosmic'),
('Old', 'Job', 35, 10, 'Y ninguno dice: ¿Dónde está Dios mi Hacedor, Que da canciones en la noche,', 'cosmic'),
('Old', 'Job', 35, 11, 'Que nos enseña más que á las bestias de la tierra, Y nos hace sabios más que las aves del cielo?', 'cosmic'),
('Old', 'Job', 35, 12, 'Allí clamarán, y él no oirá, Por la soberbia de los malos.', 'cosmic'),
('Old', 'Job', 35, 13, 'Ciertamente Dios no oirá la vanidad, Ni la mirará el Omnipotente.', 'cosmic'),
('Old', 'Job', 35, 14, 'Aunque más digas, No lo mirará; Haz juicio delante de él, y en él espera.', 'cosmic'),
('Old', 'Job', 35, 15, 'Mas ahora, porque en su ira no visita, Ni conoce con rigor,', 'cosmic'),
('Old', 'Job', 35, 16, '(35-15) Por eso Job abrió su boca vanamente, Y multiplica palabras sin sabiduría.', 'cosmic'),
('Old', 'Job', 36, 1, 'Y AñADIO Eliú, y dijo:', 'cosmic'),
('Old', 'Job', 36, 2, 'Espérame un poco, y enseñarte he; Porque todavía tengo razones en orden á Dios.', 'cosmic'),
('Old', 'Job', 36, 3, 'Tomaré mi noticia de lejos, Y atribuiré justicia á mi Hacedor.', 'cosmic'),
('Old', 'Job', 36, 4, 'Porque de cierto no son mentira mis palabras; Contigo está el que es íntegro en sus conceptos.', 'cosmic'),
('Old', 'Job', 36, 5, 'He aquí que Dios es grande, mas no desestima á nadie; Es poderoso en fuerza de sabiduría.', 'cosmic'),
('Old', 'Job', 36, 6, 'No otorgará vida al impío, Y á los afligidos dará su derecho.', 'cosmic'),
('Old', 'Job', 36, 7, 'No quitará sus ojos del justo; Antes bien con los reyes los pondrá en solio para siempre, Y serán ensalzados.', 'cosmic'),
('Old', 'Job', 36, 8, 'Y si estuvieren prendidos en grillos, Y aprisionados en las cuerdas de aflicción,', 'cosmic'),
('Old', 'Job', 36, 9, 'El les dará á conocer la obra de ellos, Y que prevalecieron sus rebeliones.', 'cosmic'),
('Old', 'Job', 36, 10, 'Despierta además el oído de ellos para la corrección, Y díce les que se conviertan de la iniquidad.', 'cosmic'),
('Old', 'Job', 36, 11, 'Si oyeren, y le sirvieren, Acabarán sus días en bien, y sus años en deleites.', 'cosmic'),
('Old', 'Job', 36, 12, 'Mas si no oyeren, serán pasados á cuchillo, Y perecerán sin sabiduría.', 'cosmic'),
('Old', 'Job', 36, 13, 'Empero los hipócritas de corazón lo irritarán más, Y no clamarán cuando él los atare.', 'cosmic'),
('Old', 'Job', 36, 14, 'Fallecerá el alma de ellos en su mocedad, Y su vida entre los sodomitas.', 'cosmic'),
('Old', 'Job', 36, 15, 'Al pobre librará de su pobreza, Y en la aflicción despertará su oído.', 'cosmic'),
('Old', 'Job', 36, 16, 'Asimismo te apartaría de la boca de la angustia A lugar espacioso, libre de todo apuro; Y te asentará mesa llena de grosura.', 'cosmic'),
('Old', 'Job', 36, 17, 'Mas tú has llenado el juicio del impío, En vez de sustentar el juicio y la justicia.', 'cosmic'),
('Old', 'Job', 36, 18, 'Por lo cual teme que en su ira no te quite con golpe, El cual no puedas apartar de ti con gran rescate.', 'cosmic'),
('Old', 'Job', 36, 19, '¿Hará él estima de tus riquezas, ni del oro, Ni de todas las fuerzas del poder?', 'cosmic'),
('Old', 'Job', 36, 20, 'No anheles la noche, En que desaparecen los pueblos de su lugar.', 'cosmic'),
('Old', 'Job', 36, 21, 'Guárdate, no tornes á la iniquidad; Pues ésta escogiste más bien que la aflicción.', 'cosmic'),
('Old', 'Job', 36, 22, 'He aquí que Dios es excelso con su potencia; ¿Qué enseñador semejante á él?', 'cosmic'),
('Old', 'Job', 36, 23, '¿Quién le ha prescrito su camino? ¿Y quién le dirá: Iniquidad has hecho?', 'cosmic'),
('Old', 'Job', 36, 24, 'Acuérdate de engrandecer su obra, La cual contemplan los hombres.', 'cosmic'),
('Old', 'Job', 36, 25, 'Los hombres todos la ven; Mírala el hombre de lejos.', 'cosmic'),
('Old', 'Job', 36, 26, 'He aquí, Dios es grande, y nosotros no le conocemos; Ni se puede rastrear el número de sus años.', 'cosmic'),
('Old', 'Job', 36, 27, 'El reduce las gotas de las aguas, Al derramarse la lluvia según el vapor;', 'cosmic'),
('Old', 'Job', 36, 28, 'Las cuales destilan las nubes, Goteando en abundancia sobre los hombres.', 'cosmic'),
('Old', 'Job', 36, 29, '¿Quién podrá tampoco comprender la extensión de las nubes, Y el sonido estrepitoso de su pabellón?', 'cosmic'),
('Old', 'Job', 36, 30, 'He aquí que sobre él extiende su luz, Y cobija con ella las raíces de la mar.', 'cosmic'),
('Old', 'Job', 36, 31, 'Bien que por esos medios castiga á los pueblos, A la multitud da comida.', 'cosmic'),
('Old', 'Job', 36, 32, 'Con las nubes encubre la luz, Y mándale no brillar, interponiendo aquéllas.', 'cosmic'),
('Old', 'Job', 36, 33, 'Tocante á ella anunciará el trueno, su compañero, Que hay acumulación de ira sobre el que se eleva.', 'cosmic'),
('Old', 'Job', 37, 1, 'A ESTO también se espanta mi corazón, Y salta de su lugar.', 'cosmic'),
('Old', 'Job', 37, 2, 'Oid atentamente su voz terrible, y el sonido que sale de su boca.', 'cosmic'),
('Old', 'Job', 37, 3, 'Debajo de todos los cielos lo dirige, Y su luz hasta los fines de la tierra.', 'cosmic'),
('Old', 'Job', 37, 4, 'Después de ella bramará el sonido, Tronará él con la voz de su magnificencia; Y aunque sea oída su voz, no los detiene.', 'cosmic'),
('Old', 'Job', 37, 5, 'Tronará Dios maravillosamente con su voz; El hace grandes cosas, que nosotros no entendemos.', 'cosmic'),
('Old', 'Job', 37, 6, 'Porque á la nieve dice: Desciende á la tierra; También á la llovizna, Y á los aguaceros de su fortaleza.', 'cosmic'),
('Old', 'Job', 37, 7, 'Así hace retirarse á todo hombre, Para que los hombres todos reconozcan su obra.', 'cosmic'),
('Old', 'Job', 37, 8, 'La bestia se entrará en su escondrijo, Y estaráse en sus moradas.', 'cosmic'),
('Old', 'Job', 37, 9, 'Del mediodía viene el torbellino, Y el frío de los vientos del norte.', 'cosmic'),
('Old', 'Job', 37, 10, 'Por el soplo de Dios se da el hielo, Y las anchas aguas son constreñidas.', 'cosmic'),
('Old', 'Job', 37, 11, 'Regando también llega á disipar la densa nube, Y con su luz esparce la niebla.', 'cosmic'),
('Old', 'Job', 37, 12, 'Asimismo por sus designios se revuelven las nubes en derredor, Para hacer sobre la haz del mundo, En la tierra, lo que él les mandara.', 'cosmic'),
('Old', 'Job', 37, 13, 'Unas veces por azote, otras pos causa de su tierra, Otras por misericordia las hará parecer.', 'cosmic'),
('Old', 'Job', 37, 14, 'Escucha esto, Job; Repósate, y considera las maravillas de Dios.', 'cosmic'),
('Old', 'Job', 37, 15, '¿Supiste tú cuándo Dios las ponía en concierto, Y hacía levantar la luz de su nube?', 'cosmic'),
('Old', 'Job', 37, 16, '¿Has tú conocido las diferencias de las nubes, Las maravillas del Perfecto en sabiduría?', 'cosmic'),
('Old', 'Job', 37, 17, '¿Por qué están calientes tus vestidos Cuando se fija el viento del mediodía sobre la tierra?', 'cosmic'),
('Old', 'Job', 37, 18, '¿Extendiste tú con él los cielos, Firmes como un espejo sólido?', 'cosmic'),
('Old', 'Job', 37, 19, 'Muéstranos qué le hemos de decir; Porque nosotros no podemos componer las ideas á causa de las tinieblas.', 'cosmic'),
('Old', 'Job', 37, 20, '¿Será preciso contarle cuando yo hablaré? Por más que el hombre razone, quedará como abismado.', 'cosmic'),
('Old', 'Job', 37, 21, 'He aquí aún: no se puede mirar la luz esplendente en los cielos, Luego que pasa el viento y los limpia,', 'cosmic'),
('Old', 'Job', 37, 22, 'Viniendo de la parte del norte la dorada claridad. En Dios hay una majestad terrible.', 'cosmic'),
('Old', 'Job', 37, 23, 'El es Todopoderoso, al cual no alcanzamos, grande en potencia; Y en juicio y en multitud de justicia no afligirá.', 'cosmic'),
('Old', 'Job', 37, 24, 'Temerlo han por tanto los hombres: El no mira á los sabios de corazón.', 'cosmic'),
('Old', 'Job', 38, 1, 'Y RESPONDIO Jehová á Job desde un torbellino, y dijo:', 'cosmic'),
('Old', 'Job', 38, 2, '¿Quién es ése que oscurece el consejo Con palabras sin sabiduría?', 'cosmic'),
('Old', 'Job', 38, 3, 'Ahora ciñe como varón tus lomos; Yo te preguntaré, y hazme saber tú.', 'cosmic'),
('Old', 'Job', 38, 4, '¿Dónde estabas cuando yo fundaba la tierra? Házme lo saber, si tienes inteligencia.', 'cosmic'),
('Old', 'Job', 38, 5, '¿Quién ordenó sus medidas, si lo sabes? ¿O quién extendió sobre ella cordel?', 'cosmic'),
('Old', 'Job', 38, 6, '¿Sobre qué están fundadas sus basas? ¿O quién puso su piedra angular,', 'cosmic'),
('Old', 'Job', 38, 7, 'Cuando las estrellas todas del alba alababan, Y se regocijaban todos los hijos de Dios?', 'cosmic'),
('Old', 'Job', 38, 8, '¿Quién encerró con puertas la mar, Cuando se derramaba por fuera como saliendo de madre;', 'cosmic'),
('Old', 'Job', 38, 9, 'Cuando puse yo nubes por vestidura suya, Y por su faja oscuridad.', 'cosmic'),
('Old', 'Job', 38, 10, 'Y establecí sobre ella mi decreto, Y le puse puertas y cerrojo,', 'cosmic'),
('Old', 'Job', 38, 11, 'Y dije: Hasta aquí vendrás, y no pasarás adelante, Y ahí parará la hinchazón de tus ondas?', 'cosmic'),
('Old', 'Job', 38, 12, '¿Has tu mandado á la mañana en tus días? ¿Has mostrado al alba su lugar,', 'cosmic'),
('Old', 'Job', 38, 13, 'Para que ocupe los fines de la tierra, Y que sean sacudidos de ella los impíos?', 'cosmic'),
('Old', 'Job', 38, 14, 'Trasmúdase como lodo bajo de sello, Y viene á estar como con vestidura:', 'cosmic'),
('Old', 'Job', 38, 15, 'Mas la luz de los impíos es quitada de ellos, Y el brazo enaltecido es quebrantado.', 'cosmic'),
('Old', 'Job', 38, 16, '¿Has entrado tú hasta los profundos de la mar, Y has andado escudriñando el abismo?', 'cosmic'),
('Old', 'Job', 38, 17, '¿Hante sido descubiertas las puertas de la muerte, Y has visto las puertas de la sombra de muerte?', 'cosmic'),
('Old', 'Job', 38, 18, '¿Has tú considerado hasta las anchuras de la tierra? Declara si sabes todo esto.', 'cosmic'),
('Old', 'Job', 38, 19, '¿Por dónde va el camino á la habitación de la luz, Y dónde está el lugar de las tinieblas?', 'cosmic'),
('Old', 'Job', 38, 20, '¿Si llevarás tú ambas cosas á sus términos, Y entenderás las sendas de su casa?', 'cosmic'),
('Old', 'Job', 38, 21, '¿Sabíaslo tú porque hubieses ya nacido, O porque es grande el número de tus días?', 'cosmic'),
('Old', 'Job', 38, 22, '¿Has tú entrado en los tesoros de la nieve, O has visto los tesoros del granizo,', 'cosmic'),
('Old', 'Job', 38, 23, 'Lo cual tengo yo reservado para el tiempo de angustia, Para el día de la guerra y de la batalla?', 'cosmic'),
('Old', 'Job', 38, 24, '¿Por qué camino se reparte la luz, Y se esparce el viento solano sobre la tierra?', 'cosmic'),
('Old', 'Job', 38, 25, '¿Quién repartió conducto al turbión, Y camino á los relámpagos y truenos,', 'cosmic'),
('Old', 'Job', 38, 26, 'Haciendo llover sobre la tierra deshabitada, Sobre el desierto, donde no hay hombre,', 'cosmic'),
('Old', 'Job', 38, 27, 'Para hartar la tierra desierta é inculta, Y para hacer brotar la tierna hierba?', 'cosmic'),
('Old', 'Job', 38, 28, '¿Tiene la lluvia padre? ¿O quién engendró las gotas del rocío?', 'cosmic'),
('Old', 'Job', 38, 29, '¿De qué vientre salió el hielo? Y la escarcha del cielo, ¿quién la engendró?', 'cosmic'),
('Old', 'Job', 38, 30, 'Las aguas se endurecen á manera de piedra, Y congélase la haz del abismo.', 'cosmic'),
('Old', 'Job', 38, 31, '¿Podrás tú impedir las delicias de las Pléyades, O desatarás las ligaduras del Orión?', 'cosmic'),
('Old', 'Job', 38, 32, '¿Sacarás tú á su tiempo los signos de los cielos, O guiarás el Arcturo con sus hijos?', 'cosmic'),
('Old', 'Job', 38, 33, '¿Supiste tú las ordenanzas de los cielos? ¿Dispondrás tú de su potestad en la tierra?', 'cosmic'),
('Old', 'Job', 38, 34, '¿Alzarás tú á las nubes tu voz, Para que te cubra muchedumbre de aguas?', 'cosmic'),
('Old', 'Job', 38, 35, '¿Enviarás tú los relámpagos, para que ellos vayan? ¿Y diránte ellos: Henos aquí?', 'cosmic'),
('Old', 'Job', 38, 36, '¿Quién puso la sabiduría en el interior? ¿O quién dió al entendimiento la inteligencia?', 'cosmic'),
('Old', 'Job', 38, 37, '¿Quién puso por cuenta los cielos con sabiduría? Y los odres de los cielos, ¿quién los hace parar,', 'cosmic'),
('Old', 'Job', 38, 38, 'Cuando el polvo se ha convertido en dureza, Y los terrones se han pegado unos con otros?', 'cosmic'),
('Old', 'Job', 38, 39, '(39-1) ¿CAZARAS tú la presa para el león? ¿Y saciarás el hambre de los leoncillos,', 'cosmic'),
('Old', 'Job', 38, 40, '(39-2) Cuando están echados en las cuevas, O se están en sus guaridas para acechar?', 'cosmic'),
('Old', 'Job', 38, 41, '(39-3) ¿Quién preparó al cuervo su alimento, Cuando sus pollos claman á Dios, Bullendo de un lado á otro por carecer de comida?', 'cosmic'),
('Old', 'Job', 39, 1, '(39-4) ¿Sabes tú el tiempo en que paren las cabras monteses? ¿O miraste tú las ciervas cuando están pariendo?', 'cosmic'),
('Old', 'Job', 39, 2, '(39-5) ¿Contaste tú los meses de su preñez, Y sabes el tiempo cuando han de parir?', 'cosmic'),
('Old', 'Job', 39, 3, '(39-6) Encórvanse, hacen salir sus hijos, Pasan sus dolores.', 'cosmic'),
('Old', 'Job', 39, 4, '(39-7) Sus hijos están sanos, crecen con el pasto: Salen y no vuelven á ellas.', 'cosmic'),
('Old', 'Job', 39, 5, '(39-8) ¿Quién echó libre al asno montés, y quién soltó sus ataduras?', 'cosmic'),
('Old', 'Job', 39, 6, '(39-9) Al cual yo puse casa en la soledad, Y sus moradas en lugares estériles.', 'cosmic'),
('Old', 'Job', 39, 7, '(39-10) Búrlase de la multitud de la ciudad: No oye las voces del arriero.', 'cosmic'),
('Old', 'Job', 39, 8, '(39-11) Lo oculto de los montes es su pasto, Y anda buscando todo lo que está verde.', 'cosmic'),
('Old', 'Job', 39, 9, '(39-12) ¿Querrá el unicornio servirte á ti, Ni quedar á tu pesebre?', 'cosmic'),
('Old', 'Job', 39, 10, '(39-13) ¿Atarás tú al unicornio con su coyunda para el surco? ¿Labrará los valles en pos de ti?', 'cosmic'),
('Old', 'Job', 39, 11, '(39-14) ¿Confiarás tú en él, por ser grande su fortaleza, Y le fiarás tu labor?', 'cosmic'),
('Old', 'Job', 39, 12, '(39-15) ¿Fiarás de él que te tornará tu simiente, Y que la allegará en tu era?', 'cosmic'),
('Old', 'Job', 39, 13, '(39-16) ¿Diste tú hermosas alas al pavo real, O alas y plumas al avestruz?', 'cosmic'),
('Old', 'Job', 39, 14, '(39-17) El cual desampara en la tierra sus huevos, Y sobre el polvo los calienta,', 'cosmic'),
('Old', 'Job', 39, 15, '(39-18) Y olvídase de que los pisará el pie, Y que los quebrará bestia del campo.', 'cosmic'),
('Old', 'Job', 39, 16, '(39-19) Endurécese para con sus hijos, como si no fuesen suyos, No temiendo que su trabajo haya sido en vano:', 'cosmic'),
('Old', 'Job', 39, 17, '(39-20) Porque le privó Dios de sabiduría, Y no le dió inteligencia.', 'cosmic'),
('Old', 'Job', 39, 18, '(39-21) Luego que se levanta en alto, Búrlase del caballo y de su jinete.', 'cosmic'),
('Old', 'Job', 39, 19, '(39-22) ¿Diste tú al caballo la fortaleza? ¿Vestiste tú su cerviz de relincho?', 'cosmic'),
('Old', 'Job', 39, 20, '(39-23) ¿Le intimidarás tú como á alguna langosta? El resoplido de su nariz es formidable:', 'cosmic'),
('Old', 'Job', 39, 21, '(39-24) Escarba la tierra, alégrase en su fuerza, Sale al encuentro de las armas:', 'cosmic'),
('Old', 'Job', 39, 22, '(39-25) Hace burla del espanto, y no teme, Ni vuelve el rostro delante de la espada.', 'cosmic'),
('Old', 'Job', 39, 23, '(39-26) Contra él suena la aljaba, El hierro de la lanza y de la pica:', 'cosmic'),
('Old', 'Job', 39, 24, '(39-27) Y él con ímpetu y furor escarba la tierra, Sin importarle el sonido de la bocina;', 'cosmic'),
('Old', 'Job', 39, 25, '(39-28) Antes como que dice entre los clarines: ¡Ea! Y desde lejos huele la batalla, el grito de los capitanes, y la vocería.', 'cosmic'),
('Old', 'Job', 39, 26, '(39-29) ¿Vuela el gavilán por tu industria, Y extiende hacia el mediodía sus alas?', 'cosmic'),
('Old', 'Job', 39, 27, '(39-30) ¿Se remonta el águila por tu mandamiento, Y pone en alto su nido?', 'cosmic'),
('Old', 'Job', 39, 28, '(39-31) Ella habita y está en la piedra, En la cumbre del peñasco y de la roca.', 'cosmic'),
('Old', 'Job', 39, 29, '(39-32) Desde allí acecha la comida: Sus ojos observan de muy lejos.', 'cosmic'),
('Old', 'Job', 39, 30, '(39-33) Sus pollos chupan la sangre: Y donde hubiere cadáveres, allí está.', 'cosmic'),
('Old', 'Job', 40, 1, '(39-34) A más de eso respondió Jehová á Job y dijo:', 'cosmic'),
('Old', 'Job', 40, 2, '(39-35) ¿Es sabiduría contender con el Omnipotente? El que disputa con Dios, responda á esto.', 'cosmic'),
('Old', 'Job', 40, 3, '(39-36) Y respondió Job á Jehová, y dijo:', 'cosmic'),
('Old', 'Job', 40, 4, '(39-37) He aquí que yo soy vil, ¿qué te responderé? Mi mano pongo sobre mi boca.', 'cosmic'),
('Old', 'Job', 40, 5, '(39-38) Una vez hablé, y no responderé: Aun dos veces, mas no tornaré á hablar.', 'cosmic'),
('Old', 'Job', 40, 6, '(40-1) ENTONCES respondió Jehová á Job desde la oscuridad, y dijo:', 'cosmic'),
('Old', 'Job', 40, 7, '(40-2) Cíñete ahora como varón tus lomos; Yo te preguntaré, y explícame.', 'cosmic'),
('Old', 'Job', 40, 8, '(40-3) ¿Invalidarás tú también mi juicio? ¿Me condenarás á mí, para justificarte á ti?', 'cosmic'),
('Old', 'Job', 40, 9, '(40-4) ¿Tienes tú brazo como Dios? ¿Y tronarás tú con voz como él?', 'cosmic'),
('Old', 'Job', 40, 10, '(40-5) Atavíate ahora de majestad y de alteza: Y vístete de honra y de hermosura.', 'cosmic'),
('Old', 'Job', 40, 11, '(40-6) Esparce furores de tu ira: Y mira á todo soberbio, y abátelo.', 'cosmic'),
('Old', 'Job', 40, 12, '(40-7) Mira á todo soberbio, y humíllalo, Y quebranta á los impíos en su asiento.', 'cosmic'),
('Old', 'Job', 40, 13, '(40-8) Encúbrelos á todos en el polvo, Venda sus rostros en la oscuridad;', 'cosmic'),
('Old', 'Job', 40, 14, '(40-9) Y yo también te confesaré Que podrá salvarte tu diestra.', 'cosmic'),
('Old', 'Job', 40, 15, '(40-10) He aquí ahora behemoth, al cual yo hice contigo; Hierba come como buey.', 'cosmic'),
('Old', 'Job', 40, 16, '(40-11) He aquí ahora que su fuerza está en sus lomos, Y su fortaleza en el ombligo de su vientre.', 'cosmic'),
('Old', 'Job', 40, 17, '(40-12) Su cola mueve como un cedro, Y los nervios de sus genitales son entretejidos.', 'cosmic'),
('Old', 'Job', 40, 18, '(40-13) Sus huesos son fuertes como bronce, Y sus miembros como barras de hierro.', 'cosmic'),
('Old', 'Job', 40, 19, '(40-14) El es la cabeza de los caminos de Dios: El que lo hizo, puede hacer que su cuchillo á él se acerque.', 'cosmic'),
('Old', 'Job', 40, 20, '(40-15) Ciertamente los montes producen hierba para él: Y toda bestia del campo retoza allá.', 'cosmic'),
('Old', 'Job', 40, 21, '(40-16) Echaráse debajo de las sombras, En lo oculto de las cañas, y de los lugares húmedos.', 'cosmic'),
('Old', 'Job', 40, 22, '(40-17) Los árboles sombríos lo cubren con su sombra; Los sauces del arroyo lo cercan.', 'cosmic'),
('Old', 'Job', 40, 23, '(40-18) He aquí que él tomará el río sin inmutarse: Y confíase que el Jordán pasará por su boca.', 'cosmic'),
('Old', 'Job', 40, 24, '(40-19) ¿Tomarálo alguno por sus ojos en armadijos, Y horadará su nariz?', 'cosmic'),
('Old', 'Job', 41, 1, '¿SACARAS tú al leviathán con el anzuelo, O con la cuerda que le echares en su lengua?', 'cosmic'),
('Old', 'Job', 41, 2, '¿Pondrás tú garfio en sus narices, Y horadarás con espinas su quijada?', 'cosmic'),
('Old', 'Job', 41, 3, '¿Multiplicará él ruegos para contigo? ¿Hablaráte él lisonjas?', 'cosmic'),
('Old', 'Job', 41, 4, '¿Hará concierto contigo Para que lo tomes por siervo perpetuo?', 'cosmic'),
('Old', 'Job', 41, 5, '¿Jugarás tú con él como con pájaro, O lo atarás para tus niñas?', 'cosmic'),
('Old', 'Job', 41, 6, '¿Harán de él banquete los compañeros? ¿Partiránlo entre los mercaderes?', 'cosmic'),
('Old', 'Job', 41, 7, '¿Cortarás tú con cuchillo su cuero, O con asta de pescadores su cabeza?', 'cosmic'),
('Old', 'Job', 41, 8, 'Pon tu mano sobre él; Te acordarás de la batalla, y nunca más tornarás.', 'cosmic'),
('Old', 'Job', 41, 9, 'He aquí que la esperanza acerca de él será burlada; Porque aun á su sola vista se desmayarán.', 'cosmic'),
('Old', 'Job', 41, 10, 'Nadie hay tan osado que lo despierte: ¿Quién pues podrá estar delante de mí?', 'cosmic'),
('Old', 'Job', 41, 11, '¿Quién me ha anticipado, para que yo restituya? Todo lo que hay debajo del cielo es mío.', 'cosmic'),
('Old', 'Job', 41, 12, 'Yo no callaré sus miembros, Ni lo de sus fuerzas y la gracia de su disposición.', 'cosmic'),
('Old', 'Job', 41, 13, '¿Quién descubrirá la delantera de su vestidura? ¿Quién se llegará á él con freno doble?', 'cosmic'),
('Old', 'Job', 41, 14, '¿Quién abrirá las puertas de su rostro? Los órdenes de sus dientes espantan.', 'cosmic'),
('Old', 'Job', 41, 15, 'La gloria de su vestido son escudos fuertes, Cerrados entre sí estrechamente.', 'cosmic'),
('Old', 'Job', 41, 16, 'El uno se junta con el otro, Que viento no entra entre ellos.', 'cosmic'),
('Old', 'Job', 41, 17, 'Pegado está el uno con el otro, Están trabados entre sí, que no se pueden apartar.', 'cosmic'),
('Old', 'Job', 41, 18, 'Con sus estornudos encienden lumbre, Y sus ojos son como los párpados del alba.', 'cosmic'),
('Old', 'Job', 41, 19, 'De su boca salen hachas de fuego, Centellas de fuego proceden.', 'cosmic'),
('Old', 'Job', 41, 20, 'De sus narices sale humo, Como de una olla ó caldero que hierve.', 'cosmic'),
('Old', 'Job', 41, 21, 'Su aliento enciende los carbones, Y de su boca sale llama.', 'cosmic'),
('Old', 'Job', 41, 22, 'En su cerviz mora la fortaleza, Y espárcese el desaliento delante de él.', 'cosmic'),
('Old', 'Job', 41, 23, 'Las partes momias de su carne están apretadas: Están en él firmes, y no se mueven.', 'cosmic'),
('Old', 'Job', 41, 24, 'Su corazón es firme como una piedra, Y fuerte como la muela de abajo.', 'cosmic'),
('Old', 'Job', 41, 25, 'De su grandeza tienen temor los fuertes, Y á causa de su desfallecimiento hacen por purificarse.', 'cosmic'),
('Old', 'Job', 41, 26, 'Cuando alguno lo alcanzare, ni espada, Ni lanza, ni dardo, ni coselete durará.', 'cosmic'),
('Old', 'Job', 41, 27, 'El hierro estima por pajas, Y el acero por leño podrido.', 'cosmic'),
('Old', 'Job', 41, 28, 'Saeta no le hace huir; Las piedras de honda se le tornan aristas.', 'cosmic'),
('Old', 'Job', 41, 29, 'Tiene toda arma por hojarascas, Y del blandir de la pica se burla.', 'cosmic'),
('Old', 'Job', 41, 30, 'Por debajo tiene agudas conchas; Imprime su agudez en el suelo.', 'cosmic'),
('Old', 'Job', 41, 31, 'Hace hervir como una olla la profunda mar, Y tórnala como una olla de ungüento.', 'cosmic'),
('Old', 'Job', 41, 32, 'En pos de sí hace resplandecer la senda, Que parece que la mar es cana.', 'cosmic'),
('Old', 'Job', 41, 33, 'No hay sobre la tierra su semejante, Hecho para nada temer.', 'cosmic'),
('Old', 'Job', 41, 34, 'Menosprecia toda cosa alta: Es rey sobre todos los soberbios.', 'cosmic'),
('Old', 'Job', 42, 1, 'Y RESPONDIO Job á Jehová, y dijo:', 'cosmic'),
('Old', 'Job', 42, 2, 'Yo conozco que todo lo puedes, Y que no hay pensamiento que se esconda de ti.', 'cosmic'),
('Old', 'Job', 42, 3, '¿Quién es el que oscurece el consejo sin ciencia? Por tanto yo denunciaba lo que no entendía; Cosas que me eran ocultas, y que no las sabía.', 'cosmic'),
('Old', 'Job', 42, 4, 'Oye te ruego, y hablaré; Te preguntaré, y tú me enseñarás.', 'cosmic'),
('Old', 'Job', 42, 5, 'De oídas te había oído; Mas ahora mis ojos te ven.', 'cosmic'),
('Old', 'Job', 42, 6, 'Por tanto me aborrezco, y me arrepiento En el polvo y en la ceniza.', 'cosmic'),
('Old', 'Job', 42, 7, 'Y aconteció que después que habló Jehová estas palabras á Job, Jehová dijo á Eliphaz Temanita: Mi ira se encendió contra ti y tus dos compañeros: porque no habéis hablado por mí lo recto, como mi siervo Job.', 'cosmic'),
('Old', 'Job', 42, 8, 'Ahora pues, tomaos siete becerros y siete carneros, y andad á mi siervo Job, y ofreced holocausto por vosotros, y mi siervo Job orará por vosotros; porque de cierto á él atenderé para no trataros afrentosamente, por cuanto no habéis hablado por mí con rectitud, como mi siervo Job.', 'cosmic'),
('Old', 'Job', 42, 9, 'Fueron pues Eliphaz Temanita, y Bildad Suhita, y Sophar Naamatita, é hicieron como Jehová les dijo: y Jehová atendió á Job.', 'cosmic'),
('Old', 'Job', 42, 10, 'Y mudó Jehová la aflicción de Job, orando él por sus amigos: y aumentó al doble todas las cosas que habían sido de Job.', 'cosmic'),
('Old', 'Job', 42, 11, 'Y vinieron é él todos sus hermanos, y todas sus hermanas, y todos los que antes le habían conocido, y comieron con él pan en su casa, y condoliéronse de él, y consoláronle de todo aquel mal que sobre él había Jehová traído; y cada uno de ellos le dió una pieza de moneda, y un zarcillo de oro.', 'cosmic'),
('Old', 'Job', 42, 12, 'Y bendijo Jehová la postrimería de Job más que su principio; porque tuvo catorce mil ovejas, y seis mil camellos, y mil yuntas de bueyes, y mil asnas.', 'cosmic'),
('Old', 'Job', 42, 13, 'Y tuvo siete hijos y tres hijas.', 'cosmic'),
('Old', 'Job', 42, 14, 'Y llamó el nombre de la una, Jemimah, y el nombre de la segunda, Cesiah, y el nombre de la tercera, Keren-happuch.', 'cosmic'),
('Old', 'Job', 42, 15, 'Y no se hallaron mujeres tan hermosas como las hijas de Job en toda la tierra: y dióles su padre herencia entre sus hermanos.', 'cosmic'),
('Old', 'Job', 42, 16, 'Y después de esto vivió Job ciento y cuarenta años, y vió á sus hijos, y á los hijos de sus hijos, hasta la cuarta generación.', 'cosmic'),
('Old', 'Job', 42, 17, 'Murió pues Job viejo, y lleno de días.', 'cosmic'),
('Old', 'Salmos', 1, 1, 'BIENAVENTURADO el varón que no anduvo en consejo de malos, Ni estuvo en camino de pecadores, Ni en silla de escarnecedores se ha sentado;', 'water'),
('Old', 'Salmos', 1, 2, 'Antes en la ley de Jehová está su delicia, Y en su ley medita de día y de noche.', 'water'),
('Old', 'Salmos', 1, 3, 'Y será como el árbol plantado junto á arroyos de aguas, Que da su fruto en su tiempo, Y su hoja no cae; Y todo lo que hace, prosperará.', 'water'),
('Old', 'Salmos', 1, 4, 'No así los malos: Sino como el tamo que arrebata el viento.', 'water'),
('Old', 'Salmos', 1, 5, 'Por tanto no se levantarán los malos en el juicio, Ni los pecadores en la congregación de los justos.', 'water'),
('Old', 'Salmos', 1, 6, 'Porque Jehová conoce el camino de los justos; Mas la senda de los malos perecerá.', 'water'),
('Old', 'Salmos', 2, 1, '¿POR qué se amotinan las gentes, Y los pueblos piensan vanidad?', 'water'),
('Old', 'Salmos', 2, 2, 'Estarán los reyes de la tierra, Y príncipes consultarán unidos Contra Jehová, y contra su ungido, diciendo:', 'water'),
('Old', 'Salmos', 2, 3, 'Rompamos sus coyundas, Y echemos de nosotros sus cuerdas.', 'water'),
('Old', 'Salmos', 2, 4, 'El que mora en los cielos se reirá; El Señor se burlará de ellos.', 'water'),
('Old', 'Salmos', 2, 5, 'Entonces hablará á ellos en su furor, Y turbarálos con su ira.', 'water'),
('Old', 'Salmos', 2, 6, 'Yo empero he puesto mi rey Sobre Sión, monte de mi santidad.', 'water'),
('Old', 'Salmos', 2, 7, 'Yo publicaré el decreto: Jehová me ha dicho: Mi hijo eres tú; Yo te engendré hoy.', 'water'),
('Old', 'Salmos', 2, 8, 'Pídeme, y te daré por heredad las gentes, Y por posesión tuya los términos de la tierra.', 'water'),
('Old', 'Salmos', 2, 9, 'Quebrantarlos has con vara de hierro: Como vaso de alfarero los desmenuzarás.', 'water'),
('Old', 'Salmos', 2, 10, 'Y ahora, reyes, entended: Admitid corrección, jueces de la tierra.', 'water'),
('Old', 'Salmos', 2, 11, 'Servid á Jehová con temor, Y alegraos con temblor.', 'water'),
('Old', 'Salmos', 2, 12, 'Besad al Hijo, porque no se enoje, y perezcáis en el camino, Cuando se encendiere un poco su furor. Bienaventurados todos los que en él confían.', 'water'),
('Old', 'Salmos', 3, 1, '¡OH Jehová, cuánto se han multiplicado mis enemigos! Muchos se levantan contra mí.', 'water'),
('Old', 'Salmos', 3, 2, 'Muchos dicen de mi vida: No hay para él salud en Dios. (Selah.)', 'water'),
('Old', 'Salmos', 3, 3, 'Mas tú, Jehová, eres escudo alrededor de mí: Mi gloria, y el que ensalza mi cabeza.', 'water'),
('Old', 'Salmos', 3, 4, 'Con mi voz clamé á Jehová, Y él me respondió desde el monte de su santidad. (Selah.)', 'water'),
('Old', 'Salmos', 3, 5, 'Yo me acosté, y dormí, Y desperté; porque Jehová me sostuvo.', 'water'),
('Old', 'Salmos', 3, 6, 'No temeré de diez millares de pueblos, Que pusieren cerco contra mí.', 'water'),
('Old', 'Salmos', 3, 7, 'Levántate, Jehová; sálvame, Dios mío: Porque tú heriste á todos mis enemigos en la quijada; Los dientes de los malos quebrantaste.', 'water'),
('Old', 'Salmos', 3, 8, 'De Jehová es la salud: Sobre tu pueblo será tu bendición. (Selah.)', 'water'),
('Old', 'Salmos', 4, 1, 'RESPONDEME cuando clamo, oh Dios de mi justicia: Estando en angustia, tú me hiciste ensanchar: Ten misericordia de mí, y oye mi oración.', 'water'),
('Old', 'Salmos', 4, 2, 'Hijos de los hombres, ¿hasta cuándo volveréis mi honra en infamia, Amaréis la vanidad, y buscaréis la mentira? (Selah.)', 'water'),
('Old', 'Salmos', 4, 3, 'Sabed pues, que Jehová hizo apartar al pío para sí: Jehová oirá cuando yo á él clamare.', 'water'),
('Old', 'Salmos', 4, 4, 'Temblad, y no pequéis: Conversad en vuestro corazón sobre vuestra cama, y desistid. (Selah.)', 'water'),
('Old', 'Salmos', 4, 5, 'Ofreced sacrificios de justicia, Y confiad en Jehová.', 'water'),
('Old', 'Salmos', 4, 6, 'Muchos dicen: ¿Quién nos mostrará el bien? Alza sobre nosotros, oh Jehová, la luz de tu rostro.', 'water'),
('Old', 'Salmos', 4, 7, 'Tú diste alegría en mi corazón, Más que tienen ellos en el tiempo que se multiplicó su grano y su mosto.', 'water'),
('Old', 'Salmos', 4, 8, 'En paz me acostaré, y asimismo dormiré; Porque solo tú, Jehová, me harás estar confiado.', 'water'),
('Old', 'Salmos', 5, 1, 'ESCUCHA, oh Jehová, mis palabras; Considera la meditación mía.', 'water'),
('Old', 'Salmos', 5, 2, 'Está atento á la voz de mi clamor, Rey mío y Dios mío, Porque á ti oraré.', 'water'),
('Old', 'Salmos', 5, 3, 'Oh Jehová, de mañana oirás mi voz; De mañana me presentaré á ti, y esperaré.', 'water'),
('Old', 'Salmos', 5, 4, 'Porque tú no eres un Dios que ame la maldad: El malo no habitará junto á ti.', 'water'),
('Old', 'Salmos', 5, 5, 'No estarán los insensatos delante de tus ojos: Aborreces á todos los que obran iniquidad.', 'water'),
('Old', 'Salmos', 5, 6, 'Destruirás á los que hablan mentira: Al hombre de sangres y de engaño abominará Jehová.', 'water'),
('Old', 'Salmos', 5, 7, 'Y yo en la multitud de tu misericordia entraré en tu casa: Adoraré hacia el templo de tu santidad en tu temor.', 'water'),
('Old', 'Salmos', 5, 8, 'Guíame, Jehová, en tu justicia á causa de mis enemigos; Endereza delante de mí tu camino.', 'water'),
('Old', 'Salmos', 5, 9, 'Porque no hay en su boca rectitud: Sus entrañas son pravedades; Sepulcro abierto su garganta: Con su lengua lisonjearán.', 'water'),
('Old', 'Salmos', 5, 10, 'Desbarátalos, oh Dios; Caigan de sus consejos: Por la multitud de sus rebeliones échalos, Porque se rebelaron contra ti.', 'water'),
('Old', 'Salmos', 5, 11, 'Y alegrarse han todos los que en ti confían; Para siempre darán voces de júbilo, porque tú los defiendes: Y en ti se regocijarán los que aman tu nombre.', 'water'),
('Old', 'Salmos', 5, 12, 'Porque tú, oh Jehová, bendecirás al justo; Lo cercarás de benevolencia como con un escudo.', 'water'),
('Old', 'Salmos', 6, 1, 'JEHOVA, no me reprendas en tu furor, Ni me castigues con tu ira.', 'water'),
('Old', 'Salmos', 6, 2, 'Ten misericordia de mí, oh Jehová, porque yo estoy debilitado: Sáname, oh Jehová, porque mis huesos están conmovidos.', 'water'),
('Old', 'Salmos', 6, 3, 'Mi alma asimismo está muy conturbada: Y tú, Jehová, ¿hasta cuándo?', 'water'),
('Old', 'Salmos', 6, 4, 'Vuelve, oh Jehová, libra mi alma; Sálvame por tu misericordia.', 'water'),
('Old', 'Salmos', 6, 5, 'Porque en la muerte no hay memoria de ti: ¿Quién te loará en el sepulcro?', 'water'),
('Old', 'Salmos', 6, 6, 'Heme consumido á fuerza de gemir: Todas las noches inundo mi lecho, Riego mi estrado con mis lágrimas.', 'water'),
('Old', 'Salmos', 6, 7, 'Mis ojos están carcomidos de descontento; Hanse envejecido á causa de todos mis angustiadores.', 'water'),
('Old', 'Salmos', 6, 8, 'Apartaos de mí, todos los obradores de iniquidad; Porque Jehová ha oído la voz de mi lloro.', 'water'),
('Old', 'Salmos', 6, 9, 'Jehová ha oído mi ruego; Ha recibido Jehová mi oración.', 'water'),
('Old', 'Salmos', 6, 10, 'Se avergonzarán, y turbaránse mucho todos mis enemigos; Volveránse y serán avergonzados subitáneamente.', 'water'),
('Old', 'Salmos', 7, 1, 'JEHOVA Dios mío, en ti he confiado: Sálvame de todos los que me persiguen, y líbrame;', 'water'),
('Old', 'Salmos', 7, 2, 'No sea que arrebate mi alma, cual león Que despedaza, sin que haya quien libre.', 'water'),
('Old', 'Salmos', 7, 3, 'Jehová Dios mío, si yo he hecho esto, Si hay en mis manos iniquidad;', 'water'),
('Old', 'Salmos', 7, 4, 'Si dí mal pago al pacífico conmigo, (Hasta he libertado al que sin causa era mi enemigo;)', 'water');