INSERT INTO scriptures (testament, book_name, chapter, verse_number, content, visual_theme) VALUES
('Old', 'Salmos', 7, 5, 'Persiga el enemigo mi alma, y alcánce la; Y pise en tierra mi vida, Y mi honra ponga en el polvo. (Selah.)', 'water'),
('Old', 'Salmos', 7, 6, 'Levántate; oh Jehová, con tu furor; Alzate á causa de las iras de mis angustiadores, Y despierta en favor mío el juicio que mandaste.', 'water'),
('Old', 'Salmos', 7, 7, 'Y te rodeará concurso de pueblo; Por cuyo amor vuélvete luego á levantar en alto.', 'water'),
('Old', 'Salmos', 7, 8, 'Jehová juzgará los pueblos: Júzgame, oh Jehová, conforme á mi justicia y conforme á mi integridad.', 'water'),
('Old', 'Salmos', 7, 9, 'Consúmase ahora la malicia de los inicuos, y establece al justo; Pues el Dios justo prueba los corazones y los riñones.', 'water'),
('Old', 'Salmos', 7, 10, 'Mi escudo está en Dios, Que salva á los rectos de corazón.', 'water'),
('Old', 'Salmos', 7, 11, 'Dios es el que juzga al justo: Y Dios está airado todos los días contra el impío.', 'water'),
('Old', 'Salmos', 7, 12, 'Si no se convirtiere, él afilará su espada: Armado tiene ya su arco, y lo ha preparado.', 'water'),
('Old', 'Salmos', 7, 13, 'Asimismo ha aparejado para él armas de muerte; Ha labrado sus saetas para los que persiguen.', 'water'),
('Old', 'Salmos', 7, 14, 'He aquí ha tenido parto de iniquidad: Concibió trabajo, y parió mentira.', 'water'),
('Old', 'Salmos', 7, 15, 'Pozo ha cavado, y ahondádolo; Y en la fosa que hizo caerá.', 'water'),
('Old', 'Salmos', 7, 16, 'Su trabajo se tornará sobre su cabeza, Y su agravio descenderá sobre su mollera.', 'water'),
('Old', 'Salmos', 7, 17, 'Alabaré yo á Jehová conforme á su justicia, Y cantaré al nombre de Jehová el Altísimo.', 'water'),
('Old', 'Salmos', 8, 1, 'OH Jehová, Señor nuestro, ¡Cuán grande es tu nombre en toda la tierra, Que has puesto tu gloria sobre los cielos!', 'water'),
('Old', 'Salmos', 8, 2, 'De la boca de los chiquitos y de los que maman, fundaste la fortaleza, A causa de tus enemigos, Para hacer cesar al enemigo, y al que se venga.', 'water'),
('Old', 'Salmos', 8, 3, 'Cuando veo tus cielos, obra de tus dedos, La luna y las estrellas que tú formaste:', 'water'),
('Old', 'Salmos', 8, 4, 'Digo: ¿Qué es el hombre, para que tengas de él memoria, Y el hijo del hombre, que lo visites?', 'water'),
('Old', 'Salmos', 8, 5, 'Pues le has hecho poco menor que los ángeles, Y coronástelo de gloria y de lustre.', 'water'),
('Old', 'Salmos', 8, 6, 'Hicístelo enseñorear de las obras de tus manos; Todo lo pusiste debajo de sus pies:', 'water'),
('Old', 'Salmos', 8, 7, 'Ovejas, y bueyes, todo ello; Y asimismo las bestias del campo,', 'water'),
('Old', 'Salmos', 8, 8, 'Las aves de los cielos, y los peces de la mar; Todo cuanto pasa por los senderos de la mar.', 'water'),
('Old', 'Salmos', 8, 9, 'Oh Jehová, Señor nuestro, ¡Cuán grande es tu nombre en toda la tierra!', 'water'),
('Old', 'Salmos', 9, 1, 'TE alabaré, oh Jehová, con todo mi corazón; Contaré todas tus maravillas.', 'water'),
('Old', 'Salmos', 9, 2, 'Alegraréme y regocijaréme en ti: Cantaré á tu nombre, oh Altísimo;', 'water'),
('Old', 'Salmos', 9, 3, 'Por haber sido mis enemigos vueltos atrás: Caerán y perecerán delante de ti.', 'water'),
('Old', 'Salmos', 9, 4, 'Porque has hecho mi juicio y mi causa: Sentástete en silla juzgando justicia.', 'water'),
('Old', 'Salmos', 9, 5, 'Reprendiste gentes, destruiste al malo, Raíste el nombre de ellos para siempre jamás.', 'water'),
('Old', 'Salmos', 9, 6, 'Oh enemigo, acabados son para siempre los asolamientos; Y las ciudades que derribaste, Su memoria pereció con ellas.', 'water'),
('Old', 'Salmos', 9, 7, 'Mas Jehová permanecerá para siempre: Dispuesto ha su trono para juicio.', 'water'),
('Old', 'Salmos', 9, 8, 'Y él juzgará el mundo con justicia; Y juzgará los pueblos con rectitud.', 'water'),
('Old', 'Salmos', 9, 9, 'Y será Jehová refugio al pobre, Refugio para el tiempo de angustia.', 'water'),
('Old', 'Salmos', 9, 10, 'Y en ti confiarán los que conocen tu nombre; Por cuanto tú, oh Jehová, no desamparaste á los que te buscaron.', 'water'),
('Old', 'Salmos', 9, 11, 'Cantad á Jehová, que habita en Sión: Noticiad en los pueblos sus obras.', 'water'),
('Old', 'Salmos', 9, 12, 'Porque demandando la sangre se acordó de ellos: No se olvidó del clamor de los pobres.', 'water'),
('Old', 'Salmos', 9, 13, 'Ten misericordia de mí, Jehová: Mira mi aflicción que padezco de los que me aborrecen, Tú que me levantas de las puertas de la muerte;', 'water'),
('Old', 'Salmos', 9, 14, 'Porque cuente yo todas tus alabanzas En las puertas de la hija de Sión, Y me goce en tu salud.', 'water'),
('Old', 'Salmos', 9, 15, 'Hundiéronse las gentes en la fosa que hicieron; En la red que escondieron fué tomado su pie.', 'water'),
('Old', 'Salmos', 9, 16, 'Jehová fué conocido en el juicio que hizo; En la obra de sus manos fué enlazado el malo. (Higaion. Selah.)', 'water'),
('Old', 'Salmos', 9, 17, 'Los malos serán trasladados al infierno, Todas las gentes que se olvidan de Dios.', 'water'),
('Old', 'Salmos', 9, 18, 'Porque no para siempre será olvidado el pobre; Ni la esperanza de los pobres perecerá perpetuamente.', 'water'),
('Old', 'Salmos', 9, 19, 'Levántate, oh Jehová; no se fortalezca el hombre; Sean juzgadas las gentes delante de ti.', 'water'),
('Old', 'Salmos', 9, 20, 'Pon, oh Jehová, temor en ellos: Conozcan las gentes que son no más que hombres. (Selah.)', 'water'),
('Old', 'Salmos', 10, 1, '¿POR qué estás lejos, oh Jehová, Y te escondes en el tiempo de la tribulación?', 'water'),
('Old', 'Salmos', 10, 2, 'Con arrogancia el malo persigue al pobre: Serán cogidos en los artificios que han ideado.', 'water'),
('Old', 'Salmos', 10, 3, 'Por cuanto se alaba el malo del deseo de su alma, Y bendice al codicioso ó quien Jehová aborrece.', 'water'),
('Old', 'Salmos', 10, 4, 'El malo, por la altivez de su rostro, no busca á Dios: No hay Dios en todos sus pensamientos.', 'water'),
('Old', 'Salmos', 10, 5, 'Sus caminos son viciosos en todo tiempo: Tus juicios los tiene muy lejos de su vista: Echa bocanadas en orden á todos sus enemigos.', 'water'),
('Old', 'Salmos', 10, 6, 'Dice en su corazón: No seré movido en ningún tiempo, Ni jamás me alcanzará el infortunio.', 'water'),
('Old', 'Salmos', 10, 7, 'Llena está su boca de maldición, y de engaños y fraude: Debajo de su lengua, vejación y maldad.', 'water'),
('Old', 'Salmos', 10, 8, 'Está en las guaridas de las aldeas: En los escondrijos mata al inocente: Sus ojos están acechando al pobre.', 'water'),
('Old', 'Salmos', 10, 9, 'Acecha en oculto, como el león desde su cama: Acecha para arrebatar al pobre: Arrebata al pobre trayéndolo á su red.', 'water'),
('Old', 'Salmos', 10, 10, 'Encógese, agáchase, Y caen en sus fuerzas muchos desdichados.', 'water'),
('Old', 'Salmos', 10, 11, 'Dice en su corazón: Dios está olvidado, Ha encubierto su rostro; nunca lo verá.', 'water'),
('Old', 'Salmos', 10, 12, 'Levántate, oh Jehová Dios, alza tu mano, No te olvides de los pobres.', 'water'),
('Old', 'Salmos', 10, 13, '¿Por qué irrita el malo á Dios? En su corazón ha dicho que no lo inquirirás.', 'water'),
('Old', 'Salmos', 10, 14, 'Tú lo tienes visto: porque tú miras el trabajo, y la vejación, para vengar le por tu mano: A ti se acoge el pobre, Tú eres el amparo del huérfano.', 'water'),
('Old', 'Salmos', 10, 15, 'Quebranta el brazo del malo: Del maligno buscarás su maldad, hasta que ninguna halles.', 'water'),
('Old', 'Salmos', 10, 16, 'Jehová, Rey eterno y perpetuo: De su tierra fueron destruídas las gentes.', 'water'),
('Old', 'Salmos', 10, 17, 'El deseo de los humildes oíste, oh Jehová: Tú dispones su corazón, y haces atento tu oído;', 'water'),
('Old', 'Salmos', 10, 18, 'Para juzgar al huérfano y al pobre, A fin de que no vuelva más á hacer violencia el hombre de la tierra.', 'water'),
('Old', 'Salmos', 11, 1, 'EN Jehová he confiado; ¿Cómo decís á mi alma: Escapa al monte cual ave?', 'water'),
('Old', 'Salmos', 11, 2, 'Porque he aquí, los malos flecharon el arco, Apercibieron sus saetas sobre la cuerda, Para asaetear en oculto á los rectos de corazón.', 'water'),
('Old', 'Salmos', 11, 3, 'Si fueren destruídos los fundamentos, ¿Qué ha de hacer el justo?', 'water'),
('Old', 'Salmos', 11, 4, 'Jehová en el templo de su santidad: La silla de Jehová está en el cielo: Sus ojos ven, sus párpados examinan á los hijos de los hombres.', 'water'),
('Old', 'Salmos', 11, 5, 'Jehová prueba al justo; Empero al malo y al que ama la violencia, su alma aborrece.', 'water'),
('Old', 'Salmos', 11, 6, 'Sobre los malos lloverá lazos; Fuego y azufre, con vientos de torbellinos, será la porción del cáliz de ellos.', 'water'),
('Old', 'Salmos', 11, 7, 'Porque el justo Jehová ama la justicia: Al recto mirará su rostro.', 'water'),
('Old', 'Salmos', 12, 1, 'SALVA, oh Jehová, porque se acabaron los misericordiosos: Porque se han acabado los fieles de entre los hijos de los hombres.', 'water'),
('Old', 'Salmos', 12, 2, 'Mentira habla cada uno con su prójimo; Con labios lisonjeros, con corazón doble hablan.', 'water'),
('Old', 'Salmos', 12, 3, 'Destruirá Jehová todos los labios lisonjeros, La lengua que habla grandezas,', 'water'),
('Old', 'Salmos', 12, 4, 'Que dijeron: Por nuestra lengua prevaleceremos; Nuestros labios están con nosotros: ¿quién nos es señor?', 'water'),
('Old', 'Salmos', 12, 5, 'Por la opresión de los pobres, por el gemido de los menesterosos, Ahora me levantaré, dice Jehová: Pondrélos en salvo del que contra ellos se engríe.', 'water'),
('Old', 'Salmos', 12, 6, 'Las palabras de Jehová, palabras limpias; Plata refinada en horno de tierra, Purificada siete veces.', 'water'),
('Old', 'Salmos', 12, 7, 'Tú, Jehová, los guardarás; Guárdalos para siempre de aquesta generación.', 'water'),
('Old', 'Salmos', 12, 8, 'Cercando andan los malos, Mientras son exaltados los más viles de los hijos de los hombres.', 'water'),
('Old', 'Salmos', 13, 1, '¿HASTA cuándo, Jehová? ¿me olvidarás para siempre? ¿Hasta cuándo esconderás tu rostro de mí?', 'water'),
('Old', 'Salmos', 13, 2, '¿Hasta cuándo pondré consejos en mi alma, Con ansiedad en mi corazón cada día? ¿Hasta cuándo será enaltecido mi enemigo sobre mí?', 'water'),
('Old', 'Salmos', 13, 3, 'Mira, óyeme, Jehová Dios mío: Alumbra mis ojos, porque no duerma en muerte;', 'water'),
('Old', 'Salmos', 13, 4, 'Porque no diga mi enemigo, Vencílo: Mis enemigos se alegrarán, si yo resbalare.', 'water'),
('Old', 'Salmos', 13, 5, 'Mas yo en tu misericordia he confiado: Alegraráse mi corazón en tu salud.', 'water'),
('Old', 'Salmos', 13, 6, 'Cantaré á Jehová, Porque me ha hecho bien.', 'water'),
('Old', 'Salmos', 14, 1, 'DIJO el necio en su corazón: No hay Dios. Corrompiéronse, hicieron obras abominables; No hay quien haga bien.', 'water'),
('Old', 'Salmos', 14, 2, 'Jehová miró desde los cielos sobre los hijos de los hombres, Por ver si había algún entendido, Que buscara á Dios.', 'water'),
('Old', 'Salmos', 14, 3, 'Todos declinaron, juntamente se han corrompido: No hay quien haga bien, no hay ni siquiera uno.', 'water'),
('Old', 'Salmos', 14, 4, '¿No tendrán conocimiento todos los que obran iniquidad, Que devoran á mi pueblo como si pan comiesen, Y á Jehová no invocaron?', 'water'),
('Old', 'Salmos', 14, 5, 'Allí temblaron de espanto; Porque Dios está con la nación de los justos.', 'water'),
('Old', 'Salmos', 14, 6, 'El consejo del pobre habéis escarnecido, Por cuanto Jehová es su esperanza.', 'water'),
('Old', 'Salmos', 14, 7, '¡Quién diese de Sión la salud de Israel! En tornando Jehová la cautividad de su pueblo, Se gozará Jacob, y alegraráse Israel.', 'water'),
('Old', 'Salmos', 15, 1, 'JEHOVA, ¿quién habitará en tu tabernáculo? ¿Quién residirá en el monte de tu santidad?', 'water'),
('Old', 'Salmos', 15, 2, 'El que anda en integridad, y obra justicia, Y habla verdad en su corazón.', 'water'),
('Old', 'Salmos', 15, 3, 'El que no detrae con su lengua, Ni hace mal á su prójimo, Ni contra su prójimo acoge oprobio alguno.', 'water'),
('Old', 'Salmos', 15, 4, 'Aquel á cuyos ojos es menospreciado el vil; Mas honra á los que temen á Jehová: Y habiendo jurado en daño suyo, no por eso muda.', 'water'),
('Old', 'Salmos', 15, 5, 'Quien su dinero no dió á usura, Ni contra el inocente tomó cohecho. El que hace estas cosas, no resbalará para siempre.', 'water'),
('Old', 'Salmos', 16, 1, 'GUARDAME, oh Dios, porque en ti he confiado.', 'water'),
('Old', 'Salmos', 16, 2, 'Dijiste, oh alma mía, á Jehová: Tú eres el Señor: Mi bien á ti no aprovecha;', 'water'),
('Old', 'Salmos', 16, 3, 'Sino á los santos que están en la tierra, Y á los íntegros: toda mi afición en ellos.', 'water'),
('Old', 'Salmos', 16, 4, 'Multiplicaránse los dolores de aquellos que sirven diligentes á otro dios: No ofreceré yo sus libaciones de sangre, Ni en mis labios tomaré sus nombres.', 'water'),
('Old', 'Salmos', 16, 5, 'Jehová es la porción de mi parte y de mi copa; Tú sustentarás mi suerte.', 'water'),
('Old', 'Salmos', 16, 6, 'Las cuerdas me cayeron en lugares deleitosos, Y es hermosa la heredad que me ha tocado.', 'water'),
('Old', 'Salmos', 16, 7, 'Bendeciré á Jehová que me aconseja: Aun en las noches me enseñan mis riñones.', 'water'),
('Old', 'Salmos', 16, 8, 'A Jehová he puesto siempre delante de mí: Porque está á mi diestra no seré conmovido.', 'water'),
('Old', 'Salmos', 16, 9, 'Alegróse por tanto mi corazón, y se gozó mi gloria: También mi carne reposará segura.', 'water'),
('Old', 'Salmos', 16, 10, 'Porque no dejarás mi alma en el sepulcro; Ni permitirás que tu santo vea corrupción.', 'water'),
('Old', 'Salmos', 16, 11, 'Me mostrarás la senda de la vida: Hartura de alegrías hay con tu rostro; Deleites en tu diestra para siempre.', 'water'),
('Old', 'Salmos', 17, 1, 'OYE, oh Jehová, justicia; está atento á mi clamor; Escucha mi oración hecha sin labios de engaño.', 'water'),
('Old', 'Salmos', 17, 2, 'De delante de tu rostro salga mi juicio; Vean tus ojos la rectitud.', 'water'),
('Old', 'Salmos', 17, 3, 'Tú has probado mi corazón, hasme visitado de noche; Me has apurado, y nada inicuo hallaste: Heme propuesto que mi boca no ha de propasarse.', 'water'),
('Old', 'Salmos', 17, 4, 'Para las obras humanas, por la palabra de tus labios Yo me he guardado de las vías del destructor.', 'water'),
('Old', 'Salmos', 17, 5, 'Sustenta mis pasos en tus caminos, Porque mis pies no resbalen.', 'water'),
('Old', 'Salmos', 17, 6, 'Yo te he invocado, por cuanto tú me oirás, oh Dios: Inclina á mí tu oído, escucha mi palabra.', 'water'),
('Old', 'Salmos', 17, 7, 'Muestra tus estupendas misericordias, tú que salvas á los que en ti confían. De los que se levantan contra tu diestra.', 'water'),
('Old', 'Salmos', 17, 8, 'Guárdame como lo negro de la niñeta del ojo, Escóndeme con la sombra de tus alas,', 'water'),
('Old', 'Salmos', 17, 9, 'De delante de los malos que me oprimen, De mis enemigos que me cercan por la vida.', 'water'),
('Old', 'Salmos', 17, 10, 'Cerrados están con su grosura; Con su boca hablan soberbiamente.', 'water'),
('Old', 'Salmos', 17, 11, 'Nuestros pasos nos han cercado ahora: Puestos tienen sus ojos para echar nos por tierra.', 'water'),
('Old', 'Salmos', 17, 12, 'Parecen al león que desea hacer presa, Y al leoncillo que está escondido.', 'water'),
('Old', 'Salmos', 17, 13, 'Levántate, oh Jehová; Prevén su encuentro, póstrale: Libra mi alma del malo con tu espada;', 'water'),
('Old', 'Salmos', 17, 14, 'De los hombres con tu mano, oh Jehová, De los hombres de mundo, cuya parte es en esta vida, Y cuyo vientre hinches de tu tesoro: Hartan sus hijos, Y dejan el resto á sus chiquitos.', 'water'),
('Old', 'Salmos', 17, 15, 'Yo en justicia veré tu rostro: Seré saciado cuando despertare á tu semejanza.', 'water'),
('Old', 'Salmos', 18, 1, 'AMARTE he, oh Jehová, fortaleza mía.', 'water'),
('Old', 'Salmos', 18, 2, 'Jehová, roca mía y castillo mío, y mi libertador; Dios mío, fuerte mío, en él confiaré; Escudo mío, y el cuerno de mi salud, mi refugio.', 'water'),
('Old', 'Salmos', 18, 3, 'Invocaré á Jehová, digno de ser alabado, Y seré salvo de mis enemigos.', 'water'),
('Old', 'Salmos', 18, 4, 'Cercáronme dolores de muerte, Y torrentes de perversidad me atemorizaron.', 'water'),
('Old', 'Salmos', 18, 5, 'Dolores del sepulcro me rodearon, Previniéronme lazos de muerte.', 'water'),
('Old', 'Salmos', 18, 6, 'En mi angustia invoqué á Jehová, Y clamé á mi Dios: El oyó mi voz desde su templo, Y mi clamor llegó delante de él, á sus oídos.', 'water'),
('Old', 'Salmos', 18, 7, 'Y la tierra fué conmovida y tembló; Y moviéronse los fundamentos de los montes, Y se estremecieron, porque se indignó él.', 'water'),
('Old', 'Salmos', 18, 8, 'Humo subió de su nariz, Y de su boca consumidor fuego; Carbones fueron por él encendidos.', 'water'),
('Old', 'Salmos', 18, 9, 'Y bajó los cielos, y descendió; Y oscuridad debajo de sus pies.', 'water'),
('Old', 'Salmos', 18, 10, 'Y cabalgó sobre un querubín, y voló: Voló sobre las alas del viento.', 'water'),
('Old', 'Salmos', 18, 11, 'Puso tinieblas por escondedero suyo, su pabellón en derredor de sí; Oscuridad de aguas, nubes de los cielos.', 'water'),
('Old', 'Salmos', 18, 12, 'Por el resplandor delante de él, sus nubes pasaron; Granizo y carbones ardientes.', 'water'),
('Old', 'Salmos', 18, 13, 'Y tronó en los cielos Jehová, Y el Altísimo dió su voz; Granizo y carbones de fuego.', 'water'),
('Old', 'Salmos', 18, 14, 'Y envió sus saetas, y desbaratólos; Y echó relámpagos, y los destruyó.', 'water'),
('Old', 'Salmos', 18, 15, 'Y aparecieron las honduras de las aguas, Y descubriéronse los cimientos del mundo, A tu reprensión, oh Jehová, Por el soplo del viento de tu nariz.', 'water'),
('Old', 'Salmos', 18, 16, 'Envió desde lo alto; tomóme, Sácome de las muchas aguas.', 'water'),
('Old', 'Salmos', 18, 17, 'Libróme de mi poderoso enemigo, Y de los que me aborrecían, aunque eran ellos más fuertes que yo.', 'water'),
('Old', 'Salmos', 18, 18, 'Asaltáronme en el día de mi quebranto: Mas Jehová fué mi apoyo.', 'water'),
('Old', 'Salmos', 18, 19, 'Y sacóme á anchura: Libróme, porque se agradó de mí.', 'water'),
('Old', 'Salmos', 18, 20, 'Hame pagado Jehová conforme á mi justicia: Conforme á la limpieza de mis manos me ha vuelto.', 'water'),
('Old', 'Salmos', 18, 21, 'Porque yo he guardado los caminos de Jehová, Y no me aparté impíamente de mi Dios.', 'water'),
('Old', 'Salmos', 18, 22, 'Pues todos sus juicios estuvieron delante de mí, Y no eché de mí sus estatutos.', 'water'),
('Old', 'Salmos', 18, 23, 'Y fuí integro para con él, y cauteléme de mi maldad.', 'water'),
('Old', 'Salmos', 18, 24, 'Pagóme pues Jehová conforme á mi justicia; Conforme á la limpieza de mis manos delante de sus ojos.', 'water'),
('Old', 'Salmos', 18, 25, 'Con el misericordioso te mostrarás misericordioso, Y recto para con el hombre íntegro.', 'water'),
('Old', 'Salmos', 18, 26, 'Limpio te mostrarás para con el limpio, Y severo serás para con el perverso.', 'water'),
('Old', 'Salmos', 18, 27, 'Y tú salvarás al pueblo humilde, Y humillarás los ojos altivos.', 'water'),
('Old', 'Salmos', 18, 28, 'Tú pues alumbrarás mi lámpara: Jehová mi Dios alumbrará mis tinieblas.', 'water'),
('Old', 'Salmos', 18, 29, 'Porque contigo desharé ejércitos; Y con mi Dios asaltaré muros.', 'water'),
('Old', 'Salmos', 18, 30, 'Dios, perfecto su camino: Es acendrada la palabra de Jehová: Escudo es á todos los que en él esperan.', 'water'),
('Old', 'Salmos', 18, 31, 'Porque ¿qué Dios hay fuera de Jehová? ¿Y qué fuerte fuera de nuestro Dios?', 'water'),
('Old', 'Salmos', 18, 32, 'Dios es el que me ciñe de fuerza, E hizo perfecto mi camino;', 'water'),
('Old', 'Salmos', 18, 33, 'Quien pone mis pies como pies de ciervas, E hízome estar sobre mis alturas;', 'water'),
('Old', 'Salmos', 18, 34, 'Quien enseña mis manos para la batalla, Y será quebrado con mis brazos el arco de acero.', 'water'),
('Old', 'Salmos', 18, 35, 'Dísteme asimismo el escudo de tu salud: Y tu diestra me sustentó, Y tu benignidad me ha acrecentado.', 'water'),
('Old', 'Salmos', 18, 36, 'Ensanchaste mis pasos debajo de mí, Y no titubearon mis rodillas.', 'water'),
('Old', 'Salmos', 18, 37, 'Perseguido he mis enemigos, y alcancélos, Y no volví hasta acabarlos.', 'water'),
('Old', 'Salmos', 18, 38, 'Helos herido, y no podrán levantarse: Cayeron debajo de mis pies.', 'water'),
('Old', 'Salmos', 18, 39, 'Pues me ceñiste de fortaleza para la pelea; Has agobiado mis enemigos debajo de mí.', 'water'),
('Old', 'Salmos', 18, 40, 'Y dísteme la cerviz de mis enemigos, Y destruí á los que me aborrecían.', 'water'),
('Old', 'Salmos', 18, 41, 'Clamaron, y no hubo quien salvase: Aun á Jehová, mas no los oyó.', 'water'),
('Old', 'Salmos', 18, 42, 'Y molílos como polvo delante del viento; Esparcílos como lodo de las calles.', 'water'),
('Old', 'Salmos', 18, 43, 'Librásteme de contiendas de pueblo: Pusísteme por cabecera de gentes: Pueblo que yo no conocía, me sirvió.', 'water'),
('Old', 'Salmos', 18, 44, 'Así que hubo oído, me obedeció; Los hijos de extraños me mintieron;', 'water'),
('Old', 'Salmos', 18, 45, 'Los extraños flaquearon, Y tuvieron miedo desde sus encerramientos.', 'water'),
('Old', 'Salmos', 18, 46, 'Viva Jehová, y sea bendita mi roca; Y ensalzado sea el Dios de mi salud:', 'water'),
('Old', 'Salmos', 18, 47, 'El Dios que me da las venganzas, Y sujetó pueblos á mí.', 'water'),
('Old', 'Salmos', 18, 48, 'Mi libertador de mis enemigos: Hicísteme también superior de mis adversarios; Librásteme de varón violento.', 'water'),
('Old', 'Salmos', 18, 49, 'Por tanto yo te confesaré entre las gentes, oh Jehová, Y cantaré á tu nombre.', 'water'),
('Old', 'Salmos', 18, 50, 'El cual engrandece las saludes de su rey, Y hace misericordia á su ungido, A David y á su simiente, para siempre.', 'water'),
('Old', 'Salmos', 19, 1, 'LOS cielos cuentan la gloria de Dios, Y la expansión denuncia la obra de sus manos.', 'water'),
('Old', 'Salmos', 19, 2, 'El un día emite palabra al otro día, Y la una noche á la otra noche declara sabiduría.', 'water'),
('Old', 'Salmos', 19, 3, 'No hay dicho, ni palabras, Ni es oída su voz.', 'water'),
('Old', 'Salmos', 19, 4, 'Por toda la tierra salió su hilo, Y al cabo del mundo sus palabras. En ellos puso tabernáculo para el sol.', 'water'),
('Old', 'Salmos', 19, 5, 'Y él, como un novio que sale de su tálamo, Alégrase cual gigante para correr el camino.', 'water'),
('Old', 'Salmos', 19, 6, 'Del un cabo de los cielos es su salida, Y su giro hasta la extremidad de ellos: Y no hay quien se esconda de su calor.', 'water'),
('Old', 'Salmos', 19, 7, 'La ley de Jehová es perfecta, que vuelve el alma: El testimonio de Jehová, fiel, que hace sabio al pequeño.', 'water'),
('Old', 'Salmos', 19, 8, 'Los mandamientos de Jehová son rectos, que alegran el corazón: El precepto de Jehová, puro, que alumbra los ojos.', 'water'),
('Old', 'Salmos', 19, 9, 'El temor de Jehová, limpio, que permanece para siempre; Los juicios de Jehová son verdad, todos justos.', 'water'),
('Old', 'Salmos', 19, 10, 'Deseables son más que el oro, y más que mucho oro afinado; Y dulces más que miel, y que la que destila del panal.', 'water'),
('Old', 'Salmos', 19, 11, 'Tu siervo es además amonestado con ellos: En guardarlos hay grande galardón.', 'water'),
('Old', 'Salmos', 19, 12, 'Los errores, ¿quién los entenderá? Líbrame de los que me son ocultos.', 'water'),
('Old', 'Salmos', 19, 13, 'Detén asimismo á tu siervo de las soberbias; Que no se enseñoreen de mí: Entonces seré íntegro, y estaré limpio de gran rebelión.', 'water'),
('Old', 'Salmos', 19, 14, 'Sean gratos los dichos de mi boca y la meditación de mi corazón delante de ti, Oh Jehová, roca mía, y redentor mío', 'water'),
('Old', 'Salmos', 20, 1, 'OIGATE Jehová en el día de conflicto; Defiéndate el nombre del Dios de Jacob.', 'water'),
('Old', 'Salmos', 20, 2, 'Envíete ayuda desde el santuario, Y desde Sión te sostenga.', 'water'),
('Old', 'Salmos', 20, 3, 'Haga memoria de todos tus presentes, Y reduzca á ceniza tu holocausto. (Selah.)', 'water'),
('Old', 'Salmos', 20, 4, 'Déte conforme á tu corazón, Y cumpla todo tu consejo.', 'water'),
('Old', 'Salmos', 20, 5, 'Nosotros nos alegraremos por tu salud, Y alzaremos pendón en el nombre de nuestro Dios: Cumpla Jehová todas tus peticiones.', 'water'),
('Old', 'Salmos', 20, 6, 'Ahora echo de ver que Jehová guarda á su ungido: Oirálo desde los cielos de su santidad, Con la fuerza de la salvación de su diestra.', 'water'),
('Old', 'Salmos', 20, 7, 'Estos confían en carros, y aquéllos en caballos: Mas nosotros del nombre de Jehová nuestro Dios tendremos memoria.', 'water'),
('Old', 'Salmos', 20, 8, 'Ellos arrodillaron, y cayeron; Mas nosotros nos levantamos, y nos enhestamos.', 'water'),
('Old', 'Salmos', 20, 9, 'Salva, Jehová: Que el Rey nos oiga el día que lo invocáremos.', 'water'),
('Old', 'Salmos', 21, 1, 'ALEGRARASE el rey en tu fortaleza, oh Jehová; Y en tu salud se gozará mucho.', 'water'),
('Old', 'Salmos', 21, 2, 'El deseo de su corazón le diste, Y no le negaste lo que sus labios pronunciaron. (Selah.)', 'water'),
('Old', 'Salmos', 21, 3, 'Pues le has salido al encuentro con bendiciones de bien: Corona de oro fino has puesto sobre su cabeza.', 'water'),
('Old', 'Salmos', 21, 4, 'Vida te demandó, y dístele Largura de días por siglos y siglos.', 'water'),
('Old', 'Salmos', 21, 5, 'Grande es su gloria en tu salud: Honra y majestad has puesto sobre él.', 'water'),
('Old', 'Salmos', 21, 6, 'Porque lo has bendecido para siempre; Llenástelo de alegría con tu rostro.', 'water'),
('Old', 'Salmos', 21, 7, 'Por cuanto el rey confía en Jehová, Y en la misericordia del Altísimo, no será conmovido.', 'water'),
('Old', 'Salmos', 21, 8, 'Alcanzará tu mano á todos tus enemigos; Tu diestra alcanzará á los que te aborrecen.', 'water'),
('Old', 'Salmos', 21, 9, 'Ponerlos has como horno de fuego en el tiempo de tu ira: Jehová los deshará en su furor, Y fuego los consumirá.', 'water'),
('Old', 'Salmos', 21, 10, 'Su fruto destruirás de la tierra, Y su simiente de entre los hijos de los hombres.', 'water'),
('Old', 'Salmos', 21, 11, 'Porque trazaron el mal contra ti: Fraguaron maquinaciones, mas no prevalecerán.', 'water'),
('Old', 'Salmos', 21, 12, 'Pues tú los pondrás en fuga, Cuando aparejares en tus cuerdas las saetas contra sus rostros.', 'water'),
('Old', 'Salmos', 21, 13, 'Ensálzate, oh Jehová, con tu fortaleza: Cantaremos y alabaremos tu poderío.', 'water'),
('Old', 'Salmos', 22, 1, 'DIOS mío, Dios mío, ¿por qué me has dejado? ¿Por qué estás lejos de mi salud, y de las palabras de mi clamor?', 'water'),
('Old', 'Salmos', 22, 2, 'Dios mío, clamo de día, y no oyes; Y de noche, y no hay para mí silencio.', 'water'),
('Old', 'Salmos', 22, 3, 'Tú empero eres santo, Tú que habitas entre las alabanzas de Israel.', 'water'),
('Old', 'Salmos', 22, 4, 'En ti esperaron nuestros padres: Esperaron, y tú los libraste.', 'water'),
('Old', 'Salmos', 22, 5, 'Clamaron á ti, y fueron librados: Esperaron en ti, y no se avergonzaron.', 'water'),
('Old', 'Salmos', 22, 6, 'Mas yo soy gusano, y no hombre; Oprobio de los hombres, y desecho del pueblo.', 'water'),
('Old', 'Salmos', 22, 7, 'Todos los que me ven, escarnecen de mí; Estiran los labios, menean la cabeza, diciendo:', 'water'),
('Old', 'Salmos', 22, 8, 'Remítese á Jehová, líbrelo; Sálvele, puesto que en él se complacía.', 'water'),
('Old', 'Salmos', 22, 9, 'Empero tú eres el que me sacó del vientre, El que me haces esperar desde que estaba á los pechos de mi madre.', 'water'),
('Old', 'Salmos', 22, 10, 'Sobre ti fuí echado desde la matriz: Desde el vientre de mi madre, tú eres mi Dios.', 'water'),
('Old', 'Salmos', 22, 11, 'No te alejes de mí, porque la angustia está cerca; Porque no hay quien ayude.', 'water'),
('Old', 'Salmos', 22, 12, 'Hanme rodeado muchos toros; Fuertes toros de Basán me han cercado.', 'water'),
('Old', 'Salmos', 22, 13, 'Abrieron sobre mí su boca, Como león rapante y rugiente.', 'water'),
('Old', 'Salmos', 22, 14, 'Heme escurrido como aguas, Y todos mis huesos se descoyuntaron: Mi corazón fué como cera, Desliéndose en medio de mis entrañas.', 'water'),
('Old', 'Salmos', 22, 15, 'Secóse como un tiesto mi vigor, Y mi lengua se pegó á mi paladar; Y me has puesto en el polvo de la muerte.', 'water'),
('Old', 'Salmos', 22, 16, 'Porque perros me han rodeado, Hame cercado cuadrilla de malignos: Horadaron mis manos y mis pies.', 'water'),
('Old', 'Salmos', 22, 17, 'Contar puedo todos mis huesos; Ellos miran, considéranme.', 'water'),
('Old', 'Salmos', 22, 18, 'Partieron entre sí mis vestidos, Y sobre mi ropa echaron suertes.', 'water'),
('Old', 'Salmos', 22, 19, 'Mas tú, Jehová, no te alejes; Fortaleza mía, apresúrate para mi ayuda.', 'water'),
('Old', 'Salmos', 22, 20, 'Libra de la espada mi alma; Del poder del perro mi única.', 'water'),
('Old', 'Salmos', 22, 21, 'Sálvame de la boca del león, Y óyeme librándome de los cuernos de los unicornios.', 'water'),
('Old', 'Salmos', 22, 22, 'Anunciaré tu nombre á mis hermanos: En medio de la congregación te alabaré.', 'water'),
('Old', 'Salmos', 22, 23, 'Los que teméis á Jehová, alabadle; Glorificadle, simiente toda de Jacob; Y temed de él, vosotros, simiente toda de Israel.', 'water'),
('Old', 'Salmos', 22, 24, 'Porque no menospreció ni abominó la aflicción del pobre, Ni de él escondió su rostro; Sino que cuando clamó á él, oyóle.', 'water'),
('Old', 'Salmos', 22, 25, 'De ti será mi alabanza en la grande congregación; Mis votos pagaré delante de los que le temen.', 'water'),
('Old', 'Salmos', 22, 26, 'Comerán los pobres, y serán saciados: Alabarán á Jehová los que le buscan: Vivirá vuestro corazón para siempre.', 'water'),
('Old', 'Salmos', 22, 27, 'Acordarse han, y volveránse á Jehová todos los términos de la tierra; Y se humillarán delante de ti todas las familias de las gentes.', 'water'),
('Old', 'Salmos', 22, 28, 'Porque de Jehová es el reino; Y él se enseñoreará de las gentes.', 'water'),
('Old', 'Salmos', 22, 29, 'Comerán y adorarán todos los poderosos de la tierra: Postraránse delante de él todos los que descienden al polvo, Si bien ninguno puede conservar la vida á su propia alma.', 'water'),
('Old', 'Salmos', 22, 30, 'La posteridad le servirá; Será ella contada por una generación de Jehová.', 'water'),
('Old', 'Salmos', 22, 31, 'Vendrán, y anunciarán al pueblo que naciere, Su justicia que él hizo.', 'water'),
('Old', 'Salmos', 23, 1, 'JEHOVA es mi pastor; nada me faltará.', 'water'),
('Old', 'Salmos', 23, 2, 'En lugares de delicados pastos me hará yacer: Junto á aguas de reposo me pastoreará.', 'water'),
('Old', 'Salmos', 23, 3, 'Confortará mi alma; Guiárame por sendas de justicia por amor de su nombre.', 'water'),
('Old', 'Salmos', 23, 4, 'Aunque ande en valle de sombra de muerte, No temeré mal alguno; porque tú estarás conmigo: Tu vara y tu cayado me infundirán aliento.', 'water'),
('Old', 'Salmos', 23, 5, 'Aderezarás mesa delante de mí, en presencia de mis angustiadores: Ungiste mi cabeza con aceite: mi copa está rebosando.', 'water'),
('Old', 'Salmos', 23, 6, 'Ciertamente el bien y la misericordia me seguirán todos los días de mi vida: Y en la casa de Jehová moraré por largos días.', 'water'),
('Old', 'Salmos', 24, 1, 'DE Jehová es la tierra y su plenitud; El mundo, y los que en él habitan.', 'water'),
('Old', 'Salmos', 24, 2, 'Porque él la fundó sobre los mares, Y afirmóla sobre los ríos.', 'water'),
('Old', 'Salmos', 24, 3, '¿Quién subirá al monte de Jehová? ¿Y quién estará en el lugar de su santidad?', 'water'),
('Old', 'Salmos', 24, 4, 'El limpio de manos, y puro de corazón: El que no ha elevado su alma á la vanidad, Ni jurado con engaño.', 'water'),
('Old', 'Salmos', 24, 5, 'El recibirá bendición de Jehová, Y justicia del Dios de salud.', 'water'),
('Old', 'Salmos', 24, 6, 'Tal es la generación de los que le buscan, De los que buscan tu rostro, oh Dios de Jacob. (Selah.)', 'water'),
('Old', 'Salmos', 24, 7, 'Alzad, oh puertas, vuestras cabezas, Y alzaos vosotras, puertas eternas, Y entrará el Rey de gloria.', 'water'),
('Old', 'Salmos', 24, 8, '¿Quién es este Rey de gloria? Jehová el fuerte y valiente, Jehová el poderoso en batalla.', 'water'),
('Old', 'Salmos', 24, 9, 'Alzad, oh puertas, vuestras cabezas, Y alzaos vosotras, puertas eternas, Y entrará el Rey de gloria.', 'water'),
('Old', 'Salmos', 24, 10, '¿Quién es este Rey de gloria? Jehová de los ejércitos, El es el Rey de la gloria. (Selah.)', 'water'),
('Old', 'Salmos', 25, 1, 'A TI, oh Jehová, levantaré mi alma.', 'water'),
('Old', 'Salmos', 25, 2, 'Dios mío, en ti confío; No sea yo avergonzado, No se alegren de mí mis enemigos.', 'water'),
('Old', 'Salmos', 25, 3, 'Ciertamente ninguno de cuantos en ti esperan será confundido: Serán avergonzados los que se rebelan sin causa.', 'water'),
('Old', 'Salmos', 25, 4, 'Muéstrame, oh Jehová, tus caminos; Enséñame tus sendas.', 'water'),
('Old', 'Salmos', 25, 5, 'Encamíname en tu verdad, y enséñame; Porque tú eres el Dios de mi salud: En ti he esperado todo el día.', 'water'),
('Old', 'Salmos', 25, 6, 'Acuérdate, oh Jehová, de tus conmiseraciones y de tus misericordias, Que son perpetuas.', 'water'),
('Old', 'Salmos', 25, 7, 'De los pecados de mi mocedad, y de mis rebeliones, no te acuerdes; Conforme á tu misericordia acuérdate de mí, Por tu bondad, oh Jehová.', 'water'),
('Old', 'Salmos', 25, 8, 'Bueno y recto es Jehová: Por tanto él enseñará á los pecadores el camino.', 'water'),
('Old', 'Salmos', 25, 9, 'Encaminará á los humildes por el juicio, Y enseñará á los mansos su carrera.', 'water'),
('Old', 'Salmos', 25, 10, 'Todas las sendas de Jehová son misericordia y verdad, Para los que guardan su pacto y sus testimonios.', 'water'),
('Old', 'Salmos', 25, 11, 'Por amor de tu nombre, oh Jehová, Perdonarás también mi pecado; porque es grande.', 'water'),
('Old', 'Salmos', 25, 12, '¿Quién es el hombre que teme á Jehová? El le enseñará el camino que ha de escoger.', 'water'),
('Old', 'Salmos', 25, 13, 'Su alma reposará en el bien, Y su simiente heredará la tierra.', 'water'),
('Old', 'Salmos', 25, 14, 'El secreto de Jehová es para los que le temen; Y á ellos hará conocer su alianza.', 'water'),
('Old', 'Salmos', 25, 15, 'Mis ojos están siempre hacia Jehová; Porque él sacará mis pies de la red.', 'water'),
('Old', 'Salmos', 25, 16, 'Mírame, y ten misericordia de mí; Porque estoy solo y afligido.', 'water'),
('Old', 'Salmos', 25, 17, 'Las angustias de mi corazón se han aumentado: Sácame de mis congojas.', 'water'),
('Old', 'Salmos', 25, 18, 'Mira mi aflicción y mi trabajo: Y perdona todos mis pecados.', 'water'),
('Old', 'Salmos', 25, 19, 'Mira mis enemigos, que se han multiplicado, Y con odio violento me aborrecen.', 'water'),
('Old', 'Salmos', 25, 20, 'Guarda mi alma, y líbrame: No sea yo avergonzado, porque en ti confié.', 'water'),
('Old', 'Salmos', 25, 21, 'Integridad y rectitud me guarden; Porque en ti he esperado.', 'water'),
('Old', 'Salmos', 25, 22, 'Redime, oh Dios, á Israel De todas sus angustias.', 'water'),
('Old', 'Salmos', 26, 1, 'JUZGAME, oh Jehová, porque yo en mi integridad he andado: Confiado he asimismo en Jehová, no vacilaré.', 'water'),
('Old', 'Salmos', 26, 2, 'Pruébame, oh Jehová, y sondéame: Examina mis riñones y mi corazón.', 'water'),
('Old', 'Salmos', 26, 3, 'Porque tu misericordia está delante de mis ojos, Y en tu verdad ando.', 'water'),
('Old', 'Salmos', 26, 4, 'No me he sentado con hombres de falsedad; Ni entré con los que andan encubiertamente.', 'water'),
('Old', 'Salmos', 26, 5, 'Aborrecí la reunión de los malignos, Y con los impíos nunca me senté.', 'water'),
('Old', 'Salmos', 26, 6, 'Lavaré en inocencia mis manos, Y andaré alrededor de tu altar, oh Jehová:', 'water'),
('Old', 'Salmos', 26, 7, 'Para exclamar con voz de acción de gracias, Y para contar todas tus maravillas.', 'water'),
('Old', 'Salmos', 26, 8, 'Jehová, la habitación de tu casa he amado, Y el lugar del tabernáculo de tu gloria.', 'water'),
('Old', 'Salmos', 26, 9, 'No juntes con los pecadores mi alma, Ni con los hombres de sangres mi vida:', 'water'),
('Old', 'Salmos', 26, 10, 'En cuyas manos está el mal, Y su diestra está llena de sobornos.', 'water'),
('Old', 'Salmos', 26, 11, 'Yo empero andaré en mi integridad: Redímeme, y ten misericordia de mí.', 'water'),
('Old', 'Salmos', 26, 12, 'Mi pie ha estado en rectitud: En las congregaciones bendeciré á Jehová.', 'water'),
('Old', 'Salmos', 27, 1, 'JEHOVA es mi luz y mi salvación: ¿de quién temeré? Jehová es la fortaleza de mi vida: ¿de quién he de atemorizarme?', 'water'),
('Old', 'Salmos', 27, 2, 'Cuando se allegaron contra mí los malignos, mis angustiadores y mis enemigos, Para comer mis carnes, ellos tropezaron y cayeron.', 'water'),
('Old', 'Salmos', 27, 3, 'Aunque se asiente campo contra mí, No temerá mi corazón: Aunque contra mí se levante guerra, Yo en esto confío.', 'water'),
('Old', 'Salmos', 27, 4, 'Una cosa he demandado á Jehová, ésta buscaré: Que esté yo en la casa de Jehová todos los días de mi vida, Para contemplar la hermosura de Jehová, y para inquirir en su templo.', 'water'),
('Old', 'Salmos', 27, 5, 'Porque él me esconderá en su tabernáculo en el día del mal; Ocultaráme en lo reservado de su pabellón; Pondráme en alto sobre una roca.', 'water'),
('Old', 'Salmos', 27, 6, 'Y luego ensalzará mi cabeza sobre mis enemigos en derredor de mí: Y yo sacrificaré en su tabernáculo sacrificios de júbilo: Cantaré y salmearé á Jehová.', 'water'),
('Old', 'Salmos', 27, 7, 'Oye, oh Jehová, mi voz con que á ti clamo; Y ten misericordia de mí, respóndeme.', 'water'),
('Old', 'Salmos', 27, 8, 'Mi corazón ha dicho de ti: Buscad mi rostro. Tu rostro buscaré, oh Jehová.', 'water'),
('Old', 'Salmos', 27, 9, 'No escondas tu rostro de mí, No apartes con ira á tu siervo: Mi ayuda has sido; No me dejes y no me desampares, Dios de mi salud.', 'water'),
('Old', 'Salmos', 27, 10, 'Aunque mi padre y mi madre me dejaran, Jehová con todo me recogerá.', 'water'),
('Old', 'Salmos', 27, 11, 'Enséñame, oh Jehová, tu camino, Y guíame por senda de rectitud, A causa de mis enemigos.', 'water'),
('Old', 'Salmos', 27, 12, 'No me entregues á la voluntad de mis enemigos; Porque se han levantado contra mí testigos falsos, y los que respiran crueldad.', 'water'),
('Old', 'Salmos', 27, 13, 'Hubiera yo desmayado, si no creyese que tengo de ver la bondad de Jehová En la tierra de los vivientes.', 'water'),
('Old', 'Salmos', 27, 14, 'Aguarda á Jehová; Esfuérzate, y aliéntese tu corazón: Sí, espera á Jehová.', 'water'),
('Old', 'Salmos', 28, 1, 'A TI clamaré, oh Jehová, Fortaleza mía: no te desentiendas de mí; Porque no sea yo, dejándome tú, Semejante á los que descienden al sepulcro.', 'water'),
('Old', 'Salmos', 28, 2, 'Oye la voz de mis ruegos cuando clamo á ti, Cuando alzo mis manos hacia el templo de tu santidad.', 'water'),
('Old', 'Salmos', 28, 3, 'No me arrebates á una con los malos, Y con los que hacen iniquidad: Los cuales hablan paz con sus prójimos, Y la maldad está en su corazón.', 'water'),
('Old', 'Salmos', 28, 4, 'Dales conforme á su obra, y conforme á la malicia de sus hechos: Dales conforme á la obra de sus manos, Dales su paga.', 'water'),
('Old', 'Salmos', 28, 5, 'Porque no atendieron á las obras de Jehová, Ni al hecho de sus manos, Derribarálos, y no los edificará.', 'water'),
('Old', 'Salmos', 28, 6, 'Bendito Jehová, Que oyó la voz de mis ruegos.', 'water'),
('Old', 'Salmos', 28, 7, 'Jehová es mi fortaleza y mi escudo: En él esperó mi corazón, y fuí ayudado; Por lo que se gozó mi corazón, Y con mi canción le alabaré.', 'water'),
('Old', 'Salmos', 28, 8, 'Jehová es su fuerza, Y la fortaleza de las saludes de su ungido.', 'water'),
('Old', 'Salmos', 28, 9, 'Salva á tu pueblo, y bendice á tu heredad; Y pastoréalos y ensálzalos para siempre.', 'water'),
('Old', 'Salmos', 29, 1, 'DAD á Jehová, oh hijos de fuertes, Dad á Jehová la gloria y la fortaleza.', 'water'),
('Old', 'Salmos', 29, 2, 'Dad á Jehová la gloria debida á su nombre: Humillaos á Jehová en el glorioso santuario.', 'water'),
('Old', 'Salmos', 29, 3, 'Voz de Jehová sobre las aguas: Hizo tronar el Dios de gloria: Jehová sobre las muchas aguas.', 'water'),
('Old', 'Salmos', 29, 4, 'Voz de Jehová con potencia; Voz de Jehová con gloria.', 'water'),
('Old', 'Salmos', 29, 5, 'Voz de Jehová que quebranta los cedros; Y quebrantó Jehová los cedros del Líbano.', 'water'),
('Old', 'Salmos', 29, 6, 'E hízolos saltar como becerros; Al Líbano y al Sirión como hijos de unicornios.', 'water'),
('Old', 'Salmos', 29, 7, 'Voz de Jehová que derrama llamas de fuego.', 'water'),
('Old', 'Salmos', 29, 8, 'Voz de Jehová que hará temblar el desierto; Hará temblar Jehová el desierto de Cades.', 'water'),
('Old', 'Salmos', 29, 9, 'Voz de Jehová que hará estar de parto á las ciervas, Y desnudará la breñas: Y en su templo todos los suyos le dicen gloria.', 'water'),
('Old', 'Salmos', 29, 10, 'Jehová preside en el diluvio, Y asentóse Jehová por rey para siempre.', 'water'),
('Old', 'Salmos', 29, 11, 'Jehová dará fortaleza á su pueblo: Jehová bendecirá á su pueblo en paz. Salmo de David.', 'water'),
('Old', 'Salmos', 30, 1, 'GLORIFICARTE he, oh Jehová; porque me has ensalzado, Y no hiciste á mis enemigos alegrarse de mí.', 'water'),
('Old', 'Salmos', 30, 2, 'Jehová Dios mío, A ti clamé, y me sanaste.', 'water'),
('Old', 'Salmos', 30, 3, 'Oh Jehová, hiciste subir mi alma del sepulcro; Dísteme vida, para que no descendiese á la sepultura.', 'water'),
('Old', 'Salmos', 30, 4, 'Cantad á Jehová, vosotros sus santos, Y celebrad la memoria de su santidad.', 'water'),
('Old', 'Salmos', 30, 5, 'Porque un momento será su furor; Mas en su voluntad está la vida: Por la tarde durará el lloró, Y á la mañana vendrá la alegría.', 'water'),
('Old', 'Salmos', 30, 6, 'Y dije yo en mi prosperidad: No seré jamás conmovido;', 'water'),
('Old', 'Salmos', 30, 7, 'Porque tú, Jehová, por tu benevolencia has asentado mi monte con fortaleza. Escondiste tu rostro, fuí conturbado.', 'water'),
('Old', 'Salmos', 30, 8, 'A ti, oh Jehová, clamaré; Y al Señor suplicaré.', 'water'),
('Old', 'Salmos', 30, 9, '¿Qué provecho hay en mi muerte, cuando yo descienda al hoyo? ¿Te alabará el polvo? ¿anunciará tu verdad?', 'water'),
('Old', 'Salmos', 30, 10, 'Oye, oh Jehová, y ten misericordia de mí: Jehová, sé tú mi ayudador.', 'water'),
('Old', 'Salmos', 30, 11, 'Has tornado mi endecha en baile; Desataste mi saco, y ceñísteme de alegría.', 'water'),
('Old', 'Salmos', 30, 12, 'Por tanto á ti cantaré, gloria mía, y no estaré callado. Jehová Dios mío, te alabaré para siempre.', 'water'),
('Old', 'Salmos', 31, 1, 'EN ti, oh Jehová, he esperado; no sea yo confundido para siempre: Líbrame en tu justicia.', 'water'),
('Old', 'Salmos', 31, 2, 'Inclina á mí tu oído, líbrame presto; Séme por roca de fortaleza, por casa fuerte para salvarme.', 'water'),
('Old', 'Salmos', 31, 3, 'Porque tú eres mi roca y mi castillo; Y por tu nombre me guiarás, y me encaminarás.', 'water'),
('Old', 'Salmos', 31, 4, 'Me sacarás de la red que han escondido para mí; Porque tú eres mi fortaleza.', 'water'),
('Old', 'Salmos', 31, 5, 'En tu mano encomiendo mi espíritu: Tú me has redimido, oh Jehová, Dios de verdad.', 'water'),
('Old', 'Salmos', 31, 6, 'Aborrecí á los que esperan en vanidades ilusorias; Mas yo en Jehová he esperado.', 'water'),
('Old', 'Salmos', 31, 7, 'Me gozaré y alegraré en tu misericordia; Porque has visto mi aflicción; Has conocido mi alma en las angustias:', 'water'),
('Old', 'Salmos', 31, 8, 'Y no me encerraste en mano del enemigo; Hiciste estar mis pies en anchura.', 'water'),
('Old', 'Salmos', 31, 9, 'Ten misericordia de mí, oh Jehová, que estoy en angustia: Hanse consumido de pesar mis ojos, mi alma, y mis entrañas.', 'water'),
('Old', 'Salmos', 31, 10, 'Porque mi vida se va gastando de dolor, y mis años de suspirar: Hase enflaquecido mi fuerza á causa de mi iniquidad, y mis huesos se han consumido.', 'water'),
('Old', 'Salmos', 31, 11, 'De todos mis enemigos he sido oprobio, Y de mis vecinos en gran manera, y horror á mis conocidos: Los que me veían fuera, huían de mí.', 'water'),
('Old', 'Salmos', 31, 12, 'He sido olvidado de su corazón como un muerto: He venido á ser como un vaso perdido.', 'water'),
('Old', 'Salmos', 31, 13, 'Porque he oído afrenta de muchos; Miedo por todas partes, Cuando consultaban juntos contra mí, E ideaban quitarme la vida.', 'water'),
('Old', 'Salmos', 31, 14, 'Mas yo en ti confié, oh Jehová: Yo dije: Dios mío eres tú.', 'water'),
('Old', 'Salmos', 31, 15, 'En tu mano están mis tiempos: Líbrame de la mano de mis enemigos, y de mis perseguidores.', 'water'),
('Old', 'Salmos', 31, 16, 'Haz resplandecer tu rostro sobre tu siervo: Sálvame por tu misericordia.', 'water'),
('Old', 'Salmos', 31, 17, 'No sea yo confundido, oh Jehová, ya que te he invocado; Sean corridos los impíos, estén mudos en el profundo.', 'water'),
('Old', 'Salmos', 31, 18, 'Enmudezcan los labios mentirosos, Que hablan contra el justo cosas duras, Con soberbia y menosprecio.', 'water'),
('Old', 'Salmos', 31, 19, '¡Cuán grande es tu bien, que has guardado para los que te temen, Que has obrado para los que esperan en ti, delante de los hijos de los hombres!', 'water'),
('Old', 'Salmos', 31, 20, 'Los esconderás en el secreto de tu rostro de las arrogancias del hombre: Los pondrás en un tabernáculo á cubierto de contención de lenguas.', 'water'),
('Old', 'Salmos', 31, 21, 'Bendito Jehová, Porque ha hecho maravillosa su misericordia para conmigo en ciudad fuerte.', 'water'),
('Old', 'Salmos', 31, 22, 'Y decía yo en mi premura: Cortado soy de delante de tus ojos: Tú empero oíste la voz de mis ruegos, cuando á ti clamaba.', 'water'),
('Old', 'Salmos', 31, 23, 'Amad á Jehová todos vosotros sus santos: A los fieles guarda Jehová, Y paga abundantemente al que obra con soberbia.', 'water'),
('Old', 'Salmos', 31, 24, 'Esforzaos todos vosotros los que esperáis en Jehová, Y tome vuestro corazón aliento.', 'water'),
('Old', 'Salmos', 32, 1, 'BIENAVENTURADO aquel cuyas iniquidades son perdonadas, y borrados sus pecados.', 'water'),
('Old', 'Salmos', 32, 2, 'Bienaventurado el hombre á quien no imputa Jehová la iniquidad, Y en cuyo espíritu no hay superchería.', 'water'),
('Old', 'Salmos', 32, 3, 'Mientras callé, envejeciéronse mis huesos En mi gemir todo el día.', 'water'),
('Old', 'Salmos', 32, 4, 'Porque de día y de noche se agravó sobre mí tu mano; Volvióse mi verdor en sequedades de estío. (Selah.)', 'water'),
('Old', 'Salmos', 32, 5, 'Mi pecado te declaré, y no encubrí mi iniquidad. Confesaré, dije, contra mí mis rebeliones á Jehová; Y tú perdonaste la maldad de mi pecado. (Selah.)', 'water'),
('Old', 'Salmos', 32, 6, 'Por esto orará á ti todo santo en el tiempo de poder hallarte: Ciertamente en la inundación de muchas aguas no llegarán éstas á él.', 'water'),
('Old', 'Salmos', 32, 7, 'Tú eres mi refugio; me guardarás de angustia; Con cánticos de liberación me rodearás. (Selah.)', 'water'),
('Old', 'Salmos', 32, 8, 'Te haré entender, y te enseñaré el camino en que debes andar: Sobre ti fijaré mis ojos.', 'water'),
('Old', 'Salmos', 32, 9, 'No seáis como el caballo, ó como el mulo, sin entendimiento: Con cabestro y con freno su boca ha de ser reprimida, Para que no lleguen á ti.', 'water'),
('Old', 'Salmos', 32, 10, 'Muchos dolores para el impío; Mas el que espera en Jehová, lo cercará misericordia.', 'water'),
('Old', 'Salmos', 32, 11, 'Alegraos en Jehová, y gozaos, justos: Y cantad todos vosotros los rectos de corazón.', 'water'),
('Old', 'Salmos', 33, 1, 'ALEGRAOS, justos, en Jehová: A los rectos es hermosa la alabanza.', 'water'),
('Old', 'Salmos', 33, 2, 'Celebrad á Jehová con arpa: Cantadle con salterio y decacordio.', 'water'),
('Old', 'Salmos', 33, 3, 'Cantadle canción nueva: Hacedlo bien tañendo con júbilo.', 'water'),
('Old', 'Salmos', 33, 4, 'Porque recta es la palabra de Jehová, Y toda su obra con verdad hecha.', 'water'),
('Old', 'Salmos', 33, 5, 'El ama justicia y juicio: De la misericordia de Jehová está llena la tierra.', 'water'),
('Old', 'Salmos', 33, 6, 'Por la palabra de Jehová fueron hechos los cielos, Y todo el ejército de ellos por el espíritu de su boca.', 'water'),
('Old', 'Salmos', 33, 7, 'El junta como en un montón las aguas de la mar: El pone en depósitos los abismos.', 'water'),
('Old', 'Salmos', 33, 8, 'Tema á Jehová toda la tierra: Teman de él todos los habitadores del mundo.', 'water'),
('Old', 'Salmos', 33, 9, 'Porque él dijo, y fué hecho; El mandó, y existió.', 'water'),
('Old', 'Salmos', 33, 10, 'Jehová hace nulo el consejo de las gentes, Y frustra las maquinaciones de los pueblos.', 'water'),
('Old', 'Salmos', 33, 11, 'El consejo de Jehová permanecerá para siempre; Los pensamientos de su corazón por todas las generaciones.', 'water'),
('Old', 'Salmos', 33, 12, 'Bienaventurada la gente de que Jehová es su Dios; El pueblo á quien escogió por heredad para sí.', 'water'),
('Old', 'Salmos', 33, 13, 'Desde los cielos miró Jehová; Vió á todos los hijos de los hombres:', 'water'),
('Old', 'Salmos', 33, 14, 'Desde la morada de su asiento miró Sobre todos los moradores de la tierra.', 'water'),
('Old', 'Salmos', 33, 15, 'El formó el corazón de todos ellos; El considera todas sus obras.', 'water'),
('Old', 'Salmos', 33, 16, 'El rey no es salvo con la multitud del ejército: No escapa el valiente por la mucha fuerza.', 'water'),
('Old', 'Salmos', 33, 17, 'Vanidad es el caballo para salvarse: Por la grandeza de su fuerza no librará.', 'water'),
('Old', 'Salmos', 33, 18, 'He aquí, el ojo de Jehová sobre los que le temen, Sobre los que esperan en su misericordia;', 'water'),
('Old', 'Salmos', 33, 19, 'Para librar sus almas de la muerte, Y para darles vida en el hambre.', 'water'),
('Old', 'Salmos', 33, 20, 'Nuestra alma esperó á Jehová; Nuestra ayuda y nuestro escudo es él.', 'water'),
('Old', 'Salmos', 33, 21, 'Por tanto en él se alegrará nuestro corazón, Porque en su santo nombre hemos confiado.', 'water'),
('Old', 'Salmos', 33, 22, 'Sea tu misericordia, oh Jehová, sobre nosotros, Como esperamos en ti.', 'water'),
('Old', 'Salmos', 34, 1, 'BENDECIRÉ á Jehová en todo tiempo; Su alabanza será siempre en mi boca.', 'water'),
('Old', 'Salmos', 34, 2, 'En Jehová se gloriará mi alma: Oiránlo los mansos, y se alegrarán.', 'water'),
('Old', 'Salmos', 34, 3, 'Engrandeced á Jehová conmigo, Y ensalcemos su nombre á una.', 'water'),
('Old', 'Salmos', 34, 4, 'Busqué á Jehová, y él me oyó, Y libróme de todos mis temores.', 'water'),
('Old', 'Salmos', 34, 5, 'A él miraron y fueron alumbrados: Y sus rostros no se avergonzaron.', 'water'),
('Old', 'Salmos', 34, 6, 'Este pobre clamó, y oyóle Jehová, Y librólo de todas sus angustias.', 'water'),
('Old', 'Salmos', 34, 7, 'El ángel de Jehová acampa en derredor de los que le temen, Y los defiende.', 'water'),
('Old', 'Salmos', 34, 8, 'Gustad, y ved que es bueno Jehová: Dichoso el hombre que confiará en él.', 'water'),
('Old', 'Salmos', 34, 9, 'Temed á Jehová, vosotros sus santos; Porque no hay falta para los que le temen.', 'water'),
('Old', 'Salmos', 34, 10, 'Los leoncillos necesitaron, y tuvieron hambre; Pero los que buscan á Jehová, no tendrán falta de ningún bien.', 'water'),
('Old', 'Salmos', 34, 11, 'Venid, hijos, oidme; El temor de Jehová os enseñaré.', 'water'),
('Old', 'Salmos', 34, 12, '¿Quién es el hombre que desea vida, Que codicia días para ver bien?', 'water'),
('Old', 'Salmos', 34, 13, 'Guarda tu lengua de mal, Y tus labios de hablar engaño.', 'water'),
('Old', 'Salmos', 34, 14, 'Apártate del mal, y haz el bien; Busca la paz, y síguela.', 'water'),
('Old', 'Salmos', 34, 15, 'Los ojos de Jehová están sobre los justos, Y atentos sus oídos al clamor de ellos.', 'water'),
('Old', 'Salmos', 34, 16, 'La ira de Jehová contra los que mal hacen, Para cortar de la tierra la memoria de ellos.', 'water'),
('Old', 'Salmos', 34, 17, 'Clamaron los justos, y Jehová oyó, Y librólos de todas sus angustias.', 'water'),
('Old', 'Salmos', 34, 18, 'Cercano está Jehová á los quebrantados de corazón; Y salvará á los contritos de espíritu.', 'water'),
('Old', 'Salmos', 34, 19, 'Muchos son los males del justo; Mas de todos ellos lo librará Jehová.', 'water'),
('Old', 'Salmos', 34, 20, 'El guarda todos sus huesos; Ni uno de ellos será quebrantado.', 'water'),
('Old', 'Salmos', 34, 21, 'Matará al malo la maldad; Y los que aborrecen al justo serán asolados.', 'water'),
('Old', 'Salmos', 34, 22, 'Jehová redime el alma de sus siervos; Y no serán asolados cuantos en él confían.', 'water'),
('Old', 'Salmos', 35, 1, 'DISPUTA, oh Jehová, con los que contra mí contienden; Pelea con los que me combaten.', 'water'),
('Old', 'Salmos', 35, 2, 'Echa mano al escudo y al pavés, Y levántate en mi ayuda.', 'water'),
('Old', 'Salmos', 35, 3, 'Y saca la lanza, cierra contra mis perseguidores; Di á mi alma: Yo soy tu salud.', 'water'),
('Old', 'Salmos', 35, 4, 'Avergüéncense y confúndanse los que buscan mi alma: Vuelvan atrás, y sean avergonzados los que mi mal intentan.', 'water'),
('Old', 'Salmos', 35, 5, 'Sean como el tamo delante del viento; Y el ángel de Jehová los acose.', 'water'),
('Old', 'Salmos', 35, 6, 'Sea su camino oscuridad y resbaladeros; Y el ángel de Jehová los persiga.', 'water'),
('Old', 'Salmos', 35, 7, 'Porque sin causa escondieron para mí su red en un hoyo; Sin causa hicieron hoyo para mi alma.', 'water'),
('Old', 'Salmos', 35, 8, 'Véngale el quebrantamiento que no sepa, Y su red que escondió lo prenda: Con quebrantamiento en ella caiga.', 'water'),
('Old', 'Salmos', 35, 9, 'Y gócese mi alma en Jehová; Y alégrese en su salud.', 'water'),
('Old', 'Salmos', 35, 10, 'Todos mis huesos dirán: Jehová, ¿quién como tú, Que libras al afligido del más fuerte que él, Y al pobre y menesteroso del que le despoja?', 'water'),
('Old', 'Salmos', 35, 11, 'Levantáronse testigos falsos; Demandáronme lo que no sabía;', 'water'),
('Old', 'Salmos', 35, 12, 'Volviéronme mal por bien, Para abatir á mi alma.', 'water'),
('Old', 'Salmos', 35, 13, 'Mas yo, cuando ellos enfermaron, me vestí de saco; Afligí con ayuno mi alma, Y mi oración se revolvía en mi seno.', 'water'),
('Old', 'Salmos', 35, 14, 'Como por mi compañero, como por mi hermano andaba; Como el que trae luto por madre, enlutado me humillaba.', 'water'),
('Old', 'Salmos', 35, 15, 'Pero ellos se alegraron en mi adversidad, y se juntaron; Juntáronse contra mí gentes despreciables, y yo no lo entendía: Despedazábanme, y no cesaban;', 'water'),
('Old', 'Salmos', 35, 16, 'Con los lisonjeros escarnecedores truhanes, Crujiendo sobre mí sus dientes.', 'water'),
('Old', 'Salmos', 35, 17, 'Señor, ¿hasta cuándo verás esto? Recobra mi alma de sus quebrantamientos, mi única de los leones.', 'water'),
('Old', 'Salmos', 35, 18, 'Te confesaré en grande congregación; Te alabaré entre numeroso pueblo.', 'water'),
('Old', 'Salmos', 35, 19, 'No se alegren de mí mis enemigos injustos: Ni los que me aborrecen sin causa hagan del ojo.', 'water'),
('Old', 'Salmos', 35, 20, 'Porque no hablan paz; Y contra los mansos de la tierra piensan palabras engañosas.', 'water'),
('Old', 'Salmos', 35, 21, 'Y ensancharon sobre mí su boca; Dijeron: ¡Ea, ea, nuestros ojos lo han visto!', 'water'),
('Old', 'Salmos', 35, 22, 'Tú lo has visto, oh Jehová; no calles: Señor, de mí no te alejes.', 'water'),
('Old', 'Salmos', 35, 23, 'Muévete y despierta para mi juicio, Para mi causa, Dios mío y Señor mío.', 'water'),
('Old', 'Salmos', 35, 24, 'Júzgame conforme á tu justicia, Jehová Dios mío; Y no se alegren de mí.', 'water'),
('Old', 'Salmos', 35, 25, 'No digan en su corazón: ¡Ea, alma nuestra! No digan: ¡Hémoslo devorado!', 'water'),
('Old', 'Salmos', 35, 26, 'Avergüencense, y sean confundidos á una los que de mi mal se alegran: Vístanse de vergüenza y de confusión los que se engrandecen contra mí.', 'water'),
('Old', 'Salmos', 35, 27, 'Canten y alégrense los que están á favor de mi justa causa, Y digan siempre: Sea ensalzado Jehová, Que ama la paz de su siervo.', 'water'),
('Old', 'Salmos', 35, 28, 'Y mi lengua hablará de tu justicia, Y de tu loor todo el día.', 'water'),
('Old', 'Salmos', 36, 1, 'LA iniquidad del impío me dice al corazón: No hay temor de Dios delante de sus ojos.', 'water'),
('Old', 'Salmos', 36, 2, 'Lisonjéase, por tanto, en sus propios ojos, Hasta que su iniquidad sea hallada aborrecible.', 'water'),
('Old', 'Salmos', 36, 3, 'Las palabras de su boca son iniquidad y fraude; No quiso entender para bien hacer.', 'water'),
('Old', 'Salmos', 36, 4, 'Iniquidad piensa sobre su cama; Está en camino no bueno, El mal no aborrece.', 'water'),
('Old', 'Salmos', 36, 5, 'Jehová, hasta los cielos es tu misericordia; Tu verdad hasta las nubes.', 'water'),
('Old', 'Salmos', 36, 6, 'Tu justicia como los montes de Dios, Tus juicios abismo grande: Oh Jehová, al hombre y al animal conservas.', 'water'),
('Old', 'Salmos', 36, 7, '¡Cuán ilustre, oh Dios, es tu misericordia! Por eso los hijos de los hombres se amparan bajo la sombra de tus alas.', 'water'),
('Old', 'Salmos', 36, 8, 'Embriagarse han de la grosura de tu casa; Y tú los abrevarás del torrente de tus delicias.', 'water'),
('Old', 'Salmos', 36, 9, 'Porque contigo está el manantial de la vida: En tu luz veremos la luz.', 'water'),
('Old', 'Salmos', 36, 10, 'Extiende tu misericordia á los que te conocen, Y tu justicia á los rectos de corazón.', 'water'),
('Old', 'Salmos', 36, 11, 'No venga contra mí pie de soberbia; Y mano de impíos no me mueva.', 'water'),
('Old', 'Salmos', 36, 12, 'Allí cayeron los obradores de iniquidad; Fueron rempujados, y no pudieron levantarse.', 'water'),
('Old', 'Salmos', 37, 1, 'NO te impacientes á causa de los malignos, Ni tengas envidia de los que hacen iniquidad.', 'water'),
('Old', 'Salmos', 37, 2, 'Porque como hierba serán presto cortados, Y decaerán como verdor de renuevo.', 'water'),
('Old', 'Salmos', 37, 3, 'Espera en Jehová, y haz bien; Vivirás en la tierra, y en verdad serás alimentado.', 'water'),
('Old', 'Salmos', 37, 4, 'Pon asimismo tu delicia en Jehová, Y él te dará las peticiones de tu corazón.', 'water'),
('Old', 'Salmos', 37, 5, 'Encomienda á Jehová tu camino, Y espera en él; y él hará.', 'water'),
('Old', 'Salmos', 37, 6, 'Y exhibirá tu justicia como la luz, Y tus derechos como el medio día.', 'water'),
('Old', 'Salmos', 37, 7, 'Calla á Jehová, y espera en él: No te alteres con motivo del que prospera en su camino, Por el hombre que hace maldades.', 'water'),
('Old', 'Salmos', 37, 8, 'Déjate de la ira, y depón el enojo: No te excites en manera alguna á hacer lo malo.', 'water'),
('Old', 'Salmos', 37, 9, 'Porque los malignos serán talados, Mas los que esperan en Jehová, ellos heredarán la tierra.', 'water'),
('Old', 'Salmos', 37, 10, 'Pues de aquí á poco no será el malo: Y contemplarás sobre su lugar, y no parecerá.', 'water'),
('Old', 'Salmos', 37, 11, 'Pero los mansos heredarán la tierra, Y se recrearán con abundancia de paz.', 'water'),
('Old', 'Salmos', 37, 12, 'Maquina el impío contra el justo, Y cruje sobre él sus dientes.', 'water'),
('Old', 'Salmos', 37, 13, 'El Señor se reirá de él; Porque ve que viene su día.', 'water'),
('Old', 'Salmos', 37, 14, 'Los impíos desenvainaron espada, y entesaron su arco, Para derribar al pobre y al menesteroso, Para matar á los de recto proceder.', 'water'),
('Old', 'Salmos', 37, 15, 'La espada de ellos entrará en su mismo corazón, Y su arco será quebrado.', 'water'),
('Old', 'Salmos', 37, 16, 'Mejor es lo poco del justo, Que las riquezas de muchos pecadores.', 'water'),
('Old', 'Salmos', 37, 17, 'Porque los brazos de los impíos serán quebrados: Mas el que sostiene á los justos es Jehová.', 'water'),
('Old', 'Salmos', 37, 18, 'Conoce Jehová los días de los perfectos: Y la heredad de ellos será para siempre.', 'water'),
('Old', 'Salmos', 37, 19, 'No serán avergonzados en el mal tiempo; Y en los días de hambre serán hartos.', 'water'),
('Old', 'Salmos', 37, 20, 'Mas los impíos perecerán, Y los enemigos de Jehová como la grasa de los carneros Serán consumidos: se disiparán como humo.', 'water'),
('Old', 'Salmos', 37, 21, 'El impío toma prestado, y no paga; Mas el justo tiene misericordia, y da.', 'water'),
('Old', 'Salmos', 37, 22, 'Porque los benditos de él heredarán la tierra; Y los malditos de él serán talados.', 'water'),
('Old', 'Salmos', 37, 23, 'Por Jehová son ordenados los pasos del hombre, Y aprueba su camino.', 'water'),
('Old', 'Salmos', 37, 24, 'Cuando cayere, no quedará postrado; Porque Jehová sostiene su mano.', 'water'),
('Old', 'Salmos', 37, 25, 'Mozo fuí, y he envejecido, Y no he visto justo desamparado, Ni su simiente que mendigue pan.', 'water'),
('Old', 'Salmos', 37, 26, 'En todo tiempo tiene misericordia, y presta; Y su simiente es para bendición.', 'water'),
('Old', 'Salmos', 37, 27, 'Apártate del mal, y haz el bien, Y vivirás para siempre.', 'water'),
('Old', 'Salmos', 37, 28, 'Porque Jehová ama la rectitud, Y no desampara sus santos: Mas la simiente de los impíos será extirpada.', 'water'),
('Old', 'Salmos', 37, 29, 'Los justos heredarán la tierra, Y vivirán para siempre sobre ella.', 'water'),
('Old', 'Salmos', 37, 30, 'La boca del justo hablara sabiduría; Y su lengua proferirá juicio.', 'water'),
('Old', 'Salmos', 37, 31, 'La ley de su Dios está en su corazón; Por tanto sus pasos no vacilarán.', 'water'),
('Old', 'Salmos', 37, 32, 'Acecha el impío al justo, Y procura matarlo.', 'water'),
('Old', 'Salmos', 37, 33, 'Jehová no lo dejará en sus manos, Ni lo condenará cuando le juzgaren.', 'water'),
('Old', 'Salmos', 37, 34, 'Espera en Jehová, y guarda su camino, Y él te ensalzará para heredar la tierra: Cuando serán talados los pecadores, lo verás.', 'water'),
('Old', 'Salmos', 37, 35, 'Vi yo al impío sumamente ensalzado, Y que se extendía como un laurel verde.', 'water'),
('Old', 'Salmos', 37, 36, 'Empero pasóse, y he aquí no parece; Y busquélo, y no fué hallado.', 'water'),
('Old', 'Salmos', 37, 37, 'Considera al íntegro, y mira al justo: Que la postrimería de cada uno de ellos es paz.', 'water'),
('Old', 'Salmos', 37, 38, 'Mas los transgresores fueron todos á una destruídos: La postrimería de los impíos fué talada.', 'water'),
('Old', 'Salmos', 37, 39, 'Pero la salvación de los justos es de Jehová, Y él es su fortaleza en el tiempo de angustia.', 'water'),
('Old', 'Salmos', 37, 40, 'Y Jehová los ayudará, Y los librará: y libertarálos de los impíos, y los salvará, Por cuanto en él esperaron.', 'water'),
('Old', 'Salmos', 38, 1, 'JEHOVA, no me reprendas en tu furor, Ni me castigues en tu ira.', 'water'),
('Old', 'Salmos', 38, 2, 'Porque tus saetas descendieron á mí, Y sobre mí ha caído tu mano.', 'water'),
('Old', 'Salmos', 38, 3, 'No hay sanidad en mi carne á causa de tu ira; Ni hay paz en mis huesos á causa de mi pecado.', 'water'),
('Old', 'Salmos', 38, 4, 'Porque mis iniquidades han pasado mi cabeza: Como carga pesada se han agravado sobre mí.', 'water'),
('Old', 'Salmos', 38, 5, 'Pudriéronse, corrompiéronse mis llagas, A causa de mi locura.', 'water'),
('Old', 'Salmos', 38, 6, 'Estoy encorvado, estoy humillado en gran manera, Ando enlutado todo el día.', 'water'),
('Old', 'Salmos', 38, 7, 'Porque mis lomos están llenos de irritación, Y no hay sanidad en mi carne.', 'water'),
('Old', 'Salmos', 38, 8, 'Estoy debilitado y molido en gran manera; Bramo á causa de la conmoción de mi corazón.', 'water'),
('Old', 'Salmos', 38, 9, 'Señor, delante de ti están todos mis deseos; Y mi suspiro no te es oculto.', 'water'),
('Old', 'Salmos', 38, 10, 'Mi corazón está acongojado, hame dejado mi vigor; Y aun la misma luz de mis ojos no está conmigo.', 'water'),
('Old', 'Salmos', 38, 11, 'Mis amigos y mis compañeros se quitaron de delante de mi plaga; Y mis cercanos se pusieron lejos.', 'water'),
('Old', 'Salmos', 38, 12, 'Y los que buscaban mi alma armaron lazos; Y los que procuraban mi mal hablaban iniquidades, Y meditaban fraudes todo el día.', 'water'),
('Old', 'Salmos', 38, 13, 'Mas yo, como si fuera sordo no oía; Y estaba como un mudo, que no abre su boca.', 'water'),
('Old', 'Salmos', 38, 14, 'Fuí pues como un hombre que no oye, Y que en su boca no tiene reprensiones.', 'water'),
('Old', 'Salmos', 38, 15, 'Porque á ti, oh Jehová, esperé yo: Tú responderás, Jehová Dios mío.', 'water'),
('Old', 'Salmos', 38, 16, 'Porque dije: Que no se alegren de mí: Cuando mi pie resbalaba, sobre mí se engrandecían.', 'water'),
('Old', 'Salmos', 38, 17, 'Empero yo estoy á pique de claudicar, Y mi dolor está delante de mí continuamente.', 'water'),
('Old', 'Salmos', 38, 18, 'Por tanto denunciaré mi maldad; Congojaréme por mi pecado.', 'water'),
('Old', 'Salmos', 38, 19, 'Porque mis enemigos están vivos y fuertes: Y hanse aumentado los que me aborrecen sin causa:', 'water'),
('Old', 'Salmos', 38, 20, 'Y pagando mal por bien Me son contrarios, por seguir yo lo bueno.', 'water'),
('Old', 'Salmos', 38, 21, 'No me desampares, oh Jehová: Dios mío, no te alejes de mí.', 'water'),
('Old', 'Salmos', 38, 22, 'Apresúrate á ayudarme, Oh Señor, mi salud.', 'water'),
('Old', 'Salmos', 39, 1, 'YO DIJE: Atenderé á mis caminos, Para no pecar con mi lengua: Guardaré mi boca con freno, En tanto que el impío fuere contra mí.', 'water'),
('Old', 'Salmos', 39, 2, 'Enmudecí con silencio, calléme aun respecto de lo bueno: Y excitóse mi dolor.', 'water'),
('Old', 'Salmos', 39, 3, 'Enardecióse mi corazón dentro de mí; Encendióse fuego en mi meditación, Y así proferí con mi lengua:', 'water'),
('Old', 'Salmos', 39, 4, 'Hazme saber, Jehová, mi fin, Y cuánta sea la medida de mis días; Sepa yo cuánto tengo de ser del mundo.', 'water'),
('Old', 'Salmos', 39, 5, 'He aquí diste á mis días término corto, Y mi edad es como nada delante de ti: Ciertamente es completa vanidad todo hombre que vive. (Selah.)', 'water'),
('Old', 'Salmos', 39, 6, 'Ciertamente en tinieblas anda el hombre; Ciertamente en vano se inquieta: Junta, y no sabe quién lo allegará.', 'water'),
('Old', 'Salmos', 39, 7, 'Y ahora, Señor, ¿qué esperaré? Mi esperanza en ti está.', 'water'),
('Old', 'Salmos', 39, 8, 'Líbrame de todas mis rebeliones; No me pongas por escarnio del insensato.', 'water'),
('Old', 'Salmos', 39, 9, 'Enmudecí, no abrí mi boca; Porque tú lo hiciste.', 'water'),
('Old', 'Salmos', 39, 10, 'Quita de sobre mí tu plaga; De la guerra de tu mano soy consumido.', 'water'),
('Old', 'Salmos', 39, 11, 'Con castigos sobre el pecado corriges al hombre, Y haces consumirse como de polilla su grandeza: Ciertamente vanidad es todo hombre. (Selah.)', 'water'),
('Old', 'Salmos', 39, 12, 'Oye mi oración, oh Jehová, y escucha mi clamor: No calles á mis lágrimas; Porque peregrino soy para contigo, Y advenedizo, como todos mis padres.', 'water'),
('Old', 'Salmos', 39, 13, 'Déjame, y tomaré fuerzas, Antes que vaya y perezca.', 'water'),
('Old', 'Salmos', 40, 1, 'RESIGNADAMENTE esperé á Jehová, E inclinóse á mí, y oyó mi clamor.', 'water'),
('Old', 'Salmos', 40, 2, 'E hízome sacar de un lago de miseria, del lodo cenagoso; Y puso mis pies sobre peña, y enderezó mis pasos.', 'water'),
('Old', 'Salmos', 40, 3, 'Puso luego en mi boca canción nueva, alabanza á nuestro Dios. Verán esto muchos, y temerán, Y esperarán en Jehová.', 'water'),
('Old', 'Salmos', 40, 4, 'Bienaventurado el hombre que puso á Jehová por su confianza, Y no mira á los soberbios, ni á los que declinan á la mentira.', 'water'),
('Old', 'Salmos', 40, 5, 'Aumentado has tú, oh Jehová Dios mío, tus maravillas; Y tus pensamientos para con nosotros, No te los podremos contar: Si yo anunciare y hablare de ellos, No pueden ser enarrados.', 'water'),
('Old', 'Salmos', 40, 6, 'Sacrificio y presente no te agrada; Has abierto mis oídos; Holocausto y expiación no has demandado.', 'water'),
('Old', 'Salmos', 40, 7, 'Entonces dije: He aquí, vengo; En el envoltorio del libro está escrito de mí:', 'water'),
('Old', 'Salmos', 40, 8, 'El hacer tu voluntad, Dios mío, hame agradado; Y tu ley está en medio de mis entrañas.', 'water'),
('Old', 'Salmos', 40, 9, 'Anunciado he justicia en grande congregación: He aquí no detuve mis labios, Jehová, tú lo sabes.', 'water'),
('Old', 'Salmos', 40, 10, 'No encubrí tu justicia dentro de mi corazón: Tu verdad y tu salvación he dicho: No oculté tu misericordia y tu verdad en grande concurso.', 'water'),
('Old', 'Salmos', 40, 11, 'Tú, Jehová, no apartes de mí tus misericordias: Tu misericordia y tu verdad me guarden siempre.', 'water'),
('Old', 'Salmos', 40, 12, 'Porque me han cercado males hasta no haber cuento: Hanme comprendido mis maldades, y no puedo levantar la vista: Hanse aumentado más que los cabellos de mi cabeza, y mi corazón me falta.', 'water'),
('Old', 'Salmos', 40, 13, 'Quieras, oh Jehová, librarme; Jehová, apresúrate á socorrerme.', 'water'),
('Old', 'Salmos', 40, 14, 'Sean avergonzados y confusos á una Los que buscan mi vida para cortarla: Vuelvan atrás y avergüéncense Los que mi mal desean.', 'water'),
('Old', 'Salmos', 40, 15, 'Sean asolados en pago de su afrenta Los que me dicen: ¡Ea, ea!', 'water'),
('Old', 'Salmos', 40, 16, 'Gócense y alégrense en ti todos los que te buscan; Y digan siempre los que aman tu salud: Jehová sea ensalzado.', 'water'),
('Old', 'Salmos', 40, 17, 'Aunque afligido yo y necesitado, Jehová pensará de mí: Mi ayuda y mi libertador eres tú; Dios mío, no te tardes.', 'water'),
('Old', 'Salmos', 41, 1, 'BIENAVENTURADO el que piensa en el pobre: En el día malo lo librará Jehová.', 'water'),
('Old', 'Salmos', 41, 2, 'Jehová lo guardé, y le dé vida: sea bienaventurado en la tierra, Y no lo entregues á la voluntad de sus enemigos.', 'water'),
('Old', 'Salmos', 41, 3, 'Jehová lo sustentará sobre el lecho del dolor: Mullirás toda su cama en su enfermedad.', 'water'),
('Old', 'Salmos', 41, 4, 'Yo dije: Jehová, ten misericordia de mí; Sana mi alma, porque contra ti he pecado.', 'water'),
('Old', 'Salmos', 41, 5, 'Mis enemigos dicen mal de mí preguntando: ¿Cuándo morirá, y perecerá su nombre?', 'water'),
('Old', 'Salmos', 41, 6, 'Y si venía á ver me, hablaba mentira: Su corazón se amontonaba iniquidad; Y salido fuera, hablába la.', 'water'),
('Old', 'Salmos', 41, 7, 'Reunidos murmuraban contra mí todos los que me aborrecían: Contra mí pensaban mal, diciendo de mí:', 'water'),
('Old', 'Salmos', 41, 8, 'Cosa pestilencial de él se ha apoderado; Y el que cayó en cama, no volverá á levantarse.', 'water'),
('Old', 'Salmos', 41, 9, 'Aun el hombre de mi paz, en quien yo confiaba, el que de mi pan comía, Alzó contra mí el calcañar.', 'water'),
('Old', 'Salmos', 41, 10, 'Mas tú, Jehová, ten misericordia de mí, y hazme levantar, Y daréles el pago.', 'water'),
('Old', 'Salmos', 41, 11, 'En esto habré conocido que te he agradado, Que mi enemigo no se holgará de mí.', 'water'),
('Old', 'Salmos', 41, 12, 'En cuanto á mí, en mi integridad me has sustentado, Y me has hecho estar delante de ti para siempre.', 'water'),
('Old', 'Salmos', 41, 13, 'Bendito sea Jehová, el Dios de Israel, Por siglos de siglos. Amén y Amén.', 'water'),
('Old', 'Salmos', 42, 1, 'COMO el ciervo brama por las corrientes de las aguas, Así clama por ti, oh Dios, el alma mía.', 'water'),
('Old', 'Salmos', 42, 2, 'Mi alma tiene sed de Dios, del Dios vivo: ¡Cuándo vendré, y pareceré delante de Dios!', 'water'),
('Old', 'Salmos', 42, 3, 'Fueron mis lágrimas mi pan de día y de noche, Mientras me dicen todos los días: ¿Dónde está tu Dios?', 'water'),
('Old', 'Salmos', 42, 4, 'Acordaréme de estas cosas, y derramaré sobre mí mi alma: Cuando pasaré en el número, iré con ellos hasta la casa de Dios, Con voz de alegría y de alabanza, haciendo fiesta la multitud.', 'water'),
('Old', 'Salmos', 42, 5, '¿Por qué te abates, oh alma mía, Y te conturbas en mí? Espera á Dios; porque aun le tengo de alabar Por las saludes de su presencia.', 'water'),
('Old', 'Salmos', 42, 6, 'Dios mío, mi alma está en mí abatida: Acordaréme por tanto de ti desde tierra del Jordán, Y de los Hermonitas, desde el monte de Mizhar.', 'water'),
('Old', 'Salmos', 42, 7, 'Un abismo llama á otro á la voz de tus canales: Todas tus ondas y tus olas han pasado sobre mí.', 'water'),
('Old', 'Salmos', 42, 8, 'De día mandará Jehová su misericordia, Y de noche su canción será conmigo, Y oración al Dios de mi vida.', 'water'),
('Old', 'Salmos', 42, 9, 'Diré á Dios: Roca mía, ¿por qué te has olvidado de mí? ¿Por qué andaré yo enlutado por la opresión del enemigo?', 'water'),
('Old', 'Salmos', 42, 10, 'Mientras se están quebrantando mis huesos, mis enemigos me afrentan, Diciéndome cada día: ¿Dónde está tu Dios?', 'water'),
('Old', 'Salmos', 42, 11, '¿Por qué te abates, oh alma mía, Y por qué te conturbas en mí? Espera á Dios; porque aun le tengo de alabar; Es él salvamento delante de mí, y el Dios mío.', 'water'),
('Old', 'Salmos', 43, 1, 'JUZGAME, oh Dios, y aboga mi causa: Líbrame de gente impía, del hombre de engaño é iniquidad.', 'water'),
('Old', 'Salmos', 43, 2, 'Pues que tú eres el Dios de mi fortaleza, ¿por qué me has desechado? ¿Por qué andaré enlutado por la opresión del enemigo?', 'water'),
('Old', 'Salmos', 43, 3, 'Envía tu luz y tu verdad: éstas me guiarán, Me conducirán al monte de tu santidad, Y á tus tabernáculos.', 'water'),
('Old', 'Salmos', 43, 4, 'Y entraré al altar de Dios, Al Dios alegría de mi gozo; Y alabaréte con arpa, oh Dios, Dios mío.', 'water'),
('Old', 'Salmos', 43, 5, '¿Por qué te abates, oh alma mía, Y por qué te conturbes en mí? Espera á Dios; porque aun le tengo de alabar; Es él salvamento delante de mí, y el Dios mío.', 'water'),
('Old', 'Salmos', 44, 1, 'OH Dios, con nuestros oídos hemos oído, nuestros padres nos han contado, La obra que hiciste en sus días, en los tiempos antiguos.', 'water'),
('Old', 'Salmos', 44, 2, 'Tú con tu mano echaste las gentes, y los plantaste á ellos; Afligiste los pueblos, y los arrojaste.', 'water'),
('Old', 'Salmos', 44, 3, 'Porque no se apoderaron de la tierra por su espada, Ni su brazo los libró; Sino tu diestra, y tu brazo, y la luz de tu rostro, Porque te complaciste en ellos.', 'water'),
('Old', 'Salmos', 44, 4, 'Tú, oh Dios, eres mi rey: Manda saludes á Jacob.', 'water'),
('Old', 'Salmos', 44, 5, 'Por medio de ti sacudiremos á nuestros enemigos: En tu nombre atropellaremos á nuestros adversarios.', 'water'),
('Old', 'Salmos', 44, 6, 'Porque no confiaré en mi arco, Ni mi espada me salvará.', 'water'),
('Old', 'Salmos', 44, 7, 'Pues tú nos has guardado de nuestros enemigos, Y has avergonzado á los que nos aborrecían.', 'water'),
('Old', 'Salmos', 44, 8, 'En Dios nos gloriaremos todo tiempo, Y para siempre loaremos tu nombre. (Selah.)', 'water'),
('Old', 'Salmos', 44, 9, 'Empero nos has desechado, y nos has hecho avergonzar; Y no sales en nuestros ejércitos.', 'water'),
('Old', 'Salmos', 44, 10, 'Nos hiciste retroceder del enemigo, Y saqueáron nos para sí los que nos aborrecían.', 'water'),
('Old', 'Salmos', 44, 11, 'Pusístenos como á ovejas para comida, Y esparcístenos entre las gentes.', 'water'),
('Old', 'Salmos', 44, 12, 'Has vendido tu pueblo de balde, Y no pujaste en sus precios.', 'water'),
('Old', 'Salmos', 44, 13, 'Pusístenos por vergüenza á nuestros vecinos, Por escarnio y por burla á los que nos rodean.', 'water'),
('Old', 'Salmos', 44, 14, 'Pusístenos por proverbio entre las gentes, Por movimiento de cabeza en los pueblos.', 'water'),
('Old', 'Salmos', 44, 15, 'Cada día mi vergüenza está delante de mí, Y cúbreme la confusión de mi rostro,', 'water'),
('Old', 'Salmos', 44, 16, 'Por la voz del que me vitupera y deshonra, Por razón del enemigo y del que se venga.', 'water'),
('Old', 'Salmos', 44, 17, 'Todo esto nos ha venido, y no nos hemos olvidado de ti; Y no hemos faltado á tu pacto.', 'water'),
('Old', 'Salmos', 44, 18, 'No se ha vuelto atrás nuestro corazón, Ni tampoco se han apartado nuestros pasos de tus caminos.', 'water'),
('Old', 'Salmos', 44, 19, 'Cuando nos quebrantaste en el lugar de los dragones, Y nos cubriste con sombra de muerte,', 'water'),
('Old', 'Salmos', 44, 20, 'Si nos hubiésemos olvidado del nombre de nuestro Dios, O alzado nuestras manos á dios ajeno,', 'water'),
('Old', 'Salmos', 44, 21, '¿No demandaría Dios esto? Porque él conoce los secretos del corazón.', 'water'),
('Old', 'Salmos', 44, 22, 'Empero por tu causa nos matan cada día; Somos tenidos como ovejas para el matadero.', 'water'),
('Old', 'Salmos', 44, 23, 'Despierta; ¿por qué duermes, Señor? Despierta, no te alejes para siempre.', 'water'),
('Old', 'Salmos', 44, 24, '¿Por qué escondes tu rostro, Y te olvidas de nuestra aflicción, y de la opresión nuestra?', 'water'),
('Old', 'Salmos', 44, 25, 'Porque nuestra alma está agobiada hasta el polvo: Nuestro vientre está pegado con la tierra.', 'water'),
('Old', 'Salmos', 44, 26, 'Levántate para ayudarnos, Y redímenos por tu misericordia.', 'water'),
('Old', 'Salmos', 45, 1, 'REBOSA mi corazón palabra buena: Refiero yo al Rey mis obras: Mi lengua es pluma de escribiente muy ligero.', 'water'),
('Old', 'Salmos', 45, 2, 'Haste hermoseado más que los hijos de los hombres; La gracia se derramó en tus labios: Por tanto Dios te ha bendecido para siempre.', 'water'),
('Old', 'Salmos', 45, 3, 'Cíñete tu espada sobre el muslo, oh valiente, Con tu gloria y con tu majestad.', 'water'),
('Old', 'Salmos', 45, 4, 'Y en tu gloria sé prosperado: Cabalga sobre palabra de verdad, y de humildad, y de justicia; Y tu diestra te enseñará cosas terribles.', 'water'),
('Old', 'Salmos', 45, 5, 'Tus saetas agudas Con que caerán pueblos debajo de ti, Penetrarán en el corazón de los enemigos del Rey.', 'water'),
('Old', 'Salmos', 45, 6, 'Tu trono, oh Dios, eterno y para siempre: Vara de justicia la vara de tu reino.', 'water'),
('Old', 'Salmos', 45, 7, 'Amaste la justicia y aborreciste la maldad: Por tanto te ungió Dios, el Dios tuyo, Con óleo de gozo sobre tus compañeros.', 'water'),
('Old', 'Salmos', 45, 8, 'Mirra, áloe, y casia exhalan todos tus vestidos: En estancias de marfil te han recreado.', 'water'),
('Old', 'Salmos', 45, 9, 'Hijas de reyes entre tus ilustres: Está la reina á tu diestra con oro de Ophir.', 'water'),
('Old', 'Salmos', 45, 10, 'Oye, hija, y mira, é inclina tu oído; Y olvida tu pueblo, y la casa de tu padre;', 'water'),
('Old', 'Salmos', 45, 11, 'Y deseará el rey tu hermosura: E inclínate á él, porque él es tu Señor.', 'water'),
('Old', 'Salmos', 45, 12, 'Y las hijas de Tiro vendrán con presente; Implorarán tu favor los ricos del pueblo.', 'water'),
('Old', 'Salmos', 45, 13, 'Toda ilustre es de dentro la hija del rey: De brocado de oro es su vestido.', 'water'),
('Old', 'Salmos', 45, 14, 'Con vestidos bordados será llevada al rey; Vírgenes en pos de ella: Sus compañeras serán traídas á ti.', 'water'),
('Old', 'Salmos', 45, 15, 'Serán traídas con alegría y gozo: Entrarán en el palacio del rey.', 'water'),
('Old', 'Salmos', 45, 16, 'En lugar de tus padres serán tus hijos, A quienes harás príncipes en toda la tierra.', 'water'),
('Old', 'Salmos', 45, 17, 'Haré perpetua la memoria de tu nombre en todas las generaciones: Por lo cual te alabarán los pueblos eternamente y para siempre.', 'water'),
('Old', 'Salmos', 46, 1, 'DIOS es nuestro amparo y fortaleza, Nuestro pronto auxilio en las tribulaciones.', 'water'),
('Old', 'Salmos', 46, 2, 'Por tanto no temeremos aunque la tierra sea removida; Aunque se traspasen los montes al corazón de la mar.', 'water'),
('Old', 'Salmos', 46, 3, 'Bramarán, turbaránse sus aguas; Temblarán los montes á causa de su braveza. (Selah.)', 'water'),
('Old', 'Salmos', 46, 4, 'Del río sus conductos alegrarán la ciudad de Dios, El santuario de las tiendas del Altísimo.', 'water'),
('Old', 'Salmos', 46, 5, 'Dios está en medio de ella; no será conmovida: Dios la ayudará al clarear la mañana.', 'water'),
('Old', 'Salmos', 46, 6, 'Bramaron las gentes, titubearon los reinos; Dió él su voz, derritióse la tierra.', 'water'),
('Old', 'Salmos', 46, 7, 'Jehová de los ejércitos es con nosotros; Nuestro refugio es el Dios de Jacob. (Selah.)', 'water'),
('Old', 'Salmos', 46, 8, 'Venid, ved las obras de Jehová, Que ha puesto asolamientos en la tierra.', 'water'),
('Old', 'Salmos', 46, 9, 'Que hace cesar las guerras hasta los fines de la tierra: Que quiebra el arco, corta la lanza, Y quema los carros en el fuego.', 'water'),
('Old', 'Salmos', 46, 10, 'Estad quietos, y conoced que yo soy Dios: Ensalzado he de ser entre las gentes, ensalzado seré en la tierra.', 'water'),
('Old', 'Salmos', 46, 11, 'Jehová de los ejércitos es con nosotros; Nuestro refugio es el Dios de Jacob. (Selah.)', 'water'),
('Old', 'Salmos', 47, 1, 'PUEBLOS todos, batid las manos; Aclamad á Dios con voz de júbilo.', 'water'),
('Old', 'Salmos', 47, 2, 'Porque Jehová el Altísimo es terrible; Rey grande sobre toda la tierra.', 'water'),
('Old', 'Salmos', 47, 3, 'El sujetará á los pueblos debajo de nosotros, Y á las gentes debajo de nuestros pies.', 'water'),
('Old', 'Salmos', 47, 4, 'El nos elegirá nuestras heredades; La hermosura de Jacob, al cual amó. (Selah.)', 'water'),
('Old', 'Salmos', 47, 5, 'Subió Dios con júbilo, Jehová con sonido de trompeta.', 'water'),
('Old', 'Salmos', 47, 6, 'Cantad á Dios, cantad: Cantad á nuestro Rey, cantad.', 'water'),
('Old', 'Salmos', 47, 7, 'Porque Dios es el Rey de toda la tierra: Cantad con inteligencia.', 'water'),
('Old', 'Salmos', 47, 8, 'Reinó Dios sobre las gentes: Asentóse Dios sobre su santo trono.', 'water'),
('Old', 'Salmos', 47, 9, 'Los príncipes de los pueblos se juntaron Al pueblo del Dios de Abraham: Porque de Dios son los escudos de la tierra; El es muy ensalzado.', 'water'),
('Old', 'Salmos', 48, 1, 'GRANDE es Jehová y digno de ser en gran manera alabado, En la ciudad de nuestro Dios, en el monte de su santuario.', 'water'),
('Old', 'Salmos', 48, 2, 'Hermosa provincia, el gozo de toda la tierra Es el monte de Sión, á los lados del aquilón, La ciudad del gran Rey.', 'water'),
('Old', 'Salmos', 48, 3, 'Dios en sus palacios es conocido por refugio.', 'water'),
('Old', 'Salmos', 48, 4, 'Porque he aquí los reyes de la tierra se reunieron; Pasaron todos.', 'water'),
('Old', 'Salmos', 48, 5, 'Y viéndola ellos así, maravilláronse, Se turbaron, diéronse priesa á huir.', 'water'),
('Old', 'Salmos', 48, 6, 'Tomólos allí temblor; Dolor, como á mujer que pare.', 'water'),
('Old', 'Salmos', 48, 7, 'Con viento solano Quiebras tú las naves de Tharsis.', 'water'),
('Old', 'Salmos', 48, 8, 'Como lo oímos, así hemos visto En la ciudad de Jehová de los ejércitos, en la ciudad de nuestro Dios: Afirmarála Dios para siempre. (Selah.)', 'water'),
('Old', 'Salmos', 48, 9, 'Esperamos tu misericordia, oh Dios, En medio de tu templo.', 'water'),
('Old', 'Salmos', 48, 10, 'Conforme á tu nombre, oh Dios, Así es tu loor hasta los fines de la tierra: De justicia está llena tu diestra.', 'water'),
('Old', 'Salmos', 48, 11, 'Alegraráse el monte de Sión; Se gozarán las hijas de Judá Por tus juicios.', 'water'),
('Old', 'Salmos', 48, 12, 'Andad alrededor de Sión, y rodeadla: Contad sus torres.', 'water'),
('Old', 'Salmos', 48, 13, 'Poned vuestro corazón á su antemuro, Mirad sus palacios; Para que lo contéis á la generación venidera.', 'water'),
('Old', 'Salmos', 48, 14, 'Porque este Dios es Dios nuestro eternalmente y para siempre: El nos capitaneará hasta la muerte.', 'water'),
('Old', 'Salmos', 49, 1, 'OID esto, pueblos todos; Escuchad, habitadores todos del mundo:', 'water'),
('Old', 'Salmos', 49, 2, 'Así los plebeyos como los nobles, El rico y el pobre juntamente.', 'water'),
('Old', 'Salmos', 49, 3, 'Mi boca hablará sabiduría; Y el pensamiento de mi corazón inteligencia.', 'water'),
('Old', 'Salmos', 49, 4, 'Acomodaré á ejemplos mi oído: Declararé con el arpa mi enigma.', 'water'),
('Old', 'Salmos', 49, 5, '¿Por qué he de temer en los días de adversidad, Cuando la iniquidad de mis insidiadores me cercare?', 'water'),
('Old', 'Salmos', 49, 6, 'Los que confían en sus haciendas, Y en la muchedumbre de sus riquezas se jactan,', 'water'),
('Old', 'Salmos', 49, 7, 'Ninguno de ellos podrá en manera alguna redimir al hermano, Ni dar á Dios su rescate.', 'water'),
('Old', 'Salmos', 49, 8, '(Porque la redención de su vida es de gran precio, Y no se hará jamás;)', 'water'),
('Old', 'Salmos', 49, 9, 'Que viva adelante para siempre, Y nunca vea la sepultura.', 'water'),
('Old', 'Salmos', 49, 10, 'Pues se ve que mueren los sabios, Así como el insensato y el necio perecen, Y dejan á otros sus riquezas.', 'water'),
('Old', 'Salmos', 49, 11, 'En su interior tienen que sus casas serán eternas, Y sus habitaciones para generación y generación: Llamaron sus tierras de sus nombres.', 'water'),
('Old', 'Salmos', 49, 12, 'Mas el hombre no permanecerá en honra: Es semejante á las bestias que perecen.', 'water'),
('Old', 'Salmos', 49, 13, 'Este su camino es su locura: Con todo, corren sus descendientes por el dicho de ellos. (Selah.)', 'water'),
('Old', 'Salmos', 49, 14, 'Como rebaños serán puestos en la sepultura; La muerte se cebará en ellos; Y los rectos se enseñorearán de ellos por la mañana: Y se consumirá su bien parecer en el sepulcro de su morada.', 'water'),
('Old', 'Salmos', 49, 15, 'Empero Dios redimirá mi vida del poder de la sepultura, Cuando me tomará. (Selah.)', 'water'),
('Old', 'Salmos', 49, 16, 'No temas cuando se enriquece alguno, Cuando aumenta la gloria de su casa;', 'water'),
('Old', 'Salmos', 49, 17, 'Porque en muriendo no llevará nada, Ni descenderá tras él su gloria.', 'water'),
('Old', 'Salmos', 49, 18, 'Si bien mientras viviere, dirá dichosa á su alma: Y tú serás loado cuando bien te tratares.', 'water'),
('Old', 'Salmos', 49, 19, 'Entrará á la generación de sus padres: No verán luz para siempre.', 'water'),
('Old', 'Salmos', 49, 20, 'El hombre en honra que no entiende, Semejante es á las bestias que perecen.', 'water'),
('Old', 'Salmos', 50, 1, 'EL Dios de dioses, Jehová, ha hablado, Y convocado la tierra desde el nacimiento del sol hasta donde se pone.', 'water'),
('Old', 'Salmos', 50, 2, 'De Sión, perfección de hermosura, Ha Dios resplandecido.', 'water'),
('Old', 'Salmos', 50, 3, 'Vendrá nuestro Dios, y no callará: Fuego consumirá delante de él, Y en derredor suyo habrá tempestad grande.', 'water'),
('Old', 'Salmos', 50, 4, 'Convocará á los cielos de arriba, Y á la tierra, para juzgar á su pueblo.', 'water'),
('Old', 'Salmos', 50, 5, 'Juntadme mis santos; Los que hicieron conmigo pacto con sacrificio.', 'water'),
('Old', 'Salmos', 50, 6, 'Y denunciarán los cielos su justicia; Porque Dios es el juez. (Selah.)', 'water'),
('Old', 'Salmos', 50, 7, 'Oye, pueblo mío, y hablaré: Escucha, Israel, y testificaré contra ti: Yo soy Dios, el Dios tuyo.', 'water'),
('Old', 'Salmos', 50, 8, 'No te reprenderé sobre tus sacrificios, Ni por tus holocaustos, que delante de mí están siempre.', 'water'),
('Old', 'Salmos', 50, 9, 'No tomaré de tu casa becerros, Ni machos cabríos de tus apriscos.', 'water'),
('Old', 'Salmos', 50, 10, 'Porque mía es toda bestia del bosque, Y los millares de animales en los collados.', 'water'),
('Old', 'Salmos', 50, 11, 'Conozco todas las aves de los montes, Y en mi poder están las fieras del campo.', 'water'),
('Old', 'Salmos', 50, 12, 'Si yo tuviese hambre, no te lo diría á ti: Porque mío es el mundo y su plenitud.', 'water'),
('Old', 'Salmos', 50, 13, '¿Tengo de comer yo carne de toros, O de beber sangre de machos cabríos?', 'water'),
('Old', 'Salmos', 50, 14, 'Sacrifica á Dios alabanza, Y paga tus votos al Altísimo.', 'water'),
('Old', 'Salmos', 50, 15, 'E invócame en el día de la angustia: Te libraré, y tú me honrarás.', 'water'),
('Old', 'Salmos', 50, 16, 'Pero al malo dijo Dios: ¿Qué tienes tú que enarrar mis leyes, Y que tomar mi pacto en tu boca,', 'water'),
('Old', 'Salmos', 50, 17, 'Pues que tú aborreces el castigo, Y echas á tu espalda mis palabras?', 'water'),
('Old', 'Salmos', 50, 18, 'Si veías al ladrón, tú corrías con él; Y con los adúlteros era tu parte.', 'water'),
('Old', 'Salmos', 50, 19, 'Tu boca metías en mal, Y tu lengua componía engaño.', 'water'),
('Old', 'Salmos', 50, 20, 'Tomabas asiento, y hablabas contra tu hermano: Contra el hijo de tu madre ponías infamia.', 'water'),
('Old', 'Salmos', 50, 21, 'Estas cosas hiciste, y yo he callado: Pensabas que de cierto sería yo como tú: Yo te argüiré, y pondré las delante de tus ojos.', 'water'),
('Old', 'Salmos', 50, 22, 'Entended ahora esto, los que os olvidáis de Dios; No sea que arrebate, sin que nadie libre.', 'water'),
('Old', 'Salmos', 50, 23, 'El que sacrifica alabanza me honrará: Y al que ordenare su camino, Le mostraré la salud de Dios.', 'water'),
('Old', 'Salmos', 51, 1, 'TEN piedad de mí, oh Dios, conforme á tu misericordia: Conforme á la multitud de tus piedades borra mis rebeliones.', 'water'),
('Old', 'Salmos', 51, 2, 'Lávame más y más de mi maldad, Y límpiame de mi pecado.', 'water'),
('Old', 'Salmos', 51, 3, 'Porque yo reconozco mis rebeliones; Y mi pecado está siempre delante de mí.', 'water'),
('Old', 'Salmos', 51, 4, 'A ti, á ti solo he pecado, Y he hecho lo malo delante de tus ojos: Porque seas reconocido justo en tu palabra, Y tenido por puro en tu juicio.', 'water'),
('Old', 'Salmos', 51, 5, 'He aquí, en maldad he sido formado, Y en pecado me concibió mi madre.', 'water'),
('Old', 'Salmos', 51, 6, 'He aquí, tú amas la verdad en lo íntimo: Y en lo secreto me has hecho comprender sabiduría.', 'water'),
('Old', 'Salmos', 51, 7, 'Purifícame con hisopo, y será limpio: Lávame, y seré emblanquecido más que la nieve.', 'water'),
('Old', 'Salmos', 51, 8, 'Hazme oir gozo y alegría; Y se recrearán los huesos que has abatido.', 'water'),
('Old', 'Salmos', 51, 9, 'Esconde tu rostro de mis pecados, Y borra todas mis maldades.', 'water'),
('Old', 'Salmos', 51, 10, 'Crea en mí, oh Dios, un corazón limpio; Y renueva un espíritu recto dentro de mí.', 'water'),
('Old', 'Salmos', 51, 11, 'No me eches de delante de ti; Y no quites de mí tu santo espíritu.', 'water'),
('Old', 'Salmos', 51, 12, 'Vuélveme el gozo de tu salud; Y el espíritu libre me sustente.', 'water'),
('Old', 'Salmos', 51, 13, 'Enseñaré á los prevaricadores tus caminos; Y los pecadores se convertirán á ti.', 'water'),
('Old', 'Salmos', 51, 14, 'Líbrame de homicidios, oh Dios, Dios de mi salud: Cantará mi lengua tu justicia.', 'water'),
('Old', 'Salmos', 51, 15, 'Señor, abre mis labios; Y publicará mi boca tu alabanza.', 'water'),
('Old', 'Salmos', 51, 16, 'Porque no quieres tú sacrificio, que yo daría; No quieres holocausto.', 'water'),
('Old', 'Salmos', 51, 17, 'Los sacrificios de Dios son el espíritu quebrantado: Al corazón contrito y humillado no despreciarás tú, oh Dios.', 'water'),
('Old', 'Salmos', 51, 18, 'Haz bien con tu benevolencia á Sión: Edifica los muros de Jerusalem.', 'water'),
('Old', 'Salmos', 51, 19, 'Entonces te agradarán los sacrificios de justicia, el holocausto ú ofrenda del todo quemada: Entonces ofrecerán sobre tu altar becerros.', 'water'),
('Old', 'Salmos', 52, 1, '¿POR qué te glorías de maldad, oh poderoso? La misericordia de Dios es continua.', 'water'),
('Old', 'Salmos', 52, 2, 'Agravios maquina tu lengua: Como navaja amolada hace engaño.', 'water'),
('Old', 'Salmos', 52, 3, 'Amaste el mal más que el bien; La mentira más que hablar justicia. (Selah.)', 'water'),
('Old', 'Salmos', 52, 4, 'Has amado toda suerte de palabras perniciosas, Engañosa lengua.', 'water'),
('Old', 'Salmos', 52, 5, 'Por tanto Dios te derribará para siempre: Te asolará y te arrancará de tu morada, Y te desarraigará de la tierra de los vivientes. (Selah.)', 'water'),
('Old', 'Salmos', 52, 6, 'Y verán los justos, y temerán; Y reiránse de él, diciendo:', 'water'),
('Old', 'Salmos', 52, 7, 'He aquí el hombre que no puso á Dios por su fortaleza, Sino que confió en la multitud de sus riquezas. Y se mantuvo en su maldad.', 'water'),
('Old', 'Salmos', 52, 8, 'Mas yo estoy como oliva verde en la casa de Dios: En la misericordia de Dios confío perpetua y eternalmente.', 'water'),
('Old', 'Salmos', 52, 9, 'Te alabaré para siempre por lo que has hecho: Y esperaré en tu nombre, porque es bueno, delante de tus santos.', 'water'),
('Old', 'Salmos', 53, 1, 'DIJO el necio en su corazón: No hay Dios. Corrompiéronse é hicieron abominable maldad: No hay quien haga bien.', 'water'),
('Old', 'Salmos', 53, 2, 'Dios desde los cielos miró sobre los hijos de los hombres, Por ver si hay algún entendido Que busque á Dios.', 'water'),
('Old', 'Salmos', 53, 3, 'Cada uno se había vuelto atrás; todos se habían corrompido: No hay quien haga bien, no hay ni aun uno.', 'water'),
('Old', 'Salmos', 53, 4, '¿No tienen conocimiento todos esos que obran iniquidad? Que comen á mi pueblo como si comiesen pan: A Dios no han invocado.', 'water'),
('Old', 'Salmos', 53, 5, 'Allí se sobresaltaron de pavor donde no había miedo: Porque Dios ha esparcido los huesos del que asentó campo contra ti: Los avergonzaste, porque Dios los desechó.', 'water'),
('Old', 'Salmos', 53, 6, '¡Oh quién diese de Sión saludes á Israel! En volviendo Dios la cautividad de su pueblo, Gozarse ha Jacob, y alegraráse Israel.', 'water'),
('Old', 'Salmos', 54, 1, 'OH Dios, sálvame por tu nombre, Y con tu poder defiéndeme.', 'water'),
('Old', 'Salmos', 54, 2, 'Oh Dios, oye mi oración; Escucha las razones de mi boca.', 'water'),
('Old', 'Salmos', 54, 3, 'Porque extraños se han levantado contra mí, Y fuertes buscan mi alma: No han puesto á Dios delante de sí. (Selah.)', 'water'),
('Old', 'Salmos', 54, 4, 'He aquí, Dios es el que me ayuda; El Señor es con los que sostienen mi vida.', 'water'),
('Old', 'Salmos', 54, 5, 'El volverá el mal á mis enemigos: Córtalos por tu verdad.', 'water'),
('Old', 'Salmos', 54, 6, 'Voluntariamente sacrificaré á ti; Alabaré tu nombre, oh Jehová, porque es bueno.', 'water'),
('Old', 'Salmos', 54, 7, 'Porque me ha librado de toda angustia, Y en mis enemigos vieron mis ojos mi deseo.', 'water'),
('Old', 'Salmos', 55, 1, 'ESCUCHA, oh Dios, mi oración, Y no te escondas de mi súplica.', 'water'),
('Old', 'Salmos', 55, 2, 'Estáme atento, y respóndeme: Clamo en mi oración, y levanto el grito,', 'water'),
('Old', 'Salmos', 55, 3, 'A causa de la voz del enemigo, Por la opresión del impío; Porque echaron sobre mí iniquidad, Y con furor me han amenazado.', 'water'),
('Old', 'Salmos', 55, 4, 'Mi corazón está doloroso dentro de mí, Y terrores de muerte sobre mí han caído.', 'water'),
('Old', 'Salmos', 55, 5, 'Temor y temblor vinieron sobre mí, Y terror me ha cubierto.', 'water'),
('Old', 'Salmos', 55, 6, 'Y dije: ¡Quién me diese alas como de paloma! Volaría yo, y descansaría.', 'water'),
('Old', 'Salmos', 55, 7, 'Ciertamente huiría lejos: Moraría en el desierto. (Selah.)', 'water'),
('Old', 'Salmos', 55, 8, 'Apresuraríame á escapar Del viento tempestuoso, de la tempestad.', 'water'),
('Old', 'Salmos', 55, 9, 'Deshace, oh Señor, divide la lengua de ellos; Porque he visto violencia y rencilla en la ciudad.', 'water'),
('Old', 'Salmos', 55, 10, 'Día y noche la rodean sobre sus muros; E iniquidad y trabajo hay en medio de ella.', 'water'),
('Old', 'Salmos', 55, 11, 'Agravios hay en medio de ella, Y el fraude y engaño no se apartan de sus plazas.', 'water'),
('Old', 'Salmos', 55, 12, 'Porque no me afrentó un enemigo, Lo cual habría soportado; Ni se alzó contra mí el que me aborrecía, Porque me hubiera ocultado de él:', 'water'),
('Old', 'Salmos', 55, 13, 'Mas tú, hombre, al parecer íntimo mío, Mi guía, y mi familiar:', 'water'),
('Old', 'Salmos', 55, 14, 'Que juntos comunicábamos dulcemente los secretos, A la casa de Dios andábamos en compañía.', 'water'),
('Old', 'Salmos', 55, 15, 'Condenados sean á muerte, Desciendan vivos al infierno: Porque maldades hay en su compañía, entre ellos.', 'water'),
('Old', 'Salmos', 55, 16, 'Yo á Dios clamaré; Y Jehová me salvará.', 'water'),
('Old', 'Salmos', 55, 17, 'Tarde y mañana y á medio día oraré y clamaré; Y él oirá mi voz.', 'water'),
('Old', 'Salmos', 55, 18, 'El ha redimido en paz mi alma de la guerra contra mí; Pues fueron contra mí muchos.', 'water'),
('Old', 'Salmos', 55, 19, 'Dios oirá, y los quebrantará luego, El que desde la antigüedad permanece (Selah); Por cuanto no se mudan, Ni temen á Dios.', 'water'),
('Old', 'Salmos', 55, 20, 'Extendió sus manos contra sus pacíficos: Viólo su pacto.', 'water'),
('Old', 'Salmos', 55, 21, 'Ablandan más que manteca su boca, Pero guerra hay en su corazón: Suavizan sus palabras más que el aceite, Mas ellas son cuchillos.', 'water'),
('Old', 'Salmos', 55, 22, 'Echa sobre Jehová tu carga, y él te sustentará; No dejará para siempre caído al justo.', 'water'),
('Old', 'Salmos', 55, 23, 'Mas tú, oh Dios, harás descender aquéllos al pozo de la sepultura: Los hombres sanguinarios y engañadores no demediarán sus días: Empero yo confiaré en ti.', 'water'),
('Old', 'Salmos', 56, 1, 'TEN misericordia de mí, oh Dios, porque me devoraría el hombre: Me oprime combatiéndome cada día.', 'water'),
('Old', 'Salmos', 56, 2, 'Apúranme mis enemigos cada día; Porque muchos son los que pelean contra mí, oh Altísimo.', 'water'),
('Old', 'Salmos', 56, 3, 'En el día que temo, Yo en ti confío.', 'water'),
('Old', 'Salmos', 56, 4, 'En Dios alabaré su palabra: En Dios he confiado, no temeré Lo que la carne me hiciere.', 'water'),
('Old', 'Salmos', 56, 5, 'Todos los días me contristan mis negocios; Contra mí son todos sus pensamientos para mal.', 'water'),
('Old', 'Salmos', 56, 6, 'Reúnense, escóndense, Miran ellos atentamente mis pasos, Esperando mi vida.', 'water'),
('Old', 'Salmos', 56, 7, '¿Escaparán ellos por la iniquidad? Oh Dios, derriba en tu furor los pueblos.', 'water'),
('Old', 'Salmos', 56, 8, 'Mis huídas has tú contado: Pon mis lágrimas en tu redoma: ¿No están ellas en tu libro?', 'water'),
('Old', 'Salmos', 56, 9, 'Serán luego vueltos atrás mis enemigos el día que yo clamare: En esto conozco que Dios es por mí.', 'water'),
('Old', 'Salmos', 56, 10, 'En Dios alabaré su palabra; En Jehová alabaré su palabra.', 'water'),
('Old', 'Salmos', 56, 11, 'En Dios he confiado: no temeré Lo que me hará el hombre.', 'water'),
('Old', 'Salmos', 56, 12, 'Sobre mí, oh Dios, están tus votos: Te tributaré alabanzas.', 'water'),
('Old', 'Salmos', 56, 13, 'Porque has librado mi vida de la muerte, Y mis pies de caída, Para que ande delante de Dios En la luz de los que viven.', 'water'),
('Old', 'Salmos', 57, 1, 'TEN misericordia de mí, oh Dios, ten misericordia de mí; Porque en ti ha confiado mi alma, Y en la sombra de tus alas me ampararé, Hasta que pasen los quebrantos.', 'water'),
('Old', 'Salmos', 57, 2, 'Clamaré al Dios Altísimo, Al Dios que me favorece.', 'water'),
('Old', 'Salmos', 57, 3, 'El enviará desde los cielos, y me salvará De la infamia del que me apura; (Selah) Dios enviará su misericordia y su verdad.', 'water'),
('Old', 'Salmos', 57, 4, 'Mi vida está entre leones; Estoy echado entre hijos de hombres encendidos: Sus dientes son lanzas y saetas, Y su lengua cuchillo agudo.', 'water'),
('Old', 'Salmos', 57, 5, 'Ensálzate sobre los cielos, oh Dios; Sobre toda la tierra tu gloria.', 'water'),
('Old', 'Salmos', 57, 6, 'Red han armado á mis pasos; Hase abatido mi alma: Hoyo han cavado delante de mí; En medio de él han caído. (Selah.)', 'water'),
('Old', 'Salmos', 57, 7, 'Pronto está mi corazón, oh Dios, mi corazón está dispuesto: Cantaré, y trovaré salmos.', 'water'),
('Old', 'Salmos', 57, 8, 'Despierta, oh gloria mía; despierta, salterio y arpa: Levantaréme de mañana.', 'water'),
('Old', 'Salmos', 57, 9, 'Alabarte he en los pueblos, oh Señor; Cantaré de ti en las naciones.', 'water'),
('Old', 'Salmos', 57, 10, 'Porque grande es hasta los cielos tu misericordia, Y hasta las nubes tu verdad.', 'water'),
('Old', 'Salmos', 57, 11, 'Ensálzate sobre los cielos, oh Dios; Sobre toda la tierra tu gloria.', 'water'),
('Old', 'Salmos', 58, 1, 'OH congregación, ¿pronunciáis en verdad justicia? ¿Juzgáis rectamente, hijos de los hombres?', 'water'),
('Old', 'Salmos', 58, 2, 'Antes con el corazón obráis iniquidades: Hacéis pesar la violencia de vuestras manos en la tierra.', 'water'),
('Old', 'Salmos', 58, 3, 'Enajenáronse los impíos desde la matriz; Descarriáronse desde el vientre, hablando mentira.', 'water'),
('Old', 'Salmos', 58, 4, 'Veneno tienen semejante al veneno de la serpiente: Son como áspide sordo que cierra su oído;', 'water'),
('Old', 'Salmos', 58, 5, 'Que no oye la voz de los que encantan, Por más hábil que el encantador sea.', 'water'),
('Old', 'Salmos', 58, 6, 'Oh Dios, quiebra sus dientes en sus bocas: Quiebra, oh Jehová, las muelas de los leoncillos.', 'water'),
('Old', 'Salmos', 58, 7, 'Corránse como aguas que se van de suyo: En entesando sus saetas, luego sean hechas pedazos.', 'water'),
('Old', 'Salmos', 58, 8, 'Pasen ellos como el caracol que se deslíe: Como el abortivo de mujer, no vean el sol.', 'water'),
('Old', 'Salmos', 58, 9, 'Antes que vuestras ollas sientan las espinas, Así vivos, así airados, los arrebatará él con tempestad.', 'water'),
('Old', 'Salmos', 58, 10, 'Alegraráse el justo cuando viere la venganza: Sus pies lavará en la sangre del impío.', 'water'),
('Old', 'Salmos', 58, 11, 'Entonces dirá el hombre: Ciertamente hay fruto para el justo; Ciertamente hay Dios que juzga en la tierra.', 'water'),
('Old', 'Salmos', 59, 1, 'LIBRAME de mis enemigos, oh Dios mío: Ponme en salvo de los que contra mí se levantan.', 'water'),
('Old', 'Salmos', 59, 2, 'Líbrame de los que obran iniquidad, Y sálvame de hombres sanguinarios.', 'water'),
('Old', 'Salmos', 59, 3, 'Porque he aquí están acechando mi vida: Hanse juntado contra mí fuertes, No por falta mía, ni pecado mío, oh Jehová.', 'water'),
('Old', 'Salmos', 59, 4, 'Sin delito mío corren y se aperciben: Despierta para venir á mi encuentro, y mira.', 'water'),
('Old', 'Salmos', 59, 5, 'Y tú, Jehová Dios de los ejércitos, Dios de Israel, Despierta para visitar todas las gentes: No hayas misericordia de todos los que se rebelan con iniquidad. (Selah.)', 'water'),
('Old', 'Salmos', 59, 6, 'Volveránse á la tarde, ladrarán como perros, Y rodearán la ciudad.', 'water'),
('Old', 'Salmos', 59, 7, 'He aquí proferirán con su boca; Cuchillos están en sus labios, Porque dicen: ¿Quién oye?', 'water'),
('Old', 'Salmos', 59, 8, 'Mas tú, Jehová, te reirás de ellos, Te burlarás de todas las gentes.', 'water'),
('Old', 'Salmos', 59, 9, 'De su fuerza esperaré yo en ti: Porque Dios es mi defensa.', 'water'),
('Old', 'Salmos', 59, 10, 'El Dios de mi misericordia me prevendrá: Dios me hará ver en mis enemigos mi deseo.', 'water'),
('Old', 'Salmos', 59, 11, 'No los matarás, porque mi pueblo no se olvide: Hazlos vagar con tu fortaleza, y abátelos. Oh Jehová, escudo nuestro,', 'water'),
('Old', 'Salmos', 59, 12, 'Por el pecado de su boca, por la palabra de sus labios; Y sean presos por su soberbia, Y por la maldición y mentira que profieren.', 'water'),
('Old', 'Salmos', 59, 13, 'Acábalos con furor, acábalos, y no sean: Y sepan que Dios domina en Jacob Hasta los fines de la tierra. (Selah).', 'water'),
('Old', 'Salmos', 59, 14, 'Vuelvan pues á la tarde, y ladren como perros, Y rodeen la ciudad.', 'water'),
('Old', 'Salmos', 59, 15, 'Anden ellos errantes para hallar qué comer: Y si no se saciaren, murmuren.', 'water'),
('Old', 'Salmos', 59, 16, 'Yo empero cantaré tu fortaleza, Y loaré de mañana tu misericordia: Porque has sido mi amparo Y refugio en el día de mi angustia.', 'water'),
('Old', 'Salmos', 59, 17, 'Fortaleza mía, á ti cantaré; Porque eres Dios de mi amparo, Dios de mi misericordia.', 'water'),
('Old', 'Salmos', 60, 1, 'OH Dios, tú nos has desechado, nos disipaste; Te has airado: vuélvete á nosotros.', 'water'),
('Old', 'Salmos', 60, 2, 'Hiciste temblar la tierra, abrístela: Sana sus quiebras, porque titubea.', 'water'),
('Old', 'Salmos', 60, 3, 'Has hecho ver á tu pueblo duras cosas: Hicístenos beber el vino de agitación.', 'water'),
('Old', 'Salmos', 60, 4, 'Has dado á los que te temen bandera Que alcen por la verdad. (Selah.)', 'water'),
('Old', 'Salmos', 60, 5, 'Para que se libren tus amados, Salva con tu diestra, y óyeme.', 'water'),
('Old', 'Salmos', 60, 6, 'Dios pronunció por su santuario; yo me alegraré; Partiré á Sichêm, y mediré el valle de Succoth.', 'water'),
('Old', 'Salmos', 60, 7, 'Mío es Galaad, y mío es Manasés; Y Ephraim es la fortaleza de mi cabeza; Judá, mi legislador;', 'water'),
('Old', 'Salmos', 60, 8, 'Moab, la vasija de mi lavatorio; Sobre Edom echaré mi zapato: Haz júbilo sobre mí, oh Palestina.', 'water'),
('Old', 'Salmos', 60, 9, '¿Quién me llevará á la ciudad fortalecida? ¿Quién me llevará hasta Idumea?', 'water'),
('Old', 'Salmos', 60, 10, 'Ciertamente, tú, oh Dios, que nos habías desechado; Y no salías, oh Dios, con nuestros ejércitos.', 'water'),
('Old', 'Salmos', 60, 11, 'Danos socorro contra el enemigo, Que vana es la salud de los hombres.', 'water'),
('Old', 'Salmos', 60, 12, 'En Dios haremos proezas; Y él hollará nuestros enemigos.', 'water'),
('Old', 'Salmos', 61, 1, 'OYE, oh Dios, mi clamor; A mi oración atiende.', 'water'),
('Old', 'Salmos', 61, 2, 'Desde el cabo de la tierra clamaré á ti, cuando mi corazón desmayare: A la peña más alta que yo me conduzcas.', 'water'),
('Old', 'Salmos', 61, 3, 'Porque tú has sido mi refugio, Y torre de fortaleza delante del enemigo.', 'water'),
('Old', 'Salmos', 61, 4, 'Yo habitaré en tu tabernáculo para siempre: Estaré seguro bajo la cubierta de tus alas.', 'water'),
('Old', 'Salmos', 61, 5, 'Porque tú, oh Dios, has oído mis votos, Has dado heredad á los que temen tu nombre.', 'water'),
('Old', 'Salmos', 61, 6, 'Días sobre días añadirás al rey: Sus años serán como generación y generación.', 'water'),
('Old', 'Salmos', 61, 7, 'Estará para siempre delante de Dios: Misericordia y verdad prepara que lo conserven.', 'water'),
('Old', 'Salmos', 61, 8, 'Así cantaré tu nombre para siempre, Pagando mis votos cada día.', 'water'),
('Old', 'Salmos', 62, 1, 'EN Dios solamente está callada mi alma: De él viene mi salud.', 'water'),
('Old', 'Salmos', 62, 2, 'El solamente es mi fuerte, y mi salud; Es mi refugio, no resbalaré mucho.', 'water'),
('Old', 'Salmos', 62, 3, '¿Hasta cuándo maquinaréis contra un hombre? Pereceréis todos vosotros, Caeréis como pared acostada, como cerca ruinosa.', 'water'),
('Old', 'Salmos', 62, 4, 'Solamente consultan de arrojarle de su grandeza; Aman la mentira, Con su boca bendicen, pero maldicen en sus entrañas. (Selah.)', 'water'),
('Old', 'Salmos', 62, 5, 'Alma mía, en Dios solamente reposa; Porque de él es mi esperanza.', 'water'),
('Old', 'Salmos', 62, 6, 'El solamente es mi fuerte y mi salud: Es mi refugio, no resbalaré.', 'water'),
('Old', 'Salmos', 62, 7, 'En Dios está mi salvación y mi gloria: En Dios está la roca de mi fortaleza, y mi refugio.', 'water'),
('Old', 'Salmos', 62, 8, 'Esperad en él en todo tiempo, oh pueblos; Derramad delante de él vuestro corazón: Dios es nuestro amparo. (Selah.)', 'water'),
('Old', 'Salmos', 62, 9, 'Por cierto, vanidad son los hijos de los hombres, mentira los hijos de varón: Pesándolos á todos igualmente en la balanza, Serán menos que la vanidad.', 'water'),
('Old', 'Salmos', 62, 10, 'No confiéis en la violencia, Ni en la rapiña; no os envanezcáis: Si se aumentare la hacienda, no pongáis el corazón en ella.', 'water'),
('Old', 'Salmos', 62, 11, 'Una vez habló Dios; Dos veces he oído esto: Que de Dios es la fortaleza.', 'water'),
('Old', 'Salmos', 62, 12, 'Y de ti, oh Señor, es la misericordia: Porque tú pagas á cada uno conforme á su obra.', 'water'),
('Old', 'Salmos', 63, 1, 'DIOS, Dios mío eres tú: levantaréme á ti de mañana: Mi alma tiene sed de ti, mi carne te desea, En tierra de sequedad y transida sin aguas;', 'water'),
('Old', 'Salmos', 63, 2, 'Para ver tu fortaleza y tu gloria, Así como te he mirado en el santuario.', 'water'),
('Old', 'Salmos', 63, 3, 'Porque mejor es tu misericordia que la vida: Mis labios te alabarán.', 'water'),
('Old', 'Salmos', 63, 4, 'Así te bendeciré en mi vida: En tu nombre alzaré mis manos.', 'water'),
('Old', 'Salmos', 63, 5, 'Como de meollo y de grosura será saciada mi alma; Y con labios de júbilo te alabará mi boca,', 'water'),
('Old', 'Salmos', 63, 6, 'Cuando me acordaré de ti en mi lecho, Cuando meditaré de ti en las velas de la noche.', 'water'),
('Old', 'Salmos', 63, 7, 'Porque has sido mi socorro; Y así en la sombra de tus alas me regocijaré.', 'water'),
('Old', 'Salmos', 63, 8, 'Está mi alma apegada á ti: Tu diestra me ha sostenido.', 'water'),
('Old', 'Salmos', 63, 9, 'Mas los que para destrucción buscaron mi alma, Caerán en los sitios bajos de la tierra.', 'water'),
('Old', 'Salmos', 63, 10, 'Destruiránlos á filo de espada; Serán porción de las zorras.', 'water'),
('Old', 'Salmos', 63, 11, 'Empero el rey se alegrará en Dios; Será alabado cualquiera que por él jura: Porque la boca de los que hablan mentira, será cerrada.', 'water'),
('Old', 'Salmos', 64, 1, 'ESCUCHA, oh Dios, mi voz en mi oración: Guarda mi vida del miedo del enemigo.', 'water'),
('Old', 'Salmos', 64, 2, 'Escóndeme del secreto consejo de los malignos; De la conspiración de los que obran iniquidad:', 'water'),
('Old', 'Salmos', 64, 3, 'Que amolaron su lengua como cuchillo, Y armaron por su saeta palabra amarga;', 'water'),
('Old', 'Salmos', 64, 4, 'Para asaetear á escondidas al íntegro: De improviso lo asaetean, y no temen.', 'water'),
('Old', 'Salmos', 64, 5, 'Obstinados en su inicuo designio, Tratan de esconder los lazos, Y dicen: ¿Quién los ha de ver?', 'water'),
('Old', 'Salmos', 64, 6, 'Inquieren iniquidades, hacen una investigación exacta; Y el íntimo pensamiento de cada uno de ellos, así como el corazón, es profundo.', 'water'),
('Old', 'Salmos', 64, 7, 'Mas Dios los herirá con saeta; De repente serán sus plagas.', 'water'),
('Old', 'Salmos', 64, 8, 'Y harán caer sobre sí sus mismas lenguas: Se espantarán todos los que los vieren.', 'water'),
('Old', 'Salmos', 64, 9, 'Y temerán todos los hombres, Y anunciarán la obra de Dios, Y entenderán su hecho.', 'water'),
('Old', 'Salmos', 64, 10, 'Alegraráse el justo en Jehová, y confiaráse en él; Y se gloriarán todos los rectos de corazón.', 'water'),
('Old', 'Salmos', 65, 1, 'A TI es plácida la alabanza en Sión, oh Dios: Y á ti se pagarán los votos.', 'water'),
('Old', 'Salmos', 65, 2, 'Tú oyes la oración: A ti vendrá toda carne.', 'water'),
('Old', 'Salmos', 65, 3, 'Palabras de iniquidades me sobrepujaron: Mas nuestras rebeliones tú las perdonarás.', 'water'),
('Old', 'Salmos', 65, 4, 'Dichoso el que tú escogieres, é hicieres llegar á ti, Para que habite en tus atrios: Seremos saciados del bien de tu casa, De tu santo templo.', 'water'),
('Old', 'Salmos', 65, 5, 'Con tremendas cosas, en justicia, nos responderás tú, Oh Dios de nuestra salud, Esperanza de todos los términos de la tierra, Y de los más remotos confines de la mar.', 'water'),
('Old', 'Salmos', 65, 6, 'Tú, el que afirma los montes con su potencia, Ceñido de valentía:', 'water'),
('Old', 'Salmos', 65, 7, 'El que amansa el estruendo de los mares, el estruendo de sus ondas, Y el alboroto de las gentes.', 'water'),
('Old', 'Salmos', 65, 8, 'Por tanto los habitadores de los fines de la tierra temen de tus maravillas. Tú haces alegrar las salidas de la mañana y de la tarde.', 'water'),
('Old', 'Salmos', 65, 9, 'Visitas la tierra, y la riegas: En gran manera la enriqueces Con el río de Dios, lleno de aguas: Preparas el grano de ellos, cuando así la dispones.', 'water'),
('Old', 'Salmos', 65, 10, 'Haces se empapen sus surcos, Haces descender sus canales: Ablándasla con lluvias, Bendices sus renuevos.', 'water'),
('Old', 'Salmos', 65, 11, 'Tú coronas el año de tus bienes; Y tus nubes destilan grosura.', 'water'),
('Old', 'Salmos', 65, 12, 'Destilan sobre las estancias del desierto; Y los collados se ciñen de alegría.', 'water'),
('Old', 'Salmos', 65, 13, 'Vístense los llanos de manadas, Y los valles se cubren de grano: Dan voces de júbilo, y aun cantan.', 'water'),
('Old', 'Salmos', 66, 1, 'ACLAMAD á Dios con alegría, toda la tierra:', 'water'),
('Old', 'Salmos', 66, 2, 'Cantad la gloria de su nombre: Poned gloria en su alabanza.', 'water'),
('Old', 'Salmos', 66, 3, 'Decid á Dios: ¡Cuán terribles tus obras! Por lo grande de tu fortaleza te mentirán tus enemigos.', 'water'),
('Old', 'Salmos', 66, 4, 'Toda la tierra te adorará, Y cantará á ti; Cantarán á tu nombre. (Selah.)', 'water'),
('Old', 'Salmos', 66, 5, 'Venid, y ved las obras de Dios, Terrible en hechos sobre los hijos de los hombres.', 'water'),
('Old', 'Salmos', 66, 6, 'Volvió la mar en seco; Por el río pasaron á pie; Allí en él nos alegramos.', 'water'),
('Old', 'Salmos', 66, 7, 'El se enseñorea con su fortaleza para siempre: Sus ojos atalayan sobre las gentes: Los rebeldes no serán ensalzados. (Selah.)', 'water'),
('Old', 'Salmos', 66, 8, 'Bendecid, pueblos, á nuestro Dios, Y haced oir la voz de su alabanza.', 'water'),
('Old', 'Salmos', 66, 9, 'El es el que puso nuestra alma en vida, Y no permitió que nuestros pies resbalasen.', 'water'),
('Old', 'Salmos', 66, 10, 'Porque tú nos probaste, oh Dios: Ensayástenos como se afina la plata.', 'water'),
('Old', 'Salmos', 66, 11, 'Nos metiste en la red; Pusiste apretura en nuestros lomos.', 'water'),
('Old', 'Salmos', 66, 12, 'Hombres hiciste subir sobre nuestra cabeza; Entramos en fuego y en aguas, Y sacástenos á hartura.', 'water'),
('Old', 'Salmos', 66, 13, 'Entraré en tu casa con holocaustos: Te pagaré mis votos,', 'water'),
('Old', 'Salmos', 66, 14, 'Que pronunciaron mis labios, Y habló mi boca, cuando angustiado estaba.', 'water'),
('Old', 'Salmos', 66, 15, 'Holocaustos de cebados te ofreceré, Con perfume de carneros: Sacrificaré bueyes y machos cabríos. (Selah.)', 'water'),
('Old', 'Salmos', 66, 16, 'Venid, oid todos los que teméis á Dios, Y contaré lo que ha hecho á mi alma.', 'water'),
('Old', 'Salmos', 66, 17, 'A él clamé con mi boca, Y ensalzado fué con mi lengua.', 'water'),
('Old', 'Salmos', 66, 18, 'Si en mi corazón hubiese yo mirado á la iniquidad, El Señor no me oyera.', 'water'),
('Old', 'Salmos', 66, 19, 'Mas ciertamente me oyó Dios; Antendió á la voz de mi súplica.', 'water'),
('Old', 'Salmos', 66, 20, 'Bendito Dios, Que no echó de sí mi oración, ni de mí su misericordia.', 'water'),
('Old', 'Salmos', 67, 1, 'DIOS tenga misericordia de nosotros, y nos bendiga; Haga resplandecer su rostro sobre nosotros (Selah);', 'water'),
('Old', 'Salmos', 67, 2, 'Para que sea conocido en la tierra tu camino, En todas las gentes tu salud.', 'water'),
('Old', 'Salmos', 67, 3, 'Alábente los pueblos, oh Dios; Alábente los pueblos todos.', 'water'),
('Old', 'Salmos', 67, 4, 'Alégrense y gocénse las gentes; Porque juzgarás los pueblos con equidad, Y pastorearás las naciones en la tierra. (Selah.)', 'water'),
('Old', 'Salmos', 67, 5, 'Alábente los pueblos, oh Dios: Todos los pueblos te alaben.', 'water'),
('Old', 'Salmos', 67, 6, 'La tierra dará su fruto: Nos bendecirá Dios, el Dios nuestro.', 'water'),
('Old', 'Salmos', 67, 7, 'Bendíganos Dios, Y témanlo todos los fines de la tierra.', 'water'),
('Old', 'Salmos', 68, 1, 'LEVANTESE Dios, sean esparcidos sus enemigos, Y huyan de su presencia los que le aborrecen.', 'water'),
('Old', 'Salmos', 68, 2, 'Como es lanzado el humo, los lanzarás: Como se derrite la cera delante del fuego, Así perecerán los impíos delante de Dios.', 'water'),
('Old', 'Salmos', 68, 3, 'Mas los justos se alegrarán: gozarse han delante de Dios, Y saltarán de alegría.', 'water'),
('Old', 'Salmos', 68, 4, 'Cantad á Dios, cantad salmos á su nombre: Ensalzad al que sube sobre los cielos En JAH su nombre, y alegraos delante de él.', 'water'),
('Old', 'Salmos', 68, 5, 'Padre de huérfanos y defensor de viudas, Es Dios en la morada de su santuario:', 'water'),
('Old', 'Salmos', 68, 6, 'El Dios que hace habitar en familia los solos; Que saca á los aprisionados con grillos: Mas los rebeldes habitan en sequedad.', 'water'),
('Old', 'Salmos', 68, 7, 'Oh Dios, cuando tú saliste delante de tu pueblo, Cuando anduviste por el desierto, (Selah,)', 'water'),
('Old', 'Salmos', 68, 8, 'La tierra tembló; También destilaron los cielos á la presencia de Dios: Aquel Sinaí tembló delante de Dios, del Dios de Israel.', 'water'),
('Old', 'Salmos', 68, 9, 'Abundante lluvia esparciste, oh Dios, á tu heredad; Y cuando se cansó, tú la recreaste.', 'water'),
('Old', 'Salmos', 68, 10, 'Los que son de tu grey han morado en ella: Por tu bondad, oh Dios, has provisto al pobre.', 'water'),
('Old', 'Salmos', 68, 11, 'El Señor daba palabra: De las evangelizantes había grande ejército.', 'water'),
('Old', 'Salmos', 68, 12, 'Huyeron, huyeron reyes de ejércitos; Y las que se quedaban en casa partían los despojos.', 'water'),
('Old', 'Salmos', 68, 13, 'Bien que fuiesteis echados entre los tiestos, Seréis como las alas de la paloma cubierta de plata, Y sus plumas con amarillez de oro.', 'water'),
('Old', 'Salmos', 68, 14, 'Cuando esparció el Omnipotente los reyes en ella, Emblanquecióse ésta como la nieve en Salmón.', 'water'),
('Old', 'Salmos', 68, 15, 'Monte de Dios es el monte de Basán; Monte alto el de Basán.', 'water'),
('Old', 'Salmos', 68, 16, '¿Por qué os levantáis, oh montes altos? Este monte amó Dios para su asiento; Ciertamente Jehová habitará en él para siempre.', 'water'),
('Old', 'Salmos', 68, 17, 'Los carros de Dios son veinte mil, y más millares de ángeles. El Señor entre ellos, como en Sinaí, así en el santuario.', 'water'),
('Old', 'Salmos', 68, 18, 'Subiste á lo alto, cautivaste la cautividad, Tomaste dones para los hombres, Y también para los rebeldes, para que habite entre ellos JAH Dios.', 'water'),
('Old', 'Salmos', 68, 19, 'Bendito el Señor; cada día nos colma de beneficios El Dios de nuestra salud. (Selah.)', 'water'),
('Old', 'Salmos', 68, 20, 'Dios, nuestro Dios ha de salvarnos; Y de Dios Jehová es el librar de la muerte.', 'water'),
('Old', 'Salmos', 68, 21, 'Ciertamente Dios herirá la cabeza de sus enemigos, La cabelluda mollera del que camina en sus pecados.', 'water'),
('Old', 'Salmos', 68, 22, 'El Señor dijo: De Basán haré volver, Te haré volver de los profundos de la mar:', 'water'),
('Old', 'Salmos', 68, 23, 'Porque tu pie se enrojecerá de sangre de tus enemigos, Y de ella la lengua de tus perros.', 'water'),
('Old', 'Salmos', 68, 24, 'Vieron tus caminos, oh Dios; Los caminos de mi Dios, de mi Rey, en el santuario.', 'water'),
('Old', 'Salmos', 68, 25, 'Los cantores iban delante, los tañedores detrás; En medio, las doncellas, con adufes.', 'water'),
('Old', 'Salmos', 68, 26, 'Bendecid á Dios en congregaciones: Al Señor, vosotros de la estirpe de Israel.', 'water'),
('Old', 'Salmos', 68, 27, 'Allí estaba el joven Benjamín señoreador de ellos, Los príncipes de Judá en su congregación, Los príncipes de Zabulón, los príncipes de Nephtalí.', 'water'),
('Old', 'Salmos', 68, 28, 'Tu Dios ha ordenado tu fuerza; Confirma, oh Dios, lo que has obrado en nosotros.', 'water'),
('Old', 'Salmos', 68, 29, 'Por razón de tu templo en Jerusalem Los reyes te ofrecerán dones.', 'water'),
('Old', 'Salmos', 68, 30, 'Reprime la reunión de gentes armadas, La multitud de toros con los becerros de los pueblos, Hasta que todos se sometan con sus piezas de plata: Disipa los pueblos que se complacen en la guerra.', 'water'),
('Old', 'Salmos', 68, 31, 'Vendrán príncipes de Egipto; Etiopía apresurará sus manos á Dios.', 'water'),
('Old', 'Salmos', 68, 32, 'Reinos de la tierra, cantad á Dios, Cantad al Señor (Selah);', 'water'),
('Old', 'Salmos', 68, 33, 'Al que cabalga sobre los cielos de los cielos que son de antiguo: He aquí á su voz dará voz de fortaleza.', 'water'),
('Old', 'Salmos', 68, 34, 'Atribuid fortaleza á Dios: Sobre Israel es su magnificencia, Y su poder está en los cielos.', 'water'),
('Old', 'Salmos', 68, 35, 'Terrible eres, oh Dios, desde tus santuarios: El Dios de Israel, él da fortaleza y vigor á su pueblo. Bendito Dios.', 'water'),
('Old', 'Salmos', 69, 1, 'SALVAME, oh Dios, Porque las aguas han entrado hasta el alma.', 'water'),
('Old', 'Salmos', 69, 2, 'Estoy hundido en cieno profundo, donde no hay pie: He venido á abismos de aguas, y la corriente me ha anegado.', 'water'),
('Old', 'Salmos', 69, 3, 'Cansado estoy de llamar; mi garganta se ha enronquecido; Han desfallecido mis ojos esperando á mi Dios.', 'water'),
('Old', 'Salmos', 69, 4, 'Hanse aumentado más que los cabellos de mi cabeza los que me aborrecen sin causa; Hanse fortalecido mis enemigos, los que me destruyen sin por qué: He venido pues á pagar lo que no he tomado.', 'water'),
('Old', 'Salmos', 69, 5, 'Dios, tú sabes mi locura; Y mis delitos no te son ocultos.', 'water'),
('Old', 'Salmos', 69, 6, 'No sean avergonzados por mi causa los que te esperan, oh Señor Jehová de los ejércitos; No sean confusos por mí los que te buscan, oh Dios de Israel.', 'water'),
('Old', 'Salmos', 69, 7, 'Porque por amor de ti he sufrido afrenta; Confusión ha cubierto mi rostro.', 'water'),
('Old', 'Salmos', 69, 8, 'He sido extrañado de mis hermanos, Y extraño á los hijos de mi madre.', 'water'),
('Old', 'Salmos', 69, 9, 'Porque me consumió el celo de tu casa; Y los denuestos de los que te vituperaban, cayeron sobre mí.', 'water'),
('Old', 'Salmos', 69, 10, 'Y lloré afligiendo con ayuno mi alma; Y esto me ha sido por afrenta.', 'water'),
('Old', 'Salmos', 69, 11, 'Puse además saco por mi vestido; Y vine á serles por proverbio.', 'water'),
('Old', 'Salmos', 69, 12, 'Hablaban contra mí los que se sentaban á la puerta, Y me zaherían en las canciones de los bebederos de sidra.', 'water'),
('Old', 'Salmos', 69, 13, 'Empero yo enderezaba mi oración á ti, oh Jehová, al tiempo de tu buena voluntad: Oh Dios, por la multitud de tu misericordia, Por la verdad de tu salud, óyeme.', 'water'),
('Old', 'Salmos', 69, 14, 'Sácame del lodo, y no sea yo sumergido: Sea yo libertado de los que me aborrecen, y del profundo de las aguas.', 'water'),
('Old', 'Salmos', 69, 15, 'No me anegue el ímpetu de las aguas, Ni me suerba la hondura, Ni el pozo cierre sobre mí su boca.', 'water'),
('Old', 'Salmos', 69, 16, 'Oyeme, Jehová, porque apacible es tu misericordia; Mírame conforme á la multitud de tus miseraciones.', 'water'),
('Old', 'Salmos', 69, 17, 'Y no escondas tu rostro de tu siervo; Porque estoy angustiado; apresúrate, óyeme.', 'water'),
('Old', 'Salmos', 69, 18, 'Acércate á mi alma, redímela: Líbrame á causa de mis enemigos.', 'water'),
('Old', 'Salmos', 69, 19, 'Tú sabes mi afrenta, y mi confusión, y mi oprobio: Delante de ti están todos mis enemigos.', 'water'),
('Old', 'Salmos', 69, 20, 'La afrenta ha quebrantado mi corazón, y estoy acongojado: Y esperé quien se compadeciese de mí, y no lo hubo: Y consoladores, y ninguno hallé.', 'water'),
('Old', 'Salmos', 69, 21, 'Pusiéronme además hiel por comida, Y en mi sed me dieron á beber vinagre.', 'water'),
('Old', 'Salmos', 69, 22, 'Sea su mesa delante de ellos por lazo, Y lo que es para bien por tropiezo.', 'water'),
('Old', 'Salmos', 69, 23, 'Sean oscurecidos sus ojos para ver, Y haz siempre titubear sus lomos.', 'water'),
('Old', 'Salmos', 69, 24, 'Derrama sobre ellos tu ira, Y el furor de tu enojo los alcance.', 'water'),
('Old', 'Salmos', 69, 25, 'Sea su palacio asolado: En sus tiendas no haya morador.', 'water'),
('Old', 'Salmos', 69, 26, 'Porque persiguieron al que tú heriste; Y cuentan del dolor de los que tú llagaste.', 'water'),
('Old', 'Salmos', 69, 27, 'Pon maldad sobre su maldad, Y no entren en tu justicia.', 'water'),
('Old', 'Salmos', 69, 28, 'Sean raídos del libro de los vivientes, Y no sean escritos con los justos.', 'water'),
('Old', 'Salmos', 69, 29, 'Y yo afligido y dolorido, Tu salud, oh Dios, me defenderá.', 'water'),
('Old', 'Salmos', 69, 30, 'Alabaré yo el nombre de Dios con cántico, Ensalzarélo con alabanza.', 'water'),
('Old', 'Salmos', 69, 31, 'Y agradará á Jehová más que sacrificio de buey, O becerro que echa cuernos y uñas.', 'water'),
('Old', 'Salmos', 69, 32, 'Veránlo los humildes, y se gozarán; Buscad á Dios, y vivirá vuestro corazón.', 'water'),
('Old', 'Salmos', 69, 33, 'Porque Jehová oye á los menesterosos, Y no menosprecia á sus prisioneros.', 'water'),
('Old', 'Salmos', 69, 34, 'Alábenlo los cielos y la tierra, Los mares, y todo lo que se mueve en ellos.', 'water'),
('Old', 'Salmos', 69, 35, 'Porque Dios guardará á Sión, y reedificará las ciudades de Judá; Y habitarán allí, y la poseerán.', 'water'),
('Old', 'Salmos', 69, 36, 'Y la simiente de sus siervos la heredará, Y los que aman su nombre habitarán en ella.', 'water'),
('Old', 'Salmos', 70, 1, 'OH Dios, acude á librarme; Apresúrate, oh Dios, á socorrerme.', 'water'),
('Old', 'Salmos', 70, 2, 'Sean avergonzados y confusos Los que buscan mi vida; Sean vueltos atrás y avergonzados Los que mi mal desean.', 'water'),
('Old', 'Salmos', 70, 3, 'Sean vueltos, en pago de su afrenta hecha, Los que dicen: ¡Ah! ¡ah!', 'water'),
('Old', 'Salmos', 70, 4, 'Gócense y alégrense en ti todos los que te buscan; Y digan siempre los que aman tu salud: Engrandecido sea Dios.', 'water'),
('Old', 'Salmos', 70, 5, 'Yo estoy afligido y menesteroso; Apresúrate á mí, oh Dios: Ayuda mía y mi libertador eres tú; Oh Jehová, no te detengas.', 'water'),
('Old', 'Salmos', 71, 1, 'EN ti, oh Jehová, he esperado; No sea yo confuso para siempre.', 'water'),
('Old', 'Salmos', 71, 2, 'Hazme escapar, y líbrame en tu justicia: Inclina tu oído y sálvame.', 'water'),
('Old', 'Salmos', 71, 3, 'Séme por peña de estancia, adonde recurra yo continuamente: Mandado has que yo sea salvo; Porque tú eres mi roca, y mi fortaleza.', 'water'),
('Old', 'Salmos', 71, 4, 'Dios mío, líbrame de la mano del impío, De la mano del perverso y violento.', 'water'),
('Old', 'Salmos', 71, 5, 'Porque tú, oh Señor Jehová, eres mi esperanza: Seguridad mía desde mi juventud.', 'water'),
('Old', 'Salmos', 71, 6, 'Por ti he sido sustentado desde el vientre: De las entrañas de mi madre tú fuiste el que me sacaste: De ti será siempre mi alabanza.', 'water'),
('Old', 'Salmos', 71, 7, 'Como prodigio he sido á muchos; Y tú mi refugio fuerte.', 'water'),
('Old', 'Salmos', 71, 8, 'Sea llena mi boca de tu alabanza, De tu gloria todo el día.', 'water'),
('Old', 'Salmos', 71, 9, 'No me deseches en el tiempo de la vejez; Cuando mi fuerza se acabare, no me desampares.', 'water'),
('Old', 'Salmos', 71, 10, 'Porque mis enemigos han tratado de mí; Y los que acechan mi alma, consultaron juntamente.', 'water'),
('Old', 'Salmos', 71, 11, 'Diciendo: Dios lo ha dejado: Perseguid y tomadle, porque no hay quien le libre.', 'water'),
('Old', 'Salmos', 71, 12, 'Oh Dios, no te alejes de mí: Dios mío, acude presto á mi socorro.', 'water'),
('Old', 'Salmos', 71, 13, 'Sean avergonzados, fallezcan los adversarios de mi alma; Sean cubiertos de vergüenza y de confusión los que mi mal buscan.', 'water'),
('Old', 'Salmos', 71, 14, 'Mas yo siempre esperaré, Y añadiré sobre toda tu alabanza.', 'water'),
('Old', 'Salmos', 71, 15, 'Mi boca publicará tu justicia Y tu salud todo el día, Aunque no sé el número de ellas.', 'water'),
('Old', 'Salmos', 71, 16, 'Vendré á las valentías del Señor Jehová: Haré memoria de sola tu justicia.', 'water'),
('Old', 'Salmos', 71, 17, 'Oh Dios, enseñásteme desde mi mocedad; Y hasta ahora he manifestado tus maravillas.', 'water'),
('Old', 'Salmos', 71, 18, 'Y aun hasta la vejez y las canas; oh Dios, no me desampares, Hasta que denuncie tu brazo á la posteridad, Tus valentías á todos los que han de venir.', 'water'),
('Old', 'Salmos', 71, 19, 'Y tu justicia, oh Dios, hasta lo excelso; Porque has hecho grandes cosas: Oh Dios, ¿quién como tú?', 'water'),
('Old', 'Salmos', 71, 20, 'Tú, que me has hecho ver muchas angustias y males, Volverás á darme vida, Y de nuevo me levantarás de los abismos de la tierra.', 'water'),
('Old', 'Salmos', 71, 21, 'Aumentarás mi grandeza, Y volverás á consolarme.', 'water'),
('Old', 'Salmos', 71, 22, 'Asimismo yo te alabaré con instrumento de salterio, Oh Dios mío: tu verdad cantaré yo á ti en el arpa, Oh Santo de Israel.', 'water'),
('Old', 'Salmos', 71, 23, 'Mis labios cantarán cuando á ti salmeare, Y mi alma, á la cual redimiste.', 'water');