INSERT INTO scriptures (testament, book_name, chapter, verse_number, content, visual_theme) VALUES
('Old', 'Salmos', 71, 24, 'Mi lengua hablará también de tu justicia todo el día: Por cuanto fueron avergonzados, porque fueron confusos los que mi mal procuraban.', 'water'),
('Old', 'Salmos', 72, 1, 'OH Dios, da tus juicios al rey, Y tu justicia al hijo del rey.', 'water'),
('Old', 'Salmos', 72, 2, 'El juzgará tu pueblo con justicia, Y tus afligidos con juicio.', 'water'),
('Old', 'Salmos', 72, 3, 'Los montes llevarán paz al pueblo, Y los collados justicia.', 'water'),
('Old', 'Salmos', 72, 4, 'Juzgará los afligidos del pueblo, Salvará los hijos del menesteroso, Y quebrantará al violento.', 'water'),
('Old', 'Salmos', 72, 5, 'Temerte han mientras duren el sol Y la luna, por generación de generaciones.', 'water'),
('Old', 'Salmos', 72, 6, 'Descenderá como la lluvia sobre la hierba cortada; Como el rocío que destila sobre la tierra.', 'water'),
('Old', 'Salmos', 72, 7, 'Florecerá en sus día justicia, Y muchedumbre de paz, hasta que no haya luna.', 'water'),
('Old', 'Salmos', 72, 8, 'Y dominará de mar á mar, Y desde el río hasta los cabos de la tierra.', 'water'),
('Old', 'Salmos', 72, 9, 'Delante de él se postrarán los Etiopes; Y sus enemigos lamerán la tierra.', 'water'),
('Old', 'Salmos', 72, 10, 'Los reyes de Tharsis y de las islas traerán presentes: Los reyes de Sheba y de Seba ofrecerán dones.', 'water'),
('Old', 'Salmos', 72, 11, 'Y arrodillarse han á él todos los reyes; Le servirán todas las gentes.', 'water'),
('Old', 'Salmos', 72, 12, 'Porque él librará al menesteroso que clamare, Y al afligido que no tuviere quien le socorra.', 'water'),
('Old', 'Salmos', 72, 13, 'Tendrá misericordia del pobre y del menesteroso, Y salvará las almas de los pobres.', 'water'),
('Old', 'Salmos', 72, 14, 'De engaño y de violencia redimirá sus almas: Y la sangre de ellos será preciosa en sus ojos.', 'water'),
('Old', 'Salmos', 72, 15, 'Y vivirá, y darásele del oro de Seba; Y oraráse por él continuamente; Todo el día se le bendecirá.', 'water'),
('Old', 'Salmos', 72, 16, 'Será echado un puño de grano en tierra, en las cumbres de los montes; Su fruto hará ruido como el Líbano, Y los de la ciudad florecerán como la hierba de la tierra.', 'water'),
('Old', 'Salmos', 72, 17, 'Será su nombre para siempre, Perpetuaráse su nombre mientras el sol dure: Y benditas serán en él todas las gentes: Llamarlo han bienaventurado.', 'water'),
('Old', 'Salmos', 72, 18, 'Bendito Jehová Dios, el Dios de Israel, Que solo hace maravillas.', 'water'),
('Old', 'Salmos', 72, 19, 'Y bendito su nombre glorioso para siempre: Y toda la tierra sea llena de su gloria. Amén y Amén.', 'water'),
('Old', 'Salmos', 72, 20, 'Acábanse las oraciones de David, hijo de Isaí.', 'water'),
('Old', 'Salmos', 73, 1, 'CIERTAMENTE bueno es Dios á Israel, A los limpios de corazón.', 'water'),
('Old', 'Salmos', 73, 2, 'Mas yo, casi se deslizaron mis pies; Por poco resbalaron mis pasos.', 'water'),
('Old', 'Salmos', 73, 3, 'Porque tuve envidia de los insensatos, Viendo la prosperidad de los impíos.', 'water'),
('Old', 'Salmos', 73, 4, 'Porque no hay ataduras para su muerte; Antes su fortaleza está entera.', 'water'),
('Old', 'Salmos', 73, 5, 'No están ellos en el trabajo humano; Ni son azotados con los otros hombres.', 'water'),
('Old', 'Salmos', 73, 6, 'Por tanto soberbia los corona: Cúbrense de vestido de violencia.', 'water'),
('Old', 'Salmos', 73, 7, 'Sus ojos están salidos de gruesos: Logran con creces los antojos del corazón.', 'water'),
('Old', 'Salmos', 73, 8, 'Soltáronse, y hablan con maldad de hacer violencia; Hablan con altanería.', 'water'),
('Old', 'Salmos', 73, 9, 'Ponen en el cielo su boca, Y su lengua pasea la tierra.', 'water'),
('Old', 'Salmos', 73, 10, 'Por eso su pueblo vuelve aquí, Y aguas de lleno le son exprimidas.', 'water'),
('Old', 'Salmos', 73, 11, 'Y dicen: ¿Cómo sabe Dios? ¿Y hay conocimiento en lo alto?', 'water'),
('Old', 'Salmos', 73, 12, 'He aquí estos impíos, Sin ser turbados del mundo, alcanzaron riquezas.', 'water'),
('Old', 'Salmos', 73, 13, 'Verdaderamente en vano he limpiado mi corazón, Y lavado mis manos en inocencia;', 'water'),
('Old', 'Salmos', 73, 14, 'Pues he sido azotado todo el día, Y empezaba mi castigo por las mañanas.', 'water'),
('Old', 'Salmos', 73, 15, 'Si dijera yo, Discurriré de esa suerte; He aquí habría negado la nación de tus hijos:', 'water'),
('Old', 'Salmos', 73, 16, 'Pensaré pues para saber esto: Es á mis ojos duro trabajo,', 'water'),
('Old', 'Salmos', 73, 17, 'Hasta que venido al santuario de Dios, Entenderé la postrimería de ellos.', 'water'),
('Old', 'Salmos', 73, 18, 'Ciertamente los has puesto en deslizaderos; En asolamientos los harás caer.', 'water'),
('Old', 'Salmos', 73, 19, '¡Cómo han sido asolados! ¡cuán en un punto! Acabáronse, fenecieron con turbaciones.', 'water'),
('Old', 'Salmos', 73, 20, 'Como sueño del que despierta, Así, Señor, cuando despertares, menospreciarás sus apariencias.', 'water'),
('Old', 'Salmos', 73, 21, 'Desazonóse á la verdad mi corazón, Y en mis riñones sentía punzadas.', 'water'),
('Old', 'Salmos', 73, 22, 'Mas yo era ignorante, y no entendía: Era como una bestia acerca de ti.', 'water'),
('Old', 'Salmos', 73, 23, 'Con todo, yo siempre estuve contigo: Trabaste de mi mano derecha.', 'water'),
('Old', 'Salmos', 73, 24, 'Hasme guiado según tu consejo, Y después me recibirás en gloria.', 'water'),
('Old', 'Salmos', 73, 25, '¿A quién tengo yo en los cielos? Y fuera de ti nada deseo en la tierra.', 'water'),
('Old', 'Salmos', 73, 26, 'Mi carne y mi corazón desfallecen: Mas la roca de mi corazón y mi porción es Dios para siempre.', 'water'),
('Old', 'Salmos', 73, 27, 'Porque he aquí, los que se alejan de ti perecerán: Tú cortarás á todo aquel que fornicando, de ti se aparta.', 'water'),
('Old', 'Salmos', 73, 28, 'Y en cuanto á mí, el acercarme á Dios es el bien: He puesto en el Señor Jehová mi esperanza, Para contar todas tus obras.', 'water'),
('Old', 'Salmos', 74, 1, '¿POR qué, oh Dios, nos has desechado para siempre? ¿Por qué ha humeado tu furor contra las ovejas de tu dehesa?', 'water'),
('Old', 'Salmos', 74, 2, 'Acuérdate de tu congregación, que adquiriste de antiguo, Cuando redimiste la vara de tu heredad; Este monte de Sión, donde has habitado.', 'water'),
('Old', 'Salmos', 74, 3, 'Levanta tus pies á los asolamientos eternos: A todo enemigo que ha hecho mal en el santuario.', 'water'),
('Old', 'Salmos', 74, 4, 'Tus enemigos han bramado en medio de tus sinagogas: Han puesto sus divisas por señas.', 'water'),
('Old', 'Salmos', 74, 5, 'Cualquiera se hacía famoso según que había levantado El hacha sobre los gruesos maderos.', 'water'),
('Old', 'Salmos', 74, 6, 'Y ahora con hachas y martillos Han quebrado todas sus entalladuras.', 'water'),
('Old', 'Salmos', 74, 7, 'Han puesto á fuego tus santuarios, Han profanado el tabernáculo de tu nombre echándolo á tierra.', 'water'),
('Old', 'Salmos', 74, 8, 'Dijeron en su corazón: Destruyámoslos de una vez; Han quemado todas las sinagogas de Dios en el tierra.', 'water'),
('Old', 'Salmos', 74, 9, 'No vemos ya nuestras señales: No hay más profeta; Ni con nosotros hay quien sepa hasta cuándo.', 'water'),
('Old', 'Salmos', 74, 10, '¿Hasta cuándo, oh Dios, el angustiador nos afrentará? ¿Ha de blasfemar el enemigo perpetuamente tu nombre?', 'water'),
('Old', 'Salmos', 74, 11, '¿Por qué retraes tu mano, y tu diestra? ¿Por qué la escondes dentro de tu seno?', 'water'),
('Old', 'Salmos', 74, 12, 'Empero Dios es mi rey ya de antiguo; El que obra saludes en medio de la tierra.', 'water'),
('Old', 'Salmos', 74, 13, 'Tú hendiste la mar con tu fortaleza: Quebrantaste cabezas de ballenas en las aguas.', 'water'),
('Old', 'Salmos', 74, 14, 'Tú magullaste las cabezas del leviathán; Dístelo por comida al pueblo de los desiertos.', 'water'),
('Old', 'Salmos', 74, 15, 'Tú abriste fuente y río; Tú secaste ríos impetuosos.', 'water'),
('Old', 'Salmos', 74, 16, 'Tuyo es el día, tuya también es la noche: Tú aparejaste la luna y el sol.', 'water'),
('Old', 'Salmos', 74, 17, 'Tú estableciste todos los términos de la tierra: El verano y el invierno tú los formaste.', 'water'),
('Old', 'Salmos', 74, 18, 'Acuerdáte de esto: que el enemigo ha dicho afrentas á Jehová, Y que el pueblo insensato ha blasfemado tu nombre.', 'water'),
('Old', 'Salmos', 74, 19, 'No entregues á las bestias el alma de tu tórtola: Y no olvides para siempre la congregación de tus afligidos.', 'water'),
('Old', 'Salmos', 74, 20, 'Mira al pacto: Porque las tenebrosidades de la tierra llenas están de habitaciones de violencia.', 'water'),
('Old', 'Salmos', 74, 21, 'No vuelva avergonzado el abatido: El afligido y el menesteroso alabarán tu nombre.', 'water'),
('Old', 'Salmos', 74, 22, 'Levántate, oh Dios, aboga tu causa: Acuérdate de cómo el insensato te injuria cada día.', 'water'),
('Old', 'Salmos', 74, 23, 'No olvides las voces de tus enemigos: El alboroto de los que se levantan contra ti sube continuamente.', 'water'),
('Old', 'Salmos', 75, 1, 'ALABARÉMOSTE, oh Dios, alabaremos; Que cercano está tu nombre: Cuenten tus maravillas.', 'water'),
('Old', 'Salmos', 75, 2, 'Cuando yo tuviere tiempo, Yo juzgaré rectamente.', 'water'),
('Old', 'Salmos', 75, 3, 'Arruinábase la tierra y sus moradores: Yo sostengo sus columnas. (Selah.)', 'water'),
('Old', 'Salmos', 75, 4, 'Dije á los insensatos: No os infatuéis; Y á los impíos: No levantéis el cuerno:', 'water'),
('Old', 'Salmos', 75, 5, 'No levantéis en alto vuestro cuerno; No habléis con cerviz erguida.', 'water'),
('Old', 'Salmos', 75, 6, 'Porque ni de oriente, ni de occidente, Ni del desierto viene el ensalzamiento.', 'water'),
('Old', 'Salmos', 75, 7, 'Mas Dios es el juez: A éste abate, y á aquel ensalza.', 'water'),
('Old', 'Salmos', 75, 8, 'Porque el cáliz está en la mano de Jehová, y el vino es tinto, Lleno de mistura; y él derrama del mismo: Ciertamente sus heces chuparán y beberán todos los impíos de la tierra.', 'water'),
('Old', 'Salmos', 75, 9, 'Mas yo anunciaré siempre, Cantaré alabanzas al Dios de Jacob.', 'water'),
('Old', 'Salmos', 75, 10, 'Y quebraré todos los cuernos de los pecadores: Los cuernos del justo serán ensalzados.', 'water'),
('Old', 'Salmos', 76, 1, 'DIOS es conocido en Judá: En Israel es grande su nombre.', 'water'),
('Old', 'Salmos', 76, 2, 'Y en Salem está su tabernáculo, Y su habitación en Sión.', 'water'),
('Old', 'Salmos', 76, 3, 'Allí quebró las saetas del arco, El escudo, y la espada, y tren de guerra. (Selah.)', 'water'),
('Old', 'Salmos', 76, 4, 'Ilustre eres tú; fuerte, más que los montes de caza.', 'water'),
('Old', 'Salmos', 76, 5, 'Los fuertes de corazón fueron despojados, durmieron su sueño; Y nada hallaron en sus manos todos los varones fuertes.', 'water'),
('Old', 'Salmos', 76, 6, 'A tu reprensión, oh Dios de Jacob, El carro y el caballo fueron entorpecidos.', 'water'),
('Old', 'Salmos', 76, 7, 'Tú, terrible eres tú: ¿Y quién parará delante de ti, en comenzando tu ira?', 'water'),
('Old', 'Salmos', 76, 8, 'Desde los cielos hiciste oir juicio; La tierra tuvo temor y quedó suspensa,', 'water'),
('Old', 'Salmos', 76, 9, 'Cuando te levantaste, oh Dios, al juicio, Para salvar á todos los mansos de la tierra. (Selah.)', 'water'),
('Old', 'Salmos', 76, 10, 'Ciertamente la ira del hombre te acarreará alabanza: Tú reprimirás el resto de las iras.', 'water'),
('Old', 'Salmos', 76, 11, 'Prometed, y pagad á Jehová vuestro Dios: Todos los que están alrededor de él, traigan presentes al Terrible.', 'water'),
('Old', 'Salmos', 76, 12, 'Cortará él el espíritu de los príncipes: Terrible es á los reyes de la tierra.', 'water'),
('Old', 'Salmos', 77, 1, 'CON mi voz clamé á Dios, A Dios clamé, y él me escuchará.', 'water'),
('Old', 'Salmos', 77, 2, 'Al Señor busqué en el día de mi angustia: Mi mal corría de noche y no cesaba: Mi alma rehusaba consuelo.', 'water'),
('Old', 'Salmos', 77, 3, 'Acordábame de Dios, y gritaba: Quejábame, y desmayaba mi espíritu. (Selah.)', 'water'),
('Old', 'Salmos', 77, 4, 'Tenías los párpados de mis ojos: Estaba yo quebrantado, y no hablaba.', 'water'),
('Old', 'Salmos', 77, 5, 'Consideraba los días desde el principio, Los años de los siglos.', 'water'),
('Old', 'Salmos', 77, 6, 'Acordábame de mis canciones de noche; Meditaba con mi corazón, Y mi espíritu inquiría.', 'water'),
('Old', 'Salmos', 77, 7, '¿Desechará el Señor para siempre, Y no volverá más á amar?', 'water'),
('Old', 'Salmos', 77, 8, '¿Hase acabado para siempre su misericordia? ¿Hase acabado la palabra suya para generación y generación?', 'water'),
('Old', 'Salmos', 77, 9, '¿Ha olvidado Dios el tener misericordia? ¿Ha encerrado con ira sus piedades? (Selah.)', 'water'),
('Old', 'Salmos', 77, 10, 'Y dije: Enfermedad mía es esta; Traeré pues á la memoria los años de la diestra del Altísimo.', 'water'),
('Old', 'Salmos', 77, 11, 'Acordaréme de las obras de JAH: Sí, haré yo memoria de tus maravillas antiguas.', 'water'),
('Old', 'Salmos', 77, 12, 'Y meditaré en todas tus obras, Y hablaré de tus hechos.', 'water'),
('Old', 'Salmos', 77, 13, 'Oh Dios, en santidad es tu camino: ¿Qué Dios grande como el Dios nuestro?', 'water'),
('Old', 'Salmos', 77, 14, 'Tú eres el Dios que hace maravillas: Tú hiciste notoria en los pueblos tu fortaleza.', 'water'),
('Old', 'Salmos', 77, 15, 'Con tu brazo redimiste á tu pueblo, A los hijos de Jacob y de José. (Selah.)', 'water'),
('Old', 'Salmos', 77, 16, 'Viéronte las aguas, oh Dios; Viéronte las aguas, temieron; Y temblaron los abismos.', 'water'),
('Old', 'Salmos', 77, 17, 'Las nubes echaron inundaciones de aguas; Tronaron los cielos, Y discurrieron tus rayos.', 'water'),
('Old', 'Salmos', 77, 18, 'Anduvo en derredor el sonido de tus truenos; Los relámpagos alumbraron el mundo; Estremecióse y tembló la tierra.', 'water'),
('Old', 'Salmos', 77, 19, 'En la mar fué tu camino, Y tus sendas en las muchas aguas; Y tus pisadas no fueron conocidas.', 'water'),
('Old', 'Salmos', 77, 20, 'Condujiste á tu pueblo como ovejas, Por mano de Moisés y de Aarón.', 'water'),
('Old', 'Salmos', 78, 1, 'ESCUCHA, pueblo mío, mi ley: Inclinad vuestro oído á las palabras de mi boca.', 'water'),
('Old', 'Salmos', 78, 2, 'Abriré mi boca en parábola; Hablaré cosas reservadas de antiguo:', 'water'),
('Old', 'Salmos', 78, 3, 'Las cuales hemos oído y entendido; Que nuestros padres nos las contaron.', 'water'),
('Old', 'Salmos', 78, 4, 'No las encubriremos á sus hijos, Contando á la generación venidera las alabanzas de Jehová, Y su fortaleza, y sus maravillas que hizo.', 'water'),
('Old', 'Salmos', 78, 5, 'El estableció testimonio en Jacob, Y pusó ley en Israel; La cual mandó á nuestros padres Que la notificasen á sus hijos;', 'water'),
('Old', 'Salmos', 78, 6, 'Para que lo sepa la generación venidera, y los hijos que nacerán; Y los que se levantarán, lo cuenten á sus hijos;', 'water'),
('Old', 'Salmos', 78, 7, 'A fin de que pongan en Dios su confianza, Y no se olviden de las obras de Dios, Y guarden sus mandamientos:', 'water'),
('Old', 'Salmos', 78, 8, 'Y no sean como sus padres, Generación contumaz y rebelde; Generación que no apercibió su corazón, Ni fué fiel para con Dios su espíritu.', 'water'),
('Old', 'Salmos', 78, 9, 'Los hijos de Ephraim armados, flecheros, Volvieron las espaldas el día de la batalla.', 'water'),
('Old', 'Salmos', 78, 10, 'No guardaron el pacto de Dios, Ni quisieron andar en su ley:', 'water'),
('Old', 'Salmos', 78, 11, 'Antes se olvidaron de sus obras, Y de sus maravillas que les había mostrado.', 'water'),
('Old', 'Salmos', 78, 12, 'Delante de sus padres hizo maravillas En la tierra de Egipto, en el campo de Zoán.', 'water'),
('Old', 'Salmos', 78, 13, 'Rompió la mar, é hízolos pasar; E hizo estar las aguas como en un montón.', 'water'),
('Old', 'Salmos', 78, 14, 'Y llevólos de día con nube, Y toda la noche con resplandor de fuego.', 'water'),
('Old', 'Salmos', 78, 15, 'Hendió las peñas en el desierto: Y dióles á beber como de grandes abismos;', 'water'),
('Old', 'Salmos', 78, 16, 'Pues sacó de la peña corrientes, E hizo descender aguas como ríos.', 'water'),
('Old', 'Salmos', 78, 17, 'Empero aun tornaron á pecar contra él, Enojando en la soledad al Altísimo.', 'water'),
('Old', 'Salmos', 78, 18, 'Pues tentaron á Dios en su corazón, Pidiendo comida á su gusto.', 'water'),
('Old', 'Salmos', 78, 19, 'Y hablaron contra Dios, Diciendo: ¿Podrá poner mesa en el desierto?', 'water'),
('Old', 'Salmos', 78, 20, 'He aquí ha herido la peña, y corrieron aguas, Y arroyos salieron ondeando: ¿Podrá también dar pan? ¿Aparejará carne á su pueblo?', 'water'),
('Old', 'Salmos', 78, 21, 'Por tanto oyó Jehová, é indignóse: Y encendióse el fuego contra Jacob, Y el furor subió también contra Israel;', 'water'),
('Old', 'Salmos', 78, 22, 'Por cuanto no habían creído á Dios, Ni habían confiado en su salud:', 'water'),
('Old', 'Salmos', 78, 23, 'A pesar de que mandó á las nubes de arriba, Y abrió las puertas de los cielos,', 'water'),
('Old', 'Salmos', 78, 24, 'E hizo llover sobre ellos maná para comer, Y dióles trigo de los cielos.', 'water'),
('Old', 'Salmos', 78, 25, 'Pan de nobles comió el hombre: Envióles comida á hartura.', 'water'),
('Old', 'Salmos', 78, 26, 'Movió el solano en el cielo, Y trajo con su fortaleza el austro.', 'water'),
('Old', 'Salmos', 78, 27, 'E hizo llover sobre ellos carne como polvo, Y aves de alas como arena de la mar.', 'water'),
('Old', 'Salmos', 78, 28, 'E hízolas caer en medio de su campo, Alrededor de sus tiendas.', 'water'),
('Old', 'Salmos', 78, 29, 'Y comieron, y hartáronse mucho: Cumplióles pues su deseo.', 'water'),
('Old', 'Salmos', 78, 30, 'No habían quitado de sí su deseo, Aun estaba su vianda en su boca,', 'water'),
('Old', 'Salmos', 78, 31, 'Cuando vino sobre ellos el furor de Dios, Y mató los más robustos de ellos, Y derribo los escogidos de Israel.', 'water'),
('Old', 'Salmos', 78, 32, 'Con todo esto pecaron aún, Y no dieron crédito á sus maravillas.', 'water'),
('Old', 'Salmos', 78, 33, 'Consumió por tanto en nada sus días, Y sus años en la tribulación.', 'water'),
('Old', 'Salmos', 78, 34, 'Si los mataba, entonces buscaban á Dios; Entonces se volvían solícitos en busca suya.', 'water'),
('Old', 'Salmos', 78, 35, 'Y acordábanse que Dios era su refugio. Y el Dios Alto su redentor.', 'water'),
('Old', 'Salmos', 78, 36, 'Mas le lisonjeaban con su boca, Y con su lengua le mentían:', 'water'),
('Old', 'Salmos', 78, 37, 'Pues sus corazones no eran rectos con él, Ni estuvieron firmes en su pacto.', 'water'),
('Old', 'Salmos', 78, 38, 'Empero él misericordioso, perdonaba la maldad, y no los destruía: Y abundó para apartar su ira, Y no despertó todo su enojo.', 'water'),
('Old', 'Salmos', 78, 39, 'Y acordóse que eran carne; Soplo que va y no vuelve.', 'water'),
('Old', 'Salmos', 78, 40, '¡Cuántas veces lo ensañaron en el desierto, Lo enojaron en la soledad!', 'water'),
('Old', 'Salmos', 78, 41, 'Y volvían, y tentaban á Dios, Y ponían límite al Santo de Israel.', 'water'),
('Old', 'Salmos', 78, 42, 'No se acordaron de su mano, Del día que los redimió de angustia;', 'water'),
('Old', 'Salmos', 78, 43, 'Cuando puso en Egipto sus señales, Y sus maravillas en el campo de Zoán;', 'water'),
('Old', 'Salmos', 78, 44, 'Y volvió sus ríos en sangre, Y sus corrientes, porque no bebiesen.', 'water'),
('Old', 'Salmos', 78, 45, 'Envió entre ellos una mistura de moscas que los comían, Y ranas que los destruyeron.', 'water'),
('Old', 'Salmos', 78, 46, 'Dió también al pulgón sus frutos, Y sus trabajos á la langosta.', 'water'),
('Old', 'Salmos', 78, 47, 'Sus viñas destruyó con granizo, Y sus higuerales con piedra;', 'water'),
('Old', 'Salmos', 78, 48, 'Y entregó al pedrisco sus bestias, Y al fuego sus ganados.', 'water'),
('Old', 'Salmos', 78, 49, 'Envió sobre ellos el furor de su saña, Ira y enojo y angustia, Con misión de malos ángeles.', 'water'),
('Old', 'Salmos', 78, 50, 'Dispuso el camino á su furor; No eximió la vida de ellos de la muerte, Sino que entregó su vida á la mortandad.', 'water'),
('Old', 'Salmos', 78, 51, 'E hirió á todo primogénito en Egipto, Las primicias de las fuerzas en las tiendas de Châm.', 'water'),
('Old', 'Salmos', 78, 52, 'Empero hizo salir á su pueblo como ovejas, Y llevólos por el desierto, como un rebaño.', 'water'),
('Old', 'Salmos', 78, 53, 'Y guiólos con seguridad, que no tuvieron miedo; Y la mar cubrió á sus enemigos.', 'water'),
('Old', 'Salmos', 78, 54, 'Metiólos después en los términos de su santuario, En este monte que ganó su mano derecha.', 'water'),
('Old', 'Salmos', 78, 55, 'Y echó las gentes de delante de ellos, Y repartióles una herencia con cuerdas; E hizo habitar en sus moradas á las tribus de Israel.', 'water'),
('Old', 'Salmos', 78, 56, 'Mas tentaron y enojaron al Dios Altísimo, Y no guardaron sus testimonios;', 'water'),
('Old', 'Salmos', 78, 57, 'Sino que se volvieron, y se rebelaron como sus padres: Volviéronse como arco engañoso.', 'water'),
('Old', 'Salmos', 78, 58, 'Y enojáronlo con sus altos, Y provocáronlo á celo con sus esculturas.', 'water'),
('Old', 'Salmos', 78, 59, 'Oyólo Dios, y enojóse, Y en gran manera aborreció á Israel.', 'water'),
('Old', 'Salmos', 78, 60, 'Dejó por tanto el tabernáculo de Silo, La tienda en que habitó entre los hombres;', 'water'),
('Old', 'Salmos', 78, 61, 'Y dió en cautividad su fortaleza, Y su gloria en mano del enemigo.', 'water'),
('Old', 'Salmos', 78, 62, 'Entregó también su pueblo á cuchillo, Y airóse contra su heredad.', 'water'),
('Old', 'Salmos', 78, 63, 'El fuego devoró sus mancebos, Y sus vírgenes no fueron loadas en cantos nupciales.', 'water'),
('Old', 'Salmos', 78, 64, 'Sus sacerdotes cayeron á cuchillo, Y sus viudas no lamentaron.', 'water'),
('Old', 'Salmos', 78, 65, 'Entonces despertó el Señor á la manera del que ha dormido, Como un valiente que grita excitado del vino:', 'water'),
('Old', 'Salmos', 78, 66, 'E hirió á sus enemigos en las partes posteriores: Dióles perpetua afrenta.', 'water'),
('Old', 'Salmos', 78, 67, 'Y desechó el tabernáculo de José, Y no escogió la tribu de Ephraim.', 'water'),
('Old', 'Salmos', 78, 68, 'Sino que escogió la tribu de Judá, El monte de Sión, al cual amó.', 'water'),
('Old', 'Salmos', 78, 69, 'Y edificó su santuario á manera de eminencia, Como la tierra que cimentó para siempre.', 'water'),
('Old', 'Salmos', 78, 70, 'Y eligió á David su siervo, Y tomólo de las majadas de las ovejas:', 'water'),
('Old', 'Salmos', 78, 71, 'De tras las paridas lo trajo, Para que apacentase á Jacob su pueblo, y á Israel su heredad.', 'water'),
('Old', 'Salmos', 78, 72, 'Y apacentólos con entereza de su corazón; Y pastoreólos con la pericia de sus manos.', 'water'),
('Old', 'Salmos', 79, 1, 'OH Dios, vinieron las gentes á tu heredad; El templo de tu santidad han contaminado; Pusieron á Jerusalem en montones.', 'water'),
('Old', 'Salmos', 79, 2, 'Dieron los cuerpos de tus siervos por comida á las aves de los cielos; La carne de tus santos á las bestias de la tierra.', 'water'),
('Old', 'Salmos', 79, 3, 'Derramaron su sangre como agua en los alrededores de Jerusalem; Y no hubo quien los enterrase.', 'water'),
('Old', 'Salmos', 79, 4, 'Somos afrentados de nuestros vecinos, Escarnecidos y burlados de los que están en nuestros alrededores.', 'water'),
('Old', 'Salmos', 79, 5, '¿Hasta cuándo, oh Jehová? ¿has de estar airado para siempre? ¿Arderá como fuego tu celo?', 'water'),
('Old', 'Salmos', 79, 6, 'Derrama tu ira sobre las gentes que no te conocen, Y sobre los reinos que no invocan tu nombre.', 'water'),
('Old', 'Salmos', 79, 7, 'Porque han consumido á Jacob, Y su morada han asolado.', 'water'),
('Old', 'Salmos', 79, 8, 'No recuerdes contra nosotros las iniquidades antiguas: Anticípennos presto tus misericordias, Porque estamos muy abatidos.', 'water'),
('Old', 'Salmos', 79, 9, 'Ayúdanos, oh Dios, salud nuestra, por la gloria de tu nombre: Y líbranos, y aplácate sobre nuestros pecados por amor de tu nombre.', 'water'),
('Old', 'Salmos', 79, 10, 'Porque dirán las gentes: ¿Dónde está su Dios? Sea notoria en las gentes, delante de nuestros ojos, La venganza de la sangre de tus siervos, que fué derramada.', 'water'),
('Old', 'Salmos', 79, 11, 'Entre ante tu acatamiento el gemido de los presos: Conforme á la grandeza de tu brazo preserva á los sentenciados á muerte.', 'water'),
('Old', 'Salmos', 79, 12, 'Y torna á nuestros vecinos en su seno siete tantos De su infamia, con que te han deshonrado, oh Jehová.', 'water'),
('Old', 'Salmos', 79, 13, 'Y nosotros, pueblo tuyo, y ovejas de tu dehesa, Te alabaremos para siempre: Por generación y generación cantaremos tus alabanzas.', 'water'),
('Old', 'Salmos', 80, 1, 'OH Pastor de Israel, escucha: Tú que pastoreas como á ovejas á José, Que estás entre querubines, resplandece.', 'water'),
('Old', 'Salmos', 80, 2, 'Despierta tu valentía delante de Ephraim, y de Benjamín, y de Manasés, Y ven á salvarnos.', 'water'),
('Old', 'Salmos', 80, 3, 'Oh Dios, haznos tornar; Y haz resplandecer tu rostro, y seremos salvos.', 'water'),
('Old', 'Salmos', 80, 4, 'Jehová, Dios de los ejércitos, ¿Hasta cuándo humearás tú contra la oración de tu pueblo?', 'water'),
('Old', 'Salmos', 80, 5, 'Dísteles á comer pan de lágrimas, Y dísteles á beber lágrimas en gran abundancia.', 'water'),
('Old', 'Salmos', 80, 6, 'Pusístenos por contienda á nuestros vecinos: Y nuestros enemigos se burlan entre sí.', 'water'),
('Old', 'Salmos', 80, 7, 'Oh Dios de los ejércitos, haznos tornar; Y haz resplandecer tu rostro, y seremos salvos.', 'water'),
('Old', 'Salmos', 80, 8, 'Hiciste venir una vid de Egipto: Echaste las gentes, y plantástela.', 'water'),
('Old', 'Salmos', 80, 9, 'Limpiaste sitio delante de ella, E hiciste arraigar sus raíces, y llenó la tierra.', 'water'),
('Old', 'Salmos', 80, 10, 'Los montes fueron cubiertos de su sombra; Y sus sarmientos como cedros de Dios.', 'water'),
('Old', 'Salmos', 80, 11, 'Extendió sus vástagos hasta la mar, Y hasta el río sus mugrones.', 'water'),
('Old', 'Salmos', 80, 12, '¿Por qué aportillaste sus vallados, Y la vendimian todos los que pasan por el camino?', 'water'),
('Old', 'Salmos', 80, 13, 'Estropeóla el puerco montés, Y pacióla la bestia del campo.', 'water'),
('Old', 'Salmos', 80, 14, 'Oh Dios de los ejércitos, vuelve ahora: Mira desde el cielo, y considera, y visita esta viña,', 'water'),
('Old', 'Salmos', 80, 15, 'Y la planta que plantó tu diestra, Y el renuevo que para ti corroboraste.', 'water'),
('Old', 'Salmos', 80, 16, 'Quemada á fuego está, asolada: Perezcan por la reprensión de tu rostro.', 'water'),
('Old', 'Salmos', 80, 17, 'Sea tu mano sobre el varón de tu diestra, Sobre el hijo del hombre que para ti corroboraste.', 'water'),
('Old', 'Salmos', 80, 18, 'Así no nos volveremos de ti: Vida nos darás, é invocaremos tu nombre.', 'water'),
('Old', 'Salmos', 80, 19, 'Oh Jehová, Dios de los ejércitos, haznos tornar; Haz resplandecer tu rostro, y seremos salvos.', 'water'),
('Old', 'Salmos', 81, 1, 'CANTAD á Dios, fortaleza nuestra: Al Dios de Jacob celebrad con júbilo.', 'water'),
('Old', 'Salmos', 81, 2, 'Tomad la canción, y tañed el adufe, El arpa deliciosa con el salterio.', 'water'),
('Old', 'Salmos', 81, 3, 'Tocad la trompeta en la nueva luna, En el día señalado, en el día de nuestra solemnidad.', 'water'),
('Old', 'Salmos', 81, 4, 'Porque estatuto es de Israel, Ordenanza del Dios de Jacob.', 'water'),
('Old', 'Salmos', 81, 5, 'Por testimonio en José lo ha constituído, Cuando salió por la tierra de Egipto; Donde oí lenguaje que no entendía.', 'water'),
('Old', 'Salmos', 81, 6, 'Aparté su hombro de debajo de la carga; Sus manos se quitaron de vasijas de barro.', 'water'),
('Old', 'Salmos', 81, 7, 'En la calamidad clamaste, y yo te libré: Te respondí en el secreto del trueno; Te probé sobre las aguas de Meriba. (Selah.)', 'water'),
('Old', 'Salmos', 81, 8, 'Oye, pueblo mío y te protestaré. Israel, si me oyeres,', 'water'),
('Old', 'Salmos', 81, 9, 'No habrá en ti dios ajeno, Ni te encorvarás á dios extraño.', 'water'),
('Old', 'Salmos', 81, 10, 'Yo soy Jehová tu Dios, Que te hice subir de la tierra de Egipto: Ensancha tu boca, y henchirla he.', 'water'),
('Old', 'Salmos', 81, 11, 'Mas mi pueblo no oyó mi voz, E Israel no me quiso á mí.', 'water'),
('Old', 'Salmos', 81, 12, 'Dejélos por tanto á la dureza de su corazón: Caminaron en sus consejos.', 'water'),
('Old', 'Salmos', 81, 13, '¡Oh, si me hubiera oído mi pueblo, Si en mis caminos hubiera Israel andado!', 'water'),
('Old', 'Salmos', 81, 14, 'En una nada habría yo derribado sus enemigos, Y vuelto mi mano sobre sus adversarios.', 'water'),
('Old', 'Salmos', 81, 15, 'Los aborrecedores de Jehová se le hubieran sometido; Y el tiempo de ellos fuera para siempre.', 'water'),
('Old', 'Salmos', 81, 16, 'Y Dios lo hubiera mantenido de grosura de trigo: Y de miel de la piedra te hubiera saciado.', 'water'),
('Old', 'Salmos', 82, 1, 'DIOS está en la reunión de los dioses; En medio de los dioses juzga.', 'water'),
('Old', 'Salmos', 82, 2, '¿Hasta cuándo juzgaréis injustamente, Y aceptaréis las personas de los impíos? (Selah.)', 'water'),
('Old', 'Salmos', 82, 3, 'Defended al pobre y al huérfano: Haced justicia al afligido y al menesteroso.', 'water'),
('Old', 'Salmos', 82, 4, 'Librad al afligido y al necesitado: Libradlo de mano de los impíos.', 'water'),
('Old', 'Salmos', 82, 5, 'No saben, no entienden, Andan en tinieblas: Vacilan todos los cimientos de la tierra.', 'water'),
('Old', 'Salmos', 82, 6, 'Yo dije: Vosotros sois dioses. E hijos todos vosotros del Altísimo.', 'water'),
('Old', 'Salmos', 82, 7, 'Empero como hombres moriréis. Y caeréis como cualquiera de los tiranos.', 'water'),
('Old', 'Salmos', 82, 8, 'Levántate, oh Dios, juzga la tierra: Porque tú heredarás en todas las gentes.', 'water'),
('Old', 'Salmos', 83, 1, 'OH Dios no tengas silencio: No calles, oh Dios, ni te estés quieto.', 'water'),
('Old', 'Salmos', 83, 2, 'Porque he aquí que braman tus enemigos; Y tus aborrecedores han alzado cabeza.', 'water'),
('Old', 'Salmos', 83, 3, 'Sobre tu pueblo han consultado astuta y secretamente, Y han entrado en consejo contra tus escondidos.', 'water'),
('Old', 'Salmos', 83, 4, 'Han dicho: Venid, y cortémoslos de ser pueblo, Y no haya más memoria del nombre de Israel.', 'water'),
('Old', 'Salmos', 83, 5, 'Por esto han conspirado de corazón á una, Contra ti han hecho liga;', 'water'),
('Old', 'Salmos', 83, 6, 'Los pabellones de los Idumeos y de los Ismaelitas, Moab y los Agarenos;', 'water'),
('Old', 'Salmos', 83, 7, 'Gebal, y Ammón, y Amalec; Los Filisteos con los habitadores de Tiro.', 'water'),
('Old', 'Salmos', 83, 8, 'También el Assur se ha juntado con ellos: Son por brazo á los hijos de Lot. (Selah.)', 'water'),
('Old', 'Salmos', 83, 9, 'Hazles como á Madián; Como á Sísara, como á Jabín en el arroyo de Cisón;', 'water'),
('Old', 'Salmos', 83, 10, 'Que perecieron en Endor, Fueron hechos muladar de la tierra.', 'water'),
('Old', 'Salmos', 83, 11, 'Pon á ellos y á sus capitanes como á Oreb y como á Zeeb; Y como á Zeba y como á Zalmunna, á todos sus príncipes;', 'water'),
('Old', 'Salmos', 83, 12, 'Que han dicho: Heredemos para nosotros Las moradas de Dios.', 'water'),
('Old', 'Salmos', 83, 13, 'Dios mío, ponlos como á torbellinos; Como á hojarascas delante del viento.', 'water'),
('Old', 'Salmos', 83, 14, 'Como fuego que quema el monte, Como llama que abrasa las breñas.', 'water'),
('Old', 'Salmos', 83, 15, 'Persíguelos así con tu tempestad, Y asómbralos con tu torbellino.', 'water'),
('Old', 'Salmos', 83, 16, 'Llena sus rostros de vergüenza; Y busquen tu nombre, oh Jehová.', 'water'),
('Old', 'Salmos', 83, 17, 'Sean afrentados y turbados para siempre; Y sean deshonrados, y perezcan.', 'water'),
('Old', 'Salmos', 83, 18, 'Y conozcan que tu nombre es JEHOVA; Tú solo Altísimo sobre toda la tierra.', 'water'),
('Old', 'Salmos', 84, 1, '¡CUAN amables son tus moradas, oh Jehová de los ejércitos!', 'water'),
('Old', 'Salmos', 84, 2, 'Codicia y aun ardientemente desea mi alma los atrios de Jehová: Mi corazón y mi carne cantan al Dios vivo.', 'water'),
('Old', 'Salmos', 84, 3, 'Aun el gorrión halla casa, Y la golondrina nido para sí, donde ponga sus pollos En tus altares, oh Jehová de los ejércitos, Rey mío, y Dios mío.', 'water'),
('Old', 'Salmos', 84, 4, 'Bienaventurados los que habitan en tu casa: Perpetuamente te alabarán (Selah.)', 'water'),
('Old', 'Salmos', 84, 5, 'Bienaventurado el hombre que tiene su fortaleza en ti; En cuyo corazón están tus caminos.', 'water'),
('Old', 'Salmos', 84, 6, 'Atravesando el valle de Baca pónenle por fuente, Cuando la lluvia llena los estanques.', 'water'),
('Old', 'Salmos', 84, 7, 'Irán de fortaleza en fortaleza, Verán á Dios en Sión.', 'water'),
('Old', 'Salmos', 84, 8, 'Jehová Dios de los ejércitos, oye mi oración: Escucha, oh Dios de Jacob (Selah.)', 'water'),
('Old', 'Salmos', 84, 9, 'Mira, oh Dios, escudo nuestro, Y pon los ojos en el rostro de tu ungido.', 'water'),
('Old', 'Salmos', 84, 10, 'Porque mejor es un día en tus atrios que mil fuera de ellos: Escogería antes estar á la puerta de la casa de mi Dios, Que habitar en las moradas de maldad.', 'water'),
('Old', 'Salmos', 84, 11, 'Porque sol y escudo es Jehová Dios: Gracia y gloria dará Jehová: No quitará el bien á los que en integridad andan.', 'water'),
('Old', 'Salmos', 84, 12, 'Jehová de los ejércitos, Dichoso el hombre que en ti confía.', 'water'),
('Old', 'Salmos', 85, 1, 'FUISTE propicio á tu tierra, oh Jehová: Volviste la cautividad de Jacob.', 'water'),
('Old', 'Salmos', 85, 2, 'Perdonaste la iniquidad de tu pueblo; Todos los pecados de ellos cubriste. (Selah.)', 'water'),
('Old', 'Salmos', 85, 3, 'Dejaste toda tu saña: Te volviste de la ira de tu furor.', 'water'),
('Old', 'Salmos', 85, 4, 'Vuélvenos, oh Dios, salud nuestra, Y haz cesar tu ira de sobre nosotros.', 'water'),
('Old', 'Salmos', 85, 5, '¿Estarás enojado contra nosotros para siempre? ¿Extenderás tu ira de generación en generación?', 'water'),
('Old', 'Salmos', 85, 6, '¿No volverás tú á darnos vida, Y tu pueblo se alegrará en ti?', 'water'),
('Old', 'Salmos', 85, 7, 'Muéstranos, oh Jehová, tu misericordia, Y danos tu salud.', 'water'),
('Old', 'Salmos', 85, 8, 'Escucharé lo que hablará el Dios Jehová: Porque hablará paz á su pueblo y á sus santos, Para que no se conviertan á la locura.', 'water'),
('Old', 'Salmos', 85, 9, 'Ciertamente cercana está su salud á los que le temen; Para que habite la gloria en nuestra tierra.', 'water'),
('Old', 'Salmos', 85, 10, 'La misericordia y la verdad se encontraron: La justicia y la paz se besaron.', 'water'),
('Old', 'Salmos', 85, 11, 'La verdad brotará de la tierra; Y la justicia mirará desde los cielos.', 'water'),
('Old', 'Salmos', 85, 12, 'Jehová dará también el bien; Y nuestra tierra dará su fruto.', 'water'),
('Old', 'Salmos', 85, 13, 'La justicia irá delante de él; Y sus pasos pondrá en camino.', 'water'),
('Old', 'Salmos', 86, 1, 'INCLINA, oh Jehová, tu oído, y óyeme; Porque estoy afligido y menesteroso.', 'water'),
('Old', 'Salmos', 86, 2, 'Guarda mi alma, porque soy pío: Salva tú, oh Dios mío, á tu siervo que en ti confía.', 'water'),
('Old', 'Salmos', 86, 3, 'Ten misericordia de mí, oh Jehová: Porque á ti clamo todo el día.', 'water'),
('Old', 'Salmos', 86, 4, 'Alegra el alma de tu siervo: Porque á ti, oh Señor, levanto mi alma.', 'water'),
('Old', 'Salmos', 86, 5, 'Porque tú, Señor, eres bueno y perdonador, Y grande en misericordia para con todos los que te invocan.', 'water'),
('Old', 'Salmos', 86, 6, 'Escucha, oh Jehová, mi oración, Y está atento á la voz de mis ruegos.', 'water'),
('Old', 'Salmos', 86, 7, 'En el día de mi angustia te llamaré: Porque tú me respondes.', 'water'),
('Old', 'Salmos', 86, 8, 'Oh Señor, ninguno hay como tú entre los dioses, Ni obras que igualen tus obras.', 'water'),
('Old', 'Salmos', 86, 9, 'Todas las gentes que hiciste vendrán y se humillarán delante de ti, Señor; Y glorificarán tu nombre.', 'water'),
('Old', 'Salmos', 86, 10, 'Porque tú eres grande, y hacedor de maravillas: Tú solo eres Dios.', 'water'),
('Old', 'Salmos', 86, 11, 'Enséñame, oh Jehová, tu camino; caminaré yo en tu verdad: Consolida mi corazón para que tema tu nombre.', 'water'),
('Old', 'Salmos', 86, 12, 'Te alabaré, oh Jehová Dios mío, con todo mi corazón; Y glorificaré tu nombre para siempre.', 'water'),
('Old', 'Salmos', 86, 13, 'Porque tu misericordia es grande para conmigo; Y has librado mi alma del hoyo profundo.', 'water'),
('Old', 'Salmos', 86, 14, 'Oh Dios, soberbios se levantaron contra mí, Y conspiración de fuertes ha buscado mi alma, Y no te pusieron delante de sí.', 'water'),
('Old', 'Salmos', 86, 15, 'Mas tú, Señor, Dios misericordioso y clemente, Lento para la ira, y grande en misericordia y verdad;', 'water'),
('Old', 'Salmos', 86, 16, 'Mírame, y ten misericordia de mí: Da tu fortaleza á tu siervo, Y guarda al hijo de tu sierva.', 'water'),
('Old', 'Salmos', 86, 17, 'Haz conmigo señal para bien, Y veánla los que me aborrecen, y sean avergonzados; Porque tú, Jehová, me ayudaste, y me consolaste.', 'water'),
('Old', 'Salmos', 87, 1, 'SU cimiento es en montes de santidad.', 'water'),
('Old', 'Salmos', 87, 2, 'Ama Jehová las puertas de Sión Más que todas las moradas de Jacob.', 'water'),
('Old', 'Salmos', 87, 3, 'Cosas ilustres son dichas de ti, Ciudad de Dios. (Selah.)', 'water'),
('Old', 'Salmos', 87, 4, 'Yo me acordaré de Rahab y de Babilonia entre los que me conocen: He aquí Palestina, y Tiro, con Etiopía: Este nació allá.', 'water'),
('Old', 'Salmos', 87, 5, 'Y de Sión se dirá: Este y aquél han nacido en ella; Y fortificarála el mismo Altísimo.', 'water'),
('Old', 'Salmos', 87, 6, 'Jehová contará cuando se escribieren los pueblos: Este nació allí. (Selah.)', 'water'),
('Old', 'Salmos', 87, 7, 'Y cantores y tañedores en ella dirán: Todas mis fuentes estarán en ti.', 'water'),
('Old', 'Salmos', 88, 1, 'OH Jehová, Dios de mi salud, Día y noche clamo delante de ti.', 'water'),
('Old', 'Salmos', 88, 2, 'Entre mi oración en tu presencia: Inclina tu oído á mi clamor.', 'water'),
('Old', 'Salmos', 88, 3, 'Porque mi alma está harta de males, Y mi vida cercana al sepulcro.', 'water'),
('Old', 'Salmos', 88, 4, 'Soy contado con los que descienden al hoyo, Soy como hombre sin fuerza:', 'water'),
('Old', 'Salmos', 88, 5, 'Libre entre los muertos, Como los matados que yacen en el sepulcro, Que no te acuerdas más de ellos, Y que son cortados de tu mano.', 'water'),
('Old', 'Salmos', 88, 6, 'Hasme puesto en el hoyo profundo, En tinieblas, en honduras.', 'water'),
('Old', 'Salmos', 88, 7, 'Sobre mí se ha acostado tu ira, Y me has afligido con todas tus ondas. (Selah.)', 'water'),
('Old', 'Salmos', 88, 8, 'Has alejado de mí mis conocidos: Hasme puesto por abominación á ellos: Encerrado estoy, y no puedo salir.', 'water'),
('Old', 'Salmos', 88, 9, 'Mis ojos enfermaron á causa de mi aflicción: Hete llamado, oh Jehová, cada día; He extendido á ti mis manos.', 'water'),
('Old', 'Salmos', 88, 10, '¿Harás tú milagro á los muertos? ¿Levantaránse los muertos para alabarte? (Selah.)', 'water'),
('Old', 'Salmos', 88, 11, '¿Será contada en el sepulcro tu misericordia, O tu verdad en la perdición?', 'water'),
('Old', 'Salmos', 88, 12, '¿Será conocida en las tinieblas tu maravilla, Ni tu justicia en la tierra del olvido?', 'water'),
('Old', 'Salmos', 88, 13, 'Mas yo á ti he clamado, oh Jehová; Y de mañana mi oración te previno.', 'water'),
('Old', 'Salmos', 88, 14, '¿Por qué, oh Jehová, desechas mi alma? ¿Por qué escondes de mí tu rostro?', 'water'),
('Old', 'Salmos', 88, 15, 'Yo soy afligido y menesteroso: Desde la mocedad he llevado tus terrores, he estado medroso.', 'water'),
('Old', 'Salmos', 88, 16, 'Sobre mí han pasado tus iras; Tus espantos me han cortado.', 'water'),
('Old', 'Salmos', 88, 17, 'Hanme rodeado como aguas de continuo; Hanme cercado á una.', 'water'),
('Old', 'Salmos', 88, 18, 'Has alejado de mí el enemigo y el compañero; Y mis conocidos se esconden en la tiniebla.', 'water'),
('Old', 'Salmos', 89, 1, 'LAS misericordias de Jehová cantaré perpetuamente; En generación y generación haré notoria tu verdad con mi boca.', 'water'),
('Old', 'Salmos', 89, 2, 'Porque dije: Para siempre será edificada misericordia; En los mismos cielos apoyarás tu verdad.', 'water'),
('Old', 'Salmos', 89, 3, 'Hice alianza con mi escogido; Juré á David mi siervo: diciendo.', 'water'),
('Old', 'Salmos', 89, 4, 'Para siempre confirmaré tu simiente, Y edificaré tu trono por todas las generaciones. (Selah.)', 'water'),
('Old', 'Salmos', 89, 5, 'Y celebrarán los cielos tu maravilla, oh Jehová; Tu verdad también en la congregación de los santos.', 'water'),
('Old', 'Salmos', 89, 6, 'Porque ¿quién en los cielos se igualará con Jehová? ¿Quién será semejante á Jehová entre los hijos de los potentados?', 'water'),
('Old', 'Salmos', 89, 7, 'Dios terrible en la grande congregación de los santos, Y formidable sobre todos cuantos están alrededor suyo.', 'water'),
('Old', 'Salmos', 89, 8, 'Oh Jehová, Dios de los ejércitos, ¿Quién como tú? Poderoso eres, Jehová, Y tu verdad está en torno de ti.', 'water'),
('Old', 'Salmos', 89, 9, 'Tú tienes dominio sobre la bravura de la mar: Cuando se levantan sus ondas, tú las sosiegas.', 'water'),
('Old', 'Salmos', 89, 10, 'Tú quebrantaste á Rahab como á un muerto: Con el brazo de tu fortaleza esparciste á tus enemigos.', 'water'),
('Old', 'Salmos', 89, 11, 'Tuyos los cielos, tuya también la tierra: El mundo y su plenitud, tú lo fundaste.', 'water'),
('Old', 'Salmos', 89, 12, 'Al aquilón y al austro tú los criaste: Tabor y Hermón cantarán en tu nombre.', 'water'),
('Old', 'Salmos', 89, 13, 'Tuyo el brazo con valentía; Fuerte es tu mano, ensalzada tu diestra.', 'water'),
('Old', 'Salmos', 89, 14, 'Justicia y juicio son el asiento de tu trono: Misericordia y verdad van delante de tu rostro.', 'water'),
('Old', 'Salmos', 89, 15, 'Bienaventurado el pueblo que sabe aclamarte: Andarán, oh Jehová, á la luz de tu rostro.', 'water'),
('Old', 'Salmos', 89, 16, 'En tu nombre se alegrarán todo el día; Y en tu justicia serán ensalzados.', 'water'),
('Old', 'Salmos', 89, 17, 'Porque tú eres la gloria de su fortaleza; Y por tu buena voluntad ensalzarás nuestro cuerno.', 'water'),
('Old', 'Salmos', 89, 18, 'Porque Jehová es nuestro escudo; Y nuestro rey es el Santo de Israel.', 'water'),
('Old', 'Salmos', 89, 19, 'Entonces hablaste en visión á tu santo, Y dijiste: Yo he puesto el socorro sobre valiente; He ensalzado un escogido de mi pueblo.', 'water'),
('Old', 'Salmos', 89, 20, 'Hallé á David mi siervo; Ungílo con el aceite de mi santidad.', 'water'),
('Old', 'Salmos', 89, 21, 'Mi mano será firme con él, Mi brazo también lo fortificará.', 'water'),
('Old', 'Salmos', 89, 22, 'No lo avasallará enemigo, Ni hijo de iniquidad lo quebrantará.', 'water'),
('Old', 'Salmos', 89, 23, 'Mas yo quebrantaré delante de él á sus enemigos, Y heriré á sus aborrecedores.', 'water'),
('Old', 'Salmos', 89, 24, 'Y mi verdad y mi misericordia serán con él; Y en mi nombre será ensalzado su cuerno.', 'water'),
('Old', 'Salmos', 89, 25, 'Asimismo pondré su mano en la mar, Y en los ríos su diestra.', 'water'),
('Old', 'Salmos', 89, 26, 'El me llamará: Mi padre eres tú, Mi Dios, y la roca de mi salud.', 'water'),
('Old', 'Salmos', 89, 27, 'Yo también le pondré por primogénito, Alto sobre los reyes de la tierra.', 'water'),
('Old', 'Salmos', 89, 28, 'Para siempre le conservaré mi misericordia; Y mi alianza será firme con él.', 'water'),
('Old', 'Salmos', 89, 29, 'Y pondré su simiente para siempre, Y su trono como los días de los cielos.', 'water'),
('Old', 'Salmos', 89, 30, 'Si dejaren sus hijos mi ley, Y no anduvieren en mis juicios;', 'water'),
('Old', 'Salmos', 89, 31, 'Si profanaren mis estatutos, Y no guardaren mis mandamientos;', 'water'),
('Old', 'Salmos', 89, 32, 'Entonces visitaré con vara su rebelión, Y con azotes sus iniquidades.', 'water'),
('Old', 'Salmos', 89, 33, 'Mas no quitaré de él mi misericordia, Ni falsearé mi verdad.', 'water'),
('Old', 'Salmos', 89, 34, 'No olvidaré mi pacto, Ni mudaré lo que ha salido de mis labios.', 'water'),
('Old', 'Salmos', 89, 35, 'Una vez he jurado por mi santidad, Que no mentiré á David.', 'water'),
('Old', 'Salmos', 89, 36, 'Su simiente será para siempre, Y su trono como el sol delante de mí.', 'water'),
('Old', 'Salmos', 89, 37, 'Como la luna será firme para siempre, Y como un testigo fiel en el cielo. (Selah.)', 'water'),
('Old', 'Salmos', 89, 38, 'Mas tú desechaste y menospreciaste á tu ungido; Y te has airado con él.', 'water'),
('Old', 'Salmos', 89, 39, 'Rompiste el pacto de tu siervo; Has profanado su corona hasta la tierra.', 'water'),
('Old', 'Salmos', 89, 40, 'Aportillaste todos sus vallados; Has quebrantado sus fortalezas.', 'water'),
('Old', 'Salmos', 89, 41, 'Menoscabáronle todos los que pasaron por el camino: Es oprobio á sus vecinos.', 'water'),
('Old', 'Salmos', 89, 42, 'Has ensalzado la diestra de sus enemigos; Has alegrado á todos sus adversarios.', 'water'),
('Old', 'Salmos', 89, 43, 'Embotaste asimismo el filo de su espada, Y no lo levantaste en la batalla.', 'water'),
('Old', 'Salmos', 89, 44, 'Hiciste cesar su brillo, Y echaste su trono por tierra.', 'water'),
('Old', 'Salmos', 89, 45, 'Has acortado los días de su juventud; Hasle cubierto de afrenta. (Selah.)', 'water'),
('Old', 'Salmos', 89, 46, '¿Hasta cuándo, oh Jehová? ¿te esconderás para siempre? ¿Arderá tu ira como el fuego?', 'water'),
('Old', 'Salmos', 89, 47, 'Acuérdate de cuán corto sea mi tiempo: ¿Por qué habrás criado en vano á todos los hijos del hombre?', 'water'),
('Old', 'Salmos', 89, 48, '¿Qué hombre vivirá y no verá muerte? ¿Librarás su vida del poder del sepulcro? (Selah.)', 'water'),
('Old', 'Salmos', 89, 49, 'Señor, ¿dónde están tus antiguas misericordias, Que juraste á David por tu verdad?', 'water'),
('Old', 'Salmos', 89, 50, 'Señor, acuérdate del oprobio de tus siervos; Oprobio que llevo yo en mi seno de muchos pueblos.', 'water'),
('Old', 'Salmos', 89, 51, 'Porque tus enemigos, oh Jehová, han deshonrado, Porque tus enemigos han deshonrado los pasos de tu ungido.', 'water'),
('Old', 'Salmos', 89, 52, 'Bendito Jehová para siempre. Amén, y Amén.', 'water'),
('Old', 'Salmos', 90, 1, 'SEñOR, tú nos has sido refugio En generación y en generación.', 'water'),
('Old', 'Salmos', 90, 2, 'Antes que naciesen los montes Y formases la tierra y el mundo, Y desde el siglo y hasta el siglo, tú eres Dios.', 'water'),
('Old', 'Salmos', 90, 3, 'Vuelves al hombre hasta ser quebrantado, Y dices: Convertíos, hijos de los hombres.', 'water'),
('Old', 'Salmos', 90, 4, 'Porque mil años delante de tus ojos, Son como el día de ayer, que pasó, Y como una de las vigilias de la noche.', 'water'),
('Old', 'Salmos', 90, 5, 'Háceslos pasar como avenida de aguas; son como sueño; Como la hierba que crece en la mañana:', 'water'),
('Old', 'Salmos', 90, 6, 'En la mañana florece y crece; A la tarde es cortada, y se seca.', 'water'),
('Old', 'Salmos', 90, 7, 'Porque con tu furor somos consumidos, Y con tu ira somos conturbados.', 'water'),
('Old', 'Salmos', 90, 8, 'Pusiste nuestras maldades delante de ti, Nuestros yerros á la luz de tu rostro.', 'water'),
('Old', 'Salmos', 90, 9, 'Porque todos nuestros días declinan á causa de tu ira; Acabamos nuestros años como un pensamiento.', 'water'),
('Old', 'Salmos', 90, 10, 'Los días de nuestra edad son setenta años; Que si en los más robustos son ochenta años, Con todo su fortaleza es molestia y trabajo; Porque es cortado presto, y volamos.', 'water'),
('Old', 'Salmos', 90, 11, '¿Quién conoce la fortaleza de tu ira, Y tu indignación según que debes ser temido?', 'water'),
('Old', 'Salmos', 90, 12, 'Enséñanos de tal modo á contar nuestros días, Que traigamos al corazón sabiduría.', 'water'),
('Old', 'Salmos', 90, 13, 'Vuélvete, oh Jehová: ¿hasta cuándo? Y aplácate para con tus siervos.', 'water'),
('Old', 'Salmos', 90, 14, 'Sácianos presto de tu misericordia: Y cantaremos y nos alegraremos todos nuestros días.', 'water'),
('Old', 'Salmos', 90, 15, 'Alégranos conforme á los días que nos afligiste, Y los años que vimos mal.', 'water'),
('Old', 'Salmos', 90, 16, 'Aparezca en tus siervos tu obra, Y tu gloria sobre sus hijos.', 'water'),
('Old', 'Salmos', 90, 17, 'Y sea la luz de Jehová nuestro Dios sobre nosotros: Y ordena en nosotros la obra de nuestras manos, La obra de nuestras manos confirma.', 'water'),
('Old', 'Salmos', 91, 1, 'EL que habita al abrigo del Altísimo, Morará bajo la sombra del Omnipotente.', 'water'),
('Old', 'Salmos', 91, 2, 'Diré yo á Jehová: Esperanza mía, y castillo mío; Mi Dios, en él confiaré.', 'water'),
('Old', 'Salmos', 91, 3, 'Y él te librará del lazo del cazador: De la peste destruidora.', 'water'),
('Old', 'Salmos', 91, 4, 'Con sus plumas te cubrirá, Y debajo de sus alas estarás seguro: Escudo y adarga es su verdad.', 'water'),
('Old', 'Salmos', 91, 5, 'No tendrás temor de espanto nocturno, Ni de saeta que vuele de día;', 'water'),
('Old', 'Salmos', 91, 6, 'Ni de pestilencia que ande en oscuridad, Ni de mortandad que en medio del día destruya.', 'water'),
('Old', 'Salmos', 91, 7, 'Caerán á tu lado mil, Y diez mil á tu diestra: Mas á ti no llegará.', 'water'),
('Old', 'Salmos', 91, 8, 'Ciertamente con tus ojos mirarás, Y verás la recompensa de los impíos.', 'water'),
('Old', 'Salmos', 91, 9, 'Porque tú has puesto á Jehová, que es mi esperanza. Al Altísimo por tu habitación,', 'water'),
('Old', 'Salmos', 91, 10, 'No te sobrevendrá mal, Ni plaga tocará tu morada.', 'water'),
('Old', 'Salmos', 91, 11, 'Pues que á sus ángeles mandará acerca de ti, Que te guarden en todos tus caminos.', 'water'),
('Old', 'Salmos', 91, 12, 'En las manos te llevarán, Porque tu pie no tropiece en piedra.', 'water'),
('Old', 'Salmos', 91, 13, 'Sobre el león y el basilisco pisarás; Hollarás al cachorro del león y al dragón.', 'water'),
('Old', 'Salmos', 91, 14, 'Por cuanto en mí ha puesto su voluntad, yo también lo libraré: Pondrélo en alto, por cuanto ha conocido mi nombre.', 'water'),
('Old', 'Salmos', 91, 15, 'Me invocará, y yo le responderé: Con él estare yo en la angustia: Lo libraré, y le glorificaré.', 'water'),
('Old', 'Salmos', 91, 16, 'Saciarélo de larga vida, Y mostraréle mi salud.', 'water'),
('Old', 'Salmos', 92, 1, 'BUENO es alabar á Jehová, Y cantar salmos á tu nombre, oh Altísimo;', 'water'),
('Old', 'Salmos', 92, 2, 'Anunciar por la mañana tu misericordia, Y tu verdad en las noches,', 'water'),
('Old', 'Salmos', 92, 3, 'En el decacordio y en el salterio, En tono suave con el arpa.', 'water'),
('Old', 'Salmos', 92, 4, 'Por cuanto me has alegrado, oh Jehová, con tus obras; En las obras de tus manos me gozo.', 'water'),
('Old', 'Salmos', 92, 5, '¡Cuán grandes son tus obras, oh Jehová! Muy profundos son tus pensamientos.', 'water'),
('Old', 'Salmos', 92, 6, 'El hombre necio no sabe, Y el insensato no entiende esto:', 'water'),
('Old', 'Salmos', 92, 7, 'Que brotan los impíos como la hierba, Y florecen todos los que obran iniquidad, Para ser destruídos para siempre.', 'water'),
('Old', 'Salmos', 92, 8, 'Mas tú, Jehová, para siempre eres Altísimo.', 'water'),
('Old', 'Salmos', 92, 9, 'Porque he aquí tus enemigos, oh Jehová, Porque he aquí, perecerán tus enemigos; Serán disipados todos los que obran maldad.', 'water'),
('Old', 'Salmos', 92, 10, 'Empero tú ensalzarás mi cuerno como el de unicornio: Seré ungido con aceite fresco.', 'water'),
('Old', 'Salmos', 92, 11, 'Y mirarán mis ojos sobre mis enemigos: Oirán mis oídos de los que se levantaron contra mí, de los malignos.', 'water'),
('Old', 'Salmos', 92, 12, 'El justo florecerá como la palma: Crecerá como cedro en el Líbano.', 'water'),
('Old', 'Salmos', 92, 13, 'Plantados en la casa de Jehová, En los atrios de nuestro Dios florecerán.', 'water'),
('Old', 'Salmos', 92, 14, 'Aun en la vejez fructificarán; Estarán vigorosos y verdes;', 'water'),
('Old', 'Salmos', 92, 15, 'Para anunciar que Jehová mi fortaleza es recto. Y que en él no hay injusticia.', 'water'),
('Old', 'Salmos', 93, 1, 'JEHOVA reina, vistióse de magnificencia, Vistióse Jehová, ciñose de fortaleza; Afirmó también el mundo, que no se moverá.', 'water'),
('Old', 'Salmos', 93, 2, 'Firme es tu trono desde entonces: Tú eres eternalmente.', 'water'),
('Old', 'Salmos', 93, 3, 'Alzaron los ríos, oh Jehová, Alzaron los ríos su sonido; Alzaron los ríos sus ondas.', 'water'),
('Old', 'Salmos', 93, 4, 'Jehová en las alturas es más poderoso Que el estruendo de las muchas aguas, Más que las recias ondas de la mar.', 'water'),
('Old', 'Salmos', 93, 5, 'Tus testimonios son muy firmes: La santidad conviene á tu casa, Oh Jehová, por los siglos y para siempre.', 'water'),
('Old', 'Salmos', 94, 1, 'JEHOVA, Dios de las venganzas, Dios de las venganzas, muéstrate.', 'water'),
('Old', 'Salmos', 94, 2, 'Ensálzate, oh Juez de la tierra: Da el pago á los soberbios.', 'water'),
('Old', 'Salmos', 94, 3, '¿Hasta cuándo los impíos, Hasta cuándo, oh Jehová, se gozarán los impíos?', 'water'),
('Old', 'Salmos', 94, 4, '¿Hasta cuándo pronunciarán, hablarán cosas duras, Y se vanagloriarán todos los que obran iniquidad?', 'water'),
('Old', 'Salmos', 94, 5, 'A tu pueblo, oh Jehová, quebrantan, Y á tu heredad afligen.', 'water'),
('Old', 'Salmos', 94, 6, 'A la viuda y al extanjero matan, Y á los huérfanos quitan la vida.', 'water'),
('Old', 'Salmos', 94, 7, 'Y dijeron: No verá JAH, Ni entenderá el Dios de Jacob.', 'water'),
('Old', 'Salmos', 94, 8, 'Entended, necios del pueblo; Y vosotros fatuos, ¿cuándo seréis sabios?', 'water'),
('Old', 'Salmos', 94, 9, 'El que plantó el oído, ¿no oirá? El que formó el ojo, ¿no verá?', 'water'),
('Old', 'Salmos', 94, 10, 'El que castiga las gentes, ¿no reprenderá? ¿No sabrá el que enseña al hombre la ciencia?', 'water'),
('Old', 'Salmos', 94, 11, 'Jehová conoce los pensamientos de los hombres, Que son vanidad.', 'water'),
('Old', 'Salmos', 94, 12, 'Bienaventurado el hombre á quien tú, JAH, castigares, Y en tu ley lo instruyeres;', 'water'),
('Old', 'Salmos', 94, 13, 'Para tranquilizarle en los días de aflicción, En tanto que para el impío se cava el hoyo.', 'water'),
('Old', 'Salmos', 94, 14, 'Porque no dejará Jehová su pueblo, Ni desamparará su heredad;', 'water'),
('Old', 'Salmos', 94, 15, 'Sino que el juicio será vuelto á justicia, Y en pos de ella irán todos los rectos de corazón.', 'water'),
('Old', 'Salmos', 94, 16, '¿Quién se levantará por mí contra los malignos? ¿Quién estará por mí contra los que obran iniquidad?', 'water'),
('Old', 'Salmos', 94, 17, 'Si no me ayudara Jehová, Presto morara mi alma en el silencio.', 'water'),
('Old', 'Salmos', 94, 18, 'Cuando yo decía: Mi pie resbala: Tu misericordia, oh Jehová, me sustentaba.', 'water'),
('Old', 'Salmos', 94, 19, 'En la multitud de mis pensamientos dentro de mí, Tus consolaciones alegraban mi alma.', 'water'),
('Old', 'Salmos', 94, 20, '¿Juntaráse contigo el trono de iniquidades, Que forma agravio en el mandamiento?', 'water'),
('Old', 'Salmos', 94, 21, 'Pónense en corros contra la vida del justo, Y condenan la sangre inocente.', 'water'),
('Old', 'Salmos', 94, 22, 'Mas Jehová me ha sido por refugio; Y mi Dios por roca de mi confianza.', 'water'),
('Old', 'Salmos', 94, 23, 'Y él hará tornar sobre ellos su iniquidad, Y los destruirá por su propia maldad; Los talará Jehová nuestro Dios.', 'water'),
('Old', 'Salmos', 95, 1, 'VENID, celebremos alegremente á Jehová: Cantemos con júbilo á la roca de nuestra salud.', 'water'),
('Old', 'Salmos', 95, 2, 'Lleguemos ante su acatamiento con alabanza; Aclamémosle con cánticos.', 'water'),
('Old', 'Salmos', 95, 3, 'Porque Jehová es Dios grande; Y Rey grande sobre todos los dioses.', 'water'),
('Old', 'Salmos', 95, 4, 'Porque en su mano están las profundidades de la tierra, Y las alturas de los montes son suyas.', 'water'),
('Old', 'Salmos', 95, 5, 'Suya también la mar, pues él la hizo; Y sus manos formaron la seca.', 'water'),
('Old', 'Salmos', 95, 6, 'Venid, adoremos y postrémonos; Arrodillémonos delante de Jehová nuestro hacedor.', 'water'),
('Old', 'Salmos', 95, 7, 'Porque él es nuestro Dios; Nosotros el pueblo de su dehesa, y ovejas de su mano. Si hoy oyereis su voz,', 'water'),
('Old', 'Salmos', 95, 8, 'No endurezcáis vuestro corazón como en Meriba, Como el día de Masa en el desierto;', 'water'),
('Old', 'Salmos', 95, 9, 'Donde me tentaron vuestros padres, Probáronme, y vieron mi obra.', 'water'),
('Old', 'Salmos', 95, 10, 'Cuarenta años estuve disgustado con la nación, Y dije: Pueblo es que divaga de corazón, Y no han conocido mis caminos.', 'water'),
('Old', 'Salmos', 95, 11, 'Por tanto juré en mi furor Que no entrarían en mi reposo.', 'water'),
('Old', 'Salmos', 96, 1, 'CANTAD á Jehová canción nueva; Cantad á Jehová, toda la tierra.', 'water'),
('Old', 'Salmos', 96, 2, 'Cantad á Jehová, bendecid su nombre: Anunciad de día en día su salud.', 'water'),
('Old', 'Salmos', 96, 3, 'Contad entre las gentes su gloria, En todos los pueblos sus maravillas.', 'water'),
('Old', 'Salmos', 96, 4, 'Porque grande es Jehová, y digno de suprema alabanza; Terrible sobre todos los dioses.', 'water'),
('Old', 'Salmos', 96, 5, 'Porque todos los dioses de los pueblos son ídolos: Mas Jehová hizo los cielos.', 'water'),
('Old', 'Salmos', 96, 6, 'Alabanza y magnificencia delante de él: Fortaleza y gloria en su santuario.', 'water'),
('Old', 'Salmos', 96, 7, 'Dad á Jehová, oh familias de los pueblos, Dad á Jehová la gloria y la fortaleza.', 'water'),
('Old', 'Salmos', 96, 8, 'Dad á Jehová la honra debida á su nombre: Tomad presentes, y venid á sus atrios.', 'water'),
('Old', 'Salmos', 96, 9, 'Encorvaos á Jehová en la hermosura de su santuario: Temed delante de él, toda la tierra.', 'water'),
('Old', 'Salmos', 96, 10, 'Decid en las gentes: Jehová reinó, También afirmó el mundo, no será conmovido: Juzgará á los pueblos en justicia.', 'water'),
('Old', 'Salmos', 96, 11, 'Alégrense los cielos, y gócese la tierra: Brame la mar y su plenitud.', 'water'),
('Old', 'Salmos', 96, 12, 'Regocíjese el campo, y todo lo que en él está: Entonces todos los árboles del bosque rebosarán de contento.', 'water'),
('Old', 'Salmos', 96, 13, 'Delante de Jehová que vino: Porque vino á juzgar la tierra. Juzgará al mundo con justicia, Y á los pueblos con su verdad.', 'water'),
('Old', 'Salmos', 97, 1, 'JEHOVA reinó: regocíjese la tierra: Alégrense las muchas islas.', 'water'),
('Old', 'Salmos', 97, 2, 'Nube y oscuridad alrededor de él: Justicia y juicio son el asiento de su trono.', 'water'),
('Old', 'Salmos', 97, 3, 'Fuego irá delante de él, Y abrasará en derredor sus enemigos.', 'water'),
('Old', 'Salmos', 97, 4, 'Sus relámpagos alumbraron el mundo: La tierra vió, y estremecióse.', 'water'),
('Old', 'Salmos', 97, 5, 'Los montes se derritieron como cera delante de Jehová, Delante del Señor de toda la tierra.', 'water'),
('Old', 'Salmos', 97, 6, 'Los cielos denunciaron su justicia, Y todos los pueblos vieron su gloria.', 'water'),
('Old', 'Salmos', 97, 7, 'Avergüéncense todos los que sirven á las imágenes de talla, Los que se alaban de los ídolos: Los dioses todos á él se encorven.', 'water'),
('Old', 'Salmos', 97, 8, 'Oyó Sión, y alegróse; Y las hijas de Judá, Oh Jehová, se gozaron por tus juicios.', 'water'),
('Old', 'Salmos', 97, 9, 'Porque tú, Jehová, eres alto sobre toda la tierra: Eres muy ensalzado sobre todos los dioses.', 'water'),
('Old', 'Salmos', 97, 10, 'Los que á Jehová amáis, aborreced el mal: Guarda él las almas de sus santos; De mano de los impíos los libra.', 'water'),
('Old', 'Salmos', 97, 11, 'Luz está sembrada para el justo, Y alegría para los rectos de corazón.', 'water'),
('Old', 'Salmos', 97, 12, 'Alegraos, justos, en Jehová: Y alabad la memoria de su santidad.', 'water'),
('Old', 'Salmos', 98, 1, 'CANTAD á Jehová canción nueva; Porque ha hecho maravillas: Su diestra lo ha salvado, y su santo brazo.', 'water'),
('Old', 'Salmos', 98, 2, 'Jehová ha hecho notoria su salud: En ojos de las gentes ha descubierto su justicia.', 'water'),
('Old', 'Salmos', 98, 3, 'Hase acordado de su misericordia y de su verdad para con la casa de Israel: Todos los términos de la tierra han visto la salud de nuestro Dios.', 'water'),
('Old', 'Salmos', 98, 4, 'Cantad alegres á Jehová, toda la tierra; Levantad la voz, y aplaudid, y salmead.', 'water'),
('Old', 'Salmos', 98, 5, 'Salmead á Jehová con arpa; Con arpa y voz de cántico.', 'water'),
('Old', 'Salmos', 98, 6, 'Aclamad con trompetas y sonidos De bocina delante del rey Jehová.', 'water'),
('Old', 'Salmos', 98, 7, 'Brame la mar y su plenitud; El mundo y los que en él habitan;', 'water'),
('Old', 'Salmos', 98, 8, 'Los ríos batan las manos; Los montes todos hagan regocijo,', 'water'),
('Old', 'Salmos', 98, 9, 'Delante de Jehová; porque vino á juzgar la tierra: Juzgará al mundo con justicia, Y á los pueblos con rectitud.', 'water'),
('Old', 'Salmos', 99, 1, 'JEHOVA reinó, temblarán los pueblos: El está sentado sobre los querubines, conmoveráse la tierra.', 'water'),
('Old', 'Salmos', 99, 2, 'Jehová en Sión es grande, Y ensalzado sobre todos los pueblos.', 'water'),
('Old', 'Salmos', 99, 3, 'Alaben tu nombre grande y tremendo: El es santo.', 'water'),
('Old', 'Salmos', 99, 4, 'Y la gloria del rey ama el juicio: Tú confirmas la rectitud; Tú has hecho en Jacob juicio y justicia.', 'water'),
('Old', 'Salmos', 99, 5, 'Ensalzad á Jehová nuestro Dios, Y encorvaos al estrado de sus pies: El es santo.', 'water'),
('Old', 'Salmos', 99, 6, 'Moisés y Aarón entre sus sacerdotes, Y Samuel entre los que invocaron su nombre; Invocaban á Jehová, y él les respondía.', 'water'),
('Old', 'Salmos', 99, 7, 'En columna de nube hablaba con ellos: Guardaban sus testimonios, y el estatuto que les había dado.', 'water'),
('Old', 'Salmos', 99, 8, 'Jehová Dios nuestro, tú les respondías: Tú les fuiste un Dios perdonador, Y vengador de sus obras.', 'water'),
('Old', 'Salmos', 99, 9, 'Ensalzad á Jehová nuestro Dios, Y encorvaos al monte de su santidad; Porque Jehová nuestro Dios es santo.', 'water'),
('Old', 'Salmos', 100, 1, 'CANTAD alegres á Dios, habitantes de toda la tierra.', 'water'),
('Old', 'Salmos', 100, 2, 'Servid á Jehová con alegría: Venid ante su acatamiento con regocijo.', 'water'),
('Old', 'Salmos', 100, 3, 'Reconoced que Jehová él es Dios: El nos hizo, y no nosotros á nosotros mismos. Pueblo suyo somos, y ovejas de su prado.', 'water'),
('Old', 'Salmos', 100, 4, 'Entrad por sus puertas con reconocimiento, Por sus atrios con alabanza: Alabadle, bendecid su nombre.', 'water'),
('Old', 'Salmos', 100, 5, 'Porque Jehová es bueno: para siempre es su misericordia, Y su verdad por todas las generaciones.', 'water'),
('Old', 'Salmos', 101, 1, 'MISERICORDIA y juicio cantaré: A ti cantaré yo, oh Jehová.', 'water'),
('Old', 'Salmos', 101, 2, 'Entenderé en el camino de la perfección Cuando vinieres á mí: En integridad de mi corazón andaré en medio de mi casa.', 'water'),
('Old', 'Salmos', 101, 3, 'No pondré delante de mis ojos cosa injusta: Aborrezco la obra de los que se desvían: Ninguno de ellos se allegará á mí.', 'water'),
('Old', 'Salmos', 101, 4, 'Corazón perverso se apartará de mí; No conoceré al malvado.', 'water'),
('Old', 'Salmos', 101, 5, 'Al que solapadamente infama á su prójimo, yo le cortaré; No sufriré al de ojos altaneros, y de corazón vanidoso.', 'water'),
('Old', 'Salmos', 101, 6, 'Mis ojos pondré en los fieles de la tierra, para que estén conmigo: El que anduviere en el camino de la perfección, éste me sevirá.', 'water'),
('Old', 'Salmos', 101, 7, 'No habitará dentro de mi casa el que hace fraude: El que habla mentiras no se afirmará delante de mis ojos.', 'water'),
('Old', 'Salmos', 101, 8, 'Por las mañanas cortaré á todos los impíos de la tierra; Para extirpar de la ciudad de Jehová á todos los que obraren iniquidad.', 'water'),
('Old', 'Salmos', 102, 1, 'JEHOVA, oye mi oración, Y venga mi clamor á ti.', 'water'),
('Old', 'Salmos', 102, 2, 'No escondas de mí tu rostro: en el día de mi angustia Inclina á mí tu oído; El día que te invocare, apresúrate á responderme.', 'water'),
('Old', 'Salmos', 102, 3, 'Porque mis días se han consumido como humo; Y mis huesos cual tizón están quemados.', 'water'),
('Old', 'Salmos', 102, 4, 'Mi corazón fué herido, y secóse como la hierba; Por lo cual me olvidé de comer mi pan.', 'water'),
('Old', 'Salmos', 102, 5, 'Por la voz de mi gemido Mis huesos se han pegado á mi carne.', 'water'),
('Old', 'Salmos', 102, 6, 'Soy semejante al pelícano del desierto; Soy como el buho de las soledades.', 'water'),
('Old', 'Salmos', 102, 7, 'Velo, y soy Como el pájaro solitario sobre el tejado.', 'water'),
('Old', 'Salmos', 102, 8, 'Cada día me afrentan mis enemigos; Los que se enfurecen contra mí, hanse contra mí conjurado.', 'water'),
('Old', 'Salmos', 102, 9, 'Por lo que como la ceniza á manera de pan, Y mi bebida mezclo con lloro,', 'water'),
('Old', 'Salmos', 102, 10, 'A causa de tu enojo y de tu ira; Pues me alzaste, y me has arrojado.', 'water'),
('Old', 'Salmos', 102, 11, 'Mis días son como la sombra que se va; Y heme secado como la hierba.', 'water'),
('Old', 'Salmos', 102, 12, 'Mas tú, Jehová, permanecerás para siempre, Y tu memoria para generación y generación.', 'water'),
('Old', 'Salmos', 102, 13, 'Tú levantándote, tendrás misericordia de Sión; Porque el tiempo de tener misericordia de ella, porque el plazo es llegado.', 'water'),
('Old', 'Salmos', 102, 14, 'Porque tus siervos aman sus piedras, Y del polvo de ella tienen compasión.', 'water'),
('Old', 'Salmos', 102, 15, 'Entonces temerán las gentes el nombre de Jehová, Y todos los reyes de la tierra tu gloria;', 'water'),
('Old', 'Salmos', 102, 16, 'Por cuanto Jehová habrá edificado á Sión, Y en su gloria será visto;', 'water'),
('Old', 'Salmos', 102, 17, 'Habrá mirado á la oración de los solitarios, Y no habrá desechado el ruego de ellos.', 'water'),
('Old', 'Salmos', 102, 18, 'Escribirse ha esto para la generación venidera: Y el pueblo que se criará, alabará á JAH.', 'water'),
('Old', 'Salmos', 102, 19, 'Porque miró de lo alto de su santuario; Jehová miró de los cielos á la tierra,', 'water'),
('Old', 'Salmos', 102, 20, 'Para oir el gemido de los presos, Para soltar á los sentenciados á muerte;', 'water'),
('Old', 'Salmos', 102, 21, 'Porque cuenten en Sión el nombre de Jehová, Y su alabanza en Jerusalem,', 'water'),
('Old', 'Salmos', 102, 22, 'Cuando los pueblos se congregaren en uno, Y los reinos, para servir á Jehová.', 'water'),
('Old', 'Salmos', 102, 23, 'El afligió mi fuerza en el camino; Acortó mis días.', 'water'),
('Old', 'Salmos', 102, 24, 'Dije: Dios mío, no me cortes en el medio de mis días: Por generación de generaciones son tus años.', 'water'),
('Old', 'Salmos', 102, 25, 'Tú fundaste la tierra antiguamente, Y los cielos son obra de tus manos.', 'water'),
('Old', 'Salmos', 102, 26, 'Ellos perecerán, y tú permanecerás; Y todos ellos como un vestido se envejecerán; Como una ropa de vestir los mudarás, y serán mudados:', 'water'),
('Old', 'Salmos', 102, 27, 'Mas tú eres el mismo, Y tus años no se acabarán.', 'water'),
('Old', 'Salmos', 102, 28, 'Los hijos de tus siervos habitarán, Y su simiente será afirmada delante de ti.', 'water'),
('Old', 'Salmos', 103, 1, 'BENDICE, alma mía á Jehová; Y bendigan todas mis entrañas su santo nombre.', 'water'),
('Old', 'Salmos', 103, 2, 'Bendice, alma mía, á Jehová, Y no olvides ninguno de sus beneficios.', 'water'),
('Old', 'Salmos', 103, 3, 'El es quien perdona todas tus iniquidades, El que sana todas tus dolencias;', 'water'),
('Old', 'Salmos', 103, 4, 'El que rescata del hoyo tu vida, El que te corona de favores y misericordias;', 'water'),
('Old', 'Salmos', 103, 5, 'El que sacia de bien tu boca De modo que te rejuvenezcas como el águila.', 'water'),
('Old', 'Salmos', 103, 6, 'Jehová el que hace justicia Y derecho á todos los que padecen violencia.', 'water'),
('Old', 'Salmos', 103, 7, 'Sus caminos notificó á Moisés, Y á los hijos de Israel sus obras.', 'water'),
('Old', 'Salmos', 103, 8, 'Misericordioso y clemente es Jehová; Lento para la ira, y grande en misericordia.', 'water'),
('Old', 'Salmos', 103, 9, 'No contenderá para siempre, Ni para siempre guardará el enojo.', 'water'),
('Old', 'Salmos', 103, 10, 'No ha hecho con nosotros conforme á nuestras iniquidades; Ni nos ha pagado conforme á nuestros pecados.', 'water'),
('Old', 'Salmos', 103, 11, 'Porque como la altura de los cielos sobre la tierra, Engrandeció su misericordia sobre los que le temen.', 'water'),
('Old', 'Salmos', 103, 12, 'Cuanto está lejos el oriente del occidente, Hizo alejar de nosotros nuestras rebeliones.', 'water'),
('Old', 'Salmos', 103, 13, 'Como el padre se compadece de los hijos, Se compadece Jehová de los que le temen.', 'water'),
('Old', 'Salmos', 103, 14, 'Porque él conoce nuestra condición; Acuérdase que somos polvo.', 'water'),
('Old', 'Salmos', 103, 15, 'El hombre, como la hierba son sus días, Florece como la flor del campo.', 'water'),
('Old', 'Salmos', 103, 16, 'Que pasó el viento por ella, y pereció: Y su lugar no la conoce más.', 'water'),
('Old', 'Salmos', 103, 17, 'Mas la misericordia de Jehová desde el siglo y hasta el siglo sobre los que le temen, Y su justicia sobre los hijos de los hijos;', 'water'),
('Old', 'Salmos', 103, 18, 'Sobre los que guardan su pacto, Y los que se acuerdan de sus mandamientos para ponerlos por obra.', 'water'),
('Old', 'Salmos', 103, 19, 'Jehová afirmó en los cielos su trono; Y su reino domina sobre todos.', 'water'),
('Old', 'Salmos', 103, 20, 'Bendecid á Jehová, vosotros sus ángeles, Poderosos en fortaleza, que ejecutáis su palabra, Obedeciendo á la voz de su precepto.', 'water'),
('Old', 'Salmos', 103, 21, 'Bendecid á Jehová, vosotros todos sus ejércitos, Ministros suyos, que hacéis su voluntad.', 'water'),
('Old', 'Salmos', 103, 22, 'Bendecid á Jehová, vosotras todas sus obras, En todos los lugares de su señorío. Bendice, alma mía á Jehová.', 'water'),
('Old', 'Salmos', 104, 1, 'BENDICE, alma mía, á Jehová. Jehová, Dios mío, mucho te has engrandecido; Haste vestido de gloria y de magnificencia.', 'water'),
('Old', 'Salmos', 104, 2, 'El que se cubre de luz como de vestidura, Que extiende los cielos como una cortina;', 'water'),
('Old', 'Salmos', 104, 3, 'Que establece sus aposentos entre las aguas; El que pone las nubes por su carroza, El que anda sobre las alas del viento;', 'water'),
('Old', 'Salmos', 104, 4, 'El que hace á sus ángeles espíritus, Sus ministros al fuego flameante.', 'water'),
('Old', 'Salmos', 104, 5, 'El fundó la tierra sobre sus basas; No será jamás removida.', 'water'),
('Old', 'Salmos', 104, 6, 'Con el abismo, como con vestido, la cubriste; Sobre los montes estaban las aguas.', 'water'),
('Old', 'Salmos', 104, 7, 'A tu reprensión huyeron; Al sonido de tu trueno se apresuraron;', 'water'),
('Old', 'Salmos', 104, 8, 'Subieron los montes, descendieron los valles, Al lugar que tú les fundaste.', 'water'),
('Old', 'Salmos', 104, 9, 'Pusísteles término, el cual no traspasarán; Ni volverán á cubrir la tierra.', 'water'),
('Old', 'Salmos', 104, 10, 'Tú eres el que envías las fuentes por los arroyos; Van entre los montes.', 'water'),
('Old', 'Salmos', 104, 11, 'Abrevan á todas las bestias del campo: Quebrantan su sed los asnos montaraces.', 'water'),
('Old', 'Salmos', 104, 12, 'Junto á aquellos habitarán las aves de los cielos; Entre las ramas dan voces.', 'water'),
('Old', 'Salmos', 104, 13, 'El que riega los montes desde sus aposentos: Del fruto de sus obras se sacia la tierra.', 'water'),
('Old', 'Salmos', 104, 14, 'El que hace producir el heno para las bestias, Y la hierba para el servicio del hombre; Sacando el pan de la tierra.', 'water'),
('Old', 'Salmos', 104, 15, 'Y el vino que alegra el corazón del hombre, Y el aceite que hace lucir el rostro, Y el pan que sustenta el corazón del hombre.', 'water'),
('Old', 'Salmos', 104, 16, 'Llénanse de jugo los árboles de Jehová, Los cedros del Líbano que él plantó.', 'water'),
('Old', 'Salmos', 104, 17, 'Allí anidan las aves; En las hayas hace su casa la cigüeña.', 'water'),
('Old', 'Salmos', 104, 18, 'Los montes altos para las cabras monteses; Las peñas, madrigueras para los conejos.', 'water'),
('Old', 'Salmos', 104, 19, 'Hizo la luna para los tiempos: El sol conoce su ocaso.', 'water'),
('Old', 'Salmos', 104, 20, 'Pone las tinieblas, y es la noche: En ella corretean todas las bestias de la selva.', 'water'),
('Old', 'Salmos', 104, 21, 'Los leoncillos braman á la presa, Y para buscar de Dios su comida.', 'water'),
('Old', 'Salmos', 104, 22, 'Sale el sol, recógense, Y échanse en sus cuevas.', 'water'),
('Old', 'Salmos', 104, 23, 'Sale el hombre á su hacienda, Y á su labranza hasta la tarde.', 'water'),
('Old', 'Salmos', 104, 24, '¡Cuán muchas son tus obras, oh Jehová! Hiciste todas ellas con sabiduría: La tierra está llena de tus beneficios.', 'water'),
('Old', 'Salmos', 104, 25, 'Asimismo esta gran mar y ancha de términos: En ella pescados sin número, Animales pequeños y grandes.', 'water'),
('Old', 'Salmos', 104, 26, 'Allí andan navíos; Allí este leviathán que hiciste para que jugase en ella.', 'water'),
('Old', 'Salmos', 104, 27, 'Todos ellos esperan en ti, Para que les des su comida á su tiempo.', 'water'),
('Old', 'Salmos', 104, 28, 'Les das, recogen; Abres tu mano, hártanse de bien.', 'water'),
('Old', 'Salmos', 104, 29, 'Escondes tu rostro, túrbanse: Les quitas el espíritu, dejan de ser, Y tórnanse en su polvo.', 'water'),
('Old', 'Salmos', 104, 30, 'Envías tu espíritu, críanse: Y renuevas la haz de la tierra.', 'water'),
('Old', 'Salmos', 104, 31, 'Sea la gloria de Jehová para siempre; Alégrese Jehová en sus obras;', 'water'),
('Old', 'Salmos', 104, 32, 'El cual mira á la tierra, y ella tiembla; Toca los montes, y humean.', 'water'),
('Old', 'Salmos', 104, 33, 'A Jehová cantaré en mi vida: A mi Dios salmearé mientras viviere.', 'water'),
('Old', 'Salmos', 104, 34, 'Serme ha suave hablar de él: Yo me alegraré en Jehová.', 'water'),
('Old', 'Salmos', 104, 35, 'Sean consumidos de la tierra los pecadores, Y los impíos dejen de ser. Bendice, alma mía, á Jehová. Aleluya.', 'water'),
('Old', 'Salmos', 105, 1, 'ALABAD á Jehová, invocad su nombre. Haced notorias sus obras en los pueblos.', 'water'),
('Old', 'Salmos', 105, 2, 'Cantadle, cantadle salmos: Hablad de todas sus maravillas.', 'water'),
('Old', 'Salmos', 105, 3, 'Gloriaos en su santo nombre: Alégrese el corazón de los que buscan á Jehová.', 'water'),
('Old', 'Salmos', 105, 4, 'Buscad á Jehová, y su fortaleza: Buscad siempre su rostro.', 'water'),
('Old', 'Salmos', 105, 5, 'Acordaos de sus maravillas que hizo, De sus prodigios y de los juicios de su boca,', 'water'),
('Old', 'Salmos', 105, 6, 'Oh vosotros, simiente de Abraham su siervo, Hijos de Jacob, sus escogidos.', 'water'),
('Old', 'Salmos', 105, 7, 'El es Jehová nuestro Dios; En toda la tierra son sus juicios.', 'water'),
('Old', 'Salmos', 105, 8, 'Acordóse para siempre de su alianza; De la palabra que mandó para mil generaciones,', 'water'),
('Old', 'Salmos', 105, 9, 'La cual concertó con Abraham; Y de su juramento á Isaac.', 'water'),
('Old', 'Salmos', 105, 10, 'Y establecióla á Jacob por decreto, A Israel por pacto sempiterno,', 'water'),
('Old', 'Salmos', 105, 11, 'Diciendo: A ti daré la tierra de Canaán Por cordel de vuestra heredad.', 'water'),
('Old', 'Salmos', 105, 12, 'Esto siendo ellos pocos hombres en número, Y extranjeros en ella.', 'water'),
('Old', 'Salmos', 105, 13, 'Y anduvieron de gente en gente, De un reino á otro pueblo.', 'water'),
('Old', 'Salmos', 105, 14, 'No consintió que hombre los agraviase; Y por causa de ellos castigó los reyes.', 'water'),
('Old', 'Salmos', 105, 15, 'No toquéis, dijo, á mis ungidos, Ni hagáis mal á mis profetas.', 'water'),
('Old', 'Salmos', 105, 16, 'Y llamó al hambre sobre la tierra, Y quebrantó todo mantenimiento de pan.', 'water'),
('Old', 'Salmos', 105, 17, 'Envió un varón delante de ellos, A José, que fué vendido por siervo.', 'water'),
('Old', 'Salmos', 105, 18, 'Afligieron sus pies con grillos; En hierro fué puesta su persona.', 'water'),
('Old', 'Salmos', 105, 19, 'Hasta la hora que llegó su palabra, El dicho de Jehová le probó.', 'water'),
('Old', 'Salmos', 105, 20, 'Envió el rey, y soltóle; El señor de los pueblos, y desatóle.', 'water'),
('Old', 'Salmos', 105, 21, 'Púsolo por señor de su casa, Y por enseñoreador en toda su posesión;', 'water'),
('Old', 'Salmos', 105, 22, 'Para que reprimiera á sus grandes como él quisiese, Y á sus ancianos enseñara sabiduría.', 'water'),
('Old', 'Salmos', 105, 23, 'Después entró Israel en Egipto, Y Jacob fué extranjero en la tierra de Châm.', 'water'),
('Old', 'Salmos', 105, 24, 'Y multiplicó su pueblo en gran manera, E hízolo fuerte más que sus enemigos.', 'water'),
('Old', 'Salmos', 105, 25, 'Volvió el corazón de ellos para que aborreciesen á su pueblo, Para que contra sus siervos pensasen mal.', 'water'),
('Old', 'Salmos', 105, 26, 'Envió á su siervo Moisés, Y á Aarón al cual escogió.', 'water'),
('Old', 'Salmos', 105, 27, 'Pusieron en ellos las palabras de sus señales, Y sus prodigios en la tierra de Châm.', 'water'),
('Old', 'Salmos', 105, 28, 'Echó tinieblas, é hizo oscuridad; Y no fueron rebeldes á su palabra.', 'water'),
('Old', 'Salmos', 105, 29, 'Volvió sus aguas en sangre, Y mató sus pescados.', 'water'),
('Old', 'Salmos', 105, 30, 'Produjo su tierra ranas, Aun en las cámaras de sus reyes.', 'water'),
('Old', 'Salmos', 105, 31, 'Dijo, y vinieron enjambres de moscas, Y piojos en todo su término.', 'water'),
('Old', 'Salmos', 105, 32, 'Volvió en su tierra sus lluvias en granizo, Y en fuego de llamaradas.', 'water'),
('Old', 'Salmos', 105, 33, 'E hirió sus viñas y sus higueras, Y quebró los árboles de su término.', 'water'),
('Old', 'Salmos', 105, 34, 'Dijo, y vinieron langostas, Y pulgón sin número;', 'water'),
('Old', 'Salmos', 105, 35, 'Y comieron toda la hierba de su país, Y devoraron el fruto de su tierra.', 'water'),
('Old', 'Salmos', 105, 36, 'Hirió además á todos los primogénitos en su tierra, El principio de toda su fuerza.', 'water'),
('Old', 'Salmos', 105, 37, 'Y sacólos con plata y oro; Y no hubo en sus tribus enfermo.', 'water'),
('Old', 'Salmos', 105, 38, 'Egipto se alegró de que salieran; Porque su terror había caído sobre ellos.', 'water'),
('Old', 'Salmos', 105, 39, 'Extendió una nube por cubierta, Y fuego para alumbrar la noche.', 'water'),
('Old', 'Salmos', 105, 40, 'Pidieron, é hizo venir codornices; Y saciólos de pan del cielo.', 'water'),
('Old', 'Salmos', 105, 41, 'Abrió la peña, y fluyeron aguas; Corrieron por los secadales como un río.', 'water'),
('Old', 'Salmos', 105, 42, 'Porque se acordó de su santa palabra, Dada á Abraham su siervo.', 'water'),
('Old', 'Salmos', 105, 43, 'Y sacó á su pueblo con gozo; Con júbilo á sus escogidos.', 'water'),
('Old', 'Salmos', 105, 44, 'Y dióles las tierras de las gentes; Y las labores de las naciones heredaron:', 'water'),
('Old', 'Salmos', 105, 45, 'Para que guardasen sus estatutos, Y observasen sus leyes. Aleluya.', 'water'),
('Old', 'Salmos', 106, 1, 'ALELUYA. Alabad á Jehová, porque es bueno; Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 106, 2, '¿Quién expresará las valentías de Jehová? ¿Quién contará sus alabanzas?', 'water'),
('Old', 'Salmos', 106, 3, 'Dichosos los que guardan juicio, Los que hacen justicia en todo tiempo.', 'water'),
('Old', 'Salmos', 106, 4, 'Acuérdate de mí, oh Jehová, según tu benevolencia para con tu pueblo: Visítame con tu salud;', 'water'),
('Old', 'Salmos', 106, 5, 'Para que yo vea el bien de tus escogidos, Para que me goce en la alegría de tu gente, Y me gloríe con tu heredad.', 'water'),
('Old', 'Salmos', 106, 6, 'Pecamos con nuestros padres, Hicimos iniquidad, hicimos impiedad.', 'water'),
('Old', 'Salmos', 106, 7, 'Nuestros padres en Egipto no entendieron tus maravillas; No se acordaron de la muchedumbre de tus misericordias; Sino que se rebelaron junto á la mar, en el mar Bermejo.', 'water'),
('Old', 'Salmos', 106, 8, 'Salvólos empero por amor de su nombre, Para hacer notoria su fortaleza.', 'water'),
('Old', 'Salmos', 106, 9, 'Y reprendió al mar Bermejo, y secólo; E hízoles ir por el abismo, como por un desierto.', 'water'),
('Old', 'Salmos', 106, 10, 'Y salvólos de mano del enemigo, Y rescatólos de mano del adversario.', 'water'),
('Old', 'Salmos', 106, 11, 'Y cubrieron las aguas á sus enemigos: No quedó uno de ellos.', 'water'),
('Old', 'Salmos', 106, 12, 'Entonces creyeron á sus palabras, Y cantaron su alabanza.', 'water'),
('Old', 'Salmos', 106, 13, 'Apresuráronse, olvidáronse de sus obras; No esperaron en su consejo.', 'water'),
('Old', 'Salmos', 106, 14, 'Y desearon con ansia en el desierto; Y tentaron á Dios en la soledad.', 'water'),
('Old', 'Salmos', 106, 15, 'Y él les dió lo que pidieron; Mas envió flaqueza en sus almas.', 'water'),
('Old', 'Salmos', 106, 16, 'Tomaron después celo contra Moisés en el campo, Y contra Aarón el santo de Jehová.', 'water'),
('Old', 'Salmos', 106, 17, 'Abrióse la tierra, y tragó á Dathán, Y cubrió la compañía de Abiram.', 'water'),
('Old', 'Salmos', 106, 18, 'Y encendióse el fuego en su junta; La llama quemó los impíos.', 'water'),
('Old', 'Salmos', 106, 19, 'Hicieron becerro en Horeb, Y encorváronse á un vaciadizo.', 'water'),
('Old', 'Salmos', 106, 20, 'Así trocaron su gloria Por la imagen de un buey que come hierba.', 'water'),
('Old', 'Salmos', 106, 21, 'Olvidaron al Dios de su salud, Que había hecho grandezas en Egipto;', 'water'),
('Old', 'Salmos', 106, 22, 'Maravillas en la tierra de Châm, Cosas formidables sobre el mar Bermejo.', 'water'),
('Old', 'Salmos', 106, 23, 'Y trató de destruirlos, A no haberse puesto Moisés su escogido al portillo delante de él, A fin de apartar su ira, para que no los destruyese.', 'water'),
('Old', 'Salmos', 106, 24, 'Empero aborrecieron la tierra deseable: No creyeron á su palabra;', 'water'),
('Old', 'Salmos', 106, 25, 'Antes murmuraron en sus tiendas, Y no oyeron la voz de Jehová.', 'water'),
('Old', 'Salmos', 106, 26, 'Por lo que alzó su mano á ellos, En orden á postrarlos en el desierto,', 'water'),
('Old', 'Salmos', 106, 27, 'Y humillar su simiente entre las gentes, Y esparcirlos por las tierras.', 'water'),
('Old', 'Salmos', 106, 28, 'Allegáronse asimismo á Baalpeor, Y comieron los sacrificios de los muertos.', 'water'),
('Old', 'Salmos', 106, 29, 'Y ensañaron á Dios con sus obras, Y desarrollóse la mortandad en ellos.', 'water'),
('Old', 'Salmos', 106, 30, 'Entonces se levantó Phinees, é hizo juicio; Y se detuvo la plaga.', 'water'),
('Old', 'Salmos', 106, 31, 'Y fuéle contado á justicia De generación en generación para siempre.', 'water'),
('Old', 'Salmos', 106, 32, 'También le irritaron en las aguas de Meriba: E hizo mal á Moisés por causa de ellos;', 'water'),
('Old', 'Salmos', 106, 33, 'Porque hicieron se rebelase su espíritu, Como lo expresó con sus labios.', 'water'),
('Old', 'Salmos', 106, 34, 'No destruyeron los pueblos Que Jehová les dijo;', 'water'),
('Old', 'Salmos', 106, 35, 'Antes se mezclaron con las gentes, Y aprendieron sus obras.', 'water'),
('Old', 'Salmos', 106, 36, 'Y sirvieron á sus ídolos; Los cuales les fueron por ruina.', 'water'),
('Old', 'Salmos', 106, 37, 'Y sacrificaron sus hijos y sus hijas á los demonios;', 'water'),
('Old', 'Salmos', 106, 38, 'Y derramaron la sangre inocente, la sangre de sus hijos y de sus hijas, Que sacrificaron á los ídolos de Canaán: Y la tierra fué contaminada con sangre.', 'water'),
('Old', 'Salmos', 106, 39, 'Contamináronse así con sus obras, Y fornicaron con sus hechos.', 'water'),
('Old', 'Salmos', 106, 40, 'Encendióse por tanto el furor de Jehová sobre su pueblo, Y abominó su heredad:', 'water'),
('Old', 'Salmos', 106, 41, 'Y entrególos en poder de las gentes, Y enseñoreáronse de ellos los que los aborrecían.', 'water'),
('Old', 'Salmos', 106, 42, 'Y sus enemigos los oprimieron, Y fueron quebrantados debajo de su mano.', 'water'),
('Old', 'Salmos', 106, 43, 'Muchas veces los libró; Mas ellos se rebelaron á su consejo, Y fueron humillados por su maldad.', 'water'),
('Old', 'Salmos', 106, 44, 'El con todo, miraba cuando estaban en angustia, Y oía su clamor:', 'water'),
('Old', 'Salmos', 106, 45, 'Y acordábase de su pacto con ellos, Y arrepentíase conforme á la muchedumbre de sus miseraciones.', 'water'),
('Old', 'Salmos', 106, 46, 'Hizo asimismo tuviesen de ellos misericordia todos los que los tenían cautivos.', 'water'),
('Old', 'Salmos', 106, 47, 'Sálvanos, Jehová Dios nuestro, Y júntanos de entre las gentes, Para que loemos tu santo nombre, Para que nos gloriemos en tus alabanzas.', 'water'),
('Old', 'Salmos', 106, 48, 'Bendito Jehová Dios de Israel, Desde el siglo y hasta el siglo: Y diga todo el pueblo, Amén. Aleluya.', 'water'),
('Old', 'Salmos', 107, 1, 'ALABAD á Jehová, porque es bueno; Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 107, 2, 'Digan lo los redimidos de Jehová, Los que ha redimido del poder del enemigo,', 'water'),
('Old', 'Salmos', 107, 3, 'Y los ha congregado de las tierras, Del oriente y del occidente, Del aquilón y de la mar.', 'water'),
('Old', 'Salmos', 107, 4, 'Anduvieron perdidos por el desierto, por la soledad sin camino, No hallando ciudad de población.', 'water'),
('Old', 'Salmos', 107, 5, 'Hambrientos y sedientos, Su alma desfallecía en ellos.', 'water'),
('Old', 'Salmos', 107, 6, 'Habiendo empero clamado á Jehová en su angustia, Librólos de sus aflicciones:', 'water'),
('Old', 'Salmos', 107, 7, 'Y dirigiólos por camino derecho, Para que viniesen á ciudad de población.', 'water'),
('Old', 'Salmos', 107, 8, 'Alaben la misericordia de Jehová, Y sus maravillas para con los hijos de los hombres.', 'water'),
('Old', 'Salmos', 107, 9, 'Porque sació al alma menesterosa, Y llenó de bien al alma hambrienta.', 'water'),
('Old', 'Salmos', 107, 10, 'Los que moraban en tinieblas y sombra de muerte, Aprisionados en aflicción y en hierros;', 'water'),
('Old', 'Salmos', 107, 11, 'Por cuanto fueron rebeldes á las palabras de Jehová, Y aborrecieron el consejo del Altísimo.', 'water'),
('Old', 'Salmos', 107, 12, 'Por lo que quebrantó él con trabajo sus corazones, Cayeron y no hubo quien los ayudase;', 'water'),
('Old', 'Salmos', 107, 13, 'Luego que clamaron á Jehová en su angustia, Librólos de sus aflicciones.', 'water'),
('Old', 'Salmos', 107, 14, 'Sacólos de las tinieblas y de la sombra de muerte, Y rompió sus prisiones.', 'water'),
('Old', 'Salmos', 107, 15, 'Alaben la misericordia de Jehová, Y sus maravillas para con los hijos de los hombres.', 'water'),
('Old', 'Salmos', 107, 16, 'Porque quebrantó las puertas de bronce, Y desmenuzó los cerrojos de hierro.', 'water'),
('Old', 'Salmos', 107, 17, 'Los insensatos, á causa del camino de su rebelión Y á causa de sus maldades, fueron afligidos.', 'water'),
('Old', 'Salmos', 107, 18, 'Su alma abominó toda vianda, Y llegaron hasta las puertas de la muerte.', 'water'),
('Old', 'Salmos', 107, 19, 'Mas clamaron á Jehová en su angustia, Y salvólos de sus aflicciones.', 'water'),
('Old', 'Salmos', 107, 20, 'Envió su palabra, y curólos, Y librólos de su ruina.', 'water'),
('Old', 'Salmos', 107, 21, 'Alaben la misericordia de Jehová, Y sus maravillas para con los hijos de los hombres:', 'water'),
('Old', 'Salmos', 107, 22, 'Y sacrifiquen sacrificios de alabanza, Y publiquen sus obras con júbilo.', 'water'),
('Old', 'Salmos', 107, 23, 'Los que descienden á la mar en navíos, Y hacen negocio en las muchas aguas,', 'water'),
('Old', 'Salmos', 107, 24, 'Ellos han visto las obras de Jehová, Y sus maravillas en el profundo.', 'water'),
('Old', 'Salmos', 107, 25, 'El dijo, é hizo saltar el viento de la tempestad, Que levanta sus ondas.', 'water'),
('Old', 'Salmos', 107, 26, 'Suben á los cielos, descienden á los abismos: Sus almas se derriten con el mal.', 'water'),
('Old', 'Salmos', 107, 27, 'Tiemblan, y titubean como borrachos, Y toda su ciencia es perdida.', 'water'),
('Old', 'Salmos', 107, 28, 'Claman empero á Jehová en su angustia, Y líbralos de sus aflicciones.', 'water'),
('Old', 'Salmos', 107, 29, 'Hace parar la tempestad en sosiego, Y se apaciguan sus ondas.', 'water'),
('Old', 'Salmos', 107, 30, 'Alégranse luego porque se reposaron; Y él los guía al puerto que deseaban.', 'water'),
('Old', 'Salmos', 107, 31, 'Alaben la misericordia de Jehová, Y sus maravillas para con los hijos de los hombres.', 'water'),
('Old', 'Salmos', 107, 32, 'Y ensálcenlo en la congregación del pueblo; Y en consistorio de ancianos lo alaben.', 'water'),
('Old', 'Salmos', 107, 33, 'El vuelve los ríos en desierto, Y los manantiales de las aguas en secadales;', 'water'),
('Old', 'Salmos', 107, 34, 'La tierra fructífera en salados, Por la maldad de los que la habitan.', 'water'),
('Old', 'Salmos', 107, 35, 'Vuelve el desierto en estanques de aguas, Y la tierra seca en manantiales.', 'water'),
('Old', 'Salmos', 107, 36, 'Y allí aposenta á los hambrientos, Y disponen ciudad para habitación;', 'water'),
('Old', 'Salmos', 107, 37, 'Y siembran campos, y plantan viñas, Y rinden crecido fruto.', 'water'),
('Old', 'Salmos', 107, 38, 'Y los bendice, y se multiplican en gran manera; Y no disminuye sus bestias.', 'water'),
('Old', 'Salmos', 107, 39, 'Y luego son menoscabados y abatidos A causa de tiranía, de males y congojas.', 'water'),
('Old', 'Salmos', 107, 40, 'El derrama menosprecio sobre los príncipes, Y les hace andar errados, vagabundos, sin camino:', 'water'),
('Old', 'Salmos', 107, 41, 'Y levanta al pobre de la miseria, Y hace multiplicar las familias como rebaños de ovejas.', 'water'),
('Old', 'Salmos', 107, 42, 'Vean los rectos, y alégrense; Y toda maldad cierre su boca.', 'water'),
('Old', 'Salmos', 107, 43, '¿Quién es sabio y guardará estas cosas, Y entenderá las misericordias de Jehová?', 'water'),
('Old', 'Salmos', 108, 1, 'MI corazón está dispuesto, oh Dios; Cantaré y salmearé todavía en mi gloria.', 'water'),
('Old', 'Salmos', 108, 2, 'Despiértate, salterio y arpa: Despertaré al alba.', 'water'),
('Old', 'Salmos', 108, 3, 'Te alabaré, oh Jehová, entre los pueblos; A ti cantaré salmos entre las naciones.', 'water'),
('Old', 'Salmos', 108, 4, 'Porque grande más que los cielos es tu misericordia, Y hasta los cielos tu verdad.', 'water'),
('Old', 'Salmos', 108, 5, 'Ensálzate, oh Dios, sobre los cielos; Y sobre toda la tierra tu gloria.', 'water'),
('Old', 'Salmos', 108, 6, 'Para que sean librados tus amados, Salva con tu diestra y respóndeme.', 'water'),
('Old', 'Salmos', 108, 7, 'Dios habló por su santuario: alegraréme, Repartiré á Sichêm, y mediré el valle de Succoth.', 'water'),
('Old', 'Salmos', 108, 8, 'Mío es Galaad, mío es Manasés; Y Eprhaim es la fortaleza de mi cabeza; Judá es mi legislador;', 'water'),
('Old', 'Salmos', 108, 9, 'Moab, la vasija de mi lavatorio: Sobre Edom echaré mi calzado; Regocijaréme sobre Palestina.', 'water'),
('Old', 'Salmos', 108, 10, '¿Quién me guiará á la ciudad fortalecida? ¿Quién me guiará hasta Idumea?', 'water'),
('Old', 'Salmos', 108, 11, 'Ciertamente tú, oh Dios, que nos habías desechado; Y no salías, oh Dios, con nuestros ejércitos.', 'water'),
('Old', 'Salmos', 108, 12, 'Danos socorro en la angustia: Porque mentirosa es la salud del hombre.', 'water'),
('Old', 'Salmos', 108, 13, 'En Dios haremos proezas; Y él hollará nuestros enemigos.', 'water'),
('Old', 'Salmos', 109, 1, 'OH Dios de mi alabanza, no calles;', 'water'),
('Old', 'Salmos', 109, 2, 'Porque boca de impío y boca de engañador se han abierto sobre mí: Han hablado de mí con lengua mentirosa,', 'water'),
('Old', 'Salmos', 109, 3, 'Y con palabras de odio me rodearon; Y pelearon contra mí sin causa.', 'water'),
('Old', 'Salmos', 109, 4, 'En pago de mi amor me han sido adversarios: Mas yo oraba.', 'water'),
('Old', 'Salmos', 109, 5, 'Y pusieron contra mí mal por bien, Y odio por amor.', 'water'),
('Old', 'Salmos', 109, 6, 'Pon sobre él al impío: Y Satán esté á su diestra.', 'water'),
('Old', 'Salmos', 109, 7, 'Cuando fuere juzgado, salga impío; Y su oración sea para pecado.', 'water'),
('Old', 'Salmos', 109, 8, 'Sean sus días pocos: Tome otro su oficio.', 'water'),
('Old', 'Salmos', 109, 9, 'Sean sus hijos huérfanos, Y su mujer viuda.', 'water'),
('Old', 'Salmos', 109, 10, 'Y anden sus hijos vagabundos, y mendiguen; Y procuren su pan lejos de sus desolados hogares.', 'water'),
('Old', 'Salmos', 109, 11, 'Enrede el acreedor todo lo que tiene, Y extraños saqueen su trabajo.', 'water'),
('Old', 'Salmos', 109, 12, 'No tenga quien le haga misericordia; Ni haya quien tenga compasión de sus huérfanos.', 'water'),
('Old', 'Salmos', 109, 13, 'Su posteridad sea talada; En segunda generación sea raído su nombre.', 'water'),
('Old', 'Salmos', 109, 14, 'Venga en memoria cerca de Jehová la maldad de sus padres, Y el pecado de su madre no sea borrado.', 'water'),
('Old', 'Salmos', 109, 15, 'Estén siempre delante de Jehová, Y él corte de la tierra su memoria.', 'water'),
('Old', 'Salmos', 109, 16, 'Por cuanto no se acordo de hacer misericordia, Y persiguió al hombre afligido y menesteroso Y quebrantado de corazón, para matar lo.', 'water'),
('Old', 'Salmos', 109, 17, 'Y amó la maldición, y vínole; Y no quiso la bendición, y ella se alejó de él.', 'water'),
('Old', 'Salmos', 109, 18, 'Y vistióse de maldición como de su vestido, Y entró como agua en sus entrañas, Y como aceite en sus huesos.', 'water'),
('Old', 'Salmos', 109, 19, 'Séale como vestido con que se cubra, Y en lugar de cinto con que se ciña siempre.', 'water'),
('Old', 'Salmos', 109, 20, 'Este sea el pago de parte de Jehová de los que me calumnian, Y de los que hablan mal contra mi alma.', 'water'),
('Old', 'Salmos', 109, 21, 'Y tú, Jehová Señor, haz conmigo por amor de tu nombre: Líbrame, porque tu misericordia es buena.', 'water'),
('Old', 'Salmos', 109, 22, 'Porque yo estoy afligido y necesitado; Y mi corazón está herido dentro de mí.', 'water'),
('Old', 'Salmos', 109, 23, 'Voime como la sombra cuando declina; Soy sacudido como langosta.', 'water'),
('Old', 'Salmos', 109, 24, 'Mis rodillas están debilitadas á causa del ayuno, Y mi carne desfallecida por falta de gordura.', 'water'),
('Old', 'Salmos', 109, 25, 'Yo he sido para ellos objeto de oprobio; Mirábanme, y meneaban su cabeza.', 'water'),
('Old', 'Salmos', 109, 26, 'Ayúdame, Jehová Dios mío: Sálvame conforme á tu misericordia.', 'water'),
('Old', 'Salmos', 109, 27, 'Y entiendan que ésta es tu mano; Que tú, Jehová, has hecho esto.', 'water'),
('Old', 'Salmos', 109, 28, 'Maldigan ellos, y bendice tú: Levántense, mas sean avergonzados, y regocíjese tu siervo.', 'water'),
('Old', 'Salmos', 109, 29, 'Sean vestidos de ignominia los que me calumnian; Y sean cubiertos de su confusión como con manto.', 'water'),
('Old', 'Salmos', 109, 30, 'Yo alabaré á Jehová en gran manera con mi boca, Y le loaré en medio de muchos.', 'water'),
('Old', 'Salmos', 109, 31, 'Porque él se pondrá á la diestra del pobre, Para librar su alma de los que le juzgan.', 'water'),
('Old', 'Salmos', 110, 1, 'JEHOVA dijo á mi Señor: Siéntate á mi diestra, En tanto que pongo tus enemigos por estrado de tus pies.', 'water'),
('Old', 'Salmos', 110, 2, 'La vara de tu fortaleza enviará Jehová desde Sión: Domina en medio de tus enemigos.', 'water'),
('Old', 'Salmos', 110, 3, 'Tu pueblo serálo de buena voluntad en el día de tu poder, En la hermosura de la santidad: desde el seno de la aurora, Tienes tú el rocío de tu juventud.', 'water'),
('Old', 'Salmos', 110, 4, 'Juró Jehová, y no se arrepentirá: Tú eres sacerdote para siempre Según el orden de Melchîsedech.', 'water'),
('Old', 'Salmos', 110, 5, 'El Señor á tu diestra Herirá á los reyes en el día de su furor:', 'water'),
('Old', 'Salmos', 110, 6, 'Juzgará en las gentes, Llenará las de cadáveres: Herirá las cabezas en muchas tierras.', 'water'),
('Old', 'Salmos', 110, 7, 'Del arroyo beberá en el camino: Por lo cual levantará cabeza.', 'water'),
('Old', 'Salmos', 111, 1, 'ALABARÉ á Jehová con todo el corazón, En la compañía y congregación de los rectos.', 'water'),
('Old', 'Salmos', 111, 2, 'Grandes son las obras de Jehová; Buscadas de todos los que las quieren.', 'water'),
('Old', 'Salmos', 111, 3, 'Gloria y hermosura es su obra; Y su justicia permanece para siempre.', 'water'),
('Old', 'Salmos', 111, 4, 'Hizo memorables sus maravillas: Clemente y misericordioso es Jehová.', 'water'),
('Old', 'Salmos', 111, 5, 'Dió mantenimiento á los que le temen; Para siempre se acordará de su pacto.', 'water'),
('Old', 'Salmos', 111, 6, 'El poder de sus obras anunció á su pueblo, Dándole la heredad de las gentes.', 'water'),
('Old', 'Salmos', 111, 7, 'Las obras de sus manos son verdad y juicio: Fieles son todos sus mandamientos;', 'water'),
('Old', 'Salmos', 111, 8, 'Afirmados por siglo de siglo, Hechos en verdad y en rectitud.', 'water'),
('Old', 'Salmos', 111, 9, 'Redención ha enviado á su pueblo; Para siempre ha ordenado su pacto: Santo y terrible es su nombre.', 'water'),
('Old', 'Salmos', 111, 10, 'El principio de la sabiduría es el temor de Jehová: Buen entendimiento tienen cuantos ponen aquéllos por obra: Su loor permanece para siempre.', 'water'),
('Old', 'Salmos', 112, 1, 'BIENAVENTURADO el hombre que teme á Jehová, Y en sus mandamientos se deleita en gran manera.', 'water'),
('Old', 'Salmos', 112, 2, 'Su simiente será poderosa en la tierra: La generación de los rectos será bendita.', 'water'),
('Old', 'Salmos', 112, 3, 'Hacienda y riquezas hay en su casa; Y su justicia permanece para siempre.', 'water'),
('Old', 'Salmos', 112, 4, 'Resplandeció en las tinieblas luz á los rectos: Es clemente, y misericordioso, y justo.', 'water'),
('Old', 'Salmos', 112, 5, 'El hombre de bien tiene misericordia y presta; Gobierna sus cosas con juicio.', 'water'),
('Old', 'Salmos', 112, 6, 'Por lo cual no resbalará para siempre: En memoria eterna será el justo.', 'water'),
('Old', 'Salmos', 112, 7, 'De mala fama no tendrá temor: Su corazón está apercibido, confiado en Jehová.', 'water'),
('Old', 'Salmos', 112, 8, 'Asentado está su corazón, no temerá, Hasta que vea en sus enemigos su deseo.', 'water'),
('Old', 'Salmos', 112, 9, 'Esparce, da á los pobres: Su justicia permanece para siempre; Su cuerno será ensalzado en gloria.', 'water'),
('Old', 'Salmos', 112, 10, 'Verálo el impío, y se despechará; Crujirá los dientes, y se repudrirá: Perecerá el deseo de los impíos.', 'water'),
('Old', 'Salmos', 113, 1, 'ALABAD, siervos de Jehová, Alabad el nombre de Jehová.', 'water'),
('Old', 'Salmos', 113, 2, 'Sea el nombre de Jehová bendito, Desde ahora y para siempre.', 'water'),
('Old', 'Salmos', 113, 3, 'Desde el nacimiento del sol hasta donde se pone, Sea alabado el nombre de Jehová.', 'water'),
('Old', 'Salmos', 113, 4, 'Alto sobre todas las naciones es Jehová; Sobre los cielos su gloria.', 'water'),
('Old', 'Salmos', 113, 5, '¿Quién como Jehová nuestro Dios, Que ha enaltecido su habitación,', 'water'),
('Old', 'Salmos', 113, 6, 'Que se humilla á mirar En el cielo y en la tierra?', 'water'),
('Old', 'Salmos', 113, 7, 'El levanta del polvo al pobre, Y al menesteroso alza del estiércol,', 'water'),
('Old', 'Salmos', 113, 8, 'Para hacerlos sentar con los príncipes, Con los príncipes de su pueblo.', 'water'),
('Old', 'Salmos', 113, 9, 'El hace habitar en familia á la estéril, Gozosa en ser madre de hijos. Aleluya.', 'water'),
('Old', 'Salmos', 114, 1, 'CUANDO salió Israel de Egipto, La casa de Jacob del pueblo bárbaro,', 'water'),
('Old', 'Salmos', 114, 2, 'Judá fué su consagrada heredad, Israel su señorío.', 'water'),
('Old', 'Salmos', 114, 3, 'La mar vió, y huyó; El Jordán se volvió atrás.', 'water'),
('Old', 'Salmos', 114, 4, 'Los montes saltaron como carneros: Los collados como corderitos.', 'water'),
('Old', 'Salmos', 114, 5, '¿Qué tuviste, oh mar, que huiste? ¿Y tú, oh Jordán, que te volviste atrás?', 'water'),
('Old', 'Salmos', 114, 6, 'Oh montes, ¿por qué saltasteis como carneros, Y vosotros, collados, como corderitos?', 'water'),
('Old', 'Salmos', 114, 7, 'A la presencia del Señor tiembla la tierra, A la presencia del Dios de Jacob;', 'water'),
('Old', 'Salmos', 114, 8, 'El cual tornó la peña en estanque de aguas, Y en fuente de aguas la roca.', 'water'),
('Old', 'Salmos', 115, 1, 'NO á nosotros, oh Jehová, no á nosotros, Sino á tu nombre da gloria; Por tu misericordia, por tu verdad.', 'water'),
('Old', 'Salmos', 115, 2, 'Por qué dirán las gentes: ¿Dónde está ahora su Dios?', 'water'),
('Old', 'Salmos', 115, 3, 'Y nuestro Dios está en los cielos: Todo lo que quiso ha hecho.', 'water'),
('Old', 'Salmos', 115, 4, 'Sus ídolos son plata y oro, Obra de manos de hombres.', 'water'),
('Old', 'Salmos', 115, 5, 'Tienen boca, mas no hablarán; Tienen ojos, mas no verán;', 'water'),
('Old', 'Salmos', 115, 6, 'Orejas tienen, mas no oirán; Tienen narices, mas no olerán;', 'water'),
('Old', 'Salmos', 115, 7, 'Manos tienen, mas no palparán; Tienen pies, mas no andarán; No hablarán con su garganta.', 'water'),
('Old', 'Salmos', 115, 8, 'Como ellos son los que los hacen; Cualquiera que en ellos confía.', 'water'),
('Old', 'Salmos', 115, 9, 'Oh Israel, confía en Jehová: El es su ayuda y su escudo.', 'water'),
('Old', 'Salmos', 115, 10, 'Casa de Aarón, confiad en Jehová: El es su ayuda y su escudo.', 'water'),
('Old', 'Salmos', 115, 11, 'Los que teméis á Jehová, confiad en Jehová: El es su ayuda y su escudo.', 'water'),
('Old', 'Salmos', 115, 12, 'Jehová se acordó de nosotros: nos bendecirá: Bendecirá á la casa de Israel; Bendecirá á la casa de Aarón.', 'water'),
('Old', 'Salmos', 115, 13, 'Bendecirá á los que temen á Jehová; A chicos y á grandes.', 'water'),
('Old', 'Salmos', 115, 14, 'Acrecentará Jehová bendición sobre vosotros; Sobre vosotros y sobre vuestros hijos.', 'water'),
('Old', 'Salmos', 115, 15, 'Benditos vosotros de Jehová, Que hizo los cielos y la tierra.', 'water'),
('Old', 'Salmos', 115, 16, 'Los cielos son los cielos de Jehová: Y ha dado la tierra á los hijos de los hombres.', 'water'),
('Old', 'Salmos', 115, 17, 'No alabarán los muertos á JAH, Ni cuantos descienden al silencio;', 'water'),
('Old', 'Salmos', 115, 18, 'Mas nosotros bendeciremos á JAH, Desde ahora para siempre. Aleluya.', 'water'),
('Old', 'Salmos', 116, 1, 'AMO á Jehová, pues ha oído Mi voz y mis súplicas.', 'water'),
('Old', 'Salmos', 116, 2, 'Porque ha inclinado á mí su oído, Invocaré le por tanto en todos mis días.', 'water'),
('Old', 'Salmos', 116, 3, 'Rodeáronme los dolores de la muerte, Me encontraron las angustias del sepulcro: Angustia y dolor había yo hallado.', 'water'),
('Old', 'Salmos', 116, 4, 'Entonces invoqué el nombre de Jehová, diciendo: Libra ahora, oh Jehová, mi alma.', 'water'),
('Old', 'Salmos', 116, 5, 'Clemente es Jehová y justo; Sí, misericordioso es nuestro Dios.', 'water'),
('Old', 'Salmos', 116, 6, 'Jehová guarda á los sinceros: Estaba yo postrado, y salvóme.', 'water'),
('Old', 'Salmos', 116, 7, 'Vuelve, oh alma mía, á tu reposo; Porque Jehová te ha hecho bien.', 'water'),
('Old', 'Salmos', 116, 8, 'Pues tú has librado mi alma de la muerte, Mis ojos de lágrimas, Y mis pies de desbarrar.', 'water'),
('Old', 'Salmos', 116, 9, 'Andaré delante de Jehová En la tierra de los vivientes.', 'water'),
('Old', 'Salmos', 116, 10, 'Creí; por tanto hablé, Estando afligido en gran manera.', 'water'),
('Old', 'Salmos', 116, 11, 'Y dije en mi apresuramiento: Todo hombre es mentiroso.', 'water'),
('Old', 'Salmos', 116, 12, '¿Qué pagaré á Jehová Por todos sus beneficios para conmigo?', 'water'),
('Old', 'Salmos', 116, 13, 'Tomaré la copa de la salud, E invocaré el nombre de Jehová.', 'water'),
('Old', 'Salmos', 116, 14, 'Ahora pagaré mis votos á Jehová Delante de todo su pueblo.', 'water'),
('Old', 'Salmos', 116, 15, 'Estimada es en los ojos de Jehová La muerte de sus santos.', 'water'),
('Old', 'Salmos', 116, 16, 'Oh Jehová, que yo soy tu siervo, Yo tu siervo, hijo de tu sierva: Rompiste mis prisiones.', 'water'),
('Old', 'Salmos', 116, 17, 'Te ofreceré sacrificio de alabanza, E invocaré el nombre de Jehová.', 'water'),
('Old', 'Salmos', 116, 18, 'A Jehová pagaré ahora mis votos Delante de todo su pueblo;', 'water'),
('Old', 'Salmos', 116, 19, 'En los atrios de la casa de Jehová, En medio de ti, oh Jerusalem. Aleluya.', 'water'),
('Old', 'Salmos', 117, 1, 'ALABAD á Jehová, naciones todas; Pueblos todos, alabadle.', 'water'),
('Old', 'Salmos', 117, 2, 'Porque ha engrandecido sobre nosotros su misericordia; Y la verdad de Jehová es para siempre. Aleluya.', 'water'),
('Old', 'Salmos', 118, 1, 'ALABAD á Jehová, porque es bueno; Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 118, 2, 'Diga ahora Israel: Que para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 118, 3, 'Diga ahora la casa de Aarón: Que para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 118, 4, 'Digan ahora los que temen á Jehová: Que para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 118, 5, 'Desde la angustia invoqué á JAH; Y respondióme JAH, poniéndome en anchura.', 'water'),
('Old', 'Salmos', 118, 6, 'Jehová está por mí: no temeré Lo que me pueda hacer el hombre.', 'water'),
('Old', 'Salmos', 118, 7, 'Jehová está por mí entre los que me ayudan: Por tanto yo veré mi deseo en los que me aborrecen.', 'water'),
('Old', 'Salmos', 118, 8, 'Mejor es esperar en Jehová Que esperar en hombre.', 'water'),
('Old', 'Salmos', 118, 9, 'Mejor es esperar en Jehová Que esperar en príncipes.', 'water'),
('Old', 'Salmos', 118, 10, 'Todas las gentes me cercaron: En nombre de Jehová, que yo los romperé.', 'water'),
('Old', 'Salmos', 118, 11, 'Cercáronme y asediáronme: En nombre de Jehová, que yo los romperé.', 'water'),
('Old', 'Salmos', 118, 12, 'Cercáronme como abejas; fueron apagados como fuegos de espinos: En nombre de Jehová, que yo los romperé.', 'water'),
('Old', 'Salmos', 118, 13, 'Empujásteme con violencia para que cayese: Empero ayudóme Jehová.', 'water'),
('Old', 'Salmos', 118, 14, 'Mi fortaleza y mi canción es JAH; Y él me ha sido por salud.', 'water'),
('Old', 'Salmos', 118, 15, 'Voz de júbilo y de salvación hay en las tiendas de los justos: La diestra de Jehová hace proezas.', 'water'),
('Old', 'Salmos', 118, 16, 'La diestra de Jehová sublime: La diestra de Jehová hace valentías.', 'water'),
('Old', 'Salmos', 118, 17, 'No moriré, sino que viviré, Y contaré las obras de JAH.', 'water'),
('Old', 'Salmos', 118, 18, 'Castigóme gravemente JAH: Mas no me entregó á la muerte.', 'water'),
('Old', 'Salmos', 118, 19, 'Abridme las puertas de la justicia: Entraré por ellas, alabaré á JAH.', 'water'),
('Old', 'Salmos', 118, 20, 'Esta puerta de Jehová, Por ella entrarán los justos.', 'water'),
('Old', 'Salmos', 118, 21, 'Te alabaré porque me has oído, Y me fuiste por salud.', 'water'),
('Old', 'Salmos', 118, 22, 'La piedra que desecharon los edificadores, Ha venido á ser cabeza del ángulo.', 'water'),
('Old', 'Salmos', 118, 23, 'De parte de Jehová es esto: Es maravilla en nuestros ojos.', 'water'),
('Old', 'Salmos', 118, 24, 'Este es el día que hizo Jehová Nos gozaremos y alegraremos en él.', 'water'),
('Old', 'Salmos', 118, 25, 'Oh Jehová, salva ahora, te ruego: Oh Jehová, ruégote hagas prosperar ahora.', 'water'),
('Old', 'Salmos', 118, 26, 'Bendito el que viene en nombre de Jehová: Desde la casa de Jehová os bendecimos.', 'water'),
('Old', 'Salmos', 118, 27, 'Dios es Jehová que nos ha resplandecido: Atad víctimas con cuerdas á los cuernos del altar.', 'water'),
('Old', 'Salmos', 118, 28, 'Mi Dios eres tú, y á ti alabaré: Dios mío, á ti ensalzaré.', 'water'),
('Old', 'Salmos', 118, 29, 'Alabad á Jehová porque es bueno; Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 119, 1, 'ALEPH. BIENAVENTURADOS los perfectos de camino; Los que andan en la ley de Jehová.', 'water'),
('Old', 'Salmos', 119, 2, 'Bienaventurados los que guardan sus testimonios, Y con todo el corazón le buscan:', 'water'),
('Old', 'Salmos', 119, 3, 'Pues no hacen iniquidad Los que andan en sus caminos.', 'water'),
('Old', 'Salmos', 119, 4, 'Tú encargaste Que sean muy guardados tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 5, '¡Ojalá fuesen ordenados mis caminos A observar tus estatutos!', 'water'),
('Old', 'Salmos', 119, 6, 'Entonces no sería yo avergonzado, Cuando atendiese á todos tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 7, 'Te alabaré con rectitud de corazón, Cuando aprendiere los juicios de tu justicia.', 'water'),
('Old', 'Salmos', 119, 8, 'Tus estatutos guardaré: No me dejes enteramente.', 'water'),
('Old', 'Salmos', 119, 9, 'BETH. ¿Con qué limpiará el joven su camino? Con guardar tu palabra.', 'water'),
('Old', 'Salmos', 119, 10, 'Con todo mi corazón te he buscado: No me dejes divagar de tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 11, 'En mi corazón he guardado tus dichos, Para no pecar contra ti.', 'water'),
('Old', 'Salmos', 119, 12, 'Bendito tú, oh Jehová: Enséñame tus estatutos.', 'water'),
('Old', 'Salmos', 119, 13, 'Con mis labios he contado Todos los juicios de tu boca.', 'water'),
('Old', 'Salmos', 119, 14, 'Heme gozado en el camino de tus testimonios, Como sobre toda riqueza.', 'water'),
('Old', 'Salmos', 119, 15, 'En tus mandamientos meditaré, Consideraré tus caminos.', 'water'),
('Old', 'Salmos', 119, 16, 'Recrearéme en tus estatutos: No me olvidaré de tus palabras.', 'water'),
('Old', 'Salmos', 119, 17, 'GIMEL. Haz bien á tu siervo; que viva Y guarde tu palabra.', 'water'),
('Old', 'Salmos', 119, 18, 'Abre mis ojos, y miraré Las maravillas de tu ley.', 'water'),
('Old', 'Salmos', 119, 19, 'Advenedizo soy yo en la tierra: No encubras de mí tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 20, 'Quebrantada está mi alma de desear Tus juicios en todo tiempo.', 'water'),
('Old', 'Salmos', 119, 21, 'Destruiste á los soberbios malditos, Que se desvían de tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 22, 'Aparta de mí oprobio y menosprecio; Porque tus testimonios he guardado.', 'water'),
('Old', 'Salmos', 119, 23, 'Príncipes también se sentaron y hablaron contra mí: Mas tu siervo meditaba en tus estatutos.', 'water'),
('Old', 'Salmos', 119, 24, 'Pues tus testimonios son mis deleites, Y mis consejeros.', 'water'),
('Old', 'Salmos', 119, 25, 'DALETH. Pegóse al polvo mi alma: Vivifícame según tu palabra.', 'water'),
('Old', 'Salmos', 119, 26, 'Mis caminos te conté, y me has respondido: Enséñame tus estatutos.', 'water'),
('Old', 'Salmos', 119, 27, 'Hazme entender el camino de tus mandamientos, Y hablaré de tus maravillas.', 'water'),
('Old', 'Salmos', 119, 28, 'Deshácese mi alma de ansiedad: Corrobórame según tu palabra.', 'water'),
('Old', 'Salmos', 119, 29, 'Aparta de mí camino de mentira; Y hazme la gracia de tu ley.', 'water'),
('Old', 'Salmos', 119, 30, 'Escogí el camino de la verdad; He puesto tus juicios delante de mí.', 'water'),
('Old', 'Salmos', 119, 31, 'Allegádome he á tus testimonios; Oh Jehová, no me avergüences.', 'water'),
('Old', 'Salmos', 119, 32, 'Por el camino de tus mandamientos correré, Cuando ensanchares mi corazón.', 'water'),
('Old', 'Salmos', 119, 33, 'HE. Enséñame, oh Jehová, el camino de tus estatutos, Y guardarélo hasta el fin.', 'water'),
('Old', 'Salmos', 119, 34, 'Dame entendimiento, y guardaré tu ley; Y la observaré de todo corazón.', 'water'),
('Old', 'Salmos', 119, 35, 'Guíame por la senda de tus mandamientos; Porque en ella tengo mi voluntad.', 'water'),
('Old', 'Salmos', 119, 36, 'Inclina mi corazón á tus testimonios, Y no á la avaricia.', 'water'),
('Old', 'Salmos', 119, 37, 'Aparta mis ojos, que no vean la vanidad; Avívame en tu camino.', 'water'),
('Old', 'Salmos', 119, 38, 'Confirma tu palabra á tu siervo, Que te teme.', 'water'),
('Old', 'Salmos', 119, 39, 'Quita de mí el oprobio que he temido: Porque buenos son tus juicios.', 'water'),
('Old', 'Salmos', 119, 40, 'He aquí yo he codiciado tus mandamientos: Vivifícame en tu justicia.', 'water'),
('Old', 'Salmos', 119, 41, 'VAV. Y venga á mí tu misericordia, oh Jehová; Tu salud, conforme á tu dicho.', 'water'),
('Old', 'Salmos', 119, 42, 'Y daré por respuesta á mi avergonzador, Que en tu palabra he confiado.', 'water'),
('Old', 'Salmos', 119, 43, 'Y no quites de mi boca en nigún tiempo la palabra de verdad; Porque á tu juicio espero.', 'water'),
('Old', 'Salmos', 119, 44, 'Y guardaré tu ley siempre, Por siglo de siglo.', 'water'),
('Old', 'Salmos', 119, 45, 'Y andaré en anchura, Porque busqué tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 46, 'Y hablaré de tus testimonios delante de los reyes, Y no me avergonzaré.', 'water'),
('Old', 'Salmos', 119, 47, 'Y deleitaréme en tus mandamientos, Que he amado.', 'water'),
('Old', 'Salmos', 119, 48, 'Alzaré asimismo mis manos á tus mandamientos que amé; Y meditaré en tus estatutos.', 'water'),
('Old', 'Salmos', 119, 49, 'ZAYIN. Acuérdate de la palabra dada á tu siervo, En la cual me has hecho esperar.', 'water'),
('Old', 'Salmos', 119, 50, 'Esta es mi consuelo en mi aflicción: Porque tu dicho me ha vivificado.', 'water'),
('Old', 'Salmos', 119, 51, 'Los soberbios se burlaron mucho de mí: Mas no me he apartado de tu ley.', 'water'),
('Old', 'Salmos', 119, 52, 'Acordéme, oh Jehová, de tus juicios antiguos, Y consoléme.', 'water'),
('Old', 'Salmos', 119, 53, 'Horror se apoderó de mí, á causa De los impíos que dejan tu ley.', 'water'),
('Old', 'Salmos', 119, 54, 'Cánticos me fueron tus estatutos En la mansión de mis peregrinaciones.', 'water'),
('Old', 'Salmos', 119, 55, 'Acordéme en la noche de tu nombre, oh Jehová, Y guardé tu ley.', 'water'),
('Old', 'Salmos', 119, 56, 'Esto tuve, Porque guardaba tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 57, 'JET. Mi porción, oh Jehová, Dije, será guardar tus palabras.', 'water'),
('Old', 'Salmos', 119, 58, 'Tu presencia supliqué de todo corazón: Ten misericordia de mí según tu palabra.', 'water'),
('Old', 'Salmos', 119, 59, 'Consideré mis caminos, Y torné mis pies á tus testimonios.', 'water'),
('Old', 'Salmos', 119, 60, 'Apresuréme, y no me retardé En guardar tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 61, 'Compañía de impíos me han robado: Mas no me he olvidado de tu ley.', 'water'),
('Old', 'Salmos', 119, 62, 'A media noche me levantaba á alabarte Sobre los juicios de tu justicia.', 'water'),
('Old', 'Salmos', 119, 63, 'Compañero soy yo de todos los que te temieren Y guardaren tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 64, 'De tu misericordia, oh Jehová, está llena la tierra: Enséñame tus estatutos.', 'water'),
('Old', 'Salmos', 119, 65, 'TETH. Bien has hecho con tu siervo, Oh Jehová, conforme á tu palabra.', 'water'),
('Old', 'Salmos', 119, 66, 'Enséñame bondad de sentido y sabiduría; Porque tus mandamientos he creído.', 'water'),
('Old', 'Salmos', 119, 67, 'Antes que fuera yo humillado, descarriado andaba; Mas ahora guardo tu palabra.', 'water'),
('Old', 'Salmos', 119, 68, 'Bueno eres tú, y bienhechor: Enséñame tus estatutos.', 'water'),
('Old', 'Salmos', 119, 69, 'Contra mí forjaron mentira los soberbios: Mas yo guardaré de todo corazón tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 70, 'Engrasóse el corazón de ellos como sebo; Mas yo en tu ley me he deleitado.', 'water'),
('Old', 'Salmos', 119, 71, 'Bueno me es haber sido humillado, Para que aprenda tus estatutos.', 'water'),
('Old', 'Salmos', 119, 72, 'Mejor me es la ley de tu boca, Que millares de oro y plata.', 'water'),
('Old', 'Salmos', 119, 73, 'YOD. Tus manos me hicieron y me formaron: Hazme entender, y aprenderé tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 74, 'Los que te temen, me verán, y se alegrarán; Porque en tu palabra he esperado.', 'water'),
('Old', 'Salmos', 119, 75, 'Conozco, oh Jehová, que tus juicios son justicia, Y que conforme á tu fidelidad me afligiste.', 'water'),
('Old', 'Salmos', 119, 76, 'Sea ahora tu misericordia para consolarme, Conforme á lo que has dicho á tu siervo.', 'water'),
('Old', 'Salmos', 119, 77, 'Vengan á mí tus misericordias, y viva; Porque tu ley es mi deleite.', 'water'),
('Old', 'Salmos', 119, 78, 'Sean avergonzados los soberbios, porque sin causa me han calumniado: Yo empero, meditaré en tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 79, 'Tórnense á mí los que te temen Y conocen tus testimonios.', 'water'),
('Old', 'Salmos', 119, 80, 'Sea mi corazón íntegro en tus estatutos; Porque no sea yo avergonzado.', 'water'),
('Old', 'Salmos', 119, 81, 'KAF. Desfallece mi alma por tu salud, Esperando en tu palabra.', 'water'),
('Old', 'Salmos', 119, 82, 'Desfallecieron mis ojos por tu palabra, Diciendo: ¿Cuándo me consolarás?', 'water'),
('Old', 'Salmos', 119, 83, 'Porque estoy como el odre al humo; Mas no he olvidado tus estatutos.', 'water'),
('Old', 'Salmos', 119, 84, '¿Cuántos son los días de tu siervo? ¿Cuándo harás juicio contra los que me persiguen?', 'water'),
('Old', 'Salmos', 119, 85, 'Los soberbios me han cavado hoyos; Mas no obran según tu ley.', 'water'),
('Old', 'Salmos', 119, 86, 'Todos tus mandamientos son verdad: Sin causa me persiguen; ayúdame.', 'water'),
('Old', 'Salmos', 119, 87, 'Casi me han echado por tierra: Mas yo no he dejado tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 88, 'Vivifícame conforme á tu misericordia; Y guardaré los testimonios de tu boca.', 'water'),
('Old', 'Salmos', 119, 89, 'LAMED. Para siempre, oh Jehová, Permenece tu palabra en los cielos.', 'water'),
('Old', 'Salmos', 119, 90, 'Por generación y generación es tu verdad: Tú afirmaste la tierra, y persevera.', 'water'),
('Old', 'Salmos', 119, 91, 'Por tu ordenación perseveran hasta hoy las cosas criadas; Porque todas ellas te sirven.', 'water'),
('Old', 'Salmos', 119, 92, 'Si tu ley no hubiese sido mis delicias, Ya en mi aflicción hubiera perecido.', 'water'),
('Old', 'Salmos', 119, 93, 'Nunca jamás me olvidaré de tus mandamientos; Porque con ellos me has vivificado.', 'water'),
('Old', 'Salmos', 119, 94, 'Tuyo soy yo, guárdame; Porque he buscado tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 95, 'Los impíos me han aguardado para destruirme: Mas yo entenderé en tus testimonios.', 'water'),
('Old', 'Salmos', 119, 96, 'A toda perfección he visto fin: Ancho sobremanera es tu mandamiento.', 'water'),
('Old', 'Salmos', 119, 97, 'MEM. ¡Cuánto amo yo tu ley! Todo el día es ella mi meditación.', 'water'),
('Old', 'Salmos', 119, 98, 'Me has hecho más sabio que mis enemigos con tus mandamientos; Porque me son eternos.', 'water'),
('Old', 'Salmos', 119, 99, 'Más que todos mis enseñadores he entendido: Porque tus testimonios son mi meditación.', 'water'),
('Old', 'Salmos', 119, 100, 'Más que los viejos he entendido, Porque he guardado tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 101, 'De todo mal camino contuve mis pies, Para guardar tu palabra.', 'water');