INSERT INTO scriptures (testament, book_name, chapter, verse_number, content, visual_theme) VALUES
('Old', 'Salmos', 119, 102, 'No me aparté de tus juicios; Porque tú me enseñaste.', 'water'),
('Old', 'Salmos', 119, 103, '¡Cuán dulces son á mi paladar tus palabras! Más que la miel á mi boca.', 'water'),
('Old', 'Salmos', 119, 104, 'De tus mandamientos he adquirido inteligencia: Por tanto he aborrecido todo camino de mentira.', 'water'),
('Old', 'Salmos', 119, 105, 'NUN. Lámpara es á mis pies tu palabra, Y lumbrera á mi camino.', 'water'),
('Old', 'Salmos', 119, 106, 'Juré y ratifiqué El guardar los juicios de tu justicia.', 'water'),
('Old', 'Salmos', 119, 107, 'Afligido estoy en gran manera: oh Jehová, Vivifícame conforme á tu palabra.', 'water'),
('Old', 'Salmos', 119, 108, 'Ruégote, oh Jehová, te sean agradables los sacrificios voluntarios de mi boca; Y enséñame tus juicios.', 'water'),
('Old', 'Salmos', 119, 109, 'De continuo está mi alma en mi mano: Mas no me he olvidado de tu ley.', 'water'),
('Old', 'Salmos', 119, 110, 'Pusiéronme lazo los impíos: Empero yo no me desvié de tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 111, 'Por heredad he tomado tus testimonios para siempre; Porque son el gozo de mi corazón.', 'water'),
('Old', 'Salmos', 119, 112, 'Mi corazón incliné á poner por obra tus estatutos De continuo, hasta el fin.', 'water'),
('Old', 'Salmos', 119, 113, 'SAMECH. Los pensamientos vanos aborrezco; Mas amo tu ley.', 'water'),
('Old', 'Salmos', 119, 114, 'Mi escondedero y mi escudo eres tú: En tu palabra he esperado.', 'water'),
('Old', 'Salmos', 119, 115, 'Apartaos de mí, malignos; Pues yo guardaré los mandamientos de mi Dios.', 'water'),
('Old', 'Salmos', 119, 116, 'Susténtame conforme á tu palabra, y viviré: Y no me avergüences de mi esperanza.', 'water'),
('Old', 'Salmos', 119, 117, 'Sosténme, y seré salvo; Y deleitaréme siempre en tus estatutos.', 'water'),
('Old', 'Salmos', 119, 118, 'Hollaste á todos los que se desvían de tus estatutos: Porque mentira es su engaño.', 'water'),
('Old', 'Salmos', 119, 119, 'Como escorias hiciste consumir á todos los impíos de la tierra: Por tanto yo he amado tus testimonios.', 'water'),
('Old', 'Salmos', 119, 120, 'Mi carne se ha extremecido por temor de ti; Y de tus juicios tengo miedo.', 'water'),
('Old', 'Salmos', 119, 121, 'AIN. Juicio y justicia he hecho; No me dejes á mis opresores.', 'water'),
('Old', 'Salmos', 119, 122, 'Responde por tu siervo para bien: No me hagan violencia los soberbios.', 'water'),
('Old', 'Salmos', 119, 123, 'Mis ojos desfallecieron por tu salud, Y por el dicho de tu justicia.', 'water'),
('Old', 'Salmos', 119, 124, 'Haz con tu siervo según tu misericordia, Y enséñame tus estatutos.', 'water'),
('Old', 'Salmos', 119, 125, 'Tu siervo soy yo, dame entendimiento; Para que sepa tus testimonios.', 'water'),
('Old', 'Salmos', 119, 126, 'Tiempo es de hacer, oh Jehová; Disipado han tu ley.', 'water'),
('Old', 'Salmos', 119, 127, 'Por eso he amado tus mandamientos Más que el oro, y más que oro muy puro.', 'water'),
('Old', 'Salmos', 119, 128, 'Por eso todos los mandamientos de todas las cosas estimé rectos: Aborrecí todo camino de mentira.', 'water'),
('Old', 'Salmos', 119, 129, 'PE. Maravillosos son tus testimonios: Por tanto los ha guardado mi alma.', 'water'),
('Old', 'Salmos', 119, 130, 'El principio de tus palabras alumbra; Hace entender á los simples.', 'water'),
('Old', 'Salmos', 119, 131, 'Mi boca abrí y suspiré; Porque deseaba tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 132, 'Mírame, y ten misericordia de mí, Como acostumbras con los que aman tu nombre.', 'water'),
('Old', 'Salmos', 119, 133, 'Ordena mis pasos con tu palabra; Y ninguna iniquidad se enseñoree de mí.', 'water'),
('Old', 'Salmos', 119, 134, 'Redímeme de la violencia de los hombres; Y guardaré tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 135, 'Haz que tu rostro resplandezca sobre tu siervo; Y enséñame tus estatutos.', 'water'),
('Old', 'Salmos', 119, 136, 'Ríos de agua descendieron de mis ojos, Porque no guardaban tu ley.', 'water'),
('Old', 'Salmos', 119, 137, 'TZADDI. Justo eres tú, oh Jehová, Y rectos tus juicios.', 'water'),
('Old', 'Salmos', 119, 138, 'Tus testimonios, que has recomendado, Son rectos y muy fieles.', 'water'),
('Old', 'Salmos', 119, 139, 'Mi celo me ha consumido; Porque mis enemigos se olvidaron de tus palabras.', 'water'),
('Old', 'Salmos', 119, 140, 'Sumamente acendrada es tu palabra; Y la ama tu siervo.', 'water'),
('Old', 'Salmos', 119, 141, 'Pequeño soy yo y desechado; Mas no me he olvidado de tus mandamientos.', 'water'),
('Old', 'Salmos', 119, 142, 'Tu justicia es justicia eterna, Y tu ley la verdad.', 'water'),
('Old', 'Salmos', 119, 143, 'Aflicción y angustia me hallaron: Mas tus mandamientos fueron mis deleites.', 'water'),
('Old', 'Salmos', 119, 144, 'Justicia eterna son tus testimonios; Dame entendimiento, y viviré.', 'water'),
('Old', 'Salmos', 119, 145, 'COPH. Clamé con todo mi corazón; respóndeme, Jehová, Y guardaré tus estatutos.', 'water'),
('Old', 'Salmos', 119, 146, 'A ti clamé; sálvame, Y guardaré tus testimonios.', 'water'),
('Old', 'Salmos', 119, 147, 'Anticipéme al alba, y clamé: Esperé en tu palabra.', 'water'),
('Old', 'Salmos', 119, 148, 'Previnieron mis ojos las vigilias de la noche, Para meditar en tus dichos.', 'water'),
('Old', 'Salmos', 119, 149, 'Oye mi voz conforme á tu misericordia; Oh Jehová, vivifícame conforme á tu juicio.', 'water'),
('Old', 'Salmos', 119, 150, 'Acercáronse á la maldad los que me persiguen; Alejáronse de tu ley.', 'water'),
('Old', 'Salmos', 119, 151, 'Cercano estás tú, oh Jehová; Y todos tus mandamientos son verdad.', 'water'),
('Old', 'Salmos', 119, 152, 'Ya ha mucho que he entendido de tus mandamientos, Que para siempre los fundaste.', 'water'),
('Old', 'Salmos', 119, 153, 'RESH. Mira mi aflicción, y líbrame; Porque de tu ley no me he olvidado.', 'water'),
('Old', 'Salmos', 119, 154, 'Aboga mi causa, y redímeme: Vivifícame con tu dicho.', 'water'),
('Old', 'Salmos', 119, 155, 'Lejos está de los impíos la salud; Porque no buscan tus estatutos.', 'water'),
('Old', 'Salmos', 119, 156, 'Muchas son tus misericordias, oh Jehová: Vivifícame conforme á tus juicios.', 'water'),
('Old', 'Salmos', 119, 157, 'Muchos son mis perseguidores y mis enemigos; Mas de tus testimonios no me he apartado.', 'water'),
('Old', 'Salmos', 119, 158, 'Veía á los prevaricadores, y carcomíame; Porque no guardaban tus palabras.', 'water'),
('Old', 'Salmos', 119, 159, 'Mira, oh Jehová, que amo tus mandamientos: Vivifícame conforme á tu misericordia.', 'water'),
('Old', 'Salmos', 119, 160, 'El principio de tu palabra es verdad; Y eterno es todo juicio de tu justicia.', 'water'),
('Old', 'Salmos', 119, 161, 'SIN. Príncipes me han perseguido sin causa; Mas mi corazón tuvo temor de tus palabras.', 'water'),
('Old', 'Salmos', 119, 162, 'Gózome yo en tu palabra, Como el que halla muchos despojos.', 'water'),
('Old', 'Salmos', 119, 163, 'La mentira aborrezco y abomino: Tu ley amo.', 'water'),
('Old', 'Salmos', 119, 164, 'Siete veces al día te alabo Sobre los juicios de tu justicia.', 'water'),
('Old', 'Salmos', 119, 165, 'Mucha paz tienen los que aman tu ley; Y no hay para ellos tropiezo.', 'water'),
('Old', 'Salmos', 119, 166, 'Tu salud he esperado, oh Jehová; Y tus mandamientos he puesto por obra.', 'water'),
('Old', 'Salmos', 119, 167, 'Mi alma ha guardado tus testimonios, Y helos amado en gran manera.', 'water'),
('Old', 'Salmos', 119, 168, 'Guardado he tus mandamientos y tus testimonios; Porque todos mis caminos están delante de ti.', 'water'),
('Old', 'Salmos', 119, 169, 'TAU. Acérquese mi clamor delante de ti, oh Jehová: Dame entendimiento conforme á tu palabra.', 'water'),
('Old', 'Salmos', 119, 170, 'Venga mi oración delante de ti: Líbrame conforme á tu dicho.', 'water'),
('Old', 'Salmos', 119, 171, 'Mis labios rebosarán alabanza, Cuando me enseñares tus estatutos.', 'water'),
('Old', 'Salmos', 119, 172, 'Hablará mi lengua tus dichos; Porque todos tus mandamientos son justicia.', 'water'),
('Old', 'Salmos', 119, 173, 'Sea tu mano en mi socorro; Porque tus mandamientos he escogido.', 'water'),
('Old', 'Salmos', 119, 174, 'Deseado he tu salud, oh Jehová; Y tu ley es mi delicia.', 'water'),
('Old', 'Salmos', 119, 175, 'Viva mi alma y alábete; Y tus juicios me ayuden.', 'water'),
('Old', 'Salmos', 119, 176, 'Yo anduve errante como oveja extraviada; busca á tu siervo; Porque no me he olvidado de tus mandamientos.', 'water'),
('Old', 'Salmos', 120, 1, 'A JEHOVA llamé estando en angustia, Y él me respondió.', 'water'),
('Old', 'Salmos', 120, 2, 'Libra mi alma, oh Jehová, de labio mentiroso, De la lengua fraudulenta.', 'water'),
('Old', 'Salmos', 120, 3, '¿Qué te dará, ó qué te aprovechará, Oh lengua engañosa?', 'water'),
('Old', 'Salmos', 120, 4, 'Agudas saetas de valiente, Con brasas de enebro.', 'water'),
('Old', 'Salmos', 120, 5, '¡Ay de mí, que peregrino en Mesech, Y habito entre las tiendas de Kedar!', 'water'),
('Old', 'Salmos', 120, 6, 'Mucho se detiene mi alma Con los que aborrecen la paz.', 'water'),
('Old', 'Salmos', 120, 7, 'Yo soy pacífico: Mas ellos, así que hablo, me hacen guerra.', 'water'),
('Old', 'Salmos', 121, 1, 'ALZARÉ mis ojos á los montes, De donde vendrá mi socorro.', 'water'),
('Old', 'Salmos', 121, 2, 'Mi socorro viene de Jehová, Que hizo los cielos y la tierra.', 'water'),
('Old', 'Salmos', 121, 3, 'No dará tu pie al resbaladero; Ni se dormirá el que te guarda.', 'water'),
('Old', 'Salmos', 121, 4, 'He aquí, no se adormecerá ni dormirá El que guarda á Israel.', 'water'),
('Old', 'Salmos', 121, 5, 'Jehová es tu guardador: Jehová es tu sombra á tu mano derecha.', 'water'),
('Old', 'Salmos', 121, 6, 'El sol no te fatigará de día, Ni la luna de noche.', 'water'),
('Old', 'Salmos', 121, 7, 'Jehová te guardará de todo mal: El guardará tu alma.', 'water'),
('Old', 'Salmos', 121, 8, 'Jehová guardará tu salida y tu entrada, Desde ahora y para siempre.', 'water'),
('Old', 'Salmos', 122, 1, 'YO me alegré con los que me decían: A la casa de Jehová iremos.', 'water'),
('Old', 'Salmos', 122, 2, 'Nuestros pies estuvieron En tus puertas, oh Jerusalem;', 'water'),
('Old', 'Salmos', 122, 3, 'Jerusalem, que se ha edificado Como una ciudad que está bien unida entre sí.', 'water'),
('Old', 'Salmos', 122, 4, 'Y allá subieron las tribus, las tribus de JAH, Conforme al testimonio dado á Israel, Para alabar el nombre de Jehová.', 'water'),
('Old', 'Salmos', 122, 5, 'Porque allá están las sillas del juicio, Las sillas de la casa de David.', 'water'),
('Old', 'Salmos', 122, 6, 'Pedid la paz de Jerusalem: Sean prosperados los que te aman.', 'water'),
('Old', 'Salmos', 122, 7, 'Haya paz en tu antemuro, Y descanso en tus palacios.', 'water'),
('Old', 'Salmos', 122, 8, 'Por amor de mis hermanos y mis compañeros Hablaré ahora paz de ti.', 'water'),
('Old', 'Salmos', 122, 9, 'A causa de la casa de Jehová nuestro Dios, Buscaré bien para ti.', 'water'),
('Old', 'Salmos', 123, 1, 'A TI que habitas en los cielos, Alcé mis ojos.', 'water'),
('Old', 'Salmos', 123, 2, 'He aquí como los ojos de los siervos miran á la mano de sus señores, Y como los ojos de la sierva á la mano de su señora; Así nuestros ojos miran á Jehová nuestro Dios, Hasta que haya misericordia de nosotros.', 'water'),
('Old', 'Salmos', 123, 3, 'Ten misericordia de nosotros, oh Jehová, ten misericordia de nosotros; Porque estamos muy hartos de menosprecio.', 'water'),
('Old', 'Salmos', 123, 4, 'Muy harta está nuestra alma Del escarnio de los holgados, Y del menosprecio de los soberbios.', 'water'),
('Old', 'Salmos', 124, 1, 'A NO haber estado Jehová por nosotros, Diga ahora Israel;', 'water'),
('Old', 'Salmos', 124, 2, 'A no haber estado Jehová por nosotros, Cuando se levantaron contra nosotros los hombres,', 'water'),
('Old', 'Salmos', 124, 3, 'Vivos nos habrían entonces tragado, Cuando se encendió su furor en nosotros.', 'water'),
('Old', 'Salmos', 124, 4, 'Entonces nos habrían inundado las aguas; Sobre nuestra alma hubiera pasado el torrente:', 'water'),
('Old', 'Salmos', 124, 5, 'Hubieran entonces pasado sobre nuestra alma las aguas soberbias.', 'water'),
('Old', 'Salmos', 124, 6, 'Bendito Jehová, Que no nos dió por presa á sus dientes.', 'water'),
('Old', 'Salmos', 124, 7, 'Nuestra alma escapó cual ave del lazo de los cazadores: Quebróse el lazo, y escapamos nosotros.', 'water'),
('Old', 'Salmos', 124, 8, 'Nuestro socorro es en el nombre de Jehová, Que hizo el cielo y la tierra.', 'water'),
('Old', 'Salmos', 125, 1, 'LOS que confían en Jehová Son como el monte de Sión que no deslizará: estará para siempre.', 'water'),
('Old', 'Salmos', 125, 2, 'Como Jerusalem tiene montes alrededor de ella, Así Jehová alrededor de su pueblo Desde ahora y para siempre.', 'water'),
('Old', 'Salmos', 125, 3, 'Porque no reposará la vara de la impiedad sobre la suerte de los justos; Porque no extiendan los justos sus manos á la iniquidad.', 'water'),
('Old', 'Salmos', 125, 4, 'Haz bien, oh Jehová, á los buenos, Y á los que son rectos en sus corazones.', 'water'),
('Old', 'Salmos', 125, 5, 'Mas á los que se apartan tras sus perversidades, Jehová los llevará con los que obran iniquidad: Y paz sea sobre Israel.', 'water'),
('Old', 'Salmos', 126, 1, 'CUANDO Jehová hiciere tornar la cautividad de Sión, Seremos como los que sueñan.', 'water'),
('Old', 'Salmos', 126, 2, 'Entonces nuestra boca se henchirá de risa, Y nuestra lengua de alabanza; Entonces dirán entre las gentes: Grandes cosas ha hecho Jehová con éstos.', 'water'),
('Old', 'Salmos', 126, 3, 'Grandes cosas ha hecho Jehová con nosotros; Estaremos alegres.', 'water'),
('Old', 'Salmos', 126, 4, 'Haz volver nuestra cautividad oh Jehová, Como los arroyos en el austro.', 'water'),
('Old', 'Salmos', 126, 5, 'Los que sembraron con lágrimas, con regocijo segarán.', 'water'),
('Old', 'Salmos', 126, 6, 'Irá andando y llorando el que lleva la preciosa simiente; Mas volverá á venir con regocijo, trayendo sus gavillas.', 'water'),
('Old', 'Salmos', 127, 1, 'SI Jehová no edificare la casa, En vano trabajan los que la edifican: Si Jehová no guardare la ciudad, En vano vela la guarda.', 'water'),
('Old', 'Salmos', 127, 2, 'Por demás os es el madrugar á levantaros, el veniros tarde á reposar, El comer pan de dolores: Pues que á su amado dará Dios el sueño.', 'water'),
('Old', 'Salmos', 127, 3, 'He aquí, heredad de Jehová son los hijos: Cosa de estima el fruto del vientre.', 'water'),
('Old', 'Salmos', 127, 4, 'Como saetas en mano del valiente, Así son los hijos habidos en la juventud.', 'water'),
('Old', 'Salmos', 127, 5, 'Bienaventurado el hombre que hinchió su aljaba de ellos: No será avergonzado Cuando hablare con los enemigos en la puerta.', 'water'),
('Old', 'Salmos', 128, 1, 'BIENAVENTURADO todo aquel que teme á Jehová, Que anda en sus caminos.', 'water'),
('Old', 'Salmos', 128, 2, 'Cuando comieres el trabajo de tus manos, Bienaventurado tú, y tendrás bien.', 'water'),
('Old', 'Salmos', 128, 3, 'Tu mujer será como parra que lleva fruto á los lados de tu casa; Tus hijos como plantas de olivas alrededor de tu mesa.', 'water'),
('Old', 'Salmos', 128, 4, 'He aquí que así será bendito el hombre Que teme á Jehová.', 'water'),
('Old', 'Salmos', 128, 5, 'Bendígate Jehová desde Sión, Y veas el bien de Jerusalem todos los días de tu vida.', 'water'),
('Old', 'Salmos', 128, 6, 'Y veas los hijos de tus hijos, Y la paz sobre Israel.', 'water'),
('Old', 'Salmos', 129, 1, 'MUCHO me han angustiado desde mi juventud, Puede decir ahora Israel;', 'water'),
('Old', 'Salmos', 129, 2, 'Mucho me han angustiado desde mi juventud; Mas no prevalecieron contra mí.', 'water'),
('Old', 'Salmos', 129, 3, 'Sobre mis espaldas araron los aradores: Hicieron largos surcos.', 'water'),
('Old', 'Salmos', 129, 4, 'Jehová es justo; Cortó las coyundas de los impíos.', 'water'),
('Old', 'Salmos', 129, 5, 'Serán avergonzados y vueltos atrás Todos los que aborrecen á Sión.', 'water'),
('Old', 'Salmos', 129, 6, 'Serán como la hierba de los tejados, Que se seca antes que crezca:', 'water'),
('Old', 'Salmos', 129, 7, 'De la cual no hinchió segador su mano, Ni sus brazos el que hace gavillas.', 'water'),
('Old', 'Salmos', 129, 8, 'Ni dijeron los que pasaban: Bendición de Jehová sea sobre vosotros; Os bendecimos en el nombre de Jehová.', 'water'),
('Old', 'Salmos', 130, 1, 'DE los profundos, oh Jehová, á ti clamo.', 'water'),
('Old', 'Salmos', 130, 2, 'Señor, oye mi voz; Estén atentos tus oídos A la voz de mi súplica.', 'water'),
('Old', 'Salmos', 130, 3, 'JAH, si mirares á los pecados, ¿Quién, oh Señor, podrá mantenerse?', 'water'),
('Old', 'Salmos', 130, 4, 'Empero hay perdón cerca de ti, Para que seas temido.', 'water'),
('Old', 'Salmos', 130, 5, 'Esperé yo á Jehová, esperó mi alma; En su palabra he esperado.', 'water'),
('Old', 'Salmos', 130, 6, 'Mi alma espera á Jehová Más que los centinelas á la mañana. Más que los vigilantes á la mañana.', 'water'),
('Old', 'Salmos', 130, 7, 'Espere Israel á Jehová; Porque en Jehová hay misericordia. Y abundante redención con él.', 'water'),
('Old', 'Salmos', 130, 8, 'Y él redimirá á Israel De todos sus pecados.', 'water'),
('Old', 'Salmos', 131, 1, 'JEHOVA, no se ha envanecido mi corazón, ni mis ojos se enaltecieron; Ni anduve en grandezas, Ni en cosas para mí demasiado sublimes.', 'water'),
('Old', 'Salmos', 131, 2, 'En verdad que me he comportado y he acallado mi alma, Como un niño destetado de su madre: Como un niño destetado está mi alma.', 'water'),
('Old', 'Salmos', 131, 3, 'Espera, oh Israel, en Jehová Desde ahora y para siempre.', 'water'),
('Old', 'Salmos', 132, 1, 'ACUÉRDATE, oh Jehová, de David, Y de toda su aflicción;', 'water'),
('Old', 'Salmos', 132, 2, 'Que juró él á Jehová, Prometió al Fuerte de Jacob:', 'water'),
('Old', 'Salmos', 132, 3, 'No entraré en la morada de mi casa, Ni subiré sobre el lecho de mi estrado;', 'water'),
('Old', 'Salmos', 132, 4, 'No daré sueño á mis ojos, Ni á mis párpados adormecimiento.', 'water'),
('Old', 'Salmos', 132, 5, 'Hasta que halle lugar para Jehová, Moradas para el Fuerte de Jacob.', 'water'),
('Old', 'Salmos', 132, 6, 'He aquí, en Ephrata oímos de ella: Hallamósla en los campos del bosque.', 'water'),
('Old', 'Salmos', 132, 7, 'Entraremos en sus tiendas; Encorvarnos hemos al estrado de sus pies.', 'water'),
('Old', 'Salmos', 132, 8, 'Levántate, oh Jehová, á tu reposo; Tú y el arca de tu fortaleza.', 'water'),
('Old', 'Salmos', 132, 9, 'Tus sacerdotes se vistan de justicia, Y regocíjense tus santos.', 'water'),
('Old', 'Salmos', 132, 10, 'Por amor de David tu siervo No vuelvas de tu ungido el rostro.', 'water'),
('Old', 'Salmos', 132, 11, 'En verdad juró Jehová á David, No se apartará de ellos: Del fruto de tu vientre pondré sobre tu trono.', 'water'),
('Old', 'Salmos', 132, 12, 'Si tus hijos guardaren mi alianza, Y mi testimonio que yo les enseñaré, Sus hijos también se sentarán sobre tu trono para siempre.', 'water'),
('Old', 'Salmos', 132, 13, 'Porque Jehová ha elegido á Sión; Deseóla por habitación para sí.', 'water'),
('Old', 'Salmos', 132, 14, 'Este es mi reposo para siempre: Aquí habitaré, porque la he deseado.', 'water'),
('Old', 'Salmos', 132, 15, 'A su mantenimiento daré bendición: Sus pobres saciaré de pan.', 'water'),
('Old', 'Salmos', 132, 16, 'Asimismo vestiré á sus sacerdotes de salud, Y sus santos darán voces de júbilo.', 'water'),
('Old', 'Salmos', 132, 17, 'Allí haré reverdecer el cuerno de David: He prevenido lámpara á mi ungido.', 'water'),
('Old', 'Salmos', 132, 18, 'A sus enemigos vestiré de confusión: Mas sobre él florecerá su corona.', 'water'),
('Old', 'Salmos', 133, 1, '¡MIRAD cuán bueno y cuán delicioso es Habitar los hermanos igualmente en uno!', 'water'),
('Old', 'Salmos', 133, 2, 'Es como el buen óleo sobre la cabeza, El cual desciende sobre la barba, La barba de Aarón, Y que baja hasta el borde de sus vestiduras;', 'water'),
('Old', 'Salmos', 133, 3, 'Como el rocío de Hermón, Que desciende sobre los montes de Sión: Porque allí envía Jehová bendición, Y vida eterna.', 'water'),
('Old', 'Salmos', 134, 1, 'MIRAD, bendecid á Jehová, Vosotros todos los siervos de Jehová, Los que en la casa de Jehová estáis por las noches.', 'water'),
('Old', 'Salmos', 134, 2, 'Alzad vuestras manos al santuario, Y bendecid á Jehová.', 'water'),
('Old', 'Salmos', 134, 3, 'Bendígate Jehová desde Sión, El cual ha hecho los cielos y la tierra.', 'water'),
('Old', 'Salmos', 135, 1, 'ALABAD el nombre de Jehová; Alabad le, siervos de Jehová;', 'water'),
('Old', 'Salmos', 135, 2, 'Los que estáis en la casa de Jehová, En los atrios de la casa de nuestro Dios.', 'water'),
('Old', 'Salmos', 135, 3, 'Alabad á JAH, porque es bueno Jehová: Cantad salmos á su nombre, porque es suave.', 'water'),
('Old', 'Salmos', 135, 4, 'Porque JAH ha escogido á Jacob para sí, A Israel por posesión suya.', 'water'),
('Old', 'Salmos', 135, 5, 'Porque yo se que Jehová es grande, Y el Señor nuestro, mayor que todos los dioses.', 'water'),
('Old', 'Salmos', 135, 6, 'Todo lo que quiso Jehová, ha hecho En los cielos y en la tierra, en las mares y en todos los abismos.', 'water'),
('Old', 'Salmos', 135, 7, 'El hace subir las nubes del cabo de la tierra; El hizo los relámpagos para la lluvia; El saca los vientos de sus tesoros.', 'water'),
('Old', 'Salmos', 135, 8, 'El es el que hirió los primogénitos de Egipto, Desde el hombre hasta la bestia.', 'water'),
('Old', 'Salmos', 135, 9, 'Envió señales y prodigios en medio de ti, oh Egipto, Sobre Faraón, y sobre todos sus siervos.', 'water'),
('Old', 'Salmos', 135, 10, 'El que hirió muchas gentes, Y mató reyes poderosos:', 'water'),
('Old', 'Salmos', 135, 11, 'A Sehón rey Amorrheo, Y á Og rey de Basán, Y á todos los reinos de Canaán.', 'water'),
('Old', 'Salmos', 135, 12, 'Y dió la tierra de ellos en heredad, En heredad á Israel su pueblo.', 'water'),
('Old', 'Salmos', 135, 13, 'Oh Jehová, eterno es tu nombre; Tu memoria, oh Jehová para generación y generación.', 'water'),
('Old', 'Salmos', 135, 14, 'Porque juzgará Jehová su pueblo, Y arrepentiráse sobre sus siervos.', 'water'),
('Old', 'Salmos', 135, 15, 'Los ídolos de las gentes son plata y oro, Obra de manos de hombres.', 'water'),
('Old', 'Salmos', 135, 16, 'Tienen boca, y no hablan; Tienen ojos, y no ven;', 'water'),
('Old', 'Salmos', 135, 17, 'Tienen orejas, y no oyen; Tampoco hay espíritu en sus bocas.', 'water'),
('Old', 'Salmos', 135, 18, 'Como ellos son los que los hacen; Todos los que en ellos confían.', 'water'),
('Old', 'Salmos', 135, 19, 'Casa de Israel, bendecid á Jehová: Casa de Aarón, bendecid á Jehová:', 'water'),
('Old', 'Salmos', 135, 20, 'Casa de Leví, bendecid á Jehová: Los que teméis á Jehová, bendecid á Jehová:', 'water'),
('Old', 'Salmos', 135, 21, 'Bendito de Sión Jehová, Que mora en Jerusalem. Aleluya.', 'water'),
('Old', 'Salmos', 136, 1, 'ALABAD á Jehová, porque es bueno; Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 136, 2, 'Alabad al Dios de los dioses, Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 136, 3, 'Alabad al Señor de los señores, Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 136, 4, 'Al solo que hace grandes maravillas, Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 136, 5, 'Al que hizo los cielos con entendimiento, Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 136, 6, 'Al que tendió la tierra sobre las aguas, Porque para siempre es su misericordia;', 'water'),
('Old', 'Salmos', 136, 7, 'Al que hizo las grandes luminarias, Porque para siempre es su misericordia;', 'water'),
('Old', 'Salmos', 136, 8, 'El sol para que dominase en el día, Porque para siempre es su misericordia;', 'water'),
('Old', 'Salmos', 136, 9, 'La luna y las estrellas para que dominasen en la noche, Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 136, 10, 'Al que hirió á Egipto en sus primogénitos, Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 136, 11, 'Al que sacó á Israel de en medio de ellos, Porque para siempre es su misericordia;', 'water'),
('Old', 'Salmos', 136, 12, 'Con mano fuerte, y brazo extendido, Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 136, 13, 'Al que dividió el mar Bermejo en partes, Porque para siempre es su misericordia;', 'water'),
('Old', 'Salmos', 136, 14, 'E hizo pasar á Israel por medio de él, Porque para siempre es su misericordia;', 'water'),
('Old', 'Salmos', 136, 15, 'Y arrojó á Faraón y á su ejército en el mar Bermejo, Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 136, 16, 'Al que pastoreó á su pueblo por el desierto, Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 136, 17, 'Al que hirió grandes reyes, Porque para siempre es su misericordia;', 'water'),
('Old', 'Salmos', 136, 18, 'Y mató reyes poderosos, Porque para siempre es su misericordia;', 'water'),
('Old', 'Salmos', 136, 19, 'A Sehón rey Amorrheo, Porque para siempre es su misericordia,', 'water'),
('Old', 'Salmos', 136, 20, 'Y á Og rey de Basán, Porque para siempre es su misericordia;', 'water'),
('Old', 'Salmos', 136, 21, 'Y dió la tierra de ellos en heredad, Porque para siempre es su misericordia;', 'water'),
('Old', 'Salmos', 136, 22, 'En heredad á Israel su siervo, Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 136, 23, 'El es el que en nuestro abatimiento se acordó de nosotros, Porque para siempre es su misericordia;', 'water'),
('Old', 'Salmos', 136, 24, 'Y nos rescató de nuestros enemigos, Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 136, 25, 'El da mantenimiento á toda carne, Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 136, 26, 'Alabad al Dios de los cielos: Porque para siempre es su misericordia.', 'water'),
('Old', 'Salmos', 137, 1, 'JUNTO á los ríos de Babilonia, Allí nos sentábamos, y aun llorábamos, Acordándonos de Sión.', 'water'),
('Old', 'Salmos', 137, 2, 'Sobre los sauces en medio de ella Colgamos nuestras arpas.', 'water'),
('Old', 'Salmos', 137, 3, 'Y los que allí nos habían llevado cautivos nos pedían que cantásemos, Y los que nos habían desolado nos pedían alegría, diciendo:', 'water'),
('Old', 'Salmos', 137, 4, 'Cantadnos algunos de los himnos de Sión. ¿Cómo cantaremos canción de Jehová En tierra de extraños?', 'water'),
('Old', 'Salmos', 137, 5, 'Si me olvidare de ti, oh Jerusalem, Mi diestra sea olvidada.', 'water'),
('Old', 'Salmos', 137, 6, 'Mi lengua se pegue á mi paladar, Si de ti no me acordare; Si no ensalzare á Jerusalem Como preferente asunto de mi alegría.', 'water'),
('Old', 'Salmos', 137, 7, 'Acuérdate, oh Jehová, de los hijos de Edom En el día de Jerusalem; Quienes decían: Arrasadla, arrasadla Hasta los cimientos.', 'water'),
('Old', 'Salmos', 137, 8, 'Hija de Babilonia destruída, Bienaventurado el que te diere el pago De lo que tú nos hiciste.', 'water'),
('Old', 'Salmos', 137, 9, 'Bienaventurado el que tomará y estrellará tus niños Contra las piedras.', 'water'),
('Old', 'Salmos', 138, 1, 'ALABARTE he con todo mi corazón: Delante de los dioses te cantaré salmos.', 'water'),
('Old', 'Salmos', 138, 2, 'Encorvaréme al templo de tu santuario, Y alabaré tu nombre por tu misericordia y tu verdad: Porque has hecho magnífico tu nombre, y tu dicho sobre todas las cosas.', 'water'),
('Old', 'Salmos', 138, 3, 'En el día que clamé, me respondiste; Esforzásteme con fortaleza en mi alma.', 'water'),
('Old', 'Salmos', 138, 4, 'Confesarte han, oh Jehová, todos los reyes de la tierra, Cuando habrán oído los dichos de tu boca.', 'water'),
('Old', 'Salmos', 138, 5, 'Y cantarán de los caminos de Jehová: Que la gloria de Jehová es grande.', 'water'),
('Old', 'Salmos', 138, 6, 'Porque el alto Jehová atiende al humilde; Mas al altivo mira de lejos.', 'water'),
('Old', 'Salmos', 138, 7, 'Si anduviere yo en medio de la angustia, tú me vivificarás: Contra la ira de mis enemigos extenderás tu mano, Y salvaráme tu diestra.', 'water'),
('Old', 'Salmos', 138, 8, 'Jehová cumplirá por mí: Tu misericordia, oh Jehová, es para siempre; No dejarás la obra de tus manos.', 'water'),
('Old', 'Salmos', 139, 1, 'OH Jehová, tú me has examinado y conocido.', 'water'),
('Old', 'Salmos', 139, 2, 'Tú has conocido mi sentarme y mi levantarme, Has entendido desde lejos mis pensamientos.', 'water'),
('Old', 'Salmos', 139, 3, 'Mi senda y mi acostarme has rodeado, Y estás impuesto en todos mis caminos.', 'water'),
('Old', 'Salmos', 139, 4, 'Pues aun no está la palabra en mi lengua, Y he aquí, oh Jehová, tú la sabes toda.', 'water'),
('Old', 'Salmos', 139, 5, 'Detrás y delante me guarneciste, Y sobre mí pusiste tu mano.', 'water'),
('Old', 'Salmos', 139, 6, 'Más maravillosa es la ciencia que mi capacidad; Alta es, no puedo comprenderla.', 'water'),
('Old', 'Salmos', 139, 7, '¿Adónde me iré de tu espíritu? ¿Y adónde huiré de tu presencia?', 'water'),
('Old', 'Salmos', 139, 8, 'Si subiere á los cielos, allí estás tú: Y si en abismo hiciere mi estrado, he aquí allí tú estás.', 'water'),
('Old', 'Salmos', 139, 9, 'Si tomare las alas del alba, Y habitare en el extremo de la mar,', 'water'),
('Old', 'Salmos', 139, 10, 'Aun allí me guiará tu mano, Y me asirá tu diestra.', 'water'),
('Old', 'Salmos', 139, 11, 'Si dijere: Ciertamente las tinieblas me encubrirán; Aun la noche resplandecerá tocante á mí.', 'water'),
('Old', 'Salmos', 139, 12, 'Aun las tinieblas no encubren de ti, Y la noche resplandece como el día: Lo mismo te son las tinieblas que la luz.', 'water'),
('Old', 'Salmos', 139, 13, 'Porque tú poseiste mis riñones; Cubrísteme en el vientre de mi madre.', 'water'),
('Old', 'Salmos', 139, 14, 'Te alabaré; porque formidables, maravillosas son tus obras: Estoy maravillado, Y mi alma lo conoce mucho.', 'water'),
('Old', 'Salmos', 139, 15, 'No fué encubierto de ti mi cuerpo, Bien que en oculto fuí formado, Y compaginado en lo más bajo de la tierra.', 'water'),
('Old', 'Salmos', 139, 16, 'Mi embrión vieron tus ojos, Y en tu libro estaban escritas todas aquellas cosas Que fueron luego formadas, Sin faltar una de ellas.', 'water'),
('Old', 'Salmos', 139, 17, 'Así que ¡cuán preciosos me son, oh Dios, tus pensamientos! ¡Cuán multiplicadas son sus cuentas!', 'water'),
('Old', 'Salmos', 139, 18, 'Si los cuento, multiplícanse más que la arena: Despierto, y aun estoy contigo.', 'water'),
('Old', 'Salmos', 139, 19, 'De cierto, oh Dios, matarás al impío; Apartaos pues de mí, hombres sanguinarios.', 'water'),
('Old', 'Salmos', 139, 20, 'Porque blasfemias dicen ellos contra ti: Tus enemigos toman en vano tu nombre', 'water'),
('Old', 'Salmos', 139, 21, '¿No tengo en odio, oh Jehová, á los que te aborrecen, Y me conmuevo contra tus enemigos?', 'water'),
('Old', 'Salmos', 139, 22, 'Aborrézcolos con perfecto odio; Téngolos por enemigos.', 'water'),
('Old', 'Salmos', 139, 23, 'Examíname, oh Dios, y conoce mi corazón: Pruébame y reconoce mis pensamientos:', 'water'),
('Old', 'Salmos', 139, 24, 'Y ve si hay en mí camino de perversidad, Y guíame en el camino eterno.', 'water'),
('Old', 'Salmos', 140, 1, 'LIBRAME, oh Jehová, de hombre malo: Guárdame de hombre violento;', 'water'),
('Old', 'Salmos', 140, 2, 'Los cuales maquinan males en el corazón, Cada día urden contiendas.', 'water'),
('Old', 'Salmos', 140, 3, 'Aguzaron su lengua como la serpiente; Veneno de áspid hay debajo de sus labios. (Selah.)', 'water'),
('Old', 'Salmos', 140, 4, 'Guárdame, oh Jehová, de manos del impío, Presérvame del hombre injurioso; Que han pensado de trastornar mis pasos.', 'water'),
('Old', 'Salmos', 140, 5, 'Hanme escondido lazo y cuerdas los soberbios; Han tendido red junto á la senda; Me han puesto lazos. (Selah.)', 'water'),
('Old', 'Salmos', 140, 6, 'He dicho á Jehová: Dios mío eres tú; Escucha, oh Jehová, la voz de mis ruegos.', 'water'),
('Old', 'Salmos', 140, 7, 'Jehová Señor, fortaleza de mi salud, Tú pusiste á cubierto mi cabeza el día de las armas.', 'water'),
('Old', 'Salmos', 140, 8, 'No des, oh Jehová, al impío sus deseos; No saques adelante su pensamiento, que no se ensoberbezca. (Selah.)', 'water'),
('Old', 'Salmos', 140, 9, 'En cuanto á los que por todas partes me rodean, La maldad de sus propios labios cubrirá su cabeza.', 'water'),
('Old', 'Salmos', 140, 10, 'Caerán sobre ellos brasas; Dios los hará caer en el fuego, En profundos hoyos de donde no salgan.', 'water'),
('Old', 'Salmos', 140, 11, 'El hombre deslenguado no será firme en la tierra: El mal cazará al hombre injusto para derribarle.', 'water'),
('Old', 'Salmos', 140, 12, 'Yo sé que hará Jehová el juicio del afligido, El juicio de los menesterosos.', 'water'),
('Old', 'Salmos', 140, 13, 'Ciertamente los justos alabarán tu nombre; Los rectos morarán en tu presencia.', 'water'),
('Old', 'Salmos', 141, 1, 'JEHOVA, á ti he clamado; apresúrate á mí; Escucha mi voz, cuando te invocare.', 'water'),
('Old', 'Salmos', 141, 2, 'Sea enderezada mi oración delante de ti como un perfume, El don de mis manos como la ofrenda de la tarde.', 'water'),
('Old', 'Salmos', 141, 3, 'Pon, oh Jehová, guarda á mi boca: Guarda la puerta de mis labios.', 'water'),
('Old', 'Salmos', 141, 4, 'No dejes se incline mi corazón á cosa mala, A hacer obras impías Con los que obran iniquidad, Y no coma yo de sus deleites.', 'water'),
('Old', 'Salmos', 141, 5, 'Que el justo me castigue, será un favor, Y que me reprenda será un excelente bálsamo. Que no me herirá la cabeza: Así que aun mi oración tendrán en sus calamidades.', 'water'),
('Old', 'Salmos', 141, 6, 'Serán derribados en lugares peñascosos sus jueces, Y oirán mis palabras, que son suaves.', 'water'),
('Old', 'Salmos', 141, 7, 'Como quien hiende y rompe la tierra, Son esparcidos nuestros huesos á la boca de la sepultura.', 'water'),
('Old', 'Salmos', 141, 8, 'Por tanto á ti, oh Jehová Señor, miran mis ojos: En ti he confiado, no desampares mi alma.', 'water'),
('Old', 'Salmos', 141, 9, 'Guárdame de los lazos que me han tendido, Y de los armadijos de los que obran iniquidad.', 'water'),
('Old', 'Salmos', 141, 10, 'Caigan los impíos á una en sus redes, Mientras yo pasaré adelante.', 'water'),
('Old', 'Salmos', 142, 1, 'CON mi voz clamaré á Jehová, Con mi voz pediré á Jehová misericordia.', 'water'),
('Old', 'Salmos', 142, 2, 'Delante de él derramaré mi querella; Delante de él denunciaré mi angustia.', 'water'),
('Old', 'Salmos', 142, 3, 'Cuando mi espíritu se angustiaba dentro de mí, tú conociste mi senda. En el camino en que andaba, me escondieron lazo.', 'water'),
('Old', 'Salmos', 142, 4, 'Miraba á la mano derecha, y observaba; mas no había quien me conociese; No tuve refugio, no había quien volviese por mi vida.', 'water'),
('Old', 'Salmos', 142, 5, 'Clamé á ti, oh Jehová, Dije: Tú eres mi esperanza, Y mi porción en la tierra de los vivientes.', 'water'),
('Old', 'Salmos', 142, 6, 'Escucha mi clamor, que estoy muy afligido; Líbrame de los que me persiguen, porque son más fuertes que yo.', 'water'),
('Old', 'Salmos', 142, 7, 'Saca mi alma de la cárcel para que alabe tu nombre: Me rodearán los justos, Porque tú me serás propicio.', 'water'),
('Old', 'Salmos', 143, 1, 'OH Jehová, oye mi oración, escucha mis ruegos: Respóndeme por tu verdad, por tu justicia.', 'water'),
('Old', 'Salmos', 143, 2, 'Y no entres en juicio con tu siervo; Porque no se justificará delante de ti ningún viviente.', 'water'),
('Old', 'Salmos', 143, 3, 'Porque ha perseguido el enemigo mi alma; Ha postrado en tierra mi vida; Hame hecho habitar en tinieblas como los ya muertos.', 'water'),
('Old', 'Salmos', 143, 4, 'Y mi espíritu se angustió dentro de mí; Pasmóse mi corazón.', 'water'),
('Old', 'Salmos', 143, 5, 'Acordéme de los días antiguos; Meditaba en todas tus obras, Reflexionaba en las obras de tus manos.', 'water'),
('Old', 'Salmos', 143, 6, 'Extendí mis manos á ti; Mi alma á ti como la tierra sedienta. (Selah.)', 'water'),
('Old', 'Salmos', 143, 7, 'Respóndeme presto, oh Jehová que desmaya mi espíritu: No escondas de mí tu rostro, Y venga yo á ser semejante á los que descienden á la sepultura.', 'water'),
('Old', 'Salmos', 143, 8, 'Hazme oir por la mañana tu misericordia, Porque en ti he confiado: Hazme saber el camino por donde ande, Porque á ti he alzado mi alma', 'water'),
('Old', 'Salmos', 143, 9, 'Líbrame de mis enemigos, oh Jehová: A ti me acojo.', 'water'),
('Old', 'Salmos', 143, 10, 'Enséñame á hacer tu voluntad, porque tú eres mi Dios: Tu buen espíritu me guíe á tierra de rectitud.', 'water'),
('Old', 'Salmos', 143, 11, 'Por tu nombre, oh Jehová me vivificarás: Por tu justicia, sacarás mi alma de angustia.', 'water'),
('Old', 'Salmos', 143, 12, 'Y por tu misericordia disiparás mis enemigos, Y destruirás todos los adversarios de mi alma: Porque yo soy tu siervo.', 'water'),
('Old', 'Salmos', 144, 1, 'BENDITO sea Jehová, mi roca, Que enseña mis manos á la batalla, Y mis dedos á la guerra:', 'water'),
('Old', 'Salmos', 144, 2, 'Misericordia mía y mi castillo, Altura mía y mi libertador, Escudo mío, en quien he confiado; El que allana mi pueblo delante de mí.', 'water'),
('Old', 'Salmos', 144, 3, 'Oh Jehová, ¿qué es el hombre, para que de él conozcas? ¿O el hijo del hombre, para que lo estimes?', 'water'),
('Old', 'Salmos', 144, 4, 'El hombre es semejante á la vanidad: Sus días son como la sombra que pasa.', 'water'),
('Old', 'Salmos', 144, 5, 'Oh Jehová, inclina tus cielos y desciende: Toca los montes, y humeen.', 'water'),
('Old', 'Salmos', 144, 6, 'Despide relámpagos, y disípalos, Envía tus saetas, y contúrbalos.', 'water'),
('Old', 'Salmos', 144, 7, 'Envía tu mano desde lo alto; Redímeme, y sácame de las muchas aguas, De la mano de los hijos de extraños;', 'water'),
('Old', 'Salmos', 144, 8, 'Cuya boca habla vanidad, Y su diestra es diestra de mentira.', 'water'),
('Old', 'Salmos', 144, 9, 'Oh Dios, á ti cantaré canción nueva: Con salterio, con decacordio cantaré á ti.', 'water'),
('Old', 'Salmos', 144, 10, 'Tú, el que da salud á los reyes, El que redime á David su siervo de maligna espada.', 'water'),
('Old', 'Salmos', 144, 11, 'Redímeme, y sálvame de mano de los hijos extraños, Cuya boca habla vanidad, Y su diestra es diestra de mentira.', 'water'),
('Old', 'Salmos', 144, 12, 'Que nuestros hijos sean como plantas crecidas en su juventud; Nuestras hijas como las esquinas labradas á manera de las de un palacio;', 'water'),
('Old', 'Salmos', 144, 13, 'Nuestros graneros llenos, provistos de toda suerte de grano; Nuestros ganados, que paran á millares y diez millares en nuestras plazas:', 'water'),
('Old', 'Salmos', 144, 14, 'Que nuestros bueyes estén fuertes para el trabajo; Que no tengamos asalto, ni que hacer salida, Ni grito de alarma en nuestras plazas.', 'water'),
('Old', 'Salmos', 144, 15, 'Bienaventurado el pueblo que tiene esto: Bienaventurado el pueblo cuyo Dios es Jehová.', 'water'),
('Old', 'Salmos', 145, 1, 'ENSALZARTE he, mi Dios, mi Rey; Y bendeciré tu nombre por siglo y para siempre.', 'water'),
('Old', 'Salmos', 145, 2, 'Cada día te bendeciré, Y alabaré tu nombre por siglo y para siempre.', 'water'),
('Old', 'Salmos', 145, 3, 'Grande es Jehová y digno de suprema alabanza: Y su grandeza es inescrutable.', 'water'),
('Old', 'Salmos', 145, 4, 'Generación á generación narrará tus obras, Y anunciarán tus valentías.', 'water'),
('Old', 'Salmos', 145, 5, 'La hermosura de la gloria de tu magnificencia, Y tus hechos maravillosos, hablaré.', 'water'),
('Old', 'Salmos', 145, 6, 'Y la terribilidad de tus valentías dirán los hombres; Y yo recontaré tu grandeza.', 'water'),
('Old', 'Salmos', 145, 7, 'Reproducirán la memoria de la muchedumbre de tu bondad, Y cantarán tu justicia.', 'water'),
('Old', 'Salmos', 145, 8, 'Clemente y misericordioso es Jehová, Lento para la ira, y grande en misericordia.', 'water'),
('Old', 'Salmos', 145, 9, 'Bueno es Jehová para con todos; Y sus misericordia sobre todas sus obras.', 'water'),
('Old', 'Salmos', 145, 10, 'Alábente, oh Jehová, todas tus obras; Y tus santos te bendigan.', 'water'),
('Old', 'Salmos', 145, 11, 'La gloria de tu reino digan, Y hablen de tu fortaleza;', 'water'),
('Old', 'Salmos', 145, 12, 'Para notificar á los hijos de los hombre sus valentías, Y la gloria de la magnificencia de su reino.', 'water'),
('Old', 'Salmos', 145, 13, 'Tu reino es reino de todos los siglos, Y tu señorío en toda generación y generación.', 'water'),
('Old', 'Salmos', 145, 14, 'Sostiene Jehová á todos los que caen, Y levanta á todos los oprimidos.', 'water'),
('Old', 'Salmos', 145, 15, 'Los ojos de todos esperan en ti, Y tú les das su comida en su tiempo.', 'water'),
('Old', 'Salmos', 145, 16, 'Abres tu mano, Y colmas de bendición á todo viviente.', 'water'),
('Old', 'Salmos', 145, 17, 'Justo es Jehová en todos sus caminos, Y misericordioso en todas sus obras.', 'water'),
('Old', 'Salmos', 145, 18, 'Cercano está Jehová á todos los que le invocan, A todos los que le invocan de veras.', 'water'),
('Old', 'Salmos', 145, 19, 'Cumplirá el deseo de los que le temen; Oirá asimismo el clamor de ellos, y los salvará.', 'water'),
('Old', 'Salmos', 145, 20, 'Jehová guarda á todos los que le aman; Empero destruirá á todos los impíos.', 'water'),
('Old', 'Salmos', 145, 21, 'La alabanza de Jehová hablará mi boca; Y bendiga toda carne su santo nombre por siglo y para siempre.', 'water'),
('Old', 'Salmos', 146, 1, 'ALABA, oh alma mía, á Jehová.', 'water'),
('Old', 'Salmos', 146, 2, 'Alabaré á Jehová en mi vida: Cantaré salmos á mi Dios mientras viviere.', 'water'),
('Old', 'Salmos', 146, 3, 'No confiéis en los príncipes, Ni en hijo de hombre, porque no hay en él salud.', 'water'),
('Old', 'Salmos', 146, 4, 'Saldrá su espíritu, tornaráse en su tierra: En aquel día perecerán sus pensamientos.', 'water'),
('Old', 'Salmos', 146, 5, 'Bienaventurado aquel en cuya ayuda es el Dios de Jacob, Cuya esperanza es en Jehová su Dios:', 'water'),
('Old', 'Salmos', 146, 6, 'El cual hizo los cielos y la tierra, La mar, y todo lo que en ellos hay; Que guarda verdad para siempre;', 'water'),
('Old', 'Salmos', 146, 7, 'Que hace derecho á los agraviados; Que da pan á los hambrientos: Jehová suelta á los aprisionados;', 'water'),
('Old', 'Salmos', 146, 8, 'Jehová abre los ojos á los ciegos; Jehová levanta á los caídos; Jehová ama á los justos.', 'water'),
('Old', 'Salmos', 146, 9, 'Jehová guarda á los extranjeros; Al huérfano y á la viuda levanta; Y el camino de los impíos trastorna.', 'water'),
('Old', 'Salmos', 146, 10, 'Reinará Jehová para siempre; Tu Dios, oh Sión, por generación y generación. Aleluya.', 'water'),
('Old', 'Salmos', 147, 1, 'ALABAD á JAH, Porque es bueno cantar salmos á nuestro Dios; Porque suave y hermosa es la alabanza.', 'water'),
('Old', 'Salmos', 147, 2, 'Jehová edifica á Jerusalem; A los echados de Israel recogerá.', 'water'),
('Old', 'Salmos', 147, 3, 'El sana á los quebrantados de corazón, Y liga sus heridas.', 'water'),
('Old', 'Salmos', 147, 4, 'El cuenta el número de las estrellas; A todas ellas llama por sus nombres.', 'water'),
('Old', 'Salmos', 147, 5, 'Grande es el Señor nuestro, y de mucha potencia; Y de su entendimiento no hay número.', 'water'),
('Old', 'Salmos', 147, 6, 'Jehová ensalza á los humildes; Humilla los impíos hasta la tierra.', 'water'),
('Old', 'Salmos', 147, 7, 'Cantad á Jehová con alabanza, Cantad con arpa á nuestro Dios.', 'water'),
('Old', 'Salmos', 147, 8, 'El es el que cubre los cielos de nubes, El que prepara la lluvia para la tierra, El que hace á los montes producir hierba.', 'water'),
('Old', 'Salmos', 147, 9, 'El da á la bestia su mantenimiento, Y á los hijos de los cuervos que claman.', 'water'),
('Old', 'Salmos', 147, 10, 'No toma contentamiento en la fortaleza del caballo, Ni se complace en las piernas del hombre.', 'water'),
('Old', 'Salmos', 147, 11, 'Complácese Jehová en los que le temen, Y en los que esperan en su misericordia.', 'water'),
('Old', 'Salmos', 147, 12, 'Alaba á Jehová, Jerusalem; Alaba á tu Dios, Sión.', 'water'),
('Old', 'Salmos', 147, 13, 'Porque fortificó los cerrojos de tus puertas; Bendijo á tus hijos dentro de ti.', 'water'),
('Old', 'Salmos', 147, 14, 'El pone en tu término la paz; Te hará saciar de grosura de trigo.', 'water'),
('Old', 'Salmos', 147, 15, 'El envía su palabra á la tierra; Muy presto corre su palabra.', 'water'),
('Old', 'Salmos', 147, 16, 'El da la nieve como lana, Derrama la escarcha como ceniza.', 'water'),
('Old', 'Salmos', 147, 17, 'El echa su hielo como pedazos: Delante de su frío ¿quién estará?', 'water'),
('Old', 'Salmos', 147, 18, 'Enviará su palabra, y los derretirá: Soplará su viento, y fluirán las aguas.', 'water'),
('Old', 'Salmos', 147, 19, 'El denuncia sus palabras á Jacob, Sus estatutos y sus juicios á Israel.', 'water'),
('Old', 'Salmos', 147, 20, 'No ha hecho esto con toda gente; Y no conocieron sus juicios. Aleluya.', 'water'),
('Old', 'Salmos', 148, 1, 'ALABAD á Jehová desde los cielos: Alabadle en las alturas.', 'water'),
('Old', 'Salmos', 148, 2, 'Alabadle, vosotros todos sus ángeles: Alabadle, vosotros todos sus ejércitos.', 'water'),
('Old', 'Salmos', 148, 3, 'Alabadle, sol y luna: Alabadle, vosotras todas, lucientes estrellas.', 'water'),
('Old', 'Salmos', 148, 4, 'Alabadle, cielos de los cielos, Y las aguas que están sobre los cielos.', 'water'),
('Old', 'Salmos', 148, 5, 'Alaben el nombre de Jehová; Porque él mandó, y fueron criadas.', 'water'),
('Old', 'Salmos', 148, 6, 'Y las hizo ser para siempre por los siglos; Púso les ley que no será quebrantada.', 'water'),
('Old', 'Salmos', 148, 7, 'Alabad á Jehová, de la tierra Los dragones y todos los abismos;', 'water'),
('Old', 'Salmos', 148, 8, 'El fuego y el granizo, la nieve y el vapor, El viento de tempestad que ejecuta su palabra;', 'water'),
('Old', 'Salmos', 148, 9, 'Los montes y todos los collados; El árbol de fruto, y todos los cedros;', 'water'),
('Old', 'Salmos', 148, 10, 'La bestia y todo animal; Reptiles y volátiles;', 'water'),
('Old', 'Salmos', 148, 11, 'Los reyes de la tierra y todos los pueblos; Los príncipes y todos los jueces de la tierra;', 'water'),
('Old', 'Salmos', 148, 12, 'Los mancebos y también las doncellas; Los viejos y los niños,', 'water'),
('Old', 'Salmos', 148, 13, 'Alaben el nombre de Jehová, Porque sólo su nombre es elevado; Su gloria es sobre tierra y cielos.', 'water'),
('Old', 'Salmos', 148, 14, 'El ensalzó el cuerno de su pueblo; Aláben le todos sus santos, los hijos de Israel, El pueblo á él cercano. Aleluya.', 'water'),
('Old', 'Salmos', 149, 1, 'CANTAD á Jehová canción nueva: Su alabanza sea en la congregación de los santos.', 'water'),
('Old', 'Salmos', 149, 2, 'Alégrese Israel en su Hacedor: Los hijos de Sión se gocen en su Rey.', 'water'),
('Old', 'Salmos', 149, 3, 'Alaben su nombre con corro: Con adufe y arpa á él canten.', 'water'),
('Old', 'Salmos', 149, 4, 'Porque Jehová toma contentamiento con su pueblo: Hermoseará á los humildes con salud.', 'water'),
('Old', 'Salmos', 149, 5, 'Gozarse han los píos con gloria: Cantarán sobre sus camas.', 'water'),
('Old', 'Salmos', 149, 6, 'Ensalzamientos de Dios modularán en sus gargantas. Y espadas de dos filos habrá en sus manos;', 'water'),
('Old', 'Salmos', 149, 7, 'Para hacer venganza de las gentes, Y castigo en los pueblos;', 'water'),
('Old', 'Salmos', 149, 8, 'Para aprisionar sus reyes en grillos, Y sus nobles con cadenas de hierro;', 'water'),
('Old', 'Salmos', 149, 9, 'Para ejecutar en ellos el juicio escrito: Gloria será esta para todos sus santos. Aleluya.', 'water'),
('Old', 'Salmos', 150, 1, 'ALABAD á Dios en su santuario: Alabadle en la extensión de su fortaleza.', 'water'),
('Old', 'Salmos', 150, 2, 'Alabadle por sus proezas: Alabadle conforme á la muchedumbre de su grandeza.', 'water'),
('Old', 'Salmos', 150, 3, 'Alabadle á son de bocina: Alabadle con salterio y arpa.', 'water'),
('Old', 'Salmos', 150, 4, 'Alabadle con adufe y flauta: Alabadle con cuerdas y órgano.', 'water'),
('Old', 'Salmos', 150, 5, 'Alabadle con címbalos resonantes: Alabadle con címbalos de júbilo.', 'water'),
('Old', 'Salmos', 150, 6, 'Todo lo que respira alabe á JAH. Aleluya.', 'water'),
('Old', 'Proverbios', 1, 1, 'LOS proverbios de Salomón, hijo de David, rey de Israel:', 'cosmic'),
('Old', 'Proverbios', 1, 2, 'Para entender sabiduría y doctrina; Para conocer las razones prudentes;', 'cosmic'),
('Old', 'Proverbios', 1, 3, 'Para recibir el consejo de prudencia, Justicia, y juicio y equidad;', 'cosmic'),
('Old', 'Proverbios', 1, 4, 'Para dar sagacidad á los simples, Y á los jóvenes inteligencia y cordura.', 'cosmic'),
('Old', 'Proverbios', 1, 5, 'Oirá el sabio, y aumentará el saber; Y el entendido adquirirá consejo;', 'cosmic'),
('Old', 'Proverbios', 1, 6, 'Para entender parábola y declaración; Palabras de sabios, y sus dichos oscuros.', 'cosmic'),
('Old', 'Proverbios', 1, 7, 'El principio de la sabiduría es el temor de Jehová: Los insensatos desprecian la sabiduría y la enseñanza.', 'cosmic'),
('Old', 'Proverbios', 1, 8, 'Oye, hijo mío, la doctrina de tu padre, Y no desprecies la dirección de tu madre:', 'cosmic'),
('Old', 'Proverbios', 1, 9, 'Porque adorno de gracia serán á tu cabeza, Y collares á tu cuello.', 'cosmic'),
('Old', 'Proverbios', 1, 10, 'Hijo mío, si los pecadores te quisieren engañar, No consientas.', 'cosmic'),
('Old', 'Proverbios', 1, 11, 'Si dijeren: Ven con nosotros, Pongamos asechanzas á la sangre, Acechemos sin motivo al inocente;', 'cosmic'),
('Old', 'Proverbios', 1, 12, 'Los tragaremos vivos como el sepulcro, Y enteros, como los que caen en sima;', 'cosmic'),
('Old', 'Proverbios', 1, 13, 'Hallaremos riquezas de todas suertes, Henchiremos nuestras casas de despojos;', 'cosmic'),
('Old', 'Proverbios', 1, 14, 'Echa tu suerte entre nosotros; Tengamos todos una bolsa:', 'cosmic'),
('Old', 'Proverbios', 1, 15, 'Hijo mío, no andes en camino con ellos; Aparta tu pie de sus veredas:', 'cosmic'),
('Old', 'Proverbios', 1, 16, 'Porque sus pies correrán al mal, E irán presurosos á derramar sangre.', 'cosmic'),
('Old', 'Proverbios', 1, 17, 'Porque en vano se tenderá la red Ante los ojos de toda ave;', 'cosmic'),
('Old', 'Proverbios', 1, 18, 'Mas ellos á su propia sangre ponen asechanzas, Y á sus almas tienden lazo.', 'cosmic'),
('Old', 'Proverbios', 1, 19, 'Tales son las sendas de todo el que es dado á la codicia, La cual prenderá el alma de sus poseedores.', 'cosmic'),
('Old', 'Proverbios', 1, 20, 'La sabiduría clama de fuera, Da su voz en las plazas:', 'cosmic'),
('Old', 'Proverbios', 1, 21, 'Clama en los principales lugares de concurso; En las entradas de las puertas de la ciudad dice sus razones:', 'cosmic'),
('Old', 'Proverbios', 1, 22, '¿Hasta cuándo, oh simples, amaréis la simpleza, Y los burladores desearán el burlar, Y los insensatos aborrecerán la ciencia?', 'cosmic'),
('Old', 'Proverbios', 1, 23, 'Volveos á mi reprensión: He aquí yo os derramaré mi espíritu, Y os haré saber mis palabras.', 'cosmic'),
('Old', 'Proverbios', 1, 24, 'Por cuanto llamé, y no quisisteis: Extendí mi mano, y no hubo quien escuchase;', 'cosmic'),
('Old', 'Proverbios', 1, 25, 'Antes desechasteis todo consejo mío, Y mi reprensión no quisisteis:', 'cosmic'),
('Old', 'Proverbios', 1, 26, 'También yo me reiré en vuestra calamidad, Y me burlaré cuando os viniere lo que teméis;', 'cosmic'),
('Old', 'Proverbios', 1, 27, 'Cuando viniere como una destrucción lo que teméis, Y vuestra calamidad llegare como un torbellino; Cuando sobre vosotros viniere tribulación y angustia.', 'cosmic'),
('Old', 'Proverbios', 1, 28, 'Entonces me llamarán, y no responderé; Buscarme han de mañana, y no me hallarán:', 'cosmic'),
('Old', 'Proverbios', 1, 29, 'Por cuanto aborrecieron la sabiduría, Y no escogieron el temor de Jehová,', 'cosmic'),
('Old', 'Proverbios', 1, 30, 'Ni quisieron mi consejo, Y menospreciaron toda reprensión mía:', 'cosmic'),
('Old', 'Proverbios', 1, 31, 'Comerán pues del fruto de su camino, Y se hartarán de sus consejos.', 'cosmic'),
('Old', 'Proverbios', 1, 32, 'Porque el reposo de los ignorantes los matará, Y la prosperidad de los necios los echará á perder.', 'cosmic'),
('Old', 'Proverbios', 1, 33, 'Mas el que me oyere, habitará confiadamente, Y vivirá reposado, sin temor de mal.', 'cosmic'),
('Old', 'Proverbios', 2, 1, 'HIJO mío, si tomares mis palabras, Y mis mandamientos guardares dentro de ti,', 'cosmic'),
('Old', 'Proverbios', 2, 2, 'Haciendo estar atento tu oído á la sabiduría; Si inclinares tu corazón á la prudencia;', 'cosmic'),
('Old', 'Proverbios', 2, 3, 'Si clamares á la inteligencia, Y á la prudencia dieres tu voz;', 'cosmic'),
('Old', 'Proverbios', 2, 4, 'Si como á la plata la buscares, Y la escudriñares como á tesoros;', 'cosmic'),
('Old', 'Proverbios', 2, 5, 'Entonces entenderás el temor de Jehová, Y hallarás el conocimiento de Dios.', 'cosmic'),
('Old', 'Proverbios', 2, 6, 'Porque Jehová da la sabiduría, Y de su boca viene el conocimiento y la inteligencia.', 'cosmic'),
('Old', 'Proverbios', 2, 7, 'El provee de sólida sabiduría á los rectos: Es escudo á los que caminan rectamente.', 'cosmic'),
('Old', 'Proverbios', 2, 8, 'Es el que guarda las veredas del juicio, Y preserva el camino de sus santos.', 'cosmic'),
('Old', 'Proverbios', 2, 9, 'Entonces entenderás justicia, juicio, Y equidad, y todo buen camino.', 'cosmic'),
('Old', 'Proverbios', 2, 10, 'Cuando la sabiduría entrare en tu corazón, Y la ciencia fuere dulce á tu alma,', 'cosmic'),
('Old', 'Proverbios', 2, 11, 'El consejo te guardará, Te preservará la inteligencia:', 'cosmic'),
('Old', 'Proverbios', 2, 12, 'Para librarte del mal camino, De los hombres que hablan perversidades;', 'cosmic'),
('Old', 'Proverbios', 2, 13, 'Que dejan las veredas derechas, Por andar en caminos tenebrosos;', 'cosmic'),
('Old', 'Proverbios', 2, 14, 'Que se alegran haciendo mal, Que se huelgan en las perversidades del vicio;', 'cosmic'),
('Old', 'Proverbios', 2, 15, 'Cuyas veredas son torcidas, Y torcidos sus caminos.', 'cosmic'),
('Old', 'Proverbios', 2, 16, 'Para librarte de la mujer extraña, De la ajena que halaga con sus palabras;', 'cosmic'),
('Old', 'Proverbios', 2, 17, 'Que desampara el príncipe de su mocedad, Y se olvida del pacto de su Dios.', 'cosmic'),
('Old', 'Proverbios', 2, 18, 'Por lo cual su casa está inclinada á la muerte, Y sus veredas hacia los muertos:', 'cosmic'),
('Old', 'Proverbios', 2, 19, 'Todos los que á ella entraren, no volverán, Ni tomarán las veredas de la vida.', 'cosmic'),
('Old', 'Proverbios', 2, 20, 'Para que andes por el camino de los buenos, Y guardes las veredas de los justos.', 'cosmic'),
('Old', 'Proverbios', 2, 21, 'Porque los rectos habitarán la tierra, Y los perfectos permanecerán en ella;', 'cosmic'),
('Old', 'Proverbios', 2, 22, 'Mas los impíos serán cortados de la tierra, Y los prevaricadores serán de ella desarraigados.', 'cosmic'),
('Old', 'Proverbios', 3, 1, 'HIJO mío, no te olvides de mi ley; Y tu corazón guarde mis mandamientos:', 'cosmic'),
('Old', 'Proverbios', 3, 2, 'Porque largura de días, y años de vida Y paz te aumentarán.', 'cosmic'),
('Old', 'Proverbios', 3, 3, 'Misericordia y verdad no te desamparen; Atalas á tu cuello, Escríbelas en la tabla de tu corazón:', 'cosmic'),
('Old', 'Proverbios', 3, 4, 'Y hallarás gracia y buena opinión En los ojos de Dios y de los hombres.', 'cosmic'),
('Old', 'Proverbios', 3, 5, 'Fíate de Jehová de todo tu corazón, Y no estribes en tu prudencia.', 'cosmic'),
('Old', 'Proverbios', 3, 6, 'Reconócelo en todos tus caminos, Y él enderezará tus veredas.', 'cosmic'),
('Old', 'Proverbios', 3, 7, 'No seas sabio en tu opinión: Teme á Jehová, y apártate del mal;', 'cosmic'),
('Old', 'Proverbios', 3, 8, 'Porque será medicina á tu ombligo, Y tuétano á tus huesos.', 'cosmic'),
('Old', 'Proverbios', 3, 9, 'Honra á Jehová de tu sustancia, Y de las primicias de todos tus frutos;', 'cosmic'),
('Old', 'Proverbios', 3, 10, 'Y serán llenas tus trojes con abundancia, Y tus lagares rebosarán de mosto.', 'cosmic'),
('Old', 'Proverbios', 3, 11, 'No deseches, hijo mío, el castigo de Jehová; Ni te fatigues de su corrección:', 'cosmic'),
('Old', 'Proverbios', 3, 12, 'Porque al que ama castiga, Como el padre al hijo á quien quiere.', 'cosmic'),
('Old', 'Proverbios', 3, 13, 'Bienaventurado el hombre que halla la sabiduría, Y que obtiene la inteligencia:', 'cosmic'),
('Old', 'Proverbios', 3, 14, 'Porque su mercadería es mejor que la mercadería de la plata, Y sus frutos más que el oro fino.', 'cosmic'),
('Old', 'Proverbios', 3, 15, 'Más preciosa es que las piedras preciosas; Y todo lo que puedes desear, no se puede comparar á ella.', 'cosmic'),
('Old', 'Proverbios', 3, 16, 'Largura de días está en su mano derecha; En su izquierda riquezas y honra.', 'cosmic'),
('Old', 'Proverbios', 3, 17, 'Sus caminos son caminos deleitosos, Y todas sus veredas paz.', 'cosmic'),
('Old', 'Proverbios', 3, 18, 'Ella es árbol de vida á los que de ella asen: Y bienaventurados son los que la mantienen.', 'cosmic'),
('Old', 'Proverbios', 3, 19, 'Jehová con sabiduría fundó la tierra; Afirmó los cielos con inteligencia.', 'cosmic'),
('Old', 'Proverbios', 3, 20, 'Con su ciencia se partieron los abismos, Y destilan el rocío los cielos.', 'cosmic'),
('Old', 'Proverbios', 3, 21, 'Hijo mío, no se aparten estas cosas de tus ojos; Guarda la ley y el consejo;', 'cosmic'),
('Old', 'Proverbios', 3, 22, 'Y serán vida á tu alma, Y gracia á tu cuello.', 'cosmic'),
('Old', 'Proverbios', 3, 23, 'Entonces andarás por tu camino confiadamente, Y tu pie no tropezará.', 'cosmic'),
('Old', 'Proverbios', 3, 24, 'Cuando te acostares, no tendrás temor; Antes te acostarás, y tu sueño será suave.', 'cosmic'),
('Old', 'Proverbios', 3, 25, 'No tendrás temor de pavor repentino, Ni de la ruina de los impíos cuando viniere:', 'cosmic'),
('Old', 'Proverbios', 3, 26, 'Porque Jehová será tu confianza, Y él preservará tu pie de ser preso.', 'cosmic'),
('Old', 'Proverbios', 3, 27, 'No detengas el bien de sus dueños, Cuando tuvieres poder para hacerlo.', 'cosmic'),
('Old', 'Proverbios', 3, 28, 'No digas á tu prójimo: Ve, y vuelve, Y mañana te daré; Cuando tienes contigo qué darle.', 'cosmic'),
('Old', 'Proverbios', 3, 29, 'No intentes mal contra tu prójimo, Estando él confiado de ti.', 'cosmic'),
('Old', 'Proverbios', 3, 30, 'No pleitees con alguno sin razón, Si él no te ha hecho agravio.', 'cosmic'),
('Old', 'Proverbios', 3, 31, 'No envidies al hombre injusto, Ni escojas alguno de sus caminos.', 'cosmic'),
('Old', 'Proverbios', 3, 32, 'Porque el perverso es abominado de Jehová: Mas su secreto es con los rectos.', 'cosmic'),
('Old', 'Proverbios', 3, 33, 'La maldición de Jehová está en la casa del impío; Mas él bendecirá la morada de los justos.', 'cosmic'),
('Old', 'Proverbios', 3, 34, 'Ciertamente él escarnecerá á los escarnecedores, Y á los humildes dará gracia.', 'cosmic'),
('Old', 'Proverbios', 3, 35, 'Los sabios heredarán honra: Mas los necios sostendrán ignominia.', 'cosmic'),
('Old', 'Proverbios', 4, 1, 'OID, hijos, la doctrina de un padre, Y estad atentos para que conozcáis cordura.', 'cosmic'),
('Old', 'Proverbios', 4, 2, 'Porque os doy buena enseñanza; No desamparéis mi ley.', 'cosmic'),
('Old', 'Proverbios', 4, 3, 'Porque yo fuí hijo de mi padre, Delicado y único delante de mi madre.', 'cosmic'),
('Old', 'Proverbios', 4, 4, 'Y él me enseñaba, y me decía: Mantenga tu corazón mis razones, Guarda mis mandamientos, y vivirás:', 'cosmic'),
('Old', 'Proverbios', 4, 5, 'Adquiere sabiduría, adquiere inteligencia; No te olvides ni te apartes de las razones de mi boca;', 'cosmic'),
('Old', 'Proverbios', 4, 6, 'No la dejes, y ella te guardará; Amala, y te conservará.', 'cosmic'),
('Old', 'Proverbios', 4, 7, 'Sabiduría ante todo: adquiere sabiduría: Y ante toda tu posesión adquiere inteligencia.', 'cosmic'),
('Old', 'Proverbios', 4, 8, 'Engrandécela, y ella te engrandecerá: Ella te honrará, cuando tú la hubieres abrazado.', 'cosmic'),
('Old', 'Proverbios', 4, 9, 'Adorno de gracia dará á tu cabeza: Corona de hermosura te entregará.', 'cosmic'),
('Old', 'Proverbios', 4, 10, 'Oye, hijo mío, y recibe mis razones; Y se te multiplicarán años de vida.', 'cosmic'),
('Old', 'Proverbios', 4, 11, 'Por el camino de la sabiduría te he encaminado, Y por veredas derechas te he hecho andar.', 'cosmic'),
('Old', 'Proverbios', 4, 12, 'Cuando anduvieres no se estrecharán tus pasos; Y si corrieres, no tropezarás.', 'cosmic'),
('Old', 'Proverbios', 4, 13, 'Ten el consejo, no lo dejes; Guárdalo, porque eso es tu vida.', 'cosmic'),
('Old', 'Proverbios', 4, 14, 'No entres por la vereda de los impíos, Ni vayas por el camino de los malos.', 'cosmic'),
('Old', 'Proverbios', 4, 15, 'Desampárala, no pases por ella; Apártate de ella, pasa.', 'cosmic'),
('Old', 'Proverbios', 4, 16, 'Porque no duermen ellos, si no hicieren mal; Y pierden su sueño, si no han hecho caer.', 'cosmic'),
('Old', 'Proverbios', 4, 17, 'Porque comen pan de maldad, y beben vino de robos.', 'cosmic'),
('Old', 'Proverbios', 4, 18, 'Mas la senda de los justos es como la luz de la aurora, Que va en aumento hasta que el día es perfecto.', 'cosmic'),
('Old', 'Proverbios', 4, 19, 'El camino de los impíos es como la oscuridad: No saben en qué tropiezan.', 'cosmic'),
('Old', 'Proverbios', 4, 20, 'Hijo mío, está atento á mis palabras; Inclina tu oído á mis razones.', 'cosmic'),
('Old', 'Proverbios', 4, 21, 'No se aparten de tus ojos; Guárdalas en medio de tu corazón.', 'cosmic'),
('Old', 'Proverbios', 4, 22, 'Porque son vida á los que las hallan, Y medicina á toda su carne.', 'cosmic'),
('Old', 'Proverbios', 4, 23, 'Sobre toda cosa guardada guarda tu corazón; Porque de él mana la vida.', 'cosmic'),
('Old', 'Proverbios', 4, 24, 'Aparta de ti la perversidad de la boca, Y aleja de ti la iniquidad de labios.', 'cosmic'),
('Old', 'Proverbios', 4, 25, 'Tus ojos miren lo recto, Y tus párpados en derechura delante de ti.', 'cosmic'),
('Old', 'Proverbios', 4, 26, 'Examina la senda de tus pies, Y todos tus caminos sean ordenados.', 'cosmic'),
('Old', 'Proverbios', 4, 27, 'No te apartes á diestra, ni á siniestra: Aparta tu pie del mal.', 'cosmic'),
('Old', 'Proverbios', 5, 1, 'HIJO mío, está atento á mi sabiduría, Y á mi inteligencia inclina tu oído;', 'cosmic'),
('Old', 'Proverbios', 5, 2, 'Para que guardes consejo, Y tus labios conserven la ciencia.', 'cosmic'),
('Old', 'Proverbios', 5, 3, 'Porque los labios de la extraña destilan miel, Y su paladar es más blando que el aceite;', 'cosmic'),
('Old', 'Proverbios', 5, 4, 'Mas su fin es amargo como el ajenjo, Agudo como cuchillo de dos filos.', 'cosmic'),
('Old', 'Proverbios', 5, 5, 'Sus pies descienden á la muerte; Sus pasos sustentan el sepulcro:', 'cosmic'),
('Old', 'Proverbios', 5, 6, 'Sus caminos son instables; no los conocerás, Si no considerares el camino de vida.', 'cosmic'),
('Old', 'Proverbios', 5, 7, 'Ahora pues, hijos, oidme, Y no os apartéis de las razones de mi boca.', 'cosmic'),
('Old', 'Proverbios', 5, 8, 'Aleja de ella tu camino, Y no te acerques á la puerta de su casa;', 'cosmic'),
('Old', 'Proverbios', 5, 9, 'Porque no des á los extraños tu honor, Y tus años á cruel;', 'cosmic'),
('Old', 'Proverbios', 5, 10, 'Porque no se harten los extraños de tu fuerza, Y tus trabajos estén en casa del extraño;', 'cosmic'),
('Old', 'Proverbios', 5, 11, 'Y gimas en tus postrimerías, Cuando se consumiere tu carne y tu cuerpo,', 'cosmic'),
('Old', 'Proverbios', 5, 12, 'Y digas: ¡Cómo aborrecí el consejo, Y mi corazón menospreció la reprensión;', 'cosmic'),
('Old', 'Proverbios', 5, 13, 'Y no oí la voz de los que me adoctrinaban, Y á los que me enseñaban no incliné mi oído!', 'cosmic'),
('Old', 'Proverbios', 5, 14, 'Casi en todo mal he estado, En medio de la sociedad y de la congregación.', 'cosmic'),
('Old', 'Proverbios', 5, 15, 'Bebe el agua de tu cisterna, Y los raudales de tu pozo.', 'cosmic'),
('Old', 'Proverbios', 5, 16, 'Derrámense por de fuera tus fuentes, En las plazas los ríos de aguas.', 'cosmic'),
('Old', 'Proverbios', 5, 17, 'Sean para ti solo, Y no para los extraños contigo.', 'cosmic'),
('Old', 'Proverbios', 5, 18, 'Sea bendito tu manantial; Y alégrate con la mujer de tu mocedad.', 'cosmic'),
('Old', 'Proverbios', 5, 19, 'Como cierva amada y graciosa corza, Sus pechos te satisfagan en todo tiempo; Y en su amor recréate siempre.', 'cosmic'),
('Old', 'Proverbios', 5, 20, '¿Y por qué, hijo mío, andarás ciego con la ajena, Y abrazarás el seno de la extraña?', 'cosmic'),
('Old', 'Proverbios', 5, 21, 'Pues que los caminos del hombre están ante los ojos de Jehová, Y él considera todas sus veredas.', 'cosmic'),
('Old', 'Proverbios', 5, 22, 'Prenderán al impío sus propias iniquidades, Y detenido será con las cuerdas de su pecado.', 'cosmic'),
('Old', 'Proverbios', 5, 23, 'El morirá por falta de corrección; Y errará por la grandeza de su locura.', 'cosmic'),
('Old', 'Proverbios', 6, 1, 'HIJO mío, si salieres fiador por tu amigo, Si tocaste tu mano por el extraño,', 'cosmic'),
('Old', 'Proverbios', 6, 2, 'Enlazado eres con las palabras de tu boca, Y preso con las razones de tu boca.', 'cosmic'),
('Old', 'Proverbios', 6, 3, 'Haz esto ahora, hijo mío, y líbrate, Ya que has caído en la mano de tu prójimo: Ve, humíllate, y asegúrate de tu amigo.', 'cosmic'),
('Old', 'Proverbios', 6, 4, 'No des sueño á tus ojos, Ni á tus párpados adormecimiento.', 'cosmic'),
('Old', 'Proverbios', 6, 5, 'Escápate como el corzo de la mano del cazador, Y como el ave de la mano del parancero.', 'cosmic'),
('Old', 'Proverbios', 6, 6, 'Ve á la hormiga, oh perezoso Mira sus caminos, y sé sabio;', 'cosmic'),
('Old', 'Proverbios', 6, 7, 'La cual no teniendo capitán, Ni gobernador, ni señor,', 'cosmic'),
('Old', 'Proverbios', 6, 8, 'Prepara en el verano su comida Y allega en el tiempo de la siega su mantenimiento.', 'cosmic'),
('Old', 'Proverbios', 6, 9, 'Perezoso, ¿hasta cuándo has de dormir? ¿Cuándo te levantarás de tu sueño?', 'cosmic'),
('Old', 'Proverbios', 6, 10, 'Un poco de sueño, un poco de dormitar, Y cruzar por un poco las manos para reposo:', 'cosmic'),
('Old', 'Proverbios', 6, 11, 'Así vendrá tu necesidad como caminante, Y tu pobreza como hombre de escudo.', 'cosmic'),
('Old', 'Proverbios', 6, 12, 'El hombre malo, el hombre depravado, Anda en perversidad de boca;', 'cosmic'),
('Old', 'Proverbios', 6, 13, 'Guiña de sus ojos, habla con sus pies, Indica con sus dedos;', 'cosmic'),
('Old', 'Proverbios', 6, 14, 'Perversidades hay en su corazón, anda pensando mal en todo tiempo; Enciende rencillas.', 'cosmic'),
('Old', 'Proverbios', 6, 15, 'Por tanto su calamidad vendrá de repente; Súbitamente será quebrantado, y no habrá remedio.', 'cosmic'),
('Old', 'Proverbios', 6, 16, 'Seis cosas aborrece Jehová, Y aun siete abomina su alma:', 'cosmic'),
('Old', 'Proverbios', 6, 17, 'Los ojos altivos, la lengua mentirosa, Las manos derramadoras de sangre inocente,', 'cosmic'),
('Old', 'Proverbios', 6, 18, 'El corazón que maquina pensamientos inicuos, Los pies presurosos para correr al mal,', 'cosmic'),
('Old', 'Proverbios', 6, 19, 'El testigo falso que habla mentiras, Y el que enciende rencillas entre los hermanos.', 'cosmic'),
('Old', 'Proverbios', 6, 20, 'Guarda, hijo mío, el mandamiento de tu padre, Y no dejes la enseñanza de tu madre:', 'cosmic'),
('Old', 'Proverbios', 6, 21, 'Atalos siempre en tu corazón, Enlázalos á tu cuello.', 'cosmic'),
('Old', 'Proverbios', 6, 22, 'Te guiarán cuando anduvieres; cuando durmieres te guardarán; Hablarán contigo cuando despertares.', 'cosmic'),
('Old', 'Proverbios', 6, 23, 'Porque el mandamiento es antorcha, y la enseñanza luz; Y camino de vida las reprensiones de la enseñanza:', 'cosmic'),
('Old', 'Proverbios', 6, 24, 'Para que te guarden de la mala mujer, De la blandura de la lengua de la extraña.', 'cosmic'),
('Old', 'Proverbios', 6, 25, 'No codicies su hermosura en tu corazón, Ni ella te prenda con sus ojos:', 'cosmic'),
('Old', 'Proverbios', 6, 26, 'Porque á causa de la mujer ramera es reducido el hombre á un bocado de pan; Y la mujer caza la preciosa alma del varón.', 'cosmic'),
('Old', 'Proverbios', 6, 27, '¿Tomará el hombre fuego en su seno, Sin que sus vestidos se quemen?', 'cosmic'),
('Old', 'Proverbios', 6, 28, '¿Andará el hombre sobre las brasas, Sin que sus pies se abrasen?', 'cosmic'),
('Old', 'Proverbios', 6, 29, 'Así el que entrare á la mujer de su prójimo; No será sin culpa cualquiera que la tocare.', 'cosmic'),
('Old', 'Proverbios', 6, 30, 'No tienen en poco al ladrón, cuando hurtare Para saciar su alma teniendo hambre:', 'cosmic'),
('Old', 'Proverbios', 6, 31, 'Empero tomado, paga las setenas, Da toda la sustancia de su casa.', 'cosmic'),
('Old', 'Proverbios', 6, 32, 'Mas el que comete adulterio con la mujer, es falto de entendimiento: Corrompe su alma el que tal hace.', 'cosmic'),
('Old', 'Proverbios', 6, 33, 'Plaga y vergüenza hallará; Y su afrenta nunca será raída.', 'cosmic'),
('Old', 'Proverbios', 6, 34, 'Porque los celos son el furor del hombre, Y no perdonará en el día de la venganza.', 'cosmic'),
('Old', 'Proverbios', 6, 35, 'No tendrá respeto á ninguna redención; Ni querrá perdonar, aunque multipliques los dones.', 'cosmic'),
('Old', 'Proverbios', 7, 1, 'HIJO mío, guarda mis razones, Y encierra contigo mis mandamientos.', 'cosmic'),
('Old', 'Proverbios', 7, 2, 'Guarda mis mandamientos, y vivirás; Y mi ley como las niñas de tus ojos.', 'cosmic'),
('Old', 'Proverbios', 7, 3, 'Lígalos á tus dedos; Escríbelos en la tabla de tu corazón.', 'cosmic'),
('Old', 'Proverbios', 7, 4, 'Di á la sabiduría: Tú eres mi hermana; Y á la inteligencia llama parienta:', 'cosmic'),
('Old', 'Proverbios', 7, 5, 'Para que te guarden de la mujer ajena, Y de la extraña que ablanda sus palabras.', 'cosmic'),
('Old', 'Proverbios', 7, 6, 'Porque mirando yo por la ventana de mi casa, Por mi celosía,', 'cosmic'),
('Old', 'Proverbios', 7, 7, 'Vi entre los simples, Consideré entre los jóvenes, Un mancebo falto de entendimiento,', 'cosmic'),
('Old', 'Proverbios', 7, 8, 'El cual pasaba por la calle, junto á la esquina de aquella, E iba camino de su casa,', 'cosmic'),
('Old', 'Proverbios', 7, 9, 'A la tarde del día, ya que oscurecía, En la oscuridad y tiniebla de la noche.', 'cosmic'),
('Old', 'Proverbios', 7, 10, 'Y he aquí, una mujer que le sale al encuentro Con atavío de ramera, astuta de corazón,', 'cosmic'),
('Old', 'Proverbios', 7, 11, 'Alborotadora y rencillosa, Sus pies no pueden estar en casa;', 'cosmic'),
('Old', 'Proverbios', 7, 12, 'Unas veces de fuera, ó bien por las plazas, Acechando por todas las esquinas.', 'cosmic'),
('Old', 'Proverbios', 7, 13, 'Y traba de él, y bésalo; Desvergonzó su rostro, y díjole:', 'cosmic'),
('Old', 'Proverbios', 7, 14, 'Sacrificios de paz había prometido, Hoy he pagado mis votos;', 'cosmic'),
('Old', 'Proverbios', 7, 15, 'Por tanto he salido á encontrarte, Buscando diligentemente tu rostro, y te he hallado.', 'cosmic'),
('Old', 'Proverbios', 7, 16, 'Con paramentos he ataviado mi cama, Recamados con cordoncillo de Egipto.', 'cosmic'),
('Old', 'Proverbios', 7, 17, 'He sahumado mi cámara Con mirra, áloes, y cinamomo.', 'cosmic'),
('Old', 'Proverbios', 7, 18, 'Ven, embriaguémonos de amores hasta la mañana; Alegrémonos en amores.', 'cosmic'),
('Old', 'Proverbios', 7, 19, 'Porque el marido no está en casa, Hase ido á un largo viaje:', 'cosmic'),
('Old', 'Proverbios', 7, 20, 'El saco de dinero llevó en su mano; El día señalado volverá á su casa.', 'cosmic'),
('Old', 'Proverbios', 7, 21, 'Rindiólo con la mucha suavidad de sus palabras, Obligóle con la blandura de sus labios.', 'cosmic'),
('Old', 'Proverbios', 7, 22, 'Vase en pos de ella luego, Como va el buey al degolladero, Y como el loco á las prisiones para ser castigado;', 'cosmic'),
('Old', 'Proverbios', 7, 23, 'Como el ave que se apresura al lazo, Y no sabe que es contra su vida, Hasta que la saeta traspasó su hígado.', 'cosmic'),
('Old', 'Proverbios', 7, 24, 'Ahora pues, hijos, oidme, Y estad atentos á las razones de mi boca.', 'cosmic'),
('Old', 'Proverbios', 7, 25, 'No se aparte á sus caminos tu corazón; No yerres en sus veredas.', 'cosmic'),
('Old', 'Proverbios', 7, 26, 'Porque á muchos ha hecho caer heridos; Y aun los más fuertes han sido muertos por ella.', 'cosmic'),
('Old', 'Proverbios', 7, 27, 'Caminos del sepulcro son su casa, Que descienden á las cámaras de la muerte.', 'cosmic'),
('Old', 'Proverbios', 8, 1, '¿NO clama la sabiduría, Y da su voz la inteligencia?', 'cosmic'),
('Old', 'Proverbios', 8, 2, 'En los altos cabezos, junto al camino, A las encrucijadas de las veredas se para;', 'cosmic'),
('Old', 'Proverbios', 8, 3, 'En el lugar de las puertas, á la entrada de la ciudad, A la entrada de las puertas da voces:', 'cosmic'),
('Old', 'Proverbios', 8, 4, 'Oh hombres, á vosotros clamo; Y mi voz es á los hijos de los hombres.', 'cosmic'),
('Old', 'Proverbios', 8, 5, 'Entended, simples, discreción; Y vosotros, locos, entrad en cordura.', 'cosmic'),
('Old', 'Proverbios', 8, 6, 'Oid, porque hablaré cosas excelentes; Y abriré mis labios para cosas rectas.', 'cosmic'),
('Old', 'Proverbios', 8, 7, 'Porque mi boca hablará verdad, Y la impiedad abominan mis labios.', 'cosmic'),
('Old', 'Proverbios', 8, 8, 'En justicia son todas las razones de mi boca; No hay en ellas cosa perversa ni torcida.', 'cosmic'),
('Old', 'Proverbios', 8, 9, 'Todas ellas son rectas al que entiende, Y razonables á los que han hallado sabiduría.', 'cosmic'),
('Old', 'Proverbios', 8, 10, 'Recibid mi enseñanza, y no plata; Y ciencia antes que el oro escogido.', 'cosmic'),
('Old', 'Proverbios', 8, 11, 'Porque mejor es la sabiduría que las piedras preciosas; Y todas las cosas que se pueden desear, no son de comparar con ella.', 'cosmic'),
('Old', 'Proverbios', 8, 12, 'Yo, la sabiduría, habito con la discreción, Y hallo la ciencia de los consejos.', 'cosmic'),
('Old', 'Proverbios', 8, 13, 'El temor de Jehová es aborrecer el mal; La soberbia y la arrogancia, y el mal camino Y la boca perversa, aborrezco.', 'cosmic'),
('Old', 'Proverbios', 8, 14, 'Conmigo está el consejo y el ser; Yo soy la inteligencia; mía es la fortaleza.', 'cosmic'),
('Old', 'Proverbios', 8, 15, 'Por mí reinan los reyes, Y los príncipes determinan justicia.', 'cosmic'),
('Old', 'Proverbios', 8, 16, 'Por mí dominan los príncipes, Y todos los gobernadores juzgan la tierra.', 'cosmic'),
('Old', 'Proverbios', 8, 17, 'Yo amo á los que me aman; Y me hallan los que madrugando me buscan.', 'cosmic'),
('Old', 'Proverbios', 8, 18, 'Las riquezas y la honra están conmigo; Sólidas riquezas, y justicia.', 'cosmic'),
('Old', 'Proverbios', 8, 19, 'Mejor es mi fruto que el oro, y que el oro refinado; Y mi rédito mejor que la plata escogida.', 'cosmic'),
('Old', 'Proverbios', 8, 20, 'Por vereda de justicia guiaré, Por en medio de sendas de juicio;', 'cosmic'),
('Old', 'Proverbios', 8, 21, 'Para hacer heredar á mis amigos el ser, Y que yo hincha sus tesoros.', 'cosmic'),
('Old', 'Proverbios', 8, 22, 'Jehová me poseía en el principio de su camino, Ya de antiguo, antes de sus obras.', 'cosmic'),
('Old', 'Proverbios', 8, 23, 'Eternalmente tuve el principado, desde el principio, Antes de la tierra.', 'cosmic'),
('Old', 'Proverbios', 8, 24, 'Antes de los abismos fuí engendrada; Antes que fuesen las fuentes de las muchas aguas.', 'cosmic'),
('Old', 'Proverbios', 8, 25, 'Antes que los montes fuesen fundados, Antes de los collados, era yo engendrada:', 'cosmic'),
('Old', 'Proverbios', 8, 26, 'No había aún hecho la tierra, ni las campiñas, Ni el principio del polvo del mundo.', 'cosmic'),
('Old', 'Proverbios', 8, 27, 'Cuando formaba los cielos, allí estaba yo; Cuando señalaba por compás la sobrefaz del abismo;', 'cosmic'),
('Old', 'Proverbios', 8, 28, 'Cuando afirmaba los cielos arriba, Cuando afirmaba las fuentes del abismo;', 'cosmic'),
('Old', 'Proverbios', 8, 29, 'Cuando ponía á la mar su estatuto, Y á las aguas, que no pasasen su mandamiento; Cuando establecía los fundamentos de la tierra;', 'cosmic'),
('Old', 'Proverbios', 8, 30, 'Con él estaba yo ordenándolo todo; Y fuí su delicia todos los días, Teniendo solaz delante de él en todo tiempo.', 'cosmic'),
('Old', 'Proverbios', 8, 31, 'Huélgome en la parte habitable de su tierra; Y mis delicias son con los hijos de los hombres.', 'cosmic'),
('Old', 'Proverbios', 8, 32, 'Ahora pues, hijos, oidme: Y bienaventurados los que guardaren mis caminos.', 'cosmic'),
('Old', 'Proverbios', 8, 33, 'Atended el consejo, y sed sabios, Y no lo menospreciéis.', 'cosmic'),
('Old', 'Proverbios', 8, 34, 'Bienaventurado el hombre que me oye, Velando á mis puertas cada día, Guardando los umbrales de mis entradas.', 'cosmic'),
('Old', 'Proverbios', 8, 35, 'Porque el que me hallare, hallará la vida, Y alcanzará el favor de Jehová.', 'cosmic'),
('Old', 'Proverbios', 8, 36, 'Mas el que peca contra mí, defrauda su alma: Todos los que me aborrecen, aman la muerte.', 'cosmic'),
('Old', 'Proverbios', 9, 1, 'LA sabiduría edificó su casa, Labró sus siete columnas;', 'cosmic'),
('Old', 'Proverbios', 9, 2, 'Mató sus víctimas, templó su vino, Y puso su mesa.', 'cosmic'),
('Old', 'Proverbios', 9, 3, 'Envió sus criadas; Sobre lo más alto de la ciudad clamó:', 'cosmic'),
('Old', 'Proverbios', 9, 4, 'Cualquiera simple, venga acá. A los faltos de cordura dijo:', 'cosmic'),
('Old', 'Proverbios', 9, 5, 'Venid, comed mi pan, Y bebed del vino que yo he templado.', 'cosmic'),
('Old', 'Proverbios', 9, 6, 'Dejad las simplezas, y vivid; Y andad por el camino de la inteligencia.', 'cosmic'),
('Old', 'Proverbios', 9, 7, 'El que corrige al escarnecedor, afrenta se acarrea: El que reprende al impío, se atrae mancha.', 'cosmic'),
('Old', 'Proverbios', 9, 8, 'No reprendas al escarnecedor, porque no te aborrezca: Corrige al sabio, y te amará.', 'cosmic'),
('Old', 'Proverbios', 9, 9, 'Da al sabio, y será más sabio: Enseña al justo, y acrecerá su saber.', 'cosmic'),
('Old', 'Proverbios', 9, 10, 'El temor de Jehová es el principio de la sabiduría; Y la ciencia de los santos es inteligencia.', 'cosmic'),
('Old', 'Proverbios', 9, 11, 'Porque por mí se aumentarán tus días, Y años de vida se te añadirán.', 'cosmic'),
('Old', 'Proverbios', 9, 12, 'Si fueres sabio, para ti lo serás: Mas si fueres escarnecedor, pagarás tú solo.', 'cosmic'),
('Old', 'Proverbios', 9, 13, 'La mujer loca es alborotadora; Es simple é ignorante.', 'cosmic'),
('Old', 'Proverbios', 9, 14, 'Siéntase en una silla á la puerta de su casa, En lo alto de la ciudad,', 'cosmic'),
('Old', 'Proverbios', 9, 15, 'Para llamar á los que pasan por el camino, Que van por sus caminos derechos.', 'cosmic'),
('Old', 'Proverbios', 9, 16, 'Cualquiera simple, dice, venga acá. A los faltos de cordura dijo:', 'cosmic'),
('Old', 'Proverbios', 9, 17, 'Las aguas hurtadas son dulces, Y el pan comido en oculto es suave.', 'cosmic'),
('Old', 'Proverbios', 9, 18, 'Y no saben que allí están los muertos; Que sus convidados están en los profundos de la sepultura.', 'cosmic'),
('Old', 'Proverbios', 10, 1, '- Las sentencias de Salomón. - EL hijo sabio alegra al padre; Y el hijo necio es tristeza de su madre.', 'cosmic'),
('Old', 'Proverbios', 10, 2, 'Los tesoros de maldad no serán de provecho: Mas la justicia libra de muerte.', 'cosmic'),
('Old', 'Proverbios', 10, 3, 'Jehová no dejará hambrear el alma del justo: Mas la iniquidad lanzará á los impíos.', 'cosmic'),
('Old', 'Proverbios', 10, 4, 'La mano negligente hace pobre: Mas la mano de los diligentes enriquece.', 'cosmic'),
('Old', 'Proverbios', 10, 5, 'El que recoge en el estío es hombre entendido: El que duerme en el tiempo de la siega es hombre afrentoso.', 'cosmic'),
('Old', 'Proverbios', 10, 6, 'Bendiciones sobre la cabeza del justo: Mas violencia cubrirá la boca de los impíos.', 'cosmic'),
('Old', 'Proverbios', 10, 7, 'La memoria del justo será bendita: Mas el nombre de los impíos se pudrirá.', 'cosmic'),
('Old', 'Proverbios', 10, 8, 'El sabio de corazón recibirá los mandamientos: Mas el loco de labios caerá.', 'cosmic'),
('Old', 'Proverbios', 10, 9, 'El que camina en integridad, anda confiado: Mas el que pervierte sus caminos, será quebrantado.', 'cosmic'),
('Old', 'Proverbios', 10, 10, 'El que guiña del ojo acarrea tristeza; Y el loco de labios será castigado.', 'cosmic'),
('Old', 'Proverbios', 10, 11, 'Vena de vida es la boca del justo: Mas violencia cubrirá la boca de los impíos.', 'cosmic'),
('Old', 'Proverbios', 10, 12, 'El odio despierta rencillas: Mas la caridad cubrirá todas las faltas.', 'cosmic'),
('Old', 'Proverbios', 10, 13, 'En los labios del prudente se halla sabiduría: Y vara á las espaldas del falto de cordura.', 'cosmic'),
('Old', 'Proverbios', 10, 14, 'Los sabios guardan la sabiduría: Mas la boca del loco es calamidad cercana.', 'cosmic'),
('Old', 'Proverbios', 10, 15, 'Las riquezas del rico son su ciudad fuerte; Y el desmayo de los pobres es su pobreza.', 'cosmic'),
('Old', 'Proverbios', 10, 16, 'La obra del justo es para vida; Mas el fruto del impío es para pecado.', 'cosmic'),
('Old', 'Proverbios', 10, 17, 'Camino á la vida es guardar la corrección: Mas el que deja la reprensión, yerra.', 'cosmic'),
('Old', 'Proverbios', 10, 18, 'El que encubre el odio es de labios mentirosos; Y el que echa mala fama es necio.', 'cosmic'),
('Old', 'Proverbios', 10, 19, 'En las muchas palabras no falta pecado: Mas el que refrena sus labios es prudente.', 'cosmic'),
('Old', 'Proverbios', 10, 20, 'Plata escogida es la lengua del justo: Mas el entendimiento de los impíos es como nada.', 'cosmic'),
('Old', 'Proverbios', 10, 21, 'Los labios del justo apacientan á muchos: Mas los necios por falta de entendimiento mueren.', 'cosmic'),
('Old', 'Proverbios', 10, 22, 'La bendición de Jehová es la que enriquece, Y no añade tristeza con ella.', 'cosmic'),
('Old', 'Proverbios', 10, 23, 'Hacer abominación es como risa al insensato: Mas el hombre entendido sabe.', 'cosmic'),
('Old', 'Proverbios', 10, 24, 'Lo que el impío teme, eso le vendrá: Mas á los justos les será dado lo que desean.', 'cosmic'),
('Old', 'Proverbios', 10, 25, 'Como pasa el torbellino, así el malo no permanece: Mas el justo, fundado para siempre.', 'cosmic'),
('Old', 'Proverbios', 10, 26, 'Como el vinagre á los dientes, y como el humo á los ojos, Así es el perezoso á los que lo envían.', 'cosmic'),
('Old', 'Proverbios', 10, 27, 'El temor de Jehová aumentará los días: Mas los años de los impíos serán acortados.', 'cosmic'),
('Old', 'Proverbios', 10, 28, 'La esperanza de los justos es alegría; Mas la esperanza de los impíos perecerá.', 'cosmic'),
('Old', 'Proverbios', 10, 29, 'Fortaleza es al perfecto el camino de Jehová: Mas espanto es á los que obran maldad.', 'cosmic'),
('Old', 'Proverbios', 10, 30, 'El justo eternalmente no será removido: Mas los impíos no habitarán la tierra.', 'cosmic'),
('Old', 'Proverbios', 10, 31, 'La boca del justo producirá sabiduría: Mas la lengua perversa será cortada.', 'cosmic'),
('Old', 'Proverbios', 10, 32, 'Los labios del justo conocerán lo que agrada: Mas la boca de los impíos habla perversidades.', 'cosmic'),
('Old', 'Proverbios', 11, 1, 'EL peso falso abominación es á Jehová: Mas la pesa cabal le agrada.', 'cosmic'),
('Old', 'Proverbios', 11, 2, 'Cuando viene la soberbia, viene también la deshonra: Mas con los humildes es la sabiduría.', 'cosmic'),
('Old', 'Proverbios', 11, 3, 'La integridad de los rectos los encaminará: Mas destruirá á los pecadores la perversidad de ellos.', 'cosmic'),
('Old', 'Proverbios', 11, 4, 'No aprovecharán las riquezas en el día de la ira: Mas la justicia librará de muerte.', 'cosmic'),
('Old', 'Proverbios', 11, 5, 'La justicia del perfecto enderezará su camino: mas el impío por su impiedad caerá.', 'cosmic'),
('Old', 'Proverbios', 11, 6, 'La justicia de los rectos los librará: Mas los pecadores en su pecado serán presos.', 'cosmic'),
('Old', 'Proverbios', 11, 7, 'Cuando muere el hombre impío, perece su esperanza; Y la espectativa de los malos perecerá.', 'cosmic'),
('Old', 'Proverbios', 11, 8, 'El justo es librado de la tribulación: Mas el impío viene en lugar suyo.', 'cosmic'),
('Old', 'Proverbios', 11, 9, 'El hipócrita con la boca daña á su prójimo: Mas los justos son librados con la sabiduría.', 'cosmic'),
('Old', 'Proverbios', 11, 10, 'En el bien de los justos la ciudad se alegra: Mas cuando los impíos perecen, hay fiestas.', 'cosmic'),
('Old', 'Proverbios', 11, 11, 'Por la bendición de los rectos la ciudad será engrandecida: Mas por la boca de los impíos ella será trastornada.', 'cosmic'),
('Old', 'Proverbios', 11, 12, 'El que carece de entendimiento, menosprecia á su prójimo: Mas el hombre prudente calla.', 'cosmic'),
('Old', 'Proverbios', 11, 13, 'El que anda en chismes, descubre el secreto: Mas el de espíritu fiel encubre la cosa.', 'cosmic'),
('Old', 'Proverbios', 11, 14, 'Cuando faltaren las industrias, caerá el pueblo: Mas en la multitud de consejeros hay salud.', 'cosmic'),
('Old', 'Proverbios', 11, 15, 'Con ansiedad será afligido el que fiare al extraño: Mas el que aborreciere las fianzas vivirá confiado.', 'cosmic'),
('Old', 'Proverbios', 11, 16, 'La mujer graciosa tendrá honra: Y los fuertes tendrán riquezas.', 'cosmic'),
('Old', 'Proverbios', 11, 17, 'A su alma hace bien el hombre misericordioso: Mas el cruel atormenta su carne.', 'cosmic'),
('Old', 'Proverbios', 11, 18, 'El impío hace obra falsa: Mas el que sembrare justicia, tendrá galardón firme.', 'cosmic'),
('Old', 'Proverbios', 11, 19, 'Como la justicia es para vida, Así el que sigue el mal es para su muerte.', 'cosmic'),
('Old', 'Proverbios', 11, 20, 'Abominación son á Jehová los perversos de corazón: Mas los perfectos de camino le son agradables.', 'cosmic'),
('Old', 'Proverbios', 11, 21, 'Aunque llegue la mano á la mano, el malo no quedará sin castigo: Mas la simiente de los justos escapará.', 'cosmic'),
('Old', 'Proverbios', 11, 22, 'Zarcillo de oro en la nariz del puerco, Es la mujer hermosa y apartada de razón.', 'cosmic'),
('Old', 'Proverbios', 11, 23, 'El deseo de los justos es solamente bien: Mas la esperanza de los impíos es enojo.', 'cosmic'),
('Old', 'Proverbios', 11, 24, 'Hay quienes reparten, y les es añadido más: Y hay quienes son escasos más de lo que es justo, mas vienen á pobreza.', 'cosmic'),
('Old', 'Proverbios', 11, 25, 'El alma liberal será engordada: Y el que saciare, él también será saciado.', 'cosmic'),
('Old', 'Proverbios', 11, 26, 'Al que retiene el grano, el pueblo lo maldecirá: Mas bendición será sobre la cabeza del que vende.', 'cosmic'),
('Old', 'Proverbios', 11, 27, 'El que madruga al bien, buscará favor: Mas el que busca el mal, vendrále.', 'cosmic'),
('Old', 'Proverbios', 11, 28, 'El que confía en sus riquezas, caerá: Mas los justos reverdecerán como ramos.', 'cosmic'),
