INSERT INTO scriptures (testament, book_name, chapter, verse_number, content, visual_theme) VALUES
('Old', 'Proverbios', 21, 16, 'El hombre que se extravía del camino de la sabiduría, Vendrá á parar en la compañía de los muertos.', 'cosmic'),
('Old', 'Proverbios', 21, 17, 'Hombre necesitado será el que ama el deleite: Y el que ama el vino y ungüentos no enriquecerá.', 'cosmic'),
('Old', 'Proverbios', 21, 18, 'El rescate del justo es el impío, Y por los rectos el prevaricador.', 'cosmic'),
('Old', 'Proverbios', 21, 19, 'Mejor es morar en tierra del desierto, Que con la mujer rencillosa é iracunda.', 'cosmic'),
('Old', 'Proverbios', 21, 20, 'Tesoro codiciable y pingüe hay en la casa del sabio; Mas el hombre insensato lo disipará.', 'cosmic'),
('Old', 'Proverbios', 21, 21, 'El que sigue la justicia y la misericordia, Hallará la vida, la justicia, y la honra.', 'cosmic'),
('Old', 'Proverbios', 21, 22, 'La ciudad de los fuertes tomó el sabio, Y derribó la fuerza en que ella confiaba.', 'cosmic'),
('Old', 'Proverbios', 21, 23, 'El que guarda su boca y su lengua, Su alma guarda de angustias.', 'cosmic'),
('Old', 'Proverbios', 21, 24, 'Soberbio y presuntuoso escarnecedor es el nombre Del que obra con orgullosa saña.', 'cosmic'),
('Old', 'Proverbios', 21, 25, 'El deseo del perezoso le mata, Porque sus manos no quieren trabajar.', 'cosmic'),
('Old', 'Proverbios', 21, 26, 'Hay quien todo el día codicia: Mas el justo da, y no desperdicia.', 'cosmic'),
('Old', 'Proverbios', 21, 27, 'El sacrificio de los impíos es abominación: ¡Cuánto más ofreciéndolo con maldad!', 'cosmic'),
('Old', 'Proverbios', 21, 28, 'El testigo mentiroso perecerá: Mas el hombre que oye, permanecerá en su dicho.', 'cosmic'),
('Old', 'Proverbios', 21, 29, 'El hombre impío afirma rostro: Mas el recto ordena sus caminos.', 'cosmic'),
('Old', 'Proverbios', 21, 30, 'No hay sabiduría, ni inteligencia, Ni consejo, contra Jehová.', 'cosmic'),
('Old', 'Proverbios', 21, 31, 'El caballo se apareja para el día de la batalla: Mas de Jehová es el salvar.', 'cosmic'),
('Old', 'Proverbios', 22, 1, 'DE más estima es la buena fama que las muchas riquezas; Y la buena gracia más que la plata y el oro.', 'cosmic'),
('Old', 'Proverbios', 22, 2, 'El rico y el pobre se encontraron: A todos ellos hizo Jehová.', 'cosmic'),
('Old', 'Proverbios', 22, 3, 'El avisado ve el mal, y escóndese: Mas los simples pasan, y reciben el daño.', 'cosmic'),
('Old', 'Proverbios', 22, 4, 'Riquezas, y honra, y vida, Son la remuneración de la humildad y del temor de Jehová.', 'cosmic'),
('Old', 'Proverbios', 22, 5, 'Espinas y lazos hay en el camino del perverso: El que guarda su alma se alejará de ellos.', 'cosmic'),
('Old', 'Proverbios', 22, 6, 'Instruye al niño en su carrera: Aun cuando fuere viejo no se apartará de ella.', 'cosmic'),
('Old', 'Proverbios', 22, 7, 'El rico se enseñoreará de los pobres; Y el que toma prestado, siervo es del que empresta.', 'cosmic'),
('Old', 'Proverbios', 22, 8, 'El que sembrare iniquidad, iniquidad segará: Y consumiráse la vara de su ira.', 'cosmic'),
('Old', 'Proverbios', 22, 9, 'El ojo misericordioso será bendito, Porque dió de su pan al indigente.', 'cosmic'),
('Old', 'Proverbios', 22, 10, 'Echa fuera al escarnecedor, y saldrá la contienda, Y cesará el pleito y la afrenta.', 'cosmic'),
('Old', 'Proverbios', 22, 11, 'El que ama la limpieza de corazón, Por la gracia de sus labios su amigo será el rey.', 'cosmic'),
('Old', 'Proverbios', 22, 12, 'Los ojos de Jehová miran por la ciencia; Mas él trastorna las cosas de los prevaricadores.', 'cosmic'),
('Old', 'Proverbios', 22, 13, 'Dice el perezoso: El león está fuera; En mitad de las calles seré muerto.', 'cosmic'),
('Old', 'Proverbios', 22, 14, 'Sima profunda es la boca de las extrañas: Aquel contra el cual estuviere Jehová airado, caerá en ella.', 'cosmic'),
('Old', 'Proverbios', 22, 15, 'La necedad está ligada en el corazón del muchacho; Mas la vara de la corrección la hará alejar de él.', 'cosmic'),
('Old', 'Proverbios', 22, 16, 'El que oprime al pobre para aumentarse él, Y que da al rico, ciertamente será pobre.', 'cosmic'),
('Old', 'Proverbios', 22, 17, 'Inclina tu oído, y oye las palabras de los sabios, Y pon tu corazón á mi sabiduría:', 'cosmic'),
('Old', 'Proverbios', 22, 18, 'Porque es cosa deleitable, si las guardares en tus entrañas; Y que juntamente sean ordenadas en tus labios.', 'cosmic'),
('Old', 'Proverbios', 22, 19, 'Para que tu confianza sea en Jehová, Te las he hecho saber hoy á ti también.', 'cosmic'),
('Old', 'Proverbios', 22, 20, '¿No te he escrito tres veces En consejos y ciencia,', 'cosmic'),
('Old', 'Proverbios', 22, 21, 'Para hacerte saber la certidumbre de las razones verdaderas, Para que puedas responder razones de verdad á los que á ti enviaren?', 'cosmic'),
('Old', 'Proverbios', 22, 22, 'No robes al pobre, porque es pobre, Ni quebrantes en la puerta al afligido:', 'cosmic'),
('Old', 'Proverbios', 22, 23, 'Porque Jehová juzgará la causa de ellos, Y despojará el alma de aquellos que los despojaren.', 'cosmic'),
('Old', 'Proverbios', 22, 24, 'No te entrometas con el iracundo, Ni te acompañes con el hombre de enojos;', 'cosmic'),
('Old', 'Proverbios', 22, 25, 'Porque no aprendas sus maneras, Y tomes lazo para tu alma.', 'cosmic'),
('Old', 'Proverbios', 22, 26, 'No estés entre los que tocan la mano, Entre los que fían por deudas.', 'cosmic'),
('Old', 'Proverbios', 22, 27, 'Si no tuvieres para pagar, ¿Por qué han de quitar tu cama de debajo de ti?', 'cosmic'),
('Old', 'Proverbios', 22, 28, 'No traspases el término antiguo Que pusieron tus padres.', 'cosmic'),
('Old', 'Proverbios', 22, 29, '¿Has visto hombre solícito en su obra? delante de los reyes estará; No estará delante de los de baja suerte.', 'cosmic'),
('Old', 'Proverbios', 23, 1, 'CUANDO te sentares á comer con algún señor, Considera bien lo que estuviere delante de ti;', 'cosmic'),
('Old', 'Proverbios', 23, 2, 'Y pon cuchillo á tu garganta, Si tienes gran apetito.', 'cosmic'),
('Old', 'Proverbios', 23, 3, 'No codicies sus manjares delicados, Porque es pan engañoso', 'cosmic'),
('Old', 'Proverbios', 23, 4, 'No trabajes por ser rico; Pon coto á tu prudencia.', 'cosmic'),
('Old', 'Proverbios', 23, 5, '¿Has de poner tus ojos en las riquezas, siendo ningunas? Porque hacerse han alas, Como alas de águila, y volarán al cielo.', 'cosmic'),
('Old', 'Proverbios', 23, 6, 'No comas pan de hombre de mal ojo, Ni codicies sus manjares:', 'cosmic'),
('Old', 'Proverbios', 23, 7, 'Porque cual es su pensamiento en su alma, tal es él. Come y bebe, te dirá; Mas su corazón no está contigo.', 'cosmic'),
('Old', 'Proverbios', 23, 8, 'Vomitarás la parte que tú comiste, Y perderás tus suaves palabras.', 'cosmic'),
('Old', 'Proverbios', 23, 9, 'No hables á oídos del necio; Porque menospreciará la prudencia de tus razones.', 'cosmic'),
('Old', 'Proverbios', 23, 10, 'No traspases el término antiguo, Ni entres en la heredad de los huérfanos:', 'cosmic'),
('Old', 'Proverbios', 23, 11, 'Porque el defensor de ellos es el Fuerte, El cual juzgará la causa de ellos contra ti.', 'cosmic'),
('Old', 'Proverbios', 23, 12, 'Aplica tu corazón á la enseñanza, Y tus oídos á las palabras de sabiduría.', 'cosmic'),
('Old', 'Proverbios', 23, 13, 'No rehuses la corrección del muchacho: Porque si lo hirieres con vara, no morirá.', 'cosmic'),
('Old', 'Proverbios', 23, 14, 'Tú lo herirás con vara, Y librarás su alma del infierno.', 'cosmic'),
('Old', 'Proverbios', 23, 15, 'Hijo mío, si tu corazón fuere sabio, También á mí se me alegrará el corazón;', 'cosmic'),
('Old', 'Proverbios', 23, 16, 'Mis entrañas también se alegrarán, Cuando tus labios hablaren cosas rectas.', 'cosmic'),
('Old', 'Proverbios', 23, 17, 'No tenga tu corazón envidia de los pecadores, Antes persevera en el temor de Jehová todo tiempo:', 'cosmic'),
('Old', 'Proverbios', 23, 18, 'Porque ciertamente hay fin, Y tu esperanza no será cortada.', 'cosmic'),
('Old', 'Proverbios', 23, 19, 'Oye tú, hijo mío, y sé sabio, Y endereza tu corazón al camino.', 'cosmic'),
('Old', 'Proverbios', 23, 20, 'No estés con los bebedores de vino, Ni con los comedores de carne:', 'cosmic'),
('Old', 'Proverbios', 23, 21, 'Porque el bebedor y el comilón empobrecerán: Y el sueño hará vestir vestidos rotos.', 'cosmic'),
('Old', 'Proverbios', 23, 22, 'Oye á tu padre, á aquel que te engendró; Y cuando tu madre envejeciere, no la menosprecies.', 'cosmic'),
('Old', 'Proverbios', 23, 23, 'Compra la verdad, y no la vendas; La sabiduría, la enseñanza, y la inteligencia.', 'cosmic'),
('Old', 'Proverbios', 23, 24, 'Mucho se alegrará el padre del justo: Y el que engendró sabio se gozará con él.', 'cosmic'),
('Old', 'Proverbios', 23, 25, 'Alégrense tu padre y tu madre, Y gócese la que te engendró.', 'cosmic'),
('Old', 'Proverbios', 23, 26, 'Dame, hijo mío, tu corazón, Y miren tus ojos por mis caminos.', 'cosmic'),
('Old', 'Proverbios', 23, 27, 'Porque sima profunda es la ramera, Y pozo angosto la extraña.', 'cosmic'),
('Old', 'Proverbios', 23, 28, 'También ella, como robador, acecha, Y multiplica entre los hombres los prevaricadores.', 'cosmic'),
('Old', 'Proverbios', 23, 29, '¿Para quién será el ay? ¿para quién el ay? ¿para quién las rencillas? ¿Para quién las quejas? ¿para quién las heridas en balde? ¿Para quién lo amoratado de los ojos?', 'cosmic'),
('Old', 'Proverbios', 23, 30, 'Fkpara los que se detienen mucho en el vino, Para los que van buscando la mistura.', 'cosmic'),
('Old', 'Proverbios', 23, 31, 'No mires al vino cuando rojea, Cuando resplandece su color en el vaso: Entrase suavemente;', 'cosmic'),
('Old', 'Proverbios', 23, 32, 'Mas al fin como serpiente morderá, Y como basilisco dará dolor:', 'cosmic'),
('Old', 'Proverbios', 23, 33, 'Tus ojos mirarán las extrañas, Y tu corazón hablará perversidades.', 'cosmic'),
('Old', 'Proverbios', 23, 34, 'Y serás como el que yace en medio de la mar, O como el que está en la punta de un mastelero.', 'cosmic'),
('Old', 'Proverbios', 23, 35, 'Y dirás: Hiriéronme, mas no me dolió; Azotáronme, mas no lo sentí; Cuando despertare, aun lo tornaré á buscar.', 'cosmic'),
('Old', 'Proverbios', 24, 1, 'NO tengas envidia de los hombres malos, Ni desees estar con ellos:', 'cosmic'),
('Old', 'Proverbios', 24, 2, 'Porque su corazón piensa en robar, E iniquidad hablan sus labios.', 'cosmic'),
('Old', 'Proverbios', 24, 3, 'Con sabiduría se edificará la casa, Y con prudencia se afirmará;', 'cosmic'),
('Old', 'Proverbios', 24, 4, 'Y con ciencia se henchirán las cámaras De todo bien preciado y agradable.', 'cosmic'),
('Old', 'Proverbios', 24, 5, 'El hombre sabio es fuerte; Y de pujante vigor el hombre docto.', 'cosmic'),
('Old', 'Proverbios', 24, 6, 'Porque con ingenio harás la guerra: Y la salud está en la multitud de consejeros.', 'cosmic'),
('Old', 'Proverbios', 24, 7, 'Alta está para el insensato la sabiduría: En la puerta no abrirá él su boca.', 'cosmic'),
('Old', 'Proverbios', 24, 8, 'Al que piensa mal hacer Le llamarán hombre de malos pensamientos.', 'cosmic'),
('Old', 'Proverbios', 24, 9, 'El pensamiento del necio es pecado: Y abominación á los hombres el escarnecedor.', 'cosmic'),
('Old', 'Proverbios', 24, 10, 'Si fueres flojo en el día de trabajo, Tu fuerza será reducida.', 'cosmic'),
('Old', 'Proverbios', 24, 11, 'Si dejares de librar los que son tomados para la muerte, Y los que son llevados al degolladero;', 'cosmic'),
('Old', 'Proverbios', 24, 12, 'Si dijeres: Ciertamente no lo supimos; ¿No lo entenderá el que pesa los corazones? El que mira por tu alma, él lo conocerá, Y dará al hombre según sus obras.', 'cosmic'),
('Old', 'Proverbios', 24, 13, 'Come, hijo mío, de la miel, porque es buena, Y del panal dulce á tu paladar:', 'cosmic'),
('Old', 'Proverbios', 24, 14, 'Tal será el conocimiento de la sabiduría á tu alma: Si la hallares tendrá recompensa, Y al fin tu esperanza no será cortada.', 'cosmic'),
('Old', 'Proverbios', 24, 15, 'Oh impío, no aceches la tienda del justo, No saquees su cámara;', 'cosmic'),
('Old', 'Proverbios', 24, 16, 'Porque siete veces cae el justo, y se torna á levantar; Mas los impíos caerán en el mal.', 'cosmic'),
('Old', 'Proverbios', 24, 17, 'Cuando cayere tu enemigo, no te huelgues; Y cuando tropezare, no se alegre tu corazón:', 'cosmic'),
('Old', 'Proverbios', 24, 18, 'Porque Jehová no lo mire, y le desagrade, Y aparte de sobre él su enojo.', 'cosmic'),
('Old', 'Proverbios', 24, 19, 'No te entrometas con los malignos, Ni tengas envidia de los impíos;', 'cosmic'),
('Old', 'Proverbios', 24, 20, 'Porque para el malo no habrá buen fin, Y la candela de los impíos será apagada.', 'cosmic'),
('Old', 'Proverbios', 24, 21, 'Teme á Jehová, hijo mío, y al rey; No te entrometas con los veleidosos:', 'cosmic'),
('Old', 'Proverbios', 24, 22, 'Porque su quebrantamiento se levantará de repente; Y el quebrantamiento de ambos, ¿quién lo comprende?', 'cosmic'),
('Old', 'Proverbios', 24, 23, 'También estas cosas pertenecen á los sabios. Tener respeto á personas en el juicio no es bueno.', 'cosmic'),
('Old', 'Proverbios', 24, 24, 'El que dijere al malo, Justo eres, Los pueblos lo maldecirán, y le detestarán las naciones:', 'cosmic'),
('Old', 'Proverbios', 24, 25, 'Mas los que lo reprenden, serán agradables, Y sobre ellos vendrá bendición de bien.', 'cosmic'),
('Old', 'Proverbios', 24, 26, 'Besados serán los labios Del que responde palabras rectas.', 'cosmic'),
('Old', 'Proverbios', 24, 27, 'Apresta tu obra de afuera, Y disponla en tu heredad; Y después edificarás tu casa.', 'cosmic'),
('Old', 'Proverbios', 24, 28, 'No seas sin causa testigo contra tu prójimo; Y no lisonjees con tus labios.', 'cosmic'),
('Old', 'Proverbios', 24, 29, 'No digas: Como me hizo, así le haré; Daré el pago al hombre según su obra.', 'cosmic'),
('Old', 'Proverbios', 24, 30, 'Pasé junto á la heredad del hombre perezoso, Y junto á la viña del hombre falto de entendimiento;', 'cosmic'),
('Old', 'Proverbios', 24, 31, 'Y he aquí que por toda ella habían ya crecido espinas, Ortigas habían ya cubierto su haz, Y su cerca de piedra estaba ya destruída.', 'cosmic'),
('Old', 'Proverbios', 24, 32, 'Y yo miré, y púse lo en mi corazón: Vi lo, y tomé consejo.', 'cosmic'),
('Old', 'Proverbios', 24, 33, 'Un poco de sueño, cabeceando otro poco, Poniendo mano sobre mano otro poco para dormir;', 'cosmic'),
('Old', 'Proverbios', 24, 34, 'Así vendrá como caminante tu necesidad, Y tu pobreza como hombre de escudo.', 'cosmic'),
('Old', 'Proverbios', 25, 1, 'TAMBIÉN estos son proverbios de Salomón, los cuales copiaron los varones de Ezechîas, rey de Judá.', 'cosmic'),
('Old', 'Proverbios', 25, 2, 'Gloria de Dios es encubrir la palabra; Mas honra del rey es escudriñar la palabra.', 'cosmic'),
('Old', 'Proverbios', 25, 3, 'Para la altura de los cielos, y para la profundidad de la tierra, Y para el corazón de los reyes, no hay investigación.', 'cosmic'),
('Old', 'Proverbios', 25, 4, 'Quita las escorias de la plata, Y saldrá vaso al fundidor.', 'cosmic'),
('Old', 'Proverbios', 25, 5, 'Aparta al impío de la presencia del rey, Y su trono se afirmará en justicia.', 'cosmic'),
('Old', 'Proverbios', 25, 6, 'No te alabes delante del rey, Ni estés en el lugar de los grandes:', 'cosmic'),
('Old', 'Proverbios', 25, 7, 'Porque mejor es que se te diga, Sube acá, Que no que seas humillado delante del príncipe Que miraron tus ojos.', 'cosmic'),
('Old', 'Proverbios', 25, 8, 'No salgas á pleito presto, No sea que no sepas qué hacer al fin, Después que tu prójimo te haya dejado confuso.', 'cosmic'),
('Old', 'Proverbios', 25, 9, 'Trata tu causa con tu compañero Y no descubras el secreto á otro.', 'cosmic'),
('Old', 'Proverbios', 25, 10, 'No sea que te deshonre el que lo oyere, Y tu infamia no pueda repararse.', 'cosmic'),
('Old', 'Proverbios', 25, 11, 'Manzana de oro con figuras de plata Es la palabra dicha como conviene.', 'cosmic'),
('Old', 'Proverbios', 25, 12, 'Como zarcillo de oro y joyel de oro fino, Es el que reprende al sabio que tiene oído dócil.', 'cosmic'),
('Old', 'Proverbios', 25, 13, 'Como frío de nieve en tiempo de la siega, Así es el mensajero fiel á los que lo envían: Pues al alma de su señor da refrigerio.', 'cosmic'),
('Old', 'Proverbios', 25, 14, 'Como nubes y vientos sin lluvia, Así es el hombre que se jacta de vana liberalidad.', 'cosmic'),
('Old', 'Proverbios', 25, 15, 'Con larga paciencia se aplaca el príncipe; Y la lengua blanda quebranta los huesos.', 'cosmic'),
('Old', 'Proverbios', 25, 16, '¿Hallaste la miel? come lo que te basta; No sea que te hartes de ella, y la vomites.', 'cosmic'),
('Old', 'Proverbios', 25, 17, 'Detén tu pie de la casa de tu vecino, Porque harto de ti no te aborrezca.', 'cosmic'),
('Old', 'Proverbios', 25, 18, 'Martillo y cuchillo y saeta aguda, Es el hombre que habla contra su prójimo falso testimonio.', 'cosmic'),
('Old', 'Proverbios', 25, 19, 'Diente quebrado y pie resbalador, Es la confianza en el prevaricador en tiempo de angustia.', 'cosmic'),
('Old', 'Proverbios', 25, 20, 'El que canta canciones al corazón afligido, Es como el que quita la ropa en tiempo de frío, ó el que sobre el jabón echa vinagre.', 'cosmic'),
('Old', 'Proverbios', 25, 21, 'Si el que te aborrece tuviere hambre, dale de comer pan; Y si tuviere sed, dale de beber agua:', 'cosmic'),
('Old', 'Proverbios', 25, 22, 'Porque ascuas allegas sobre su cabeza, Y Jehová te lo pagará.', 'cosmic'),
('Old', 'Proverbios', 25, 23, 'El viento del norte ahuyenta la lluvia, Y el rostro airado la lengua detractora.', 'cosmic'),
('Old', 'Proverbios', 25, 24, 'Mejor es estar en un rincón de casa, Que con la mujer rencillosa en espaciosa casa.', 'cosmic'),
('Old', 'Proverbios', 25, 25, 'Como el agua fría al alma sedienta, Así son las buenas nuevas de lejanas tierras.', 'cosmic'),
('Old', 'Proverbios', 25, 26, 'Como fuente turbia y manantial corrompido, Es el justo que cae delante del impío.', 'cosmic'),
('Old', 'Proverbios', 25, 27, 'Comer mucha miel no es bueno: Ni el buscar la propia gloria es gloria.', 'cosmic'),
('Old', 'Proverbios', 25, 28, 'Como ciudad derribada y sin muro, Es el hombre cuyo espíritu no tiene rienda.', 'cosmic'),
('Old', 'Proverbios', 26, 1, 'COMO la nieve en el verano, y la lluvia en la siega, Así conviene al necio la honra.', 'cosmic'),
('Old', 'Proverbios', 26, 2, 'Como el gorrión en su vagar, y como la golondrina en su vuelo, Así la maldición sin causa nunca vendrá.', 'cosmic'),
('Old', 'Proverbios', 26, 3, 'El látigo para el caballo, y el cabestro para el asno, Y la vara para la espalda del necio.', 'cosmic'),
('Old', 'Proverbios', 26, 4, 'Nunca respondas al necio en conformidad á su necedad, Para que no seas tú también como él.', 'cosmic'),
('Old', 'Proverbios', 26, 5, 'Responde al necio según su necedad, Porque no se estime sabio en su opinión.', 'cosmic'),
('Old', 'Proverbios', 26, 6, 'Como el que se corta los pies y bebe su daño, Así es el que envía algo por mano de un necio.', 'cosmic'),
('Old', 'Proverbios', 26, 7, 'Alzar las piernas del cojo. Así es el proverbio en la boca del necio.', 'cosmic'),
('Old', 'Proverbios', 26, 8, 'Como quien liga la piedra en la honda, Así hace el que al necio da honra.', 'cosmic'),
('Old', 'Proverbios', 26, 9, 'Espinas hincadas en mano del embriagado, Tal es el proverbio en la boca de los necios.', 'cosmic'),
('Old', 'Proverbios', 26, 10, 'El grande cría todas las cosas; y da la paga al insensato, Y la da á los transgresores.', 'cosmic'),
('Old', 'Proverbios', 26, 11, 'Como perro que vuelve á su vómito, Así el necio que repite su necedad.', 'cosmic'),
('Old', 'Proverbios', 26, 12, '¿Has visto hombre sabio en su opinión? Más esperanza hay del necio que de él.', 'cosmic'),
('Old', 'Proverbios', 26, 13, 'Dice el perezoso: El león está en el camino; El león está en las calles.', 'cosmic'),
('Old', 'Proverbios', 26, 14, 'Las puertas se revuelven en sus quicios: Así el perezoso en su cama.', 'cosmic'),
('Old', 'Proverbios', 26, 15, 'Esconde el perezoso su mano en el seno; Cánsase de tornarla á su boca.', 'cosmic'),
('Old', 'Proverbios', 26, 16, 'A su ver es el perezoso más sabio Que siete que le den consejo.', 'cosmic'),
('Old', 'Proverbios', 26, 17, 'El que pasando se deja llevar de la ira en pleito ajeno, Es como el que toma al perro por las orejas.', 'cosmic'),
('Old', 'Proverbios', 26, 18, 'Como el que enloquece, y echa llamas Y saetas y muerte,', 'cosmic'),
('Old', 'Proverbios', 26, 19, 'Tal es el hombre que daña á su amigo, Y dice: Ciertamente me chanceaba.', 'cosmic'),
('Old', 'Proverbios', 26, 20, 'Sin leña se apaga el fuego: Y donde no hay chismoso, cesa la contienda.', 'cosmic'),
('Old', 'Proverbios', 26, 21, 'El carbón para brasas, y la leña para el fuego: Y el hombre rencilloso para encender contienda.', 'cosmic'),
('Old', 'Proverbios', 26, 22, 'Las palabras del chismoso parecen blandas; Mas ellas entran hasta lo secreto del vientre.', 'cosmic'),
('Old', 'Proverbios', 26, 23, 'Como escoria de plata echada sobre el tiesto, Son los labios enardecidos y el corazón malo.', 'cosmic'),
('Old', 'Proverbios', 26, 24, 'Otro parece en los labios al que aborrece; Mas en su interior pone engaño.', 'cosmic'),
('Old', 'Proverbios', 26, 25, 'Cuando hablare amigablemente, no le creas; Porque siete abominaciones hay en su corazón.', 'cosmic'),
('Old', 'Proverbios', 26, 26, 'Encúbrese el odio con disimulo; Mas su malicia será descubierta en la congregación.', 'cosmic'),
('Old', 'Proverbios', 26, 27, 'El que cavare sima, caerá en ella: Y el que revuelva la piedra, á él volverá.', 'cosmic'),
('Old', 'Proverbios', 26, 28, 'La falsa lengua atormenta al que aborrece: Y la boca lisonjera hace resbaladero.', 'cosmic'),
('Old', 'Proverbios', 27, 1, 'NO te jactes del día de mañana; Porque no sabes qué dará de sí el día.', 'cosmic'),
('Old', 'Proverbios', 27, 2, 'Alábete el extraño, y no tu boca; El ajeno, y no tus labios.', 'cosmic'),
('Old', 'Proverbios', 27, 3, 'Pesada es la piedra, y la arena pesa; Mas la ira del necio es más pesada que ambas cosas.', 'cosmic'),
('Old', 'Proverbios', 27, 4, 'Cruel es la ira, é impetuoso el furor; Mas ¿quién parará delante de la envidia?', 'cosmic'),
('Old', 'Proverbios', 27, 5, 'Mejor es reprensión manifiesta Que amor oculto.', 'cosmic'),
('Old', 'Proverbios', 27, 6, 'Fieles son las heridas del que ama; Pero importunos los besos del que aborrece.', 'cosmic'),
('Old', 'Proverbios', 27, 7, 'El alma harta huella el panal de miel; Mas al alma hambrienta todo lo amargo es dulce.', 'cosmic'),
('Old', 'Proverbios', 27, 8, 'Cual ave que se va de su nido, Tal es el hombre que se va de su lugar.', 'cosmic'),
('Old', 'Proverbios', 27, 9, 'El ungüento y el perfume alegran el corazón: Y el amigo al hombre con el cordial consejo.', 'cosmic'),
('Old', 'Proverbios', 27, 10, 'No dejes á tu amigo, ni al amigo de tu padre; Ni entres en casa de tu hermano el día de tu aflicción. Mejor es el vecino cerca que el hermano lejano.', 'cosmic'),
('Old', 'Proverbios', 27, 11, 'Sé sabio, hijo mío, y alegra mi corazón, Y tendré qué responder al que me deshonrare.', 'cosmic'),
('Old', 'Proverbios', 27, 12, 'El avisado ve el mal, y escóndese, Mas los simples pasan, y llevan el daño.', 'cosmic'),
('Old', 'Proverbios', 27, 13, 'Quítale su ropa al que fió al extraño; Y al que fió á la extraña, tómale prenda.', 'cosmic'),
('Old', 'Proverbios', 27, 14, 'El que bendice á su amigo en alta voz, madrugando de mañana, Por maldición se le contará.', 'cosmic'),
('Old', 'Proverbios', 27, 15, 'Gotera continua en tiempo de lluvia, Y la mujer rencillosa, son semejantes:', 'cosmic'),
('Old', 'Proverbios', 27, 16, 'El que pretende contenerla, arresta el viento: O el aceite en su mano derecha.', 'cosmic'),
('Old', 'Proverbios', 27, 17, 'Hierro con hierro se aguza; Y el hombre aguza el rostro de su amigo.', 'cosmic'),
('Old', 'Proverbios', 27, 18, 'El que guarda la higuera, comerá su fruto; Y el que guarda á su señor, será honrado.', 'cosmic'),
('Old', 'Proverbios', 27, 19, 'Como un agua se parece á otra, Así el corazón del hombre al otro.', 'cosmic'),
('Old', 'Proverbios', 27, 20, 'El sepulcro y la perdición nunca se hartan: Así los ojos del hombre nunca están satisfechos.', 'cosmic'),
('Old', 'Proverbios', 27, 21, 'El crisol prueba la plata, y la hornaza el oro: Y al hombre la boca del que lo alaba.', 'cosmic'),
('Old', 'Proverbios', 27, 22, 'Aunque majes al necio en un mortero entre granos de trigo á pisón majados, No se quitará de él su necedad.', 'cosmic'),
('Old', 'Proverbios', 27, 23, 'Considera atentamente el aspecto de tus ovejas; Pon tu corazón á tus rebaños:', 'cosmic'),
('Old', 'Proverbios', 27, 24, 'Porque las riquezas no son para siempre; ¿Y será la corona para perpetuas generaciones?', 'cosmic'),
('Old', 'Proverbios', 27, 25, 'Saldrá la grama, aparecerá la hierba, Y segaránse las hierbas de los montes.', 'cosmic'),
('Old', 'Proverbios', 27, 26, 'Los corderos para tus vestidos, Y los cabritos para el precio del campo:', 'cosmic'),
('Old', 'Proverbios', 27, 27, 'Y abundancia de leche de las cabras para tu mantenimiento, y para mantenimiento de tu casa, Y para sustento de tus criadas.', 'cosmic'),
('Old', 'Proverbios', 28, 1, 'HUYE el impío sin que nadie lo persiga: Mas el justo está confiado como un leoncillo.', 'cosmic'),
('Old', 'Proverbios', 28, 2, 'Por la rebelión de la tierra sus príncipes son muchos: Mas por el hombre entendido y sabio permanecerá sin mutación.', 'cosmic'),
('Old', 'Proverbios', 28, 3, 'El hombre pobre y robador de los pobres, Es lluvia de avenida y sin pan.', 'cosmic'),
('Old', 'Proverbios', 28, 4, 'Los que dejan la ley, alaban á los impíos: Mas los que la guardan, contenderán con ellos.', 'cosmic'),
('Old', 'Proverbios', 28, 5, 'Los hombres malos no entienden el juicio: Mas los que buscan á Jehová, entienden todas las cosas.', 'cosmic'),
('Old', 'Proverbios', 28, 6, 'Mejor es el pobre que camina en su integridad, Que el de perversos caminos, y rico.', 'cosmic'),
('Old', 'Proverbios', 28, 7, 'El que guarda la ley es hijo prudente: Mas el que es compañero de glotones, avergüenza á su padre.', 'cosmic'),
('Old', 'Proverbios', 28, 8, 'El que aumenta sus riquezas con usura y crecido interés, Para que se dé á los pobres lo allega.', 'cosmic'),
('Old', 'Proverbios', 28, 9, 'El que aparta su oído para no oir la ley, Su oración también es abominable.', 'cosmic'),
('Old', 'Proverbios', 28, 10, 'El que hace errar á los rectos por el mal camino, Él caerá en su misma sima: Mas los perfectos heredarán el bien.', 'cosmic'),
('Old', 'Proverbios', 28, 11, 'El hombre rico es sabio en su opinión: Mas el pobre entendido lo examinará.', 'cosmic'),
('Old', 'Proverbios', 28, 12, 'Cuando los justos se alegran, grande es la gloria; Mas cuando los impíos son levantados, es buscado el hombre.', 'cosmic'),
('Old', 'Proverbios', 28, 13, 'El que encubre sus pecados, no prosperará: Mas el que los confiesa y se aparta, alcanzará misericordia.', 'cosmic'),
('Old', 'Proverbios', 28, 14, 'Bienaventurado el hombre que siempre está temeroso: Mas el que endurece su corazón, caerá en mal.', 'cosmic'),
('Old', 'Proverbios', 28, 15, 'León rugiente y oso hambriento, Es el príncipe impío sobre el pueblo pobre.', 'cosmic'),
('Old', 'Proverbios', 28, 16, 'El príncipe falto de entendimiento multiplicará los agravios: Mas el que aborrece la avaricia, prolongará sus días.', 'cosmic'),
('Old', 'Proverbios', 28, 17, 'El hombre que hace violencia con sangre de persona, Huirá hasta el sepulcro, y nadie le detendrá.', 'cosmic'),
('Old', 'Proverbios', 28, 18, 'El que en integridad camina, será salvo; Mas el de perversos caminos caerá en alguno.', 'cosmic'),
('Old', 'Proverbios', 28, 19, 'El que labra su tierra, se hartará de pan: Mas el que sigue los ociosos, se hartará de pobreza.', 'cosmic'),
('Old', 'Proverbios', 28, 20, 'El hombre de verdad tendrá muchas bendiciones: Mas el que se apresura á enriquecer, no será sin culpa.', 'cosmic'),
('Old', 'Proverbios', 28, 21, 'Tener acepción de personas, no es bueno: Hasta por un bocado de pan prevaricará el hombre.', 'cosmic'),
('Old', 'Proverbios', 28, 22, 'Apresúrase á ser rico el hombre de mal ojo; Y no conoce que le ha de venir pobreza.', 'cosmic'),
('Old', 'Proverbios', 28, 23, 'El que reprende al hombre, hallará después mayor gracia Que el que lisonjea con la lengua.', 'cosmic'),
('Old', 'Proverbios', 28, 24, 'El que roba á su padre ó á su madre, y dice que no es maldad, Compañero es del hombre destruidor.', 'cosmic'),
('Old', 'Proverbios', 28, 25, 'El altivo de ánimo suscita contiendas: Mas el que en Jehová confía, medrará.', 'cosmic'),
('Old', 'Proverbios', 28, 26, 'El que confía en su corazón es necio; Mas el que camina en sabiduría, será salvo.', 'cosmic'),
('Old', 'Proverbios', 28, 27, 'El que da al pobre, no tendrá pobreza: Mas el que aparta sus ojos, tendrá muchas maldiciones.', 'cosmic'),
('Old', 'Proverbios', 28, 28, 'Cuando los impíos son levantados, esconderáse el hombre: Mas cuando perecen, los justos se multiplican.', 'cosmic'),
('Old', 'Proverbios', 29, 1, 'EL hombre que reprendido endurece la cerviz, De repente será quebrantado; ni habrá para él medicina.', 'cosmic'),
('Old', 'Proverbios', 29, 2, 'Cuando los justos dominan, el pueblo se alegra: Mas cuando domina el impío, el pueblo gime.', 'cosmic'),
('Old', 'Proverbios', 29, 3, 'El hombre que ama la sabiduría, alegra á su padre: Mas el que mantiene rameras, perderá la hacienda.', 'cosmic'),
('Old', 'Proverbios', 29, 4, 'El rey con el juicio afirma la tierra: Mas el hombre de presentes la destruirá.', 'cosmic'),
('Old', 'Proverbios', 29, 5, 'El hombre que lisonjea á su prójimo, Red tiende delante de sus pasos.', 'cosmic'),
('Old', 'Proverbios', 29, 6, 'En la prevaricación del hombre malo hay lazo: Mas el justo cantará y se alegrará.', 'cosmic'),
('Old', 'Proverbios', 29, 7, 'Conoce el justo la causa de los pobres: Mas el impío no entiende sabiduría.', 'cosmic'),
('Old', 'Proverbios', 29, 8, 'Los hombres escarnecedores enlazan la ciudad: Mas los sabios apartan la ira.', 'cosmic'),
('Old', 'Proverbios', 29, 9, 'Si el hombre sabio contendiere con el necio, Que se enoje ó que se ría, no tendrá reposo.', 'cosmic'),
('Old', 'Proverbios', 29, 10, 'Los hombres sanguinarios aborrecen al perfecto: Mas los rectos buscan su contentamiento.', 'cosmic'),
('Old', 'Proverbios', 29, 11, 'El necio da suelta á todo su espíritu; Mas el sabio al fin le sosiega.', 'cosmic'),
('Old', 'Proverbios', 29, 12, 'Del señor que escucha la palabra mentirosa, Todos sus ministros son impíos.', 'cosmic'),
('Old', 'Proverbios', 29, 13, 'El pobre y el usurero se encontraron: Jehová alumbra los ojos de ambos.', 'cosmic'),
('Old', 'Proverbios', 29, 14, 'El rey que juzga con verdad á los pobres, Su trono será firme para siempre.', 'cosmic'),
('Old', 'Proverbios', 29, 15, 'La vara y la corrección dan sabiduría: Mas el muchacho consentido avergonzará á su madre.', 'cosmic'),
('Old', 'Proverbios', 29, 16, 'Cuando los impíos son muchos, mucha es la prevaricación; Mas los justos verán la ruina de ellos.', 'cosmic'),
('Old', 'Proverbios', 29, 17, 'Corrige á tu hijo, y te dará descanso, Y dará deleite á tu alma.', 'cosmic'),
('Old', 'Proverbios', 29, 18, 'Sin profecía el pueblo será disipado: Mas el que guarda la ley, bienaventurado él.', 'cosmic'),
('Old', 'Proverbios', 29, 19, 'El siervo no se corregirá con palabras: Porque entiende, mas no corresponde.', 'cosmic'),
('Old', 'Proverbios', 29, 20, '¿Has visto hombre ligero en sus palabras? Más esperanza hay del necio que de él.', 'cosmic'),
('Old', 'Proverbios', 29, 21, 'El que regala á su siervo desde su niñez, A la postre será su hijo:', 'cosmic'),
('Old', 'Proverbios', 29, 22, 'El hombre iracundo levanta contiendas; Y el furioso muchas veces peca.', 'cosmic'),
('Old', 'Proverbios', 29, 23, 'La soberbia del hombre le abate; Pero al humilde de espíritu sustenta la honra.', 'cosmic'),
('Old', 'Proverbios', 29, 24, 'El aparcero del ladrón aborrece su vida; Oirá maldiciones, y no lo denunciará.', 'cosmic'),
('Old', 'Proverbios', 29, 25, 'El temor del hombre pondrá lazo: Mas el que confía en Jehová será levantado.', 'cosmic'),
('Old', 'Proverbios', 29, 26, 'Muchos buscan el favor del príncipe: Mas de Jehová viene el juicio de cada uno.', 'cosmic'),
('Old', 'Proverbios', 29, 27, 'Abominación es á los justos el hombre inicuo; Y abominación es al impío el de rectos caminos.', 'cosmic'),
('Old', 'Proverbios', 30, 1, 'PALABRAS de Agur, hijo de Jachê: La profecía que dijo el varón á Ithiel, á Ithiel y á Ucal.', 'cosmic'),
('Old', 'Proverbios', 30, 2, 'Ciertamente más rudo soy yo que ninguno, Ni tengo entendimiento de hombre.', 'cosmic'),
('Old', 'Proverbios', 30, 3, 'Yo ni aprendí sabiduría, Ni conozco la ciencia del Santo.', 'cosmic'),
('Old', 'Proverbios', 30, 4, '¿Quién subió al cielo, y descendió? ¿Quién encerró los vientos en sus puños? ¿Quién ató las aguas en un paño? ¿Quién afirmó todos los términos de la tierra? ¿Cuál es su nombre, y el nombre de su hijo, si sabes?', 'cosmic'),
('Old', 'Proverbios', 30, 5, 'Toda palabra de Dios es limpia: Es escudo á los que en él esperan.', 'cosmic'),
('Old', 'Proverbios', 30, 6, 'No añadas á sus palabras, porque no te reprenda, Y seas hallado mentiroso.', 'cosmic'),
('Old', 'Proverbios', 30, 7, 'Dos cosas te he demandado; No me las niegues antes que muera.', 'cosmic'),
('Old', 'Proverbios', 30, 8, 'Vanidad y palabra mentirosa aparta de mí. No me des pobreza ni riquezas; Manténme del pan que he menester;', 'cosmic'),
('Old', 'Proverbios', 30, 9, 'No sea que me harte, y te niegue, y diga, ¿Quién es Jehová? O no sea que siendo pobre, hurte, Y blasfeme el nombre de mi Dios.', 'cosmic'),
('Old', 'Proverbios', 30, 10, 'No acuses al siervo ante su señor, Porque no te maldiga, y peques.', 'cosmic'),
('Old', 'Proverbios', 30, 11, 'Hay generación que maldice á su padre, Y á su madre no bendice.', 'cosmic'),
('Old', 'Proverbios', 30, 12, 'Hay generación limpia en su opinión, Si bien no se ha limpiado su inmundicia.', 'cosmic'),
('Old', 'Proverbios', 30, 13, 'Hay generación cuyos ojos son altivos, Y cuyos párpados son alzados.', 'cosmic'),
('Old', 'Proverbios', 30, 14, 'Hay generación cuyos dientes son espadas, y sus muelas cuchillos, Para devorar á los pobres de la tierra, y de entre los hombres á los menesterosos.', 'cosmic'),
('Old', 'Proverbios', 30, 15, 'La sanguijuela tiene dos hijas que se llaman, Trae, trae. Tres cosas hay que nunca se hartan; Aun la cuarta nunca dice, Basta:', 'cosmic'),
('Old', 'Proverbios', 30, 16, 'El sepulcro, y la matriz estéril, La tierra no harta de aguas, Y el fuego que jamás dice, Basta.', 'cosmic'),
('Old', 'Proverbios', 30, 17, 'El ojo que escarnece á su padre, Y menosprecia la enseñanza de la madre, Los cuervos lo saquen de la arroyada, Y tráguenlo los hijos del águila.', 'cosmic'),
('Old', 'Proverbios', 30, 18, 'Tres cosas me son ocultas; Aun tampoco sé la cuarta:', 'cosmic'),
('Old', 'Proverbios', 30, 19, 'El rastro del águila en el aire; El rastro de la culebra sobre la peña; El rastro de la nave en medio de la mar; Y el rastro del hombre en la moza.', 'cosmic'),
('Old', 'Proverbios', 30, 20, 'Tal es el rastro de la mujer adúltera: Come, y limpia su boca, Y dice: No he hecho maldad.', 'cosmic'),
('Old', 'Proverbios', 30, 21, 'Por tres cosas se alborota la tierra, Y la cuarta no puede sufrir:', 'cosmic'),
('Old', 'Proverbios', 30, 22, 'Por el siervo cuando reinare; Y por el necio cuando se hartare de pan;', 'cosmic'),
('Old', 'Proverbios', 30, 23, 'Por la aborrecida cuando se casare; Y por la sierva cuando heredare á su señora.', 'cosmic'),
('Old', 'Proverbios', 30, 24, 'Cuatro cosas son de las más pequeñas de la tierra, Y las mismas son más sabias que los sabios:', 'cosmic'),
('Old', 'Proverbios', 30, 25, 'Las hormigas, pueblo no fuerte, Y en el verano preparan su comida;', 'cosmic'),
('Old', 'Proverbios', 30, 26, 'Los conejos, pueblo nada esforzado, Y ponen su casa en la piedra;', 'cosmic'),
('Old', 'Proverbios', 30, 27, 'Las langostas, no tienen rey, Y salen todas acuadrilladas;', 'cosmic'),
('Old', 'Proverbios', 30, 28, 'La araña, ase con las manos, Y está en palacios de rey.', 'cosmic'),
('Old', 'Proverbios', 30, 29, 'Tres cosas hay de hermoso andar, Y la cuarta pasea muy bien:', 'cosmic'),
('Old', 'Proverbios', 30, 30, 'El león, fuerte entre todos los animales, Que no torna atrás por nadie;', 'cosmic'),
('Old', 'Proverbios', 30, 31, 'El lebrel ceñido de lomos; asimismo el macho cabrío; Y un rey contra el cual ninguno se levanta.', 'cosmic'),
('Old', 'Proverbios', 30, 32, 'Si caiste, fué porque te enalteciste; Y si mal pensaste, Pon el dedo sobre la boca.', 'cosmic'),
('Old', 'Proverbios', 30, 33, 'Ciertamente el que exprime la leche, sacará manteca; Y el que recio se suena las narices, sacará sangre: Y el que provoca la ira, causará contienda.', 'cosmic'),
('Old', 'Proverbios', 31, 1, 'PALABRAS del rey Lemuel; la profecía con que le enseñó su madre.', 'cosmic'),
('Old', 'Proverbios', 31, 2, '¿Qué, hijo mío? ¿y qué, hijo de mi vientre? ¿Y qué, hijo de mis deseos?', 'cosmic'),
('Old', 'Proverbios', 31, 3, 'No des á las mujeres tu fuerza, Ni tus caminos á lo que es para destruir los reyes.', 'cosmic'),
('Old', 'Proverbios', 31, 4, 'No es de los reyes, oh Lemuel, no es de los reyes beber vino, Ni de los príncipes la cerveza.', 'cosmic'),
('Old', 'Proverbios', 31, 5, 'No sea que bebiendo olviden la ley, Y perviertan el derecho de todos los hijos afligidos.', 'cosmic'),
('Old', 'Proverbios', 31, 6, 'Dad la cerveza al desfallecido, Y el vino á los de amargo ánimo:', 'cosmic'),
('Old', 'Proverbios', 31, 7, 'Beban, y olvídense de su necesidad, Y de su miseria no más se acuerden.', 'cosmic'),
('Old', 'Proverbios', 31, 8, 'Abre tu boca por el mudo, En el juicio de todos los hijos de muerte.', 'cosmic'),
('Old', 'Proverbios', 31, 9, 'Abre tu boca, juzga justicia, Y el derecho del pobre y del menesteroso.', 'cosmic'),
('Old', 'Proverbios', 31, 10, 'Mujer fuerte, ¿quién la hallará? Porque su estima sobrepuja largamente á la de piedras preciosas.', 'cosmic'),
('Old', 'Proverbios', 31, 11, 'El corazón de su marido está en ella confiado, Y no tendrá necesidad de despojo.', 'cosmic'),
('Old', 'Proverbios', 31, 12, 'Darále ella bien y no mal, Todos los días de su vida.', 'cosmic'),
('Old', 'Proverbios', 31, 13, 'Buscó lana y lino, Y con voluntad labró de sus manos.', 'cosmic'),
('Old', 'Proverbios', 31, 14, 'Fué como navío de mercader: Trae su pan de lejos.', 'cosmic'),
('Old', 'Proverbios', 31, 15, 'Levantóse aun de noche, Y dió comida á su familia, Y ración á sus criadas.', 'cosmic'),
('Old', 'Proverbios', 31, 16, 'Consideró la heredad, y compróla; Y plantó viña del fruto de sus manos.', 'cosmic'),
('Old', 'Proverbios', 31, 17, 'Ciñó sus lomos de fortaleza, Y esforzó sus brazos.', 'cosmic'),
('Old', 'Proverbios', 31, 18, 'Gustó que era buena su granjería: Su candela no se apagó de noche.', 'cosmic'),
('Old', 'Proverbios', 31, 19, 'Aplicó sus manos al huso, Y sus manos tomaron la rueca.', 'cosmic'),
('Old', 'Proverbios', 31, 20, 'Alargó su mano al pobre, Y extendió sus manos al menesteroso.', 'cosmic'),
('Old', 'Proverbios', 31, 21, 'No tendrá temor de la nieve por su familia, Porque toda su familia está vestida de ropas dobles.', 'cosmic'),
('Old', 'Proverbios', 31, 22, 'Ella se hizo tapices; De lino fino y púrpura es su vestido.', 'cosmic'),
('Old', 'Proverbios', 31, 23, 'Conocido es su marido en las puertas, Cuando se sienta con los ancianos de la tierra.', 'cosmic'),
('Old', 'Proverbios', 31, 24, 'Hizo telas, y vendió; Y dió cintas al mercader.', 'cosmic'),
('Old', 'Proverbios', 31, 25, 'Fortaleza y honor son su vestidura; Y en el día postrero reirá.', 'cosmic'),
('Old', 'Proverbios', 31, 26, 'Abrió su boca con sabiduría: Y la ley de clemencia está en su lengua.', 'cosmic'),
('Old', 'Proverbios', 31, 27, 'Considera los caminos de su casa, Y no come el pan de balde.', 'cosmic'),
('Old', 'Proverbios', 31, 28, 'Levantáronse sus hijos, y llamáronla bienaventurada; Y su marido también la alabó.', 'cosmic'),
('Old', 'Proverbios', 31, 29, 'Muchas mujeres hicieron el bien; Mas tú las sobrepujaste á todas.', 'cosmic'),
('Old', 'Proverbios', 31, 30, 'Engañosa es la gracia, y vana la hermosura: La mujer que teme á Jehová, ésa será alabada.', 'cosmic'),
('Old', 'Proverbios', 31, 31, 'Dadle el fruto de sus manos, Y alábenla en las puertas sus hechos.', 'cosmic'),
('Old', 'Eclesiastés', 1, 1, 'PALABRAS del Predicador, hijo de David, rey en Jerusalem.', 'cosmic'),
('Old', 'Eclesiastés', 1, 2, 'Vanidad de vanidades, dijo el Predicador; vanidad de vanidades, todo vanidad.', 'cosmic'),
('Old', 'Eclesiastés', 1, 3, '¿Qué provecho tiene el hombre de todo su trabajo con que se afana debajo del sol?', 'cosmic'),
('Old', 'Eclesiastés', 1, 4, 'Generación va, y generación viene: mas la tierra siempre permanece.', 'cosmic'),
('Old', 'Eclesiastés', 1, 5, 'Y sale el sol, y pónese el sol, y con deseo vuelve á su lugar donde torna á nacer.', 'cosmic'),
('Old', 'Eclesiastés', 1, 6, 'El viento tira hacia el mediodía, y rodea al norte; va girando de continuo, y á sus giros torna el viento de nuevo.', 'cosmic'),
('Old', 'Eclesiastés', 1, 7, 'Los ríos todos van á la mar, y la mar no se hinche; al lugar de donde los ríos vinieron, allí tornan para correr de nuevo.', 'cosmic'),
('Old', 'Eclesiastés', 1, 8, 'Todas las cosas andan en trabajo mas que el hombre pueda decir: ni los ojos viendo se hartan de ver, ni los oídos se hinchen de oir.', 'cosmic'),
('Old', 'Eclesiastés', 1, 9, '¿Qué es lo que fué? Lo mismo que será. ¿Qué es lo que ha sido hecho? Lo mismo que se hará: y nada hay nuevo debajo del sol.', 'cosmic'),
('Old', 'Eclesiastés', 1, 10, '¿Hay algo de que se pueda decir: He aquí esto es nuevo? Ya fué en los siglos que nos han precedido.', 'cosmic'),
('Old', 'Eclesiastés', 1, 11, 'No hay memoria de lo que precedió, ni tampoco de lo que sucederá habrá memoria en los que serán después.', 'cosmic'),
('Old', 'Eclesiastés', 1, 12, 'Yo el Predicador fuí rey sobre Israel en Jerusalem.', 'cosmic'),
('Old', 'Eclesiastés', 1, 13, 'Y dí mi corazón á inquirir y buscar con sabiduría sobre todo lo que se hace debajo del cielo: este penoso trabajo dió Dios á los hijos de los hombres, en que se ocupen.', 'cosmic'),
('Old', 'Eclesiastés', 1, 14, 'Yo miré todas las obras que se hacen debajo del sol; y he aquí, todo ello es vanidad y aflicción de espíritu.', 'cosmic'),
('Old', 'Eclesiastés', 1, 15, 'Lo torcido no se puede enderezar; y lo falto no puede contarse.', 'cosmic'),
('Old', 'Eclesiastés', 1, 16, 'Hablé yo con mi corazón, diciendo: He aquí hállome yo engrandecido, y he crecido en sabiduría sobre todos los que fueron antes de mí en Jerusalem; y mi corazón ha percibido muchedumbre de sabiduría y ciencia.', 'cosmic'),
('Old', 'Eclesiastés', 1, 17, 'Y dí mi corazón á conocer la sabiduría, y también á entender las locuras y los desvaríos: conocí que aun esto era aflicción de espíritu.', 'cosmic'),
('Old', 'Eclesiastés', 1, 18, 'Porque en la mucha sabiduría hay mucha molestia; y quien añade ciencia, añade dolor.', 'cosmic'),
('Old', 'Eclesiastés', 2, 1, 'DIJE yo en mi corazón: Ven ahora, te probaré con alegría, y gozarás de bienes. Mas he aquí esto también era vanidad.', 'cosmic'),
('Old', 'Eclesiastés', 2, 2, 'A la risa dije: Enloqueces; y al placer: ¿De qué sirve esto?', 'cosmic'),
('Old', 'Eclesiastés', 2, 3, 'Propuse en mi corazón agasajar mi carne con vino, y que anduviese mi corazón en sabiduría, con retención de la necedad, hasta ver cuál fuese el bien de los hijos de los hombres, en el cual se ocuparan debajo del cielo todos los días de su vida.', 'cosmic'),
('Old', 'Eclesiastés', 2, 4, 'Engrandecí mis obras, edifiquéme casas, plantéme viñas;', 'cosmic'),
('Old', 'Eclesiastés', 2, 5, 'Híceme huertos y jardines, y planté en ellos árboles de todos frutos;', 'cosmic'),
('Old', 'Eclesiastés', 2, 6, 'Híceme estanques de aguas, para regar de ellos el bosque donde los árboles crecían.', 'cosmic'),
('Old', 'Eclesiastés', 2, 7, 'Poseí siervos y siervas, y tuve hijos de familia; también tuve posesión grande de vacas y ovejas, sobre todos los que fueron antes de mí en Jerusalem;', 'cosmic'),
('Old', 'Eclesiastés', 2, 8, 'Alleguéme también plata y oro, y tesoro preciado de reyes y de provincias; híceme de cantores y cantoras, y los deleites de los hijos de los hombres, instrumentos músicos y de todas suertes.', 'cosmic'),
('Old', 'Eclesiastés', 2, 9, 'Y fuí engrandecido, y aumentado más que todos los que fueron antes de mí en Jerusalem: á más de esto perseveró conmigo mi sabiduría.', 'cosmic'),
('Old', 'Eclesiastés', 2, 10, 'No negué á mis ojos ninguna cosa que desearan, ni aparté mi corazón de placer alguno, porque mi corazón gozó de todo mi trabajo: y ésta fué mi parte de toda mi faena.', 'cosmic'),
('Old', 'Eclesiastés', 2, 11, 'Miré yo luego todas las obras que habían hecho mis manos, y el trabajo que tomé para hacer las: y he aquí, todo vanidad y aflicción de espíritu, y no hay provecho debajo del sol.', 'cosmic'),
('Old', 'Eclesiastés', 2, 12, 'Después torné yo á mirar para ver la sabiduría y los desvaríos y la necedad; (porque ¿qué hombre hay que pueda seguir al rey en lo que ya hicieron?)', 'cosmic'),
('Old', 'Eclesiastés', 2, 13, 'Y he visto que la sabiduría sobrepuja á la necedad, como la luz á las tinieblas.', 'cosmic'),
('Old', 'Eclesiastés', 2, 14, 'El sabio tiene sus ojos en su cabeza, mas el necio anda en tinieblas: empero también entendí yo que un mismo suceso acaecerá al uno que al otro.', 'cosmic'),
('Old', 'Eclesiastés', 2, 15, 'Entonces dije yo en mi corazón: Como sucederá al necio me sucederá también á mí: ¿para qué pues he trabajado hasta ahora por hacerme más sabio? Y dije en mi corazón, que también esto era vanidad.', 'cosmic'),
('Old', 'Eclesiastés', 2, 16, 'Porque ni del sabio ni del necio habrá memoria para siempre; pues en los días venideros ya todo será olvidado, y también morirá el sabio como el necio.', 'cosmic'),
('Old', 'Eclesiastés', 2, 17, 'Aborrecí por tanto la vida, porque la obra que se hace debajo del sol me era fastidiosa; por cuanto todo es vanidad y aflicción de espíritu.', 'cosmic'),
('Old', 'Eclesiastés', 2, 18, 'Yo asimismo aborrecí todo mi trabajo que había puesto por obra debajo del sol; el cual dejaré á otro que vendrá después de mí.', 'cosmic'),
('Old', 'Eclesiastés', 2, 19, '¿Y quién sabe si será sabio, ó necio, el que se enseñoreará de todo mi trabajo en que yo me afané, y en que ocupé debajo del sol mi sabiduría? Esto también es vanidad.', 'cosmic'),
('Old', 'Eclesiastés', 2, 20, 'Tornéme por tanto á desesperanzar mi corazón acerca de todo el trabajo en que me afané, y en que había ocupado debajo del sol mi sabiduría.', 'cosmic'),
('Old', 'Eclesiastés', 2, 21, '¡Que el hombre trabaje con sabiduría, y con ciencia, y con rectitud, y que haya de dar su hacienda á hombre que nunca trabajó en ello! También es esto vanidad y mal grande.', 'cosmic'),
('Old', 'Eclesiastés', 2, 22, 'Porque ¿qué tiene el hombre de todo su trabajo, y fatiga de su corazón, con que debajo del sol él se afanara?', 'cosmic'),
('Old', 'Eclesiastés', 2, 23, 'Porque todos sus días no son sino dolores, y sus trabajos molestias: aun de noche su corazón no reposa. Esto también es vanidad.', 'cosmic'),
('Old', 'Eclesiastés', 2, 24, 'No hay cosa mejor para el hombre sino que coma y beba, y que su alma vea el bien de su trabajo. También tengo yo visto que esto es de la mano de Dios.', 'cosmic'),
('Old', 'Eclesiastés', 2, 25, 'Porque ¿quién comerá, y quién se cuidará, mejor que yo?', 'cosmic'),
('Old', 'Eclesiastés', 2, 26, 'Porque al hombre que le agrada, Dios le da sabiduría y ciencia y gozo, mas al pecador da trabajo, el que allegue y amontone, para que dé al que agrada á Dios. También esto es vanidad y aflicción de espíritu.', 'cosmic'),
('Old', 'Eclesiastés', 3, 1, 'PARA todas las cosas hay sazón, y todo lo que se quiere debajo del cielo, tiene su tiempo:', 'cosmic'),
('Old', 'Eclesiastés', 3, 2, 'Tiempo de nacer, y tiempo de morir; tiempo de plantar, y tiempo de arrancar lo plantado;', 'cosmic'),
('Old', 'Eclesiastés', 3, 3, 'Tiempo de matar, y tiempo de curar; tiempo de destruir, y tiempo de edificar;', 'cosmic'),
('Old', 'Eclesiastés', 3, 4, 'Tiempo de llorar, y tiempo de reir; tiempo de endechar, y tiempo de bailar;', 'cosmic'),
('Old', 'Eclesiastés', 3, 5, 'Tiempo de esparcir las piedras, y tiempo de allegar las piedras; tiempo de abrazar, y tiempo de alejarse de abrazar;', 'cosmic'),
('Old', 'Eclesiastés', 3, 6, 'Tiempo de agenciar, y tiempo de perder; tiempo de guardar, y tiempo de arrojar;', 'cosmic'),
('Old', 'Eclesiastés', 3, 7, 'Tiempo de romper, y tiempo de coser; tiempo de callar, y tiempo de hablar;', 'cosmic'),
('Old', 'Eclesiastés', 3, 8, 'Tiempo de amar, y tiempo de aborrecer; tiempo de guerra, y tiempo de paz.', 'cosmic'),
('Old', 'Eclesiastés', 3, 9, '¿Qué provecho tiene el que trabaja en lo que trabaja?', 'cosmic'),
('Old', 'Eclesiastés', 3, 10, 'Yo he visto el trabajo que Dios ha dado á los hijos de los hombres para que en él se ocupasen.', 'cosmic'),
('Old', 'Eclesiastés', 3, 11, 'Todo lo hizo hermoso en su tiempo: y aun el mundo dió en su corazón, de tal manera que no alcance el hombre la obra de Dios desde el principio hasta el cabo.', 'cosmic'),
('Old', 'Eclesiastés', 3, 12, 'Yo he conocido que no hay mejor para ellos, que alegrarse, y hacer bien en su vida:', 'cosmic'),
('Old', 'Eclesiastés', 3, 13, 'Y también que es don de Dios que todo hombre coma y beba, y goce el bien de toda su labor.', 'cosmic'),
('Old', 'Eclesiastés', 3, 14, 'He entendido que todo lo que Dios hace, ésto será perpetuo: sobre aquello no se añadirá, ni de ello se disminuirá; y hácelo Dios, para que delante de él teman los hombres.', 'cosmic'),
('Old', 'Eclesiastés', 3, 15, 'Aquello que fué, ya es: y lo que ha de ser, fué ya; y Dios restaura lo que pasó.', 'cosmic'),
('Old', 'Eclesiastés', 3, 16, 'Vi más debajo del sol: en lugar del juicio, allí la impiedad; y en lugar de la justicia, allí la iniquidad.', 'cosmic'),
('Old', 'Eclesiastés', 3, 17, 'Y dije yo en mi corazón: Al justo y al impío juzgará Dios; porque allí hay tiempo á todo lo que se quiere y sobre todo lo que se hace.', 'cosmic'),
('Old', 'Eclesiastés', 3, 18, 'Dije en mi corazón, en orden á la condición de los hijos de los hombres, que Dios los probaría, para que así echaran de ver ellos mismos que son semejantes á las bestias.', 'cosmic'),
('Old', 'Eclesiastés', 3, 19, 'Porque el suceso de los hijos de los hombres, y el suceso del animal, el mismo suceso es: como mueren los unos, así mueren los otros; y una misma respiración tienen todos; ni tiene más el hombre que la bestia: porque todo es vanidad.', 'cosmic'),
('Old', 'Eclesiastés', 3, 20, 'Todo va á un lugar: todo es hecho del polvo, y todo se tornará en el mismo polvo.', 'cosmic'),
('Old', 'Eclesiastés', 3, 21, '¿Quién sabe que el espíritu de los hijos de los hombres suba arriba, y que el espíritu del animal descienda debajo de la tierra?', 'cosmic'),
('Old', 'Eclesiastés', 3, 22, 'Así que he visto que no hay cosa mejor que alegrarse el hombre con lo que hiciere; porque esta es su parte: porque ¿quién lo llevará para que vea lo que ha de ser después de él?', 'cosmic'),
('Old', 'Eclesiastés', 4, 1, 'Y TORNÉME yo, y vi todas las violencias que se hacen debajo del sol: y he aquí las lágrimas de los oprimidos, y sin tener quien los consuele; y la fuerza estaba en la mano de sus opresores, y para ellos no había consolador.', 'cosmic'),
('Old', 'Eclesiastés', 4, 2, 'Y alabé yo los finados que ya murieron, más que los vivientes que hasta ahora están vivos.', 'cosmic'),
('Old', 'Eclesiastés', 4, 3, 'Y tuve por mejor que unos y otros al que no ha sido aún, que no ha visto las malas obras que debajo del sol se hacen.', 'cosmic'),
('Old', 'Eclesiastés', 4, 4, 'Visto he asimismo que todo trabajo y toda excelencia de obras mueve la envidia del hombre contra su prójimo. También esto es vanidad y aflicción de espíritu.', 'cosmic'),
('Old', 'Eclesiastés', 4, 5, 'El necio dobla sus manos y come su carne.', 'cosmic'),
('Old', 'Eclesiastés', 4, 6, 'Mas vale el un puño lleno con descanso, que ambos puños llenos con trabajo y aflicción de espíritu.', 'cosmic'),
('Old', 'Eclesiastés', 4, 7, 'Yo me torné otra vez, y vi vanidad debajo del sol.', 'cosmic'),
('Old', 'Eclesiastés', 4, 8, 'Está un hombre solo y sin sucesor; que ni tiene hijo ni hermano; mas nunca cesa de trabajar, ni sus ojos se hartan de sus riquezas, ni se pregunta: ¿Para quién trabajo yo, y defraudo mi alma del bien? También esto es vanidad, y duro trabajo.', 'cosmic'),
('Old', 'Eclesiastés', 4, 9, 'Mejores son dos que uno; porque tienen mejor paga de su trabajo.', 'cosmic'),
('Old', 'Eclesiastés', 4, 10, 'Porque si cayeren, el uno levantará á su compañero: mas ¡ay del solo! que cuando cayere, no habrá segundo que lo levante.', 'cosmic'),
('Old', 'Eclesiastés', 4, 11, 'También si dos durmieren juntos, se calentarán; mas ¿cómo se calentará uno solo?', 'cosmic'),
('Old', 'Eclesiastés', 4, 12, 'Y si alguno prevaleciere contra el uno, dos estarán contra él; y cordón de tres dobleces no presto se rompe.', 'cosmic'),
('Old', 'Eclesiastés', 4, 13, 'Mejor es el muchacho pobre y sabio, que el rey viejo y fatuo que no sabe ser aconsejado.', 'cosmic'),
('Old', 'Eclesiastés', 4, 14, 'Porque de la cárcel salió para reinar; mientras el nacido en su reino se hizo pobre.', 'cosmic'),
('Old', 'Eclesiastés', 4, 15, 'Vi todos los vivientes debajo del sol caminando con el muchacho, sucesor, que estará en lugar de aquél.', 'cosmic'),
('Old', 'Eclesiastés', 4, 16, 'No tiene fin todo el pueblo que fué antes de ellos: tampoco los que vendrán después estarán con él contentos. Y esto es también vanidad y aflicción de espíritu.', 'cosmic'),
('Old', 'Eclesiastés', 5, 1, 'CUANDO fueres á la casa de Dios, guarda tu pie; y acércate más para oir que para dar el sacrificio de los necios: porque no saben que hacen mal.', 'cosmic'),
('Old', 'Eclesiastés', 5, 2, 'No te des priesa con tu boca, ni tu corazón se apresure á proferir palabra delante de Dios; porque Dios está en el cielo, y tú sobre la tierra: por tanto, sean pocas tus palabras.', 'cosmic'),
('Old', 'Eclesiastés', 5, 3, 'Porque de la mucha ocupación viene el sueño, y de la multitud de las palabras la voz del necio.', 'cosmic'),
('Old', 'Eclesiastés', 5, 4, 'Cuando á Dios hicieres promesa, no tardes en pagarla; porque no se agrada de los insensatos. Paga lo que prometieres.', 'cosmic'),
('Old', 'Eclesiastés', 5, 5, 'Mejor es que no prometas, que no que prometas y no pagues.', 'cosmic'),
('Old', 'Eclesiastés', 5, 6, 'No sueltes tu boca para hacer pecar á tu carne; ni digas delante del ángel, que fué ignorancia. ¿Por qué harás que Dios se aire á causa de tu voz, y que destruya la obra de tus manos?', 'cosmic'),
('Old', 'Eclesiastés', 5, 7, 'Donde los sueños son en multitud, también lo son las vanidades y muchas las palabras; mas tú teme á Dios.', 'cosmic'),
('Old', 'Eclesiastés', 5, 8, 'Si violencias de pobres, y extorsión de derecho y de justicia vieres en la porvincia, no te maravilles de esta licencia; porque alto está mirando sobre alto, y uno más alto está sobre ellos.', 'cosmic'),
('Old', 'Eclesiastés', 5, 9, 'Además el provecho de la tierra es para todos: el rey mismo está sujeto á los campos.', 'cosmic'),
('Old', 'Eclesiastés', 5, 10, 'El que ama el dinero, no se hartará de dinero; y el que ama el mucho tener, no sacará fruto. También esto es vanidad.', 'cosmic'),
('Old', 'Eclesiastés', 5, 11, 'Cuando los bienes se aumentan, también se aumentan sus comedores. ¿Qué bien, pues, tendrá su dueño, sino ver los con sus ojos?', 'cosmic'),
('Old', 'Eclesiastés', 5, 12, 'Dulce es el sueño del trabajador, ora coma mucho ó poco; mas al rico no le deja dormir la hartura.', 'cosmic'),
('Old', 'Eclesiastés', 5, 13, 'Hay una trabajosa enfermedad que he visto debajo del sol: las riquezas guardadas de sus dueños para su mal;', 'cosmic'),
('Old', 'Eclesiastés', 5, 14, 'Las cuales se pierden en malas ocupaciones, y á los hijos que engendraron nada les queda en la mano.', 'cosmic'),
('Old', 'Eclesiastés', 5, 15, 'Como salió del vientre de su madre, desnudo, así se vuelve, tornando como vino; y nada tuvo de su trabajo para llevar en su mano.', 'cosmic'),
('Old', 'Eclesiastés', 5, 16, 'Este también es un gran mal, que como vino, así haya de volver. ¿Y de qué le aprovechó trabajar al viento?', 'cosmic'),
('Old', 'Eclesiastés', 5, 17, 'Demás de esto, todos los días de su vida comerá en tinieblas, con mucho enojo y dolor y miseria.', 'cosmic'),
('Old', 'Eclesiastés', 5, 18, 'He aquí pues el bien que yo he visto: Que lo bueno es comer y beber, y gozar uno del bien de todo su trabajo con que se fatiga debajo del sol, todos los días de su vida que Dios le ha dado; porque esta es su parte.', 'cosmic'),
('Old', 'Eclesiastés', 5, 19, 'Asimismo, á todo hombre á quien Dios dió riquezas y hacienda, y le dió también facultad para que coma de ellas, y tome su parte, y goce su trabajo; esto es don de Dios.', 'cosmic'),
('Old', 'Eclesiastés', 5, 20, 'Porque no se acordará mucho de los días de su vida; pues Dios le responderá con alegría de su corazón.', 'cosmic'),
('Old', 'Eclesiastés', 6, 1, 'HAY un mal que he visto debajo del cielo, y muy común entre los hombres:', 'cosmic'),
('Old', 'Eclesiastés', 6, 2, 'Hombre á quien Dios dió riquezas, y hacienda, y honra, y nada le falta de todo lo que su alma desea; mas Dios no le dió facultad de comer de ello, sino que los extraños se lo comen. Esto vanidad es, y enfermedad trabajosa.', 'cosmic'),
('Old', 'Eclesiastés', 6, 3, 'Si el hombre engendrare ciento, y viviere muchos años, y los días de su edad fueren numerosos; si su alma no se hartó del bien, y también careció de sepultura, yo digo que el abortivo es mejor que él.', 'cosmic'),
('Old', 'Eclesiastés', 6, 4, 'Porque en vano vino, y á tinieblas va, y con tinieblas será cubierto su nombre.', 'cosmic'),
('Old', 'Eclesiastés', 6, 5, 'Aunque no haya visto el sol, ni conocido nada, más reposo tiene éste que aquél.', 'cosmic'),
('Old', 'Eclesiastés', 6, 6, 'Porque si viviere aquel mil años dos veces, si no ha gozado del bien, cierto todos van á un lugar.', 'cosmic'),
('Old', 'Eclesiastés', 6, 7, 'Todo el trabajo del hombre es para su boca, y con todo eso su alma no se harta.', 'cosmic'),
('Old', 'Eclesiastés', 6, 8, 'Porque ¿qué más tiene el sabio que el necio? ¿qué más tiene el pobre que supo caminar entre los vivos?', 'cosmic'),
('Old', 'Eclesiastés', 6, 9, 'Más vale vista de ojos que deseo que pasa. Y también esto es vanidad y aflicción de espíritu.', 'cosmic'),
('Old', 'Eclesiastés', 6, 10, 'El que es, ya su nombre ha sido nombrado; y se sabe que es hombre, y que no podrá contender con el que es más fuerte que él.', 'cosmic'),
('Old', 'Eclesiastés', 6, 11, 'Ciertamente las muchas palabras multiplican la vanidad. ¿Qué más tiene el hombre?', 'cosmic'),
('Old', 'Eclesiastés', 6, 12, 'Porque ¿quién sabe cuál es el bien del hombre en la vida, todos los días de la vida de su vanidad, los cuales él pasa como sombra? Porque ¿quién enseñará al hombre qué será después de él debajo del sol?', 'cosmic'),
('Old', 'Eclesiastés', 7, 1, 'MEJOR es la buena fama que el buen ungüento; y el día de la muerte que el día del nacimiento.', 'cosmic'),
('Old', 'Eclesiastés', 7, 2, 'Mejor es ir á la casa del luto que á la casa del convite: porque aquello es el fin de todos los hombres; y el que vive parará mientes.', 'cosmic'),
('Old', 'Eclesiastés', 7, 3, 'Mejor es el enojo que la risa: porque con la tristeza del rostro se enmendará el corazón.', 'cosmic'),
('Old', 'Eclesiastés', 7, 4, 'El corazón de los sabios, en la casa del luto; mas el corazón de los insensatos, en la casa del placer.', 'cosmic'),
('Old', 'Eclesiastés', 7, 5, 'Mejor es oir la reprensión del sabio, que la canción de los necios.', 'cosmic'),
('Old', 'Eclesiastés', 7, 6, 'Porque la risa del necio es como el estrépito de las espinas debajo de la olla. Y también esto es vanidad.', 'cosmic'),
('Old', 'Eclesiastés', 7, 7, 'Ciertamente la opresión hace enloquecer al sabio: y el presente corrompe el corazón.', 'cosmic'),
('Old', 'Eclesiastés', 7, 8, 'Mejor es el fin del negocio que su principio: mejor es el sufrido de espíritu que el altivo de espíritu.', 'cosmic'),
('Old', 'Eclesiastés', 7, 9, 'No te apresures en tu espíritu á enojarte: porque la ira en el seno de los necios reposa.', 'cosmic'),
('Old', 'Eclesiastés', 7, 10, 'Nunca digas: ¿Qué es la causa que los tiempos pasados fueron mejores que éstos? Porque nunca de esto preguntarás con sabiduría.', 'cosmic'),
('Old', 'Eclesiastés', 7, 11, 'Buena es la ciencia con herencia; y más á los que ven el sol.', 'cosmic'),
('Old', 'Eclesiastés', 7, 12, 'Porque escudo es la ciencia, y escudo es el dinero: mas la sabiduría excede en que da vida á sus poseedores.', 'cosmic'),
('Old', 'Eclesiastés', 7, 13, 'Mira la obra de Dios; porque ¿quién podrá enderezar lo que él torció?', 'cosmic'),
('Old', 'Eclesiastés', 7, 14, 'En el día del bien goza del bien; y en el día del mal considera. Dios también hizo esto delante de lo otro, porque el hombre no halle nada tras de él.', 'cosmic'),
('Old', 'Eclesiastés', 7, 15, 'Todo esto he visto en los días de mi vanidad. Justo hay que perece por su justicia, y hay impío que por su maldad alarga sus días.', 'cosmic'),
('Old', 'Eclesiastés', 7, 16, 'No seas demasiado justo, ni seas sabio con exceso: ¿por qué te destruirás?', 'cosmic'),
('Old', 'Eclesiastés', 7, 17, 'No hagas mal mucho, ni seas insensato: ¿por qué morirás antes de tu tiempo?', 'cosmic'),
('Old', 'Eclesiastés', 7, 18, 'Bueno es que tomes esto, y también de estotro no apartes tu mano; porque el que á Dios teme, saldrá con todo.', 'cosmic'),
('Old', 'Eclesiastés', 7, 19, 'La sabiduría fortifica al sabio más que diez poderosos la ciudad en que fueron.', 'cosmic'),
('Old', 'Eclesiastés', 7, 20, 'Ciertamente no hay hombre justo en la tierra, que haga bien y nunca peque.', 'cosmic'),
('Old', 'Eclesiastés', 7, 21, 'Tampoco apliques tu corazón á todas las cosas que se hablaren, porque no oigas á tu siervo que dice mal de ti:', 'cosmic'),
('Old', 'Eclesiastés', 7, 22, 'Porque tu corazón sabe, como tú también dijiste mal de otros muchas veces.', 'cosmic'),
('Old', 'Eclesiastés', 7, 23, 'Todas estas cosas probé con sabiduría, diciendo: Hacerme he sabio: mas ella se alejó de mí.', 'cosmic'),
('Old', 'Eclesiastés', 7, 24, 'Lejos está lo que fué; y lo muy profundo ¿quién lo hallará?', 'cosmic'),
('Old', 'Eclesiastés', 7, 25, 'Yo he rodeado con mi corazón por saber, y examinar, é inquirir la sabiduría, y la razón; y por conocer la maldad de la insensatez, y el desvarío del error;', 'cosmic'),
('Old', 'Eclesiastés', 7, 26, 'Y yo he hallado más amarga que la muerte la mujer, la cual es redes, y lazos su corazón; sus manos como ligaduras. El que agrada á Dios escapará de ella; mas el pecador será preso en ella.', 'cosmic'),
('Old', 'Eclesiastés', 7, 27, 'He aquí, esto he hallado, dice el Predicador, pesando las cosas una por una para hallar la razón;', 'cosmic'),
('Old', 'Eclesiastés', 7, 28, 'Lo que aun busca mi alma, y no encuentro: un hombre entre mil he hallado; mas mujer de todas éstas nunca hallé.', 'cosmic'),
('Old', 'Eclesiastés', 7, 29, 'He aquí, solamente he hallado esto: que Dios hizo al hombre recto, mas ellos buscaron muchas cuentas.', 'cosmic'),
('Old', 'Eclesiastés', 8, 1, '¿Quién como el sabio? ¿y quién como el que sabe la declaración de las cosas? La sabiduría del hombre hará relucir su rostro, y mudaráse la tosquedad de su semblante.', 'cosmic'),
('Old', 'Eclesiastés', 8, 2, 'Yo te aviso que guardes el mandamiento del rey y la palabra del juramento de Dios.', 'cosmic'),
('Old', 'Eclesiastés', 8, 3, 'No te apresures á irte de delante de él, ni en cosa mala persistas; porque él hará todo lo que quisiere:', 'cosmic'),
('Old', 'Eclesiastés', 8, 4, 'Pues la palabra del rey es con potestad, ¿y quién le dirá, Qué haces?', 'cosmic'),
('Old', 'Eclesiastés', 8, 5, 'El que guarda el mandamiento no experimentará mal; y el tiempo y el juicio conoce el corazón del sabio.', 'cosmic'),
('Old', 'Eclesiastés', 8, 6, 'Porque para todo lo que quisieres hay tiempo y juicio; mas el trabajo del hombre es grande sobre él;', 'cosmic'),
('Old', 'Eclesiastés', 8, 7, 'Porque no sabe lo que ha de ser; y el cuándo haya de ser, ¿quién se lo enseñará?', 'cosmic'),
('Old', 'Eclesiastés', 8, 8, 'No hay hombre que tenga potestad sobre el espíritu para retener el espíritu, ni potestad sobre el día de la muerte: y no valen armas en tal guerra; ni la impiedad librará al que la posee.', 'cosmic'),
('Old', 'Eclesiastés', 8, 9, 'Todo esto he visto, y puesto he mi corazón en todo lo que debajo del sol se hace: hay tiempo en que el hombre se enseñorea del hombre para mal suyo.', 'cosmic'),
('Old', 'Eclesiastés', 8, 10, 'Esto vi también: que los impíos sepultados vinieron aún en memoria; mas los que partieron del lugar santo, fueron luego puestos en olvido en la ciudad donde con rectitud habían obrado. Esto también es vanidad.', 'cosmic'),
('Old', 'Eclesiastés', 8, 11, 'Porque no se ejecuta luego sentencia sobre la mala obra, el corazón de los hijos de los hombres está en ellos lleno para hacer mal.', 'cosmic'),
('Old', 'Eclesiastés', 8, 12, 'Bien que el pecador haga mal cien veces, y le sea dilatado el castigo, con todo yo también sé que los que á Dios temen tendrán bien, los que temieren ante su presencia;', 'cosmic'),
('Old', 'Eclesiastés', 8, 13, 'Y que el impío no tendrá bien, ni le serán prolongados los días, que son como sombra; por cuanto no temió delante de la presencia de Dios.', 'cosmic'),
('Old', 'Eclesiastés', 8, 14, 'Hay vanidad que se hace sobre la tierra: que hay justos á quienes sucede como si hicieran obras de impíos; y hay impíos á quienes acaece como si hicieran obras de justos. Digo que esto también es vanidad.', 'cosmic'),
('Old', 'Eclesiastés', 8, 15, 'Por tanto alabé yo la alegría; que no tiene el hombre bien debajo del sol, sino que coma y beba, y se alegre; y que esto se le quede de su trabajo los días de su vida que Dios le dió debajo del sol.', 'cosmic'),
('Old', 'Eclesiastés', 8, 16, 'Yo pues dí mi corazón á conocer sabiduría, y á ver la faena que se hace sobre la tierra; (porque hay quien ni de noche ni de día ve sueño en su ojos;)', 'cosmic'),
('Old', 'Eclesiastés', 8, 17, 'Y he visto todas las obras de Dios, que el hombre no puede alcanzar la obra que debajo del sol se hace; por mucho que trabaje el hombre buscándola, no la hallará: aunque diga el sabio que la sabe, no por eso podrá alcanzarla.', 'cosmic'),
('Old', 'Eclesiastés', 9, 1, 'CIERTAMENTE dado he mi corazón á todas estas cosas, para declarar todo esto: que los justos y los sabios, y sus obras, están en la mano de Dios; y que no sabe el hombre ni el amor ni el odio por todo lo que pasa delante de él.', 'cosmic'),
('Old', 'Eclesiastés', 9, 2, 'Todo acontece de la misma manera á todos: un mismo suceso ocurre al justo y al impío; al bueno y al limpio y al no limpio; al que sacrifica, y al que no sacrifica: como el bueno, así el que peca; el que jura, como el que teme el juramento.', 'cosmic'),
('Old', 'Eclesiastés', 9, 3, 'Este mal hay entre todo lo que se hace debajo del sol, que todos tengan un mismo suceso, y también que el corazón de los hijos de los hombres esté lleno de mal, y de enloquecimiento en su corazón durante su vida: y después, á los muertos.', 'cosmic'),
('Old', 'Eclesiastés', 9, 4, 'Aún hay esperanza para todo aquél que está entre los vivos; porque mejor es perro vivo que león muerto.', 'cosmic'),
('Old', 'Eclesiastés', 9, 5, 'Porque los que viven saben que han de morir: mas los muertos nada saben, ni tienen más paga; porque su memoria es puesta en olvido.', 'cosmic'),
('Old', 'Eclesiastés', 9, 6, 'También su amor, y su odio y su envidia, feneció ya: ni tiene ya más parte en el siglo, en todo lo que se hace debajo del sol.', 'cosmic'),
('Old', 'Eclesiastés', 9, 7, 'Anda, y come tu pan con gozo, y bebe tu vino con alegre corazón: porque tus obras ya son agradables á Dios.', 'cosmic'),
('Old', 'Eclesiastés', 9, 8, 'En todo tiempo sean blancos tus vestidos, y nunca falte ungüento sobre tu cabeza.', 'cosmic'),
('Old', 'Eclesiastés', 9, 9, 'Goza de la vida con la mujer que amas, todos los días de la vida de tu vanidad, que te son dados debajo del sol, todos los días de tu vanidad; porque esta es tu parte en la vida, y en tu trabajo con que te afanas debajo del sol.', 'cosmic'),
('Old', 'Eclesiastés', 9, 10, 'Todo lo que te viniere á la mano para hacer, hazlo según tus fuerzas; porque en el sepulcro, adonde tú vas, no hay obra, ni industria, ni ciencia, ni sabiduría.', 'cosmic'),
('Old', 'Eclesiastés', 9, 11, 'Tornéme, y vi debajo del sol, que ni es de los ligeros la carrera, ni la guerra de los fuertes, ni aun de los sabios el pan, ni de los prudentes las riquezas, ni de los elocuentes el favor; sino que tiempo y ocasión acontece á todos.', 'cosmic'),
('Old', 'Eclesiastés', 9, 12, 'Porque el hombre tampoco conoce su tiempo: como los peces que son presos en la mala red, y como las aves que se prenden en lazo, así son enlazados los hijos de los hombres en el tiempo malo, cuando cae de repente sobre ellos.', 'cosmic'),
('Old', 'Eclesiastés', 9, 13, 'También vi esta sabiduría debajo del sol, la cual me parece grande:', 'cosmic'),
('Old', 'Eclesiastés', 9, 14, 'Una pequeña ciudad, y pocos hombres en ella; y viene contra ella un gran rey, y cércala, y edifica contra ella grandes baluartes:', 'cosmic'),
('Old', 'Eclesiastés', 9, 15, 'Y hállase en ella un hombre pobre, sabio, el cual libra la ciudad con su sabiduría; y nadie se acordaba de aquel pobre hombre.', 'cosmic'),
('Old', 'Eclesiastés', 9, 16, 'Entonces dije yo: Mejor es la sabiduría que la fortaleza; aunque la ciencia del pobre sea menospreciada, y no sean escuchadas sus palabras.', 'cosmic'),
('Old', 'Eclesiastés', 9, 17, 'Las palabras del sabio con reposo son oídas, más que el clamor del señor entre los necios.', 'cosmic'),
('Old', 'Eclesiastés', 9, 18, 'Mejor es la sabiduría que las armas de guerra; mas un pecador destruye mucho bien.', 'cosmic'),
('Old', 'Eclesiastés', 10, 1, 'LAS moscas muertas hacen heder y dar mal olor el perfume del perfumista: así una pequeña locura, al estimado por sabiduría y honra.', 'cosmic'),
('Old', 'Eclesiastés', 10, 2, 'El corazón del sabio está á su mano derecha; mas el corazón del necio á su mano izquierda.', 'cosmic'),
('Old', 'Eclesiastés', 10, 3, 'Y aun mientras va el necio por el camino, fálta le, su cordura, y dice á todos, que es necio.', 'cosmic'),
('Old', 'Eclesiastés', 10, 4, 'Si el espíritu del príncipe se exaltare contra ti, no dejes tu lugar; porque la lenidad hará cesar grandes ofensas.', 'cosmic'),
('Old', 'Eclesiastés', 10, 5, 'Hay un mal que debajo del sol he visto, á manera de error emanado del príncipe:', 'cosmic'),
('Old', 'Eclesiastés', 10, 6, 'La necedad está colocada en grandes alturas, y los ricos están sentados en lugar bajo.', 'cosmic'),
('Old', 'Eclesiastés', 10, 7, 'Vi siervos en caballos, y príncipes que andaban como siervos sobre la tierra.', 'cosmic'),
('Old', 'Eclesiastés', 10, 8, 'El que hiciere el hoyo caerá en él; y el que aportillare el vallado, morderále la serpiente.', 'cosmic'),
('Old', 'Eclesiastés', 10, 9, 'El que mudare las piedras, trabajo tendrá en ellas: el que cortare la leña, en ella peligrará.', 'cosmic'),
('Old', 'Eclesiastés', 10, 10, 'Si se embotare el hierro, y su filo no fuere amolado, hay que añadir entonces más fuerza: empero excede la bondad de la sabiduría.', 'cosmic'),
('Old', 'Eclesiastés', 10, 11, 'Muerde la serpiente cuando no está encantada, y el lenguaraz no es mejor.', 'cosmic'),
('Old', 'Eclesiastés', 10, 12, 'Las palabras de la boca del sabio son gracia; mas los labios del necio causan su propia ruina.', 'cosmic'),
('Old', 'Eclesiastés', 10, 13, 'El principio de las palabras de su boca es necedad; y el fin de su charla nocivo desvarío.', 'cosmic'),
('Old', 'Eclesiastés', 10, 14, 'El necio multiplica palabras: no sabe hombre lo que ha de ser; ¿y quién le hará saber lo que después de él será?', 'cosmic'),
('Old', 'Eclesiastés', 10, 15, 'El trabajo de los necios los fatiga; porque no saben por dónde ir á la ciudad.', 'cosmic'),
('Old', 'Eclesiastés', 10, 16, '¡Ay de ti, tierra, cuando tu rey es muchacho, y tus príncipes comen de mañana!', 'cosmic'),
('Old', 'Eclesiastés', 10, 17, '¡Bienaventurada, tú, tierra, cuando tu rey es hijo de nobles, y tus príncipes comen á su hora, por refección, y no por el beber!', 'cosmic'),
('Old', 'Eclesiastés', 10, 18, 'Por la pereza se cae la techumbre, y por flojedad de manos se llueve la casa.', 'cosmic'),
('Old', 'Eclesiastés', 10, 19, 'Por el placer se hace el convite, y el vino alegra los vivos: y el dinero responde á todo.', 'cosmic'),
('Old', 'Eclesiastés', 10, 20, 'Ni aun en tu pensamiento digas mal del rey, ni en los secretos de tu cámara digas mal del rico; porque las aves del cielo llevarán la voz, y las que tienen alas harán saber la palabra.', 'cosmic'),
('Old', 'Eclesiastés', 11, 1, 'ECHA tu pan sobre las aguas; que después de muchos días lo hallarás.', 'cosmic'),
('Old', 'Eclesiastés', 11, 2, 'Reparte á siete, y aun á ocho: porque no sabes el mal que vendrá sobre la tierra.', 'cosmic'),
('Old', 'Eclesiastés', 11, 3, 'Si las nubes fueren llenas de agua, sobre la tierra la derramarán: y si el árbol cayere al mediodía, ó al norte, al lugar que el árbol cayere, allí quedará.', 'cosmic'),
('Old', 'Eclesiastés', 11, 4, 'El que al viento mira, no sembrará; y el que mira á las nubes, no segará.', 'cosmic'),
('Old', 'Eclesiastés', 11, 5, 'Como tú no sabes cuál es el camino del viento, ó como se crían los huesos en el vientre de la mujer preñada, así ignoras la obra de Dios, el cual hace todas las cosas.', 'cosmic'),
('Old', 'Eclesiastés', 11, 6, 'Por la mañana siembra tu simiente, y á la tarde no dejes reposar tu mano: porque tú no sabes cuál es lo mejor, si esto ó lo otro, ó si ambas á dos cosas son buenas.', 'cosmic'),
('Old', 'Eclesiastés', 11, 7, 'Suave ciertamente es la luz, y agradable á los ojos ver el sol:', 'cosmic'),
('Old', 'Eclesiastés', 11, 8, 'Mas si el hombre viviere muchos años, y en todos ellos hubiere gozado alegría; si después trajere á la memoria los días de las tinieblas, que serán muchos, todo lo que le habrá pasado, dirá haber sido vanidad.', 'cosmic'),
('Old', 'Eclesiastés', 11, 9, 'Alégrate, mancebo, en tu mocedad, y tome placer tu corazón en los días de tu juventud; y anda en los caminos de tu corazón, y en la vista de tus ojos: mas sabe, que sobre todas estas cosas te traerá Dios á juicio.', 'cosmic'),
('Old', 'Eclesiastés', 11, 10, 'Quita pues el enojo de tu corazón, y aparta el mal de tu carne: porque la mocedad y la juventud son vainidad.', 'cosmic'),
('Old', 'Eclesiastés', 12, 1, 'Y ACUÉRDATE de tu Criador en los días de tu juventud, antes que vengan los malos días, y lleguen los años, de los cuales digas, No tengo en ellos contentamiento;', 'cosmic'),
('Old', 'Eclesiastés', 12, 2, 'Antes que se oscurezca el sol, y la luz, y la luna y las estrellas, y las nubes se tornen tras la lluvia:', 'cosmic'),
('Old', 'Eclesiastés', 12, 3, 'Cuando temblarán los guardas de la casa, y se encorvarán los hombres fuertes, y cesarán las muelas, porque han disminuído, y se oscurecerán los que miran por las ventanas;', 'cosmic'),
('Old', 'Eclesiastés', 12, 4, 'Y las puertas de afuera se cerrarán, por la bajeza de la voz de la muela; y levantaráse á la voz del ave, y todas las hijas de canción serán humilladas;', 'cosmic'),
('Old', 'Eclesiastés', 12, 5, 'Cuando también temerán de lo alto, y los tropezones en el camino; y florecerá el almendro, y se agravará la langosta, y perderáse el apetito: porque el hombre va á la casa de su siglo, y los endechadores andarán en derredor por la plaza:', 'cosmic'),
('Old', 'Eclesiastés', 12, 6, 'Antes que la cadena de plata se quiebre, y se rompa el cuenco de oro, y el cántaro se quiebre junto á la fuente, y la rueda sea rota sobre el pozo;', 'cosmic'),
('Old', 'Eclesiastés', 12, 7, 'Y el polvo se torne á la tierra, como era, y el espíritu se vuelva á Dios que lo dió.', 'cosmic'),
('Old', 'Eclesiastés', 12, 8, 'Vanidad de vanidades, dijo el Predicador, todo vanidad.', 'cosmic'),
('Old', 'Eclesiastés', 12, 9, 'Y cuanto más sabio fué el Predicador, tanto más enseñó sabiduría al pueblo; é hizo escuchar, é hizo escudriñar, y compuso muchos proverbios.', 'cosmic'),
('Old', 'Eclesiastés', 12, 10, 'Procuró el Predicador hallar palabras agradables, y escritura recta, palabras de verdad.', 'cosmic'),
('Old', 'Eclesiastés', 12, 11, 'Las palabras de los sabios son como aguijones; y como clavos hincados, las de los maestros de las congregaciones, dadas por un Pastor.', 'cosmic'),
('Old', 'Eclesiastés', 12, 12, 'Ahora, hijo mío, á más de esto, sé avisado. No hay fin de hacer muchos libros; y el mucho estudio aflicción es de la carne.', 'cosmic'),
('Old', 'Eclesiastés', 12, 13, 'El fin de todo el discurso oído es este: Teme á Dios, y guarda sus mandamientos; porque esto es el todo del hombre.', 'cosmic'),
('Old', 'Eclesiastés', 12, 14, 'Porque Dios traerá toda obra á juicio, el cual se hará sobre toda cosa oculta, buena ó mala.', 'cosmic'),
('Old', 'Cantares', 1, 1, 'CANCION de canciones, la cual es de Salomón.', 'cosmic'),
('Old', 'Cantares', 1, 2, '¡Oh si él me besara con ósculos de su boca! Porque mejores son tus amores que el vino.', 'cosmic'),
('Old', 'Cantares', 1, 3, 'Por el olor de tus suaves unguüentos, (Ungüento derramado es tu nombre,) Por eso las doncellas te amaron.', 'cosmic'),
('Old', 'Cantares', 1, 4, 'Llévame en pos de ti, correremos. Metióme el rey en sus cámaras: Nos gozaremos y alegraremos en ti; Acordarémonos de tus amores más que del vino: Los rectos te aman.', 'cosmic'),
('Old', 'Cantares', 1, 5, 'Morena soy, oh hijas de Jerusalem, Mas codiciable; Como las cabañas de Cedar, Como las tiendas de Salomón.', 'cosmic'),
('Old', 'Cantares', 1, 6, 'No miréis en que soy morena, Porque el sol me miró. Los hijos de mi madre se airaron contra mí, Hiciéronme guarda de viñas; Y mi viña, que era mía, no guardé.', 'cosmic'),
('Old', 'Cantares', 1, 7, 'Hazme saber, ó tú á quien ama mi alma, Dónde repastas, dónde haces tener majada al medio día: Porque, ¿por qué había yo de estar como vagueando Tras los rebaños de tus compañeros?', 'cosmic'),
('Old', 'Cantares', 1, 8, 'Si tú no lo sabes, oh hermosa entre las mujeres, Sal, yéndote por las huellas del rebaño, Y apacienta tus cabritas junto á las cabañas de los pastores.', 'cosmic'),
('Old', 'Cantares', 1, 9, 'A yegua de los carros de Faraón Te he comparado, amiga mía.', 'cosmic'),
('Old', 'Cantares', 1, 10, 'Hermosas son tus mejillas entre los pendientes, Tu cuello entre los collares.', 'cosmic'),
('Old', 'Cantares', 1, 11, 'Zarcillos de oro te haremos, Con clavos de plata.', 'cosmic'),
('Old', 'Cantares', 1, 12, 'Mientras que el rey estaba en su reclinatorio, Mi nardo dió su olor.', 'cosmic'),
('Old', 'Cantares', 1, 13, 'Mi amado es para mí un manojito de mirra, Que reposa entre mis pechos.', 'cosmic'),
('Old', 'Cantares', 1, 14, 'Racimo de copher en las viñas de Engadi Es para mí mi amado.', 'cosmic'),
('Old', 'Cantares', 1, 15, 'He aquí que tú eres hermosa, amiga mía; He aquí que eres bella: tus ojos de paloma.', 'cosmic'),
('Old', 'Cantares', 1, 16, 'He aquí que tú eres hermoso, amado mío, y suave: Nuestro lecho también florido.', 'cosmic'),
('Old', 'Cantares', 1, 17, 'Las vigas de nuestra casa son de cedro, Y de ciprés los artesonados.', 'cosmic'),
('Old', 'Cantares', 2, 1, 'YO soy la rosa de Sarón, Y el lirio de los valles.', 'cosmic'),
('Old', 'Cantares', 2, 2, 'Como el lirio entre las espinas, Así es mi amiga entre las doncellas.', 'cosmic'),
('Old', 'Cantares', 2, 3, 'Como el manzano entre los árboles silvestres, Así es mi amado entre los mancebos: Bajo la sombra del deseado me senté, Y su fruto fué dulce en mi paladar.', 'cosmic'),
('Old', 'Cantares', 2, 4, 'Llevóme á la cámara del vino, Y su bandera sobre mí fué amor.', 'cosmic'),
('Old', 'Cantares', 2, 5, 'Sustentadme con frascos, corroboradme con manzanas; Porque estoy enferma de amor.', 'cosmic'),
('Old', 'Cantares', 2, 6, 'Su izquierda esté debajo de mi cabeza, Y su derecha me abrace.', 'cosmic'),
('Old', 'Cantares', 2, 7, 'Yo os conjuro, oh doncellas de Jerusalem, Por las gamas y por las ciervas del campo, Que no despertéis ni hagáis velar al amor Hasta que quiera.', 'cosmic'),
('Old', 'Cantares', 2, 8, '¡La voz de mi amado! He aquí él viene Saltando sobre los montes, brincando sobre los collados.', 'cosmic'),
('Old', 'Cantares', 2, 9, 'Mi amado es semejante al gamo, ó al cabrito de los ciervos. Helo aquí, está tras nuestra pared, Mirando por las ventanas, Mostrándose por las rejas.', 'cosmic'),
('Old', 'Cantares', 2, 10, 'Mi amado habló, y me dijo: Levántate, oh amiga mía, hermosa mía, y vente.', 'cosmic'),
('Old', 'Cantares', 2, 11, 'Porque he aquí ha pasado el invierno, Hase mudado, la lluvia se fué;', 'cosmic'),
('Old', 'Cantares', 2, 12, 'Hanse mostrado las flores en la tierra, El tiempo de la canción es venido, Y en nuestro país se ha oído la voz de la tórtola;', 'cosmic'),
('Old', 'Cantares', 2, 13, 'La higuera ha echado sus higos, Y las vides en cierne Dieron olor: Levántate, oh amiga mía, hermosa mía, y vente.', 'cosmic'),
('Old', 'Cantares', 2, 14, 'Paloma mía, que estás en los agujeros de la peña, en lo escondido de escarpados parajes, Muéstrame tu rostro, hazme oir tu voz; Porque dulce es la voz tuya, y hermoso tu aspecto.', 'cosmic'),
('Old', 'Cantares', 2, 15, 'Cazadnos las zorra, las zorras pequeñas, que echan á perder las viñas; Pues que nuestras viñas están en cierne.', 'cosmic'),
('Old', 'Cantares', 2, 16, 'Mi amado es mío, y yo suya; El apacienta entre lirios.', 'cosmic'),
('Old', 'Cantares', 2, 17, 'Hasta que apunte el día, y huyan las sombras, Tórnate, amado mío; sé semejante al gamo, ó al cabrito de los ciervos, Sobre los montes de Bether.', 'cosmic'),
('Old', 'Cantares', 3, 1, 'POR las noches busqué en mi lecho al que ama mi alma: Busquélo, y no lo hallé.', 'cosmic'),
('Old', 'Cantares', 3, 2, 'Levantaréme ahora, y rodearé por la ciudad; Por las calles y por las plazas Buscaré al que ama mi alma: Busquélo, y no lo hallé.', 'cosmic'),
('Old', 'Cantares', 3, 3, 'Halláronme los guardas que rondan la ciudad, Y díjeles: ¿Habéis visto al que ama mi alma?', 'cosmic'),
('Old', 'Cantares', 3, 4, 'Pasando de ellos un poco, Hallé luego al que mi alma ama: Trabé de él, y no lo dejé, Hasta que lo metí en casa de mi madre, Y en la cámara de la que me engendró.', 'cosmic'),
('Old', 'Cantares', 3, 5, 'Yo os conjuro, oh doncellas de Jerusalem, Por las gamas y por las ciervas del campo, Que no despertéis ni hagáis velar al amor, Hasta que quiera.', 'cosmic'),
('Old', 'Cantares', 3, 6, '¿Quién es ésta que sube del desierto como columnita de humo, Sahumada de mirra y de incienso, Y de todos polvos aromáticos?', 'cosmic'),
('Old', 'Cantares', 3, 7, 'He aquí es la litera de Salomón: Sesenta valientes la rodean, De los fuertes de Israel.', 'cosmic'),
('Old', 'Cantares', 3, 8, 'Todos ellos tienen espadas, diestros en la guerra; Cada uno su espada sobre su muslo, Por los temores de la noche.', 'cosmic'),
('Old', 'Cantares', 3, 9, 'El rey Salomón se hizo una carroza De madera del Líbano.', 'cosmic'),
('Old', 'Cantares', 3, 10, 'Sus columnas hizo de plata, Su respaldo de oro, su cielo de grana, Su interior enlosado de amor, Por las doncellas de Jerusalem.', 'cosmic'),
('Old', 'Cantares', 3, 11, 'Salid, oh doncellas de Sión, y ved al rey Salomón Con la corona con que le coronó su madre el día de su desposorio, Y el día del gozo de su corazón.', 'cosmic'),
('Old', 'Cantares', 4, 1, 'HE aquí que tú eres hermosa, amiga mía, he aquí que tú eres hermosa; Tus ojos entre tus guedejas como de paloma; Tus cabellos como manada de cabras, Que se muestran desde el monte de Galaad.', 'cosmic'),
('Old', 'Cantares', 4, 2, 'Tus dientes, como manadas de trasquiladas ovejas, Que suben del lavadero, Todas con crías mellizas, Y ninguna entre ellas estéril.', 'cosmic'),
('Old', 'Cantares', 4, 3, 'Tus labios, como un hilo de grana, Y tu habla hermosa; Tus sienes, como cachos de granada á la parte adentro de tus guedejas.', 'cosmic'),
('Old', 'Cantares', 4, 4, 'Tu cuello, como la torre de David, edificada para muestra; Mil escudos están colgados de ella, Todos escudos de valientes.', 'cosmic'),
('Old', 'Cantares', 4, 5, 'Tus dos pechos, como dos cabritos mellizos de gama, Que son apacentados entre azucenas.', 'cosmic'),
('Old', 'Cantares', 4, 6, 'Hasta que apunte el día y huyan las sombras, Iréme al monte de la mirra, Y al collado del incienso.', 'cosmic'),
('Old', 'Cantares', 4, 7, 'Toda tú eres hermosa, amiga mía Y en ti no hay mancha.', 'cosmic'),
('Old', 'Cantares', 4, 8, 'Conmigo del Líbano, oh esposa, Conmigo ven del Líbano: Mira desde la cumbre de Amana, Desde la cumbre de Senir y de Hermón, Desde las guaridas de los leones, Desde los montes de los tigres.', 'cosmic'),
('Old', 'Cantares', 4, 9, 'Prendiste mi corazón, hermana, esposa mía; Has preso mi corazón con uno de tus ojos, Con una gargantilla de tu cuello.', 'cosmic'),
('Old', 'Cantares', 4, 10, '¡Cuán hermosos son tus amores, hermana, esposa mía! ¡Cuánto mejores que el vino tus amores, Y el olor de tus ungüentos que todas las especias aromáticas!', 'cosmic'),
('Old', 'Cantares', 4, 11, 'Como panal de miel destilan tus labios, oh esposa; Miel y leche hay debajo de tu lengua; Y el olor de tus vestidos como el olor del Líbano.', 'cosmic'),
('Old', 'Cantares', 4, 12, 'Huerto cerrado eres, mi hermana, esposa mía; Fuente cerrada, fuente sellada.', 'cosmic'),
('Old', 'Cantares', 4, 13, 'Tus renuevos paraíso de granados, con frutos suaves, De cámphoras y nardos,', 'cosmic'),
('Old', 'Cantares', 4, 14, 'Nardo y azafrán, Caña aromática y canela, con todos los árboles de incienso; Mirra y áloes, con todas las principales especias.', 'cosmic'),
('Old', 'Cantares', 4, 15, 'Fuente de huertos, Pozo de aguas vivas, Que corren del Líbano.', 'cosmic'),
('Old', 'Cantares', 4, 16, 'Levántate, Aquilón, y ven, Austro: Sopla mi huerto, despréndanse sus aromas. Venga mi amado á su huerto, Y coma de su dulce fruta.', 'cosmic'),
('Old', 'Cantares', 5, 1, 'YO vine á mi huerto, oh hermana, esposa mía: Cogido he mi mirra y mis aromas; He comido mi panal y mi miel, Mi vino y mi leche he bebido. Comed, amigos; Babed, amados, y embriagaos.', 'cosmic'),
('Old', 'Cantares', 5, 2, 'Yo dormía, pero mi corazón velaba: La voz de mi amado que llamaba: Abreme, hermana mía, amiga mía, paloma mía, perfecta mía; Porque mi cabeza está llena de rocío, Mis cabellos de las gotas de la noche.', 'cosmic'),
('Old', 'Cantares', 5, 3, 'Heme desnudado mi ropa; ¿cómo la tengo de vestir? He lavado mis pies; ¿cómo los tengo de ensuciar?', 'cosmic'),
('Old', 'Cantares', 5, 4, 'Mi amado metió su mano por el agujero, Y mis entrañas se conmovieron dentro de mí.', 'cosmic'),
('Old', 'Cantares', 5, 5, 'Yo me levanté para abrir á mi amado, Y mis manos gotearon mirra, Y mis dedos mirra que corría Sobre las aldabas del candado.', 'cosmic'),
('Old', 'Cantares', 5, 6, 'Abrí yo á mi amado; Mas mi amado se había ido, había ya pasado: Y tras su hablar salió mi alma: Busquélo, y no lo hallé; Llamélo, y no me respondió.', 'cosmic'),
('Old', 'Cantares', 5, 7, 'Halláronme los guardas que rondan la ciudad: Hiriéronme, llagáronme, Quitáronme mi manto de encima los guardas de los muros.', 'cosmic'),
('Old', 'Cantares', 5, 8, 'Yo os conjuro, oh doncellas de Jerusalem, si hallareis á mi amado, Que le hagáis saber cómo de amor estoy enferma.', 'cosmic'),
('Old', 'Cantares', 5, 9, '¿Qué es tu amado más que otro amado, Oh la más hermosa de todas las mujeres? ¿Qué es tu amado más que otro amado, Que así nos conjuras?', 'cosmic'),
('Old', 'Cantares', 5, 10, 'Mi amado es blanco y rubio, Señalado entre diez mil.', 'cosmic'),
('Old', 'Cantares', 5, 11, 'Su cabeza, como, oro finísimo; Sus cabellos crespos, negros como el cuervo.', 'cosmic'),
('Old', 'Cantares', 5, 12, 'Sus ojos, como palomas junto á los arroyos de las aguas, Que se lavan con leche, y a la perfección colocados.', 'cosmic'),
('Old', 'Cantares', 5, 13, 'Sus mejillas, como una era de especias aromáticas, como fragantes flores: Sus labios, como lirios que destilan mirra que trasciende.', 'cosmic'),
('Old', 'Cantares', 5, 14, 'Sus manos, como anillos de oro engastados de jacintos: Su vientre, como claro marfil cubierto de zafiros.', 'cosmic'),
('Old', 'Cantares', 5, 15, 'Sus piernas, como columnas de mármol fundadas sobre basas de fino oro: Su aspecto como el Líbano, escogido como los cedros.', 'cosmic'),
('Old', 'Cantares', 5, 16, 'Su paladar, dulcísimo: y todo él codiciable. Tal es mi amado, tal es mi amigo, Oh doncellas de Jerusalem.', 'cosmic'),
('Old', 'Cantares', 6, 1, '¿DONDE se ha ido tu amado, Oh la más hermosa de todas las mujeres? ¿Adónde se apartó tu amado, Y le buscaremos contigo?', 'cosmic'),
('Old', 'Cantares', 6, 2, 'Mi amado descendió á su huerto, á las eras de los aromas Para apacentar en los huertos, y para coger los lirios.', 'cosmic'),
('Old', 'Cantares', 6, 3, 'Yo soy de mi amado, y mi amado es mío: El apacienta entre los lirios.', 'cosmic'),
('Old', 'Cantares', 6, 4, 'Hermosa eres tú, oh amiga mía, como Tirsa; De desear, como Jerusalem; Imponente como ejércitos en orden.', 'cosmic'),
('Old', 'Cantares', 6, 5, 'Aparta tus ojos de delante de mí, Porque ellos me vencieron. Tu cabello es como manada de cabras, Que se muestran en Galaad.', 'cosmic'),
('Old', 'Cantares', 6, 6, 'Tus dientes, como manada de ovejas Que suben del lavadero, Todas con crías mellizas, Y estéril no hay entre ellas.', 'cosmic'),
('Old', 'Cantares', 6, 7, 'Como cachos de granada son tus sienes Entre tus guedejas.', 'cosmic'),
('Old', 'Cantares', 6, 8, 'Sesenta son las reinas, y ochenta las concubinas, Y las doncellas sin cuento:', 'cosmic'),
('Old', 'Cantares', 6, 9, 'Mas una es la paloma mía, la perfecta mía; Unica es á su madre, Escogida á la que la engendró. Viéronla las doncellas, y llamáronla bienaventurada; Las reinas y las concubinas, y la alabaron.', 'cosmic'),
('Old', 'Cantares', 6, 10, '¿Quién es ésta que se muestra como el alba, Hermosa como la luna, Esclarecida como el sol, Imponente como ejércitos en orden?', 'cosmic'),
('Old', 'Cantares', 6, 11, 'Al huerto de los nogales descendí A ver los frutos del valle, Y para ver si brotaban las vides, Si florecían los granados.', 'cosmic'),
('Old', 'Cantares', 6, 12, 'No lo supe: hame mi alma hecho Como los carros de Amminadab.', 'cosmic'),
('Old', 'Cantares', 6, 13, 'Tórnate, tórnate, oh Sulamita; Tórnate, tórnate, y te miraremos. ¿Qué veréis en la Sulamita? Como la reunión de dos campamentos.', 'cosmic'),
('Old', 'Cantares', 7, 1, '¡CUAN hermosos son tus pies en los calzados, oh hija de príncipe! Los contornos de tus muslos son como joyas, Obra de mano de excelente maestro.', 'cosmic'),
('Old', 'Cantares', 7, 2, 'Tu ombligo, como una taza redonda, Que no le falta bebida. Tu vientre, como montón de trigo, Cercado de lirios.', 'cosmic'),
('Old', 'Cantares', 7, 3, 'Tus dos pechos, como dos cabritos Mellizos de gama.', 'cosmic'),
('Old', 'Cantares', 7, 4, 'Tu cuello, como torre de marfil; Tus ojos, como las pesqueras de Hesbón junto á la puerta de Bat-rabbim; Tu nariz, como la torre del Líbano, Que mira hacia Damasco.', 'cosmic'),
('Old', 'Cantares', 7, 5, 'Tu cabeza encima de ti, como el Carmelo; Y el cabello de tu cabeza, como la púrpura del rey Ligada en los corredores.', 'cosmic'),
('Old', 'Cantares', 7, 6, '¡Qué hermosa eres, y cuán suave, Oh amor deleitoso!', 'cosmic'),
('Old', 'Cantares', 7, 7, 'Y tu estatura es semejante á la palma, Y tus pechos á los racimos!', 'cosmic'),
('Old', 'Cantares', 7, 8, 'Yo dije: Subiré á la palma, Asiré sus ramos: Y tus pechos serán ahora como racimos de vid, Y el olor de tu boca como de manzanas;', 'cosmic'),
('Old', 'Cantares', 7, 9, 'Y tu paladar como el buen vino, Que se entra á mi amado suavemente, Y hace hablar los labios de los viejos.', 'cosmic'),
('Old', 'Cantares', 7, 10, 'Yo soy de mi amado, Y conmigo tiene su contentamiento.', 'cosmic'),
('Old', 'Cantares', 7, 11, 'Ven, oh amado mío, salgamos al campo, Moremos en las aldeas.', 'cosmic'),
('Old', 'Cantares', 7, 12, 'Levantémonos de mañana á las viñas; Veamos si brotan las vides, si se abre el cierne, Si han florecido los granados; Allí te daré mis amores.', 'cosmic'),
('Old', 'Cantares', 7, 13, 'Las mandrágoras han dado olor, Y á nuestras puertas hay toda suerte de dulces frutas, nuevas y añejas. Que para ti, oh amado mío, he guardado.', 'cosmic'),
('Old', 'Cantares', 8, 1, '¡OH quién te me diese como hermano Que mamó los pechos de mi madre; De modo que te halle yo fuera, y te bese, Y no me menosprecien!', 'cosmic'),
('Old', 'Cantares', 8, 2, 'Yo te llevaría, te metiera en casa de mi madre: Tú me enseñarías, Y yo te hiciera beber vino Adobado del mosto de mis granadas.', 'cosmic'),
('Old', 'Cantares', 8, 3, 'Su izquierda esté debajo de mi cabeza, Y su derecha me abrace.', 'cosmic'),
('Old', 'Cantares', 8, 4, 'Conjúroos, oh doncellas de Jerusalem, Que no despertéis, ni hagáis velar al amor, Hasta que quiera.', 'cosmic'),
('Old', 'Cantares', 8, 5, '¿Quién es ésta que sube del desierto, Recostada sobre su amado? Debajo de un manzano te desperté: Allí tuvo tu madre dolores, Allí tuvo dolores la que te parió.', 'cosmic'),
('Old', 'Cantares', 8, 6, 'Ponme como un sello sobre tu corazón, como una marca sobre tu brazo: Porque fuerte es como la muerte el amor; Duro como el sepulcro el celo: Sus brasas, brasas de fuego, Fuerte llama.', 'cosmic'),
('Old', 'Cantares', 8, 7, 'Las muchas aguas no podrán apagar el amor, Ni lo ahogarán los ríos. Si diese el hombre toda la hacienda de su casa por este amor, De cierto lo menospreciaran.', 'cosmic'),
('Old', 'Cantares', 8, 8, 'Tenemos una pequeña hermana, Que no tiene pechos: ¿Qué haremos á nuestra hermana Cuando de ella se hablare?', 'cosmic'),
('Old', 'Cantares', 8, 9, 'Si ella es muro, Edificaremos sobre él un palacio de plata: Y si fuere puerta, La guarneceremos con tablas de cedro.', 'cosmic'),
('Old', 'Cantares', 8, 10, 'Yo soy muro, y mis pechos como torres, Desde que fuí en sus ojos como la que halla paz.', 'cosmic'),
('Old', 'Cantares', 8, 11, 'Salomón tuvo una viña en Baal-hamón, La cual entregó á guardas, Cada uno de los cuales debía traer mil monedas de plata por su fruto.', 'cosmic'),
('Old', 'Cantares', 8, 12, 'Mi viña, que es mía, está delante de mí: Las mil serán tuyas, oh Salomón, Y doscientas, de los que guardan su fruto.', 'cosmic'),
('Old', 'Cantares', 8, 13, 'Oh tú la que moras en los huertos, Los compañeros escuchan tu voz: Házmela oir.', 'cosmic'),
('Old', 'Cantares', 8, 14, 'Huye, amado mío; Y sé semejante al gamo, ó al cervatillo, Sobre las montañas de los aromas.', 'cosmic'),
('Old', 'Isaías', 1, 1, 'VISION de Isaías hijo de Amoz, la cual vió sobre Judá y Jerusalem, en días de Uzzías, Jotham, Achâz y Ezechîas, reyes de Judá.', 'deep-space'),
('Old', 'Isaías', 1, 2, 'Oid, cielos, y escucha tú, tierra; porque habla Jehová: Crié hijos, y engrandecílos, y ellos se rebelaron contra mí.', 'deep-space'),
('Old', 'Isaías', 1, 3, 'El buey conoce á su dueño, y el asno el pesebre de su señor: Israel no conoce, mi pueblo no tiene entendimiento.', 'deep-space'),
('Old', 'Isaías', 1, 4, '¡Oh gente pecadora, pueblo cargado de maldad, generación de malignos, hijos depravados! Dejaron á Jehová, provocaron á ira al Santo de Israel, tornáronse atrás.', 'deep-space'),
('Old', 'Isaías', 1, 5, '¿Para qué habéis de ser castigados aún? todavía os rebelaréis. Toda cabeza está enferma, y todo corazón doliente.', 'deep-space'),
('Old', 'Isaías', 1, 6, 'Desde la planta del pie hasta la cabeza no hay en él cosa ilesa, sino herida, hinchazón y podrida llaga: no están curadas, ni vendadas, ni suavizadas con aceite.', 'deep-space'),
('Old', 'Isaías', 1, 7, 'Vuestra tierra está destruída, vuestras ciudades puestas á fuego, vuestra tierra delante de vosotros comida de extranjeros, y asolada como asolamiento de extraños.', 'deep-space'),
('Old', 'Isaías', 1, 8, 'Y queda la hija de Sión como choza en viña, y como cabaña en melonar, como ciudad asolada.', 'deep-space'),
('Old', 'Isaías', 1, 9, 'Si Jehová de los ejércitos no hubiera hecho que nos quedasen muy cortos residuos, como Sodoma fuéramos, y semejantes á Gomorra.', 'deep-space'),
('Old', 'Isaías', 1, 10, 'Príncipes de Sodoma, oid la palabra de Jehová; escuchad la ley de nuestro Dios, pueblo de Gomorra.', 'deep-space'),
('Old', 'Isaías', 1, 11, '¿Para qué á mí, dice Jehová, la multitud de vuestros sacrificios? Harto estoy de holocaustos de carneros, y de sebo de animales gruesos: no quiero sangre de bueyes, ni de ovejas, ni de machos cabríos.', 'deep-space'),
('Old', 'Isaías', 1, 12, '¿Quién demandó esto de vuestras manos, cuando vinieseis á presentaros delante de mí, para hollar mis atrios?', 'deep-space'),
('Old', 'Isaías', 1, 13, 'No me traigáis más vano presente: el perfume me es abominación: luna nueva y sábado, el convocar asambleas, no las puedo sufrir: son iniquidad vuestras solemnidades.', 'deep-space'),
('Old', 'Isaías', 1, 14, 'Vuestras lunas nuevas y vuestras solemnidades tiene aborrecidas mi alma: me son gravosas; cansado estoy de llevarlas.', 'deep-space'),
('Old', 'Isaías', 1, 15, 'Cuando extendiereis vuestras manos, yo esconderé de vosotros mis ojos: asimismo cuando multiplicareis la oración, yo no oiré: llenas están de sangre vuestras manos.', 'deep-space'),
('Old', 'Isaías', 1, 16, 'Lavad, limpiaos; quitad la iniquidad de vuestras obras de ante mis ojos; dejad de hacer lo malo:', 'deep-space'),
('Old', 'Isaías', 1, 17, 'Aprended á hacer bien: buscad juicio, restituid al agraviado, oid en derecho al huérfano, amparad á la viuda.', 'deep-space'),
('Old', 'Isaías', 1, 18, 'Venid luego, dirá Jehová, y estemos á cuenta: si vuestros pecados fueren como la grana, como la nieve serán emblanquecidos: si fueren rojos como el carmesí, vendrán á ser como blanca lana.', 'deep-space'),
('Old', 'Isaías', 1, 19, 'Si quisiereis y oyereis, comieréis el bien de la tierra:', 'deep-space'),
('Old', 'Isaías', 1, 20, 'Si no quisiereis y fuereis rebeldes, seréis consumidos á espada: porque la boca de Jehová lo ha dicho.', 'deep-space'),
('Old', 'Isaías', 1, 21, '¿Cómo te has tornado ramera, oh ciudad fiel? Llena estuvo de juicio, en ella habitó equidad; mas ahora, homicidas.', 'deep-space'),
('Old', 'Isaías', 1, 22, 'Tu plata se ha tornado escorias, tu vino mezclado está con agua.', 'deep-space'),
('Old', 'Isaías', 1, 23, 'Tus príncipes, prevaricadores y compañeros de ladrones: todos aman las dádivas, y van tras las recompensas: no oyen en juicio al huérfano, ni llega a ellos la causa de la viuda.', 'deep-space'),
('Old', 'Isaías', 1, 24, 'Por tanto, dice el Señor Jehová de los ejércitos, el Fuerte de Israel: Ea, tomaré satisfacción de mis enemigos, vengaréme de mis adversarios:', 'deep-space'),
('Old', 'Isaías', 1, 25, 'Y volveré mi mano sobre ti, y limpiaré hasta lo más puro tus escorias, y quitaré todo tu estaño:', 'deep-space'),
('Old', 'Isaías', 1, 26, 'Y restituiré tus jueces como al principio, y tus consejeros como de primero: entonces te llamarán Ciudad de justicia, Ciudad fiel.', 'deep-space'),
('Old', 'Isaías', 1, 27, 'Sión con juicio será rescatada, y los convertidos de ella con justicia.', 'deep-space'),
('Old', 'Isaías', 1, 28, 'Mas los rebeldes y pecadores á una serán quebrantados, y los que dejan á Jehová serán consumidos.', 'deep-space'),
('Old', 'Isaías', 1, 29, 'Entonces os avergonzarán los olmos que amasteis, y os afrentarán los bosques que escogisteis.', 'deep-space'),
('Old', 'Isaías', 1, 30, 'Porque seréis como el olmo que se le cae la hoja, y como huerto que le faltan las aguas.', 'deep-space'),
('Old', 'Isaías', 1, 31, 'Y el fuerte será como estopa, y lo que hizo como centella; y ambos serán encendidos juntamente, y no habrá quien apague.', 'deep-space'),
('Old', 'Isaías', 2, 1, 'LO que vió Isaías, hijo de Amoz, tocante á Judá y a Jerusalem.', 'deep-space'),
('Old', 'Isaías', 2, 2, 'Y acontecerá en lo postrero de los tiempos, que será confirmado el monte de la casa de Jehová por cabeza de los montes, y será ensalzado sobre los collados, y correrán á él todas las gentes.', 'deep-space'),
('Old', 'Isaías', 2, 3, 'Y vendrán muchos pueblos, y dirán: Venid, y subamos al monte de Jehová, á la casa del Dios de Jacob; y nos enseñará en sus caminos, y caminaremos por sus sendas. Porque de Sión saldrá la ley, y de Jerusalem la palabra de Jehová.', 'deep-space'),
('Old', 'Isaías', 2, 4, 'Y juzgará entre las gentes, y reprenderá á muchos pueblos; y volverán sus espadas en rejas de arado, y sus lanzas en hoces: no alzará espada gente contra gente, ni se ensayarán más para la guerra.', 'deep-space'),
('Old', 'Isaías', 2, 5, 'Venid, oh casa de Jacob, y caminemos á la luz de Jehová.', 'deep-space'),
('Old', 'Isaías', 2, 6, 'Ciertamente tú has dejado tu pueblo, la casa de Jacob, porque son henchidos de oriente, y de agoreros, como los Filisteos; y en hijos ajenos descansan.', 'deep-space'),
('Old', 'Isaías', 2, 7, 'Su tierra está llena de plata y oro, sus tesoros no tienen fin. También está su tierra llena de caballos; ni sus carros tienen número.', 'deep-space'),
('Old', 'Isaías', 2, 8, 'Además está su tierra llena de ídolos, y á la obra de sus manos se han arrodillado, á lo que fabricaron sus dedos.', 'deep-space'),
('Old', 'Isaías', 2, 9, 'Y hase inclinado el hombre, y el varón se ha humillado: por tanto no los perdonarás.', 'deep-space'),
('Old', 'Isaías', 2, 10, 'Métete en la piedra, escóndete en el polvo, de la presencia espantosa de Jehová y del resplandor de su majestad.', 'deep-space'),
('Old', 'Isaías', 2, 11, 'La altivez de los ojos del hombre será abatida, y la soberbia de los hombres será humillada; y Jehová solo será ensalzado en aquel día.', 'deep-space'),
('Old', 'Isaías', 2, 12, 'Porque día de Jehová de los ejércitos vendrá sobre todo soberbio y altivo, y sobre todo ensalzado; y será abatido:', 'deep-space'),
('Old', 'Isaías', 2, 13, 'Y sobre todos los cedros del Líbano altos y sublimes, y sobre todos los alcornoques de Basán;', 'deep-space'),
('Old', 'Isaías', 2, 14, 'Y sobre todos los montes altos, y sobre todos los collados levantados;', 'deep-space'),
('Old', 'Isaías', 2, 15, 'Y sobre toda torre alta, y sobre todo muro fuerte;', 'deep-space'),
('Old', 'Isaías', 2, 16, 'Y sobre todas las naves de Tarsis, y sobre todas pinturas preciadas.', 'deep-space'),
('Old', 'Isaías', 2, 17, 'Y la altivez del hombre será abatida, y la soberbia de los hombres será humillada; y solo Jehová será ensalzado en aquel día.', 'deep-space'),
('Old', 'Isaías', 2, 18, 'Y quitará totalmente los ídolos.', 'deep-space'),
('Old', 'Isaías', 2, 19, 'Y meteránse en las cavernas de las peñas, y en las aberturas de la tierra, por la presencia espantosa de Jehová, y por el resplandor de su majestad, cuando se levantare él para herir la tierra.', 'deep-space'),
('Old', 'Isaías', 2, 20, 'Aquel día arrojará el hombre, á los topos y murciélagos, sus ídolos de plata y sus ídolos de oro, que le hicieron para que adorase;', 'deep-space'),
('Old', 'Isaías', 2, 21, 'Y se entrarán en las hendiduras de las rocas y en las cavernas de las peñas, por la presencia formidable de Jehová, y por el resplandor de su majestad, cuando se levantare para herir la tierra.', 'deep-space'),
('Old', 'Isaías', 2, 22, 'Dejaos del hombre, cuyo hálito está en su nariz; porque ¿de qué es él estimado?', 'deep-space'),
('Old', 'Isaías', 3, 1, 'PORQUE he aquí que el Señor Jehová de los ejércitos quita de Jerusalem y de Judá el sustentador y el fuerte, todo sustento de pan y todo socorro de agua;', 'deep-space'),
('Old', 'Isaías', 3, 2, 'El valiente y el hombre de guerra, el juez y el profeta, el adivino y el anciano;', 'deep-space'),
('Old', 'Isaías', 3, 3, 'El capitán de cincuenta, y el hombre de respeto, y el consejero, y el artífice excelente, y el hábil orador.', 'deep-space'),
('Old', 'Isaías', 3, 4, 'Y pondréles mozos por príncipes, y muchachos serán sus señores.', 'deep-space'),
('Old', 'Isaías', 3, 5, 'Y el pueblo hará violencia los unos á los otros, cada cual contra su vecino: el mozo se levantará contra el viejo, y el villano contra el noble.', 'deep-space'),
('Old', 'Isaías', 3, 6, 'Cuando alguno trabare de su hermano, de la familia de su padre, y le dijere, Que vestir tienes, tú serás nuestro príncipe, y sea en tu mano esta ruina;', 'deep-space'),
('Old', 'Isaías', 3, 7, 'El jurará aquel día, diciendo: No tomaré ese cuidado; porque en mi casa ni hay pan, ni qué vestir: no me hagáis príncipe del pueblo.', 'deep-space'),
('Old', 'Isaías', 3, 8, 'Pues arruinada está Jerusalem, y Judá ha caído; porque la lengua de ellos y sus obras han sido contra Jehová, para irritar los ojos de su majestad.', 'deep-space'),
('Old', 'Isaías', 3, 9, 'La apariencia del rostro de ellos los convence: que como Sodoma predican su pecado, no lo disimulan. ¡Ay del alma de ellos! porque allegaron mal para sí.', 'deep-space'),
('Old', 'Isaías', 3, 10, 'Decid al justo que le irá bien: porque comerá de los frutos de sus manos.', 'deep-space'),
('Old', 'Isaías', 3, 11, '¡Ay del impío! mal le irá: porque según las obras de sus manos le será pagado.', 'deep-space'),
('Old', 'Isaías', 3, 12, 'Los exactores de mi pueblo son muchachos, y mujeres se enseñorearon de él. Pueblo mío, los que te guían te engañan, y tuercen la carrera de tus caminos.', 'deep-space'),
('Old', 'Isaías', 3, 13, 'Jehová está en pie para litigar, y está para juzgar los pueblos.', 'deep-space'),
('Old', 'Isaías', 3, 14, 'Jehová vendrá á juicio contra los ancianos de su pueblo y contra sus príncipes; porque vosotros habéis devorado la viña, y el despojo del pobre está en vuestras casas.', 'deep-space'),
('Old', 'Isaías', 3, 15, '¿Qué pensáis vosotros que majáis mi pueblo, y moléis las caras de los pobres? dice el Señor Jehová de los ejércitos.', 'deep-space'),
('Old', 'Isaías', 3, 16, 'Asimismo dice Jehová: Por cuanto las hijas de Sión se ensoberbecen, y andan cuellierguidas y los ojos descompuestos; cuando andan van danzando, y haciendo son con los pies:', 'deep-space'),
('Old', 'Isaías', 3, 17, 'Por tanto, pelará el Señor la mollera de las hijas de Sión, y Jehová descubrirá sus vergüenzas.', 'deep-space'),
('Old', 'Isaías', 3, 18, 'Aquel día quitará el Señor el atavío de los calzados, y las redecillas, y las lunetas;', 'deep-space'),
('Old', 'Isaías', 3, 19, 'Los collares, y los joyeles, y los brazaletes;', 'deep-space'),
('Old', 'Isaías', 3, 20, 'Las escofietas, y los atavíos de las piernas, los partidores del pelo, los pomitos de olor, y los zarcillos;', 'deep-space'),
('Old', 'Isaías', 3, 21, 'Los anillos, y los joyeles de las narices;', 'deep-space'),
('Old', 'Isaías', 3, 22, 'Las ropas de remuda, los mantoncillos, los velos, y los alfileres;', 'deep-space'),
('Old', 'Isaías', 3, 23, 'Los espejos, los pañizuelos, las gasas, y los tocados.', 'deep-space'),
('Old', 'Isaías', 3, 24, 'Y será que en lugar de los perfumes aromáticos vendrá hediondez; y desgarrón en lugar de cinta; y calvez en lugar de la compostura del cabello; y en lugar de faja ceñimiento de saco; y quemadura en vez de hermosura.', 'deep-space'),
('Old', 'Isaías', 3, 25, 'Tus varones caerán á cuchillo, y tu fuerza en la guerra.', 'deep-space'),
('Old', 'Isaías', 3, 26, 'Sus puertas se entristecerán y enlutarán, y ella, desamparada, sentaráse en tierra.', 'deep-space'),
('Old', 'Isaías', 4, 1, 'Y ECHARAN mano de un hombre siete mujeres en aquel tiempo, diciendo: Nosotras comeremos de nuestro pan, y nos vestiremos de nuestras ropas; solamente sea llamado tu nombre sobre nosotras, quita nuestro oprobio.', 'deep-space'),
('Old', 'Isaías', 4, 2, 'En aquel tiempo el renuevo de Jehová será para hermosura y gloria, y el fruto de la tierra para grandeza y honra, á los librados de Israel.', 'deep-space'),
('Old', 'Isaías', 4, 3, 'Y acontecerá que el que quedare en Sión, y el que fuere dejado en Jerusalem, será llamado santo; todos los que en Jerusalem están escritos entre los vivientes;', 'deep-space'),
('Old', 'Isaías', 4, 4, 'Cuando el Señor lavare las inmundicias de las hijas de Sión, y limpiare las sangres de Jerusalem de en medio de ella, con espíritu de juicio y con espíritu de ardimiento.', 'deep-space'),
('Old', 'Isaías', 4, 5, 'Y criará Jehová sobre toda la morada del monte de Sión, y sobre los lugares de sus convocaciones, nube y oscuridad de día, y de noche resplandor de fuego que eche llamas: porque sobre toda gloria habrá cobertura.', 'deep-space'),
('Old', 'Isaías', 4, 6, 'Y habrá sombrajo para sombra contra el calor del día, para acogida y escondedero contra el turbión y contra el aguacero.', 'deep-space'),
('Old', 'Isaías', 5, 1, 'AHORA cantaré por mi amado el cantar de mi amado á su viña. Tenía mi amado una viña en un recuesto, lugar fértil.', 'deep-space'),
('Old', 'Isaías', 5, 2, 'Habíala cercado, y despedregádola, y plantádola de vides escogidas: había edificado en medio de ella una torre, y también asentado un lagar en ella: y esperaba que llevase uvas, y llevó uvas silvestres.', 'deep-space'),
('Old', 'Isaías', 5, 3, 'Ahora pues, vecinos de Jerusalem y varones de Judá, juzgad ahora entre mí y mi viña.', 'deep-space'),
('Old', 'Isaías', 5, 4, '¿Qué más se había de hacer á mi viña, que yo no haya hecho en ella? ¿Cómo, esperando yo que llevase uvas, ha llevado uvas silvestres?', 'deep-space'),
('Old', 'Isaías', 5, 5, 'Os mostraré pues ahora lo que haré yo á mi viña: Quitaréle su vallado, y será para ser consumida; aportillaré su cerca, y será para ser hollada;', 'deep-space'),
('Old', 'Isaías', 5, 6, 'Haré que quede desierta; no será podada ni cavada, y crecerá el cardo y las espinas: y aun á las nubes mandaré que no derramen lluvia sobre ella.', 'deep-space'),
('Old', 'Isaías', 5, 7, 'Ciertamente la viña de Jehová de los ejércitos es la casa de Israel, y los hombres de Judá planta suya deleitosa. Esperaba juicio, y he aquí vileza; justicia, y he aquí clamor.', 'deep-space'),
('Old', 'Isaías', 5, 8, '¡Ay de los que juntan casa con casa, y allegan heredad á heredad hasta acabar el término! ¿Habitaréis vosotros solos en medio de la tierra?', 'deep-space'),
('Old', 'Isaías', 5, 9, 'Ha llegado á mis oídos de parte de Jehová de los ejércitos, que las muchas casas han de quedar asoladas, sin morador las grandes y hermosas.', 'deep-space'),
('Old', 'Isaías', 5, 10, 'Y diez huebras de viña producirán un zaque, y treinta modios de simiente darán tres modios.', 'deep-space'),
('Old', 'Isaías', 5, 11, '¡Ay de los que se levantan de mañana para seguir la embriaguez; que se están hasta la noche, hasta que el vino los enciende!', 'deep-space'),
('Old', 'Isaías', 5, 12, 'Y en sus banquetes hay arpas, vihuelas, tamboriles, flautas, y vino; y no miran la obra de Jehová, ni consideran la obra de sus manos.', 'deep-space'),
('Old', 'Isaías', 5, 13, 'Por tanto mi pueblo fué llevado cautivo, porque no tuvo ciencia: y su gloria pereció de hambre, y su multitud se secó de sed.', 'deep-space'),
('Old', 'Isaías', 5, 14, 'Por eso ensanchó su interior el sepulcro, y sin medida extendió su boca; y allá descenderá la gloria de ellos, y su multitud, y su fausto, y el que en él se holgaba.', 'deep-space'),
('Old', 'Isaías', 5, 15, 'Y el hombre será humillado, y el varón será abatido, y bajados serán los ojos de los altivos.', 'deep-space'),
('Old', 'Isaías', 5, 16, 'Mas Jehová de los ejércitos será ensalzado en juicio, y el Dios Santo será santificado con justicia.', 'deep-space'),
('Old', 'Isaías', 5, 17, 'Y los corderos serán apacentados según su costumbre; y extraños comerán las gruesas desamparadas.', 'deep-space'),
('Old', 'Isaías', 5, 18, '¡Ay de los que traen la iniquidad con cuerdas de vanidad, y el pecado como con coyundas de carreta,', 'deep-space'),
('Old', 'Isaías', 5, 19, 'Los cuales dicen: Venga ya, apresúrese su obra, y veamos: acérquese, y venga el consejo del Santo de Israel, para que lo sepamos!', 'deep-space'),
('Old', 'Isaías', 5, 20, '¡Ay de los que á lo malo dicen bueno, y á lo bueno malo; que hacen de la luz tinieblas, y de las tinieblas luz; que ponen lo amargo por dulce, y lo dulce por amargo!', 'deep-space'),
('Old', 'Isaías', 5, 21, '¡Ay de los sabios en sus ojos, y de los que son prudentes delante de sí mismos!', 'deep-space'),
('Old', 'Isaías', 5, 22, '¡Ay de los que son valientes para beber vino, y hombres fuertes para mezclar bebida;', 'deep-space'),
('Old', 'Isaías', 5, 23, 'Los que dan por justo al impío por cohechos, y al justo quitan su justicia!', 'deep-space'),
('Old', 'Isaías', 5, 24, 'Por tanto, como la lengua del fuego consume las aristas, y la llama devora la paja, así será su raíz como pudrimiento, y su flor se desvanecerá como polvo: porque desecharon la ley de Jehová de los ejércitos, y abominaron la palabra del Santo de Israel.', 'deep-space'),
('Old', 'Isaías', 5, 25, 'Por esta causa se encendió el furor de Jehová contra su pueblo, y extendió contra él su mano, é hirióle; y se estremecieron los montes, y sus cadáveres fueron arrojados en medio de las calles. Con todo esto no ha cesado su furor, antes está su mano todavía extendida.', 'deep-space'),
('Old', 'Isaías', 5, 26, 'Y alzará pendón á gentes de lejos, y silbará al que está en el cabo de la tierra; y he aquí que vendrá pronto y velozmente.', 'deep-space'),
('Old', 'Isaías', 5, 27, 'No habrá entre ellos cansado, ni que vacile; ninguno se dormirá ni le tomará sueño; á ninguno se le desatará el cinto de los lomos, ni se le romperá la correa de sus zapatos.', 'deep-space'),
('Old', 'Isaías', 5, 28, 'Sus saetas amoladas, y todos sus arcos entesados; las uñas de sus caballos parecerán como de pedernal, y las ruedas de sus carros como torbellino.', 'deep-space'),
('Old', 'Isaías', 5, 29, 'Su bramido como de león; rugirá á manera de leoncillos, rechinará los dientes, y arrebatará la presa; la apañara, y nadie se la quitará.', 'deep-space'),
('Old', 'Isaías', 5, 30, 'Y bramará sobre él en aquel día como bramido de la mar: entonces mirará hacia la tierra, y he aquí tinieblas de tribulación, y en sus cielos se oscurecerá la luz.', 'deep-space'),
('Old', 'Isaías', 6, 1, 'EN el año que murió el rey Uzzías vi yo al Señor sentado sobre un trono alto y sublime, y sus faldas henchían el templo.', 'deep-space'),
('Old', 'Isaías', 6, 2, 'Y encima de él estaban serafines: cada uno tenía seis alas; con dos cubrían sus rostros, y con dos cubrían sus pies, y con dos volaban.', 'deep-space'),
('Old', 'Isaías', 6, 3, 'Y el uno al otro daba voces, diciendo: Santo, santo, santo, Jehová de los ejércitos: toda la tierra está llena de su gloria.', 'deep-space'),
('Old', 'Isaías', 6, 4, 'Y los quiciales de las puestas se estremecieron con la voz del que clamaba, y la casa se hinchió de humo.', 'deep-space'),
('Old', 'Isaías', 6, 5, 'Entonces dije: ¡Ay de mí! que soy muerto; que siendo hombre inmundo de labios, y habitando en medio de pueblo que tiene labios inmundos, han visto mis ojos al Rey, Jehová de los ejércitos.', 'deep-space'),
('Old', 'Isaías', 6, 6, 'Y voló hacia mí uno de los serafines, teniendo en su mano un carbón encendido, tomado del altar con unas tenazas:', 'deep-space'),
('Old', 'Isaías', 6, 7, 'Y tocando con él sobre mi boca, dijo: He aquí que esto tocó tus labios, y es quitada tu culpa, y limpio tu pecado.', 'deep-space'),
('Old', 'Isaías', 6, 8, 'Después oí la voz del Señor, que decía: ¿A quién enviaré, y quién nos irá? Entonces respondí yo: Heme aquí, envíame á mí.', 'deep-space'),
('Old', 'Isaías', 6, 9, 'Y dijo: Anda, y di á este pueblo: Oid bien, y no entendáis; ved por cierto, mas no comprendáis.', 'deep-space'),
('Old', 'Isaías', 6, 10, 'Engruesa el corazón de aqueste pueblo, y agrava sus oídos, y ciega sus ojos; porque no vea con sus ojos, ni oiga con sus oídos, ni su corazón entienda, ni se convierta, y haya para él sanidad.', 'deep-space'),
('Old', 'Isaías', 6, 11, 'Y yo dije: ¿Hasta cuándo, Señor? Y respondió él: Hasta que las ciudades estén asoladas, y sin morador, ni hombre en las casas, y la tierra sea tornada en desierto;', 'deep-space'),
('Old', 'Isaías', 6, 12, 'Hasta que Jehová hubiere echado lejos los hombres, y multiplicare en medio de la tierra la desamparada.', 'deep-space'),
('Old', 'Isaías', 6, 13, 'Pues aun quedará en ella una décima parte, y volverá, bien que habrá sido asolada: como el olmo y como el alcornoque, de los cuales en la tala queda el tronco, así será el tronco de ella la simiente santa.', 'deep-space'),
('Old', 'Isaías', 7, 1, 'ACONTECIO en los días de Achâz hijo de Jotham, hijo de Uzzías, rey de Judá, que Rezín rey de Siria, y Peca hijo de Remalías, rey de Israel, subieron á Jerusalem para combatirla; mas no la pudieron tomar.', 'deep-space'),
('Old', 'Isaías', 7, 2, 'Y vino la nueva á la casa de David, diciendo: Siria se ha confederado con Ephraim. Y estremeciósele el corazón, y el corazón de su pueblo, como se estremecen los árboles del monte á causa del viento.', 'deep-space'),
('Old', 'Isaías', 7, 3, 'Entonces dijo Jehová á Isaías: Sal ahora al encuentro de Achâz, tú, y Sear-jasub tu hijo, al cabo del conducto de la Pesquera de arriba, en el camino de la heredad del Lavador,', 'deep-space'),
('Old', 'Isaías', 7, 4, 'Y dile: Guarda, y repósate; no temas, ni se enternezca tu corazón á causa de estos dos cabos de tizón que humean, por el furor de la ira de Rezín y del Siro, y del hijo de Remalías.', 'deep-space'),
('Old', 'Isaías', 7, 5, 'Por haber acordado maligno consejo contra ti el Siro, con Ephraim y con el hijo de Remalías, diciendo:', 'deep-space'),
('Old', 'Isaías', 7, 6, 'Vamos contra Judá, y la despertaremos, y la partiremos entre nosotros, y pondremos en medio de ella por rey al hijo de Tabeel:', 'deep-space'),
('Old', 'Isaías', 7, 7, 'El Señor Jehová dice así: No subsistirá, ni será.', 'deep-space'),
('Old', 'Isaías', 7, 8, 'Porque la cabeza de Siria es Damasco, y la cabeza de Damasco, Rezín: y dentro de sesenta y cinco años Ephraim será quebrantado hasta dejar de ser pueblo.', 'deep-space'),
('Old', 'Isaías', 7, 9, 'Entretanto la cabeza de Ephraim es Samaria, y la cabeza de Samaria el hijo de Remalías. Si vosotros no creyereis, de cierto no permaneceréis.', 'deep-space'),
('Old', 'Isaías', 7, 10, 'Y habló más Jehová á Achâz, diciendo:', 'deep-space'),
('Old', 'Isaías', 7, 11, 'Pide para ti señal de Jehová tu Dios, demandándola en lo profundo, ó arriba en lo alto.', 'deep-space'),
('Old', 'Isaías', 7, 12, 'Y respondió Achâz: No pediré, y no tentaré á Jehová.', 'deep-space'),
('Old', 'Isaías', 7, 13, 'Dijo entonces Isaías: Oid ahora casa de David. ¿Os es poco el ser molestos á los hombres, sino que también lo seáis á mi Dios?', 'deep-space'),
('Old', 'Isaías', 7, 14, 'Por tanto el mismo Señor os dará señal: He aquí que la virgen concebirá, y parirá hijo, y llamará su nombre Emmanuel.', 'deep-space'),
('Old', 'Isaías', 7, 15, 'Comerá manteca y miel, para que sepa desechar lo malo y escoger lo bueno.', 'deep-space'),
('Old', 'Isaías', 7, 16, 'Porque antes que el niño sepa desechar lo malo y escoger lo bueno, la tierra que tú aborreces será dejada de sus dos reyes.', 'deep-space'),
('Old', 'Isaías', 7, 17, 'Jehová hará venir sobre ti, y sobre tu pueblo, y sobre la casa de tu padre, días cuales nunca vinieron desde el día que Ephraim se apartó de Judá, es á saber, al rey de Asiria.', 'deep-space'),
('Old', 'Isaías', 7, 18, 'Y acontecerá que aquel día silbará Jehová á la mosca que está en el fin de los ríos de Egipto, y á la abeja que está en la tierra de Asiria.', 'deep-space'),
('Old', 'Isaías', 7, 19, 'Y vendrán, y se asentarán todos en los valles desiertos, y en las cavernas de las piedras, y en todos los zarzales, y en todas las matas.', 'deep-space'),
('Old', 'Isaías', 7, 20, 'En aquel día raerá el Señor con navaja alquilada, con los que habitan de la otra parte del río, á saber, con el rey de Asiria, cabeza y pelos de los pies; y aun la barba también quitará.', 'deep-space'),
('Old', 'Isaías', 7, 21, 'Y acontecerá en aquel tiempo, que críe un hombre una vaca y dos ovejas;', 'deep-space'),
('Old', 'Isaías', 7, 22, 'Y será que á causa de la abundancia de leche que darán, comerá manteca: cierto manteca y miel comerá el que quedare en medio de la tierra.', 'deep-space'),
('Old', 'Isaías', 7, 23, 'Acontecerá también en aquel tiempo, que el lugar donde había mil vides que valían mil siclos de plata, será para los espinos y cardos.', 'deep-space'),
('Old', 'Isaías', 7, 24, 'Con saetas y arco irán allá; porque toda la tierra será espinos y cardos.', 'deep-space'),
('Old', 'Isaías', 7, 25, 'Y á todos los montes que se cavaban con azada, no llegará allá el temor de los espinos y de los cardos: mas serán para pasto de bueyes, y para ser hollados de los ganados.', 'deep-space'),
('Old', 'Isaías', 8, 1, 'Y DIJOME Jehová: Tómate un gran volumen, y escribe en él en estilo de hombre tocante á Maher-salal-hash-baz.', 'deep-space'),
('Old', 'Isaías', 8, 2, 'Y junté conmigo por testigos fieles á Urías sacerdote, y á Zacarías hijo de Jeberechîas.', 'deep-space'),
('Old', 'Isaías', 8, 3, 'Y juntéme con la profetisa, la cual concibió, y parió un hijo. Y díjome Jehová: Ponle por nombre Maher-salal-hash-baz.', 'deep-space'),
('Old', 'Isaías', 8, 4, 'Porque antes que el niño sepa decir, Padre mío, y Madre mía, será quitada la fuerza de Damasco y los despojos de Samaria, en la presencia del rey de Asiria.', 'deep-space'),
('Old', 'Isaías', 8, 5, 'Otra vez tornó Jehová á hablarme, diciendo:', 'deep-space'),
('Old', 'Isaías', 8, 6, 'Por cuanto desechó este pueblo las aguas de Siloé, que corren mansamente, y holgóse con Rezín y con el hijo de Remalías,', 'deep-space'),
('Old', 'Isaías', 8, 7, 'He aquí por tanto que el Señor hace subir sobre ellos aguas de ríos, impetuosas y muchas, á saber, al rey de Asiria con todo su poder; el cual subirá sobre todos sus ríos, y pasará sobre todas sus riberas:', 'deep-space'),
('Old', 'Isaías', 8, 8, 'Y pasando hasta Judá, inundará, y sobrepujará, y llegará hasta la garganta; y extendiendo sus alas, llenará la anchura de tu tierra, oh Emmanuel.', 'deep-space'),
('Old', 'Isaías', 8, 9, 'Juntaos, pueblos, y seréis quebrantados; oid todos los que sois de lejanas tierras: poneos á punto, y seréis quebrantados; apercibíos, y seréis quebrantados.', 'deep-space'),
('Old', 'Isaías', 8, 10, 'Tomad consejo, y será deshecho; proferid palabra, y no será firme: porque Dios con nosotros.', 'deep-space'),
('Old', 'Isaías', 8, 11, 'Porque Jehová me dijo de esta manera con mano fuerte, y enseñóme que no caminase por el camino de este pueblo, diciendo:', 'deep-space'),
('Old', 'Isaías', 8, 12, 'No digáis, Conjuración, á todas las cosas á que este pueblo dice, Conjuración, ni temáis lo que temen, ni tengáis miedo.', 'deep-space'),
('Old', 'Isaías', 8, 13, 'A Jehová de los ejércitos, á él santificad: sea él vuestro temor, y él sea vuestro miedo.', 'deep-space'),
('Old', 'Isaías', 8, 14, 'Entonces él será por santuario; mas á las dos casas de Israel por piedra para tropezar, y por tropezadero para caer, y por lazo y por red al morador de Jerusalem.', 'deep-space'),
('Old', 'Isaías', 8, 15, 'Y muchos tropezarán entre ellos, y caerán, y serán quebrantados: enredaránse, y serán presos.', 'deep-space'),
('Old', 'Isaías', 8, 16, 'Ata el testimonio, sella la ley entre mis discípulos.', 'deep-space'),
('Old', 'Isaías', 8, 17, 'Esperaré pues á Jehová, el cual escondió su rostro de la casa de Jacob, y á él aguardaré.', 'deep-space'),
('Old', 'Isaías', 8, 18, 'He aquí, yo y los hijos que me dió Jehová, por señales y prodigios en Israel, de parte de Jehová de los ejércitos que mora en el monte de Sión.', 'deep-space'),
('Old', 'Isaías', 8, 19, 'Y si os dijeren: Preguntad á los pythones y á los adivinos, que susurran hablando, responded: ¿No consultará el pueblo á su Dios? ¿Apelará por los vivos á los muertos?', 'deep-space'),
('Old', 'Isaías', 8, 20, '¡A la ley y al testimonio! Si no dijeren conforme á esto, es porque no les ha amanecido.', 'deep-space'),
('Old', 'Isaías', 8, 21, 'Y pasarán por él fatigados y hambrientos, y acontecerá que teniendo hambre, se enojarán y maldecirán á su rey y á su Dios, levantando el rostro en alto.', 'deep-space'),
('Old', 'Isaías', 8, 22, 'Y mirarán á la tierra, y he aquí tribulación y tiniebla, oscuridad y angustia; y serán sumidos en las tinieblas.', 'deep-space'),
('Old', 'Isaías', 9, 1, 'AUNQUE no será esta oscuridad tal como la aflicción que le vino en el tiempo que livianamente tocaron la primera vez á la tierra de Zabulón, y á la tierra de Nephtalí; y después cuando agravaron por la vía de la mar, de esa parte del Jordán, en Galilea de las gentes.', 'deep-space'),
('Old', 'Isaías', 9, 2, 'El pueblo que andaba en tinieblas vió gran luz: los que moraban en tierra de sombra de muerte, luz resplandeció sobre ellos.', 'deep-space'),
('Old', 'Isaías', 9, 3, 'Aumentando la gente, no aumentaste la alegría. Alegraránse delante de ti como se alegran en la siega, como se gozan cuando reparten despojos.', 'deep-space'),
('Old', 'Isaías', 9, 4, 'Porque tú quebraste su pesado yugo, y la vara de su hombro, y el cetro de su exactor, como en el día de Madián.', 'deep-space'),
('Old', 'Isaías', 9, 5, 'Porque toda batalla de quien pelea es con estruendo, y con revolcamiento de vestidura en sangre: mas esto será para quema, y pábulo del fuego.', 'deep-space'),
('Old', 'Isaías', 9, 6, 'Porque un niño nos es nacido, hijo nos es dado; y el principado sobre su hombro: y llamaráse su nombre Admirable, Consejero, Dios fuerte, Padre eterno, Príncipe de paz.', 'deep-space'),
('Old', 'Isaías', 9, 7, 'Lo dilatado de su imperio y la paz no tendrán término, sobre el trono de David, y sobre su reino, disponiéndolo y confirmándolo en juicio y en justicia desde ahora para siempre. El celo de Jehová de los ejércitos hará esto.', 'deep-space'),
('Old', 'Isaías', 9, 8, 'El Señor envió palabra á Jacob, y cayó en Israel.', 'deep-space'),
('Old', 'Isaías', 9, 9, 'Y la sabrá el pueblo, todo él, Ephraim y los moradores de Samaria, que con soberbia y con altivez de corazón dicen:', 'deep-space'),
('Old', 'Isaías', 9, 10, 'Los ladrillos cayeron, mas edificaremos de cantería; cortaron los cabrahigos, mas cedros pondremos en su lugar.', 'deep-space'),
('Old', 'Isaías', 9, 11, 'Empero Jehová ensalzará los enemigos de Rezín contra él, y juntará sus enemigos;', 'deep-space'),
('Old', 'Isaías', 9, 12, 'De oriente los Siros, y los Filisteos de poniente; y con toda la boca se tragarán á Israel. Ni con todo eso ha cesado su furor, antes todavía su mano extendida.', 'deep-space'),
('Old', 'Isaías', 9, 13, 'Mas el pueblo no se convirtió al que lo hería, ni buscaron á Jehová de los ejércitos.', 'deep-space'),
('Old', 'Isaías', 9, 14, 'Y Jehová cortará de Israel cabeza y cola, ramo y caña en un mismo día.', 'deep-space'),
('Old', 'Isaías', 9, 15, 'El viejo y venerable de rostro es la cabeza: el profeta que enseña mentira, este es cola.', 'deep-space'),
('Old', 'Isaías', 9, 16, 'Porque los gobernadores de este pueblo son engañadores; y sus gobernados, perdidos.', 'deep-space'),
('Old', 'Isaías', 9, 17, 'Por tanto, el Señor no tomará contentamiento en sus mancebos, ni de sus huérfanos y viudas tendrá misericordia: porque todos son falsos y malignos, y toda boca habla despropósitos. Con todo esto no ha cesado su furor, antes todavía su mano extendida.', 'deep-space'),
('Old', 'Isaías', 9, 18, 'Porque la maldad se encendió como fuego, cardos y espinas devorará; y encenderáse en lo espeso de la breña, y serán alzados como humo.', 'deep-space'),
('Old', 'Isaías', 9, 19, 'Por la ira de Jehová de los ejércitos se oscureció la tierra, y será el pueblo como pábulo del fuego: el hombre no tendrá piedad de su hermano.', 'deep-space'),
('Old', 'Isaías', 9, 20, 'Cada uno hurtará á la mano derecha, y tendrá hambre; y comerá á la izquierda, y no se hartará: cada cual comerá la carne de su brazo:', 'deep-space'),
('Old', 'Isaías', 9, 21, 'Manasés á Ephraim, y Ephraim á Manasés, y entrambos contra Judá. Ni con todo esto ha cesado su furor, antes todavía extendida su mano.', 'deep-space'),
('Old', 'Isaías', 10, 1, '¡AY de los que establecen leyes injustas, y determinando prescriben tiranía,', 'deep-space'),
('Old', 'Isaías', 10, 2, 'Por apartar del juicio á los pobres, y por quitar el derecho á los afligidos de mi pueblo; por despojar las viudas, y robar los huérfanos!', 'deep-space'),
('Old', 'Isaías', 10, 3, '¿Y qué haréis en el día de la visitación? ¿y á quién os acogeréis que os ayude, cuando viniere de lejos el asolamiento? ¿y en dónde dejaréis vuestra gloria?', 'deep-space'),
('Old', 'Isaías', 10, 4, 'Sin mí se inclinarán entre los presos, y entre los muertos caerán. Ni con todo esto ha cesado su furor, antes todavía extendida su mano.', 'deep-space'),
('Old', 'Isaías', 10, 5, 'Oh Assur, vara y bastón de mi furor: en su mano he puesto mi ira.', 'deep-space'),
('Old', 'Isaías', 10, 6, 'Mandaréle contra una gente fementida, y sobre el pueblo de mi ira le enviaré, para que quite despojos, y arrebate presa, y que lo ponga á ser hollado como lodo de las calles.', 'deep-space'),
('Old', 'Isaías', 10, 7, 'Aunque él no lo pensará así, ni su corazón lo imaginará de esta manera; sino que su pensamiento será desarraigar y cortar gentes no pocas.', 'deep-space'),
('Old', 'Isaías', 10, 8, 'Porque él dice: Mis príncipes ¿no son todos reyes?', 'deep-space'),
('Old', 'Isaías', 10, 9, '¿No es Calno como Carchêmis, Hamath como Arphad, y Samaria como Damasco?', 'deep-space'),
('Old', 'Isaías', 10, 10, 'Como halló mi mano los reinos de los ídolos, siendo sus imágenes más que Jerusalem y Samaria;', 'deep-space'),
('Old', 'Isaías', 10, 11, 'Como hice á Samaria y á sus ídolos, ¿no haré también así á Jerusalem y á sus ídolos?', 'deep-space'),
('Old', 'Isaías', 10, 12, 'Mas acontecerá que después que el Señor hubiere acabado toda su obra en el monte de Sión, y en Jerusalem, visitaré sobre el fruto de la soberbia del corazón del rey de Asiria, y sobre la gloria de la altivez de sus ojos.', 'deep-space'),
('Old', 'Isaías', 10, 13, 'Porque dijo: Con la fortaleza de mi mano lo he hecho, y con mi sabiduría; porque he sido prudente: y quité los términos de los pueblos, y saqué sus tesoros, y derribé como valientes los que estaban sentados:', 'deep-space'),
('Old', 'Isaías', 10, 14, 'Y halló mi mano como nido las riquezas de los pueblos; y como se cogen los huevos dejados, así me apoderé yo de toda la tierra; y no hubo quien moviese ala, ó abriese boca y graznase.', 'deep-space'),
('Old', 'Isaías', 10, 15, '¿Gloriaráse el hacha contra el que con ella corta? ¿se ensoberbecerá la sierra contra el que la mueve? como si el bordón se levantase contra los que lo levantan; como si se levantase la vara: ¿no es leño?', 'deep-space'),
('Old', 'Isaías', 10, 16, 'Por tanto el Señor Jehová de los ejércitos enviará flaqueza sobre sus gordos; y debajo de su gloria encenderá encendimiento, como ardor de fuego.', 'deep-space'),
('Old', 'Isaías', 10, 17, 'Y la luz de Israel será por fuego, y su Santo por llama que abrase y consuma en un día sus cardos y sus espinas.', 'deep-space'),
('Old', 'Isaías', 10, 18, 'La gloria de su bosque y de su campo fértil consumirá, desde el alma hasta la carne: y vendrá á ser como abanderado en derrota.', 'deep-space'),
('Old', 'Isaías', 10, 19, 'Y los árboles que quedaren en su bosque, serán en número que un niño los pueda contar.', 'deep-space'),
('Old', 'Isaías', 10, 20, 'Y acontecerá en aquel tiempo, que los que hubieren quedado de Israel, y los que hubieren quedado de la casa de Jacob, nunca más estriben sobre el que los hirió; sino que se apoyarán con verdad en Jehová Santo de Israel.', 'deep-space'),
('Old', 'Isaías', 10, 21, 'Las reliquias se convertirán, las reliquias de Jacob, al Dios fuerte.', 'deep-space'),
('Old', 'Isaías', 10, 22, 'Porque si tu pueblo, oh Israel, fuere como las arenas de la mar, las reliquias de él se convertirán: la destrucción acordada rebosará justicia.', 'deep-space'),
('Old', 'Isaías', 10, 23, 'Pues el Señor Jehová de los ejércitos hará consumación y fenecimiento en medio de la tierra.', 'deep-space'),
('Old', 'Isaías', 10, 24, 'Por tanto el Señor Jehová de los ejércitos dice así: Pueblo mío, morador de Sión, no temas de Assur. Con vara te herirá, y contra ti alzará su palo, á la manera de Egipto:', 'deep-space'),
('Old', 'Isaías', 10, 25, 'Mas de aquí á muy poco tiempo, se acabará el furor y mi enojo, para fenecimiento de ellos.', 'deep-space'),
('Old', 'Isaías', 10, 26, 'Y levantará Jehová de los ejércitos azote contra él, cual la matanza de Madián en la peña de Oreb: y alzará su vara sobre la mar, según hizo por la vía de Egipto.', 'deep-space'),
('Old', 'Isaías', 10, 27, 'Y acaecerá en aquel tiempo, que su carga será quitada de tu hombro, y su yugo de tu cerviz, y el yugo se empodrecerá por causa de la unción.', 'deep-space'),
('Old', 'Isaías', 10, 28, 'Vino hasta Ajad, pasó hasta Migrón; en Michmas contará su ejército:', 'deep-space'),
('Old', 'Isaías', 10, 29, 'Pasaron el vado; alojaron en Geba: Ramá tembló; Gabaa de Saúl huyó.', 'deep-space'),
('Old', 'Isaías', 10, 30, 'Grita en alta voz, hija de Galim; haz que se oiga hacia Lais, pobrecilla Anathoth.', 'deep-space'),
('Old', 'Isaías', 10, 31, 'Madmena se alborotó: los moradores de Gebim se juntarán.', 'deep-space'),
('Old', 'Isaías', 10, 32, 'Aún vendrá día cuando reposará en Nob: alzará su mano al monte de la hija de Sión, al collado de Jerusalem.', 'deep-space'),
('Old', 'Isaías', 10, 33, 'He aquí el Señor Jehová de los ejércitos desgajará el ramo con fortaleza: y los de grande altura serán cortados, y los altos serán humillados.', 'deep-space'),
('Old', 'Isaías', 10, 34, 'Y cortará con hierro la espesura del bosque, y el Líbano caerá con fortaleza.', 'deep-space'),
('Old', 'Isaías', 11, 1, 'Y SALDRA una vara del tronco de Isaí, y un vástago retoñará de sus raíces.', 'deep-space'),
('Old', 'Isaías', 11, 2, 'Y reposará sobre él el espíritu de Jehová; espíritu de sabiduría y de inteligencia, espíritu de consejo y de fortaleza, espíritu de conocimiento y de temor de Jehová.', 'deep-space'),
('Old', 'Isaías', 11, 3, 'Y harále entender diligente en el temor de Jehová. No juzgará según la vista de sus ojos, ni argüirá por lo que oyeren sus oídos;', 'deep-space'),
('Old', 'Isaías', 11, 4, 'Sino que juzgará con justicia á los pobres, y argüirá con equidad por los mansos de la tierra: y herirá la tierra con la vara de su boca, y con el espíritu de sus labios matará al impío.', 'deep-space'),
('Old', 'Isaías', 11, 5, 'Y será la justicia cinto de sus lomos, y la fidelidad ceñidor de sus riñones.', 'deep-space'),
('Old', 'Isaías', 11, 6, 'Morará el lobo con el cordero, y el tigre con el cabrito se acostará: el becerro y el león y la bestia doméstica andarán juntos, y un niño los pastoreará.', 'deep-space'),
('Old', 'Isaías', 11, 7, 'La vaca y la osa pacerán, sus crías se echarán juntas; y el león como el buey comerá paja.', 'deep-space'),
('Old', 'Isaías', 11, 8, 'Y el niño de teta se entretendrá sobre la cueva del áspid, y el recién destetado extenderá su mano sobre la caverna del basilisco.', 'deep-space'),
('Old', 'Isaías', 11, 9, 'No harán mal ni dañarán en todo mi santo monte; porque la tierra será llena del conocimiento de Jehová, como cubren la mar las aguas.', 'deep-space'),
('Old', 'Isaías', 11, 10, 'Y acontecerá en aquel tiempo que la raíz de Isaí, la cual estará puesta por pendón á los pueblos, será buscada de las gentes; y su holganza será gloria.', 'deep-space'),
('Old', 'Isaías', 11, 11, 'Asimismo acontecerá en aquel tiempo, que Jehová tornará á poner otra vez su mano para poseer las reliquias de su pueblo que fueron dejadas de Assur, y de Egipto, y de Parthia, y de Etiopía, y de Persia, y de Caldea, y de Amath, y de las Islas de la mar.', 'deep-space'),
('Old', 'Isaías', 11, 12, 'Y levantará pendón á las gentes, y juntará los desterrados de Israel, y reunirá los esparcidos de Judá de los cuatro cantones de la tierra.', 'deep-space'),
('Old', 'Isaías', 11, 13, 'Y se disipará la envidia de Ephraim, y los enemigos de Judá serán talados. Ephraim no tendrá envidia contra Judá, ni Judá afligirá á Ephraim;', 'deep-space'),
('Old', 'Isaías', 11, 14, 'Mas volarán sobre los hombros de los Filisteos al occidente, meterán también á saco á los de oriente: Edom y Moab les servirán, y los hijos de Ammón les darán obediencia.', 'deep-space'),
('Old', 'Isaías', 11, 15, 'Y secará Jehová la lengua de la mar de Egipto; y levantará su mano con fortaleza de su espíritu sobre el río, y herirálo en sus siete brazos, y hará que pasen por él con zapatos.', 'deep-space'),
('Old', 'Isaías', 11, 16, 'Y habrá camino para las reliquias de su pueblo, las que quedaron de Assur, de la manera que lo hubo para Israel el día que subió de la tierra de Egipto.', 'deep-space'),
('Old', 'Isaías', 12, 1, 'Y DIRAS en aquel día: Cantaré á ti, oh Jehová: pues aunque te enojaste contra mí, tu furor se apartó, y me has consolado.', 'deep-space'),
('Old', 'Isaías', 12, 2, 'He aquí Dios es salud mía; aseguraréme, y no temeré; porque mi fortaleza y mi canción es JAH Jehová, el cual ha sido salud para mí.', 'deep-space'),
('Old', 'Isaías', 12, 3, 'Sacaréis aguas con gozo de la fuentes de la salud.', 'deep-space'),
('Old', 'Isaías', 12, 4, 'Y diréis en aquel día: Cantad á Jehová, aclamad su nombre, haced célebres en los pueblos sus obras, recordad que su nombre es engrandecido.', 'deep-space'),
('Old', 'Isaías', 12, 5, 'Cantad salmos á Jehová; porque ha hecho cosas magníficas: sea sabido esto por toda la tierra.', 'deep-space'),
('Old', 'Isaías', 12, 6, 'Regocíjate y canta, oh moradora de Sión: porque grande es en medio de ti el Santo de Israel.', 'deep-space'),
('Old', 'Isaías', 13, 1, 'CARGA de Babilonia, que vió Isaías, hijo de Amoz.', 'deep-space'),
('Old', 'Isaías', 13, 2, 'Levantad bandera sobre un alto monte; alzad la voz á ellos, alzad la mano, para que entren por puertas de príncipes.', 'deep-space'),
('Old', 'Isaías', 13, 3, 'Yo mandé á mis santificados, asimismo llamé á mis valientes para mi ira, á los que se alegran con mi gloria.', 'deep-space'),
('Old', 'Isaías', 13, 4, 'Murmullo de multitud en los montes, como de mucho pueblo; murmullo de ruido de reinos, de gentes reunidas: Jehová de los ejércitos ordena las tropas de la batalla.', 'deep-space'),
('Old', 'Isaías', 13, 5, 'Vienen de lejana tierra, de lo postrero de los cielos, Jehová y los instrumentos de su furor, para destruir toda la tierra.', 'deep-space'),
('Old', 'Isaías', 13, 6, 'Aullad, porque cerca está el día de Jehová; vendrá como asolamiento del Todopoderoso.', 'deep-space'),
('Old', 'Isaías', 13, 7, 'Por tanto, se enervarán todas la manos, y desleiráse todo corazón de hombre:', 'deep-space'),
('Old', 'Isaías', 13, 8, 'Y se llenarán de terror; angustias y dolores los comprenderán; tendrán dolores como mujer de parto; pasmaráse cada cual al mirar á su compañero; sus rostros, rostros de llamas.', 'deep-space'),
('Old', 'Isaías', 13, 9, 'He aquí el día de Jehová viene, crudo, y de saña y ardor de ira, para tornar la tierra en soledad, y raer de ella sus pecadores.', 'deep-space'),
('Old', 'Isaías', 13, 10, 'Por lo cual las estrellas de los cielos y sus luceros no derramarán su lumbre; y el sol se oscurecerá en naciendo, y la luna no echará su resplandor.', 'deep-space'),
('Old', 'Isaías', 13, 11, 'Y visitaré la maldad sobre el mundo, y sobre los impíos su iniquidad; y haré que cese la arrogancia de los soberbios, y abatiré la altivez de los fuertes.', 'deep-space'),
('Old', 'Isaías', 13, 12, 'Haré más precioso que el oro fino al varón, y más que el oro de Ophir al hombre.', 'deep-space'),
('Old', 'Isaías', 13, 13, 'Porque haré estremecer los cielos, y la tierra se moverá de su lugar, en la indignación de Jehová de los ejércitos, y en el día de la ira de su furor.', 'deep-space'),
('Old', 'Isaías', 13, 14, 'Y será que como corza amontada, y como oveja sin pastor, cada cual mirará hacia su pueblo, y cada uno huirá á su tierra.', 'deep-space'),
('Old', 'Isaías', 13, 15, 'Cualquiera que fuere hallado, será alanceado; y cualquiera que á ellos se juntare, caerá á cuchillo.', 'deep-space'),
('Old', 'Isaías', 13, 16, 'Sus niños serán estrellados delante de ellos; sus casas serán saqueadas, y forzadas sus mujeres.', 'deep-space'),
('Old', 'Isaías', 13, 17, 'He aquí que yo despierto contra ellos á los Medos, que no curarán de la plata, ni codiciarán oro.', 'deep-space'),
('Old', 'Isaías', 13, 18, 'Y con arcos tirarán á los niños, y no tendrán misericordia de fruto de vientre, ni su ojo perdonará á hijos.', 'deep-space'),
('Old', 'Isaías', 13, 19, 'Y Babilonia, hermosura de reinos y ornamento de la grandeza de los Caldeos, será como Sodoma y Gomorra, á las que trastornó Dios.', 'deep-space'),
('Old', 'Isaías', 13, 20, 'Nunca más será habitada, ni se morará en ella de generación en generación; ni hincará allí tienda el Arabe, ni pastores tendrán allí majada:', 'deep-space'),
('Old', 'Isaías', 13, 21, 'Sino que dormirán allí bestias fieras, y sus casas se llenarán de hurones, allí habitarán hijas del buho, y allí saltarán peludos.', 'deep-space'),
('Old', 'Isaías', 13, 22, 'Y en sus palacios gritarán gatos cervales, y chacales en sus casas de deleite: y abocado está á venir su tiempo, y sus días no se alargarán.', 'deep-space'),
('Old', 'Isaías', 14, 1, 'PORQUE Jehová tendrá piedad de Jacob, y todavía escogerá á Israel, y les hará reposar en su tierra: y á ellos se unirán extranjeros, y allegaránse á la familia de Jacob.', 'deep-space'),
('Old', 'Isaías', 14, 2, 'Y los tomarán los pueblos, y traeránlos á su lugar: y la casa de Israel los poseerá por siervos y criadas en la tierra de Jehová: y cautivarán á los que los cautivaron, y señorearán á los que los oprimieron.', 'deep-space'),
('Old', 'Isaías', 14, 3, 'Y será en el día que Jehová te diera reposo de tu trabajo, y de tu temor, y de la dura servidumbre en que te hicieron servir,', 'deep-space'),
('Old', 'Isaías', 14, 4, 'Que levantarás esta parábola sobre el rey de Babilonia, y dirás: ¡Cómo paró el exactor, cómo cesó la ciudad codiciosa del oro!', 'deep-space'),
('Old', 'Isaías', 14, 5, 'Quebrantó Jehová el bastón de los impíos, el cetro de los señores;', 'deep-space'),
('Old', 'Isaías', 14, 6, 'Al que con ira hería los pueblos de llaga permanente, el cual se enseñoreaba de las gentes con furor, y las perseguía con crueldad.', 'deep-space'),
('Old', 'Isaías', 14, 7, 'Descansó, sosegó toda la tierra: cantaron alabanza.', 'deep-space'),
('Old', 'Isaías', 14, 8, 'Aun las hayas se holgaron de ti, y los cedros del Líbano, diciendo: Desde que tú pereciste, no ha subido cortador contra nosotros.', 'deep-space'),
('Old', 'Isaías', 14, 9, 'El infierno abajo se espantó de ti; te despertó muertos que en tu venida saliesen á recibirte, hizo levantar de sus sillas á todos los príncipes de la tierra, á todos los reyes de las naciones.', 'deep-space'),
('Old', 'Isaías', 14, 10, 'Todos ellos darán voces, y te dirán: ¿Tú también enfermaste como nosotros, y como nosotros fuiste?', 'deep-space'),
('Old', 'Isaías', 14, 11, 'Descendió al sepulcro tu soberbia, y el sonido de tus vihuelas: gusanos serán tu cama, y gusanos te cubrirán.', 'deep-space'),
('Old', 'Isaías', 14, 12, '¡Cómo caiste del cielo, oh Lucero, hijo de la mañana! Cortado fuiste por tierra, tú que debilitabas las gentes.', 'deep-space'),
('Old', 'Isaías', 14, 13, 'Tú que decías en tu corazón: Subiré al cielo, en lo alto junto á las estrellas de Dios ensalzaré mi solio, y en el monte del testimonio me sentaré, á los lados del aquilón;', 'deep-space'),
('Old', 'Isaías', 14, 14, 'Sobre las alturas de las nubes subiré, y seré semejante al Altísimo.', 'deep-space'),
('Old', 'Isaías', 14, 15, 'Mas tú derribado eres en el sepulcro, á los lados de la huesa.', 'deep-space'),
('Old', 'Isaías', 14, 16, 'Inclinarse han hacia ti los que te vieren, te considerarán diciendo: ¿Es este aquel varón que hacía temblar la tierra, que trastornaba los reinos;', 'deep-space'),
('Old', 'Isaías', 14, 17, 'Que puso el mundo como un desierto, que asoló sus ciudades; que á sus presos nunca abrió la cárcel?', 'deep-space'),
('Old', 'Isaías', 14, 18, 'Todos los reyes de las gentes, todos ellos yacen con honra cada uno en su casa.', 'deep-space'),
('Old', 'Isaías', 14, 19, 'Mas tú echado eres de tu sepulcro como tronco abominable, como vestido de muertos pasados á cuchillo, que descendieron al fondo de la sepultura; como cuerpo muerto hollado.', 'deep-space'),
('Old', 'Isaías', 14, 20, 'No serás contado con ellos en la sepultura: porque tú destruiste tu tierra, mataste tu pueblo. No será nombrada para siempre la simiente de los malignos.', 'deep-space'),
('Old', 'Isaías', 14, 21, 'Aparejad sus hijos para el matadero por la maldad de sus padres: no se levanten, ni posean la tierra, é hinchan la haz del mundo de ciudades.', 'deep-space'),
('Old', 'Isaías', 14, 22, 'Porque yo me levantaré sobre ellos, dice Jehová de los ejércitos, y raeré de Babilonia el nombre y las reliquias, hijo y nieto, dice Jehová.', 'deep-space'),
('Old', 'Isaías', 14, 23, 'Y convertiréla en posesión de erizos, y en lagunas de agua; y la barreré con escobas de destrucción, dice Jehová de los ejércitos.', 'deep-space'),
('Old', 'Isaías', 14, 24, 'Jehová de los ejércitos juró, diciendo: Ciertamente se hará de la manera que lo he pensado, y será confirmado como lo he determinado:', 'deep-space'),
('Old', 'Isaías', 14, 25, 'Que quebrantaré al Asirio en mi tierra, y en mis montes lo hollaré; y su yugo será apartado de ellos, y su carga será quitada de su hombro.', 'deep-space'),
('Old', 'Isaías', 14, 26, 'Este es el consejo que está acordado sobre toda la tierra; y ésta, la mano extendida sobre todas las gentes.', 'deep-space'),
('Old', 'Isaías', 14, 27, 'Porque Jehová de los ejércitos ha determinado: ¿y quién invalidará? Y su mano extendida, ¿quién la hará tornar?', 'deep-space'),
('Old', 'Isaías', 14, 28, 'En el año que murió el rey Achâz fué esta carga:', 'deep-space'),
('Old', 'Isaías', 14, 29, 'No te alegres tú, Filistea toda, por haberse quebrado la vara del que te hería; porque de la raíz de la culebra saldrá basilisco, y su fruto, ceraste volador.', 'deep-space'),
('Old', 'Isaías', 14, 30, 'Y los primogénitos de los pobres serán apacentados, y los menesterosos se acostarán seguramente: mas yo haré morir de hambre tu raíz, y mataré tus reliquias.', 'deep-space'),
('Old', 'Isaías', 14, 31, 'Aulla, oh puerta; clama, oh ciudad; disuelta estás toda tú, Filistea: porque humo vendrá de aquilón, no quedará uno solo en sus asambleas.', 'deep-space'),
('Old', 'Isaías', 14, 32, '¿Y qué se responderá á los mensajeros de la gentilidad? Que Jehová fundó á Sión, y que á ella se acogerán los afligidos de su pueblo.', 'deep-space'),
('Old', 'Isaías', 15, 1, 'CARGA de Moab.', 'deep-space'),
('Old', 'Isaías', 15, 2, 'Subió á Bayith y á Dibón, lugares altos, á llorar; sobre Nebo y sobre Medeba aullará Moab: toda cabeza de ella será raída, y toda barba se mesará.', 'deep-space'),
('Old', 'Isaías', 15, 3, 'Ceñiránse de sacos en sus plazas: en sus terrados y en sus calles aullarán todos, descendiendo en llanto.', 'deep-space'),
('Old', 'Isaías', 15, 4, 'Hesbón y Eleale gritarán, hasta Jahas se oirá su voz: por lo que aullarán los armados de Moab, lamentaráse el alma de cada uno de por sí.', 'deep-space'),
('Old', 'Isaías', 15, 5, 'Mi corazón dará gritos por Moab; sus fugitivos huirán hasta Zoar, como novilla de tres años. Por la cuesta de Luhith subirán llorando, y levantarán grito de quebrantamiento por el camino de Horonaim.', 'deep-space'),
('Old', 'Isaías', 15, 6, 'Las aguas de Nimrim serán consumidas, y secaráse la hierba, marchitaránse los retoños, todo verdor perecerá.', 'deep-space'),
('Old', 'Isaías', 15, 7, 'Por tanto las riquezas que habrán adquirido, y las que habrán reservado, llevaránlas al torrente de los sauces.', 'deep-space'),
('Old', 'Isaías', 15, 8, 'Porque el llanto rodeó los términos de Moab; hasta Eglaim llegó su alarido, y hasta Beer-elim su clamor.', 'deep-space'),
('Old', 'Isaías', 15, 9, 'Y las aguas de Dimón se henchirán de sangre: porque yo pondré sobre Dimón añadiduras, leones á los que escaparen de Moab, y á las reliquias de la tierra.', 'deep-space'),
('Old', 'Isaías', 16, 1, 'ENVIAD cordero al enseñoreador de la tierra, desde la Piedra del desierto al monte de la hija de Sión.', 'deep-space'),
('Old', 'Isaías', 16, 2, 'Y será que cual ave espantada que se huye de su nido, así serán las hijas de Moab en los vados de Arnón.', 'deep-space'),
('Old', 'Isaías', 16, 3, 'Reune consejo, haz juicio; pon tu sombra en medio del día como la noche: esconde los desterrados, no entregues á los que andan errantes.', 'deep-space'),
('Old', 'Isaías', 16, 4, 'Moren contigo mis desterrados, oh Moab; séles escondedero de la presencia del destruidor: porque el atormentador fenecerá, el destruidor tendrá fin, el hollador será consumido de sobre la tierra.', 'deep-space'),
('Old', 'Isaías', 16, 5, 'Y dispondráse trono en misericordia; y sobre él se sentará firmemente, en el tabernáculo de David, quien juzgue y busque el juicio, y apresure la justicia.', 'deep-space'),
('Old', 'Isaías', 16, 6, 'Oído hemos la soberbia de Moab, por extremo soberbio; su soberbia y su arrogancia, y su altivez; mas sus mentiras no serán firmes.', 'deep-space'),
('Old', 'Isaías', 16, 7, 'Por tanto aullará Moab, todo él aullará: gemiréis por los fundamentos de Kir-hareseth, en gran manera heridos.', 'deep-space'),
('Old', 'Isaías', 16, 8, 'Porque los campos de Hesbón fueron talados, y las vides de Sibma; señores de gentes hollaron sus generosos sarmientos; habían llegado hasta Jazer, y extendídose por el desierto; extendiéronse sus plantas, pasaron la mar.', 'deep-space'),
('Old', 'Isaías', 16, 9, 'Por lo cual lamentaré con lloro de Jazer la viña de Sibma; embriagarte hé de mis lágrimas, oh Hesbón y Eleale: porque sobre tus cosechas y sobre tu siega caerá la algazara.', 'deep-space'),
('Old', 'Isaías', 16, 10, 'Quitado es el gozo y la alegría del campo fértil; en las viñas no cantarán, ni se regocijarán; no pisará vino en los lagares el pisador: la canción he hecho cesar.', 'deep-space'),
('Old', 'Isaías', 16, 11, 'Por tanto mis entrañas sonarán como arpa acerca de Moab, y mi interior en orden á Kir-hareseth.', 'deep-space'),
('Old', 'Isaías', 16, 12, 'Y acaecerá, que cuando Moab pareciere que está cansado sobre los altos, entonces vendrá á su santuario á orar, y no le valdrá.', 'deep-space'),
('Old', 'Isaías', 16, 13, 'Esta es la palabra que pronunció Jehová sobre Moab desde aquel tiempo.', 'deep-space'),
('Old', 'Isaías', 16, 14, 'Empero ahora Jehová ha hablado, diciendo: Dentro de tres años, como años de mozo de soldada, será abatida la gloria de Moab, con toda su grande multitud: y los residuos serán pocos, pequeños, y no fuertes.', 'deep-space'),
('Old', 'Isaías', 17, 1, 'CARGA de Damasco.', 'deep-space'),
('Old', 'Isaías', 17, 2, 'Las ciudades de Aroer desamparadas, en majadas se tornarán; dormirán allí, y no habrá quien los espante.', 'deep-space'),
('Old', 'Isaías', 17, 3, 'Y cesará el socorro de Ephraim, y el reino de Damasco; y lo que quedare de Siria, será como la gloria de los hijos de Israel, dice Jehová de los ejércitos.', 'deep-space'),
('Old', 'Isaías', 17, 4, 'Y será que en aquel tiempo la gloria de Jacob se atenuará, y enflaqueceráse la grosura de su carne.', 'deep-space'),
('Old', 'Isaías', 17, 5, 'Y será como cuando el segador coge la mies, y con su brazo siega las espigas: será también como el que coge espigas en el valle de Rephaim.', 'deep-space'),
('Old', 'Isaías', 17, 6, 'Y quedarán en él rebuscos, como cuando sacuden el aceituno, dos ó tres granos en la punta del ramo, cuatro ó cinco en sus ramas fructíferas, dice Jehová Dios de Israel.', 'deep-space'),
('Old', 'Isaías', 17, 7, 'En aquel día mirará el hombre á su Hacedor, y sus ojos contemplarán al Santo de Israel.', 'deep-space'),
('Old', 'Isaías', 17, 8, 'Y no mirará á los altares que hicieron sus manos, ni mirará á lo que hicieron sus dedos, ni á los bosques, ni á las imágenes del sol.', 'deep-space'),
('Old', 'Isaías', 17, 9, 'En aquel día las ciudades de su fortaleza serán como los frutos que quedan en los pimpollos y en las ramas, las cuales fueron dejadas á causa de los hijos de Israel; y habrá asolamiento.', 'deep-space'),
('Old', 'Isaías', 17, 10, 'Porque te olvidaste del Dios de tu salud, y no te acordaste de la roca de tu fortaleza; por tanto plantarás plantas hermosas, y sembrarás sarmiento extraño.', 'deep-space'),
('Old', 'Isaías', 17, 11, 'El día que las plantares, las harás crecer, y harás que tu simiente brote de mañana; mas la cosecha será arrebatada en el día del coger, y del dolor desesperado.', 'deep-space'),
('Old', 'Isaías', 17, 12, '¡Ay! multitud de muchos pueblos que harán ruido como estruendo de la mar: y murmullo de naciones hará alboroto como murmurio de muchas aguas.', 'deep-space'),
('Old', 'Isaías', 17, 13, 'Los pueblos harán estrépito á manera de ruido de grandes aguas: mas Dios le reprenderá, y huirá lejos; será ahuyentado como el tamo de los montes delante del viento, y como el polvo delante del torbellino.', 'deep-space'),
('Old', 'Isaías', 17, 14, 'Al tiempo de la tarde he aquí turbación; y antes de la mañana ya no es. Esta es la parte de los que nos huellan, y la suerte de los que nos saquean.', 'deep-space'),
('Old', 'Isaías', 18, 1, '¡AY de la tierra que hace sombra con las alas, que está tras los ríos de Etiopía;', 'deep-space'),
('Old', 'Isaías', 18, 2, 'Que envía mensajeros por la mar, y en navíos de junco sobre las aguas! Andad, ligeros mensajeros, á la gente tirada y repelada, al pueblo asombroso desde su principio y después; gente harta de esperar y hollada, cuya tierra destruyeron los ríos.', 'deep-space');