INSERT INTO scriptures (testament, book_name, chapter, verse_number, content, visual_theme) VALUES
('Old', 'Zacarías', 9, 1, 'CARGA de la palabra de Jehová contra tierra de Hadrach, y de Damasco su reposo: porque á Jehová están vueltos los ojos de los hombres, y de todas las tribus de Israel.', 'cosmic'),
('Old', 'Zacarías', 9, 2, 'Y también Hamath tendrá término en ella; Tiro y Sidón, aunque muy sabia sea.', 'cosmic'),
('Old', 'Zacarías', 9, 3, 'Bien que Tiro se edificó fortaleza, y amontonó plata como polvo, y oro como lodo de las calles,', 'cosmic'),
('Old', 'Zacarías', 9, 4, 'He aquí, el Señor la empobrecerá, y herirá en la mar su fortaleza, y ella será consumida de fuego.', 'cosmic'),
('Old', 'Zacarías', 9, 5, 'Ascalón verá, y temerá; Gaza también, y se dolerá en gran manera: asimismo Ecrón, porque su esperanza será confundida; y de Gaza perecerá el rey, y Ascalón no será habitada.', 'cosmic'),
('Old', 'Zacarías', 9, 6, 'Y habitará en Asdod un extranjero, y yo talaré la soberbia de los Palestinos:', 'cosmic'),
('Old', 'Zacarías', 9, 7, 'Y quitaré sus sangres de su boca, y sus abominaciones de sus dientes, y quedarán ellos también para nuestro Dios, y serán como capitanes en Judá, y Ecrón como el Jebuseo.', 'cosmic'),
('Old', 'Zacarías', 9, 8, 'Y seré real de ejército á mi casa, á causa del que va y del que viene: y no pasará más sobre ellos angustiador; porque ahora miré con mis ojos.', 'cosmic'),
('Old', 'Zacarías', 9, 9, 'Alégrate mucho, hija de Sión; da voces de júbilo, hija de Jerusalem: he aquí, tu rey vendrá á ti, justo y salvador, humilde, y cabalgando sobre un asno, así sobre un pollino hijo de asna.', 'cosmic'),
('Old', 'Zacarías', 9, 10, 'Y de Ephraim destruiré los carros, y los caballo de Jerusalem; y los arcos de guerra serán quebrados: y hablará paz á las gentes; y su señorío será de mar á mar, y desde el río hasta los fines de la tierra.', 'cosmic'),
('Old', 'Zacarías', 9, 11, 'Y tú también por la sangre de tu pacto serás salva; yo he sacado tus presos del aljibe en que no hay agua.', 'cosmic'),
('Old', 'Zacarías', 9, 12, 'Tornaos á la fortaleza, oh presos de esperanza: hoy también os anuncio que os daré doblado.', 'cosmic'),
('Old', 'Zacarías', 9, 13, 'Porque entesado he para mí á Judá como arco, henchí á Ephraim; y despertaré tus hijos, oh Sión, contra tus hijos, oh Grecia, y te pondré como espada de valiente.', 'cosmic'),
('Old', 'Zacarías', 9, 14, 'Y Jehová será visto sobre ellos, y su dardo saldrá como relámpago: y el Señor Jehová tocará trompeta, é irá como torbellinos del austro.', 'cosmic'),
('Old', 'Zacarías', 9, 15, 'Jehová de los ejércitos los amparará, y ellos devorarán, y sujetarán á las piedras de la honda, y beberán y harán estrépido como tomados del vino; y se llenarán como cuenco, ó como los lados del altar.', 'cosmic'),
('Old', 'Zacarías', 9, 16, 'Y los salvará en aquel día Jehová su Dios como á rebaño de su pueblo: porque serán engrandecidos en su tierra como piedras de corona.', 'cosmic'),
('Old', 'Zacarías', 9, 17, 'Porque ¡cuánta es su bondad, y cuánta su hermosura! El trigo alegrará á los mancebos, y el vino á las doncellas.', 'cosmic'),
('Old', 'Zacarías', 10, 1, 'PEDID á Jehová lluvia en la sazón tardía: Jehová hará relámpagos, y os dará lluvia abundante, y hierba en el campo á cada uno.', 'cosmic'),
('Old', 'Zacarías', 10, 2, 'Porque las imágenes han hablado vanidad, y los adivinos han visto mentira, y han hablado sueños vanos, en vano consuelan: por lo cual se fueron ellos como ovejas, fueron humillados porque no tuvieron pastor.', 'cosmic'),
('Old', 'Zacarías', 10, 3, 'Contra los pastores se ha encendido mi enojo, y castigaré los machos de cabrío: mas Jehová de los ejércitos visitará su rebaño, la casa de Judá, y tornarálos como su caballo de honor en la guerra.', 'cosmic'),
('Old', 'Zacarías', 10, 4, 'De él saldrá el ángulo, de él la clavija, de él el arco de la guerra, de él también todo apremiador.', 'cosmic'),
('Old', 'Zacarías', 10, 5, 'Y serán como valientes, que en la batalla pisan al enemigo en el lodo de las calles; y pelearán, porque Jehová será con ellos; y los que cabalgan en caballos serán avergonzados.', 'cosmic'),
('Old', 'Zacarías', 10, 6, 'Porque yo fortificaré la casa de Judá, y guardaré la casa de José; y harélos volver, porque de ellos tendré piedad; y serán como si no los hubiera desechado; porque yo soy Jehová su Dios, que los oiré.', 'cosmic'),
('Old', 'Zacarías', 10, 7, 'Y será Ephraim como valiente, y alegraráse su corazón como de vino: sus hijos también verán y se alegrarán; su corazón se gozará en Jehová.', 'cosmic'),
('Old', 'Zacarías', 10, 8, 'Yo les silbaré y los juntaré, porque los he redimido; y serán multiplicados como fueron multiplicados.', 'cosmic'),
('Old', 'Zacarías', 10, 9, 'Bien que los sembraré entre los pueblos, aun en lejanos países se acordarán de mí; y vivirán con sus hijos, y tornarán.', 'cosmic'),
('Old', 'Zacarías', 10, 10, 'Porque yo los tornaré de la tierra de Egipto, y los recogeré de la Asiria; y traerélos á la tierra de Galaad y del Líbano, y no les bastará.', 'cosmic'),
('Old', 'Zacarías', 10, 11, 'Y la tribulación pasará por la mar, y en la mar herirá las ondas, y se secarán todas las honduras del río: y la soberbia de Assur será derribada, y se perderá el cetro de Egipto.', 'cosmic'),
('Old', 'Zacarías', 10, 12, 'Y yo los fortificaré en Jehová, y caminarán en su nombre, dice Jehová.', 'cosmic'),
('Old', 'Zacarías', 11, 1, 'OH Líbano, abre tus puertas, y queme fuego tus cedros.', 'cosmic'),
('Old', 'Zacarías', 11, 2, 'Aulla, oh haya, porque el cedro cayó, porque los magníficos son talados. Aullad, alcornoques de Basán, porque el fuerte monte es derribado.', 'cosmic'),
('Old', 'Zacarías', 11, 3, 'Voz de aullido de pastores, porque su magnificencia es asolada; estruendo de bramidos de cachorros de leones, porque la soberbia del Jordán es destruída.', 'cosmic'),
('Old', 'Zacarías', 11, 4, 'Así ha dicho Jehová mi Dios: Apacienta las ovejas de la matanza;', 'cosmic'),
('Old', 'Zacarías', 11, 5, 'A las cuales mataban sus compradores, y no se tenían por culpables; y el que las vendía, decía: Bendito sea Jehová, que he enriquecido; ni sus pastores tenían piedad de ellas.', 'cosmic'),
('Old', 'Zacarías', 11, 6, 'Por tanto, no más tendré piedad de los moradores de la tierra, dice Jehová: porque he aquí, yo entregaré los hombres, cada cual en mano de su compañero, y en mano de su rey; y quebrantarán la tierra, y yo no libraré de sus manos.', 'cosmic'),
('Old', 'Zacarías', 11, 7, 'Apacenté pues las ovejas de la matanza, es á saber, los pobres del rebaño. Y me tomé dos cayados; al uno puse por nombre Suavidad, y al otro Ataduras; y apacenté las ovejas.', 'cosmic'),
('Old', 'Zacarías', 11, 8, 'E hice matar tres pastores en un mes, y mi alma se angustió por ellos, y también el alma de ellos me aborreció á mí.', 'cosmic'),
('Old', 'Zacarías', 11, 9, 'Y dije: No os apacentaré; la que muriere, muera; y la que se perdiere, se pierda; y las que quedaren, que cada una coma la carne de su compañera.', 'cosmic'),
('Old', 'Zacarías', 11, 10, 'Tomé luego mi cayado Suavidad, y quebrélo, para deshacer mi pacto que concerté con todos los pueblos.', 'cosmic'),
('Old', 'Zacarías', 11, 11, 'Y fué deshecho en ese día, y así conocieron los pobres del rebaño que miran á mí, que era palabra de Jehová.', 'cosmic'),
('Old', 'Zacarías', 11, 12, 'Y díjeles: Si os parece bien, dadme mi salario; y si no, dejadlo. Y pesaron para mi salario treinta piezas de plata.', 'cosmic'),
('Old', 'Zacarías', 11, 13, 'Y díjome Jehová: Echalo al tesorero, hermoso precio con que me han apreciado. Y tomé las treinta piezas de plata, y echélas en la casa de Jehová al tesorero.', 'cosmic'),
('Old', 'Zacarías', 11, 14, 'Quebré luego el otro mi cayado Ataduras, para romper la hermandad entre Judá é Israel.', 'cosmic'),
('Old', 'Zacarías', 11, 15, 'Y díjome Jehová: Toma aún el hato de un pastor insensato;', 'cosmic'),
('Old', 'Zacarías', 11, 16, '(11-15) porque he aquí, yo levanto pastor en la tierra, que no visitará las perdidas, no buscará la pequeña, no curará la perniquebrada, ni llevará la cansada á cuestas; sino que se comerá la carne de la gruesa, y romperá sus uñas.', 'cosmic'),
('Old', 'Zacarías', 11, 17, '(11-16) Mal haya el pastor de nada, que deja el ganado. Espada sobre su brazo, y sobre su ojo derecho: del todo se secará su brazo, y enteramente será su ojo derecho oscurecido.', 'cosmic'),
('Old', 'Zacarías', 12, 1, 'CARGA de la palabra de Jehová acerca de Israel. Jehová, que extiende los cielos, y funda la tierra, y forma el espíritu del hombre dentro de él, ha dicho:', 'cosmic'),
('Old', 'Zacarías', 12, 2, 'He aquí, yo pongo á Jerusalem por vaso de temblor á todos los pueblos de alrededor cuando estén en el sitio contra Judá y contra Jerusalem.', 'cosmic'),
('Old', 'Zacarías', 12, 3, 'Y será en aquel día, que yo pondré á Jerusalem por piedra pesada á todos los pueblos: todos los que se la cargaren, serán despedazados, bien que todas las gentes de la tierra se juntarán contra ella.', 'cosmic'),
('Old', 'Zacarías', 12, 4, 'En aquel día, dice Jehová, heriré con aturdimiento á todo caballo, y con locura al que en él sube; mas sobre la casa de Judá abriré mis ojos, y á todo caballo de los pueblos heriré con ceguera.', 'cosmic'),
('Old', 'Zacarías', 12, 5, 'Y los capitanes de Judá dirán en su corazón: Mi fuerza son los moradores de Jerusalem en Jehová de los ejércitos su Dios.', 'cosmic'),
('Old', 'Zacarías', 12, 6, 'En aquel día pondré los capitanes de Judá como un brasero de fuego en leña, y como una hacha de fuego en gavillas; y consumirán á diestra y á siniestra todos los pueblos alrededor: y Jerusalem será otra vez habitada en su lugar, en Jerusalem.', 'cosmic'),
('Old', 'Zacarías', 12, 7, 'Y librará Jehová las tiendas de Judá primero, porque la gloria de la casa de David y del morador de Jerusalem no se engrandezca sobre Judá.', 'cosmic'),
('Old', 'Zacarías', 12, 8, 'En aquel día Jehová defenderá al morador de Jerusalem: y el que entre ellos fuere flaco, en aquel tiempo será como David; y la casa de David como ángeles, como el ángel de Jehová delante de ellos.', 'cosmic'),
('Old', 'Zacarías', 12, 9, 'Y será que en aquel día yo procuraré quebrantar todas las gentes que vinieren contra Jerusalem.', 'cosmic'),
('Old', 'Zacarías', 12, 10, 'Y derramaré sobre la casa de David, y sobre los moradores de Jerusalem, espíritu de gracia y de oración; y mirarán á mí, á quien traspasaron, y harán llanto sobre él, como llanto sobre unigénito, afligiéndose sobre él como quien se aflige sobre primogénito.', 'cosmic'),
('Old', 'Zacarías', 12, 11, 'En aquel día habrá gran llanto en Jerusalem, como el llanto de Adadrimón en el valle de Megiddo.', 'cosmic'),
('Old', 'Zacarías', 12, 12, 'Y la tierra lamentará, cada linaje de por sí; el linaje de la casa de David por sí, y sus mujeres por sí; el linaje de la casa de Nathán por sí, y sus mujeres por sí;', 'cosmic'),
('Old', 'Zacarías', 12, 13, 'El linaje de la casa de Leví por sí, y sus mujeres por sí; el linaje de Simei por sí, y sus mujeres por sí;', 'cosmic'),
('Old', 'Zacarías', 12, 14, 'Todos los otros linajes, los linajes por sí, y sus mujeres por sí.', 'cosmic'),
('Old', 'Zacarías', 13, 1, 'EN aquel tiempo habrá manantial abierto para la casa de David y para los moradores de Jerusalem, para el pecado y la inmundicia.', 'cosmic'),
('Old', 'Zacarías', 13, 2, 'Y será en aquel día, dice Jehová de los ejércitos, que talaré de la tierra los nombres de las imágenes, y nunca más vendrán en memoria: y también haré talar de la tierra los profetas, y espíritu de inmundicia.', 'cosmic'),
('Old', 'Zacarías', 13, 3, 'Y será que cuando alguno más profetizare, diránle su padre y su madre que lo engendraron: No vivirás, porque has hablado mentira en el nombre de Jehová: y su padre y su madre que lo engendraron, le alancearán cuando profetizare.', 'cosmic'),
('Old', 'Zacarías', 13, 4, 'Y será en aquel tiempo, que todos los profetas se avergonzarán de su visión cuando profetizaren; ni nunca más se vestirán de manto velloso para mentir', 'cosmic'),
('Old', 'Zacarías', 13, 5, 'Y dirá: No soy profeta; labrador soy de la tierra: porque esto aprendí del hombre desde mi juventud.', 'cosmic'),
('Old', 'Zacarías', 13, 6, 'Y le preguntarán: ¿Qué heridas son éstas en tus manos? Y él responderá: Con ellas fuí herido en casa de mis amigos.', 'cosmic'),
('Old', 'Zacarías', 13, 7, 'Levántate, oh espada, sobre el pastor, y sobre el hombre compañero mío, dice Jehová de los ejércitos. Hiere al pastor, y se derramarán las ovejas: mas tornaré mi mano sobre los chiquitos.', 'cosmic'),
('Old', 'Zacarías', 13, 8, 'Y acontecerá en toda la tierra, dice Jehová, que las dos partes serán taladas en ella, y se perderán; mas la tercera quedará en ella.', 'cosmic'),
('Old', 'Zacarías', 13, 9, 'Y meteré en el fuego la tercera parte, y los fundiré como se funde la plata, y probarélos como se prueba el oro. El invocará mi nombre, y yo le oiré, y diré: Pueblo mío: y él dirá: Jehová es mi Dios.', 'cosmic'),
('Old', 'Zacarías', 14, 1, 'HE aquí, el día de Jehová viene, y tus despojos serán repartidos en medio de ti.', 'cosmic'),
('Old', 'Zacarías', 14, 2, 'Porque yo reuniré todas las gentes en batalla contra Jerusalem; y la ciudad será tomada, y saqueadas serán las casas, y forzadas las mujeres: y la mitad de la ciudad irá en cautiverio, mas el resto del pueblo no será talado de la ciudad.', 'cosmic'),
('Old', 'Zacarías', 14, 3, 'Después saldrá Jehová, y peleará con aquellas gentes, como peleó el día de la batalla.', 'cosmic'),
('Old', 'Zacarías', 14, 4, 'Y afirmaránse sus pies en aquel día sobre el monte de las Olivas, que está en frente de Jerusalem á la parte de oriente: y el monte de las Olivas, se partirá por medio de sí hacia el oriente y hacia el occidente haciendo un muy grande valle; y la mitad del monte se apartará hacia el norte, y la otra mitad hacia el mediodía.', 'cosmic'),
('Old', 'Zacarías', 14, 5, 'Y huiréis al valle de los montes; porque el valle de los montes llegará hasta Hasal; y huiréis de la manera que huisteis por causa del terremoto en los días de Uzzías, rey de Judá: y vendrá Jehová mi Dios, y con él todos los santos.', 'cosmic'),
('Old', 'Zacarías', 14, 6, 'Y acontecerá que en ese día no habrá luz clara, ni oscura.', 'cosmic'),
('Old', 'Zacarías', 14, 7, 'Y será un día, el cual es conocido de Jehová, que ni será día ni noche; mas acontecerá que al tiempo de la tarde habrá luz.', 'cosmic'),
('Old', 'Zacarías', 14, 8, 'Acontecerá también en aquel día, que saldrán de Jerusalem aguas vivas; la mitad de ellas hacia la mar oriental, y la otra mitad hacia la mar occidental, en verano y en invierno.', 'cosmic'),
('Old', 'Zacarías', 14, 9, 'Y Jehová será rey sobre toda la tierra. En aquel día Jehová será uno, y uno su nombre.', 'cosmic'),
('Old', 'Zacarías', 14, 10, 'Y toda la tierra se tornará como llanura desde Gabaa hasta Rimmón al mediodía de Jerusalem: y ésta será enaltecida, y habitarse ha en su lugar desde la puerta de Benjamín hasta el lugar de la puerta primera, hasta la puerta de los rincones; y desde la torre de Hananeel hasta los lagares del rey.', 'cosmic'),
('Old', 'Zacarías', 14, 11, 'Y morarán en ella, y nunca más será anatema: sino que será Jerusalem habitada confiadamente.', 'cosmic'),
('Old', 'Zacarías', 14, 12, 'Y esta será la plaga con que herirá Jehová á todos los pueblos que pelearon contra Jerusalem: la carne de ellos se disolverá estando ellos sobre sus pies, y se consumirán sus ojos en sus cuencas, y su lengua se les deshará en su boca.', 'cosmic'),
('Old', 'Zacarías', 14, 13, 'Y acontecerá en aquel día que habrá en ellos gran quebrantamiento de Jehová; porque trabará cada uno de la mano de su compañero, y su mano echará contra la mano de su compañero.', 'cosmic'),
('Old', 'Zacarías', 14, 14, 'Y Judá también peleará en Jerusalem. Y serán reunidas las riquezas de todas las gentes de alrededor: oro, y plata, y ropas de vestir, en grande abundancia.', 'cosmic'),
('Old', 'Zacarías', 14, 15, 'Y tal como esto será la plaga de los caballos, de los mulos, de los camellos, de los asnos, y de todas las bestias que estuvieren en aquellos campamentos.', 'cosmic'),
('Old', 'Zacarías', 14, 16, 'Y todos los que quedaren de las gentes que vinieron contra Jerusalem subirán de año en año á adorar al Rey, Jehová de los ejércitos, y á celebrar la fiesta de las Cabañas.', 'cosmic'),
('Old', 'Zacarías', 14, 17, 'Y acontecerá, que los de las familias de la tierra que no subieren á Jerusalem á adorar al Rey, Jehová de los ejércitos, no vendrá sobre ellos lluvia.', 'cosmic'),
('Old', 'Zacarías', 14, 18, 'Y si la familia de Egipto no subiere, y no vinere, sobre ellos no habrá lluvia; vendrá la plaga con que Jehová herirá las gentes que no subieren á celebrar la fiesta de las Cabañas.', 'cosmic'),
('Old', 'Zacarías', 14, 19, 'Esta será la pena del pecado de Egipto, y del pecado de todas las gentes que no subieran á celebrar la fiesta de las Cabañas.', 'cosmic'),
('Old', 'Zacarías', 14, 20, 'En aquel tiempo estará sobre las campanillas de los caballos: SANTIDAD A JEHOVA; y las ollas en la casa de Jehová serán como los tazones delante del altar.', 'cosmic'),
('Old', 'Zacarías', 14, 21, 'Y será toda olla en Jerusalem y en Judá santidad á Jehová de los ejércitos: y todos los que sacrificaren, vendrán y tomarán de ellas, y cocerán en ellas: y no habrá más Cananeo alguno en la casa de Jehová de los ejércitos en aquel tiempo.', 'cosmic'),
('Old', 'Malaquías', 1, 1, 'CARGA de la palabra de Jehová contra Israel, por mano de Malaquías.', 'cosmic'),
('Old', 'Malaquías', 1, 2, 'Yo os he amado, dice Jehová: y dijisteis: ¿En qué nos amaste? ¿No era Esaú hermano de Jacob, dice Jehová, y amé á Jacob,', 'cosmic'),
('Old', 'Malaquías', 1, 3, 'Y á Esaú aborrecí, y torné sus montes en asolamiento, y su posesión para los chacales del desierto?', 'cosmic'),
('Old', 'Malaquías', 1, 4, 'Cuando Edom dijere: Nos hemos empobrecido, mas tornemos á edificar lo arruinado; así ha dicho Jehová de los ejércitos: Ellos edificarán, y yo destruiré: y les llamarán Provincia de impiedad, y, Pueblo contra quien Jehová se airó para siempre.', 'cosmic'),
('Old', 'Malaquías', 1, 5, 'Y vuestros ojos lo verán, y diréis: Sea Jehová engrandecido sobre la provincia de Israel.', 'cosmic'),
('Old', 'Malaquías', 1, 6, 'El hijo honra al padre, y el siervo á su señor: si pues soy yo padre, ¿qué es de mi honra? y si soy señor, ¿qué es de mi temor?, dice Jehová de los ejércitos á vosotros, oh sacerdotes, que menospreciáis mi nombre. Y decís: ¿En qué hemos menospreciado tu nombre?', 'cosmic'),
('Old', 'Malaquías', 1, 7, 'Que ofrecéis sobre mi altar pan inmundo. Y dijisteis: ¿En qué te hemos amancillado? En que decís: La mesa de Jehová es despreciable.', 'cosmic'),
('Old', 'Malaquías', 1, 8, 'Y cuando ofrecéis el animal ciego para sacrificar, ¿no es malo? asimismo cuando ofrecéis el cojo ó el enfermo, ¿no es malo? Preséntalo pues á tu príncipe: ¿acaso se agradará de ti, ó le serás acepto? dice Jehová de los ejércitos.', 'cosmic'),
('Old', 'Malaquías', 1, 9, 'Ahora pues, orad á la faz de Dios que tenga piedad de nosotros: esto de vuestra mano vino: ¿le seréis agradables? dice Jehová de los ejércitos.', 'cosmic'),
('Old', 'Malaquías', 1, 10, '¿Quién también hay de vosotros que cierre las puertas ó alumbre mi altar de balde? Yo no recibo contentamiento en vosotros, dice Jehová de los ejércitos, ni de vuestra mano me será agradable el presente.', 'cosmic'),
('Old', 'Malaquías', 1, 11, 'Porque desde donde el sol nace hasta donde se pone, es grande mi nombre entre las gentes; y en todo lugar se ofrece á mi nombre perfume, y presente limpio: porque grande es mi nombre entre las gentes, dice Jehová de los ejércitos.', 'cosmic'),
('Old', 'Malaquías', 1, 12, 'Y vosotros lo habéis profanado cuando decís: Inmunda es la mesa de Jehová; y cuando hablan que su alimento es despreciable.', 'cosmic'),
('Old', 'Malaquías', 1, 13, 'Habéis además dicho: ¡Oh qué trabajo! y lo desechasteis, dice Jehová de los ejércitos; y trajisteis lo hurtado, ó cojo, ó enfermo, y presentasteis ofrenda. ¿Seráme acepto eso de vuesta mano? dice Jehová.', 'cosmic'),
('Old', 'Malaquías', 1, 14, 'Maldito el engañoso, que tiene macho en su rebaño, y promete, y sacrifica lo dañado á Jehová: porque yo soy Gran Rey, dice Jehová de los ejércitos, y mi nombre es formidable entre las gentes.', 'cosmic'),
('Old', 'Malaquías', 2, 1, 'AHORA pues, oh sacerdotes, á vosotros es este mandamiento.', 'cosmic'),
('Old', 'Malaquías', 2, 2, 'Si no oyereis, y si no acordareis dar gloria á mi nombre, ha dicho Jehová de los ejércitos, enviaré maldición sobre vosotros, y maldeciré vuestras bendiciones; y aun las he maldecido, porque no lo ponéis en vuestro corazón.', 'cosmic'),
('Old', 'Malaquías', 2, 3, 'He aquí, yo os daño la sementera, y esparciré el estiércol sobre vuestros rostros, el estiércol de vuestras solemnidades, y con él seréis removidos.', 'cosmic'),
('Old', 'Malaquías', 2, 4, 'Y sabréis que yo os envié este mandamiento, para que fuese mi pacto con Leví, ha dicho Jehová de los ejércitos.', 'cosmic'),
('Old', 'Malaquías', 2, 5, 'Mi pacto fué con él de vida y de paz, las cuales cosas yo le dí por el temor; porque me temió, y delante de mi nombre estuvo humillado.', 'cosmic'),
('Old', 'Malaquías', 2, 6, 'La Ley de verdad estuvo en su boca, é iniquidad no fué hallada en sus labios: en paz y en justicia anduvo conmigo, y á muchos hizo apartar de la iniquidad.', 'cosmic'),
('Old', 'Malaquías', 2, 7, 'Porque los labios de los sacerdotes han de guardar la sabiduría, y de su boca buscarán la ley; porque mensajero es de Jehová de los ejércitos.', 'cosmic'),
('Old', 'Malaquías', 2, 8, 'Mas vosotros os habéis apartado del camino; habéis hecho tropezar á muchos en la ley; habéis corrompido el pacto de Leví, dice Jehová de los ejércitos.', 'cosmic'),
('Old', 'Malaquías', 2, 9, 'Por tanto, yo también os torné viles y bajos á todo el pueblo, según que vosotros no habéis guardado mis caminos, y en la ley tenéis acepción de personas.', 'cosmic'),
('Old', 'Malaquías', 2, 10, '¿No tenemos todos un mismo padre? ¿No nos ha criado un mismo Dios? ¿Por qué menospreciaremos cada uno á su hermano, quebrantando el pacto de nuestros padres?', 'cosmic'),
('Old', 'Malaquías', 2, 11, 'Prevaricó Judá, y en Israel y en Jerusalem ha sido cometida abominación; porque Judá ha profanado la santidad de Jehová que amó, y casádose con hija de dios extraño.', 'cosmic'),
('Old', 'Malaquías', 2, 12, 'Jehová talará de las tiendas de Jacob al hombre que hiciere esto, al que vela, y al que responde, y al que ofrece presente á Jehová de los ejércitos.', 'cosmic'),
('Old', 'Malaquías', 2, 13, 'Y esta otra vez haréis cubrir el altar de Jehová de lágrimas, de llanto, y de clamor; así que no miraré más á presente, para aceptarlo con gusto de vuestra mano.', 'cosmic'),
('Old', 'Malaquías', 2, 14, 'Mas diréis: ¿Por qué? Porque Jehová ha atestiguado entre ti y la mujer de tu mocedad, contra la cual tú has sido desleal, siendo ella tu compañera, y la mujer de tu pacto.', 'cosmic'),
('Old', 'Malaquías', 2, 15, 'Pues qué ¿no hizo él uno solo aunque tenía la abundancia del espíritu? ¿Y por qué uno? Para que procurara una simiente de Dios. Guardaos pues en vuestros espíritus, y contra la mujer de vuestra mocedad no seáis desleales.', 'cosmic'),
('Old', 'Malaquías', 2, 16, 'Porque Jehová Dios de Israel ha dicho que él aborrece que sea repudiada; y cubra la iniquidad con su vestido, dijo Jehová de los ejércitos. Guardaos pues en vuestros espíritus, y no seáis desleales.', 'cosmic'),
('Old', 'Malaquías', 2, 17, 'Habéis hecho cansar á Jehová con vuestras palabras. Y diréis: ¿En qué le hemos cansado? Cuando decís: Cualquiera que mal hace agrada á Jehová, y en los tales toma contentamiento: de otra manera, ¿dónde está el Dios de juicio?', 'cosmic'),
('Old', 'Malaquías', 3, 1, 'HE aquí, yo envío mi mensajero, el cual preparará el camino delante de mí: y luego vendrá á su templo el Señor á quien vosotros buscáis, y el ángel del pacto, á quien deseáis vosotros. He aquí viene, ha dicho Jehová de los ejércitos.', 'cosmic'),
('Old', 'Malaquías', 3, 2, '¿Y quién podrá sufrir el tiempo de su venida? ó ¿quién podrá estar cuando él se mostrará? Porque él es como fuego purificador, y como jabón de lavadores.', 'cosmic'),
('Old', 'Malaquías', 3, 3, 'Y sentarse ha para afinar y limpiar la plata: porque limpiará los hijos de Leví, los afinará como á oro y como á plata; y ofrecerán á Jehová ofrenda con justicia.', 'cosmic'),
('Old', 'Malaquías', 3, 4, 'Y será suave á Jehová la ofrenda de Judá y de Jerusalem, como en los días pasados, y como en los años antiguos.', 'cosmic'),
('Old', 'Malaquías', 3, 5, 'Y llegarme he á vosotros á juicio; y seré pronto testigo contra los hechiceros y adúlteros; y contra los que juran mentira, y los que detienen el salario del jornalero, de la viuda, y del huérfano, y los que hacen agravio al extranjero, no teniendo temor de mí, dice Jehová de los ejércitos.', 'cosmic'),
('Old', 'Malaquías', 3, 6, 'Porque yo Jehová, no me mudo; y así vosotros, hijos de Jacob, no habéis sido consumidos.', 'cosmic'),
('Old', 'Malaquías', 3, 7, 'Desde los días de vuestros padres os habéis apartado de mis leyes, y no las guardasteis. Tornaos á mí, y yo me tornaré á vosotros, ha dicho Jehová de los ejércitos. Mas dijisteis: ¿En qué hemos de tornar?', 'cosmic'),
('Old', 'Malaquías', 3, 8, '¿Robará el hombre á Dios? Pues vosotros me habéis robado. Y dijisteis: ¿En qué te hemos robado? Los diezmos y las primicias.', 'cosmic'),
('Old', 'Malaquías', 3, 9, 'Malditos sois con maldición, porque vosotros, la nación toda, me habéis robado.', 'cosmic'),
('Old', 'Malaquías', 3, 10, 'Traed todos los diezmos al alfolí, y haya alimento en mi casa; y probadme ahora en esto, dice Jehová de los ejércitos, si no os abriré las ventanas de los cielos, y vaciaré sobre vosotros bendición hasta que sobreabunde.', 'cosmic'),
('Old', 'Malaquías', 3, 11, 'Increparé también por vosotros al devorador, y no os corromperá el fruto de la tierra; ni vuestra vid en el campo abortará, dice Jehová de los ejércitos.', 'cosmic'),
('Old', 'Malaquías', 3, 12, 'Y todas las gentes os dirán bienaventurados; porque seréis tierra deseable, dice Jehová de los ejércitos.', 'cosmic'),
('Old', 'Malaquías', 3, 13, 'Vuestras palabras han prevalecido contra mí, dice Jehová. Y dijisteis: ¿Qué hemos hablado contra ti?', 'cosmic'),
('Old', 'Malaquías', 3, 14, 'Habéis dicho: Por demás es servir á Dios; ¿y qué aprovecha que guardemos su ley, y que andemos tristes delante de Jehová de los ejércitos?', 'cosmic'),
('Old', 'Malaquías', 3, 15, 'Decimos pues ahora, que bienaventurados los soberbios, y también que los que hacen impiedad son los prosperados: bien que tentaron á Dios, escaparon.', 'cosmic'),
('Old', 'Malaquías', 3, 16, 'Entonces los que temen á Jehová hablaron cada uno á su compañero; y Jehová escuchó y oyó, y fué escrito libro de memoria delante de él para los que temen á Jehová, y para los que piensan en su nombre.', 'cosmic'),
('Old', 'Malaquías', 3, 17, 'Y serán para mí especial tesoro, ha dicho Jehová de los ejércitos, en el día que yo tengo de hacer: y perdonarélos como el hombre que perdona á su hijo que le sirve.', 'cosmic'),
('Old', 'Malaquías', 3, 18, 'Entonces os tornaréis, y echaréis de ver la diferencia entre el justo y el malo, entre el que sirve á Dios y el que no le sirve.', 'cosmic'),
('Old', 'Malaquías', 4, 1, 'PORQUE he aquí, viene el día ardiente como un horno; y todos los soberbios, y todos los que hacen maldad, serán estopa; y aquel día que vendrá, los abrasará, ha dicho Jehová de los ejércitos, el cual no les dejará ni raíz ni rama.', 'cosmic'),
('Old', 'Malaquías', 4, 2, 'Mas á vosotros los que teméis mi nombre, nacerá el Sol de justicia, y en sus alas traerá salud: y saldréis, y saltaréis como becerros de la manada.', 'cosmic'),
('Old', 'Malaquías', 4, 3, 'Y hollaréis á los malos, los cuales serán ceniza bajo las plantas de vuestros pies, en el día que yo hago, ha dicho Jehová de los ejércitos.', 'cosmic'),
('Old', 'Malaquías', 4, 4, 'Acordaos de la ley de Moisés mi siervo, al cual encargué en Horeb ordenanzas y leyes para todo Israel.', 'cosmic'),
('Old', 'Malaquías', 4, 5, 'He aquí, yo os envío á Elías el profeta, antes que venga el día de Jehová grande y terrible.', 'cosmic'),
('Old', 'Malaquías', 4, 6, 'El convertirá el corazón de los padres á los hijos, y el corazón de los hijos á los padres: no sea que yo venga, y con destrucción hiera la tierra.', 'cosmic'),
('New', 'Mateo', 1, 1, 'LIBRO de la generación de Jesucristo, hijo de David, hijo de Abraham.', 'divine-light'),
('New', 'Mateo', 1, 2, 'Abraham engendró á Isaac: é Isaac engendró á Jacob: y Jacob engendró á Judas y á sus hermanos:', 'divine-light'),
('New', 'Mateo', 1, 3, 'Y Judas engendró de Thamar á Phares y á Zara: y Phares engendró á Esrom: y Esrom engendró á Aram:', 'divine-light'),
('New', 'Mateo', 1, 4, 'Y Aram engendró á Aminadab: y Aminadab engendró á Naassón: y Naassón engendró á Salmón:', 'divine-light'),
('New', 'Mateo', 1, 5, 'Y Salmón engendró de Rachâb á Booz, y Booz engendró de Ruth á Obed y Obed engendró á Jessé:', 'divine-light'),
('New', 'Mateo', 1, 6, 'Y Jessé engendró al rey David: y el rey David engendró á Salomón de la que fué mujer de Urías:', 'divine-light'),
('New', 'Mateo', 1, 7, 'Y Salomón engendró á Roboam: y Roboam engendró á Abía: y Abía engendró á Asa:', 'divine-light'),
('New', 'Mateo', 1, 8, 'Y Asa engendró á Josaphat: y Josaphat engendró á Joram: y Joram engendró á Ozías:', 'divine-light'),
('New', 'Mateo', 1, 9, 'Y Ozías engendró á Joatam: y Joatam engendró á Achâz: y Achâz engendró á Ezechîas:', 'divine-light'),
('New', 'Mateo', 1, 10, 'Y Ezechîas engendró á Manasés: y Manasés engendró á Amón: y Amón engendró á Josías:', 'divine-light'),
('New', 'Mateo', 1, 11, 'Y Josías engendró á Jechônías y á sus hermanos, en la transmigración de Babilonia.', 'divine-light'),
('New', 'Mateo', 1, 12, 'Y después de la transmigración de Babilonia, Jechônías engendró á Salathiel: y Salathiel engendró á Zorobabel:', 'divine-light'),
('New', 'Mateo', 1, 13, 'Y Zorobabel engendró á Abiud: y Abiud engendró á Eliachîm: y Eliachîm engendró á Azor:', 'divine-light'),
('New', 'Mateo', 1, 14, 'Y Azor engendró á Sadoc: y Sadoc engendró á Achîm: y Achîm engendró á Eliud:', 'divine-light'),
('New', 'Mateo', 1, 15, 'Y Eliud engendró á Eleazar: y Eleazar engendró á Mathán: y Mathán engendró á Jacob:', 'divine-light'),
('New', 'Mateo', 1, 16, 'Y Jacob engendró á José, marido de María, de la cual nació Jesús, el cual es llamado el Cristo.', 'divine-light'),
('New', 'Mateo', 1, 17, 'De manera que todas las generaciones desde Abraham hasta David son catorce generaciones: y desde David hasta la transmigración de Babilonia, catorce generaciones: y desde la transmigración de Babilonia hasta Cristo, catorce generaciones.', 'divine-light'),
('New', 'Mateo', 1, 18, 'Y el nacimiento de Jesucristo fué así: Que siendo María su madre desposada con José, antes que se juntasen, se halló haber concebido del Espíritu Santo.', 'divine-light'),
('New', 'Mateo', 1, 19, 'Y José su marido, como era justo, y no quisiese infamarla, quiso dejarla secretamente.', 'divine-light'),
('New', 'Mateo', 1, 20, 'Y pensando él en esto, he aquí el ángel del Señor le aparece en sueños, diciendo: José, hijo de David, no temas de recibir á María tu mujer, porque lo que en ella es engendrado, del Espíritu Santo es.', 'divine-light'),
('New', 'Mateo', 1, 21, 'Y parirá un hijo, y llamarás su nombre JESUS, porque él salvará á su pueblo de sus pecados.', 'divine-light'),
('New', 'Mateo', 1, 22, 'Todo esto aconteció para que se cumpliese lo que fué dicho por el Señor, por el profeta que dijo:', 'divine-light'),
('New', 'Mateo', 1, 23, 'He aquí la virgen concebirá y parirá un hijo, Y llamarás su nombre Emmanuel, que declarado, es: Con nosotros Dios.', 'divine-light'),
('New', 'Mateo', 1, 24, 'Y despertando José del sueño, hizo como el ángel del Señor le había mandado, y recibió á su mujer.', 'divine-light'),
('New', 'Mateo', 1, 25, 'Y no la conoció hasta que parió á su hijo primogénito: y llamó su nombre JESUS.', 'divine-light'),
('New', 'Mateo', 2, 1, 'Y COMO fué nacido Jesús en Bethlehem de Judea en días del rey Herodes, he aquí unos magos vinieron del oriente á Jerusalem,', 'divine-light'),
('New', 'Mateo', 2, 2, 'Diciendo: ¿Dónde está el Rey de los Judíos, que ha nacido? porque su estrella hemos visto en el oriente, y venimos á adorarle.', 'divine-light'),
('New', 'Mateo', 2, 3, 'Y oyendo esto el rey Herodes, se turbó, y toda Jerusalem con él.', 'divine-light'),
('New', 'Mateo', 2, 4, 'Y convocados todos los príncipes de los sacerdotes, y los escribas del pueblo, les preguntó dónde había de nacer el Cristo.', 'divine-light'),
('New', 'Mateo', 2, 5, 'Y ellos le dijeron: En Bethlehem de Judea; porque así está escrito por el profeta:', 'divine-light'),
('New', 'Mateo', 2, 6, 'Y tú, Bethlehem, de tierra de Judá, No eres muy pequeña entre los príncipes de Judá; Porque de ti saldrá un guiador, Que apacentará á mi pueblo Israel.', 'divine-light'),
('New', 'Mateo', 2, 7, 'Entonces Herodes, llamando en secreto á los magos, entendió de ellos diligentemente el tiempo del aparecimiento de la estrella;', 'divine-light'),
('New', 'Mateo', 2, 8, 'Y enviándolos á Bethlehem, dijo: Andad allá, y preguntad con diligencia por el niño; y después que le hallareis, hacédmelo saber, para que yo también vaya y le adore.', 'divine-light'),
('New', 'Mateo', 2, 9, 'Y ellos, habiendo oído al rey, se fueron: y he aquí la estrella que habían visto en el oriente, iba delante de ellos, hasta que llegando, se puso sobre donde estaba el niño.', 'divine-light'),
('New', 'Mateo', 2, 10, 'Y vista la estrella, se regocijaron con muy grande gozo.', 'divine-light'),
('New', 'Mateo', 2, 11, 'Y entrando en la casa, vieron al niño con su madre María, y postrándose, le adoraron; y abriendo sus tesoros, le ofrecieron dones, oro, é incienso y mirra.', 'divine-light'),
('New', 'Mateo', 2, 12, 'Y siendo avisados por revelación en sueños que no volviesen á Herodes, se volvieron á su tierra por otro camino.', 'divine-light'),
('New', 'Mateo', 2, 13, 'Y partidos ellos, he aquí el ángel del Señor aparece en sueños á José, diciendo: Levántate, y toma al niño y á su madre, y huye á Egipto, y estáte allá hasta que yo te lo diga; porque ha de acontecer, que Herodes buscará al niño para matarlo.', 'divine-light'),
('New', 'Mateo', 2, 14, 'Y él despertando, tomó al niño y á su madre de noche, y se fué á Egipto;', 'divine-light'),
('New', 'Mateo', 2, 15, 'Y estuvo allá hasta la muerte de Herodes: para que se cumpliese lo que fué dicho por el Señor, por el profeta que dijo: De Egipto llamé á mi Hijo.', 'divine-light'),
('New', 'Mateo', 2, 16, 'Herodes entonces, como se vió burlado de los magos, se enojó mucho, y envió, y mató á todos los niños que había en Bethlehem y en todos sus términos, de edad de dos años abajo, conforme al tiempo que había entendido de los magos.', 'divine-light'),
('New', 'Mateo', 2, 17, 'Entonces fué cumplido lo que se había dicho por el profeta Jeremías, que dijo:', 'divine-light'),
('New', 'Mateo', 2, 18, 'Voz fué oída en Ramá, Grande lamentación, lloro y gemido: Rachêl que llora sus hijos, Y no quiso ser consolada, porque perecieron.', 'divine-light'),
('New', 'Mateo', 2, 19, 'Mas muerto Herodes, he aquí el ángel del Señor aparece en sueños á José en Egipto,', 'divine-light'),
('New', 'Mateo', 2, 20, 'Diciendo: Levántate, y toma al niño y á su madre, y vete á tierra de Israel; que muertos son los que procuraban la muerte del niño.', 'divine-light'),
('New', 'Mateo', 2, 21, 'Entonces él se levantó, y tomó al niño y á su madre, y se vino á tierra de Israel.', 'divine-light'),
('New', 'Mateo', 2, 22, 'Y oyendo que Archelao reinaba en Judea en lugar de Herodes su padre, temió ir allá: mas amonestado por revelación en sueños, se fué á las partes de Galilea.', 'divine-light'),
('New', 'Mateo', 2, 23, 'Y vino, y habitó en la ciudad que se llama Nazaret: para que se cumpliese lo que fué dicho por los profetas, que había de ser llamado Nazareno.', 'divine-light'),
('New', 'Mateo', 3, 1, 'Y EN aquellos días vino Juan el Bautista predicando en el desierto de Judea,', 'divine-light'),
('New', 'Mateo', 3, 2, 'Y diciendo: Arrepentíos, que el reino de los cielos se ha acercado.', 'divine-light'),
('New', 'Mateo', 3, 3, 'Porque éste es aquel del cual fué dicho por el profeta Isaías, que dijo: Voz de uno que clama en el desierto: Aparejad el camino del Señor, Enderezad sus veredas.', 'divine-light'),
('New', 'Mateo', 3, 4, 'Y tenía Juan su vestido de pelos de camellos, y una cinta de cuero alrededor de sus lomos; y su comida era langostas y miel silvestre.', 'divine-light'),
('New', 'Mateo', 3, 5, 'Entonces salía á él Jerusalem, y toda Judea, y toda la provincia de alrededor del Jordán;', 'divine-light'),
('New', 'Mateo', 3, 6, 'Y eran bautizados de él en el Jordán, confesando sus pecados.', 'divine-light'),
('New', 'Mateo', 3, 7, 'Y viendo él muchos de los Fariseos y de los Saduceos, que venían á su bautismo, decíales: Generación de víboras, ¿quién os ha enseñado á huir de la ira que vendrá?', 'divine-light'),
('New', 'Mateo', 3, 8, 'Haced pues frutos dignos de arrepentimiento,', 'divine-light'),
('New', 'Mateo', 3, 9, 'Y no penséis decir dentro de vosotros: á Abraham tenemos por padre: porque yo os digo, que puede Dios despertar hijos á Abraham aun de estas piedras.', 'divine-light'),
('New', 'Mateo', 3, 10, 'Ahora, ya también la segur está puesta á la raíz de los árboles; y todo árbol que no hace buen fruto, es cortado y echado en el fuego.', 'divine-light'),
('New', 'Mateo', 3, 11, 'Yo á la verdad os bautizo en agua para arrepentimiento; mas el que viene tras mí, más poderoso es que yo; los zapatos del cual yo no soy digno de llevar; él os bautizará en Espíritu Santo y en fuego', 'divine-light'),
('New', 'Mateo', 3, 12, 'Su aventador en su mano está, y aventará su era: y allegará su trigo en el alfolí, y quemará la paja en fuego que nunca se apagará.', 'divine-light'),
('New', 'Mateo', 3, 13, 'Entonces Jesús vino de Galilea á Juan al Jordán, para ser bautizado de él.', 'divine-light'),
('New', 'Mateo', 3, 14, 'Mas Juan lo resistía mucho, diciendo: Yo he menester ser bautizado de ti, ¿y tú vienes á mí?', 'divine-light'),
('New', 'Mateo', 3, 15, 'Empero respondiendo Jesús le dijo: Deja ahora; porque así nos conviene cumplir toda justicia. Entonces le dejó.', 'divine-light'),
('New', 'Mateo', 3, 16, 'Y Jesús, después que fué bautizado, subió luego del agua; y he aquí los cielos le fueron abiertos, y vió al Espíritu de Dios que descendía como paloma, y venía sobre él.', 'divine-light'),
('New', 'Mateo', 3, 17, 'Y he aquí una voz de los cielos que decía: Este es mi Hijo amado, en el cual tengo contentamiento.', 'divine-light'),
('New', 'Mateo', 4, 1, 'ENTONCES Jesús fué llevado del Espíritu al desierto, para ser tentado del diablo.', 'divine-light'),
('New', 'Mateo', 4, 2, 'Y habiendo ayunado cuarenta días y cuarenta noches, después tuvo hambre.', 'divine-light'),
('New', 'Mateo', 4, 3, 'Y llegándose á él el tentador, dijo: Si eres Hijo de Dios, di que estas piedras se hagan pan.', 'divine-light'),
('New', 'Mateo', 4, 4, 'Mas él respondiendo, dijo: Escrito está: No con solo el pan vivirá el hombre, mas con toda palabra que sale de la boca de Dios.', 'divine-light'),
('New', 'Mateo', 4, 5, 'Entonces el diablo le pasa á la santa ciudad, y le pone sobre las almenas del templo,', 'divine-light'),
('New', 'Mateo', 4, 6, 'Y le dice: Si eres Hijo de Dios, échate abajo; que escrito está: A sus ángeles mandará por ti, Y te alzarán en las manos, Para que nunca tropieces con tu pie en piedra.', 'divine-light'),
('New', 'Mateo', 4, 7, 'Jesús le dijo: Escrito está además: No tentarás al Señor tu Dios.', 'divine-light'),
('New', 'Mateo', 4, 8, 'Otra vez le pasa el diablo á un monte muy alto, y le muestra todos los reinos del mundo, y su gloria,', 'divine-light'),
('New', 'Mateo', 4, 9, 'Y dícele: Todo esto te daré, si postrado me adorares.', 'divine-light'),
('New', 'Mateo', 4, 10, 'Entonces Jesús le dice: Vete, Satanás, que escrito está: Al Señor tu Dios adorarás y á él solo servirás.', 'divine-light'),
('New', 'Mateo', 4, 11, 'El diablo entonces le dejó: y he aquí los ángeles llegaron y le servían.', 'divine-light'),
('New', 'Mateo', 4, 12, 'Mas oyendo Jesús que Juan era preso, se volvió á Galilea;', 'divine-light'),
('New', 'Mateo', 4, 13, 'Y dejando á Nazaret, vino y habitó en Capernaum, ciudad marítima, en los confines de Zabulón y de Nephtalim:', 'divine-light'),
('New', 'Mateo', 4, 14, 'Para que se cumpliese lo que fué dicho por el profeta Isaías, que dijo:', 'divine-light'),
('New', 'Mateo', 4, 15, 'La tierra de Zabulón, y la tierra de Nephtalim, Camino de la mar, de la otra parte del Jordán, Galilea de los Gentiles;', 'divine-light'),
('New', 'Mateo', 4, 16, 'El pueblo asentado en tinieblas, Vió gran luz; Y á los sentados en región y sombra de muerte, Luz les esclareció.', 'divine-light'),
('New', 'Mateo', 4, 17, 'Desde entonces comenzó Jesús á predicar, y á decir: Arrepentíos, que el reino de los cielos se ha acercado.', 'divine-light'),
('New', 'Mateo', 4, 18, 'Y andando Jesús junto á la mar de Galilea, vió á dos hermanos, Simón, que es llamado Pedro, y Andrés su hermano, que echaban la red en la mar; porque eran pescadores.', 'divine-light'),
('New', 'Mateo', 4, 19, 'Y díceles: Venid en pos de mí, y os haré pescadores de hombres.', 'divine-light'),
('New', 'Mateo', 4, 20, 'Ellos entonces, dejando luego las redes, le siguieron.', 'divine-light'),
('New', 'Mateo', 4, 21, 'Y pasando de allí vió otros dos hermanos, Jacobo, hijo de Zebedeo, y Juan su hermano, en el barco con Zebedeo, su padre, que remendaban sus redes; y los llamó.', 'divine-light'),
('New', 'Mateo', 4, 22, 'Y ellos, dejando luego el barco y á su padre, le siguieron.', 'divine-light'),
('New', 'Mateo', 4, 23, 'Y rodeó Jesús toda Galilea, enseñando en las sinagogas de ellos, y predicando el evangelio del reino, y sanando toda enfermedad y toda dolencia en el pueblo.', 'divine-light'),
('New', 'Mateo', 4, 24, 'Y corría su fama por toda la Siria; y le trajeron todos los que tenían mal: los tomados de diversas enfermedades y tormentos, y los endemoniados, y lunáticos, y paralíticos, y los sanó.', 'divine-light'),
('New', 'Mateo', 4, 25, 'Y le siguieron muchas gentes de Galilea y de Decápolis y de Jerusalem y de Judea y de la otra parte del Jordán.', 'divine-light'),
('New', 'Mateo', 5, 1, 'Y VIENDO las gentes, subió al monte; y sentándose, se llegaron á él sus discípulos.', 'divine-light'),
('New', 'Mateo', 5, 2, 'Y abriendo su boca, les enseñaba, diciendo:', 'divine-light'),
('New', 'Mateo', 5, 3, 'Bienaventurados los pobres en espíritu: porque de ellos es el reino de los cielos.', 'divine-light'),
('New', 'Mateo', 5, 4, 'Bienaventurados los que lloran: porque ellos recibirán consolación.', 'divine-light'),
('New', 'Mateo', 5, 5, 'Bienaventurados los mansos: porque ellos recibirán la tierra por heredad.', 'divine-light'),
('New', 'Mateo', 5, 6, 'Bienaventurados los que tienen hambre y sed de justicia: porque ellos serán hartos.', 'divine-light'),
('New', 'Mateo', 5, 7, 'Bienaventurados los misericordiosos: porque ellos alcanzarán misericordia.', 'divine-light'),
('New', 'Mateo', 5, 8, 'Bienaventurados los de limpio corazón: porque ellos verán á Dios.', 'divine-light'),
('New', 'Mateo', 5, 9, 'Bienaventurados los pacificadores: porque ellos serán llamados hijos de Dios.', 'divine-light'),
('New', 'Mateo', 5, 10, 'Bienaventurados los que padecen persecución por causa de la justicia: porque de ellos es el reino de los cielos.', 'divine-light'),
('New', 'Mateo', 5, 11, 'Bienaventurados sois cuando os vituperaren y os persiguieren, y dijeren de vosotros todo mal por mi causa, mintiendo.', 'divine-light'),
('New', 'Mateo', 5, 12, 'Gozaos y alegraos; porque vuestra merced es grande en los cielos: que así persiguieron á los profetas que fueron antes de vosotros.', 'divine-light'),
('New', 'Mateo', 5, 13, 'Vosotros sois la sal de la tierra: y si la sal se desvaneciere ¿con qué será salada? no vale más para nada, sino para ser echada fuera y hollada de los hombres.', 'divine-light'),
('New', 'Mateo', 5, 14, 'Vosotros sois la luz del mundo: una ciudad asentada sobre un monte no se puede esconder.', 'divine-light'),
('New', 'Mateo', 5, 15, 'Ni se enciende una lámpara y se pone debajo de un almud, mas sobre el candelero, y alumbra á todos los que están en casa.', 'divine-light'),
('New', 'Mateo', 5, 16, 'Así alumbre vuestra luz delante de los hombres, para que vean vuestras obras buenas, y glorifiquen á vuestro Padre que está en los cielos.', 'divine-light'),
('New', 'Mateo', 5, 17, 'No penséis que he venido para abrogar la ley ó los profetas: no he venido para abrogar, sino á cumplir.', 'divine-light'),
('New', 'Mateo', 5, 18, 'Porque de cierto os digo, que hasta que perezca el cielo y la tierra, ni una jota ni un tilde perecerá de la ley, hasta que todas las cosas sean hechas.', 'divine-light'),
('New', 'Mateo', 5, 19, 'De manera que cualquiera que infringiere uno de estos mandamientos muy pequeños, y así enseñare á los hombres, muy pequeño será llamado en el reino de los cielos: mas cualquiera que hiciere y enseñare, éste será llamado grande en el reino de los cielos.', 'divine-light'),
('New', 'Mateo', 5, 20, 'Porque os digo, que si vuestra justicia no fuere mayor que la de los escribas y de los Fariseos, no entraréis en el reino de los cielos.', 'divine-light'),
('New', 'Mateo', 5, 21, 'Oísteis que fué dicho á los antiguos: No matarás; mas cualquiera que matare, será culpado del juicio.', 'divine-light'),
('New', 'Mateo', 5, 22, 'Mas yo os digo, que cualquiera que se enojare locamente con su hermano, será culpado del juicio; y cualquiera que dijere á su hermano, Raca, será culpado del concejo; y cualquiera que dijere, Fatuo, será culpado del infierno del fuego.', 'divine-light'),
('New', 'Mateo', 5, 23, 'Por tanto, si trajeres tu presente al altar, y allí te acordares de que tu hermano tiene algo contra ti,', 'divine-light'),
('New', 'Mateo', 5, 24, 'Deja allí tu presente delante del altar, y vete, vuelve primero en amistad con tu hermano, y entonces ven y ofrece tu presente.', 'divine-light'),
('New', 'Mateo', 5, 25, 'Concíliate con tu adversario presto, entre tanto que estás con él en el camino; porque no acontezca que el adversario te entregue al juez, y el juez te entregue al alguacil, y seas echado en prisión.', 'divine-light'),
('New', 'Mateo', 5, 26, 'De cierto te digo, que no saldrás de allí, hasta que pagues el último cuadrante.', 'divine-light'),
('New', 'Mateo', 5, 27, 'Oísteis que fué dicho: No adulterarás:', 'divine-light'),
('New', 'Mateo', 5, 28, 'Mas yo os digo, que cualquiera que mira á una mujer para codiciarla, ya adulteró con ella en su corazón.', 'divine-light'),
('New', 'Mateo', 5, 29, 'Por tanto, si tu ojo derecho te fuere ocasión de caer, sácalo, y échalo de ti: que mejor te es que se pierda uno de tus miembros, que no que todo tu cuerpo sea echado al infierno.', 'divine-light'),
('New', 'Mateo', 5, 30, 'Y si tu mano derecha te fuere ocasión de caer, córtala, y échala de ti: que mejor te es que se pierda uno de tus miembros, que no que todo tu cuerpo sea echado al infierno.', 'divine-light'),
('New', 'Mateo', 5, 31, 'También fué dicho: Cualquiera que repudiare á su mujer, déle carta de divorcio:', 'divine-light'),
('New', 'Mateo', 5, 32, 'Mas yo os digo, que el que repudiare á su mujer, fuera de causa de fornicación, hace que ella adultere; y el que se casare con la repudiada, comete adulterio.', 'divine-light'),
('New', 'Mateo', 5, 33, 'Además habéis oído que fué dicho á los antiguos: No te perjurarás; mas pagarás al Señor tus juramentos.', 'divine-light'),
('New', 'Mateo', 5, 34, 'Mas yo os digo: No juréis en ninguna manera: ni por el cielo, porque es el trono de Dios;', 'divine-light'),
('New', 'Mateo', 5, 35, 'Ni por la tierra, porque es el estrado de sus pies; ni por Jerusalem, porque es la ciudad del gran Rey.', 'divine-light'),
('New', 'Mateo', 5, 36, 'Ni por tu cabeza jurarás, porque no puedes hacer un cabello blanco ó negro.', 'divine-light'),
('New', 'Mateo', 5, 37, 'Mas sea vuestro hablar: Sí, sí; No, no; porque lo que es más de esto, de mal procede.', 'divine-light'),
('New', 'Mateo', 5, 38, 'Oísteis que fué dicho á los antiguos: Ojo por ojo, y diente por diente.', 'divine-light'),
('New', 'Mateo', 5, 39, 'Mas yo os digo: No resistáis al mal; antes á cualquiera que te hiriere en tu mejilla diestra, vuélvele también la otra;', 'divine-light'),
('New', 'Mateo', 5, 40, 'Y al que quisiere ponerte á pleito y tomarte tu ropa, déjale también la capa;', 'divine-light'),
('New', 'Mateo', 5, 41, 'Y á cualquiera que te cargare por una milla, ve con él dos.', 'divine-light'),
('New', 'Mateo', 5, 42, 'Al que te pidiere, dale; y al que quisiere tomar de ti prestado, no se lo rehuses.', 'divine-light'),
('New', 'Mateo', 5, 43, 'Oísteis que fué dicho: Amarás á tu prójimo, y aborrecerás á tu enemigo.', 'divine-light'),
('New', 'Mateo', 5, 44, 'Mas yo os digo: Amad á vuestros enemigos, bendecid á los que os maldicen, haced bien á los que os aborrecen, y orad por los que os ultrajan y os persiguen;', 'divine-light'),
('New', 'Mateo', 5, 45, 'Para que seáis hijos de vuestro Padre que está en los cielos: que hace que su sol salga sobre malos y buenos, y llueve sobre justos é injustos.', 'divine-light'),
('New', 'Mateo', 5, 46, 'Porque si amareis á los que os aman, ¿qué recompensa tendréis? ¿no hacen también lo mismo los publicanos?', 'divine-light'),
('New', 'Mateo', 5, 47, 'Y si abrazareis á vuestros hermanos solamente, ¿qué hacéis de más? ¿no hacen también así los Gentiles?', 'divine-light'),
('New', 'Mateo', 5, 48, 'Sed, pues, vosotros perfectos, como vuestro Padre que está en los cielos es perfecto.', 'divine-light'),
('New', 'Mateo', 6, 1, 'MIRAD que no hagáis vuestra justicia delante de los hombres, para ser vistos de ellos: de otra manera no tendréis merced de vuestro Padre que está en los cielos.', 'divine-light'),
('New', 'Mateo', 6, 2, 'Cuando pues haces limosna, no hagas tocar trompeta delante de ti, como hacen los hipócritas en las sinagogas y en las plazas, para ser estimados de los hombres: de cierto os digo, que ya tienen su recompensa.', 'divine-light'),
('New', 'Mateo', 6, 3, 'Mas cuando tú haces limosna, no sepa tu izquierda lo que hace tu derecha;', 'divine-light'),
('New', 'Mateo', 6, 4, 'Para que sea tu limosna en secreto: y tu Padre que ve en secreto, él te recompensará en público.', 'divine-light'),
('New', 'Mateo', 6, 5, 'Y cuando oras, no seas como los hipócritas; porque ellos aman el orar en las sinagogas, y en los cantones de las calles en pie, para ser vistos de los hombres: de cierto os digo, que ya tienen su pago.', 'divine-light'),
('New', 'Mateo', 6, 6, 'Mas tú, cuando oras, éntrate en tu cámara, y cerrada tu puerta, ora á tu Padre que está en secreto; y tu Padre que ve en secreto, te recompensará en público.', 'divine-light'),
('New', 'Mateo', 6, 7, 'Y orando, no seáis prolijos, como los Gentiles; que piensan que por su parlería serán oídos.', 'divine-light'),
('New', 'Mateo', 6, 8, 'No os hagáis, pues, semejantes á ellos; porque vuestro Padre sabe de qué cosas tenéis necesidad, antes que vosotros le pidáis.', 'divine-light'),
('New', 'Mateo', 6, 9, 'Vosotros pues, oraréis así: Padre nuestro que estás en los cielos, santificado sea tu nombre.', 'divine-light'),
('New', 'Mateo', 6, 10, 'Venga tu reino. Sea hecha tu voluntad, como en el cielo, así también en la tierra.', 'divine-light'),
('New', 'Mateo', 6, 11, 'Danos hoy nuestro pan cotidiano.', 'divine-light'),
('New', 'Mateo', 6, 12, 'Y perdónanos nuestras deudas, como también nosotros perdonamos á nuestros deudores.', 'divine-light'),
('New', 'Mateo', 6, 13, 'Y no nos metas en tentación, mas líbranos del mal: porque tuyo es el reino, y el poder, y la gloria, por todos los siglos. Amén.', 'divine-light'),
('New', 'Mateo', 6, 14, 'Porque si perdonareis á los hombres sus ofensas, os perdonará también á vosotros vuestro Padre celestial.', 'divine-light'),
('New', 'Mateo', 6, 15, 'Mas si no perdonareis á los hombres sus ofensas, tampoco vuestro Padre os perdonará vuestras ofensas.', 'divine-light'),
('New', 'Mateo', 6, 16, 'Y cuando ayunáis, no seáis como los hipócritas, austeros; porque ellos demudan sus rostros para parecer á los hombres que ayunan: de cierto os digo, que ya tienen su pago.', 'divine-light'),
('New', 'Mateo', 6, 17, 'Mas tú, cuando ayunas, unge tu cabeza y lava tu rostro;', 'divine-light'),
('New', 'Mateo', 6, 18, 'Para no parecer á los hombres que ayunas, sino á tu Padre que está en secreto: y tu Padre que ve en secreto, te recompensará en público.', 'divine-light'),
('New', 'Mateo', 6, 19, 'No os hagáis tesoros en la tierra, donde la polilla y el orín corrompe, y donde ladronas minan y hurtan;', 'divine-light'),
('New', 'Mateo', 6, 20, 'Mas haceos tesoros en el cielo, donde ni polilla ni orín corrompe, y donde ladrones no minan ni hurtan:', 'divine-light'),
('New', 'Mateo', 6, 21, 'Porque donde estuviere vuestro tesoro, allí estará vuestro corazón.', 'divine-light'),
('New', 'Mateo', 6, 22, 'La lámpara del cuerpo es el ojo: así que, si tu ojo fuere sincero, todo tu cuerpo será luminoso:', 'divine-light'),
('New', 'Mateo', 6, 23, 'Mas si tu ojo fuere malo, todo tu cuerpo será tenebroso. Así que, si la lumbre que en ti hay son tinieblas, ¿cuántas serán las mismas tinieblas?', 'divine-light'),
('New', 'Mateo', 6, 24, 'Ninguno puede servir á dos señores; porque ó aborrecerá al uno y amará al otro, ó se llegará al uno y menospreciará al otro: no podéis servir á Dios y á Mammón.', 'divine-light'),
('New', 'Mateo', 6, 25, 'Por tanto os digo: No os congojéis por vuestra vida, qué habéis de comer, ó que habéis de beber; ni por vuestro cuerpo, qué habéis de vestir: ¿no es la vida más que el alimento, y el cuerpo que el vestido?', 'divine-light'),
('New', 'Mateo', 6, 26, 'Mirad las aves del cielo, que no siembran, ni siegan, ni allegan en alfolíes; y vuestro Padre celestial las alimenta. ¿No sois vosotros mucho mejores que ellas?.', 'divine-light'),
('New', 'Mateo', 6, 27, 'Mas ¿quién de vosotros podrá, congojándose, añadir á su estatura un codo?', 'divine-light'),
('New', 'Mateo', 6, 28, 'Y por el vestido ¿por qué os congojáis? Reparad los lirios del campo, cómo crecen; no trabajan ni hilan;', 'divine-light'),
('New', 'Mateo', 6, 29, 'Mas os digo, que ni aun Salomón con toda su gloria fué vestido así como uno de ellos.', 'divine-light'),
('New', 'Mateo', 6, 30, 'Y si la hierba del campo que hoy es, y mañana es echada en el horno, Dios la viste así, ¿no hará mucho más á vosotros, hombres de poca fe?', 'divine-light'),
('New', 'Mateo', 6, 31, 'No os congojéis pues, diciendo: ¿Qué comeremos, ó qué beberemos, ó con qué nos cubriremos?', 'divine-light'),
('New', 'Mateo', 6, 32, 'Porque los Gentiles buscan todas estas cosas: que vuestro Padre celestial sabe que de todas estas cosas habéis menester.', 'divine-light'),
('New', 'Mateo', 6, 33, 'Mas buscad primeramente el reino de Dios y su justicia, y todas estas cosas os serán añadidas.', 'divine-light'),
('New', 'Mateo', 6, 34, 'Así que, no os congojéis por el día de mañana; que el día de mañana traerá su fatiga: basta al día su afán.', 'divine-light'),
('New', 'Mateo', 7, 1, 'NO juzguéis, para que no seáis juzgados.', 'divine-light'),
('New', 'Mateo', 7, 2, 'Porque con el juicio con que juzgáis, seréis juzgados; y con la medida con que medís, os volverán á medir.', 'divine-light'),
('New', 'Mateo', 7, 3, 'Y ¿por qué miras la mota que está en el ojo de tu hermano, y no echas de ver la viga que está en tu ojo?', 'divine-light'),
('New', 'Mateo', 7, 4, 'O ¿cómo dirás á tu hermano: Espera, echaré de tu ojo la mota, y he aquí la viga en tu ojo?', 'divine-light'),
('New', 'Mateo', 7, 5, '¡Hipócrita! echa primero la viga de tu ojo, y entonces mirarás en echar la mota del ojo de tu hermano.', 'divine-light'),
('New', 'Mateo', 7, 6, 'No deis lo santo á los perros, ni echéis vuestras perlas delante de los puercos; porque no las rehuellen con sus pies, y vuelvan y os despedacen.', 'divine-light'),
('New', 'Mateo', 7, 7, 'Pedid, y se os dará; buscad, y hallaréis; llamad, y se os abrirá.', 'divine-light'),
('New', 'Mateo', 7, 8, 'Porque cualquiera que pide, recibe; y el que busca, halla; y al que llama, se abrirá.', 'divine-light'),
('New', 'Mateo', 7, 9, '¿Qué hombre hay de vosotros, á quien si su hijo pidiere pan, le dará una piedra?', 'divine-light'),
('New', 'Mateo', 7, 10, '¿Y si le pidiere un pez, le dará una serpiente?', 'divine-light'),
('New', 'Mateo', 7, 11, 'Pues si vosotros, siendo malos, sabéis dar buenas dádivas á vuestros hijos, ¿cuánto más vuestro Padre que está en los cielos, dará buenas cosas á los que le piden?', 'divine-light'),
('New', 'Mateo', 7, 12, 'Así que, todas las cosas que quisierais que los hombres hiciesen con vosotros, así también haced vosotros con ellos; porque esta es la ley y los profetas.', 'divine-light'),
('New', 'Mateo', 7, 13, 'Entrad por la puerta estrecha: porque ancha es la puerta, y espacioso el camino que lleva á perdición, y muchos son los que entran por ella.', 'divine-light'),
('New', 'Mateo', 7, 14, 'Porque estrecha es la puerta, y angosto el camino que lleva á la vida, y pocos son los que la hallan.', 'divine-light'),
('New', 'Mateo', 7, 15, 'Y guardaos de los falsos profetas, que vienen á vosotros con vestidos de ovejas, mas de dentro son lobos rapaces.', 'divine-light'),
('New', 'Mateo', 7, 16, 'Por sus frutos los conoceréis. ¿Cógense uvas de los espinos, ó higos de los abrojos?', 'divine-light'),
('New', 'Mateo', 7, 17, 'Así, todo buen árbol lleva buenos frutos; mas el árbol maleado lleva malos frutos.', 'divine-light'),
('New', 'Mateo', 7, 18, 'No puede el buen árbol llevar malos frutos, ni el árbol maleado llevar frutos buenos.', 'divine-light'),
('New', 'Mateo', 7, 19, 'Todo árbol que no lleva buen fruto, córtase y échase en el fuego.', 'divine-light'),
('New', 'Mateo', 7, 20, 'Así que, por sus frutos los conoceréis.', 'divine-light'),
('New', 'Mateo', 7, 21, 'No todo el que me dice: Señor, Señor, entrará en el reino de los cielos: mas el que hiciere la voluntad de mi Padre que está en los cielos.', 'divine-light'),
('New', 'Mateo', 7, 22, 'Muchos me dirán en aquel día: Señor, Señor, ¿no profetizamos en tu nombre, y en tu nombre lanzamos demonios, y en tu nombre hicimos mucho milagros?', 'divine-light'),
('New', 'Mateo', 7, 23, 'Y entonces les protestaré: Nunca os conocí; apartaos de mí, obradores de maldad.', 'divine-light'),
('New', 'Mateo', 7, 24, 'Cualquiera, pues, que me oye estas palabras, y las hace, le compararé á un hombre prudente, que edificó su casa sobre la peña;', 'divine-light'),
('New', 'Mateo', 7, 25, 'Y descendió lluvia, y vinieron ríos, y soplaron vientos, y combatieron aquella casa; y no cayó: porque estaba fundada sobre la peña.', 'divine-light'),
('New', 'Mateo', 7, 26, 'Y cualquiera que me oye estas palabras, y no las hace, le compararé á un hombre insensato, que edificó su casa sobre la arena;', 'divine-light'),
('New', 'Mateo', 7, 27, 'Y descendió lluvia, y vinieron ríos, y soplaron vientos, é hicieron ímpetu en aquella casa; y cayó, y fué grande su ruina.', 'divine-light'),
('New', 'Mateo', 7, 28, 'Y fué que, como Jesús acabó estas palabras, las gentes se admiraban de su doctrina;', 'divine-light'),
('New', 'Mateo', 7, 29, 'Porque les enseñaba como quien tiene autoridad, y no como los escribas.', 'divine-light'),
('New', 'Mateo', 8, 1, 'Y COMO descendió del monte, le seguían muchas gentes.', 'divine-light'),
('New', 'Mateo', 8, 2, 'Y he aquí un leproso vino, y le adoraba, diciendo: Señor, si quisieres, puedes limpiarme.', 'divine-light'),
('New', 'Mateo', 8, 3, 'Y extendiendo Jesús su mano, le tocó, diciendo: Quiero; sé limpio. Y luego su lepra fué limpiada.', 'divine-light'),
('New', 'Mateo', 8, 4, 'Entonces Jesús le dijo: Mira, no lo digas á nadie; mas ve, muéstrate al sacerdote, y ofrece el presente que mandó Moisés, para testimonio á ellos.', 'divine-light'),
('New', 'Mateo', 8, 5, 'Y entrando Jesús en Capernaum, vino á él un centurión, rogándole,', 'divine-light'),
('New', 'Mateo', 8, 6, 'Y diciendo: Señor, mi mozo yace en casa paralítico, gravemente atormentado.', 'divine-light'),
('New', 'Mateo', 8, 7, 'Y Jesús le dijo: Yo iré y le sanaré.', 'divine-light'),
('New', 'Mateo', 8, 8, 'Y respondió el centurión, y dijo: Señor, no soy digno de que entres debajo de mi techado; mas solamente di la palabra, y mi mozo sanará.', 'divine-light'),
('New', 'Mateo', 8, 9, 'Porque también yo soy hombre bajo de potestad, y tengo bajo de mí soldados: y digo á éste: Ve, y va; y al otro: Ven, y viene; y á mi siervo: Haz esto, y lo hace.', 'divine-light'),
('New', 'Mateo', 8, 10, 'Y oyendo Jesús, se maravilló, y dijo á los que le seguían: De cierto os digo, que ni aun en Israel he hallado fe tanta.', 'divine-light'),
('New', 'Mateo', 8, 11, 'Y os digo que vendrán muchos del oriente y del occidente, y se sentarán con Abraham, é Isaac, y Jacob, en el reino de los cielos:', 'divine-light'),
('New', 'Mateo', 8, 12, 'Mas los hijos del reino serán echados á las tinieblas de afuera: allí será el lloro y el crujir de dientes.', 'divine-light'),
('New', 'Mateo', 8, 13, 'Entonces Jesús dijo al centurión: Ve, y como creiste te sea hecho. Y su mozo fué sano en el mismo momento.', 'divine-light'),
('New', 'Mateo', 8, 14, 'Y vino Jesús á casa de Pedro, y vió á su suegra echada en cama, y con fiebre.', 'divine-light'),
('New', 'Mateo', 8, 15, 'Y tocó su mano, y la fiebre la dejó: y ella se levantó, y les servía.', 'divine-light'),
('New', 'Mateo', 8, 16, 'Y como fué ya tarde, trajeron á él muchos endemoniados: y echó los demonios con la palabra, y sanó á todos los enfermos;', 'divine-light'),
('New', 'Mateo', 8, 17, 'Para que se cumpliese lo que fué dicho por el profeta Isaías, que dijo: El mismo tomó nuestras enfermedades, y llevó nuestras dolencias.', 'divine-light'),
('New', 'Mateo', 8, 18, 'Y viendo Jesús muchas gentes alrededor de sí, mandó pasar á la otra parte del lago.', 'divine-light'),
('New', 'Mateo', 8, 19, 'Y llegándose un escriba, le dijo: Maestro, te seguiré á donde quiera que fueres.', 'divine-light'),
('New', 'Mateo', 8, 20, 'Y Jesús le dijo: Las zorras tienen cavernas, y las aves del cielo nidos; mas el Hijo del hombre no tiene donde recueste su cabeza.', 'divine-light'),
('New', 'Mateo', 8, 21, 'Y otro de sus discípulos le dijo: Señor, dame licencia para que vaya primero, y entierre á mi padre.', 'divine-light'),
('New', 'Mateo', 8, 22, 'Y Jesús le dijo: Sígueme; deja que los muertos entierren á sus muertos.', 'divine-light'),
('New', 'Mateo', 8, 23, 'Y entrando él en el barco, sus discípulos le siguieron.', 'divine-light'),
('New', 'Mateo', 8, 24, 'Y he aquí, fué hecho en la mar un gran movimiento, que el barco se cubría de las ondas; mas él dormía.', 'divine-light'),
('New', 'Mateo', 8, 25, 'Y llegándose sus discípulos, le despertaron, diciendo: Señor, sálvanos, que perecemos.', 'divine-light'),
('New', 'Mateo', 8, 26, 'Y él les dice: ¿Por qué teméis, hombres de poca fe? Entonces, levantándose, reprendió á los vientos y á la mar; y fué grande bonanza.', 'divine-light'),
('New', 'Mateo', 8, 27, 'Y los hombres se maravillaron, diciendo: ¿Qué hombre es éste, que aun los vientos y la mar le obedecen?', 'divine-light'),
('New', 'Mateo', 8, 28, 'Y como él hubo llegado en la otra ribera al país de los Gergesenos, le vinieron al encuentro dos endemoniados que salían de los sepulcros, fieros en gran manera, que nadie podía pasar por aquel camino.', 'divine-light'),
('New', 'Mateo', 8, 29, 'Y he aquí clamaron, diciendo: ¿Qué tenemos contigo, Jesús, Hijo de Dios? ¿has venido acá á molestarnos antes de tiempo?', 'divine-light'),
('New', 'Mateo', 8, 30, 'Y estaba lejos de ellos un hato de muchos puercos paciendo.', 'divine-light'),
('New', 'Mateo', 8, 31, 'Y los demonios le rogaron, diciendo: Si nos echas, permítenos ir á aquel hato de puercos.', 'divine-light'),
('New', 'Mateo', 8, 32, 'Y les dijo: Id. Y ellos salieron, y se fueron á aquel hato de puercos: y he aquí, todo el hato de los puercos se precipitó de un despeñadero en la mar, y murieron en las aguas.', 'divine-light'),
('New', 'Mateo', 8, 33, 'Y los porqueros huyeron, y viniendo á la ciudad, contaron todas las cosas, y lo que había pasado con los endemoniados.', 'divine-light'),
('New', 'Mateo', 8, 34, 'Y he aquí, toda la ciudad salió á encontrar á Jesús: Y cuando le vieron, le rogaban que saliese de sus términos.', 'divine-light'),
('New', 'Mateo', 9, 1, 'ENTONCES entrando en el barco, pasó á la otra parte, y vino á su ciudad.', 'divine-light'),
('New', 'Mateo', 9, 2, 'Y he aquí le trajeron un paralítico, echado en una cama: y viendo Jesús la fe de ellos, dijo al paralítico: Confía, hijo; tus pecados te son perdonados.', 'divine-light'),
('New', 'Mateo', 9, 3, 'Y he aquí, algunos de los escribas decían dentro de sí: Este blasfema.', 'divine-light'),
('New', 'Mateo', 9, 4, 'Y viendo Jesús sus pensamientos, dijo: ¿Por qué pensáis mal en vuestros corazones?', 'divine-light'),
('New', 'Mateo', 9, 5, 'Porque, ¿qué es más fácil, decir: Los pecados te son perdonados; ó decir: Levántate, y anda?', 'divine-light'),
('New', 'Mateo', 9, 6, 'Pues para que sepáis que el Hijo del hombre tiene potestad en la tierra de perdonar pecados, (dice entonces al paralítico): Levántate, toma tu cama, y vete á tu casa.', 'divine-light'),
('New', 'Mateo', 9, 7, 'Entonces él se levantó y se fué á su casa.', 'divine-light'),
('New', 'Mateo', 9, 8, 'Y las gentes, viéndolo, se maravillaron, y glorificaron á Dios, que había dado tal potestad á los hombres.', 'divine-light'),
('New', 'Mateo', 9, 9, 'Y pasando Jesús de allí, vió á un hombre que estaba sentado al banco de los públicos tributos, el cual se llamaba Mateo; y dícele: Sígueme. Y se levantó, y le siguió.', 'divine-light'),
('New', 'Mateo', 9, 10, 'Y aconteció que estando él sentado á la mesa en casa, he aquí que muchos publicanos y pecadores, que habían venido, se sentaron juntamente á la mesa con Jesús y sus discípulos.', 'divine-light'),
('New', 'Mateo', 9, 11, 'Y viendo esto los Fariseos, dijeron á sus discípulos: ¿Por qué come vuestro Maestro con los publicanos y pecadores?', 'divine-light'),
('New', 'Mateo', 9, 12, 'Y oyéndolo Jesús, le dijo: Los que están sanos no tienen necesidad de médico, sino los enfermos.', 'divine-light'),
('New', 'Mateo', 9, 13, 'Andad pues, y aprended qué cosa es: Misericordia quiero, y no sacrificio: porque no he venido á llamar justos, sino pecadores á arrepentimiento.', 'divine-light'),
('New', 'Mateo', 9, 14, 'Entonces los discípulos de Juan vienen á él, diciendo: ¿Por qué nosotros y los Fariseos ayunamos muchas veces, y tus discípulos no ayunan?', 'divine-light'),
('New', 'Mateo', 9, 15, 'Y Jesús les dijo: ¿Pueden los que son de bodas tener luto entre tanto que el esposo está con ellos? mas vendrán días cuando el esposo será quitado de ellos, y entonces ayunarán.', 'divine-light'),
('New', 'Mateo', 9, 16, 'Y nadie echa remiendo de paño recio en vestido viejo; porque el tal remiendo tira del vestido, y se hace peor la rotura.', 'divine-light'),
('New', 'Mateo', 9, 17, 'Ni echan vino nuevo en cueros viejos: de otra manera los cueros se rompen, y el vino se derrama, y se pierden los cueros; mas echan el vino nuevo en cueros nuevos, y lo uno y lo otro se conserva juntamente.', 'divine-light'),
('New', 'Mateo', 9, 18, 'Hablando él estas cosas á ellos, he aquí vino un principal, y le adoraba, diciendo: Mi hija es muerta poco ha: mas ven y pon tu mano sobre ella, y vivirá.', 'divine-light'),
('New', 'Mateo', 9, 19, 'Y se levantó Jesús, y le siguió, y sus discípulos.', 'divine-light'),
('New', 'Mateo', 9, 20, 'Y he aquí una mujer enferma de flujo de sangre doce años había, llegándose por detrás, tocó la franja de su vestido:', 'divine-light'),
('New', 'Mateo', 9, 21, 'Porque decía entre sí: Si tocare solamente su vestido, seré salva.', 'divine-light'),
('New', 'Mateo', 9, 22, 'Mas Jesús volviéndose, y mirándola, dijo: Confía, hija, tu fe te ha salvado. Y la mujer fué salva desde aquella hora.', 'divine-light'),
('New', 'Mateo', 9, 23, 'Y llegado Jesús á casa del principal, viendo los tañedores de flautas, y la gente que hacía bullicio,', 'divine-light'),
('New', 'Mateo', 9, 24, 'Díceles: Apartaos, que la muchacha no es muerta, mas duerme. Y se burlaban de él.', 'divine-light'),
('New', 'Mateo', 9, 25, 'Y como la gente fué echada fuera, entró, y tomóla de la mano, y se levantó la muchacha.', 'divine-light'),
('New', 'Mateo', 9, 26, 'Y salió esta fama por toda aquella tierra.', 'divine-light'),
('New', 'Mateo', 9, 27, 'Y pasando Jesús de allí, le siguieron dos ciegos, dando voces y diciendo: Ten misericordia de nosotros, Hijo de David.', 'divine-light'),
('New', 'Mateo', 9, 28, 'Y llegado á la casa, vinieron á él los ciegos; y Jesús les dice: ¿Creéis que puedo hacer esto? Ellos dicen: Sí, Señor.', 'divine-light'),
('New', 'Mateo', 9, 29, 'Entonces tocó los ojos de ellos, diciendo: Conforme á vuestra fe os sea hecho.', 'divine-light'),
('New', 'Mateo', 9, 30, 'Y los ojos de ellos fueron abiertos. Y Jesús les encargó rigurosamente, diciendo: Mirad que nadie lo sepa.', 'divine-light'),
('New', 'Mateo', 9, 31, 'Mas ellos salidos, divulgaron su fama por toda aquella tierra.', 'divine-light'),
('New', 'Mateo', 9, 32, 'Y saliendo ellos, he aquí, le trajeron un hombre mudo, endemoniado.', 'divine-light'),
('New', 'Mateo', 9, 33, 'Y echado fuera el demonio, el mudo habló; y las gentes se maravillaron, diciendo: Nunca ha sido vista cosa semejante en Israel.', 'divine-light'),
('New', 'Mateo', 9, 34, 'Mas los Fariseos decían: Por el príncipe de los demonios echa fuera los demonios.', 'divine-light'),
('New', 'Mateo', 9, 35, 'Y rodeaba Jesús por todas las ciudades y aldeas, enseñando en las sinagogas de ellos, y predicando el evangelio del reino, y sanando toda enfermedad y todo achaque en el pueblo.', 'divine-light'),
('New', 'Mateo', 9, 36, 'Y viendo las gentes, tuvo compasión de ellas; porque estaban derramadas y esparcidas como ovejas que no tienen pastor.', 'divine-light'),
('New', 'Mateo', 9, 37, 'Entonces dice á sus discípulos: A la verdad la mies es mucha, mas los obreros pocos.', 'divine-light'),
('New', 'Mateo', 9, 38, 'Rogad, pues, al Señor de la mies, que envíe obreros á su mies.', 'divine-light'),
('New', 'Mateo', 10, 1, 'ENTONCES llamando á sus doce discípulos, les dió potestad contra los espíritus inmundos, para que los echasen fuera, y sanasen toda enfermedad y toda dolencia.', 'divine-light'),
('New', 'Mateo', 10, 2, 'Y los nombres de los doce apóstoles son estos: el primero, Simón, que es dicho Pedro, y Andrés su hermano; Jacobo, hijo de Zebedeo, y Juan su hermano;', 'divine-light'),
('New', 'Mateo', 10, 3, 'Felipe, y Bartolomé; Tomás, y Mateo el publicano; Jacobo hijo de Alfeo, y Lebeo, por sobrenombre Tadeo;', 'divine-light'),
('New', 'Mateo', 10, 4, 'Simón el Cananita y Judas Iscariote, que también le entregó.', 'divine-light'),
('New', 'Mateo', 10, 5, 'á estos doce envió Jesús, á los cuales dió mandamiento, diciendo: Por el camino de los Gentiles no iréis, y en ciudad de Samaritanos no entréis;', 'divine-light'),
('New', 'Mateo', 10, 6, 'Mas id antes á las ovejas perdidas de la casa de Israel.', 'divine-light'),
('New', 'Mateo', 10, 7, 'Y yendo, predicad, diciendo: El reino de los cielos se ha acercado.', 'divine-light'),
('New', 'Mateo', 10, 8, 'Sanad enfermos, limpiad leprosos, resucitad muertos, echad fuera demonios: de gracia recibisteis, dad de gracia.', 'divine-light'),
('New', 'Mateo', 10, 9, 'No aprestéis oro, ni plata, ni cobre en vuestras bolsas;', 'divine-light'),
('New', 'Mateo', 10, 10, 'Ni alforja para el camino, ni dos ropas de vestir, ni zapatos, ni bordón; porque el obrero digno es de su alimento.', 'divine-light'),
('New', 'Mateo', 10, 11, 'Mas en cualquier ciudad, ó aldea donde entrareis, investigad quién sea en ella digno, y reposad allí hasta que salgáis.', 'divine-light'),
('New', 'Mateo', 10, 12, 'Y entrando en la casa, saludadla.', 'divine-light'),
('New', 'Mateo', 10, 13, 'Y si la casa fuere digna, vuestra paz vendrá sobre ella; mas si no fuere digna, vuestra paz se volverá á vosotros.', 'divine-light'),
('New', 'Mateo', 10, 14, 'Y cualquiera que no os recibiere, ni oyere vuestras palabras, salid de aquella casa ó ciudad, y sacudid el polvo de vuestros pies.', 'divine-light'),
('New', 'Mateo', 10, 15, 'De cierto os digo, que el castigo será más tolerable á la tierra de los de Sodoma y de los de Gomorra en el día del juicio, que á aquella ciudad.', 'divine-light'),
('New', 'Mateo', 10, 16, 'He aquí, yo os envío como á ovejas en medio de lobos: sed pues prudentes como serpientes, y sencillos como palomas.', 'divine-light'),
('New', 'Mateo', 10, 17, 'Y guardaos de los hombres: porque os entregarán en concilios, y en sus sinagogas os azotarán;', 'divine-light'),
('New', 'Mateo', 10, 18, 'Y aun á príncipes y á reyes seréis llevados por causa de mí, por testimonio á ellos y á los Gentiles.', 'divine-light'),
('New', 'Mateo', 10, 19, 'Mas cuando os entregaren, no os apuréis por cómo ó qué hablaréis; porque en aquella hora os será dado qué habéis de hablar.', 'divine-light'),
('New', 'Mateo', 10, 20, 'Porque no sois vosotros los que habláis, sino el Espíritu de vuestro Padre que habla en vosotros.', 'divine-light'),
('New', 'Mateo', 10, 21, 'Y el hermano entregará al hermano á la muerte, y el padre al hijo; y los hijos se levantarán contra los padres, y los harán morir.', 'divine-light'),
('New', 'Mateo', 10, 22, 'Y seréis aborrecidos de todos por mi nombre; mas el que soportare hasta el fin, éste será salvo.', 'divine-light'),
('New', 'Mateo', 10, 23, 'Mas cuando os persiguieren en esta ciudad, huid á la otra: porque de cierto os digo, que no acabaréis de andar todas las ciudades de Israel, que no venga el Hijo del hombre.', 'divine-light'),
('New', 'Mateo', 10, 24, 'El discípulo no es más que su maestro, ni el siervo más que su señor.', 'divine-light'),
('New', 'Mateo', 10, 25, 'Bástale al discípulo ser como su maestro, y al siervo como su señor. Si al padre de la familia llamaron Beelzebub, ¿cuánto más á los de su casa?', 'divine-light'),
('New', 'Mateo', 10, 26, 'Así que, no los temáis; porque nada hay encubierto, que no haya de ser manifestado; ni oculto, que no haya de saberse.', 'divine-light'),
('New', 'Mateo', 10, 27, 'Lo que os digo en tinieblas, decidlo en la luz; y lo que oís al oído predicadlo desde los terrados.', 'divine-light'),
('New', 'Mateo', 10, 28, 'Y no temáis á los que matan el cuerpo, mas al alma no pueden matar: temed antes á aquel que puede destruir el alma y el cuerpo en el infierno.', 'divine-light'),
('New', 'Mateo', 10, 29, '¿No se venden dos pajarillos por un cuarto? Con todo, ni uno de ellos cae á tierra sin vuestro Padre.', 'divine-light'),
('New', 'Mateo', 10, 30, 'Pues aun vuestros cabellos están todos contados.', 'divine-light'),
('New', 'Mateo', 10, 31, 'Así que, no temáis: más valéis vosotros que muchos pajarillos.', 'divine-light'),
('New', 'Mateo', 10, 32, 'Cualquiera pues que me confesare delante de los hombres, le confesaré yo también delante de mi Padre que está en los cielos.', 'divine-light'),
('New', 'Mateo', 10, 33, 'Y cualquiera que me negare delante de los hombres, le negaré yo también delante de mi Padre que está en los cielos.', 'divine-light'),
('New', 'Mateo', 10, 34, 'No penséis que he venido para meter paz en la tierra: no he venido para meter paz, sino espada.', 'divine-light'),
('New', 'Mateo', 10, 35, 'Porque he venido para hacer disensión del hombre contra su padre, y de la hija contra su madre, y de la nuera contra su suegra.', 'divine-light'),
('New', 'Mateo', 10, 36, 'Y los enemigos del hombre serán los de su casa.', 'divine-light'),
('New', 'Mateo', 10, 37, 'El que ama padre ó madre más que á mí, no es digno de mí; y el que ama hijo ó hija más que á mí, no es digno de mí.', 'divine-light'),
('New', 'Mateo', 10, 38, 'Y el que no toma su cruz, y sigue en pos de mí, no es digno de mí.', 'divine-light'),
('New', 'Mateo', 10, 39, 'El que hallare su vida, la perderá; y el que perdiere su vida por causa de mí, la hallará.', 'divine-light'),
('New', 'Mateo', 10, 40, 'El que os recibe á vosotros, á mí recibe; y el que á mí recibe, recibe al que me envió.', 'divine-light'),
('New', 'Mateo', 10, 41, 'El que recibe profeta en nombre de profeta, merced de profeta recibirá; y el que recibe justo en nombre de justo, merced de justo recibirá.', 'divine-light'),
('New', 'Mateo', 10, 42, 'Y cualquiera que diere á uno de estos pequeñitos un vaso de agua fría solamente, en nombre de discípulo, de cierto os digo, que no perderá su recompensa.', 'divine-light'),
('New', 'Mateo', 11, 1, 'Y FUÉ, que acabando Jesús de dar mandamientos á sus doce discípulos, se fué de allí á enseñar y á predicar en las ciudades de ellos.', 'divine-light'),
('New', 'Mateo', 11, 2, 'Y oyendo Juan en la prisión los hechos de Cristo, le envió dos de sus discípulos,', 'divine-light'),
('New', 'Mateo', 11, 3, 'Diciendo: ¿Eres tú aquél que había de venir, ó esperaremos á otro?', 'divine-light'),
('New', 'Mateo', 11, 4, 'Y respondiendo Jesús, les dijo: Id, y haced saber á Juan las cosas que oís y veis:', 'divine-light'),
('New', 'Mateo', 11, 5, 'Los ciegos ven, y los cojos andan; los leprosos son limpiados, y los sordos oyen; los muertos son resucitados, y á los pobres es anunciado el evangelio.', 'divine-light'),
('New', 'Mateo', 11, 6, 'Y bienaventurado es el que no fuere escandalizado en mí.', 'divine-light'),
('New', 'Mateo', 11, 7, 'E idos ellos, comenzó Jesús á decir de Juan á las gentes: ¿Qué salisteis á ver al desierto? ¿una caña que es meneada del viento?', 'divine-light'),
('New', 'Mateo', 11, 8, 'Mas ¿qué salisteis á ver? ¿un hombre cubierto de delicados vestidos? He aquí, los que traen vestidos delicados, en las casas de los reyes están.', 'divine-light'),
('New', 'Mateo', 11, 9, 'Mas ¿qué salisteis á ver? ¿un profeta? También os digo, y más que profeta.', 'divine-light'),
('New', 'Mateo', 11, 10, 'Porque éste es de quien está escrito: He aquí, yo envío mi mensajero delante de tu faz, Que aparejará tu camino delante de ti.', 'divine-light'),
('New', 'Mateo', 11, 11, 'De cierto os digo, que no se levantó entre los que nacen de mujeres otro mayor que Juan el Bautista; mas el que es muy más pequeño en el reino de los cielos, mayor es que él.', 'divine-light'),
('New', 'Mateo', 11, 12, 'Desde los días de Juan el Bautista hasta ahora, al reino de los cielos se hace fuerza, y los valientes lo arrebatan.', 'divine-light'),
('New', 'Mateo', 11, 13, 'Porque todos los profetas y la ley hasta Juan profetizaron.', 'divine-light'),
('New', 'Mateo', 11, 14, 'Y si queréis recibir, él es aquel Elías que había de venir.', 'divine-light'),
('New', 'Mateo', 11, 15, 'El que tiene oídos para oir, oiga.', 'divine-light'),
('New', 'Mateo', 11, 16, 'Mas ¿á quién compararé esta generación? Es semejante á los muchachos que se sientan en las plazas, y dan voces á sus compañeros,', 'divine-light'),
('New', 'Mateo', 11, 17, 'Y dicen: Os tañimos flauta, y no bailasteis; os endechamos, y no lamentasteis.', 'divine-light'),
('New', 'Mateo', 11, 18, 'Porque vino Juan, que ni comía ni bebía, y dicen: Demonio tiene.', 'divine-light'),
('New', 'Mateo', 11, 19, 'Vino el Hijo del hombre, que come y bebe, y dicen: He aquí un hombre comilón, y bebedor de vino, amigo de publicanos y de pecadores. Mas la sabiduría es justificada por sus hijos.', 'divine-light'),
('New', 'Mateo', 11, 20, 'Entonces comenzó á reconvenir á las ciudades en las cuales habían sido hechas muy muchas de sus maravillas, porque no se habían arrepentido, diciendo:', 'divine-light'),
('New', 'Mateo', 11, 21, '¡Ay de ti, Corazín! ¡Ay de ti, Bethsaida! porque si en Tiro y en Sidón fueran hechas las maravillas que han sido hechas en vosotras, en otro tiempo se hubieran arrepentido en saco y en ceniza.', 'divine-light'),
('New', 'Mateo', 11, 22, 'Por tanto os digo, que á Tiro y á Sidón será más tolerable el castigo en el día del juicio, que á vosotras.', 'divine-light'),
('New', 'Mateo', 11, 23, 'Y tú, Capernaum, que eres levantada hasta el cielo, hasta los infiernos serás abajada; porque si en los de Sodoma fueran hechas las maravillas que han sido hechas en ti, hubieran quedado hasta el día de hoy.', 'divine-light'),
('New', 'Mateo', 11, 24, 'Por tanto os digo, que á la tierra de los de Sodoma será más tolerable el castigo en el día del juicio, que á ti.', 'divine-light'),
('New', 'Mateo', 11, 25, 'En aquel tiempo, respondiendo Jesús, dijo: Te alabo, Padre, Señor del cielo y de la tierra, que hayas escondido estas cosas de los sabios y de los entendidos, y las hayas revelado á los niños.', 'divine-light'),
('New', 'Mateo', 11, 26, 'Así, Padre, pues que así agradó en tus ojos.', 'divine-light'),
('New', 'Mateo', 11, 27, 'Todas las cosas me son entregadas de mi Padre: y nadie conoció al Hijo, sino el Padre; ni al Padre conoció alguno, sino el Hijo, y aquel á quien el Hijo lo quisiere revelar.', 'divine-light'),
('New', 'Mateo', 11, 28, 'Venid á mí todos los que estáis trabajados y cargados, que yo os haré descansar.', 'divine-light'),
('New', 'Mateo', 11, 29, 'Llevad mi yugo sobre vosotros, y aprended de mí, que soy manso y humilde de corazón; y hallaréis descanso para vuestras almas.', 'divine-light'),
('New', 'Mateo', 11, 30, 'Porque mi yugo es fácil, y ligera mi carga.', 'divine-light'),
('New', 'Mateo', 12, 1, 'EN aquel tiempo iba Jesús por los sembrados en sábado; y sus discípulos tenían hambre, y comenzaron á coger espigas, y á comer.', 'divine-light'),
('New', 'Mateo', 12, 2, 'Y viéndolo los Fariseos, le dijeron: He aquí tus discípulos hacen lo que no es lícito hacer es sábado.', 'divine-light'),
('New', 'Mateo', 12, 3, 'Y él les dijo: ¿No habéis leído qué hizo David, teniendo él hambre y los que con él estaban:', 'divine-light'),
('New', 'Mateo', 12, 4, 'Cómo entró en la casa de Dios, y comió los panes de la proposición, que no le era lícito comer, ni á los que estaban con él, sino á solos los sacerdotes¿', 'divine-light'),
('New', 'Mateo', 12, 5, 'O ¿no habéis leído en la ley, que los sábados en el templo los sacerdotes profanan el sábado, y son sin culpa?', 'divine-light'),
('New', 'Mateo', 12, 6, 'Pues os digo que uno mayor que el templo está aquí.', 'divine-light'),
('New', 'Mateo', 12, 7, 'Mas si supieseis qué es: Misericordia quiero y no sacrificio, no condenarías á los inocentes:', 'divine-light'),
('New', 'Mateo', 12, 8, 'Porque Señor es del sábado el Hijo del hombre.', 'divine-light'),
('New', 'Mateo', 12, 9, 'Y partiéndose de allí, vino á la sinagoga de ellos.', 'divine-light'),
('New', 'Mateo', 12, 10, 'Y he aquí había allí uno que tenía una mano seca: y le preguntaron, diciendo: ¿Es lícito curar en sábado? por acusarle.', 'divine-light'),
('New', 'Mateo', 12, 11, 'Y él les dijo: ¿Qué hombre habrá de vosotros, que tenga una oveja, y si cayere ésta en una fosa en sábado, no le eche mano, y la levante?', 'divine-light'),
('New', 'Mateo', 12, 12, 'Pues ¿cuánto más vale un hombre que una oveja? Así que, lícito es en los sábados hacer bien.', 'divine-light'),
('New', 'Mateo', 12, 13, 'Entonces dijo á aquel hombre: Extiende tu mano. Y él la extendió, y fué restituída sana como la otra.', 'divine-light'),
('New', 'Mateo', 12, 14, 'Y salidos los Fariseos, consultaron contra él para destruirle.', 'divine-light'),
('New', 'Mateo', 12, 15, 'Mas sabiendo lo Jesús, se apartó de allí: y le siguieron muchas gentes, y sanaba á todos.', 'divine-light'),
('New', 'Mateo', 12, 16, 'Y él les encargaba eficazmente que no le descubriesen:', 'divine-light'),
('New', 'Mateo', 12, 17, 'Para que se cumpliese lo que estaba dicho por el profeta Isaías, que dijo:', 'divine-light'),
('New', 'Mateo', 12, 18, 'He aquí mi siervo, al cual he escogido; Mi Amado, en el cual se agrada mi alma: Pondré mi Espíritu sobre él Y á los Gentiles anunciará juicio.', 'divine-light'),
('New', 'Mateo', 12, 19, 'No contenderá, ni voceará: Ni nadie oirá en las calles su voz.', 'divine-light'),
('New', 'Mateo', 12, 20, 'La caña cascada no quebrará, Y el pábilo que humea no apagará, Hasta que saque á victoria el juicio.', 'divine-light'),
('New', 'Mateo', 12, 21, 'Y en su nombre esperarán los Gentiles.', 'divine-light'),
('New', 'Mateo', 12, 22, 'Entonces fué traído á él un endemoniado, ciego y mudo, y le sanó; de tal manera, que el ciego y mudo hablaba y veía.', 'divine-light'),
('New', 'Mateo', 12, 23, 'Y todas las gentes estaban atónitas, y decían: ¿Será éste aquel Hijo de David?', 'divine-light'),
('New', 'Mateo', 12, 24, 'Mas los Fariseos, oyéndolo, decían: Este no echa fuera los demonios, sino por Beelzebub, príncipe de los demonios.', 'divine-light'),
('New', 'Mateo', 12, 25, 'Y Jesús, como sabía los pensamientos de ellos, les dijo: Todo reino dividido contra sí mismo, es desolado; y toda ciudad ó casa dividida contra sí misma, no permanecerá.', 'divine-light'),
('New', 'Mateo', 12, 26, 'Y si Satanás echa fuera á Satanás, contra sí mismo está dividido; ¿cómo, pues, permanecerá su reino?', 'divine-light'),
('New', 'Mateo', 12, 27, 'Y si yo por Beelzebub echo fuera los demonios, ¿vuestros hijos por quién los echan? Por tanto, ellos serán vuestros jueces.', 'divine-light'),
('New', 'Mateo', 12, 28, 'Y si por espíritu de Dios yo echo fuera los demonios, ciertamente ha llegado á vosotros el reino de Dios.', 'divine-light'),
('New', 'Mateo', 12, 29, 'Porque, ¿cómo puede alguno entrar en la casa del valiente, y saquear sus alhajas, si primero no prendiere al valiente? y entonces saqueará su casa.', 'divine-light'),
('New', 'Mateo', 12, 30, 'El que no es conmigo, contra mí es; y el que conmigo no recoge, derrama.', 'divine-light'),
('New', 'Mateo', 12, 31, 'Por tanto os digo: Todo pecado y blasfemia será perdonado á los hombres: mas la blasfemia contra el Espíritu no será perdonada á los hombres.', 'divine-light'),
('New', 'Mateo', 12, 32, 'Y cualquiera que hablare contra el Hijo del hombre, le será perdonado: mas cualquiera que hablare contra el Espíritu Santo, no le será perdonado, ni en este siglo, ni en el venidero.', 'divine-light'),
('New', 'Mateo', 12, 33, 'O haced el árbol bueno, y su fruto bueno, ó haced el árbol corrompido, y su fruto dañado; porque por el fruto es conocido el árbol.', 'divine-light'),
('New', 'Mateo', 12, 34, 'Generación de víboras, ¿cómo podéis hablar bien, siendo malos? porque de la abundancia del corazón habla la boca.', 'divine-light'),
('New', 'Mateo', 12, 35, 'El hombre bueno del buen tesoro del corazón saca buenas cosas: y el hombre malo del mal tesoro saca malas cosas.', 'divine-light'),
('New', 'Mateo', 12, 36, 'Mas yo os digo, que toda palabra ociosa que hablaren los hombres, de ella darán cuenta en el día del juicio;', 'divine-light'),
('New', 'Mateo', 12, 37, 'Porque por tus palabras serás justificado, y por tus palabras serás condenado.', 'divine-light'),
('New', 'Mateo', 12, 38, 'Entonces respondiendo algunos de los escribas y de los Fariseos, diciendo: Maestro, deseamos ver de ti señal.', 'divine-light'),
('New', 'Mateo', 12, 39, 'Y él respondió, y les dijo: La generación mala y adulterina demanda señal; mas señal no le será dada, sino la señal de Jonás profeta.', 'divine-light'),
('New', 'Mateo', 12, 40, 'Porque como estuvo Jonás en el vientre de la ballena tres días y tres noches, así estará el Hijo del hombre en el corazón de la tierra tres días y tres noches.', 'divine-light'),
('New', 'Mateo', 12, 41, 'Los hombres de Nínive se levantarán en el juicio con esta generación, y la condenarán; porque ellos se arrepintieron á la predicación de Jonás; y he aquí más que Jonás en este lugar.', 'divine-light'),
('New', 'Mateo', 12, 42, 'La reina del Austro se levantará en el juicio con esta generación, y la condenará; porque vino de los fines de la tierra para oir la sabiduría de Salomón: y he aquí más que Salomón en este lugar.', 'divine-light'),
('New', 'Mateo', 12, 43, 'Cuando el espíritu inmundo ha salido del hombre, anda por lugares secos, buscando reposo, y no lo halla.', 'divine-light'),
('New', 'Mateo', 12, 44, 'Entonces dice: Me volvere á mi casa de donde salí: y cuando viene, la halla desocupada, barrida y adornada.', 'divine-light'),
('New', 'Mateo', 12, 45, 'Entonces va, y toma consigo otros siete espíritus peores que él, y entrados, moran allí; y son peores las cosas; últimas del tal hombre que las primeras: así también acontecerá á esta generación mala.', 'divine-light'),
('New', 'Mateo', 12, 46, 'Y estando él aún hablando á las gentes, he aquí su madre y sus hermanos estaban fuera, que le querían hablar.', 'divine-light'),
('New', 'Mateo', 12, 47, 'Y le dijo uno: He aquí tu madre y tus hermanos están fuera, que te quieren hablar.', 'divine-light'),
('New', 'Mateo', 12, 48, 'Y respondiendo él al que le decía esto, dijo: ¿Quién es mi madre y quiénes son mis hermanos?', 'divine-light'),
('New', 'Mateo', 12, 49, 'Y extendiendo su mano hacia sus discípulos, dijo: He aquí mi madre y mis hermanos.', 'divine-light'),
('New', 'Mateo', 12, 50, 'Porque todo aquel que hiciere la voluntad de mi Padre que está en los cielos, ese es mi hermano, y hermana, y madre.', 'divine-light'),
('New', 'Mateo', 13, 1, 'Y AQUEL día, saliendo Jesús de casa, se sentó junto á la mar.', 'divine-light'),
('New', 'Mateo', 13, 2, 'Y se allegaron á él muchas gentes; y entrándose él en el barco, se sentó, y toda la gente estaba á la ribera.', 'divine-light'),
('New', 'Mateo', 13, 3, 'Y les habló muchas cosas por parábolas, diciendo: He aquí el que sembraba salió á sembrar.', 'divine-light'),
('New', 'Mateo', 13, 4, 'Y sembrando, parte de la simiente cayó junto al camino; y vinieron las aves, y la comieron.', 'divine-light'),
('New', 'Mateo', 13, 5, 'Y parte cayó en pedregales, donde no tenía mucha tierra; y nació luego, porque no tenía profundidad de tierra:', 'divine-light'),
('New', 'Mateo', 13, 6, 'Mas en saliendo el sol, se quemó; y secóse, porque no tenía raíz.', 'divine-light'),
('New', 'Mateo', 13, 7, 'Y parte cayó en espinas; y las espinas crecieron, y la ahogaron.', 'divine-light'),
('New', 'Mateo', 13, 8, 'Y parte cayó en buena tierra, y dió fruto, cuál a ciento, cuál á sesenta, y cuál á treinta.', 'divine-light'),
('New', 'Mateo', 13, 9, 'Quien tiene oídos para oir, oiga.', 'divine-light'),
('New', 'Mateo', 13, 10, 'Entonces, llegándose los discípulos, le dijeron: ¿Por qué les hablas por parábolas?', 'divine-light'),
('New', 'Mateo', 13, 11, 'Y él respondiendo, les dijo: Por que á vosotros es concedido saber los misterios del reino de los cielos; mas á ellos no es concedido.', 'divine-light'),
('New', 'Mateo', 13, 12, 'Porque á cualquiera que tiene, se le dará, y tendrá más; pero al que no tiene, aun lo que tiene le será quitado.', 'divine-light'),
('New', 'Mateo', 13, 13, 'Por eso les hablo por parábolas; porque viendo no ven, y oyendo no oyen, ni entienden.', 'divine-light'),
('New', 'Mateo', 13, 14, 'De manera que se cumple en ellos la profecía de Isaías, que dice: De oído oiréis, y no entenderéis; Y viendo veréis, y no miraréis.', 'divine-light'),
('New', 'Mateo', 13, 15, 'Porque el corazón de este pueblo está engrosado, Y de los oídos oyen pesadamente, Y de sus ojos guiñan: Para que no vean de los ojos, Y oigan de los oídos, Y del corazón entiendan, Y se conviertan, Y yo los sane.', 'divine-light'),
('New', 'Mateo', 13, 16, 'Mas bienaventurados vuestros ojos, porque ven; y vuestros oídos, porque oyen.', 'divine-light'),
('New', 'Mateo', 13, 17, 'Porque de cierto os digo, que muchos profetas y justos desearon ver lo que veis, y no lo vieron: y oir lo que oís, y no lo oyeron.', 'divine-light'),
('New', 'Mateo', 13, 18, 'Oid, pues, vosotros la parábola del que siembra:', 'divine-light'),
('New', 'Mateo', 13, 19, 'Oyendo cualquiera la palabra del reino, y no entendiéndola, viene el malo, y arrebata lo que fué sembrado en su corazón: éste es el que fué sembrado junto al camino.', 'divine-light'),
('New', 'Mateo', 13, 20, 'Y el que fué sembrado en pedregales, éste es el que oye la palabra, y luego la recibe con gozo.', 'divine-light'),
('New', 'Mateo', 13, 21, 'Mas no tiene raíz en sí, antes es temporal que venida la aflicción ó la persecución por la palabra, luego se ofende.', 'divine-light'),
('New', 'Mateo', 13, 22, 'Y el que fué sembrado en espinas, éste es el que oye la palabra; pero el afán de este siglo y el engaño de las riquezas, ahogan la palabra, y hácese infructuosa.', 'divine-light'),
('New', 'Mateo', 13, 23, 'Mas el que fué sembrado en buena tierra, éste es el que oye y entiende la palabra, y el que lleva fruto: y lleva uno á ciento, y otro á sesenta, y otro á treinta.', 'divine-light'),
('New', 'Mateo', 13, 24, 'Otra parábola les propuso, diciendo: El reino de los cielos es semejante al hombre que siembra buena simiente en su campo:', 'divine-light'),
('New', 'Mateo', 13, 25, 'Mas durmiendo los hombres, vino su enemigo, y sembró cizaña entre el trigo, y se fué.', 'divine-light'),
('New', 'Mateo', 13, 26, 'Y como la hierba salió é hizo fruto, entonces apareció también la cizaña.', 'divine-light'),
('New', 'Mateo', 13, 27, 'Y llegándose los siervos del padre de la familia, le dijeron: Señor, ¿no sembraste buena simiente en tu campo? ¿de dónde, pues, tiene cizaña?', 'divine-light'),
('New', 'Mateo', 13, 28, 'Y él les dijo: Un hombre enemigo ha hecho esto. Y los siervos le dijeron: ¿Quieres, pues, que vayamos y la cojamos?', 'divine-light'),
('New', 'Mateo', 13, 29, 'Y él dijo: No; porque cogiendo la cizaña, no arranquéis también con ella el trigo.', 'divine-light'),
('New', 'Mateo', 13, 30, 'Dejad crecer juntamente lo uno y lo otro hasta la siega; y al tiempo de la siega yo diré á los segadores: Coged primero la cizaña, y atadla en manojos para quemarla; mas recoged el trigo en mi alfolí.', 'divine-light'),
('New', 'Mateo', 13, 31, 'Otra parábola les propuso, diciendo: El reino de los cielos es semejante al grano de mostaza, que tomándolo alguno lo sembró en su campo:', 'divine-light'),
('New', 'Mateo', 13, 32, 'El cual á la verdad es la más pequeña de todas las simientes; mas cuando ha crecido, es la mayor de las hortalizas, y se hace árbol, que vienen las aves del cielo y hacen nidos en sus ramas.', 'divine-light'),
('New', 'Mateo', 13, 33, 'Otra parábola les dijo: El reino de los cielos es semejante á la levadura que tomó una mujer, y escondió en tres medidas de harina, hasta que todo quedó leudo.', 'divine-light'),
('New', 'Mateo', 13, 34, 'Todo esto habló Jesús por parábolas á las gentes, y sin parábolas no les hablaba:', 'divine-light'),
('New', 'Mateo', 13, 35, 'Para que se cumpliese lo que fué dicho por el profeta, que dijo: Abriré en parábolas mi boca; Rebosaré cosas escondidas desde la fundación del mundo.', 'divine-light'),
('New', 'Mateo', 13, 36, 'Entonces, despedidas las gentes, Jesús se vino á casa; y llegándose á él sus discípulos, le dijeron: Decláranos la parábola de la cizaña del campo.', 'divine-light'),
('New', 'Mateo', 13, 37, 'Y respondiendo él, les dijo: El que siembra la buena simiente es el Hijo del hombre;', 'divine-light'),
('New', 'Mateo', 13, 38, 'Y el campo es el mundo; y la buena simiente son los hijos del reino, y la cizaña son los hijos del malo;', 'divine-light'),
('New', 'Mateo', 13, 39, 'Y el enemigo que la sembró, es el diablo; y la siega es el fin del mundo, y los segadores son los ángeles.', 'divine-light'),
('New', 'Mateo', 13, 40, 'De manera que como es cogida la cizaña, y quemada al fuego, así será en el fin de este siglo.', 'divine-light'),
('New', 'Mateo', 13, 41, 'Enviará el Hijo del hombre sus ángeles, y cogerán de su reino todos los escándalos, y los que hacen iniquidad,', 'divine-light'),
('New', 'Mateo', 13, 42, 'Y los echarán en el horno de fuego: allí será el lloro y el crujir de dientes.', 'divine-light'),
('New', 'Mateo', 13, 43, 'Entonces los justos resplandecerán como el sol en el reino de su Padre: el que tiene oídos para oir, oiga.', 'divine-light'),
('New', 'Mateo', 13, 44, 'Además, el reino de los cielos es semejante al tesoro escondido en el campo; el cual hallado, el hombre lo encubre, y de gozo de ello va, y vende todo lo que tiene, y compra aquel campo.', 'divine-light'),
('New', 'Mateo', 13, 45, 'También el reino de los cielos es semejante al hombre tratante, que busca buenas perlas;', 'divine-light'),
('New', 'Mateo', 13, 46, 'Que hallando una preciosa perla, fué y vendió todo lo que tenía, y la compró.', 'divine-light'),
('New', 'Mateo', 13, 47, 'Asimismo el reino de los cielos es semejante á la red, que echada en la mar, coge de todas suertes de peces:', 'divine-light'),
('New', 'Mateo', 13, 48, 'La cual estando llena, la sacaron á la orilla; y sentados, cogieron lo bueno en vasos, y lo malo echaron fuera.', 'divine-light'),
('New', 'Mateo', 13, 49, 'Así será al fin del siglo: saldrán los ángeles, y apartarán á los malos de entre los justos,', 'divine-light'),
('New', 'Mateo', 13, 50, 'Y los echarán en el horno del fuego: allí será el lloro y el crujir de dientes.', 'divine-light'),
('New', 'Mateo', 13, 51, 'Díceles Jesús: ¿Habéis entendido todas estas cosas? Ellos responden: Sí, Señor.', 'divine-light'),
('New', 'Mateo', 13, 52, 'Y él les dijo: Por eso todo escriba docto en el reino de los cielos, es semejante á un padre de familia, que saca de su tesoro cosas nuevas y cosas viejas.', 'divine-light'),
('New', 'Mateo', 13, 53, 'Y aconteció que acabando Jesús estas parábolas, pasó de allí.', 'divine-light'),
('New', 'Mateo', 13, 54, 'Y venido á su tierra, les enseñaba en la sinagoga de ellos, de tal manera que ellos estaban atónitos, y decían: ¿De dónde tiene éste esta sabiduría, y estas maravillas?', 'divine-light'),
('New', 'Mateo', 13, 55, '¿No es éste el hijo del carpintero? ¿no se llama su madre María, y sus hermanos Jacobo y José, y Simón, y Judas?', 'divine-light'),
('New', 'Mateo', 13, 56, '¿Y no están todas sus hermanas con nosotros? ¿De dónde, pues, tiene éste todas estas cosas?', 'divine-light'),
('New', 'Mateo', 13, 57, 'Y se escandalizaban en él. Mas Jesús les dijo: No hay profeta sin honra sino en su tierra y en su casa.', 'divine-light'),
('New', 'Mateo', 13, 58, 'Y no hizo allí muchas maravillas, á causa de la incredulidad de ellos.', 'divine-light'),
('New', 'Mateo', 14, 1, 'EN aquel tiempo Herodes el tetrarca oyó la fama de Jesús,', 'divine-light'),
('New', 'Mateo', 14, 2, 'Y dijo á sus criados: Este es Juan el Bautista: él ha resucitado de los muertos, y por eso virtudes obran en él.', 'divine-light'),
('New', 'Mateo', 14, 3, 'Porque Herodes había prendido á Juan, y le había aprisionado y puesto en la cárcel, por causa de Herodías, mujer de Felipe su hermano;', 'divine-light'),
('New', 'Mateo', 14, 4, 'Porque Juan le decía: No te es lícito tenerla.', 'divine-light'),
('New', 'Mateo', 14, 5, 'Y quería matarle, mas temía al pueblo; porque le tenían como á profeta.', 'divine-light'),
('New', 'Mateo', 14, 6, 'Mas celebrándose el día del nacimiento de Herodes, la hija de Herodías danzó en medio, y agradó á Herodes.', 'divine-light'),
('New', 'Mateo', 14, 7, 'Y prometió él con juramento de darle todo lo que pidiese.', 'divine-light'),
('New', 'Mateo', 14, 8, 'Y ella, instruída primero de su madre, dijo: Dame aquí en un plato la cabeza de Juan el Bautista.', 'divine-light'),
('New', 'Mateo', 14, 9, 'Entonces el rey se entristeció; mas por el juramento, y por los que estaban juntamente á la mesa, mandó que se le diese.', 'divine-light'),
('New', 'Mateo', 14, 10, 'Y enviando, degolló á Juan en la cárcel.', 'divine-light'),
('New', 'Mateo', 14, 11, 'Y fué traída su cabeza en un plato y dada á la muchacha; y ella la presentó á su madre.', 'divine-light'),
('New', 'Mateo', 14, 12, 'Entonces llegaron sus discípulos, y tomaron el cuerpo, y lo enterraron; y fueron, y dieron las nuevas á Jesús.', 'divine-light'),
('New', 'Mateo', 14, 13, 'Y oyéndo lo Jesús, se apartó de allí en un barco á un lugar descierto, apartado: y cuando las gentes lo oyeron, le siguieron á pie de las ciudades.', 'divine-light'),
('New', 'Mateo', 14, 14, 'Y saliendo Jesús, vió un gran gentío, y tuvo compasión de ellos, y sanó á los que de ellos había enfermos.', 'divine-light'),
('New', 'Mateo', 14, 15, 'Y cuando fué la tarde del día, se llegaron á él sus discípulos, diciendo: El lugar es desierto, y el tiempo es ya pasado: despide las gentes, para que se vayan por las aldeas, y compren para sí de comer.', 'divine-light'),
('New', 'Mateo', 14, 16, 'Y Jesús les dijo: No tienen necesidad de irse: dadles vosotros de comer.', 'divine-light'),
('New', 'Mateo', 14, 17, 'Y ellos dijeron: No tenemos aquí sino cinco panes y dos peces.', 'divine-light'),
('New', 'Mateo', 14, 18, 'Y él les dijo: Traédmelos acá.', 'divine-light'),
('New', 'Mateo', 14, 19, 'Y mandando á las gentes recostarse sobre la hierba, tomando los cinco panes y los dos peces, alzando los ojos al cielo, bendijo, y partió y dió los panes á los discípulos, y los discípulos á las gentes.', 'divine-light'),
('New', 'Mateo', 14, 20, 'Y comieron todos, y se hartaron; y alzaron lo que sobró de los pedazos, doce cestas llenas.', 'divine-light'),
('New', 'Mateo', 14, 21, 'Y los que comieron fueron como cinco mil hombres, sin las mujeres y los niños.', 'divine-light'),
('New', 'Mateo', 14, 22, 'Y luego Jesús hizo á sus discípulos entrar en el barco, é ir delante de él á la otra parte del lago, entre tanto que él despedía á las gentes.', 'divine-light'),
('New', 'Mateo', 14, 23, 'Y despedidas las gentes, subió al monte, apartado, á orar: y como fué la tarde del día, estaba allí solo.', 'divine-light'),
('New', 'Mateo', 14, 24, 'Y ya el barco estaba en medio de la mar, atormentado de las ondas; porque el viento era contrario.', 'divine-light'),
('New', 'Mateo', 14, 25, 'Mas á la cuarta vela de la noche, Jesús fué á ellos andando sobre la mar.', 'divine-light'),
('New', 'Mateo', 14, 26, 'Y los discípulos, viéndole andar sobre la mar, se turbaron, diciendo: Fantasma es. Y dieron voces de miedo.', 'divine-light'),
('New', 'Mateo', 14, 27, 'Mas luego Jesús les habló, diciendo: Confiad, yo soy; no tengáis miedo.', 'divine-light'),
('New', 'Mateo', 14, 28, 'Entonces le respondió Pedro, y dijo: Señor, si tú eres, manda que yo vaya á ti sobre las aguas.', 'divine-light'),
('New', 'Mateo', 14, 29, 'Y él dijo: Ven. Y descendiendo Pedro del barco, andaba sobre las aguas para ir á Jesús.', 'divine-light'),
('New', 'Mateo', 14, 30, 'Mas viendo el viento fuerte, tuvo miedo; y comenzándose á hundir, dió voces, diciendo: Señor, sálvame.', 'divine-light'),
('New', 'Mateo', 14, 31, 'Y luego Jesús, extendiendo la mano, trabó de él, y le dice: Oh hombre de poca fe, ¿por qué dudaste?', 'divine-light'),
('New', 'Mateo', 14, 32, 'Y como ellos entraron en el barco, sosegóse el viento.', 'divine-light'),
('New', 'Mateo', 14, 33, 'Entonces los que estaban en el barco, vinieron y le adoraron, diciendo: Verdaderamente eres Hijo de Dios.', 'divine-light'),
('New', 'Mateo', 14, 34, 'Y llegando á la otra parte, vinieron á la tierra de Genezaret.', 'divine-light'),
('New', 'Mateo', 14, 35, 'Y como le conocieron los hombres de aquel lugar, enviaron por toda aquella tierra alrededor, y trajeron á él todos los enfermos;', 'divine-light'),
('New', 'Mateo', 14, 36, 'Y le rogaban que solamente tocasen el borde de su manto; y todos los que tocaron, quedaron sanos.', 'divine-light'),
('New', 'Mateo', 15, 1, 'ENTONCES llegaron á Jesús ciertos escribas y Fariseos de Jerusalem, diciendo:', 'divine-light'),
('New', 'Mateo', 15, 2, '¿Por qué tus discípulos traspasan la tradición de los ancianos? porque no se lavan las manos cuando comen pan.', 'divine-light'),
('New', 'Mateo', 15, 3, 'Y él respondiendo, les dijo: ¿Por qué también vosotros traspasáis el mandamiento de Dios por vuestra tradición?', 'divine-light'),
('New', 'Mateo', 15, 4, 'Porque Dios mandó, diciendo: Honra al padre y á la madre, y, El que maldijere al padre ó á la madre, muera de muerte.', 'divine-light'),
('New', 'Mateo', 15, 5, 'Mas vosotros decís: Cualquiera que dijere al padre ó á la madre: Es ya ofrenda mía á Dios todo aquello con que pudiera valerte;', 'divine-light'),
('New', 'Mateo', 15, 6, 'No deberá honrar á su padre ó á su madre con socorro. Así habéis invalidado el mandamiento de Dios por vuestra tradición.', 'divine-light'),
('New', 'Mateo', 15, 7, 'Hipócritas, bien profetizó de vosotros Isaías, diciendo:', 'divine-light'),
('New', 'Mateo', 15, 8, 'Este pueblo de labios me honra; Mas su corazón lejos está de mí.', 'divine-light'),
('New', 'Mateo', 15, 9, 'Mas en vano me honran, Enseñando doctrinas y mandamientos de hombres.', 'divine-light'),
('New', 'Mateo', 15, 10, 'Y llamando á sí las gentes, les dijo: Oid, y entended:', 'divine-light'),
('New', 'Mateo', 15, 11, 'No lo que entra en la boca contamina al hombre; mas lo que sale de la boca, esto contamina al hombre.', 'divine-light'),
('New', 'Mateo', 15, 12, 'Entonces llegándose sus discípulos, le dijeron: ¿Sabes que los Fariseos oyendo esta palabra se ofendieron?', 'divine-light'),
('New', 'Mateo', 15, 13, 'Mas respondiendo él, dijo: Toda planta que no plantó mi Padre celestial, será desarraigada.', 'divine-light'),
('New', 'Mateo', 15, 14, 'Dejadlos: son ciegos guías de ciegos; y si el ciego guiare al ciego, ambos caerán en el hoyo.', 'divine-light'),
('New', 'Mateo', 15, 15, 'Y respondiendo Pedro, le dijo: Decláranos esta parábola.', 'divine-light'),
('New', 'Mateo', 15, 16, 'Y Jesús dijo: ¿Aun también vosotros sois sin entendimiento?', 'divine-light'),
('New', 'Mateo', 15, 17, '¿No entendéis aún, que todo lo que entra en la boca, va al vientre, y es echado en la letrina?', 'divine-light'),
('New', 'Mateo', 15, 18, 'Mas lo que sale de la boca, del corazón sale; y esto contamina al hombre.', 'divine-light'),
('New', 'Mateo', 15, 19, 'Porque del corazón salen los malos pensamientos, muertes, adulterios, fornicaciones, hurtos, falsos testimonios, blasfemias.', 'divine-light'),
('New', 'Mateo', 15, 20, 'Estas cosas son las que contaminan al hombre: que comer con las manos por lavar no contamina al hombre.', 'divine-light'),
('New', 'Mateo', 15, 21, 'Y saliendo Jesús de allí, se fué á las partes de Tiro y de Sidón.', 'divine-light'),
('New', 'Mateo', 15, 22, 'Y he aquí una mujer Cananea, que había salido de aquellos términos, clamaba, diciéndole: Señor, Hijo de David, ten misericordia de mí; mi hija es malamente atormentada del demonio.', 'divine-light'),
('New', 'Mateo', 15, 23, 'Mas él no le respondió palabra. Entonces llegándose sus discípulos, le rogaron, diciendo: Despáchala, pues da voces tras nosotros.', 'divine-light'),
('New', 'Mateo', 15, 24, 'Y él respondiendo, dijo: No soy enviado sino á las ovejas perdidas de la casa de Israel.', 'divine-light'),
('New', 'Mateo', 15, 25, 'Entonces ella vino, y le adoró, diciendo: Señor socórreme.', 'divine-light'),
('New', 'Mateo', 15, 26, 'Y respondiendo él, dijo: No es bien tomar el pan de los hijos, y echarlo á los perrillos.', 'divine-light'),
('New', 'Mateo', 15, 27, 'Y ella dijo: Sí, Señor; mas los perrillos comen de las migajas que caen de la mesa de sus señores.', 'divine-light'),
('New', 'Mateo', 15, 28, 'Entonces respondiendo Jesús, dijo: Oh mujer, grande es tu fe; sea hecho contigo como quieres. Y fué sana su hija desde aquella hora.', 'divine-light'),
('New', 'Mateo', 15, 29, 'Y partido Jesús de allí, vino junto al mar de Galilea: y subiendo al monte, se sentó allí.', 'divine-light'),
('New', 'Mateo', 15, 30, 'Y llegaron á él muchas gentes, que tenían consigo cojos, ciegos, mudos, mancos, y otros muchos enfermos: y los echaron á los pies de Jesús, y los sanó:', 'divine-light'),
('New', 'Mateo', 15, 31, 'De manera que se maravillaban las gentes, viendo hablar los mudos, los mancos sanos, andar los cojos, y ver los ciegos: y glorificaron al Dios de Israel.', 'divine-light'),
('New', 'Mateo', 15, 32, 'Y Jesús llamando á sus discípulos, dijo: Tengo lástima de la gente, que ya hace tres días que perseveran conmigo, y no tienen qué comer; y enviarlos ayunos no quiero, porque no desmayen en el camino.', 'divine-light'),
('New', 'Mateo', 15, 33, 'Entonces sus discípulos le dicen: ¿Dónde tenemos nosotros tantos panes en el desierto, que hartemos á tan gran compañía?', 'divine-light'),
('New', 'Mateo', 15, 34, 'Y Jesús les dice: ¿Cuántos panes tenéis? Y ellos dijeron: Siete, y unos pocos pececillos.', 'divine-light'),
('New', 'Mateo', 15, 35, 'Y mandó á las gentes que se recostasen sobre la tierra.', 'divine-light'),
('New', 'Mateo', 15, 36, 'Y tomando los siete panes y los peces, haciendo gracias, partió y dió á sus discípulos; y los discípulos á la gente.', 'divine-light'),
('New', 'Mateo', 15, 37, 'Y comieron todos, y se hartaron: y alzaron lo que sobró de los pedazos, siete espuertas llenas.', 'divine-light'),
('New', 'Mateo', 15, 38, 'Y eran los que habían comido, cuatro mil hombres, sin las mujeres y los niños.', 'divine-light'),
('New', 'Mateo', 15, 39, 'Entonces, despedidas las gentes, subió en el barco: y vino á los términos de Magdalá.', 'divine-light'),
('New', 'Mateo', 16, 1, 'Y LLEGANDOSE los Fariseos y los Saduceos para tentarle, le pedían que les mostrase señal del cielo.', 'divine-light'),
('New', 'Mateo', 16, 2, 'Mas él respondiendo, les dijo: Cuando es la tarde del día, decís: Sereno; porque el cielo tiene arreboles.', 'divine-light'),
('New', 'Mateo', 16, 3, 'Y á la mañana: Hoy tempestad; porque tiene arreboles el cielo triste. Hipócritas, que sabéis hacer diferencia en la faz del cielo; ¿y en las señales de los tiempos no podéis?', 'divine-light'),
('New', 'Mateo', 16, 4, 'La generación mala y adulterina demanda señal; mas señal no le será dada, sino la señal de Jonás profeta. Y dejándolos, se fué.', 'divine-light'),
('New', 'Mateo', 16, 5, 'Y viniendo sus discípulos de la otra parte del lago, se habían olvidado de tomar pan.', 'divine-light'),
('New', 'Mateo', 16, 6, 'Y Jesús les dijo: Mirad, y guardaos de la levadura de los Fariseos y de los Saduceos.', 'divine-light'),
('New', 'Mateo', 16, 7, 'Y ellos pensaban dentro de sí, diciendo: Esto dice porque no tomamos pan.', 'divine-light'),
('New', 'Mateo', 16, 8, 'Y entendiéndolo Jesús, les dijo: ¿Por qué pensáis dentro de vosotros, hombres de poca fe, que no tomasteis pan?', 'divine-light'),
('New', 'Mateo', 16, 9, '¿No entendéis aún, ni os acordáis de los cinco panes entre cinco mil hombres, y cuántos cestos alzasteis?', 'divine-light'),
('New', 'Mateo', 16, 10, '¿Ni de los siete panes entre cuatro mil, y cuántas espuertas tomasteis?', 'divine-light'),
('New', 'Mateo', 16, 11, '¿Cómo es que no entendéis que no por el pan os dije, que os guardaseis de la levadura de los Fariseos y de los Saduceos?', 'divine-light'),
('New', 'Mateo', 16, 12, 'Entonces entendieron que no les había dicho que se guardasen de la levadura de pan, sino de la doctrina de los Fariseos y de los Saduceos.', 'divine-light'),
('New', 'Mateo', 16, 13, 'Y viniendo Jesús á las partes de Cesarea de Filipo, preguntó á sus discípulos, diciendo: ¿Quién dicen los hombres que es el Hijo del hombre?', 'divine-light'),
('New', 'Mateo', 16, 14, 'Y ellos dijeron: Unos, Juan el Bautista; y otros, Elías; y otros; Jeremías, ó alguno de los profetas.', 'divine-light'),
('New', 'Mateo', 16, 15, 'El les dice: Y vosotros, ¿quién decís que soy?', 'divine-light'),
('New', 'Mateo', 16, 16, 'Y respondiendo Simón Pedro, dijo: Tú eres el Cristo, el Hijo del Dios viviente.', 'divine-light'),
('New', 'Mateo', 16, 17, 'Entonces, respondiendo Jesús, le dijo: Bienaventurado eres, Simón, hijo de Jonás; porque no te lo reveló carne ni sangre, mas mi Padre que está en los cielos.', 'divine-light'),
('New', 'Mateo', 16, 18, 'Mas yo también te digo, que tú eres Pedro, y sobre esta piedra edificaré mi iglesia; y las puertas del infierno no prevalecerán contra ella.', 'divine-light'),
('New', 'Mateo', 16, 19, 'Y á ti daré las llaves del reino de los cielos; y todo lo que ligares en la tierra será ligado en los cielos; y todo lo que desatares en la tierra será desatado en los cielos.', 'divine-light'),
('New', 'Mateo', 16, 20, 'Entonces mandó á sus discípulos que á nadie dijesen que él era Jesús el Cristo.', 'divine-light'),
('New', 'Mateo', 16, 21, 'Desde aquel tiempo comenzó Jesús á declarar á sus discípulos que le convenía ir á Jerusalem, y padecer mucho de los ancianos, y de los príncipes de los sacerdotes, y de los escribas; y ser muerto, y resucitar al tercer día.', 'divine-light'),
('New', 'Mateo', 16, 22, 'Y Pedro, tomándolo aparte, comenzó á reprenderle, diciendo: Señor, ten compasión de ti: en ninguna manera esto te acontezca.', 'divine-light'),
('New', 'Mateo', 16, 23, 'Entonces él, volviéndose, dijo á Pedro: Quítate de delante de mí, Satanás; me eres escándalo; porque no entiendes lo que es de Dios sino lo que es de los hombres.', 'divine-light'),
('New', 'Mateo', 16, 24, 'Entonces Jesús dijo á sus discípulos: Si alguno quiere venir en pos de mí, niéguese á sí mismo, y tome su cruz, y sígame.', 'divine-light'),
('New', 'Mateo', 16, 25, 'Porque cualquiera que quisiere salvar su vida, la perderá, y cualquiera que perdiere su vida por causa de mí, la hallará.', 'divine-light'),
('New', 'Mateo', 16, 26, 'Porque ¿de qué aprovecha al hombre, si granjeare todo el mundo, y perdiere su alma? O ¿qué recompensa dará el hombre por su alma?', 'divine-light'),
('New', 'Mateo', 16, 27, 'Porque el Hijo del hombre vendrá en la gloria de su Padre con sus ángeles, y entonces pagará á cada uno conforme á sus obras.', 'divine-light'),
('New', 'Mateo', 16, 28, 'De cierto os digo: hay algunos de los que están aquí, que no gustarán la muerte, hasta que hayan visto al Hijo del hombre viniendo en su reino.', 'divine-light'),
('New', 'Mateo', 17, 1, 'Y DESPUÉS de seis días, Jesús toma á Pedro, y á Jacobo, y á Juan su hermano, y los lleva aparte á un monte alto:', 'divine-light'),
('New', 'Mateo', 17, 2, 'Y se transfiguró delante de ellos; y resplandeció su rostro como el sol, y sus vestidos fueron blancos como la luz.', 'divine-light'),
('New', 'Mateo', 17, 3, 'Y he aquí les aparecieron Moisés y Elías, hablando con él.', 'divine-light'),
('New', 'Mateo', 17, 4, 'Y respondiendo Pedro, dijo á Jesús: Señor, bien es que nos quedemos aquí: si quieres, hagamos aquí tres pabellones: para ti uno, y para Moisés otro, y otro para Elías.', 'divine-light'),
('New', 'Mateo', 17, 5, 'Y estando aún él hablando, he aquí una nube de luz que los cubrió; y he aquí una voz de la nube, que dijo: Este es mi Hijo amado, en el cual tomo contentamiento: á él oíd.', 'divine-light'),
('New', 'Mateo', 17, 6, 'Y oyendo esto los discípulos, cayeron sobre sus rostros, y temieron en gran manera.', 'divine-light'),
('New', 'Mateo', 17, 7, 'Entonces Jesús llegando, los tocó, y dijo: Levantaos, y no temáis.', 'divine-light'),
('New', 'Mateo', 17, 8, 'Y alzando ellos sus ojos, á nadie vieron, sino á solo Jesús.', 'divine-light'),
('New', 'Mateo', 17, 9, 'Y como descendieron del monte, les mandó Jesús, diciendo: No digáis á nadie la visión, hasta que el Hijo del hombre resucite de los muertos.', 'divine-light'),
('New', 'Mateo', 17, 10, 'Entonces sus discípulos le preguntaron, diciendo: ¿Por qué dicen pues los escribas que es menester que Elías venga primero?', 'divine-light'),
('New', 'Mateo', 17, 11, 'Y respondiendo Jesús, les dijo: á la verdad, Elías vendrá primero, y restituirá todas las cosas.', 'divine-light'),
('New', 'Mateo', 17, 12, 'Mas os digo, que ya vino Elías, y no le conocieron; antes hicieron en él todo lo que quisieron: así también el Hijo del hombre padecerá de ellos.', 'divine-light'),
('New', 'Mateo', 17, 13, 'Los discípulos entonces entendieron, que les habló de Juan el Bautista.', 'divine-light'),
('New', 'Mateo', 17, 14, 'Y como ellos llegaron al gentío, vino á él un hombre hincándosele de rodillas,', 'divine-light'),
('New', 'Mateo', 17, 15, 'Y diciendo: Señor, ten misericordia de mi hijo, que es lunático, y padece malamente; porque muchas veces cae en el fuego, y muchas en el agua.', 'divine-light'),
('New', 'Mateo', 17, 16, 'Y le he presentado á tus discípulos, y no le han podido sanar.', 'divine-light'),
('New', 'Mateo', 17, 17, 'Y respondiendo Jesús, dijo: ¡Oh generación infiel y torcida! ¿hasta cuándo tengo de estar con vosotros? ¿hasta cuándo os tengo de sufrir? traédmele acá.', 'divine-light'),
('New', 'Mateo', 17, 18, 'Y Jesús le reprendió, y salió el demonio de él; y el mozo fué sano desde aquella hora.', 'divine-light'),
('New', 'Mateo', 17, 19, 'Entonces, llegándose los discípulos á Jesús, aparte, dijeron: ¿Por qué nosotros no lo pudimos echar fuera?', 'divine-light'),
('New', 'Mateo', 17, 20, 'Y Jesús les dijo: Por vuestra incredulidad; porque de cierto os digo, que si tuviereis fe como un grano de mostaza, diréis á este monte: Pásate de aquí allá: y se pasará: y nada os será imposible.', 'divine-light'),
('New', 'Mateo', 17, 21, 'Mas este linaje no sale sino por oración y ayuno.', 'divine-light'),
('New', 'Mateo', 17, 22, 'Y estando ellos en Galilea, Jesús les dijo: El Hijo del hombre será entregado en manos de hombres,', 'divine-light'),
('New', 'Mateo', 17, 23, 'Y le matarán; mas al tercer día resucitará. Y ellos se entristecieron en gran manera.', 'divine-light'),
('New', 'Mateo', 17, 24, 'Y como llegaron á Capernaum, vinieron á Pedro los que cobraban las dos dracmas, y dijeron: ¿Vuestro Maestro no paga las dos dracmas?', 'divine-light'),
('New', 'Mateo', 17, 25, 'El dice: Sí. Y entrando él en casa, Jesús le habló antes, diciendo: ¿Qué te parece, Simón? Los reyes de la tierra, ¿de quién cobran los tributos ó el censo? ¿de sus hijos ó de los extraños?', 'divine-light'),
('New', 'Mateo', 17, 26, 'Pedro le dice: De los extraños. Jesús le dijo: Luego los hijos son francos.', 'divine-light'),
('New', 'Mateo', 17, 27, 'Mas porque no los escandalicemos, ve á la mar, y echa el anzuelo, y el primer pez que viniere, tómalo, y abierta su boca, hallarás un estatero: tómalo, y dáselo por mí y por ti.', 'divine-light'),
('New', 'Mateo', 18, 1, 'EN aquel tiempo se llegaron los discípulos á Jesús, diciendo: ¿Quién es el mayor en el reino de los cielos?', 'divine-light'),
('New', 'Mateo', 18, 2, 'Y llamando Jesús á un niño, le puso en medio de ellos,', 'divine-light'),
('New', 'Mateo', 18, 3, 'Y dijo: De cierto os digo, que si no os volviereis, y fuereis como niños, no entraréis en el reino de los cielos.', 'divine-light'),
('New', 'Mateo', 18, 4, 'Así que, cualquiera que se humillare como este niño, éste es el mayor en el reino de los cielos.', 'divine-light'),
('New', 'Mateo', 18, 5, 'Y cualquiera que recibiere á un tal niño en mi nombre, á mí recibe.', 'divine-light'),
('New', 'Mateo', 18, 6, 'Y cualquiera que escandalizare á alguno de estos pequeños que creen en mí, mejor le fuera que se le colgase al cuello una piedra de molino de asno, y que se le anegase en el profundo de la mar.', 'divine-light'),
('New', 'Mateo', 18, 7, '¡Ay del mundo por los escándalos! porque necesario es que vengan escándalos; mas ¡ay de aquel hombre por el cual viene el escándalo!', 'divine-light'),
('New', 'Mateo', 18, 8, 'Por tanto, si tu mano ó tu pie te fuere ocasión de caer, córtalo y echaló de ti: mejor te es entrar cojo ó manco en la vida, que teniendo dos manos ó dos pies ser echado en el fuego eterno.', 'divine-light'),
('New', 'Mateo', 18, 9, 'Y si tu ojo te fuere ocasión de caer, sácalo y échalo de ti: mejor te es entrar con un solo ojo en la vida, que teniendo dos ojos ser echado en el infierno del fuego.', 'divine-light'),
('New', 'Mateo', 18, 10, 'Mirad no tengáis en poco á alguno de estos pequeños; porque os digo que sus ángeles en los cielos ven siempre la faz de mi Padre que está en los cielos.', 'divine-light'),
('New', 'Mateo', 18, 11, 'Porque el Hijo del hombre ha venido para salvar lo que se había perdido.', 'divine-light'),
('New', 'Mateo', 18, 12, '¿Qué os parece? Si tuviese algún hombre cien ovejas, y se descarriase una de ellas, ¿no iría por los montes, dejadas las noventa y nueve, á buscar la que se había descarriado?', 'divine-light'),
('New', 'Mateo', 18, 13, 'Y si aconteciese hallarla, de cierto os digo, que más se goza de aquélla, que de las noventa y nueve que no se descarriaron.', 'divine-light'),
('New', 'Mateo', 18, 14, 'Así, no es la voluntad de vuestro Padre que está en los cielos, que se pierda uno de estos pequeños.', 'divine-light'),
('New', 'Mateo', 18, 15, 'Por tanto, si tu hermano pecare contra ti, ve, y redargúyele entre ti y él solo: si te oyere, has ganado á tu hermano.', 'divine-light'),
('New', 'Mateo', 18, 16, 'Mas si no te oyere, toma aún contigo uno ó dos, para que en boca de dos ó de tres testigos conste toda palabra.', 'divine-light'),
('New', 'Mateo', 18, 17, 'Y si no oyere á ellos, dilo á la iglesia: y si no oyere á la iglesia, tenle por étnico y publicano.', 'divine-light'),
('New', 'Mateo', 18, 18, 'De cierto os digo que todo lo que ligareis en la tierra, será ligado en el cielo; y todo lo que desatareis en la tierra, será desatado en el cielo.', 'divine-light'),
('New', 'Mateo', 18, 19, 'Otra vez os digo, que si dos de vosotros se convinieren en la tierra, de toda cosa que pidieren, les será hecho por mi Padre que está en los cielos.', 'divine-light'),
('New', 'Mateo', 18, 20, 'Porque donde están dos ó tres congregados en mi nombre, allí estoy en medio de ellos.', 'divine-light'),
('New', 'Mateo', 18, 21, 'Entonces Pedro, llegándose á él, dijo: Señor, ¿cuántas veces perdonaré á mi hermano que pecare contra mí? ¿hasta siete?', 'divine-light'),
('New', 'Mateo', 18, 22, 'Jesús le dice: No te digo hasta siete, mas aun hasta setenta veces siete.', 'divine-light'),
('New', 'Mateo', 18, 23, 'Por lo cual, el reino de los cielos es semejante á un hombre rey, que quiso hacer cuentas con sus siervos.', 'divine-light'),
('New', 'Mateo', 18, 24, 'Y comenzando á hacer cuentas, le fué presentado uno que le debía diez mil talentos.', 'divine-light'),
('New', 'Mateo', 18, 25, 'Mas á éste, no pudiendo pagar, mandó su señor venderle, y á su mujer é hijos, con todo lo que tenía, y que se le pagase.', 'divine-light'),
('New', 'Mateo', 18, 26, 'Entonces aquel siervo, postrado, le adoraba, diciendo: Señor, ten paciencia conmigo, y yo te lo pagaré todo.', 'divine-light'),
('New', 'Mateo', 18, 27, 'El señor, movido á misericordia de aquel siervo, le soltó y le perdonó la deuda.', 'divine-light'),
('New', 'Mateo', 18, 28, 'Y saliendo aquel siervo, halló á uno de sus consiervos, que le debía cien denarios; y trabando de él, le ahogaba, diciendo: Págame lo que debes.', 'divine-light'),
('New', 'Mateo', 18, 29, 'Entonces su consiervo, postrándose á sus pies, le rogaba, diciendo: Ten paciencia conmigo, y yo te lo pagaré todo.', 'divine-light'),
('New', 'Mateo', 18, 30, 'Mas él no quiso; sino fué, y le echó en la cárcel hasta que pagase la deuda.', 'divine-light'),
('New', 'Mateo', 18, 31, 'Y viendo sus consiervos lo que pasaba, se entristecieron mucho, y viniendo, declararon á su señor todo lo que había pasado.', 'divine-light'),
('New', 'Mateo', 18, 32, 'Entonces llamándole su señor, le dice: Siervo malvado, toda aquella deuda te perdoné, porque me rogaste:', 'divine-light'),
('New', 'Mateo', 18, 33, '¿No te convenía también á ti tener misericordia de tu consiervo, como también yo tuve misericordia de ti?', 'divine-light'),
('New', 'Mateo', 18, 34, 'Entonces su señor, enojado, le entregó á los verdugos, hasta que pagase todo lo que le debía.', 'divine-light'),
('New', 'Mateo', 18, 35, 'Así también hará con vosotros mi Padre celestial, si no perdonareis de vuestros corazones cada uno á su hermano sus ofensas.', 'divine-light'),
('New', 'Mateo', 19, 1, 'Y ACONTECIO que acabando Jesús estas palabras, se pasó de Galilea, y vino á los términos de Judea, pasado el Jordán.', 'divine-light'),
('New', 'Mateo', 19, 2, 'Y le siguieron muchas gentes, y los sanó allí.', 'divine-light'),
('New', 'Mateo', 19, 3, 'Entonces se llegaron á él los Fariseos, tentándole, y diciéndole: ¿Es lícito al hombre repudiar á su mujer por cualquiera causa?', 'divine-light'),
('New', 'Mateo', 19, 4, 'Y él respondiendo, les dijo: ¿No habéis leído que el que los hizo al principio, macho y hembra los hizo,', 'divine-light'),
('New', 'Mateo', 19, 5, 'Y dijo: Por tanto, el hombre dejará padre y madre, y se unirá á su mujer, y serán dos en una carne?', 'divine-light'),
('New', 'Mateo', 19, 6, 'Así que, no son ya más dos, sino una carne: por tanto, lo que Dios juntó, no lo aparte el hombre.', 'divine-light'),
('New', 'Mateo', 19, 7, 'Dícenle: ¿Por qué, pues, Moisés mandó dar carta de divorcio, y repudiarla?', 'divine-light'),
('New', 'Mateo', 19, 8, 'Díceles: Por la dureza de vuestro corazón Moisés os permitió repudiar á vuestras mujeres: mas al principio no fué así.', 'divine-light'),
('New', 'Mateo', 19, 9, 'Y yo os digo que cualquiera que repudiare á su mujer, si no fuere por causa de fornicación, y se casare con otra, adultera: y el que se casare con la repudiada, adultera.', 'divine-light'),
('New', 'Mateo', 19, 10, 'Dícenle sus discípulos: Si así es la condición del hombre con su mujer, no conviene casarse.', 'divine-light'),
('New', 'Mateo', 19, 11, 'Entonces él les dijo: No todos reciben esta palabra, sino aquellos á quienes es dado.', 'divine-light'),
('New', 'Mateo', 19, 12, 'Porque hay eunucos que nacieron así del vientre de su madre; y hay eunucos, que son hechos eunucos por los hombres; y hay eunucos que se hicieron á sí mismos eunucos por causa del reino de los cielos; el que pueda ser capaz de eso, séalo.', 'divine-light'),
('New', 'Mateo', 19, 13, 'Entonces le fueron presentados unos niños, para que pusiese las manos sobre ellos, y orase; y los discípulos les riñeron.', 'divine-light'),
('New', 'Mateo', 19, 14, 'Y Jesús dijo: Dejad á los niños, y no les impidáis de venir á mí; porque de los tales es el reino de los cielos.', 'divine-light'),
('New', 'Mateo', 19, 15, 'Y habiendo puesto sobre ellos las manos se partió de allí.', 'divine-light'),
('New', 'Mateo', 19, 16, 'Y he aquí, uno llegándose le dijo: Maestro bueno, ¿qué bien haré para tener la vida eterna?', 'divine-light'),
('New', 'Mateo', 19, 17, 'Y él le dijo: ¿Por qué me llamas bueno? Ninguno es bueno sino uno, es á saber, Dios: y si quieres entrar en la vida, guarda los mandamientos.', 'divine-light'),
('New', 'Mateo', 19, 18, 'Dícele: ¿Cuáles? Y Jesús dijo: No mataras: No adulterarás: No hurtarás: No dirás falso testimonio:', 'divine-light'),
('New', 'Mateo', 19, 19, 'Honra á tu padre y á tu madre: y, Amarás á tu prójimo como á ti mismo.', 'divine-light'),
('New', 'Mateo', 19, 20, 'Dícele el mancebo: Todo esto guardé desde mi juventud: ¿qué más me falta?', 'divine-light'),
('New', 'Mateo', 19, 21, 'Dícele Jesús: Si quieres ser perfecto, anda, vende lo que tienes, y da lo á los pobres, y tendrás tesoro en el cielo; y ven, sígueme.', 'divine-light'),
('New', 'Mateo', 19, 22, 'Y oyendo el mancebo esta palabra, se fué triste, porque tenía muchas posesiones.', 'divine-light'),
('New', 'Mateo', 19, 23, 'Entonces Jesús dijo á sus discípulos: De cierto os digo, que un rico difícilmente entrará en el reino de los cielos.', 'divine-light'),
('New', 'Mateo', 19, 24, 'Mas os digo, que más liviano trabajo es pasar un camello por el ojo de una aguja, que entrar un rico en el reino de Dios.', 'divine-light'),
('New', 'Mateo', 19, 25, 'Mas sus discípulos, oyendo estas cosas, se espantaron en gran manera, diciendo: ¿Quién pues podrá ser salvo?', 'divine-light'),
('New', 'Mateo', 19, 26, 'Y mirándo los Jesús, les dijo: Para con los hombres imposible es esto; mas para con Dios todo es posible.', 'divine-light'),
('New', 'Mateo', 19, 27, 'Entonces respondiendo Pedro, le dijo: He aquí, nosotros hemos dejado todo, y te hemos seguido: ¿qué pues tendremos?', 'divine-light'),
('New', 'Mateo', 19, 28, 'Y Jesús les dijo: De cierto os digo, que vosotros que me habéis seguido, en la regeneración, cuando se sentará el Hijo del hombre en el trono de su gloria, vosotros también os sentaréis sobre doce tronos, para juzgar á las doce tribus de Israel.', 'divine-light'),
('New', 'Mateo', 19, 29, 'Y cualquiera que dejare casas, ó hermanos, ó hermanas, ó padre, ó madre, ó mujer, ó hijos, ó tierras, por mi nombre, recibirá cien veces tanto, y heredará la vida eterna.', 'divine-light'),
('New', 'Mateo', 19, 30, 'Mas muchos primeros serán postreros, y postreros primeros.', 'divine-light'),
('New', 'Mateo', 20, 1, 'PORQUE el reino de los cielos es semejante á un hombre, padre de familia, que salió por la mañana á ajustar obreros para su viña.', 'divine-light'),
('New', 'Mateo', 20, 2, 'Y habiéndose concertado con los obreros en un denario al día, los envió á su viña.', 'divine-light'),
('New', 'Mateo', 20, 3, 'Y saliendo cerca de la hora de las tres, vió otros que estaban en la plaza ociosos;', 'divine-light'),
('New', 'Mateo', 20, 4, 'Y les dijo: Id también vosotros á mi viña, y os daré lo que fuere justo. Y ellos fueron.', 'divine-light'),
('New', 'Mateo', 20, 5, 'Salió otra vez cerca de las horas sexta y nona, é hizo lo mismo.', 'divine-light'),
('New', 'Mateo', 20, 6, 'Y saliendo cerca de la hora undécima, halló otros que estaban ociosos; y díceles: ¿Por qué estáis aquí todo el día ociosos?', 'divine-light'),
('New', 'Mateo', 20, 7, 'Dícenle: Porque nadie nos ha ajustado. Díceles: Id también vosotros á la viña, y recibiréis lo que fuere justo.', 'divine-light'),
('New', 'Mateo', 20, 8, 'Y cuando fué la tarde del día, el señor de la viña dijo á su mayordomo: Llama á los obreros y págales el jornal, comenzando desde los postreros hasta los primeros.', 'divine-light'),
('New', 'Mateo', 20, 9, 'Y viniendo los que habían ido cerca de la hora undécima, recibieron cada uno un denario.', 'divine-light'),
('New', 'Mateo', 20, 10, 'Y viniendo también los primeros, pensaron que habían de recibir más; pero también ellos recibieron cada uno un denario.', 'divine-light'),
('New', 'Mateo', 20, 11, 'Y tomándolo, murmuraban contra el padre de la familia,', 'divine-light'),
('New', 'Mateo', 20, 12, 'Diciendo: Estos postreros sólo han trabajado una hora, y los has hecho iguales á nosotros, que hemos llevado la carga y el calor del día.', 'divine-light'),
('New', 'Mateo', 20, 13, 'Y él respondiendo, dijo á uno de ellos: Amigo, no te hago agravio; ¿no te concertaste conmigo por un denario?', 'divine-light'),
('New', 'Mateo', 20, 14, 'Toma lo que es tuyo, y vete; mas quiero dar á este postrero, como á ti.', 'divine-light'),
('New', 'Mateo', 20, 15, '¿No me es lícito á mi hacer lo que quiero con lo mío? ó ¿es malo tu ojo, porque yo soy bueno?', 'divine-light'),
('New', 'Mateo', 20, 16, 'Así los primeros serán postreros, y los postreros primeros: porque muchos son llamados, mas pocos escogidos.', 'divine-light'),
('New', 'Mateo', 20, 17, 'Y subiendo Jesús á Jerusalem, tomó sus doce discípulos aparte en el camino, y les dijo:', 'divine-light'),
('New', 'Mateo', 20, 18, 'He aquí subimos á Jerusalem, y el Hijo del hombre será entregado á los principes de los sacerdotes y á los escribas, y le condenarán á muerte;', 'divine-light'),
('New', 'Mateo', 20, 19, 'Y le entregarán á los Gentiles para que le escarnezcan, y azoten, y crucifiquen; mas al tercer día resucitará.', 'divine-light'),
('New', 'Mateo', 20, 20, 'Entonces se llegó á él la madre de los hijos de Zebedeo con sus hijos, adorándo le, y pidiéndole algo.', 'divine-light'),
('New', 'Mateo', 20, 21, 'Y él le dijo: ¿Qué quieres? Ella le dijo: Di que se sienten estos dos hijos míos, el uno á tu mano derecha, y el otro á tu izquierda, en tu reino.', 'divine-light'),
('New', 'Mateo', 20, 22, 'Entonces Jesús respondiendo, dijo: No sabéis lo que pedís: ¿podéis beber el vaso que yo he de beber, y ser bautizados del bautismo de que yo soy bautizado? Y ellos le dicen: Podemos.', 'divine-light'),
('New', 'Mateo', 20, 23, 'Y él les dice: A la verdad mi vaso beberéis, y del bautismo de que yo soy bautizado, seréis bautizados; mas el sentaros á mi mano derecha y á mi izquierda, no es mío dar lo, sino á aquellos para quienes está aparejado de mi Padre.', 'divine-light'),
('New', 'Mateo', 20, 24, 'Y como los diez oyeron esto, se enojaron de los dos hermanos.', 'divine-light'),
('New', 'Mateo', 20, 25, 'Entonces Jesús llamándolos, dijo: Sabéis que los príncipes de los Gentiles se enseñorean sobre ellos, y los que son grandes ejercen sobre ellos potestad.', 'divine-light'),
('New', 'Mateo', 20, 26, 'Mas entre vosotros no será así; sino el que quisiere entre vosotros hacerse grande, será vuestro servidor;', 'divine-light'),
('New', 'Mateo', 20, 27, 'Y el que quisiere entre vosotros ser el primero, será vuestro siervo:', 'divine-light'),
('New', 'Mateo', 20, 28, 'Como el Hijo del hombre no vino para ser servido, sino para servir, y para dar su vida en rescate por muchos.', 'divine-light'),
('New', 'Mateo', 20, 29, 'Entonces saliendo ellos de Jericó, le seguía gran compañía.', 'divine-light'),
('New', 'Mateo', 20, 30, 'Y he aquí dos ciegos sentados junto al camino, como oyeron que Jesús pasaba, clamaron, diciendo: Señor, Hijo de David, ten misericordia de nosotros.', 'divine-light'),
('New', 'Mateo', 20, 31, 'Y la gente les reñía para que callasen; mas ellos clamaban más, diciendo: Señor, Hijo de David, ten misericordia de nosotros.', 'divine-light'),
('New', 'Mateo', 20, 32, 'Y parándose Jesús, los llamó, y dijo: ¿Qué queréis que haga por vosotros?', 'divine-light'),
('New', 'Mateo', 20, 33, 'Ellos le dicen: Señor, que sean abiertos nuestros ojos.', 'divine-light'),
('New', 'Mateo', 20, 34, 'Entonces Jesús, teniendo misericordia de ellos, les tocó los ojos, y luego sus ojos recibieron la vista; y le siguieron.', 'divine-light'),
('New', 'Mateo', 21, 1, 'Y COMO se acercaron á Jerusalem, y vinieron á Bethfagé, al monte de las Olivas, entonces Jesús envió dos discípulos,', 'divine-light'),
('New', 'Mateo', 21, 2, 'Diciéndoles: Id á la aldea que está delante de vosotros, y luego hallaréis una asna atada, y un pollino con ella: desatad la, y traédme los.', 'divine-light'),
('New', 'Mateo', 21, 3, 'Y si alguno os dijere algo, decid: El Señor los ha menester. Y luego los dejará.', 'divine-light'),
('New', 'Mateo', 21, 4, 'Y todo esto fué hecho, para que se cumpliese lo que fué dicho por el profeta, que dijo:', 'divine-light'),
('New', 'Mateo', 21, 5, 'Decid á la hija de Sión: He aquí, tu Rey viene á ti, Manso, y sentado sobre una asna, Y sobre un pollino, hijo de animal de yugo.', 'divine-light'),
('New', 'Mateo', 21, 6, 'Y los discípulos fueron, é hicieron como Jesús les mandó;', 'divine-light'),
('New', 'Mateo', 21, 7, 'Y trajeron el asna y el pollino, y pusieron sobre ellos sus mantos; y se sentó sobre ellos.', 'divine-light'),
('New', 'Mateo', 21, 8, 'Y la compañía, que era muy numerosa, tendía sus mantos en el camino: y otros cortaban ramos de los árboles, y los tendían por el camino.', 'divine-light'),
('New', 'Mateo', 21, 9, 'Y las gentes que iban delante, y las que iban detrás, aclamaban diciendo: ¡Hosanna al Hijo de David! ¡Bendito el que viene en el nombre del Señor! ¡Hosanna en las alturas!', 'divine-light'),
('New', 'Mateo', 21, 10, 'Y entrando él en Jerusalem, toda la ciudad se alborotó, diciendo. ¿Quién es éste?', 'divine-light'),
('New', 'Mateo', 21, 11, 'Y las gentes decían: Este es Jesús, el profeta, de Nazaret de Galilea.', 'divine-light'),
('New', 'Mateo', 21, 12, 'Y entró Jesús en el templo de Dios, y echó fuera todos los que vendían y compraban en el templo, y trastornó las mesas de los cambiadores, y las sillas de los que vendían palomas;', 'divine-light'),
('New', 'Mateo', 21, 13, 'Y les dice: Escrito está: Mi casa, casa de oración será llamada; mas vosotros cueva de ladrones la habéis hecho.', 'divine-light'),
('New', 'Mateo', 21, 14, 'Entonces vinieron á él ciegos y cojos en el templo, y los sanó.', 'divine-light'),
('New', 'Mateo', 21, 15, 'Mas los príncipes de los sacerdotes y los escribas, viendo las maravillas que hacía, y á los muchachos aclamando en el templo y diciendo: ¡Hosanna al Hijo de David! se indignaron,', 'divine-light'),
('New', 'Mateo', 21, 16, 'Y le dijeron: ¿Oyes lo que éstos dicen? Y Jesús les dice: Sí: ¿nunca leísteis: De la boca de los niños y de los que maman perfeccionaste la alabanza?', 'divine-light'),
('New', 'Mateo', 21, 17, 'Y dejándolos, se salió fuera de la ciudad, á Bethania; y posó allí.', 'divine-light'),
('New', 'Mateo', 21, 18, 'Y por la mañana volviendo á la ciudad, tuvo hambre.', 'divine-light'),
('New', 'Mateo', 21, 19, 'Y viendo una higuera cerca del camino, vino á ella, y no halló nada en ella, sino hojas solamente, y le dijo: Nunca más para siempre nazca de ti fruto. Y luego se secó la higuera.', 'divine-light'),
('New', 'Mateo', 21, 20, 'Y viendo esto los discípulos, maravillados decían: ¿Cómo se secó luego la higuera?', 'divine-light'),
('New', 'Mateo', 21, 21, 'Y respondiendo Jesús les dijo: De cierto os digo, que si tuviereis fe, y no dudareis, no sólo haréis esto de la higuera: mas si á este monte dijereis: Quítate y échate en la mar, será hecho.', 'divine-light'),
('New', 'Mateo', 21, 22, 'Y todo lo que pidiereis en oración, creyendo, lo recibiréis.', 'divine-light'),
('New', 'Mateo', 21, 23, 'Y como vino al templo, llegáronse á él cuando estaba enseñando, los príncipes de los sacerdotes y los ancianos del pueblo, diciendo. ¿Con qué autoridad haces esto? ¿y quién te dió esta autoridad?', 'divine-light'),
('New', 'Mateo', 21, 24, 'Y respondiendo Jesús, les dijo: Yo también os preguntaré una palabra, la cual si me dijereis, también yo os diré con qué autoridad hago esto.', 'divine-light'),
('New', 'Mateo', 21, 25, 'El bautismo de Juan, ¿de dónde era? ¿del cielo, ó de los hombres? Ellos entonces pensaron entre sí, diciendo: Si dijéremos, del cielo, nos dirá: ¿Por qué pues no le creísteis?', 'divine-light'),
('New', 'Mateo', 21, 26, 'Y si dijéremos, de los hombres, tememos al pueblo; porque todos tienen á Juan por profeta.', 'divine-light'),
('New', 'Mateo', 21, 27, 'Y respondiendo á Jesús, dijeron: No sabemos. Y él también les dijo: Ni yo os digo con qué autoridad hago esto.', 'divine-light'),
('New', 'Mateo', 21, 28, 'Mas, ¿qué os parece? Un hombre tenía dos hijos, y llegando al primero, le dijo: Hijo, ve hoy á trabajar en mi viña.', 'divine-light'),
('New', 'Mateo', 21, 29, 'Y respondiendo él, dijo: No quiero; mas después, arrepentido, fué.', 'divine-light'),
('New', 'Mateo', 21, 30, 'Y llegando al otro, le dijo de la misma manera; y respondiendo él, dijo: Yo, señor, voy. Y no fué.', 'divine-light'),
('New', 'Mateo', 21, 31, '¿Cuál de los dos hizo la voluntad de su padre? Dicen ellos: El primero. Díceles Jesús: De cierto os digo, que los publicanos y las rameras os van delante al reino de Dios.', 'divine-light'),
('New', 'Mateo', 21, 32, 'Porque vino á vosotros Juan en camino de justicia, y no le creísteis; y los publicanos y las rameras le creyeron; y vosotros, viendo esto, no os arrepentisteis después para creerle.', 'divine-light'),
('New', 'Mateo', 21, 33, 'Oíd otra parábola: Fué un hombre, padre de familia, el cual plantó una viña; y la cercó de vallado, y cavó en ella un lagar, y edificó una torre, y la dió á renta á labradores, y se partió lejos.', 'divine-light'),
('New', 'Mateo', 21, 34, 'Y cuando se acercó el tiempo de los frutos, envió sus siervos á los labradores, para que recibiesen sus frutos.', 'divine-light'),
('New', 'Mateo', 21, 35, 'Mas los labradores, tomando á los siervos, al uno hirieron, y al otro mataron, y al otro apedrearon.', 'divine-light'),
('New', 'Mateo', 21, 36, 'Envió de nuevo otros siervos, más que los primeros; é hicieron con ellos de la misma manera.', 'divine-light'),
('New', 'Mateo', 21, 37, 'Y á la postre les envió su hijo, diciendo: Tendrán respeto á mi hijo.', 'divine-light'),
('New', 'Mateo', 21, 38, 'Mas los labradores, viendo al hijo, dijeron entre sí: Este es el heredero; venid, matémosle, y tomemos su heredad.', 'divine-light'),
('New', 'Mateo', 21, 39, 'Y tomado, le echaron fuera de la viña, y le mataron.', 'divine-light'),
('New', 'Mateo', 21, 40, 'Pues cuando viniere el señor de la viña, ¿qué hará á aquellos labradores?', 'divine-light'),
('New', 'Mateo', 21, 41, 'Dícenle: á los malos destruirá miserablemente, y su viña dará á renta á otros labradores, que le paguen el fruto á sus tiempos.', 'divine-light'),
('New', 'Mateo', 21, 42, 'Díceles Jesús: ¿Nunca leísteis en las Escrituras: La piedra que desecharon los que edificaban, Esta fué hecha por cabeza de esquina: Por el Señor es hecho esto, Y es cosa maravillosa en nuestros ojos?', 'divine-light'),
('New', 'Mateo', 21, 43, 'Por tanto os digo, que el reino de Dios será quitado de vosotros, y será dado á gente que haga los frutos de él.', 'divine-light'),
('New', 'Mateo', 21, 44, 'Y el que cayere sobre esta piedra, será quebrantado; y sobre quien ella cayere, le desmenuzará.', 'divine-light'),
('New', 'Mateo', 21, 45, 'Y oyendo los príncipes de los sacerdotes y los Fariseos sus parábolas, entendieron que hablaba de ellos.', 'divine-light'),
('New', 'Mateo', 21, 46, 'Y buscando cómo echarle mano, temieron al pueblo; porque le tenían por profeta.', 'divine-light'),
('New', 'Mateo', 22, 1, 'Y RESPONDIENDO Jesús, les volvió á hablar en parábolas, diciendo:', 'divine-light'),
('New', 'Mateo', 22, 2, 'El reino de los cielos es semejante á un hombre rey, que hizo bodas á su hijo;', 'divine-light'),
('New', 'Mateo', 22, 3, 'Y envió sus siervos para que llamasen los llamados á las bodas; mas no quisieron venir.', 'divine-light'),
('New', 'Mateo', 22, 4, 'Volvió á enviar otros siervos, diciendo: Decid á los llamados: He aquí, mi comida he aparejado; mis toros y animales engordados son muertos, y todo está prevenido: venid á las bodas.', 'divine-light'),
('New', 'Mateo', 22, 5, 'Mas ellos no se cuidaron, y se fueron, uno á su labranza, y otro á sus negocios;', 'divine-light'),
('New', 'Mateo', 22, 6, 'Y otros, tomando á sus siervos, los afrentaron y los mataron.', 'divine-light'),
('New', 'Mateo', 22, 7, 'Y el rey, oyendo esto, se enojó; y enviando sus ejércitos, destruyó á aquellos homicidas, y puso fuego á su ciudad.', 'divine-light'),
('New', 'Mateo', 22, 8, 'Entonces dice á sus siervos: Las bodas á la verdad están aparejadas; mas los que eran llamados no eran dignos.', 'divine-light'),
('New', 'Mateo', 22, 9, 'Id pues á las salidas de los caminos, y llamad á las bodas á cuantos hallareis.', 'divine-light'),
('New', 'Mateo', 22, 10, 'Y saliendo los siervos por los caminos, juntaron á todos los que hallaron, juntamente malos y buenos: y las bodas fueron llenas de convidados.', 'divine-light'),
('New', 'Mateo', 22, 11, 'Y entró el rey para ver los convidados, y vió allí un hombre no vestido de boda.', 'divine-light'),
('New', 'Mateo', 22, 12, 'Y le dijo: Amigo, ¿cómo entraste aquí no teniendo vestido de boda? Mas él cerró la boca.', 'divine-light'),
('New', 'Mateo', 22, 13, 'Entonces el rey dijo á los que servían: Atado de pies y de manos tomadle, y echadle en las tinieblas de afuera: allí será el lloro y el crujir de dientes.', 'divine-light'),
('New', 'Mateo', 22, 14, 'Porque muchos son llamados, y pocos escogidos.', 'divine-light'),
('New', 'Mateo', 22, 15, 'Entonces, idos los Fariseos, consultaron cómo le tomarían en alguna palabra.', 'divine-light'),
('New', 'Mateo', 22, 16, 'Y envían á él los discípulos de ellos, con los Herodianos, diciendo: Maestro, sabemos que eres amador de la verdad, y que enseñas con verdad el camino de Dios, y que no te curas de nadie, porque no tienes acepción de persona de hombres.', 'divine-light'),
('New', 'Mateo', 22, 17, 'Dinos pues, ¿qué te parece? ¿es lícito dar tributo á César, ó no?', 'divine-light'),
('New', 'Mateo', 22, 18, 'Mas Jesús, entendida la malicia de ellos, les dice: ¿Por qué me tentáis, hipócritas?', 'divine-light'),
('New', 'Mateo', 22, 19, 'Mostradme la moneda del tributo. Y ellos le presentaron un denario.', 'divine-light'),
('New', 'Mateo', 22, 20, 'Entonces les dice: ¿Cúya es esta figura, y lo que está encima escrito?', 'divine-light'),
('New', 'Mateo', 22, 21, 'Dícenle: De César. Y díceles: Pagad pues á César lo que es de César, y á Dios lo que es de Dios.', 'divine-light'),
('New', 'Mateo', 22, 22, 'Y oyendo esto, se maravillaron, y dejándole se fueron.', 'divine-light'),
('New', 'Mateo', 22, 23, 'Aquel día llegaron á él los Saduceos, que dicen no haber resurrección, y le preguntaron,', 'divine-light'),
('New', 'Mateo', 22, 24, 'Diciendo: Maestro, Moisés dijo: Si alguno muriere sin hijos, su hermano se casará con su mujer, y despertará simiente á su hermano.', 'divine-light'),
('New', 'Mateo', 22, 25, 'Fueron pues, entre nosotros siete hermanos: y el primero tomó mujer, y murió; y no teniendo generación, dejó su mujer á su hermano.', 'divine-light'),
('New', 'Mateo', 22, 26, 'De la misma manera también el segundo, y el tercero, hasta los siete.', 'divine-light'),
('New', 'Mateo', 22, 27, 'Y después de todos murió también la mujer.', 'divine-light'),
('New', 'Mateo', 22, 28, 'En la resurrección pues, ¿de cuál de los siete será ella mujer? porque todos la tuvieron.', 'divine-light'),
('New', 'Mateo', 22, 29, 'Entonces respondiendo Jesús, les dijo: Erráis ignorando las Escrituras, y el poder de Dios.', 'divine-light'),
('New', 'Mateo', 22, 30, 'Porque en la resurrección, ni los hombres tomarán mujeres, ni las mujeres marido; mas son como los ángeles de Dios en el cielo.', 'divine-light'),
('New', 'Mateo', 22, 31, 'Y de la resurrección de los muertos, ¿no habéis leído lo que os es dicho por Dios, que dice:', 'divine-light'),
('New', 'Mateo', 22, 32, 'Yo soy el Dios de Abraham, y el Dios de Isaac, y el Dios de Jacob? Dios no es Dios de muertos, sino de vivos.', 'divine-light'),
('New', 'Mateo', 22, 33, 'Y oyendo esto las gentes, estaban atónitas de su doctrina.', 'divine-light'),
('New', 'Mateo', 22, 34, 'Entonces los Fariseos, oyendo que había cerrado la boca á los Saduceos, se juntaron á una.', 'divine-light'),
('New', 'Mateo', 22, 35, 'Y preguntó uno de ellos, intérprete de la ley, tentándole y diciendo:', 'divine-light'),
('New', 'Mateo', 22, 36, 'Maestro, ¿cuál es el mandamiento grande en la ley?', 'divine-light'),
('New', 'Mateo', 22, 37, 'Y Jesús le dijo: Amarás al Señor tu Dios de todo tu corazón, y de toda tu alma, y de toda tu mente.', 'divine-light'),
('New', 'Mateo', 22, 38, 'Este es el primero y el grande mandamiento.', 'divine-light'),
('New', 'Mateo', 22, 39, 'Y el segundo es semejante á éste: Amarás á tu prójimo como á ti mismo.', 'divine-light'),
('New', 'Mateo', 22, 40, 'De estos dos mandamientos depende toda la ley y los profetas.', 'divine-light'),
('New', 'Mateo', 22, 41, 'Y estando juntos los Fariseos, Jesús les preguntó,', 'divine-light'),
('New', 'Mateo', 22, 42, 'Diciendo: ¿Qué os parece del Cristo? ¿de quién es Hijo? Dícenle: De David.', 'divine-light'),
('New', 'Mateo', 22, 43, 'El les dice: ¿Pues cómo David en Espíritu le llama Señor, diciendo:', 'divine-light'),
('New', 'Mateo', 22, 44, 'Dijo el Señor á mi Señor: Siéntate á mi diestra, Entre tanto que pongo tus enemigos por estrado de tus pies?', 'divine-light'),
('New', 'Mateo', 22, 45, 'Pues si David le llama Señor, ¿cómo es su Hijo?', 'divine-light'),
('New', 'Mateo', 22, 46, 'Y nadie le podía responder palabra; ni osó alguno desde aquel día preguntarle más.', 'divine-light'),
('New', 'Mateo', 23, 1, 'ENTONCES habló Jesús á las gentes y á sus discípulos,', 'divine-light'),
('New', 'Mateo', 23, 2, 'Diciendo: Sobre la cátedra de Moisés se sentaron los escribas y los Fariseos:', 'divine-light'),
('New', 'Mateo', 23, 3, 'Así que, todo lo que os dijeren que guardéis, guardad lo y haced lo; mas no hagáis conforme á sus obras: porque dicen, y no hacen.', 'divine-light'),
('New', 'Mateo', 23, 4, 'Porque atan cargas pesadas y difíciles de llevar, y las ponen sobre los hombros de los hombres; mas ni aun con su dedo las quieren mover.', 'divine-light'),
('New', 'Mateo', 23, 5, 'Antes, todas sus obras hacen para ser mirados de los hombres; porque ensanchan sus filacterias, y extienden los flecos de sus mantos;', 'divine-light'),
('New', 'Mateo', 23, 6, 'Y aman los primeros asientos en las cenas, y las primeras sillas en las sinagogas;', 'divine-light'),
('New', 'Mateo', 23, 7, 'Y las salutaciones en las plazas, y ser llamados de los hombres Rabbí, Rabbí.', 'divine-light'),
('New', 'Mateo', 23, 8, 'Mas vosotros, no queráis ser llamados Rabbí; porque uno es vuestro Maestro, el Cristo; y todos vosotros sois hermanos.', 'divine-light'),
('New', 'Mateo', 23, 9, 'Y vuestro padre no llaméis á nadie en la tierra; porque uno es vuestro Padre, el cual está en los cielos.', 'divine-light'),
('New', 'Mateo', 23, 10, 'Ni seáis llamados maestros; porque uno es vuestro Maestro, el Cristo.', 'divine-light'),
('New', 'Mateo', 23, 11, 'El que es el mayor de vosotros, sea vuestro siervo.', 'divine-light'),
('New', 'Mateo', 23, 12, 'Porque el que se ensalzare, será humillado; y el que se humillare, será ensalzado.', 'divine-light'),
('New', 'Mateo', 23, 13, 'Mas ¡ay de vosotros, escribas y Fariseos, hipócritas! porque cerráis el reino de los cielos delante de los hombres; que ni vosotros entráis, ni á los que están entrando dejáis entrar.', 'divine-light'),
('New', 'Mateo', 23, 14, '¡Ay de vosotros, escribas y Fariseos, hipócritas! porque coméis las casas de las viudas, y por pretexto hacéis larga oración: por esto llevaréis mas grave juicio.', 'divine-light'),
('New', 'Mateo', 23, 15, '¡Ay de vosotros, escribas y Fariseos, hipócritas! porque rodeáis la mar y la tierra por hacer un prosélito; y cuando fuere hecho, le hacéis hijo del infierno doble más que vosotros.', 'divine-light'),
('New', 'Mateo', 23, 16, '¡Ay de vosotros, guías ciegos! que decís: Cualquiera que jurare por el templo es nada; mas cualquiera que jurare por el oro del templo, deudor es.', 'divine-light'),
('New', 'Mateo', 23, 17, '¡Insensatos y ciegos! porque ¿cuál es mayor, el oro, ó el templo que santifica al oro?', 'divine-light'),
('New', 'Mateo', 23, 18, 'Y: Cualquiera que jurare por el altar, es nada; mas cualquiera que jurare por el presente que está sobre él, deudor es.', 'divine-light'),
('New', 'Mateo', 23, 19, '¡Necios y ciegos! porque, ¿cuál es mayor, el presente, ó el altar que santifica al presente?', 'divine-light'),
('New', 'Mateo', 23, 20, 'Pues el que jurare por el altar, jura por él, y por todo lo que está sobre él;', 'divine-light'),
('New', 'Mateo', 23, 21, 'Y el que jurare por el templo, jura por él, y por Aquél que habita en él;', 'divine-light'),
('New', 'Mateo', 23, 22, 'Y el que jura por el cielo, jura por el trono de Dios, y por Aquél que está sentado sobre él.', 'divine-light'),
('New', 'Mateo', 23, 23, '¡Ay de vosotros, escribas y Fariseos, hipócritas! porque diezmáis la menta y el eneldo y el comino, y dejasteis lo que es lo más grave de la ley, es á saber, el juicio y la misericordia y la fe: esto era menester hacer, y no dejar lo otro.', 'divine-light'),
('New', 'Mateo', 23, 24, '¡Guías ciegos, que coláis el mosquito, mas tragáis el camello!', 'divine-light'),
('New', 'Mateo', 23, 25, '¡Ay de vosotros, escribas y Fariseos, hipócritas! porque limpiais lo que está de fuera del vaso y del plato; mas de dentro están llenos de robo y de injusticia.', 'divine-light'),
('New', 'Mateo', 23, 26, '¡Fariseo ciego, limpia primero lo de dentro del vaso y del plato, para que también lo de fuera se haga limpio!', 'divine-light'),
('New', 'Mateo', 23, 27, '¡Ay de vosotros, escribas y Fariseos, hipócritas! porque sois semejantes á sepulcros blanqueados, que de fuera, á la verdad, se muestran hermosos, mas de dentro están llenos de huesos de muertos y de toda suciedad.', 'divine-light'),
('New', 'Mateo', 23, 28, 'Así también vosotros de fuera, á la verdad, os mostráis justos á los hombres; mas de dentro, llenos estáis de hipocresía é iniquidad.', 'divine-light'),
('New', 'Mateo', 23, 29, '¡Ay de vosotros, escribas y Fariseos, hipócritas! porque edificáis los sepulcros de los profetas, y adornáis los monumentos de los justos,', 'divine-light'),
('New', 'Mateo', 23, 30, 'Y decís: Si fuéramos en los días de nuestros padres, no hubiéramos sido sus compañeros en la sangre de los profetas.', 'divine-light'),
('New', 'Mateo', 23, 31, 'Así que, testimonio dais á vosotros mismos, que sois hijos de aquellos que mataron á los profetas.', 'divine-light'),
('New', 'Mateo', 23, 32, '¡Vosotros también henchid la medida de vuestros padres!', 'divine-light'),
('New', 'Mateo', 23, 33, '¡Serpientes, generación de víboras! ¿cómo evitaréis el juicio del infierno?', 'divine-light'),
('New', 'Mateo', 23, 34, 'Por tanto, he aquí, yo envío á vosotros profetas, y sabios, y escribas: y de ellos, á unos mataréis y crucificaréis, y á otros de ellos azotaréis en vuestras sinagogas, y perseguiréis de ciudad en ciudad:', 'divine-light'),
('New', 'Mateo', 23, 35, 'Para que venga sobre vosotros toda la sangre justa que se ha derramado sobre la tierra, desde la sangre de Abel el justo, hasta la sangre de Zacarías, hijo de Barachîas, al cual matasteis entre el templo y el altar.', 'divine-light'),
('New', 'Mateo', 23, 36, 'De cierto os digo que todo esto vendrá sobre esta generación.', 'divine-light'),
('New', 'Mateo', 23, 37, '¡Jerusalem, Jerusalem, que matas á los profetas, y apedreas á los que son enviados á ti! ¡cuántas veces quise juntar tus hijos, como la gallina junta sus pollos debajo de las alas, y no quisiste!', 'divine-light'),
('New', 'Mateo', 23, 38, 'He aquí vuestra casa os es dejada desierta.', 'divine-light'),
('New', 'Mateo', 23, 39, 'Porque os digo que desde ahora no me veréis, hasta que digáis: Bendito el que viene en el nombre del Señor.', 'divine-light'),
('New', 'Mateo', 24, 1, 'Y SALIDO Jesús, íbase del templo; y se llegaron sus discípulos, para mostrarle los edificios del templo.', 'divine-light'),
('New', 'Mateo', 24, 2, 'Y respondiendo él, les dijo: ¿Veis todo esto? de cierto os digo, que no será dejada aquí piedra sobre piedra, que no sea destruída.', 'divine-light'),
('New', 'Mateo', 24, 3, 'Y sentándose él en el monte de las Olivas, se llegaron á él los discípulos aparte, diciendo: Dinos, ¿cuándo serán estas cosas, y qué señal habrá de tu venida, y del fin del mundo?', 'divine-light'),
('New', 'Mateo', 24, 4, 'Y respondiendo Jesús, les dijo: Mirad que nadie os engañe.', 'divine-light'),
('New', 'Mateo', 24, 5, 'Porque vendrán muchos en mi nombre, diciendo: Yo soy el Cristo; y á muchos engañarán.', 'divine-light'),
('New', 'Mateo', 24, 6, 'Y oiréis guerras, y rumores de guerras: mirad que no os turbéis; porque es menester que todo esto acontezca; mas aún no es el fin.', 'divine-light'),
('New', 'Mateo', 24, 7, 'Porque se levantará nación contra nación, y reino contra reino; y habrá pestilencias, y hambres, y terremotos por los lugares.', 'divine-light'),
('New', 'Mateo', 24, 8, 'Y todas estas cosas, principio de dolores.', 'divine-light'),
('New', 'Mateo', 24, 9, 'Entonces os entregarán para ser afligidos, y os matarán; y seréis aborrecidos de todas las gentes por causa de mi nombre.', 'divine-light'),
('New', 'Mateo', 24, 10, 'Y muchos entonces serán escandalizados; y se entregarán unos á otros, y unos á otros se aborrecerán.', 'divine-light'),
('New', 'Mateo', 24, 11, 'Y muchos falsos profetas se levantarán y engañarán á muchos.', 'divine-light'),
('New', 'Mateo', 24, 12, 'Y por haberse multiplicado la maldad, la caridad de muchos se resfriará.', 'divine-light'),
('New', 'Mateo', 24, 13, 'Mas el que perseverare hasta el fin, éste será salvo.', 'divine-light'),
('New', 'Mateo', 24, 14, 'Y será predicado este evangelio del reino en todo el mundo, por testimonio á todos los Gentiles; y entonces vendrá el fin.', 'divine-light'),
('New', 'Mateo', 24, 15, 'Por tanto, cuando viereis la abominación del asolamiento, que fué dicha por Daniel profeta, que estará en el lugar santo, (el que lee, entienda),', 'divine-light'),
('New', 'Mateo', 24, 16, 'Entonces los que están en Judea, huyan á los montes;', 'divine-light'),
('New', 'Mateo', 24, 17, 'Y el que sobre el terrado, no descienda á tomar algo de su casa;', 'divine-light'),
('New', 'Mateo', 24, 18, 'Y el que en el campo, no vuelva atrás á tomar sus vestidos.', 'divine-light'),
('New', 'Mateo', 24, 19, 'Mas ¡ay de las preñadas, y de las que crían en aquellos días!', 'divine-light'),
('New', 'Mateo', 24, 20, 'Orad, pues, que vuestra huída no sea en invierno ni en sábado;', 'divine-light'),
('New', 'Mateo', 24, 21, 'Porque habrá entonces grande aflicción, cual no fué desde el principio del mundo hasta ahora, ni será.', 'divine-light'),
('New', 'Mateo', 24, 22, 'Y si aquellos días no fuesen acortados, ninguna carne sería salva; mas por causa de los escogidos, aquellos días serán acortados.', 'divine-light'),
('New', 'Mateo', 24, 23, 'Entonces, si alguno os dijere: He aquí está el Cristo, ó allí, no creáis.', 'divine-light'),
('New', 'Mateo', 24, 24, 'Porque se levantarán falsos Cristos, y falsos profetas, y darán señales grandes y prodigios; de tal manera que engañarán, si es posible, aun á los escogidos.', 'divine-light'),
('New', 'Mateo', 24, 25, 'He aquí os lo he dicho antes.', 'divine-light'),
('New', 'Mateo', 24, 26, 'Así que, si os dijeren: He aquí en el desierto está; no salgáis: He aquí en las cámaras; no creáis.', 'divine-light'),
('New', 'Mateo', 24, 27, 'Porque como el relámpago que sale del oriente y se muestra hasta el occidente, así será también la venida del Hijo del hombre.', 'divine-light'),
('New', 'Mateo', 24, 28, 'Porque donde quiera que estuviere el cuerpo muerto, allí se juntarán las águilas.', 'divine-light'),
('New', 'Mateo', 24, 29, 'Y luego después de la aflicción de aquellos días, el sol se obscurecerá, y la luna no dará su lumbre, y las estrellas caerán del cielo, y las virtudes de los cielos serán conmovidas.', 'divine-light'),
('New', 'Mateo', 24, 30, 'Y entonces se mostrará la señal del Hijo del hombre en el cielo; y entonces lamentarán todas las tribus de la tierra, y verán al Hijo del hombre que vendrá sobre las nubes del cielo, con grande poder y gloria.', 'divine-light'),
('New', 'Mateo', 24, 31, 'Y enviará sus ángeles con gran voz de trompeta, y juntarán sus escogidos de los cuatro vientos, de un cabo del cielo hasta el otro.', 'divine-light'),
('New', 'Mateo', 24, 32, 'De la higuera aprended la parábola: Cuando ya su rama se enternece, y las hojas brotan, sabéis que el verano está cerca.', 'divine-light'),
('New', 'Mateo', 24, 33, 'Así también vosotros, cuando viereis todas estas cosas, sabed que está cercano, á las puertas.', 'divine-light'),
('New', 'Mateo', 24, 34, 'De cierto os digo, que no pasará esta generación, que todas estas cosas no acontezcan.', 'divine-light'),
('New', 'Mateo', 24, 35, 'El cielo y la tierra pasarán, mas mis palabras no pasarán.', 'divine-light'),
('New', 'Mateo', 24, 36, 'Empero del día y hora nadie sabe, ni aun los ángeles de los cielos, sino mi Padre solo.', 'divine-light'),
('New', 'Mateo', 24, 37, 'Mas como los días de Noé, así será la venida del Hijo del hombre.', 'divine-light'),
('New', 'Mateo', 24, 38, 'Porque como en los días antes del diluvio estaban comiendo y bebiendo, casándose y dando en casamiento, hasta el día que Noé entró en el arca,', 'divine-light'),
('New', 'Mateo', 24, 39, 'Y no conocieron hasta que vino el diluvio y llevó á todos, así será también la venida del Hijo del hombre.', 'divine-light'),
('New', 'Mateo', 24, 40, 'Entonces estarán dos en el campo; el uno será tomado, y el otro será dejado:', 'divine-light'),
('New', 'Mateo', 24, 41, 'Dos mujeres moliendo á un molinillo; la una será tomada, y la otra será dejada.', 'divine-light'),
('New', 'Mateo', 24, 42, 'Velad pues, porque no sabéis á qué hora ha de venir vuestro Señor.', 'divine-light');